(window.webpackJsonp=window.webpackJsonp||[]).push([[1089],{1444:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1954-đanh-bac-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1954-đanh-bac-thuong"}},[n._v("#")]),n._v(" Chương 1954: Đánh bạc (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thuốc này hữu hiệu?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh hốc mắt đỏ lên."),t("br"),t("br"),n._v("Gia gia thương thế, nàng biết rất rõ ràng, nếu không phải tu vi cao thâm, khẳng định đã sớm chết, dù vậy, cũng đến sinh mệnh phần cuối, không có gì bất ngờ xảy ra, hai ngày này nhất định không kiên trì nổi... Giờ phút này, uống một bình ngọc nước sạch, liền tỉnh lại, còn có thể nói chuyện, nồng đậm sau khi hết khiếp sợ, tràn đầy khó tin."),t("br"),t("br"),n._v("Coi như tông môn cao minh nhất Tục Mệnh đan, cũng không có như vậy lợi hại hiệu quả đi!"),t("br"),t("br"),n._v("“Lão gia...”"),t("br"),t("br"),n._v("Bạch Phong cũng vội vàng đi tới trước mặt, đem Bạch Diệp trưởng lão đỡ dậy, chân khí phun trào, hướng đối phương trong cơ thể dò xét."),t("br"),t("br"),n._v("Một lát sau, con mắt trợn tròn: “Lão gia thương thế bên trong cơ thể, vậy mà khôi phục một chút...”"),t("br"),t("br"),n._v("Tuy là khôi phục cũng không rõ ràng, dù sao khôi phục, đặc biệt là trong cơ thể cái loại này, không ngừng ăn mòn nội tạng, chân khí, linh hồn, không cách nào khu trừ tĩnh mịch lực lượng, vậy mà đạt được chặn chế!"),t("br"),t("br"),n._v("Đây chính là Hãm Không chi thành đặc thù lực lượng... Bất kỳ dược vật, đều cơ hồ vô hiệu, không nghĩ tới, tiểu thư lấy ra cái kia bình nước sạch làm được!"),t("br"),t("br"),n._v("“Tiểu thư, ngươi bình thuốc này... Từ nơi nào mua?”"),t("br"),t("br"),n._v("Vội vàng nhìn lại."),t("br"),t("br"),n._v("“Liền bên kia nội môn thị trường giao dịch...” Bạch Nguyễn Khanh một chỉ."),t("br"),t("br"),n._v("“Mua một bình?”"),t("br"),t("br"),n._v("Vừa rồi lão gia uống quá ít, cho nên, chỉ khôi phục một tia, nhưng bình này nước sạch, đối thương thế áp chế tác dụng, hết sức rõ ràng, chỉ cần số lượng đủ, có thể đoán được, làm cho cả tông môn đều bó tay toàn tập bị thương nặng, có thể hoàn mỹ khôi phục!"),t("br"),t("br"),n._v("“Ta cho là giả... Cho nên ít mua chút...” Bạch Nguyễn Khanh hơi đỏ mặt."),t("br"),t("br"),n._v("“Không những không phải giả, còn là thần dược, mau dẫn ta đi qua, có bao nhiêu mua bao nhiêu, chỉ cần số lượng nhiều, lão gia tuyệt đối có thể khôi phục...” Bạch Phong kìm nén không được."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Thấy đối phương bộ dáng này, Bạch Nguyễn Khanh cũng biết, lần này khẳng định là tìm tới thuốc tốt, lúc này xoay người hướng phi đi Thánh thú bay lượn mà đi."),t("br"),t("br"),n._v("“Quá chậm, ta mang ngươi tới!”"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, Bạch Phong âm thanh vang lên, ngay sau đó liền cảm thấy trên người chợt nhẹ, bị gia gia vị này lão bộc nắm lấy, thẳng tắp hướng về nội môn đệ tử thị trường giao dịch bay tới."),t("br"),t("br"),n._v("Mấy hơi thở sau đó, đi tới thị trường, Bạch Nguyễn Khanh vội vã đi tới vừa rồi mua thuốc địa phương, lại phát hiện đã trống không."),t("br"),t("br"),n._v("“Vừa rồi cái nào bán thuốc người đâu?”"),t("br"),t("br"),n._v("Quay đầu nhìn về phía bên cạnh nội môn đệ tử, Bạch Nguyễn Khanh đôi mi thanh tú nhăn lại."),t("br"),t("br"),n._v("Nhìn thấy vị này nữ bạo long, nhanh như vậy liền trở lại, hơn nữa còn mang theo vị biết bay lão giả, bên cạnh nội môn đệ tử giật nảy mình: “Mới vừa đi...”"),t("br"),t("br"),n._v("“Ngươi có biết hắn là cái nào chi mạch, cái nào trưởng lão môn hạ đệ tử?”"),t("br"),t("br"),n._v("Nữ hài tiếp tục hỏi."),t("br"),t("br"),n._v("“Không biết...” Bên cạnh nội môn đệ tử một mặt căng thẳng: “Hắn là ngày hôm nay mới tới, chúng ta không quen, coi như bán thuốc giả... Cùng ta cũng không quan hệ...”"),t("br"),t("br"),n._v("“Đúng vậy a, chúng ta thật không nhận ra, biết cái tên này không đáng tin cậy, muốn mua chúng ta đồ vật, chúng ta đều không có bán...”"),t("br"),t("br"),n._v("“Vừa nhìn liền biết không phải người tốt lành gì... Lần sau tới, ta nhất định đuổi ra ngoài, tuyệt không để hắn đi vào...”"),t("br"),t("br"),n._v("Cách đó không xa mấy vị nội môn đệ tử, cũng vội vàng đạo."),t("br"),t("br"),n._v("Vừa rồi Trương Huyền bán thuốc thời điểm, bọn họ đều thấy được, giờ phút này nữ bạo long vội vã như thế xông lại, khẳng định là muốn tìm phiền toái, đương nhiên là trước phủi sạch quan hệ, thuận tiện lại giả mạo người tốt."),t("br"),t("br"),n._v("Thấy mọi người tất cả đều lòng đầy căm phẫn, Bạch Nguyễn Khanh sửng sốt một chút: “Ta tại sao phải giận chó đánh mèo?”"),t("br"),t("br"),n._v("“Hắn không phải bán thuốc giả cho ngươi ư?” Bên cạnh nội môn đệ tử nhìn qua."),t("br"),t("br"),n._v("“Là thuốc thật, ta tới, là muốn nhiều mua mấy bình...” Bạch Nguyễn Khanh lắc đầu: “Thuận tiện cảm ơn hắn một chút!”"),t("br"),t("br"),n._v("“Thuốc thật? Cảm ơn?” Một đám mua đồ nội môn đệ tử, tất cả đều choáng váng."),t("br"),t("br"),n._v("Viết như vậy khoa trương, tuỳ bày cái quầy hàng, trò đùa đồng dạng... Thật?"),t("br"),t("br"),n._v("“Hắn không phải bán đồ ư? Đi như thế nào nhanh như vậy?”"),t("br"),t("br"),n._v("Thấy mọi người vẻ mặt không đúng, Bạch Nguyễn Khanh lông mày nhíu một cái: “Ta muốn nghe lời thật, các ngươi hẳn phải biết, lừa gạt ta kết quả!”"),t("br"),t("br"),n._v("“Cái này... Hắn bán thuốc mục đích là muốn mua sắm thông thần ngọc phù, kết quả chúng ta không bán cho hắn, còn để hắn nhanh lên một chút rời đi, cho nên...” Bên cạnh nội môn đệ tử sắp khóc."),t("br"),t("br"),n._v("“Để hắn rời đi?” Bạch Nguyễn Khanh rốt cuộc minh bạch tới, gương mặt xinh đẹp trầm xuống: “Gia gia của ta ân nhân cứu mạng, bị các ngươi đuổi đi, đã như vậy...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Bóng người lóe lên, sau một khắc, bán đồ rất nhiều nội môn đệ tử, tất cả đều nằm trên mặt đất, kêu thảm liên tục."),t("br"),t("br"),n._v("Nữ bạo long quả nhiên vẫn là nữ bạo long, một lời không hợp, liền động thủ... Hơn nữa ác như vậy..."),t("br"),t("br"),n._v("Rất nhanh ngừng lại, nữ bạo long hai tay chống nạnh, nhìn quanh một vòng, âm thanh băng lãnh: “Đều cho ta đi tìm hắn, trước khi trời tối, tìm không thấy, ta sẽ tiếp tục ra tay, lại đánh các ngươi một hồi! Đừng nghĩ đến trốn, ta muốn đánh người... Chỉ cần không rời đi tông môn hoặc là chết rồi, hiện nay, còn không người thoát khỏi!”"),t("br"),t("br"),n._v("“...” Mọi người."),t("br"),t("br"),n._v("Bán thuốc đại ca... Có thể hay không có chút phẩm đức nghề nghiệp?"),t("br"),t("br"),n._v("Bán chút thuốc giả không được sao?"),t("br"),t("br"),n._v("Bán thuốc giả, muốn đánh cũng chỉ đánh ngươi một cái... Chúng ta mẹ nó trêu ai ghẹo ai?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền trở lại chỗ ở."),t("br"),t("br"),n._v("Đem Tào Thành Lập lấy được thông thần ngọc phù lấy ra ngoài, máu tươi nhỏ vào đi nhận chủ, tinh thần khẽ động, tiến vào bên trong."),t("br"),t("br"),n._v("Lần này xe nhẹ đường quen, mười phần đơn giản."),t("br"),t("br"),n._v("Ngọc phù này cùng trước đó Thông Thần điện khác biệt, không có Khổng sư giới thiệu, tiến vào bên trong, cảm ứng thân thể một cái tình huống, cùng mọi người nói đồng dạng, thực lực vì Cổ Thánh nhất trọng!"),t("br"),t("br"),n._v("Tu vi quá thấp, không ít võ kỹ cùng chiêu số, đều không có phương pháp thuận lợi thi triển, Cổ Thánh nhất trọng thông thần ngọc phù, tại Lăng Vân kiếm các, đã được cho yếu nhất."),t("br"),n._v("“Lần này không thể gọi Thiên Nhai...”"),t("br"),t("br"),n._v("Dừng lại một chút."),t("br"),t("br"),n._v("Trước đó tại Tinh Diệu thành Thông Thần điện, lấy tên Thiên Nhai, Lục Vân trưởng lão bọn người tưởng lầm là Thiện Hiểu Thiên, lúc này lại lấy cái tên này xuất hiện, rất dễ dàng bị phát giác, đến lúc đó, còn thế nào quyền đánh tông chủ, chân đá trưởng lão?"),t("br"),t("br"),n._v("Còn thế nào ẩn nấp thân phận, điều tra Lạc Nhược Hi vị trí Linh Thần Cung?"),t("br"),t("br"),n._v("“Dù sao đều là ngoại hiệu, đã Thiên Nhai không được, Dương Huyền không cần thiết... Vậy thì dựa theo ta tính cách tới đi... Gọi !”"),t("br"),t("br"),n._v("Trầm tư phút chốc, Trương Huyền lấy một cái thoả mãn ngoại hiệu."),t("br"),t("br"),n._v("Trước đó Thiên Nhai, thực sự thật khó nghe!"),t("br"),t("br"),n._v("Đặc biệt là được người xưng hô... Trời cọng lông, nhai cọng lông... Quả thực chính là làm nhục!"),t("br"),t("br"),n._v("Ta Rất Biết Điều... Đã phù hợp khí chất, lại phù hợp hắn không kiêu căng tính cách."),t("br"),t("br"),n._v("Lấy tên rất hay, Trương Huyền đi thẳng về phía trước, đồng dạng là một ngôi đại điện, phía trên viết Lăng Vân kiếm các Thông Thần điện chữ."),t("br"),t("br"),n._v("Đẩy cửa đi vào, người đông nghìn nghịt, không biết bao nhiêu, đều trên người cõng trường kiếm, kiếm khí ngút trời."),t("br"),t("br"),n._v("Tìm người hỏi thăm một chút."),t("br"),t("br"),n._v("Cùng phổ thông Thông Thần điện đồng dạng, kiếm lấy kiếm các tệ phương pháp nhanh nhất chính là một đối một tỷ thí, thấy như thế đơn giản, rất mau tới đến chỗ so kiếm."),t("br"),t("br"),n._v("Đinh đinh đinh đinh!"),t("br"),t("br"),n._v("Trên đài hai người trẻ tuổi ngay tại tỷ thí."),t("br"),t("br"),n._v("Bởi vì trong này dung mạo, cũng không phải bản thân, chiến đấu hai bên, đến cùng là ai, không ai biết được, vì tốt hơn phân chia, một cái thanh y, một cái bạch y."),t("br"),t("br"),n._v("Thanh niên trường kiếm lay động, kiếm mang lăng lệ, bạch y thân ảnh như gió, kiếm khí như hồng."),t("br"),t("br"),n._v("Hai người đấu lực lượng ngang nhau."),t("br"),t("br"),n._v("“Không tệ!”"),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền không khỏi gật đầu."),t("br"),t("br"),n._v("Không hổ là Lăng Vân kiếm các nội môn đệ tử, kiếm thuật quả nhiên so Hoắc Giang Hà đám người cao minh không biết bao nhiêu, một chiêu một thức, đều tự thành pháp luật, đã có tông sư phong thái."),t("br"),t("br"),n._v("Loại kiếm thuật này, nếu như đến danh sư đại lục, Hưng Mộng kiếm thánh đám người kiếm thuật, khả năng đều xa xa không kịp."),t("br"),t("br"),n._v("“Huynh đệ, có muốn hay không đặt cược?”"),t("br"),t("br"),n._v("Đang muốn nghiên cứu một chút, trên đài song phương kiếm thuật cùng với kiếm chiêu, liền nghe đến cách đó không xa một cái đè thấp âm thanh vang lên, quay đầu nhìn lại, chỉ thấy một người mặc khôi bào thanh niên, một mặt lén lén lút lút đi tới trước mặt."),t("br"),t("br"),n._v("Thông Thần điện phía trong dung mạo, đều cùng mở mỹ nhan cùng PS, nhìn không ra đẹp xấu, có điều, cái tên này, ánh mắt lóe lên, có vẻ hơi hèn hạ."),t("br"),t("br"),n._v("“Làm sao đặt?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Lăng Vân kiếm các, vì sáu đại tông môn một trong, làm việc quang minh chính đại, dưới tình huống bình thường, là không cho phép đệ tử so kiếm đặt cược, cho nên, nơi này cũng không có công khai đánh bạc, cái tên này, như vậy quỷ quái, khẳng định cũng là sợ bị người phát hiện, muốn nhân cơ hội kiếm ít tiền lẻ."),t("br"),t("br"),n._v("“Rất đơn giản, thanh y 1:1.5, bạch y, 1:1,1, hoà nhau gấp năm lần!” Khôi bào thanh niên nói."),t("br"),t("br"),n._v("“Bạch y, vì sao mới như vậy chút?” Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("“Hắn chiến thắng tỉ lệ lớn, giao đấu qua ba trăm tràng, thắng được hơn hai trăm tràng, kiếm thuật khẳng định cao minh hơn một ít!” Khôi bào thanh niên cười hắc hắc: “Có muốn hay không áp một ít chơi đùa?”"),t("br"),t("br"),n._v("“Có thể!”"),t("br"),t("br"),n._v("Hướng trên đài hai người nhìn sang, Trương Huyền ngay sau đó nhẹ nhàng cười một tiếng, duỗi ra hai ngón tay: “Ta áp thanh y chiến thắng, hai mươi viên kiếm các tệ!”"),t("br"),t("br"),n._v("“Hai mươi viên?”"),t("br"),t("br"),n._v("Không nghĩ tới cái này lạ mặt gia hỏa, vừa ra tay cứ như vậy xa xỉ, khôi bào thanh niên giật nảy mình."),t("br"),t("br"),n._v("Cùng Trương Huyền biết đồng dạng, kiếm các là không cho phép đánh bạc, có điều, một ít đệ tử tiểu đả tiểu nháo, cũng không có người đi quản, dưới tình huống bình thường, như vậy so kiếm đặt cược, hai, ba cái kiếm các tệ, mười phần bình thường, năm cái coi như nhiều, một hơi áp hai mươi viên, còn lần đầu tiên thấy."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Mua định rời tay, liền không thể nuốt lời...”"),t("br"),t("br"),n._v("Khôi bào thanh niên ngẩng đầu nhìn về phía trên đài, bạch y rõ ràng chiếm thượng phong, thanh y liên tục bại lui, bất cứ lúc nào cũng sẽ thua, cười hắc hắc, lấy ra một cái hai mươi mệnh giá thẻ đưa tới: “Đem tiền cho ta, tỷ thí sau khi kết thúc, cầm thứ này, hướng ta hối đoái... Đương nhiên, nếu như thua, một phân tiền cũng không có!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Lúc tiến vào, liền đem nơi này thông thần thẻ, chuẩn bị xong, hai mươi viên kiếm các tệ, cũng tất cả đều để vào trong đó, nhẹ nhàng điểm một cái, trên thẻ hai mươi bị cắt mất."),t("br"),t("br"),n._v("“Khà khà!”"),t("br"),t("br"),n._v("Nhìn trong tay nhiều ra hai mươi viên kiếm các tệ, khôi bào thanh niên nhẹ nhàng cười một tiếng, xoay người hướng một bên đi tới."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Mới vừa đi tới một bên, lại một người mặc tương đồng quần áo thanh niên đi tới trước mặt."),t("br"),t("br"),n._v("“Gặp được một cái thằng ngốc, vừa đè ép thanh y chiến thắng, trọn hai mươi viên kiếm các tệ!”"),t("br"),t("br"),n._v("Khôi bào thanh niên hạ giọng."),t("br"),t("br"),n._v("“Hai mươi? Thật là ngốc!” Sửng sốt một chút, cái thứ hai thanh niên ngay sau đó con mắt sáng lên: “Người khác không biết, trên đài chiến đấu là ai, ta nhưng mà điều tra rõ ràng, bạch y là Mạt Giang sư huynh, thanh y là Hồ Tiếu sư đệ, người sau mới nhập môn không đến hai năm, làm sao có thể là Mạt sư huynh đối thủ!”"),t("br"),t("br"),n._v("“Đúng vậy a, cái này hai mươi xem như tự nhiên kiếm được...” Khôi bào thanh niên cười hắc hắc, chính giác gặp được đến một cái thằng ngốc, một chút kiếm lời nhiều như vậy, ngay sau đó nhìn thấy trên đài liên tục bại lui thanh y, đi tới bên bàn, kiếm khí đột nhiên quét ngang."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Bạch y bị đâm trúng ngực, một cái xuyên qua."),t("br"),t("br"),n._v("Thanh y, chiến thắng!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1966"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1968"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);