(window.webpackJsonp=window.webpackJsonp||[]).push([[109],{461:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1075-phong-thanh-ta-co-the-thu-xem"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1075-phong-thanh-ta-co-the-thu-xem"}},[n._v("#")]),n._v(" Chương 1075: Phong Thánh, ta có thể thử xem")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1075: Phong Thánh, ta có thể thử xem"),t("br"),t("br"),n._v("“Đả kích... Tán loạn? Có ý tứ gì?”"),t("br"),t("br"),n._v("Kê Nham, Đàm Thanh tất cả đều sững sờ, nguyên một đám mặt mũi ngẩn người."),t("br"),t("br"),n._v("Đối phương nếu như nói, không tìm được cửa, không đi qua mang theo Khưu Ngô cung ba chữ bia đá, hoặc là chưa đi đến vào trong đó, bọn họ có thể lý giải, nhưng... Bị đả kích tán loạn..."),t("br"),t("br"),n._v("Làm sao cảm giác thật thâm ảo, hoàn toàn nghe không hiểu đâu?"),t("br"),t("br"),n._v("Có thể hay không làm phiền ngươi nói rõ ràng một chút?"),t("br"),t("br"),n._v("“Vẫn là ngươi nói đi...”"),t("br"),t("br"),n._v("Thấy hai người một mặt tò mò, Ngô sư hơi đỏ mặt, không biết làm sao mở miệng, đành phải quay đầu nhìn về phía Hàn hội trưởng."),t("br"),t("br"),n._v("Hắn không phải danh sư, từ hắn nói, hiệu quả sẽ rất nhiều."),t("br"),t("br"),n._v("“Tốt a, nhưng thật ra là như vậy...”"),t("br"),t("br"),n._v("Cười khổ một tiếng, Hàn hội trưởng chuẩn bị tìm từ, đem sự tình ngọn nguồn cẩn thận nói một lần."),t("br"),t("br"),n._v("“Ngươi nói... Trương sư nhìn ra cổ thánh chưởng pháp thiếu hụt? Không chỉ tu luyện thành công, còn thuận tiện sửa lại?”"),t("br"),t("br"),n._v("“Còn muốn thu cổ thánh... Làm đồ đệ?”"),t("br"),t("br"),n._v("Hai đại phó viện trưởng trợn mắt hốc mồm, suýt chút nữa không tại chỗ hộc máu ngất đi."),t("br"),t("br"),n._v("Đây là tại nghe chuyện xưa a?"),t("br"),t("br"),n._v("Trên thế giới còn có như thế kéo danh sư?"),t("br"),t("br"),n._v("Đây chính là Khưu Ngô cổ thánh... Phàm là nghe qua tên, đều sẽ xem như “Thần” đồng dạng phụng dưỡng, nhìn thấy không dám nói chuyện lớn tiếng, cái tên này ngược lại tốt... Mới mở miệng thu đồ... Ngươi là không thu qua đồ đệ, vẫn là lá gan mập đến cảnh giới nhất định?"),t("br"),t("br"),n._v("“Khụ khụ... Ta chỉ là luận sự, không nghĩ tới Khưu Ngô cổ thánh yếu ớt như vậy, như thế không chịu được nữa đả kích, ai, xem ra tâm cảnh cần cố gắng rèn luyện ah...”"),t("br"),t("br"),n._v("Thấy hai người ánh mắt nhìn tới, Trương Huyền cảm khái."),t("br"),t("br"),n._v("Xuất hiện loại tình huống này, hắn cũng không muốn."),t("br"),t("br"),n._v("Đây không phải không có cách nào nha..."),t("br"),t("br"),n._v("“Yếu ớt...”"),t("br"),t("br"),n._v("“Rèn luyện?”"),t("br"),t("br"),n._v("Hai người đồng thời im lặng."),t("br"),t("br"),n._v("Danh Sư đường nổi danh cổ thánh một trong, vạn thế chi sư tùy tùng... Ngươi một cái mấy vạn năm sau vãn bối, muốn thu người ta làm đồ đệ, đối phương không tức chết là tốt lắm rồi, còn muốn đánh như thế nào đánh?"),t("br"),t("br"),n._v("Đem người ta tự sáng tạo, vẫn lấy làm kiêu ngạo chưởng pháp tại chỗ sửa đổi, mấu chốt còn uy lực đại tăng... Đối phương có thể kiên trì nói dứt lời, liền thành tâm không tệ."),t("br"),t("br"),n._v("“Không có đạt được truyền thừa... Nhìn tới, chúng ta là thật không ra được...” Một lát sau, Kê Nham cười khổ lắc đầu."),t("br"),t("br"),n._v("Như bảo sơn mà tay không về, như thế nói nhảm danh sư, đến cùng làm sao lăn lộn thành học viện viện trưởng?"),t("br"),t("br"),n._v("Bọn họ thật có chút vì Hồng Viễn học viện tương lai lo lắng."),t("br"),t("br"),n._v("Bất quá, không được đến truyền thừa, liên quan tới không gian phong ấn, liền phá giải không được, ra không được, lo lắng cũng vô dụng."),t("br"),t("br"),n._v("Vốn đang chờ mong nhận được bọn họ tin tức người, có thể đem hắn cứu ra ngoài, hiện tại xem ra... Suy nghĩ nhiều."),t("br"),t("br"),n._v("“Phong Thánh các đến cùng là địa phương nào? Cùng Phong Thánh đài có liên hệ gì ư?”"),t("br"),t("br"),n._v("Không để ý tới mọi người thái độ, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("Phong Thánh các, Phong Thánh đài, tên như vậy gần, cũng đều cùng Khổng sư liên quan đến, muốn nói không liên hệ, hắn cũng không tin."),t("br"),t("br"),n._v("“Hai cái này quả thực có liên hệ, nếu như đoán không sai...”"),t("br"),t("br"),n._v("Thấy hắn hỏi thăm, Kê Nham đành phải trả lời, vừa mới mở miệng, mọi người liền cảm thấy mặt đất một hồi kịch liệt lay động, giống như động đất, dường như toàn bộ kiến trúc bất cứ lúc nào cũng sẽ sụp đổ."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Ngô sư đám người con ngươi co rụt lại."),t("br"),t("br"),n._v("“Thứ này mỗi ngày đều không quy luật, hôm nay bắt đầu... Đi, ta mang các ngươi đi gặp Chương Dẫn Khâu, có điều, trước thời hạn cảnh cáo một câu, chúng ta chỉ có một nén hương thời gian, nhất định phải trong đoạn thời gian này chạy về, nếu không, sẽ cùng trước đó đồng dạng, lại khó chạy trốn...”"),t("br"),t("br"),n._v("Kê Nham tựa hồ đối với loại này lay động tập mãi thành thói quen, dừng lại một chút, cắn răng nói."),t("br"),t("br"),n._v("“Đi gặp Chương Dẫn Khâu?” Tất cả mọi người sửng sốt một chút, lập tức nhẹ gật đầu: “Tốt!”"),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Không nói thêm lời, Kê Nham đi đầu đi ra ngoài, Đàm Thanh bởi vì không có tay chân, có nhiều chỗ không cách nào phi hành, hành động khá là phiền toái, lưu lại."),t("br"),t("br"),n._v("Vòng qua bên ngoài phòng rất nhiều trận pháp bẫy rập, mọi người một đường hướng lên, thời gian không dài đi tới cái kiến trúc trên cùng, lúc này mới phát hiện, cái này tương tự lầu các kiến trúc, nóc phòng lại là bình, ở giữa nhất là cái không lớn hình nón cụt, đang tản ra hào quang chói sáng."),t("br"),t("br"),n._v("Vừa rồi chấn động, chính là cái này hình nón cụt kích phát mà ra."),t("br"),t("br"),n._v("“Đây là...”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Thứ này cùng trước đó tại Phong Thánh đài, phá hư tâm đắc sau xuất hiện hình nón cụt, giống nhau như đúc."),t("br"),t("br"),n._v("Lúc trước chỉ ra Khổng sư tâm đắc sai lầm chỗ, bia đá nổ tung, hình nón cụt theo trong vách đá xuất hiện, đi tới phía trên, hắn cùng Lạc Nhược Hi trực tiếp bị truyền tống đến một cái không gian gấp bên trong."),t("br"),t("br"),n._v("Lúc đó, vẫn cho rằng cái kia hình nón cụt, liền là Khổng sư phong Thánh lúc ngồi, nhìn thấy cái này, đột nhiên phát giác vô cùng có khả năng không phải."),t("br"),t("br"),n._v("“Không sai, đây chính là lúc trước Khổng sư phong Thánh lúc, lưu lại Phong Thánh đài, phía ngoài cái kia, bất quá là cái phục chế phẩm thôi...”"),t("br"),n._v("Nhìn ra hắn nghi hoặc, Kê Nham nhẹ gật đầu."),t("br"),t("br"),n._v("“Chân chính Phong Thánh đài?” Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Đúng."),t("br"),t("br"),n._v("Trời nhận Thánh giả, cũng không phải người bình thường, phong Thánh chỗ, chịu đến thiên địa pháp tắc hun đúc, làm sao có thể chỉ có một ít vầng sáng? Lại thế nào khả năng giấu ở ngọn núi bên trên, không e ngại người khác tìm kiếm?"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển, hướng trước mắt Phong Thánh đài nhìn lại."),t("br"),t("br"),n._v("Chỉ thấy một cái đặc thù lồng ánh sáng đứng sừng sững trên đó, phía trên gợn sóng lưu chuyển, cho người ta một loại đặc thù cảm giác, dường như hấp thu một ngụm, liền có thể để tu vi tăng nhiều, trong cơ thể Thánh vực khí tức càng thêm nồng đậm."),t("br"),t("br"),n._v("“Thiên địa phong Thánh, dù là năng lượng tán dật một tia, đối với chúng ta những người tu luyện này tới nói, đều là một món tài sản khổng lồ, ta sở dĩ tốc độ tu luyện nhanh, chính là bởi vì cái này...” Kê Nham nhẹ gật đầu."),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("Hình nón cụt toả ra ánh sáng chiếu lên trên người ấm áp, cho người ta một loại như mộc thánh quang cảm giác, mặc dù mọi người vừa tới đến không lâu, liền rõ ràng cảm thấy tư duy tăng nhanh, bắp thịt toàn thân sục sôi, tu luyện cái gì, đều tựa hồ làm ít công to."),t("br"),t("br"),n._v("Ở loại địa phương này tiến hành tu luyện, chỉ cần thiên phú không tính quá kém, tuyệt đối tiến bộ cực nhanh, ngắn ngủi hai năm tấn thăng ba cái đại cấp bậc, cũng liền có thể thông cảm được."),t("br"),t("br"),n._v("Xem vẻ mặt của mọi người, biết bọn họ đã hiểu được, Kê Nham lại không giải thích, đi về phía trước mấy bước, một ngón tay Phong Thánh đài cách đó không xa: “Viện trưởng ngay tại cái này...”"),t("br"),t("br"),n._v("Mọi người sững sờ, vội vàng nhìn lại, liền thấy một cái tượng đá đứng sừng sững ở cách đó không xa."),t("br"),t("br"),n._v("Là cái lão giả, điêu khắc sinh động như thật, đang cùng học viên bên trong, Chương Dẫn Khâu chân dung giống nhau như đúc."),t("br"),t("br"),n._v("“Ngô sư? Ngươi đã đến...”"),t("br"),t("br"),n._v("Đang tại kỳ quái, liền nghe đến trong tượng đá một cái thanh âm nhàn nhạt vang lên, vội vàng nhìn lại, chỉ thấy tượng đá, thế mà cùng trước đó Khưu Ngô cổ thánh đồng dạng, mở miệng nói chuyện."),t("br"),t("br"),n._v("“Chương Dẫn Khâu, ngươi sao lại thế...”"),t("br"),t("br"),n._v("Trong lòng đau xót, Ngô sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("Khó trách Kê Nham nói, bọn họ chỉ có không được ba người, Chương Dẫn Khâu có thể nói chuyện, có thể nhận ra hắn, đủ để chứng minh còn có ý thức, không tính tử vong... Có thể loại này bộ dáng, đã cùng chết không có gì khác biệt."),t("br"),t("br"),n._v("“Viện trưởng vì cứu chúng ta, bị Thiên Diệp Vương hủy diệt thân thể, lúc ấy vừa vặn Phong Thánh đài vận chuyển, thánh quang chiếu xạ, hồn phách của hắn cũng không tiêu tán, bị bám vào một cái trên tảng đá, không cách nào rời khỏi, cũng không cách nào chạy trốn, có điều, còn có ý thức của mình, cũng không tính hoàn toàn tử vong... Liền thành hiện tại bộ dáng!”"),t("br"),t("br"),n._v("Kê Nham giải thích."),t("br"),t("br"),n._v("Lúc đó tình huống phức tạp, Chương Dẫn Khâu viện trưởng thân thể bị tại chỗ đánh bại, vốn cho rằng hẳn phải chết không nghi ngờ, nằm mộng cũng nghĩ không ra, Khổng sư lưu lại cái này Phong Thánh đài, có được lớn lao ma lực, thánh quang bao phủ xuống, hồn phách thế mà không có chết, bám vào tại một cái trên tảng đá còn sống."),t("br"),t("br"),n._v("Dần dà, lột xác thành tượng đá, có điều, không thể chạy trốn, cũng không có cách nào di chuyển, chỉ có thể an tĩnh đứng ở chỗ này, nói thật... Cùng chết không có gì khác biệt."),t("br"),t("br"),n._v("“Lão viện trưởng thuần phục Tử Dương thú, cũng không phải là linh hồn thuần phục, mà là máu tươi khế ước, thân thể tử vong, chủ tớ quan hệ đương nhiên cũng liền giải trừ... Khó trách thư viện lúc trước dò xét tên kia thời điểm, cũng không biết chủ nhân là ai...”"),t("br"),t("br"),n._v("Trương Huyền hiểu được."),t("br"),t("br"),n._v("Hắn thuần phục Thánh thú, linh thú, là linh hồn hiến tế, thú sủng hồn phách, hoàn toàn ở bản thân linh hồn trong khống chế, mà lão viện trưởng, rõ ràng không đi cái này con đường, chỉ là sử dụng tinh huyết, ký kết khế ước."),t("br"),t("br"),n._v("Loại khế ước này, mặc dù cũng có thể thuần phục Thánh thú, nhưng một khi thân thể tử vong, chủ tớ quan hệ sẽ tự động giải trừ."),t("br"),t("br"),n._v("Trước đó điều tra Tử Dương thú thời điểm, không biết chủ nhân là lão viện trưởng, lúc này mới náo động lên Ô Long... Còn tưởng rằng hắn thật đã chết rồi, nguyên lai là hiện tại loại tình huống này."),t("br"),t("br"),n._v("“Thật ra thì không có gì, như vậy cũng rất tốt, chí ít xem như sống...”"),t("br"),t("br"),n._v("Thấy mọi người đều có chút đau thương, nhất là Ngô sư vị lão hữu này, vẻ mặt trở nên trắng bệch, Chương Dẫn Khâu tượng đá cười nói."),t("br"),t("br"),n._v("Loại tình huống này, không phải hắn nghĩ tới, nhưng... Chí ít so hồn phi phách tán, người chết đèn tắt muốn tốt một chút."),t("br"),t("br"),n._v("“Các ngươi có thể đi vào, chắc hẳn đã thấy ta dấu vết lưu lại, nhận được Khưu Ngô cổ thánh truyền thừa a?”"),t("br"),t("br"),n._v("An ủi mọi người một câu, Chương Dẫn Khâu nhìn qua."),t("br"),t("br"),n._v("Chỉ cần nhận được truyền thừa, chẳng khác nào có thể trốn đi, cũng không cần một mực bị vây ở chỗ này."),t("br"),t("br"),n._v("Hắn mặc dù không cách nào rời khỏi, nhưng Kê Nham, Đàm Thanh hai vị phó viện trưởng, lại có thể thuận lợi chạy trốn, cũng coi như có thể bảo đảm danh sư học viện một chút thực lực."),t("br"),t("br"),n._v("“Không được đến truyền thừa...”"),t("br"),t("br"),n._v("Thấy viện trưởng hỏi thăm, lần này không cần Ngô sư trả lời, Kê Nham đem chuyện đã xảy ra, vội vàng nói một lần."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nghe xong giải thích, Chương Dẫn Khâu trợn mắt lên, giống như là gặp quỷ đồng dạng, nhìn Trương Huyền một cái, muốn nói cái gì, cuối cùng đành phải ngừng lại, tràn đầy bất đắc dĩ: “Không có đạt được cổ thánh truyền thừa, muốn rời khỏi, gần như là không thể nào... Trừ phi, có thể cùng Khổng sư đồng dạng, được thiên địa công nhận, bị trực tiếp phong Thánh!”"),t("br"),t("br"),n._v("“Phong Thánh?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Ừm!” Chương Dẫn Khâu nói tiếp: “Cái này Phong Thánh các, nắm giữ chân chính Phong Thánh đài, còn có thành Thánh rất nhiều tâm đắc cùng giới thiệu, mục đích chỉ có một cái... Là Khổng sư lưu cho hậu nhân, hi vọng hậu nhân cũng có thể được ông trời công nhận! Chỉ cần có người có thể tại đây cái Phong Thánh đài bên trên đột phá, trở thành trời nhận Thánh giả, ngăn tại bốn phía phong ấn, đồng dạng sẽ biến mất!”"),t("br"),t("br"),n._v("Nghe được hắn nói ra có thể rời đi điều kiện, Ngô sư đám người cười khổ: “Đó căn bản không có khả năng hoàn thành...”"),t("br"),t("br"),n._v("“Đúng vậy a, quả thực là không có khả năng...”"),t("br"),t("br"),n._v("Từ trước tới nay, cũng chỉ có Khổng sư một người hoàn thành qua, như vậy có thể thấy được độ khó, muốn làm được, làm sao có thể?"),t("br"),t("br"),n._v("Liền mấy người bọn hắn quân ô hợp?"),t("br"),t("br"),n._v("“Cho nên... Chúng ta chưa hề nghĩ đến có thể ra ngoài, tới đây trước kia, liền giao phó xong hậu sự, bây giờ còn có thể kéo dài hơi tàn, cũng xem là không tệ!”"),t("br"),t("br"),n._v("Kê Nham nói."),t("br"),t("br"),n._v("“Thật ra thì...”"),t("br"),t("br"),n._v("Thấy mọi người tất cả đều khí phách tiêu điều, Trương Huyền do dự một chút, nhìn lại."),t("br"),t("br"),n._v("“Phong Thánh... Ta có thể thử xem!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1083"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1085"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);