(window.webpackJsonp=window.webpackJsonp||[]).push([[1093],{1448:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1957-xin-goi-ta-cao-tien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1957-xin-goi-ta-cao-tien"}},[n._v("#")]),n._v(" Chương 1957: Xin gọi ta Cao Tiến")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nhất Kiếm Tiêu Tương, tại nội môn tất cả kiếm pháp bên trong, được cho đỉnh tiêm, đặc biệt là chín kiếm hợp nhất, triệt để thi triển đi ra, cùng cấp bậc tuyệt đối hàng tiến lên ba."),t("br"),t("br"),n._v("Chu Ngôn Chi cứ việc chỉ tu luyện tám kiếm, vẫn như trước thập phần cường đại, nhưng đối phương... Dường như biết hắn kiếm này nội tình, vừa động thủ xông vào không cách nào thi triển ra kiếm thứ chín khe hở!"),t("br"),t("br"),n._v("Bởi vì không có luyện thành, thành kiếm chiêu sơ hở lớn nhất!"),t("br"),t("br"),n._v("Một chiêu bắt đầu, tìm đến nơi này, để hắn muốn thương tổn người đều không làm được... Làm sao làm được?"),t("br"),t("br"),n._v("Coi như nội môn xếp hàng thứ nhất vị kia, cũng không cách nào hoàn thành đi!"),t("br"),t("br"),n._v("Khắp khuôn mặt là sốt ruột, trường kiếm chuyển một cái, muốn biến thành mặt khác tuyệt chiêu, đem đối phương chém xuống, chỉ thấy thanh niên trường kiếm trong tay, đột nhiên nâng lên, hướng hắn mi tâm đâm tới."),t("br"),t("br"),n._v("Một chiêu này, không chỉ có thể phá giải hắn Nhất Kiếm Tiêu Tương, còn có thể nhẹ nhõm chặt đứt hắn biến chiêu, để hắn không có bất kỳ cái gì phản kháng đường sống."),t("br"),t("br"),n._v("Nói cách khác, kiếm này thật rơi xuống, không cần biến chiêu, càng không cần tiếp tục thi triển, tuyệt đối sẽ bị một cái xuyên qua mi tâm, bị mất mạng tại chỗ!"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thân thể băng lãnh, Chu Ngôn Chi một mặt sợ hãi."),t("br"),t("br"),n._v("Còn tưởng rằng, vừa ra tay thi triển ra tuyệt chiêu, nhất định có thể làm cho đối phương để lộ nội tình, không nghĩ tới, liền nửa chiêu đều không chịu đựng nổi, liền bị chém giết!"),t("br"),t("br"),n._v("Loại thực lực này bên dưới, hai người bọn họ thật có thể tra ra đối phương xác thực thân phận?"),t("br"),t("br"),n._v("Không tra được... Hưng sư động chúng chạy tới tỷ thí, chẳng phải lại muốn thua một hồi?"),t("br"),t("br"),n._v("Tràn đầy hối hận, đang nghĩ ngợi, không nên nghe người khác, tiếp tục đánh cược, chỉ thấy trước mắt bất cứ lúc nào cũng sẽ đâm chết hắn một kiếm, đột nhiên ngừng lại."),t("br"),t("br"),n._v("Nhìn lại, chỉ thấy vị này “Ta Rất Biết Điều”, khắp khuôn mặt là rầu rĩ."),t("br"),t("br"),n._v("Chu Ngôn Chi trở về từ cõi chết, vội vàng lui về sau một bước, tránh đi sát chiêu, lần nữa chuyển một cái, lại một cái tuyệt chiêu thi triển đi ra."),t("br"),t("br"),n._v("Nhất Kiếm Tiêu Tương, cứ việc lợi hại, nhưng bị người phá giải, liền không uy hiếp nữa, lần này thi triển ra chiêu số, uy lực bên trên không bằng trước người, nhưng thắng ở một cái “Nhanh” chữ."),t("br"),t("br"),n._v("Tật Phong Khoái Kiếm!"),t("br"),t("br"),n._v("Một chiêu so một chiêu nhanh, liên tục mười chiêu sau đó, gió táp mưa rào, căn bản không đỡ được."),t("br"),t("br"),n._v("Chiêu này, Cổ Thánh tứ trọng cường giả thi triển đi ra, thích hợp nhất, Cổ Thánh nhất trọng chân khí, thân thể thi triển, áp lực rất lớn, nhưng tình cảnh nguy cấp, chỉ là dùng đi ra."),t("br"),t("br"),n._v("Ô ô ô!"),t("br"),t("br"),n._v("Khoái kiếm bí mật mang theo gió táp, hóa thành từng đạo kiếm ảnh, phá không mà đến, Chu Ngôn Chi vốn cho rằng, chiêu này thi triển, coi như cực kỳ, đối phương cũng sẽ chật vật không chịu nổi, ngay sau đó phát hiện... Đối phương không có bất kỳ cái gì dư thừa động tác, lần nữa tiến về phía trước một bước, lại một kiếm ép xuống."),t("br"),t("br"),n._v("Đại xảo bất công, vừa vặn đánh vào khoái kiếm yếu kém khớp xương, đồng dạng chỉ cần thân kiếm rơi xuống, hắn tất nhiên sẽ bị một cái chém thành hai khúc, tại chỗ bỏ mình."),t("br"),t("br"),n._v("Nói cách khác, khoái kiếm cứ việc thi triển đi ra, lần nữa bị đối phương một chiêu phá giải!"),t("br"),t("br"),n._v("Chu Ngôn Chi sắp điên rồi!"),t("br"),t("br"),n._v("Ngay tại tiếp tục tính toán nhắm mắt đợi chết thời khắc, chỉ thấy “Ta Rất Biết Điều” lần nữa một mặt rầu rĩ, trường kiếm lần nữa ngừng lại."),t("br"),t("br"),n._v("Vội vàng lăn một vòng, tránh thoát một kiếp, miệng lớn thở hổn hển, Chu Ngôn Chi lại thi triển ra một cái tuyệt chiêu."),t("br"),t("br"),n._v("Tại Lăng Vân kiếm các nhiều năm như vậy, lợi hại kiếm chiêu, học được không biết bao nhiêu bộ, mỗi một chiêu đều có cực lớn uy lực."),t("br"),t("br"),n._v("Cùng vừa rồi đồng dạng, mới thi triển ra một thức, đối phương lại giống là phá giải đồng dạng, trường kiếm ngay sau đó theo tới, chỉ cần rơi xuống, liền có thể giết người, có thể... Chính là mai một đi."),t("br"),t("br"),n._v("Liên tục đổi bảy bộ thân pháp, mười hai sáo kiếm thuật, mỗi lần đều là như vậy, Chu Ngôn Chi đều sắp phát điên."),t("br"),t("br"),n._v("Không chỉ hắn như vậy, phía dưới nhìn tỷ thí người cũng từng cái nhìn nhau, tất cả đều trợn mắt hốc mồm."),t("br"),t("br"),n._v("“Rõ ràng một chiêu liền có thể thắng được... Vì sao mỗi lần thi triển đến một nửa, dừng lại?”"),t("br"),t("br"),n._v("“Không biết... Chỉ cần thi triển đi ra, nhất định có thể thắng ah!”"),t("br"),t("br"),n._v("“Vốn ta tưởng là, thi triển ra nửa chiêu, liền bị ‘Chuẩn xác’ áp chế không cách nào nhúc nhích, hiện tại xem ra, căn bản không phải lý do này!”"),t("br"),t("br"),n._v("“ ‘Chuẩn xác’ bị đánh không có sức đánh trả chút nào, hắn là cố ý dừng lại, nhưng... Thực sự không nghĩ ra, vì sao muốn ngừng...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lại nhìn một hồi, tất cả mọi người đều có chút mờ mịt."),t("br"),t("br"),n._v("Trên đài “Ta Rất Biết Điều”, rõ ràng một chiêu, liền có thể chiến thắng, nhưng lại mỗi lần đều dừng lại, liền giống như rõ ràng kích thích điểm cao nhất, chỉ cần tiếp tục, liền có thể nghiêng trời lệch đất, lại một mực... Muốn nghỉ ngơi."),t("br"),t("br"),n._v("Một lần có thể nói là nhịn không được, nhiều lần đều như vậy, cũng có chút cố ý!"),t("br"),t("br"),n._v("“Ngươi rốt cuộc muốn làm gì...”"),t("br"),t("br"),n._v("Người phía dưới choáng váng, trên đài Chu Ngôn Chi không chịu nổi, một lần quay người sau đó, cũng nhịn không được nữa, nhảy ra ngoài."),t("br"),t("br"),n._v("Tỷ thí nhiều tràng như vậy, còn là lần đầu tiên nghĩ như vậy bị người một kiếm chém chết!"),t("br"),t("br"),n._v("“Không phải đang cùng ngươi tỷ thí ư?”"),t("br"),t("br"),n._v("Gặp hắn dừng tay, Trương Huyền ngừng lại."),t("br"),t("br"),n._v("“Tỷ thí... Rõ ràng có thể một kiếm đánh chết ta, tại sao phải dừng lại...”"),t("br"),t("br"),n._v("Răng cắn chặt, Chu Ngôn Chi nói."),t("br"),t("br"),n._v("Dù sao tất cả mọi người đã nhìn ra, cũng không có gì có thể giấu diếm."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền gãi đầu một cái: “Xem ta tên, ngươi hẳn phải biết, một kiếm liền đem ngươi giết... Không phù hợp tính cách? Ta không muốn dẫn tới oanh động!”"),t("br"),t("br"),n._v("“Không muốn dẫn tới oanh động?”"),t("br"),t("br"),n._v("Chu Ngôn Chi thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Ngươi quản cái đồ chơi này gọi khiêm tốn?"),t("br"),t("br"),n._v("Tốn cái rắm ah!"),t("br"),t("br"),n._v("Mỗi một chiêu, đều tìm đến sơ hở, chính là không chém... Đây không phải khiêm tốn, mà là cố ý tới trang bức, khoe khoang ngươi kiếm pháp cao minh a!"),t("br"),t("br"),n._v("Còn không muốn dẫn tới oanh động?"),t("br"),t("br"),n._v("Ngươi đây là cho rằng oanh động còn chưa đủ đi..."),t("br"),t("br"),n._v("Người phía dưới, mỗi cái miệng đều cùng nhét vào trứng gà giống như... So một kiếm giết ta còn muốn để cho người ta oanh động có được hay không?"),t("br"),t("br"),n._v("“Ta nhận thua...”"),t("br"),t("br"),n._v("Biết tiếp tục làm hạ thấp đi, sẽ bị tươi sống tức chết, cắn răng một cái Chu Ngôn Chi xoay người rời đi."),t("br"),t("br"),n._v("Không phải là không muốn so, mà là thực sự chịu không được áp lực này!"),t("br"),t("br"),n._v("Cùng đối phương tỷ thí, trong lòng một mực có gan cảm giác... Đó chính là, nhanh đánh chết ta đi, đánh chết ta đi..."),t("br"),t("br"),n._v("“Ai, ta là thật không muốn quá lộ liễu...”"),t("br"),t("br"),n._v("Gặp hắn không tin, Trương Huyền bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Thật ra thì làm cho đối phương nhiều thi triển mấy chiêu, chỉ là một nguyên nhân... Đều thua nhiều tiền như vậy, một cái đánh chết... Thực sự xấu hổ!"),t("br"),t("br"),n._v("Bất kể nói thế nào, hắn đều là cái người có lễ phép."),t("br"),t("br"),n._v("Tiền kiếm lời, tự nhiên cũng phải để cho người ta nhiều thi triển mấy chiêu, ý tứ một cái."),t("br"),t("br"),n._v("“Chuẩn xác” nhận thua, cái thứ hai khôi bào thanh niên “Nhập Dạ” đi tới."),t("br"),t("br"),n._v("“Ngươi rất mạnh...”"),t("br"),t("br"),n._v("Cầm trong tay trường kiếm, “Nhập Dạ” đứng tại tỷ thí trên đài, híp mắt lại."),t("br"),t("br"),n._v("Vốn nghĩ, hai người bọn họ thay nhau công kích, đối phương mạnh hơn, cũng khẳng định sẽ thi triển ra tuyệt chiêu, từ đó khó mà che giấu thân phận, nằm mơ cũng không ngờ tới, Chu Ngôn Chi chiến đấu một hồi, đối phương một điểm tổn thương đều không có nhận, thậm chí liền nguy hiểm đều không có tạo thành!"),t("br"),t("br"),n._v("Như vậy trải qua, áp lực toàn bộ tập trung ở trên người hắn."),t("br"),n._v("“Ta hi vọng, ngươi không cần lưu thủ, dùng công kích mạnh nhất, đem ta đánh bại!”"),t("br"),t("br"),n._v("Lông mày giương lên, “Nhập Dạ” nói."),t("br"),t("br"),n._v("“Dùng công kích mạnh nhất?” Thấy đối phương đưa ra yêu cầu này, Trương Huyền do dự."),t("br"),t("br"),n._v("“Không tệ!” “Nhập Dạ” gật đầu."),t("br"),t("br"),n._v("Chỉ cần thi triển ra công kích mạnh nhất, dù là hắn thua, cũng có thể suy đoán ra thân phận, không tính chịu thiệt."),t("br"),t("br"),n._v("“Cái này... Tốt a!”"),t("br"),t("br"),n._v("Thấy đối phương ánh mắt chân thành, Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Hít sâu một hơi, “Nhập Dạ” toàn thân vận chuyển chân khí, trường kiếm lay động, vừa định động thủ, liền cảm thấy hoa mắt, một thanh kiếm đã đi tới cái cổ phía trước, sắc bén kiếm mang, chèo làn da, dường như chỉ cần khẽ động, liền sẽ bị tại chỗ chém giết."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("“Nhập Dạ” một mặt ngỡ ngàng."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Đại ca, ngươi lúc nào thì tới?"),t("br"),t("br"),n._v("Ngươi họ gì?"),t("br"),t("br"),n._v("Dùng cái chiêu số gì?"),t("br"),t("br"),n._v("Ta vì sao lại bị kiếm gác ở trên cổ?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Vốn cho rằng đối phương sẽ thi triển ra tuyệt chiêu, để hắn nhìn ra manh mối, kết quả... Nhân gia đều đưa kiếm đặt ở trên cổ, bản thân thậm chí ngay cả dùng cái chiêu số gì đều không rõ ràng..."),t("br"),t("br"),n._v("Có muốn hay không nhanh như vậy?"),t("br"),t("br"),n._v("“Ngươi thua...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Vốn còn nghĩ, chiến đấu mấy chiêu, làm cho đối phương chiến thắng, đã có loại yêu cầu này, cũng cái gọi là, dù sao bọn họ trả tiền, chỉ cần cho sảng khoái, yêu cầu gì đều có thể tiếp nhận!"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("“Nhập Dạ” muốn tự tử đều có."),t("br"),t("br"),n._v("Kiếm đều dựng đến trên cổ, không thừa nhận cũng không được, khẳng định là thua."),t("br"),t("br"),n._v("Hai trận tỷ thí xong, một điểm uy hiếp đều không có đưa đến, chớ nói chi là nhìn ra cụ thể kiếm chiêu!"),t("br"),t("br"),n._v("Bất quá, cứ việc cuối cùng không có xác định là ai, nhưng cũng xác định phạm vi."),t("br"),t("br"),n._v("Có thể có loại thực lực này, nội môn tuyệt không vượt qua năm cái!"),t("br"),t("br"),n._v("Có thể... Có loại thực lực này người, nhận tông môn bồi dưỡng, tài nguyên không thiếu, kiếm các tệ, muốn kiếm lấy, cũng hết sức dễ dàng, chắc chắn sẽ không vì một điểm nhỏ tiền, mà tham gia đánh bạc, dù sao, có quý báu tương lai, bởi vì điểm ấy lợi nhỏ hủy diệt mà nói, được không bù mất..."),t("br"),t("br"),n._v("Trước mắt vị này, nhưng giống như là tham tiền đồng dạng, một phân tiền đều không buông tha..."),t("br"),t("br"),n._v("Cùng mấy vị kia, hoàn toàn treo không mắc câu!"),t("br"),t("br"),n._v("Nghĩ như thế nào, đều cho rằng có chút choáng váng."),t("br"),t("br"),n._v("“Các ngươi thua, cho ta 720 kiếm các tệ!”"),t("br"),t("br"),n._v("Rời đi tỷ thí đài, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Hai người trước mắt chân thực thân phận, đi qua tỷ thí, toàn bộ biết, không sợ bọn họ không trả tiền."),t("br"),t("br"),n._v("“Cho...”"),t("br"),t("br"),n._v("Ăn trộm gà bất thành còn mất nắm gạo, Chu Ngôn Chi cùng Nhập Dạ, vô cùng đau đớn, nhưng cũng hết cách rồi, đành phải đụng đụng, đem tiền trả nợ."),t("br"),t("br"),n._v("“Chào đón lần sau tiếp tục cùng ta đánh cược...”"),t("br"),t("br"),n._v("Thấy đi tới không đến hai mươi phút, kiếm lời hơn 1400 kiếm các tệ, Trương Huyền thoả mãn, hết sức cao hứng nhìn trước mắt hai vị thần tài."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhìn thấy đối phương chân thành ánh mắt, Chu Ngôn Chi cùng Nhập Dạ, đồng thời cho rằng trái tim đau nhức."),t("br"),t("br"),n._v("Chúng ta không phải đưa tiền, mà là muốn thắng tiền..."),t("br"),t("br"),n._v("Làm sao cảm giác trong mắt hắn, cùng “Đại thằng ngốc” đồng dạng..."),t("br"),t("br"),n._v("Suy nghĩ kỹ một chút, ngắn ngủi khoảng hai mươi phút, liền đưa ra ngoài nhiều năm như vậy tích góp... Còn giống như thật sự là “Đại thằng ngốc”, hơn nữa còn là vô cùng ngốc cái chủng loại kia..."),t("br"),t("br"),n._v("“Có thể hay không nói cho ta biết... Tên thật của ngươi?”"),t("br"),t("br"),n._v("Gặp hắn thắng tiền muốn rời khỏi, cũng nhịn không được nữa, Chu Ngôn Chi mở miệng hỏi."),t("br"),t("br"),n._v("“Ta? Tên thật?”"),t("br"),t("br"),n._v("Liếc mắt nhìn, thấy bọn họ hai người ánh mắt mong chờ, Trương Huyền đem đầu tóc mở ra, dùng nước thấm ướt, chải đến sau lưng, hai tay chắp sau lưng, để lại cho đối phương một cái bóng lưng: “Xin gọi ta... Cao Tiến!”"),t("br"),t("br"),n._v("Nói xong không tiếp tục để ý một mặt mộng bức hai người, tinh thần khẽ động, rời đi Thông Thần điện."),t("br"),t("br"),n._v("Lần này tới, mục đích là vì kiếm tiền, tiền bạc bây giờ có 1440 kiếm các tệ khoản tiền lớn, hẳn là đủ tốn một đoạn thời gian, không cần thiết tiếp tục ở lại."),t("br"),t("br"),n._v("“Cao Tiến?”"),t("br"),t("br"),n._v("“Có nội môn đệ tử gọi Cao Tiến? Đi thăm dò...”"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, hai người vội vàng đi thăm dò, một lát sau, từng cái nghiến răng nghiến lợi..."),t("br"),t("br"),n._v("Cái tên này, đang gạt bọn họ!"),t("br"),t("br"),n._v("“Lần sau không để ngươi, đem ăn của chúng ta phun ra, ta liền không gọi Chu Ngôn Chi...”"),t("br"),t("br"),n._v("Càng nghĩ càng giận, Chu Ngôn Chi nghiến răng."),t("br"),t("br"),n._v("“Hắn thực lực mạnh như vậy, chúng ta có thể làm sao?”"),t("br"),t("br"),n._v("Nhập Dạ cũng khí sắp bùng nổ, nhưng không có biện pháp gì."),t("br"),t("br"),n._v("“Rất đơn giản, hắn rất lợi hại, nội môn cũng chưa chắc có thể xếp bên trên đầu tiên... Đã như vậy, chúng ta có thể đi tìm mấy vị kia... Liền nói cho bọn hắn, Thông Thần điện xuất hiện một vị cao thủ tuyệt thế, để cho bọn họ động thủ! Sau đó... Chúng ta đặt cược!” Chu Ngôn Chi mắt sáng lên."),t("br"),t("br"),n._v("“Đúng a!”"),t("br"),t("br"),n._v("Nhập Dạ ánh mắt sáng lên: “Cứ làm như thế!”"),t("br"),t("br"),n._v("Vị này “Ta Rất Biết Điều” tuy là lợi hại, nhưng cũng chưa hẳn có thể tại nội môn hàng bên trên đầu tiên... Đã như vậy, sao không để mấy vị kia yêu nghiệt tới?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền lần nữa về đến phòng, suy tư phút chốc, lần nữa đi ra cửa phòng."),t("br"),t("br"),n._v("Tu vi tấn cấp phá toái hư không rất lâu, là thời điểm nên xung kích cảnh giới cao hơn!"),t("br"),t("br"),n._v("Bất quá... Muốn xung kích cảnh giới cao hơn, cần đủ công pháp, tại Thông Thần điện mua sắm, giá cả không ít, còn không bằng đến kiếm các tàng thư khố nhìn!"),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới để cho Thiện Hiểu Thiên trở thành Lăng Vân trong Kiếm các cửa đệ tử."),t("br"),t("br"),n._v("Ra khỏi phòng, còn không có rời đi sân nhỏ, liền nghe đến cửa chính “Kẹt kẹt!” Một tiếng mở ra, Lục Vân trưởng lão tràn đầy hưng phấn đi vào."),t("br"),t("br"),n._v("Lúc này Lục Vân trưởng lão, một thân khí tức mơ hồ, cao thâm khó dò, mấy canh giờ không thấy, vậy mà đã đột phá Hư Tiên cảnh, đạt đến Chân Tiên cảnh giới!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1969"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1971"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);