(window.webpackJsonp=window.webpackJsonp||[]).push([[1095],{1449:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1959-thuan-phuc-ho-lo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1959-thuan-phuc-ho-lo"}},[n._v("#")]),n._v(" Chương 1959: Thuần phục hồ lô")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Đừng động!”"),t("br"),t("br"),n._v("Thấy hồ lô bất cứ lúc nào cũng sẽ xông lại, đem đan dược nuốt vào, Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Bị cái này hai hàng, giật đồ sợ."),t("br"),t("br"),n._v("Chỉ cần có đồ tốt, so chó cái mũi đều linh, thoáng cái liền xuất hiện... Một cái hai trăm kiếm các tệ mới mua đan dược, bị ngươi nuốt, ta còn thế nào đột phá tu vi?"),t("br"),t("br"),n._v("“Cho ta ăn...”"),t("br"),t("br"),n._v("Hồ lô vặn vẹo uốn éo: “Ta không phải nói cho ngươi sao? Hồ lô chỉ là ta biểu tượng, ta bản thân, là ngang dọc thiên địa, quát tháo phong vân tuyệt đỉnh Thần thú, chỉ cần khôi phục lực lượng, có thể mang ngươi rong ruổi thiên địa, không người có thể chống lại... Cho ta ăn, so ngươi tăng thêm một chút tu vi phải tốt hơn nhiều...”"),t("br"),t("br"),n._v("“Cút sang một bên!”"),t("br"),t("br"),n._v("Thấy cái tên này như vậy phí miệng, trên trán gân xanh nhảy loạn, Trương Huyền một chân đem hắn đá văng, một cái liền đem lòng bàn tay đan dược nuốt xuống."),t("br"),t("br"),n._v("Muốn gạt ta thuốc uống, nghĩ hay lắm!"),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),t("br"),n._v("Đan dược vào cổ họng tức dung, một cỗ lực lượng hùng hồn, lan ra toàn thân, trước đó trì trệ không tiến lực lượng, lần nữa có tiến bộ."),t("br"),t("br"),n._v("Phá toái hư không hậu kỳ!"),t("br"),t("br"),n._v("Phá toái hư không đỉnh phong!"),t("br"),t("br"),n._v("Nuốt vào hai cái trung phẩm cấp bậc Tiên Nguyên đan, tu vi liền đạt đến Cổ Thánh tứ trọng đỉnh phong nhất, khoảng cách Hư Tiên cảnh, chỉ có kém một bước."),t("br"),t("br"),n._v("Cảm nhận được trong cơ thể tràn đầy lực lượng, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Loại thực lực này, tại Lăng Vân kiếm các tuy là không tính là cái gì, nhưng trong nội môn đệ tử, tuyệt đối ít có đỉnh tiêm."),t("br"),t("br"),n._v("Còn thừa lại ba cái Tiên Nguyên đan, cho phân thân dùng hai cái, để hắn thuận lợi đột phá, lại cho Ngoan Nhân một cái, hai người đều thuận lợi tấn cấp đến phá toái hư không đỉnh phong."),t("br"),t("br"),n._v("Hai cái này, đều là lá bài tẩy của hắn, gặp được nguy hiểm, tương đương với thực lực thoáng cái bạo tăng gấp hai, không thể coi nhẹ."),t("br"),t("br"),n._v("“Ta đây...”"),t("br"),t("br"),n._v("Thấy năm cái đan dược rất nhanh chia xong, hồ lô tràn đầy u oán."),t("br"),t("br"),n._v("“Chỉ có biết ăn, không có tác dụng gì, đan dược không còn...” Trương Huyền xua tay."),t("br"),t("br"),n._v("“Ngươi uống qua ta nước tắm!” Hồ lô nói."),t("br"),t("br"),n._v("“Ta không cần phế vật, ngươi cả ngày trừ ăn ra, đã làm gì sự tình ư? Gặp được nguy hiểm, cũng không ra hỗ trợ, vì sao phải cho ngươi đan dược?” Trương Huyền tức giận nói."),t("br"),t("br"),n._v("“Ngươi uống qua ta nước tắm!” Hồ lô nói."),t("br"),t("br"),n._v("“Ngươi mỗi ngày trốn ở đan điền ta, vụng trộm hấp thu ta Thiên đạo chân khí, đừng cho là ta không biết! Quả thực chính là ký sinh trùng, làm sao, vẫn để ý thẳng khí tráng? Không có đem ngươi đuổi đi ra là tốt lắm rồi...” Trương Huyền nghiến răng."),t("br"),t("br"),n._v("“Ngươi uống qua ta nước tắm!” Hồ lô nói."),t("br"),t("br"),n._v("“...” Trương Huyền run rẩy: “Ta có thể không đề cập tới nước tắm sự tình ư?”"),t("br"),t("br"),n._v("“Ta nước tắm, giúp ngươi chữa khỏi tổn thương! Ngươi còn bán cho người khác, kiếm tiền!” Hồ lô tiếp tục nói."),t("br"),t("br"),n._v("Trương Huyền nhức đầu: “Tốt a, ngươi nói ngươi là ngang dọc thiên địa Thần thú, như vậy đi, chỉ cần ngươi nhận chủ, về sau nghe lời của ta, hiện tại liền cho ngươi mua sắm trung phẩm Tiên Nguyên đan, thậm chí tốt hơn dược vật! Giúp ngươi mau chóng khôi phục!”"),t("br"),t("br"),n._v("“Nhận chủ?” Hồ lô vặn vẹo."),t("br"),t("br"),n._v("“Không sai, thiên hạ không có uổng phí tới bữa trưa, chỉ cần ngươi trở thành ta thú sủng, chân khí của ta, tuỳ hấp thu, ta cũng sẽ cố gắng kiếm tiền, cho ngươi đủ đan dược, để ngươi mau chóng khôi phục!” Trương Huyền nói."),t("br"),t("br"),n._v("Cái hồ lô này, tuy là không biết cấp bậc gì, cũng không biết cụ thể là cái gì, nhưng nấu thoáng cái canh, liền có thể chữa trị thương thế, hiệu quả so Thiên đạo chân khí đều lợi hại, cấp bậc khẳng định không thấp!"),t("br"),t("br"),n._v("Coi như không giống nó nói như vậy, là ngang dọc thiên địa Thần thú, hẳn là cũng sẽ không quá kém!"),t("br"),t("br"),n._v("Nếu không, cũng không có khả năng, cướp đoạt đồ vật, giấu vào đan điền, liền hắn hiện tại, đều không thể ra sức."),t("br"),t("br"),n._v("Bởi vậy, gay gắt như vậy đồ vật, trước thu làm sủng vật, ký kết khế ước lại nói."),t("br"),t("br"),n._v("Nếu không, một khi thật muốn khôi phục, khẳng định lại không nhận khống chế."),t("br"),t("br"),n._v("“Ta là ngang dọc thiên địa, quát tháo phong vân tuyệt đỉnh Thần thú, ngươi không đối ta hạ bái, là tốt lắm rồi, lại còn muốn phục tùng ta? Quả thực nằm mơ...”"),t("br"),t("br"),n._v("Hồ lô hừ một tiếng, lời còn chưa dứt, chỉ thấy Trương Huyền trầm tư phút chốc, bàn tay khẽ động, một cái trung phẩm Tiên Nguyên đan cầm tại lòng bàn tay."),t("br"),t("br"),n._v("“Hừ, ta là ngang dọc thiên địa, quát tháo phong vân tuyệt đỉnh Thần thú, ngươi cho rằng sẽ vì một cái đan dược khuất phục?”"),t("br"),t("br"),n._v("Nước bọt chảy ròng, hồ lô cố gắng khắc chế."),t("br"),t("br"),n._v("Không nói thêm gì nữa, Trương Huyền cổ tay khẽ đảo, lòng bàn tay lại thêm ra một cái trung phẩm Tiên Nguyên đan."),t("br"),t("br"),n._v("Còn thừa lại bốn trăm kiếm các tệ, lại mua hai cái."),t("br"),t("br"),n._v("“Hai cái đan dược cũng không được, ít nhất... Ít nhất... Ít nhất cũng phải hai mươi cái, không một trăm cái đan dược mới được!” Hồ lô ngạo kiều đi lòng vòng thân."),t("br"),t("br"),n._v("“Một trăm cái? Tốt a, ngươi chơi ngươi a, ta nghỉ ngơi trước một hồi...”"),t("br"),t("br"),n._v("Lông mày nhảy dựng, Trương Huyền chẳng muốn tiếp tục nói nhảm, lật bàn tay một cái lần nữa đem đan dược thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("Một cái trung phẩm Tiên Nguyên đan, giá cả hai trăm kiếm các tệ, một trăm cái, muốn chỉnh cả hai vạn... Coi như đem hắn mệt chết tại tỷ thí đài, cũng không kiếm được ah!"),t("br"),t("br"),n._v("Đã như vậy, quên đi."),t("br"),t("br"),n._v("“Cái này, thật ra thì, đan dược sự tình dễ nói...”"),t("br"),t("br"),n._v("Không ngừng chảy nước bọt, hồ lô vội vàng xoay người, âm thanh lại không còn vừa rồi kiên định, ngược lại mang theo lấy lòng: “Nếu không như vậy, ngươi trước tiên đem cái này hai cái cho ta... Lại cho ta làm cái mười cái tám cái, ta liền cân nhắc, nhận ngươi làm chủ nhân...”"),t("br"),t("br"),n._v("Trước mắt vị này tu vi, đối với nó tới nói, tuy là không cao, nhưng chân khí trong cơ thể, cho nó một loại thoải mái dễ chịu cảm giác, lại thêm mơ hồ mang theo một cỗ siêu thoát tự nhiên lực lượng, nói thật nhận chủ, cũng là có thể..."),t("br"),t("br"),n._v("Chỉ là, đối phương nói chuyện liền nhận, chẳng phải lộ ra nó hồ lô, không có giá trị bản thân?"),t("br"),t("br"),n._v("“Cho!”"),t("br"),t("br"),n._v("Thấy nó nhả ra, Trương Huyền không nói thêm lời, cong ngón tay búng một cái, hai cái đan dược xuất hiện lần nữa tại đầu ngón tay, thẳng tắp bay đi."),t("br"),t("br"),n._v("Hô!"),t("br"),n._v("Nuốt vào cái bụng, hồ lô đánh cái nấc, tràn đầy vẫn chưa thỏa mãn: “Dược vật này mặc dù không tệ, cấp bậc vẫn là thấp, với ta mà nói, càng cao càng tốt...”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy loạn."),t("br"),t("br"),n._v("Hắn nuốt lấy hai cái, tu vi nhẹ nhõm từ phá toái hư không hậu kỳ đi đến đỉnh phong, cái tên này, thế mà một chút hình dáng đều không có thay đổi... Cũng quá có thể ăn đi!"),t("br"),t("br"),n._v("Chẳng lẽ, thật muốn cho nó làm ra một trăm viên, hoặc là cấp bậc cao hơn?"),t("br"),t("br"),n._v("“Có thể nhẹ nhõm nuốt vào trung phẩm Tiên Nguyên đan, một chút biến hóa không có, cái tên này cấp bậc khẳng định so với trong tưởng tượng cao... Trước thuần phục lại nói!”"),t("br"),t("br"),n._v("Biết cái tên này, cùng trước kia thuần phục linh thú, Thánh thú khác biệt, đánh thuần thú pháp, căn bản không có tác dụng, Trương Huyền dừng lại một chút, để cái tên này lần nữa trở lại đan điền, bản thân thì tinh thần khẽ động, lần nữa trở lại Thông Thần điện."),t("br"),t("br"),n._v("Vốn cho rằng vừa đánh cược kiếm lời hơn một ngàn bốn trăm kiếm các tệ, đủ tốn một hồi, trải qua sung túc sinh sống, nằm mơ đều không có nghĩ đến, mới ra ngoài không đến hai canh giờ, lần nữa sạch sẽ trơn tru."),t("br"),t("br"),n._v("Tính toán đâu ra đấy, chỉ có 32 viên kiếm các tệ, một phần năm đan dược đều mua không lên."),t("br"),t("br"),n._v("“Vẫn là muốn kiếm tiền ah...”"),t("br"),t("br"),n._v("Bất đắc dĩ xoa xoa mi tâm, lần nữa hướng Thông Thần điện tỷ võ phương hướng đi tới."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nội môn đệ tử vị trí sơn phong đỉnh cao nhất."),t("br"),t("br"),n._v("Một tòa đứng sừng sững ở bên vách núi trong sân, một thanh niên khoanh chân ngồi dưới đất, hai mắt nhắm nghiền."),t("br"),t("br"),n._v("Trên bầu trời hùng hậu linh khí, tại chung quanh hắn chậm chạp hội tụ, hình thành một vòng xoáy khổng lồ."),t("br"),t("br"),n._v("Toàn thân huyệt đạo mở ra, thôn phệ lấy những linh khí này, tựa như hồ nước hội tụ dòng suối, không biết qua bao lâu, bỗng nhiên há mồm ra."),t("br"),t("br"),n._v("Ô!"),t("br"),t("br"),n._v("Một đạo khí kiếm thẳng tắp bắn ra ngoài, rơi vào cách đó không xa nham thạch bên trên, đánh ra một vết nứt."),t("br"),t("br"),n._v("Nếu như nhìn kỹ, cỗ này kiếm khí, chính là vừa rồi nuốt những cái kia linh khí, không kém chút nào."),t("br"),t("br"),n._v("Hắn hấp thu linh khí, cũng không ngưng tụ thành chân khí, mà là đi qua mặt khác phương thức phun ra."),t("br"),t("br"),n._v("Làm xong những này, từ từ mở mắt, ngay sau đó nhìn thấy lại một thanh niên đi tới, mang trên mặt nụ cười thản nhiên."),t("br"),t("br"),n._v("“Làm sao? Vẫn là không đột phá? Ngươi muốn đem tu vi áp chế tới khi nào?”"),t("br"),t("br"),n._v("“Ta muốn tiếp tục rèn luyện cùng rèn luyện kiếm ý, còn đột phá, sau này hãy nói!” Cái thứ nhất thanh niên đứng dậy."),t("br"),t("br"),n._v("“Ngươi cái này đều rèn luyện trọn ba năm đi!” Cái thứ hai thanh niên nói."),t("br"),t("br"),n._v("“Ba năm vẫn là quá ít, kiếm các đã từng xuất hiện vị kia Thập Lý Kiếm Thần, đột phá Hư Tiên cảnh phía trước, tôi luyện kiếm ý, trọn mười năm! Lúc này mới một kiếm xuất khiếu long trời lở đất! Ta tuy là không đuổi kịp thiên phú của hắn, cũng muốn đi càng xa một ít!”"),t("br"),t("br"),n._v("Cái thứ nhất thanh niên, lên tiếng, lúc này mới nhìn về phía thanh niên trước mắt, hỏi: “Làm sao có thời gian tới tìm ta? Có muốn hay không tỷ thí một trận?”"),t("br"),t("br"),n._v("“Không thể so sánh, dù sao cũng không sánh bằng... Vừa rồi Chu Ngôn Chi cùng Ngụy Tùy Phong tới tìm ta! Nói một việc, ta cảm thấy rất thú vị, ngươi có muốn hay không cũng nghe thoáng cái?”"),t("br"),t("br"),n._v("Cái thứ hai thanh niên mỉm cười."),t("br"),t("br"),n._v("“Hai người bọn họ, từ trước tới nay đầu cơ trục lợi, không muốn cố gắng tu luyện, có thể có chuyện gì? Ta còn muốn tu luyện, đã không thấy tăm hơi...”"),t("br"),t("br"),n._v("Cái thứ nhất thanh niên lắc đầu, tựa hồ đối với lấy hai người, cũng không rất ưa thích."),t("br"),t("br"),n._v("“Đừng có gấp từ chối, nghe ta nói hết lời...” Dường như sớm biết hắn sẽ có cái phản ứng này, cái thứ hai thanh niên, nói: “Bọn họ nói, tại nội môn đệ tử Thông Thần điện, gặp một vị cao thủ sử dụng kiếm, hai người bọn họ xa luân chiến, thậm chí ngay cả đối phương tuyệt chiêu đều không có bức đi ra!”"),t("br"),t("br"),n._v("“Xa luân chiến, không có ép ra đối phương tuyệt chiêu?”"),t("br"),t("br"),n._v("Sửng sốt một chút, cái thứ nhất thanh niên nhướng mày: “Chu Ngôn Chi cùng Ngụy Tùy Phong, cứ việc tiểu tâm tư không ít, nhưng nhập môn vô cùng sớm, thực lực chân chính cũng đều đạt đến phá toái hư không đỉnh phong, đối kiếm thuật lý giải, tại nội môn, cũng xem là tốt... Hai người xa luân chiến, cùng cấp bậc bên dưới, đối phương liền tuyệt chiêu đều không có thi triển liền đem hắn đánh bại? Cái này... Không thể nào!”"),t("br"),t("br"),n._v("“Ta xem, xác thực như vậy, bọn họ còn mang theo hình ảnh, ngươi có muốn hay không nhìn một chút?”"),t("br"),t("br"),n._v("Gặp hắn bắt đầu tò mò, cái thứ hai thanh niên nói."),t("br"),t("br"),n._v("“Để bọn hắn vào đi!”"),t("br"),t("br"),n._v("Không nói thêm lời, cái thứ nhất thanh niên khoát tay áo."),t("br"),t("br"),n._v("“Ừm!” Cái thứ hai thanh niên lên tiếng, thời gian qua một lát, hai bóng người đi vào."),t("br"),t("br"),n._v("Chính là trước đó Trương Huyền tại Thông Thần điện tới giao thủ Chu Ngôn Chi cùng “Nhập Dạ”."),t("br"),t("br"),n._v("“Nhập Dạ” chỉ là ngoại hiệu, tên thật Ngụy Tùy Phong."),t("br"),t("br"),n._v("“Gặp qua Lưu sư huynh, Lưu sư huynh!”"),t("br"),t("br"),n._v("Đi tới trước mặt, hai người hành lễ."),t("br"),t("br"),n._v("Trước mắt hai vị, chính là trong nội môn đệ tử, xếp hàng thứ nhất Lưu Lộ Kiệt, cùng bài danh thứ ba vương kiệm đông!"),t("br"),t("br"),n._v("“Ngươi nói Thông Thần điện xuất hiện một vị cao thủ?”"),t("br"),t("br"),n._v("Vị thứ nhất thanh niên, Lưu Lộ Kiệt vẻ mặt lạnh nhạt nhìn lại."),t("br"),t("br"),n._v("“Vâng!” Chu Ngôn Chi gật gật đầu, cổ tay khẽ đảo lấy ra một cái ghi chép ngọc tinh, đưa tới: “Đây là chúng ta tới giao chiến hình ảnh, còn xin sư huynh xem qua!”"),t("br"),t("br"),n._v("Ngẩng đầu nhìn vương kiệm đông liếc mắt, gặp hắn không nói gì, Lưu Lộ Kiệt ngón tay một chút, một đạo chân khí tiến vào bên trong."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Hình ảnh kích hoạt, hai bên giao chiến hình ảnh xuất hiện tại phía trước."),t("br"),t("br"),n._v("“Liếc mắt liền nhìn thấu ngươi tuyệt chiêu bên trong thiếu hụt... Có tài khống chế?”"),t("br"),t("br"),n._v("Con mắt rơi vào phía trên, một lát sau, Lưu Lộ Kiệt lông mày đột nhiên nâng lên."),t("br"),t("br"),n._v("(Kẹt văn, đến muộn, xấu hổ!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1971"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1973"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);