(window.webpackJsonp=window.webpackJsonp||[]).push([[1097],{1452:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1961-khieu-chien-mot-đam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1961-khieu-chien-mot-đam"}},[n._v("#")]),n._v(" Chương 1961: Khiêu chiến một đám")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Vâng!” Vương Kiệm Đông gật gật đầu, lấy ra trên người thẻ, hướng đi trước đài."),t("br"),t("br"),n._v("Lúc này, tất cả mọi người ở đây đều bị đối phương chọc giận, cũng từng cái đem đeo trên người kiếm các tệ, thế chấp tại trước đài, làm như vậy, coi như bị giết, cũng có thể hoàn thành cá cược."),t("br"),t("br"),n._v("“Cái tên này là bản thân tự tìm cái chết...”"),t("br"),t("br"),n._v("Thấy cảnh này, Chu Ngôn Chi, Ngụy Tùy Phong hưng phấn mà sắp nổ tung."),t("br"),t("br"),n._v("Vốn tìm tới Lưu Lộ Kiệt cùng Vương Kiệm Đông dụng ý rất đơn giản, là muốn mượn tay của bọn hắn dạy dỗ cái tên này một hồi, để hắn hiển lộ thân phận, sau đó đem thua trận tiền thắng trở về, không nghĩ tới cái tên này tự mình tìm đường chết... Một người muốn khiêu chiến tất cả nội môn đệ tử!"),t("br"),t("br"),n._v("Quả thực điên rồi!"),t("br"),t("br"),n._v("Thực lực của người này, là không tệ, có thể song quyền khó chống đỡ bốn tay, huống chi nơi này có hơn một ngàn tay..."),t("br"),t("br"),n._v("“Lần này cái tên này, coi như không thi triển tuyệt chiêu, cũng khẳng định không phải nhiều người như vậy đối thủ, nhận tông môn trừng phạt là nhất định...”"),t("br"),t("br"),n._v("Trong lòng hai người đồng thời thăng bằng không ít."),t("br"),t("br"),n._v("Tiền bị đối phương thắng đi, vốn cảm thấy rất tức giận, bây giờ đối phương thảm trạng có thể đoán được, lại không còn trước đó phẫn nộ."),t("br"),t("br"),n._v("“Cái tên này, tuy là não tàn, lá gan xác thực đủ lớn...”"),t("br"),t("br"),n._v("“Đúng vậy a, một người khiêu chiến nhiều như vậy, đổi thành ta tuyệt đối không dám!”"),t("br"),t("br"),n._v("“Lần này cho dù chết, đoán chừng cũng muốn danh chấn cả tòa kiếm các...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người tuy là tức giận, nhưng cũng có không ít khâm phục, vị này “Ta Rất Biết Điều” dũng khí."),t("br"),t("br"),n._v("Chí ít, làm như vậy chết hành động, toàn bộ Lăng Vân kiếm các, đã nhiều năm không có xuất hiện qua."),t("br"),t("br"),n._v("“Ai tới trước!”"),t("br"),t("br"),n._v("Không để ý tới mọi người khen chê không đồng nhất, Trương Huyền con mắt sáng lên, tràn đầy kích động nhảy lên tỷ thí đài, nhìn quanh một vòng."),t("br"),t("br"),n._v("Sớm biết, khiêu chiến tất cả mọi người, chỉ cần chiến thắng, có thể đem tất cả mọi người ví tiền đều cướp sạch, đã sớm động thủ, cũng không đến mức rầu rĩ cái này hơn mười phút!"),t("br"),t("br"),n._v("“Thật là phách lối... Ta nhịn không được, ta trước giết chết hắn!”"),t("br"),t("br"),n._v("Một vị nội môn đệ tử cũng nhịn không được nữa, tung người nhảy đi lên."),t("br"),t("br"),n._v("“Tại hạ Ngô Phong, lần trước nội môn đại bỉ, xếp hạng thứ một trăm năm mươi bảy, tu vi không cao, nhưng tuyệt không tiếp nhận làm nhục!”"),t("br"),t("br"),n._v("Vị này nội môn đệ tử, hét dài một tiếng, trường kiếm trong tay, múa ra kiếm hoa, nhìn về phía ngay phía trước “Ta Rất Biết Điều”, ánh mắt lộ ra nồng đậm sát ý."),t("br"),t("br"),n._v("Đối phương dám nói như vậy, thực lực khẳng định không đơn giản, hắn đoán chừng không phải là đối thủ, nhưng... Vậy thì sao? Chết thì chết mà thôi, tuyệt không tiếp nhận làm nhục!"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Một cái nháy mắt, đâm ra mười tám kiếm, một kiếm so một kiếm nhanh!"),t("br"),t("br"),n._v("Võ kỹ, Sơn Lộ Thập Bát Loan!"),t("br"),t("br"),n._v("Là hắn mạnh nhất võ kỹ, dùng chiêu này, chém giết qua không ít cùng cấp bậc kẻ địch."),t("br"),t("br"),n._v("Lúc này thi triển đi ra, xe nhẹ đường quen, kiếm khí gào thét."),t("br"),t("br"),n._v("“Xuất kiếm đi!” Đem kiếm khí bảo vệ toàn thân, Ngô Phong cao giọng rống to."),t("br"),t("br"),n._v("“Xuất kiếm?” Đứng tại chỗ, Trương Huyền ngượng ngùng gãi đầu một cái: “Ta đã ra ah...”"),t("br"),t("br"),n._v("“Ra?”"),t("br"),t("br"),n._v("Ngô Phong sững sờ, lúc này mới cảm thấy cổ họng một hồi đau đớn kịch liệt, vừa định cúi đầu, máu tươi phun tới, “Lạch cạch!” Một tiếng, đầu rơi trên mặt đất."),t("br"),t("br"),n._v("“Cái này... Điều đó không có khả năng!”"),t("br"),t("br"),n._v("Hóa thành đầy trời điểm sáng, biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Hắn thi triển ra toàn lực một kiếm, đối phương chẳng những không có ngăn cản, còn đem đầu hắn cắt xuống... Làm sao làm được?"),t("br"),t("br"),n._v("Không riêng gì hắn, dưới đài một hồi yên lặng."),t("br"),t("br"),n._v("Trước đó, tất cả mọi người cho rằng cái tên này, quá mức ngạo mạn, hiện tại xem ra, quả nhiên có ngạo mạn thực lực."),t("br"),t("br"),n._v("Chí ít, trước đó một kiếm kia, đến cùng làm sao ra tay, lại như thế nào cắt đứt Ngô Phong cái cổ, không có người nhìn ra."),t("br"),t("br"),n._v("“Thật lợi hại!”"),t("br"),t("br"),n._v("“Lợi hại thì có ích lợi gì? Chúng ta xa luân chiến, không tin chân khí của hắn, có thể dùng lâu như vậy!”"),t("br"),t("br"),n._v("“Đúng vậy a, mọi người không cần sợ, cùng lắm thì tổn thất một cái thông thần ngọc phù mà thôi!”"),t("br"),t("br"),n._v("“Trước cùng hắn đối chiến, ta không tin hắn có thể kiên trì mấy cái!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, trong đám người vang lên gào thét, lại có người nhảy lên đài đi."),t("br"),t("br"),n._v("Nếu như là thật bị giết, có lẽ còn có người sẽ do dự, Thông Thần điện phía trong, nhiều nhất tổn thất cái thông thần ngọc phù, chỉ cần có thể đem cái tên này giết chết, xa luân chiến cũng muốn đem hắn chân khí tiêu hao hầu như không còn!"),t("br"),t("br"),n._v("“Tại hạ...”"),t("br"),t("br"),n._v("Nhảy lên đài nội môn đệ tử, dài bằng bàn tay kiếm nâng lên, vừa định nói hai câu lời hung ác, cổ họng một hồi ngứa, ngay sau đó thấy được bản thân gót chân..."),t("br"),t("br"),n._v("Đầu đồng dạng rơi mất."),t("br"),t("br"),n._v("“Các ngươi đi lên nhanh một chút a, như vậy ta cũng có thể tiết kiệm chút thời gian, nếu không, từng cái nhảy lên, nhiều người như vậy, muốn giết tới lúc nào?”"),t("br"),t("br"),n._v("Chém giết đối phương, Trương Huyền nhíu nhíu mày, nhìn lại."),t("br"),t("br"),n._v("Dưới đài chừng hơn tám trăm người, nghe được tin tức phía sau, lại có không ít người chạy tới, từng cái nhảy lên mà nói, giết tới ngày mai cũng giết không hết ah, còn không bằng, để cho bọn họ cùng đi."),t("br"),t("br"),n._v("“Tự tìm cái chết...”"),t("br"),t("br"),n._v("Phía dưới mọi người, tức giận đến thật sắp nổ."),t("br"),t("br"),n._v("Từng cái bên trên, tuy là khôi phục thời gian rất ngắn, nhưng ít ra còn có khôi phục chân khí khả năng, rất nhiều người cùng tiến lên mà nói, cũng không phải là bánh xe, mà là vây công!"),t("br"),t("br"),n._v("“Chúng ta cùng một chỗ!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Nhảy lên năm cái nội môn đệ tử."),t("br"),t("br"),n._v("Cái này năm cái, tu vi đều không yếu, hầu như đều xếp tại đại bỉ 300 người đứng đầu phía trong."),t("br"),t("br"),n._v("“Yên tâm, chúng ta sẽ không thi triển hợp kích chiêu số... Sẽ từng cái cùng ngươi đối chiến!”"),t("br"),t("br"),n._v("Cầm đầu hừ lạnh."),t("br"),t("br"),n._v("Nhiều người như vậy vây công, liền rất mất thân phận, nếu là đang sử dụng hợp kích chiến pháp, thực sự không vứt được người này."),t("br"),t("br"),n._v("“Không quan trọng, nhanh lên một chút liền tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, dài bằng bàn tay kiếm giơ lên, nhẹ nhàng run rẩy, từ năm người trong đám người đi tới: “Tốt, đám tiếp theo...”"),t("br"),t("br"),n._v("Phù phù! Phù phù! Phù phù! Phù phù! Phù phù!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, năm người đầu đồng thời rơi trên mặt đất, thân thể hóa thành điểm sáng biến mất."),t("br"),t("br"),n._v("Mọi người biết xa luân chiến, vây công sẽ đại lượng tiêu hao chân khí, Trương Huyền tự nhiên cũng hiểu, đổi lại hắn bản tôn, tu luyện Thiên đạo công pháp, lại trải qua phong Thánh, chân khí vô cùng vô tận, không cần lo lắng, nhưng cái này Thông Thần điện ban cho thân thể, liền không có mạnh như vậy!"),t("br"),t("br"),n._v("Cho nên, vừa ra tay liền trực tiếp giết chết, không cho chiêu thứ hai cơ hội!"),t("br"),n._v(" Có thể tiết kiệm không ít thể lực."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("“Cái tên này kiếm rất nhanh, đừng tìm hắn nói nhảm, đi lên trực tiếp thi triển mạnh nhất kiếm chiêu!”"),t("br"),t("br"),n._v("“Không sai, mọi người không nên lưu tình, đem lợi hại nhất công kích thi triển đi ra, coi như giết không chết hắn, cũng muốn bảo đảm có thể để cho hắn tiêu hao nhiều hơn khí lực...”"),t("br"),t("br"),n._v("Không nghĩ tới năm người còn chưa kịp vây công, liền bị cắt đứt đầu, phía dưới lần nữa xôn xao, lại có năm người vọt lên."),t("br"),t("br"),n._v("Mọi người giờ phút này đều biết vị này thực lực, quả thực rất đáng sợ."),t("br"),t("br"),n._v("Nhưng mà, vẫn như cũ không tin, dựa vào hắn lực lượng một người, có thể đem nhiều người như vậy toàn bộ chém giết!"),t("br"),t("br"),n._v("Coong coong coong coong ông!"),t("br"),t("br"),n._v("Lần này năm người vừa lên đài, không có một câu nói nhảm, từng người kiếm khí ngang dọc, đem mạnh nhất kiếm chiêu, phát huy ra."),t("br"),t("br"),n._v("Cứ việc không có có thể thi triển hợp kích trận pháp, nhưng người người đều là cao thủ, vừa ra tay, năm đạo kiếm khí vẫn như cũ liên hợp cùng một chỗ, đem Trương Huyền chung quanh, tất cả có thể hành động khu ở giữa, toàn bộ phong tỏa."),t("br"),t("br"),n._v("Nhìn thấy mấy người này so vừa rồi muốn thông minh, cười nhạt một tiếng, Trương Huyền thân thể run rẩy, lập tức hướng kiếm võng bên trong chui vào."),t("br"),t("br"),n._v("Hắn khẽ động, kiếm võng lập tức theo chi vũ động."),t("br"),t("br"),n._v("Liên tục lung lay hai cái."),t("br"),t("br"),n._v("Phốc! Phốc!"),t("br"),t("br"),n._v("Không thấy hắn động kiếm, liền có hai người đầu rơi trên mặt đất, lần nữa lui về phía sau một bước, lại có một người bị tại chỗ chém giết."),t("br"),t("br"),n._v("Còn thừa lại hai cái liền đơn giản nhiều, trường kiếm ra tay, không có qua một cái hô hấp, đồng dạng biến mất tại tỷ thí đài."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Dưới đài Lưu Lộ Kiệt thấy cảnh này, thân thể run rẩy, đồng tử gầy yếu: “Hắn là đem năm người liên hợp, xem như một thể thống nhất, mượn tay của người khác, chém giết đối phương!”"),t("br"),t("br"),n._v("“Làm như vậy, có thể tiết kiệm không ít chân khí, đem tiêu hao giảm bớt thấp nhất!”"),t("br"),t("br"),n._v("Vương Kiệm Đông gật đầu, con mắt cũng trừng tựa như đèn lồng, quái lạ đồng dạng biểu lộ."),t("br"),t("br"),n._v("Mấy người vây công, mượn đối phương mất khống chế kiếm khí, chém giết người khác, mượn lực đả lực... Nghe rất đơn giản, trên thực tế làm thực sự quá khó khăn."),t("br"),t("br"),n._v("Thứ nhất, cần đối năm người chiêu số, tìm hiểu cực sâu, biết bọn họ mỗi một cái chiêu hướng đi, cùng bước tiếp theo động tác! Như vậy, mới có thể xông vào bầy kiếm, mà bảo đảm bản thân không bị thương!"),t("br"),t("br"),n._v("Thứ hai, biết mỗi người tuyệt chiêu, cùng với như thế nào mới có thể ép hắn thi triển... Như vậy mới có thể để cho mượn kiếm của đối phương chiêu, giết chết người của đối phương!"),t("br"),t("br"),n._v("Thứ ba, đối chiêu số tấn công phương hướng, khống chế đến lông tóc không sai, tâm cảnh muốn vô cùng kiên định, nếu không, hơi có chút hoảng loạn, đừng nói đem mọi người chém giết, khả năng bản thân sẽ bị tại chỗ giết chết!"),t("br"),t("br"),n._v("Cái này ba điểm, thiếu một thứ cũng không được, thực lực của hắn, tuy là có thể đồng dạng thắng được năm người, có thể một màn này, tuyệt đối không làm được!"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, nhìn về phía bên người Lưu Lộ Kiệt, muốn biết vị này nội môn đệ nhất nhân, có thể hay không hoàn thành."),t("br"),t("br"),n._v("“Ta cũng làm không được... Hắn chém giết năm người này, chỉ dùng nhất đạo chân khí, liền hoàn thành, ta ít nhất cần năm đạo...” Lưu Lộ Kiệt lắc đầu."),t("br"),t("br"),n._v("Hắn cũng có thể xông vào trong năm người mượn lực lượng của đối phương, đem hắn chém giết, nhưng... Tuyệt đối không làm được đối phương như thế, cử trọng nhược khinh, thành thạo!"),t("br"),t("br"),n._v("Thậm chí... Làm như vậy, tiêu hao chân khí càng lớn!"),t("br"),t("br"),n._v("“Bất quá, chỉ cần tìm ra hắn mạnh nhất tuyệt chiêu cùng át chủ bài, ta dùng lực lượng mạnh nhất đối với hắn công kích, hẳn là có thể liền đem nó giết chết!”"),t("br"),t("br"),n._v("Dừng lại một hồi, Lưu Lộ Kiệt nói."),t("br"),t("br"),n._v("Đối phương chiêu số cứ việc rất nhanh cùng quỷ dị, nhưng hắn cũng có thể làm được, chỉ là, thân thể này chân khí số lượng có hạn, nhất định phải tốc chiến tốc thắng mới được."),t("br"),t("br"),n._v("Chỉ cần nhìn ra hắn am hiểu nhất chiêu số là cái gì, sớm làm ra đề phòng, dựa vào thực lực của hắn, chiến thắng không khó lắm!"),t("br"),t("br"),n._v("“Mọi người cẩn thận, tuyệt đối không nên thi triển hợp kích trận pháp, cũng không được liên thủ công kích, nếu không sẽ bị hắn mượn lực lượng...”"),t("br"),t("br"),n._v("Hắn cùng Vương Kiệm Đông nhìn ra Trương Huyền mượn chiêu giết người, những người khác cũng đã nhìn ra, đồng thời kinh hô."),t("br"),t("br"),n._v("Người một nhà giết người một nhà, còn thế nào tiêu hao chân khí của hắn, cứ thế mà đem hắn mài chết?"),t("br"),t("br"),n._v("“Làm sao bây giờ?”"),t("br"),t("br"),n._v("“Không thể cùng một chỗ công kích, từng cái đi lên, chết thảm hại hơn...”"),t("br"),t("br"),n._v("Trong nháy mắt, đại điện một hồi yên tĩnh, không có người tiếp tục xông đi lên."),t("br"),t("br"),n._v("Từng cái đi qua không được, một đống người đi qua, không phối hợp không được, phối hợp càng không được... Bộ này không có cách nào đánh ah!"),t("br"),t("br"),n._v("Đánh như thế nào, tại sao thua!"),t("br"),t("br"),n._v("“Ta tới đi...”"),t("br"),t("br"),n._v("Gặp không có người động đậy, lại nhìn Lưu Lộ Kiệt liếc mắt, Vương Kiệm Đông thân thể kéo dài, nhấc chân hướng trên đài đi tới."),t("br"),t("br"),n._v("Còn chưa đi xa, đè thấp âm thanh truyền tới: “Ta dùng hết toàn lực, nghĩ biện pháp ép hắn thi triển ra tuyệt chiêu, ngươi xem thật kỹ thoáng cái, nghĩ ra phá giải phương pháp, sau đó... Một đòn giết chết!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết mục đích của hắn, Lưu Lộ Kiệt gật gật đầu."),t("br"),t("br"),n._v("Trước đó xông tới thực lực cũng không tính là quá cao, nội môn đệ tử xếp hạng năm mươi vị trí đầu, một cái đều không có."),t("br"),t("br"),n._v("Vương Kiệm Đông bài danh thứ ba, một khi đi lên, nhất định có thể ép ra lai lịch của đối phương, để hắn nhìn ra vài thứ."),t("br"),t("br"),n._v("Như vậy hắn lại đến đi, liền có thể có càng lớn nắm chắc, đem đánh bại!"),t("br"),t("br"),n._v("“Tại hạ Vương Kiệm Đông, để cho ta mở mang kiến thức một chút, các hạ tuyệt chiêu!”"),t("br"),t("br"),n._v("Đi tới trên đài, Vương Kiệm Đông lại không còn trước đó bình thường hình dáng, chân khí trong cơ thể gào thét, trong nháy mắt, khí tức ngút trời, tựa như Chiến Thần."),t("br"),t("br"),n._v("Trường kiếm trong tay phát ra nghẹn ngào thanh âm, dường như bị hắn trong cơ thể kiếm ý lây nhiễm, tựa như gào thét cự long."),t("br"),t("br"),n._v("“Vương Kiệm Đông sư huynh?”"),t("br"),t("br"),n._v("“Hắn vậy mà cũng tới, quá tốt rồi...”"),t("br"),t("br"),n._v("“Nội môn đệ tử xếp hạng thứ ba, kiếm pháp hơn người, hắn ra tay, cái tên này khẳng định sẽ bị chém giết!”"),t("br"),t("br"),n._v("“Coi như không chết, cũng sẽ hao tổn nhiều chân khí...”"),t("br"),t("br"),n._v("Nghe được tên, tất cả mọi người đều trở nên hưng phấn."),t("br"),t("br"),n._v("Lưu Lộ Kiệt cùng Vương Kiệm Đông, cũng dùng đều là ngoại hiệu, không báo tính mạng, những người khác nhận không ra."),t("br"),t("br"),n._v("“Vương Kiệm Đông? Thứ ba?”"),t("br"),t("br"),n._v("Nghe được thảo luận, Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Không sai, động thủ đi...”"),t("br"),t("br"),n._v("Trường kiếm trong tay lắc một cái, Vương Kiệm Đông kiếm khí gào thét."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền tiến về phía trước một bước."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Vương Kiệm Đông đầu rơi trên mặt đất."),t("br"),t("br"),n._v("Bốn phía, lặng ngắt như tờ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1973"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1975"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);