(window.webpackJsonp=window.webpackJsonp||[]).push([[1099],{1454:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1963-bach-nguyen-khanh-toi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1963-bach-nguyen-khanh-toi"}},[n._v("#")]),n._v(" Chương 1963: Bạch Nguyễn Khanh tới")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nội môn đỉnh núi sân nhỏ."),t("br"),t("br"),n._v("Vương Kiệm Đông mở mắt ra, trong đầu từng cơn chóng mặt."),t("br"),t("br"),n._v("“Bị người chém xuống đầu cảm giác, thật không tốt lắm...”"),t("br"),t("br"),n._v("Điều tức thoáng cái, khôi phục lại, đầy mặt cười khổ."),t("br"),t("br"),n._v("Nội môn chiến đấu, từ trước tới nay đều là hắn đem người khác chém giết, lớn như vậy, còn lần đầu tiên bị người chém đứt đầu, tuy là tại Thông Thần điện phía trong, sẽ không tổn thương tính mạng, nhưng này một khắc, trên tâm lý mang tới áp lực, đến bây giờ đều có chút hoảng sợ."),t("br"),t("br"),n._v("“Mặc dù tổn thất một khối thông thần ngọc phù, Lộ Kiệt sư huynh hẳn là nhìn ra một vài vấn đề, có cơ hội đem chiến thắng đi!”"),t("br"),t("br"),n._v("Rất nhanh, khôi phục tự tin."),t("br"),t("br"),n._v("Đối với vị này nội môn đệ nhất Lưu Lộ Kiệt sư huynh, hắn từ nội tâm chỗ sâu khâm phục."),t("br"),t("br"),n._v("Những năm gần đây, bất luận hắn học được bao nhiêu lợi hại chiêu số, thi triển ra cường đại cỡ nào công kích, ở trước mặt đối phương, đều cùng hài đồng đồng dạng, không có nổi chút tác dụng nào."),t("br"),t("br"),n._v("Nói cách khác..."),t("br"),t("br"),n._v("Thực lực của đối phương, sâu không lường được!"),t("br"),t("br"),n._v("Liền cùng ngươi thi 99 điểm, học bá thi 100 điểm đồng dạng, cả hai chênh lệch, cũng không phải là chỉ có một điểm, mà là... Chỉ có một trăm điểm, học bá chân chính thực lực, xa không chỉ tại đây."),t("br"),t("br"),n._v("Mạnh như thế người, sớm nhìn đối phương chiêu số, chém giết còn không nhẹ nhàng nới lỏng chút?"),t("br"),t("br"),n._v("Đông đông đông!"),t("br"),t("br"),n._v("Ngay tại cảm xúc, sân nhỏ bên ngoài vang lên tiếng đập cửa."),t("br"),t("br"),n._v("“Lúc này ai tới?”"),t("br"),t("br"),n._v("Lộ ra một tia nghi ngờ, Vương Kiệm Đông đứng dậy, đi ra ngoài."),t("br"),t("br"),n._v("Đây là Lưu Lộ Kiệt sư huynh sân trong, dựa theo tình huống bình thường, hắn không nên vượt quá chức phận, làm thay việc của người khác, nhưng bây giờ sư huynh đang chìm ngâm ở Thông Thần điện, chém giết vị kia ngạo mạn “Ta Rất Biết Điều”, chỉ có thể hắn đi mở cửa."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Sân nhỏ mở ra, mấy cái nội môn đệ tử, cúi đầu run rẩy nhìn qua: “Lưu sư huynh bớt giận, chúng ta không phải cố ý quấy rầy...”"),t("br"),t("br"),n._v("Tiếng nói còn không có kết thúc, nhìn rõ ràng trước mắt vị này hình dáng, xấu hổ cười một tiếng: “Nguyên lai là Vương sư huynh, không biết Lưu sư huynh có đó không?”"),t("br"),t("br"),n._v("“Chuyện gì?”"),t("br"),t("br"),n._v("Vương Kiệm Đông cau mày."),t("br"),t("br"),n._v("Mấy người này, tại nội môn bên trong, thực lực liền trước một ngàn đều không xếp hạng tới, tuy là biết, nhưng lại chưa bao giờ tiếp xúc qua, lúc này chạy tới, tìm Lưu sư huynh muốn làm gì?"),t("br"),t("br"),n._v("“Lưu sư huynh là nội môn đệ nhất nhân, lực ảnh hưởng lớn, chúng ta muốn phiền phức hắn một việc...” Hơi đỏ mặt, lời mới vừa nói nội môn đệ tử, bận rộn lo lắng mở miệng."),t("br"),t("br"),n._v("“Không rảnh!”"),t("br"),t("br"),n._v("Không nghĩ tới lại có yêu cầu này, Vương Kiệm Đông sầm mặt lại, liền muốn đóng cửa."),t("br"),t("br"),n._v("Nội môn thứ nhất, lực ảnh hưởng là rất lớn, nhưng mà phổ thông đệ tử đi tìm tới thì giúp một tay mà nói, còn có cái gì uy tín?"),t("br"),t("br"),n._v("Cái này đầu, tuyệt đối không thể mở."),t("br"),t("br"),n._v("“Không rảnh? Thật là lớn khí thế, nội môn đệ tử, hiện tại cũng lợi hại như vậy ư?”"),t("br"),t("br"),n._v("Cửa còn không đóng bên trên, một thanh âm vang lên, Vương Kiệm Đông vội vàng ngẩng đầu, ngay sau đó nhìn thấy một cái vóc người nóng bỏng nữ tử đi tới, mang trên mặt không vui."),t("br"),t("br"),n._v("“Bạch sư tỷ...”"),t("br"),t("br"),n._v("Nhìn rõ ràng người này dung mạo, Vương Kiệm Đông lông mày nhảy loạn, vội vàng ôm quyền hành lễ."),t("br"),t("br"),n._v("Xuất hiện ở trước mắt không phải người khác, chính là trong trung tâm tên tuổi lẫy lừng nữ bá vương, Bạch Nguyễn Khanh!"),t("br"),t("br"),n._v("Hạch tâm đệ tử bên trong, nàng thực lực mặc dù không phải cao nhất, thiên phú cũng không phải mạnh nhất, nhưng nàng có cái lợi hại ông nội, cũng chính là tông môn là tam đại trưởng lão một trong Bạch Diệp trưởng lão!"),t("br"),t("br"),n._v("Cứ việc Bạch trưởng lão gần nhất bị thương, địa vị ở nơi đó bày biện, vẫn như cũ không ai dám coi thường."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, vị này Bạch Nguyễn Khanh, đả thương qua không ít hạch tâm đệ tử, cũng không ai dám đem thế nào, ngược lại cả đám đều tránh xa xa, không dám phí lời."),t("br"),t("br"),n._v("Bình thường cùng nàng không có gì quan hệ, lúc này đột nhiên đi tìm tới làm gì?"),t("br"),t("br"),n._v("“Ta tới, là muốn cho Lưu Lộ Kiệt hỗ trợ tìm nội môn đệ tử, chuyện này hắn có thể làm a?” Bạch Nguyễn Khanh hừ một tiếng."),t("br"),t("br"),n._v("“Bạch sư tỷ có chuyện dặn dò chính là, Lưu sư huynh cùng ta, cũng sẽ không từ chối...” Vương Kiệm Đông liền vội vàng gật đầu."),t("br"),t("br"),n._v("Không dám không đáp ứng ah!"),t("br"),t("br"),n._v("Ngộ nhỡ cái tên này tức giận, đem hắn điểm chí mạng một chân đá nát... Cũng chỉ có thể nhẫn nhịn lấy!"),t("br"),t("br"),n._v("Đừng nói nội môn, hạch tâm, cũng sẽ không có người nói với ngươi lý."),t("br"),t("br"),n._v("“Sư tỷ mời vào...”"),t("br"),t("br"),n._v("Đi vào trong viện, hai bên vào chỗ, Vương Kiệm Đông có chút hiếu kỳ: “Không biết nội môn cái kia không có mắt đệ tử, đắc tội sư tỷ, chỉ cần nói ra, ta lập tức tìm người đi thăm dò...”"),t("br"),t("br"),n._v("“Ta tìm hắn có chuyện!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nhíu nhíu mày, cũng không giải thích, cong ngón tay búng một cái, một bức họa xuất hiện tại lòng bàn tay: “Đây là bộ dáng của hắn!”"),t("br"),t("br"),n._v("Nàng không chỉ tu vi cực cao, còn là một vị đan thanh cao thủ, đem Trương Huyền dung mạo vẽ giống như đúc, không kém chút nào."),t("br"),t("br"),n._v("“Vị này?”"),t("br"),t("br"),n._v("Nhìn sang, Vương Kiệm Đông rất nhanh cau mày: “Thực sự xấu hổ, vị này... Ta chưa bao giờ thấy qua!”"),t("br"),t("br"),n._v("Nhập môn thời gian vô cùng lâu, nội môn đệ tử, hắn hầu như tất cả đều biết, nhưng trước mắt này cái, chưa bao giờ thấy qua."),t("br"),t("br"),n._v("“Ngươi cũng chưa từng thấy qua? Lưu Lộ Kiệt đâu? Hắn đi chỗ nào? Để hắn cũng đi ra phân biệt thoáng cái!” Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Lưu sư huynh ngay tại Thông Thần điện cùng người giao đấu!” Vương Kiệm Đông vội nói."),t("br"),t("br"),n._v("“Giao đấu?” Bạch Nguyễn Khanh đôi mi thanh tú nhíu: “Hắn không phải nội môn đệ nhất ư? Tại Thông Thần điện cùng ai giao đấu?”"),t("br"),t("br"),n._v("Nội môn thứ nhất, chạy đến Thông Thần điện, chẳng phải là nghiền ép? Có ai có thể đánh được?"),t("br"),t("br"),n._v("“Cùng một cái ngạo mạn gia hỏa, gọi ‘Ta Rất Biết Điều’... Bất quá, không quan trọng, sư huynh chỉ cần ra tay, khẳng định có thể đem chém giết...”"),t("br"),t("br"),n._v("Cười cười, Vương Kiệm Đông tiếng nói còn không có kết thúc, liền nghe đến trong phòng, ghế ngã xuống đất âm thanh vang lên, vội vàng ngẩng đầu, ngay sau đó nhìn thấy Lưu Lộ Kiệt sư huynh, vẻ mặt trắng bệch đi ra."),t("br"),t("br"),n._v("“Sư huynh...”"),t("br"),t("br"),n._v("Vội vàng đứng dậy, cái khác mấy cái nội môn đệ tử, cũng đồng thời đứng lên."),t("br"),t("br"),n._v("“Ngươi chính là Lưu Lộ Kiệt?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nhìn qua."),t("br"),t("br"),n._v("“Gặp qua Bạch sư tỷ...”"),t("br"),n._v(" Lưu Lộ Kiệt vội vàng ôm quyền."),t("br"),t("br"),n._v("“Sư huynh, thế nào? Đem người kia giết chưa?” Vương Kiệm Đông tò mò nhìn qua."),t("br"),t("br"),n._v("Lưu Lộ Kiệt lắc đầu: “Ta bị giết!”"),t("br"),t("br"),n._v("“Ngươi... Bị giết?” Đồng tử co rụt lại, Vương Kiệm Đông con mắt trợn tròn: “Sư huynh... Có thể ép hắn thi triển ra tuyệt chiêu?”"),t("br"),t("br"),n._v("“Cái này...” Lưu Lộ Kiệt đầy mặt cười khổ: “Ta xông tới, liền bị đối phương một kiếm chém thành hai khúc, sau đó... Liền chết!”"),t("br"),t("br"),n._v("“Một kiếm hai nửa?”"),t("br"),t("br"),n._v("Vương Kiệm Đông choáng váng."),t("br"),t("br"),n._v("Hắn không phải là đối thủ, hắn nhận! Có thể vị này là Lưu sư huynh, nội môn đệ nhất nhân... Đồng dạng bị một kiếm đánh chết..."),t("br"),t("br"),n._v("Cái tên này đến cùng là ai?"),t("br"),t("br"),n._v("Chỗ nào xuất hiện?"),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Lúc này cũng phát hiện không hợp lý, Bạch Nguyễn Khanh nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Bẩm báo Bạch sư tỷ, là như vậy...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt đem trước sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Có người tại nội môn Thông Thần điện diễu võ giương oai, muốn khiêu chiến trong các ngươi môn các đệ tử?” Bạch Nguyễn Khanh ngẩn ngơ: “Thật còn có người so ta cuồng hơn?”"),t("br"),t("br"),n._v("Quá mạnh a?"),t("br"),t("br"),n._v("Nội môn đệ tử, cộng lại, chừng mấy vạn... Một người khiêu chiến nhiều như vậy, thật hay giả?"),t("br"),t("br"),n._v("Nàng bởi vì dựa dẫm ông nội, tại trong tông môn liền đủ cuồng, không nghĩ tới còn có người so với nàng cuồng hơn, càng kéo!"),t("br"),t("br"),n._v("Quan trọng... Như vậy chảnh, còn gọi cái “Ta Rất Biết Điều” ngoại hiệu..."),t("br"),t("br"),n._v("Đủ hung ác!"),t("br"),t("br"),n._v("Quả thực chính là kiêu ngạo tới cực điểm."),t("br"),t("br"),n._v("“Ta muốn kiến thức thoáng cái cái tên này...” Đột nhiên đứng dậy, Bạch Nguyễn Khanh ánh mắt lộ ra ý chí chiến đấu dày đặc."),t("br"),t("br"),n._v("Sở dĩ được xưng là nữ bạo long, không chỉ bởi vì tính tình bạo, càng quan trọng hơn là, ưa thích chiến đấu, thực lực lại mạnh!"),t("br"),t("br"),n._v("Nội môn thoáng cái toát ra như vậy một cái siêu cấp cường giả, liền đứng hàng thứ nhất đều tuỳ tiện đánh bại, nàng làm sao không động lòng, không đi qua thử một chút?"),t("br"),t("br"),n._v("“Sư tỷ là... Hạch tâm đệ tử, đi qua mà nói, xem như làm trái quy tắc...” Lưu Lộ Kiệt hơi đỏ mặt."),t("br"),t("br"),n._v("Bất kể nói thế nào, đây đều là trong nội môn đệ tử tranh đấu, Bạch sư tỷ đường đường hạch tâm đệ tử, chẳng phải vượt quá chức phận, làm thay việc của người khác?"),t("br"),t("br"),n._v("“Ta đi qua nhìn một chút, có phải hay không cái kia hạch tâm đệ tử, giả mạo nội môn, đến tìm các ngươi phiền phức!” Bạch Nguyễn Khanh vung tay lên: “Đương nhiên, cũng thuận tiện nhìn một chút, đến cùng dùng dạng gì chiêu số, để ngươi đều không thể phản kháng!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt sửng sốt một chút."),t("br"),t("br"),n._v("Tuy là hắn trên miệng nói, đối phương không thể nào là hạch tâm đệ tử, nhưng ngộ nhỡ có như vậy hỗn bất lận chủ đâu?"),t("br"),t("br"),n._v("Bạch sư tỷ làm hạch tâm danh khí lớn nhất nữ bạo long, đi qua nhìn một chút, ngược lại cũng không sao, chí ít có thể nhìn một chút, đối phương chân thực thân phận, có phải hay không vượt qua nội môn."),t("br"),t("br"),n._v("Thật sự là như vậy, cái gọi là tỷ thí, cũng liền không có cái gì ý nghĩa."),t("br"),t("br"),n._v("“Vậy thì phiền phức Bạch sư tỷ!”"),t("br"),t("br"),n._v("Rõ ràng điểm ấy, không nói thêm lời, lại lấy ra hai cái thông thần ngọc phù, một cái bản thân giữ lại, một cái đưa tới."),t("br"),t("br"),n._v("Thứ này, có thể tốt hơn ngụy trang thân phận, bình thường đệ tử, đều chuẩn bị kỹ càng mấy cái đặt ở trên người, để phòng bất cứ tình huống nào."),t("br"),t("br"),n._v("Liền giống như, một người không chỉ có một cái QQ đồng dạng."),t("br"),t("br"),n._v("“Không phiền phức, ta giúp ngươi nhìn tên kia là ai, thuận tiện giúp ngươi tìm ra chiêu số bên trong thiếu hụt, cho các ngươi đem hắn đánh bại, mà các ngươi, giúp ta tìm người kia là ai là được...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh gật đầu: “Ta không thiếu người ân tình, dạng này tính là hòa nhau!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt gật đầu, cùng Vương Kiệm Đông đám người lần nữa trở lại Thông Thần điện."),t("br"),t("br"),n._v("Lần này dùng một khối khác ngọc phù, dung mạo cũng cùng lần trước khác biệt, vì phòng ngừa người khác nhận ra, Bạch Nguyễn Khanh cũng thay đổi dung mạo."),t("br"),t("br"),n._v("Ba người đi vào đại điện, không có một người nhận ra."),t("br"),t("br"),n._v("Giờ phút này, tỷ thí trước đài đã tích tụ người đông nghìn nghịt."),t("br"),t("br"),n._v("Trước đó không biết tin tức này nội môn đệ tử, lúc này tất cả đều vây quanh, từng cái lên cơn giận dữ, không ngừng hướng trên đài tích tụ mà đi."),t("br"),t("br"),n._v("“Hắn đã giết bao nhiêu?”"),t("br"),t("br"),n._v("Nhìn thấy trên đài, mười mấy người lên một lượt đài, thời gian nháy mắt liền bị “Ta Rất Biết Điều” chém giết, một tốp tiếp theo một tốp, như thủy triều, Lưu Lộ Kiệt cũng nhịn không được nữa, hỏi hướng cách đó không xa một cái nội môn đệ tử."),t("br"),t("br"),n._v("“Hẳn là vượt qua hai trăm... Chỉ cần lên đài, liền bị trong nháy mắt chém giết, không có người có thể chống nổi hợp lại! Ngay cả Lưu Lộ Kiệt sư huynh, đều bị hắn một kiếm chém thành hai nửa...”"),t("br"),t("br"),n._v("Cái này nội môn đệ tử, nhịn không được nói."),t("br"),t("br"),n._v("Tuy là đối tên lớn lối này, lòng đầy căm phẫn, nhưng đối phương thực lực, là thật rất mạnh, không phục không được!"),t("br"),t("br"),n._v("Nội môn đệ nhất nhân, đều bị tuỳ tiện chém giết, những người khác càng không phải là đối thủ... Hiện tại chỉ hy vọng, có thể báo tin càng nhiều người đi vào, dựa vào chiến thuật biển người, đem hắn triệt để giết chết."),t("br"),t("br"),n._v("“Vượt qua hai trăm?”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt lông mày nhảy dựng."),t("br"),t("br"),n._v("Hắn vừa rời đi bao lâu? Ba phút? Năm phút đồng hồ?"),t("br"),t("br"),n._v("Cái tên này lại giết một trăm bảy, tám mươi người?"),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Có muốn hay không mạnh như vậy?"),t("br"),t("br"),n._v("Nội môn đệ tử, tại trong tay đối phương, quả thực cùng sâu kiến không có gì khác nhau ah!"),t("br"),t("br"),n._v("“Bạch sư tỷ, vị này... Có phải hay không hạch tâm đệ tử ngụy trang?”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, nhìn về phía bên người Bạch Nguyễn Khanh."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhìn một hồi, Bạch Nguyễn Khanh nhịn không được lắc đầu: “Các ngươi những người này, không có cách nào ép hắn thi triển ra tuyệt chiêu, ta... Nhìn không ra!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1975"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1977"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);