(window.webpackJsonp=window.webpackJsonp||[]).push([[110],{463:function(n,h,c){"use strict";c.r(h);var g=c(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-1076-han-giet-chet-chin-cai"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1076-han-giet-chet-chin-cai"}},[n._v("#")]),n._v(" Chương 1076: Hắn giết chết chín cái")]),n._v(" "),c("hr"),n._v(" "),c("p",[n._v("Chương 1076: Hắn giết chết chín cái"),c("br"),c("br"),n._v("“Ngươi?”"),c("br"),c("br"),n._v("Nghe được hắn chen vào nói, mọi người dừng lại."),c("br"),c("br"),n._v("Nguyên một đám giống xem đồ đần đồng dạng nhìn qua."),c("br"),c("br"),n._v("“Khụ khụ!” Ngô sư thực sự nhịn không được đánh gãy xấu hổ: “Trương sư, ta biết ngươi thiên phú hơn người, về mặt tu luyện cũng có đặc biệt phương pháp cùng tâm đắc, nhưng... Thiên địa phong Thánh, không phải ai có lẽ liền có thể tới, dính dáng cực lớn, không có khả năng có người thành công!”"),c("br"),c("br"),n._v("“Đúng vậy a, ta chuyên môn nghiên cứu qua Khổng sư phong Thánh quá trình, cũng đi qua Phong Thánh đài, xem qua không biết bao nhiêu tư liệu. Căn cứ suy đoán của ta, muốn phong Thánh, đầu tiên, cái thứ nhất cần thiết điều kiện là trời nhận danh sư! Chỉ có thiên địa công nhận nghề nghiệp của ngươi, mới có phong Thánh tư cách. Thứ nhì, còn muốn tu luyện công pháp đặc thù, chân khí vô cùng hùng hậu, mới có thể gõ khai thiên địa chi môn, loại bỏ dơ bẩn, đạt tới từ xưa đến nay vô thượng thành tựu!”"),c("br"),c("br"),n._v("Cách đó không xa Chương Dẫn Khâu, thấy kẻ trước mắt này nói như vậy, cũng không nhịn được an ủi một câu: “Ngươi có thể xem thấu Khưu Ngô cổ thánh chưởng pháp bên trong vấn đề, nói rõ ngươi đối võ kỹ lý giải cực sâu, nhờ vào Tòng Thánh thực lực liền có thể đi tới nơi này, nói rõ chiến lực cũng không tệ, nhưng chỉ dựa đây, liền muốn phong Thánh... Vẫn là kém quá nhiều!”"),c("br"),c("br"),n._v("“Phong Thánh không phải trò đùa, Thánh Nhân quý tộc thiên tài nhiều hay không? Chiến sư đường thiên tài nhiều hay không? Nhiều cường giả như vậy, vài vạn năm đến, đều không có nghe nói qua có người thành công, đủ thấy độ khó, Trương sư, ngươi có thể thử nghiệm, nhưng ta trước thời hạn nói cho ngươi, đừng ôm hy vọng quá lớn...” Hàn hội trưởng cũng cười khổ."),c("br"),c("br"),n._v("Mọi người không một cái xem trọng."),c("br"),c("br"),n._v("“Cái này... Tốt a!”"),c("br"),c("br"),n._v("Muốn nói gì, Trương Huyền lắc đầu, cuối cùng không ra khỏi miệng."),c("br"),c("br"),n._v("Nói thật, mặc dù tu luyện Phong Thánh giải, nhưng có thể hay không phong Thánh, cũng không dám xác định, cùng đối phương nói đồng dạng, chỉ có tận lực mà thôi."),c("br"),c("br"),n._v("Hắn vốn cũng không phải cái khoa trương người, chuyện không có nắm chắc, tốt nhất vẫn là đừng nói nữa, miễn cho đến lúc đó làm không được, bị người nhạo báng."),c("br"),c("br"),n._v("“Ừm, ngươi có thể suy nghĩ ra là tốt rồi, phong Thánh cũng không đơn giản, có thể làm được tốt nhất, làm không được, cũng chỉ có thể cam chịu số phận...”"),c("br"),c("br"),n._v("Kê Nham cũng nhẹ gật đầu, đang muốn tiếp tục nói chuyện, chỉ thấy trước mắt bệ đá lồng ánh sáng đung đưa, ánh sáng dần dần yếu bớt."),c("br"),c("br"),n._v("Thấy cảnh này, biến sắc: “Đã đến giờ, mọi người đi mau, Chương viện trưởng, chúng ta ngày mai trở lại thăm ngươi...”"),c("br"),c("br"),n._v("Nói hết, gọi mọi người, xoay người liền hướng phía dưới đi đến."),c("br"),c("br"),n._v("Mọi người thấy hắn bối rối, cũng không nhiều lời, theo sát ở phía sau đuổi theo, thời gian không dài, lần nữa trở lại cư trú chật hẹp phòng nhỏ, Kê Nham lúc này mới thở phào nhẹ nhõm."),c("br"),c("br"),n._v("“Chuyện gì xảy ra? Vì sao phải cuống cuồng chạy về?”"),c("br"),c("br"),n._v("Ngô sư nhịn không được nói."),c("br"),c("br"),n._v("“Ta không phải đã nói, chúng ta chỉ có thời gian một nén nhang ư?” Kê Nham nhìn qua."),c("br"),c("br"),n._v("“Ừm!” Mọi người gật đầu, hắn lúc trước, quả thực nói qua như vậy"),c("br"),c("br"),n._v("“Phong Thánh đài, gần đã qua một năm, mỗi ngày đều sẽ phóng thích thánh quang, chiếu rọi bốn phía, lúc này, Thiên Diệp Vương chỉ có thể giấu ở chỗ ở, không dám động đậy, nếu không, sẽ bị thánh quang thiêu đốt, nguyên thần tổn hao nhiều! Mà chúng ta danh sư, thì có thể thừa cơ đi ra hoạt động... Bất quá, thời gian này chỉ có một nén nhang, một khi bỏ lỡ, Thiên Diệp Vương liền sẽ lao ra, thừa cơ đối với chúng ta tiến hành chém giết!”"),c("br"),c("br"),n._v("Kê Nham giải thích nói."),c("br"),c("br"),n._v("Mọi người bừng tỉnh hiểu ra."),c("br"),c("br"),n._v("Khó trách trước mắt vị này nhìn thấy thánh quang sắp dập tắt, lo lắng như thế, một khi muộn, Thiên Diệp Vương xuất hiện, bọn họ chỉ sợ thật đúng là khó mà chạy trốn."),c("br"),c("br"),n._v("“Ta liền nói bị nhốt hai năm, thực lực của các ngươi làm sao tiến bộ như thế lớn, mà Thiên Diệp Vương, vẫn như cũ chỉ là Xuất Khiếu cảnh sơ kỳ, chỉ sợ cũng cùng cái này thánh quang liên quan đến đi!”"),c("br"),c("br"),n._v("Ngô sư hiểu được."),c("br"),c("br"),n._v("“Ừm! Danh sư hấp thu thánh quang, có thể tăng cao tu vi, Thiên Diệp Vương không được, cho nên hắn tiến bộ không có chúng ta nhanh, có điều, hắn chiếm cứ toàn bộ Phong Thánh các, chúng ta chỉ có thể co quắp tại nơi này, năm đó Khổng sư vì để cho hậu nhân thuận lợi đột phá phong Thánh, lưu lại không ít đồ tốt, hầu như đều bị hắn chiếm đoạt! Trong hai năm qua, cho dù không có lớn đột phá, nguyên thần cũng càng ngày càng mạnh dẻo dai, không phải chúng ta có thể chống lại.”"),c("br"),c("br"),n._v("Kê Nham lắc đầu."),c("br"),c("br"),n._v("“Ngươi nói, gần đã qua một năm, mỗi ngày đều sẽ phóng thích thánh quang? Trước kia không phóng thích ư?” Trương Huyền chú ý điểm khác biệt, đối Phong Thánh đài càng thêm tò mò, nhịn không được hỏi."),c("br"),c("br"),n._v("“Ừm, một năm trước Phong Thánh đài hết sức bình thường, mặc dù cũng sẽ phóng thích thánh quang, nhưng mà số lượng cực ít, cũng không có quy luật! Đại khái từ năm trước cuối tháng tám, tháng chín ra bắt đầu, đột nhiên toả hào quang rực rỡ, hình như đang chờ đợi người nào đến, tình huống cụ thể ta cũng không rõ ràng...”"),c("br"),c("br"),n._v("Kê Nham suy nghĩ một chút, gật đầu nói."),c("br"),c("br"),n._v("Bọn họ bị vây ở chỗ này, không có ngày đêm thay đổi, thời gian đã có chút tính toán không cho phép, có điều, thân là danh sư, còn có thể tính toán ra cái đại khái phạm vi, sẽ không chênh lệch quá nhiều."),c("br"),c("br"),n._v("“Chờ người nào? Chẳng lẽ Khổng sư còn có thể trở lại?”"),c("br"),c("br"),n._v("Hàn hội trưởng cười lắc đầu."),c("br"),c("br"),n._v("Khổng sư mấy vạn năm trước liền mất tích, ngay cả hắn bảy mươi hai vị chân truyền đệ tử, đều không rõ ràng, huống chi hậu nhân."),c("br"),c("br"),n._v("Trở về, là tuyệt không có khả năng!"),c("br"),c("br"),n._v("“Liền xem như Thánh giả, cũng không có vài vạn năm tuổi thọ! Đám người, ta cảm thấy không có khả năng, có thể là các ngươi đi tới, phát động cái gì cơ quan, lúc này mới dẫn đến mỗi ngày lấp lánh thánh quang...” Ngô sư gật đầu."),c("br"),c("br"),n._v("“Có khả năng đi...”"),c("br"),c("br"),n._v("Kê Nham hai người đồng thời gật đầu."),c("br"),c("br"),n._v("Bọn họ bị giới hạn thực lực cùng kiến thức, rất nhiều chuyện, là không cách nào giải thích, không nói cái khác, Phong Thánh đài không ngừng lóe lên thánh khí từ chỗ nào mà tới? Lại vì cái gì có thể ngăn cản Thiên Diệp Vương, đối danh sư có chỗ tốt?"),c("br"),c("br"),n._v("“Được rồi, trước không trò chuyện cái này, những vật này, cùng thiên địa phong Thánh đồng dạng, cách chúng ta quá mức xa xôi. Đàm Thanh viện trưởng, ta chỗ này có một cái Đoạn Tục đan, ngươi thử xem có thể hay không đối ngươi hữu dụng...”"),c("br"),c("br"),n._v("Lắc đầu, mọi người không tại xoắn xuýt những vấn đề này, Ngô sư nhìn về phía cách đó không xa Đàm viện trưởng, lấy ra một cái bình ngọc."),c("br"),c("br"),n._v("Lúc này Đàm viện trưởng, hai chân cùng cánh tay đều bị chém đứt, biến thành tàn tật, cũng chỉ có trong truyền thuyết cấp bảy Đoạn Tục đan, mới có thể để cho gãy chi lại nối tiếp."),c("br"),c("br"),n._v("Trước đó, Trương Huyền liền từng chuyên môn luyện chế qua loại đan dược này, cho Ngụy Như Yên dùng, để hắn thân thể nhận được khôi phục."),c("br"),c("br"),n._v("“Đa tạ Ngô sư!”"),c("br"),n._v("Thấy có loại này dược vật, Đàm Thanh ánh mắt sáng lên, tràn đầy kích động."),c("br"),c("br"),n._v("Có thể làm cho gãy chi lần nữa sinh trưởng, ai cũng không cách nào cự tuyệt."),c("br"),c("br"),n._v("Vội vàng nhận lấy bình ngọc, bên trong chứa đựng một cái đan dược, chính là Đoạn Tục đan, chỉ bất quá cấp bậc không cao, miễn cưỡng thành đan."),c("br"),c("br"),n._v("Đem đan dược dùng, một cỗ khổng lồ sinh cơ tràn vào thân thể, Đàm Thanh viện trưởng lập tức cảm thấy toàn thân ngứa, trước đó bị chém đứt cánh tay, đùi, bắt đầu chậm chạp sinh trưởng, có điều, còn không có hoàn toàn mọc ra, chỉ thấy sắc mặt hắn ảm đạm, mặt mũi cười khổ lắc đầu."),c("br"),c("br"),n._v("“Đoạn Tục đan mặc dù dược lực kinh người, nhưng cái này viên, cấp bậc quá thấp, lại thêm tứ chi của ta bị Thiên Diệp Vương chém xuống, ẩn chứa trong đó hắn chém giết chân khí... Xem bộ dáng là không cách nào khôi phục...”"),c("br"),c("br"),n._v("Đoạn Tục đan mặc dù trân quý, sinh cơ mạnh mẽ, nhưng hắn bị thương thực sự quá nghiêm trọng, một cái căn bản không đủ để để hắn hoàn toàn khôi phục."),c("br"),c("br"),n._v("“Cái này... Ta liền một cái...” Ngô sư cau mày."),c("br"),c("br"),n._v("Loại thuốc này vật, mười phần trân quý, cho dù tại Thanh Nguyên đế quốc đều mười phần hiếm thấy, hắn cũng là hao tốn cực lớn đánh đổi, mới đến một cái, một mực không cam lòng sử dụng, không nghĩ tới... Đối với Đàm Thanh, không đưa đến tác dụng quá lớn."),c("br"),c("br"),n._v("“Đoạn Tục đan ta luyện chế qua, nếu có đầy đủ dược vật, hoàn toàn có thể luyện chế...” Trương Huyền nói."),c("br"),c("br"),n._v("“Luyện chế? Ta có phụ tu nghề nghiệp là luyện đan sư, nhưng mà... Những dược vật khác cũng có thể gom góp, nhưng làm chủ dược Đoạn Tục thảo, ta chỗ này không có!” Ngô sư nói."),c("br"),c("br"),n._v("“Đoạn Tục thảo, quá mức thưa thớt, ta chỗ này cũng không có...”"),c("br"),c("br"),n._v("Hàn hội trưởng lắc đầu."),c("br"),c("br"),n._v("Trương Huyền cũng đầy mặt cười khổ."),c("br"),c("br"),n._v("Đoạn Tục thảo đúng là không tốt lắm tìm, lúc trước hắn cũng là chuyên môn hướng Thanh Nguyên đế quốc luyện đan sư công hội xin, mới đến một gốc, cuối cùng luyện đan thành công."),c("br"),c("br"),n._v("Mặc dù trong khoảng thời gian này, chém giết rất nhiều Dị Linh tộc vương giả, nhận được không ít đồ tốt, loại dược liệu này, nhưng lại chưa từng thấy."),c("br"),c("br"),n._v("“Thật ra thì, Đoạn Tục thảo... Cái này Phong Thánh các liền có!” Thấy mọi người lắc đầu, một bên Kê Nham đột nhiên nói."),c("br"),c("br"),n._v("“Nơi này có?” Mọi người sững sờ."),c("br"),c("br"),n._v("“Ừm, ta trước đó không phải đã nói, Khổng sư vì để cho hậu nhân đột phá, lưu lại không ít bảo vật ư? Trong đó có Đoạn Tục thảo... Lúc trước ta từng mạo hiểm lẻn vào qua nơi đó, tận mắt nhìn thấy, chỉ bất quá, bị Thiên Diệp Vương chiếm trước!” Kê Nham nói."),c("br"),c("br"),n._v("“Cái này...”"),c("br"),c("br"),n._v("Mọi người cười khổ."),c("br"),c("br"),n._v("Đổi lại địa phương khác có lẽ có thể mượn nhờ Phong Thánh đài phóng thích thánh quang thời điểm đi lấy, nhưng bị Thiên Diệp Vương chiếm trước... Lại nghĩ nắm bắt tới tay, gần như là không thể nào."),c("br"),c("br"),n._v("“Các ngươi nói những bảo vật này, ở nơi nào? Ta có thể trôi qua lặng lẽ nhìn một chút, ngộ nhỡ muốn thành công, Đàm viện trưởng cũng sẽ không cần chống đỡ loại thống khổ này!”"),c("br"),c("br"),n._v("Trương Huyền cười cười."),c("br"),c("br"),n._v("“Ngươi?” Ngô sư đám người cau mày: “Nếu là Thiên Diệp Vương trông coi, khẳng định mười phần nguy hiểm, ta xem... Thôi được rồi!”"),c("br"),c("br"),n._v("“Đúng vậy a, Thiên Diệp Vương là Nguyên Thần cảnh cường giả, tại chỗ mình ở, giống như chúng ta, cũng bố trí đủ loại phong ấn, tùy tiện đi qua, một khi bị nhốt, liền ai cũng cứu không được...”"),c("br"),c("br"),n._v("Kê Nham mấy người cũng nói."),c("br"),c("br"),n._v("“Yên tâm đi, ta có biện pháp! Chỉ là đi qua nhìn một chút, có cơ hội, hãy cầm về đến, không có liền sẽ trở về, yên tâm đi, không sẽ cùng hắn phát sinh xung đột...”"),c("br"),c("br"),n._v("Trương Huyền nói."),c("br"),c("br"),n._v("“Cái này...” Mọi người nhìn nhau, Ngô sư còn muốn tiếp tục nói chuyện, bị Trương Huyền lần nữa đánh gãy: “Không cần lo lắng, cho dù gặp được Thiên Diệp Vương, bị hắn bắt lấy... Ta có biện pháp chạy trốn!”"),c("br"),c("br"),n._v("“Tốt a!”"),c("br"),c("br"),n._v("Thấy hắn như thế tự tin, Ngô sư nhẹ gật đầu."),c("br"),c("br"),n._v("“Vậy ta đi ra ngoài trước...”"),c("br"),c("br"),n._v("Không nói thêm lời, Trương Huyền nhẹ nhàng cười một tiếng, đi ra khỏi phòng, ở bên ngoài thân ảnh lung lay mấy lần, liền biến mất tại mọi người trước mắt."),c("br"),c("br"),n._v("“Cái này... Ngô sư, để một mình hắn đi tìm Thiên Diệp Vương, thực sự quá lỗ mãng...”"),c("br"),c("br"),n._v("Đàm Thanh nhịn không được mở miệng."),c("br"),c("br"),n._v("Cũng không muốn người khác vì cứu hắn, mà mất đi tính mạng."),c("br"),c("br"),n._v("Kê Nham cũng có đồng cảm, tràn đầy lo lắng."),c("br"),c("br"),n._v("“Thật ra thì ta cũng cảm thấy lỗ mãng, nhưng mà... Nếu như ngươi biết vị này Trương viện trưởng cùng Dị Linh tộc nhân đối chiến sự tích, đoán chừng liền cảm thấy... Hắn có lẽ thật sự có thể thành công!”"),c("br"),c("br"),n._v("Biết hắn lại như vậy hỏi, Ngô sư cười khổ lắc đầu."),c("br"),c("br"),n._v("“Sự tích? Sự tích gì?” Hai người nhìn qua."),c("br"),c("br"),n._v("“Các ngươi cùng Thiên Diệp Vương ở chung được lâu như vậy, Thanh Điền thập đại vương giả hẳn là đều nghe nói qua chứ!” Ngô sư nhìn qua."),c("br"),c("br"),n._v("“Đương nhiên, nghe nói đều là không kém gì Thiên Diệp Vương cao thủ, nguyên một đám so một cái mạnh, đáng sợ đến cực điểm!” Kê Nham gật đầu."),c("br"),c("br"),n._v("“Đúng rất đáng sợ, mỗi một cái đều không thể so với ta kém, thậm chí càng càng mạnh! Nhưng chúng ta vị này Trương viện trưởng, một người...”"),c("br"),c("br"),n._v("Ngô sư cười khổ."),c("br"),c("br"),n._v("“Giết chết chín cái!”"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-1084"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-1086"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);