(window.webpackJsonp=window.webpackJsonp||[]).push([[1100],{1455:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1964-cac-nguoi-cung-len-đi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1964-cac-nguoi-cung-len-đi"}},[n._v("#")]),n._v(" Chương 1964: Các ngươi cùng lên đi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Đối phương ngụy trang thân phận, dung mạo, động tác cùng bất luận một vị nào hạch tâm đệ tử đều không tương tự, không ép hắn, thi triển ra tuyệt chiêu hoặc là chân chính bản lĩnh, muốn xem ra, gần như không có khả năng!"),t("br"),t("br"),n._v("Thật giống tuyệt đỉnh Hacker, đi hack một cái cá nhân xưởng trang web, một chút áp lực đều không có, khẳng định dấu vết gì cũng sẽ không lưu lại, làm sao kiểm tra đều không tra được, trừ phi... Có tương đồng cấp bậc Hacker vây quét, lúc này mới có thể để hắn lưu lại sơ hở, cuối cùng một lần dò ra đối phương cụ thể thân phận."),t("br"),t("br"),n._v("Trên đài vị này “Ta Rất Biết Điều”, chém giết tất cả nội môn đệ tử chiêu số, cùng đập muỗi không có gì khác nhau, một kiếm một cái, thỉnh thoảng hai cái, không có bất kỳ cái gì chiêu số mà nói, coi như nàng đối tất cả hạch tâm đệ tử, tất cả đều rõ như lòng bàn tay, cũng đều không cách nào nhận ra."),t("br"),t("br"),n._v("“Nhưng... Chúng ta không có cách nào cho hắn tạo thành áp lực...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt cười khổ."),t("br"),t("br"),n._v("Vương Kiệm Đông xông tới, cho rằng có thể ép sử dụng ra tuyệt chiêu, bị một kiếm chém đầu, bản thân xông tới, tại chỗ chém thành hai khúc... Hai người bọn họ một cái xếp hàng thứ nhất, một cái bài danh thứ ba, đều không làm được, những người khác càng đừng hy vọng!"),t("br"),t("br"),n._v("Ngắn ngủi mấy phút, giết hơn hai trăm vị nội môn đệ tử, thở mạnh đều không có thở... Dựa vào chiến thuật biển người, ép đối phương thi triển tuyệt chiêu khả năng cũng không lớn!"),t("br"),t("br"),n._v("Dù sao, tuyệt chiêu đối chân khí cùng thể lực yêu cầu cực cao, giết tới cuối cùng, khẳng định sẽ lực lượng hao hết, đến lúc đó, coi như để hắn thi triển, cũng là không thi triển ra được!"),t("br"),t("br"),n._v("“Ta thử một chút!”"),t("br"),t("br"),n._v("Mắt sáng lên, Bạch Nguyễn Khanh lộ ra vẻ hưng phấn."),t("br"),t("br"),n._v("Thân là hạch tâm đệ tử, tam đại trưởng lão một trong cháu gái ruột, tuổi không lớn lắm, nhưng đối kiếm đạo bên trên lý giải, nhưng vô cùng cao thâm, trên đài vị thanh niên này, một kiếm một cái, bổ dưa thái rau đơn giản như vậy giết người, nàng cũng có thể làm được, nhưng Thông Thần điện phân phối thân thể này, chân khí có hạn, muốn giết nhiều người như vậy, vẫn như cũ sừng sững không ngã liền không làm được!"),t("br"),t("br"),n._v("Nói cách khác, trên đài gia hỏa, có khả năng còn mạnh hơn nàng... Cao thủ như vậy, làm chiến đấu cuồng, nữ bạo long, đương nhiên muốn tự thân ra tay thăm dò thoáng cái!"),t("br"),t("br"),n._v("“Sư tỷ...”"),t("br"),t("br"),n._v("Nhìn nhau, Lưu Lộ Kiệt cùng Vương Kiệm Đông tràn đầy xấu hổ: “Ngươi là hạch tâm đệ tử, nhúng tay nội môn đệ tử tranh đấu, một khi bị phát hiện...”"),t("br"),t("br"),n._v("Đối phương có thể là khổ tu nội môn đệ tử, nhiều người như vậy tới chiến đấu liền rất không công bằng, còn tìm hạch tâm đệ tử tới... Bị phát hiện, mặt của bọn hắn hướng chỗ nào đặt?"),t("br"),t("br"),n._v("Mất mặt đều sẽ mất chết!"),t("br"),t("br"),n._v("“Ta dùng ngươi thông thần ngọc bài, chỉ cần các ngươi hai người không nói, sẽ không có người biết!” Bạch Nguyễn Khanh bàn tay lớn chặn lại: “Không cần nói nhiều, ta đi lên chỉ là thăm dò thoáng cái cái tên này, ép hắn thi triển ra tuyệt chiêu, từ đó xác định thân phận! Chỉ cần xác định hắn không phải hạch tâm đệ tử, liền sẽ nhận thua xuống. Sẽ không phá hoại trong các ngươi môn đệ tử chiến đấu! Đương nhiên, nếu là xác định là hạch tâm đệ tử, sẽ trực tiếp động thủ giết, cũng không tính làm trái quy củ!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Chần chờ một chút, Lưu Lộ Kiệt khom người đến cùng: “Vậy làm phiền Bạch sư tỷ!”"),t("br"),t("br"),n._v("Đối phương nói không sai, đi lên chỉ là thăm dò, không hạ sát thủ mà nói, quả thực không tính làm trái quy tắc."),t("br"),t("br"),n._v("“Sư tỷ cẩn thận, cái tên này kiếm rất nhanh...” Gặp xác định được, Vương Kiệm Đông hạ giọng nói."),t("br"),t("br"),n._v("“Nhanh? Ta không sợ nhất chính là nhanh! Nói thật cho các ngươi biết, ta năm tuổi, Bạch Phong gia gia liền truyền thụ cho ta , trong tuyết luyện kiếm, hàng năm đều sẽ đi cực bắc chi địa, đợi một tháng, tiến hành tôi luyện, cho tới bây giờ đã trọn mười lăm năm, sớm đã tu luyện tới đại thành chi cảnh! Có kiếm pháp này, các ngươi cho rằng, tốc độ của hắn, ta sẽ sợ hãi?”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Bạch Nguyễn Khanh ánh mắt lộ ra nhất đạo tự ngạo chi khí."),t("br"),t("br"),n._v("Tốc độ của đối phương là rất nhanh, nhưng đối với nhanh, nàng am hiểu hơn!"),t("br"),t("br"),n._v("Nếu không, cũng không có khả năng, một chân đá nát hạch tâm đệ tử chỗ hiểm, đối phương còn không cách nào phản kháng."),t("br"),t("br"),n._v("“Trầm Tuyết kiếm pháp?”"),t("br"),t("br"),n._v("Biết vị này Bạch Nguyễn Khanh thực lực rất mạnh, tính tình so dáng người còn muốn nóng bỏng, nhưng mà cụ thể tu luyện kiếm pháp, cũng không hiểu rõ tình hình, nghe được cái tên này, Lưu Lộ Kiệt cùng Vương Kiệm Đông, đồng thời mắt sáng rực lên."),t("br"),t("br"),n._v("Trầm Tuyết kiếm pháp, tại trong tông môn tên tuổi lẫy lừng, là hơn một ngàn năm trước, một vị tiền bối tại tuyết rơi trời sáng lập ra, tu luyện tới cảnh giới tối cao, dưới bông tuyết rơi trong nháy mắt, có thể xuất liên tục sáu kiếm, đem hắn hình sáu cạnh, toàn bộ tước mất!"),t("br"),t("br"),n._v("Bông tuyết không còn sáu cạnh, cùng tuyết cầu không có gì khác biệt, hạ xuống tốc độ đương nhiên tăng tốc, lại không giống trước đó như thế phất phới, nên tên là Trầm Tuyết, để tuyết rơi nặng!"),t("br"),t("br"),n._v("Tuyết rất nhẹ, một chút gió cũng có thể làm cho hắn bay đi, xuất liên tục sáu kiếm, không chỉ yêu cầu đối lực lượng khống chế hết sức nhỏ như phát, xuất kiếm tốc độ, cũng được nghĩ mà biết!"),t("br"),t("br"),n._v("Đem loại kiếm pháp này tu luyện tới đại thành cấp bậc... Tốc độ, lực khống chế, ánh mắt, cũng đã đi đến bọn họ không thể lý giải cảnh giới!"),t("br"),t("br"),n._v("“Sư tỷ đem loại kiếm pháp này tu luyện tới đại thành, vị này ‘Ta Rất Biết Điều’ khẳng định không phải là đối thủ...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt lần nữa khom người, một mặt chân thành: “Còn xin sư tỷ, thăm dò ra thân phận của hắn phía sau, liền dừng tay, để chúng ta nội môn đệ tử, đem hắn chém giết!”"),t("br"),t("br"),n._v("Bất kể nói thế nào, đối phương đều là khiêu chiến nội môn đệ tử, vũ nhục nhiều như vậy nội môn đệ tử, nếu là sư tỷ đem hắn giết, nhóm người mình mất hết mặt mũi."),t("br"),t("br"),n._v("“Quá nhiều phí lời, yên tâm, sẽ để lại cho các ngươi!”"),t("br"),t("br"),n._v("Hơi không kiên nhẫn, Bạch Nguyễn Khanh tùy ý khoát tay áo, cầm lấy trường kiếm, thẳng tắp hướng trên đài đi tới."),t("br"),t("br"),n._v("“Những người khác trước không vội đi lên, ta muốn cùng vị này ‘Ta Rất Biết Điều’ đơn đả độc đấu!”"),t("br"),t("br"),n._v("Đi tới đài cao, Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("Thân là hạch tâm đệ tử, đối kiếm đạo lý giải, vượt xa tất cả mọi người ở đây, cứ việc không có ra tay, đồng dạng cho người ta một loại khó mà nói rõ áp bức."),t("br"),t("br"),n._v("“Mặc dù không biết là vị nào sư tỷ, nhưng tu vi không thấp, ta cảm giác kiếm ý của mình, bị áp chế không dám động đậy!”"),t("br"),t("br"),n._v("“Ta cũng vậy, có thể là khổ tu giả, xếp hạng không hiện, cũng không có danh khí gì, nhưng chân chính kiếm thuật, so với Lưu Lộ Kiệt sư huynh, đều không hề yếu!”"),t("br"),t("br"),n._v("“Nhìn a, có lẽ có thể đem cái tên này giết chết, thay chúng ta trút giận!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe nói như thế, vốn tính toán xông đi lên vây công mọi người, ngừng lại, từng cái âm thầm gật đầu."),t("br"),t("br"),n._v("Trong nội môn đệ tử, khổ tu không ít, bình thường không thế nào lộ diện, không có danh khí gì, nhưng chân chính thực lực thi triển đi ra, xếp hạng mười vị trí đầu cũng chưa chắc có thể là đối thủ!"),t("br"),t("br"),n._v("Vị này mới vừa lên đài nữ sư tỷ, cùng vị kia “Ta Rất Biết Điều”, cũng đều là, nếu không, giải thích như thế nào, nội môn đột nhiên xuất hiện cấp bậc như vậy cao thủ?"),t("br"),t("br"),n._v("“Muốn động thủ, cũng nhanh chút, giết ngươi, ta còn muốn tiếp tục giết những người khác!”"),t("br"),n._v("Gặp lại xông lên một cái tự cho là đúng, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đơn đả độc đấu, thật quá lãng phí thời gian."),t("br"),t("br"),n._v("“Rất lâu chưa thấy qua cuồng vọng như vậy người, tự tìm cái chết!”"),t("br"),t("br"),n._v("Một tiếng khẽ kêu, Bạch Nguyễn Khanh trường kiếm trong tay đâm đi ra."),t("br"),t("br"),n._v("Trầm Tuyết kiếm pháp!"),t("br"),t("br"),n._v("Vừa ra tay chính là công kích mạnh nhất, gào thét kiếm khí, trên đài tựa như tạo thành một hồi bão tuyết, bông tuyết tung bay, để cho người ta thấy không rõ lắm."),t("br"),t("br"),n._v("“Thế nào? Có phải hay không so ngươi nhanh hơn?”"),t("br"),t("br"),n._v("Kiếm pháp thi triển đi ra, Bạch Nguyễn Khanh cũng không sốt ruột tấn công, mà là tự tin lộ ra tươi cười."),t("br"),t("br"),n._v("Nàng đối Lưu Lộ Kiệt đám người khoe khoang, nói Trầm Tuyết kiếm pháp tu luyện đến đại thành tình trạng, trên thực tế, còn kém chút hỏa hầu, không phải mỗi một lần đều có thể thi triển cường đại như vậy."),t("br"),t("br"),n._v("Lần này nhìn đối phương chém giết nội môn đệ tử cùng giết gà đồng dạng dễ dàng, áp bức phía dưới, lại có chút đột phá, thi triển ra bình thường mười lần, cũng chưa chắc có thể sử dụng một lần uy lực, lần này vậy mà thuận lợi tác dụng, trong lòng liền tràn đầy hưng phấn."),t("br"),t("br"),n._v("Đổi lại trước đó, thật đúng là không dám nói, có thể đem nhẹ nhõm giết chết, lúc này chiêu này thi triển đi ra, kiếm khí của đối phương, kiếm ý, toàn bộ bao quát trong đó, chiến thắng hẳn là rất đơn giản!"),t("br"),t("br"),n._v("“Nhanh?”"),t("br"),t("br"),n._v("Sửng sốt một chút, gật gật đầu, Trương Huyền đối trước mắt cô bé này tương đối hài lòng: “Nhanh tốt, ta không có thời gian!”"),t("br"),t("br"),n._v("Nói xong trường kiếm đâm ra."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Kiếm của hắn vừa ra tay, Bạch Nguyễn Khanh vừa định ngăn cản, cũng cảm giác cổ họng tê rần, ngay sau đó, ánh mắt của mình cũng nhìn thấy gót chân."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Thi thể nằm trên mặt đất."),t("br"),t("br"),n._v("“Quên đi, như vậy quá chậm, các ngươi cùng lên đi! Không được, lôi đài quá nhỏ, vẫn là ta xuống cùng các ngươi chiến đấu đi!”"),t("br"),t("br"),n._v("Một kiếm đâm chết Bạch Nguyễn Khanh, Trương Huyền giống như là đâm chết rồi một đầu bé nhỏ không đáng kể kiến, gặp bốn phía nội môn đệ tử càng hội tụ càng nhiều, hắn giết đến tốc độ, xa xa không đuổi kịp nghe tin chạy tới, một mặt u oán, vừa tung người nhảy vào đám người."),t("br"),t("br"),n._v("Lôi đài quá nhỏ, tối đa cũng liền thoáng cái bên trên mười người, nhiều hơn nữa liền chật chội, coi như một đợt mười cái... Đem đại sảnh mấy ngàn người toàn bộ giết chết, cũng không biết phải bao lâu!"),t("br"),t("br"),n._v("Huống chi, còn thỉnh thoảng có chút khoe anh hùng, chạy tới đơn đả độc đấu..."),t("br"),t("br"),n._v("Phiền toái như vậy, không bằng xông vào đám người, tốc độ có thể nhanh một chút."),t("br"),t("br"),n._v("“Ngươi tự tìm cái chết...”"),t("br"),t("br"),n._v("“Mọi người cùng nhau ra tay, ta không tin hắn có thể mọc ra mười cái cánh tay...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không nghĩ tới vị này cường hãn sư tỷ, đồng dạng một cái hô hấp không có chống đỡ, liền bị giết chết, vị này “Ta Rất Biết Điều” càng là vọt thẳng tới, mọi người tất cả đều điên rồi."),t("br"),t("br"),n._v("Trên lôi đài nhỏ, tất cả mọi người không xông lên được, thật ra thì cũng cho đối phương thuận tiện, không đến mức hai mặt thụ địch, khó mà chống cự, cái tên này ngược lại tốt... Vọt thẳng vào đám người, bốn phía đều là nội môn đệ tử, đều là trường kiếm, từng cái phương hướng đều có thể tấn công..."),t("br"),t("br"),n._v("Còn thế nào chặn?"),t("br"),t("br"),n._v("Tốc độ lại nhanh, cũng không có khả năng nhanh hơn hai mươi thanh trường kiếm, ba mươi chuôi, thậm chí năm mươi chuôi, một trăm chuôi đi!"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Trong nháy mắt, đủ loại kiếm khí, như mưa bão tố bay xuống."),t("br"),t("br"),n._v("“Bạch sư tỷ...”"),t("br"),t("br"),n._v("Nhìn thấy vị này hạch tâm đệ tử bên trong nữ bá vương, thi triển ra Trầm Tuyết kiếm pháp, đều không có chặn lại thoáng cái, Lưu Lộ Kiệt cùng Vương Kiệm Đông triệt để điên rồi."),t("br"),t("br"),n._v("Trước đó, còn muốn lấy, để Bạch sư tỷ nương tay... Kết quả, nhìn đối phương bộ dạng, cùng chém giết bọn họ không có khác nhau quá nhiều, một chút áp lực đều không có ah!"),t("br"),t("br"),n._v("Hạch tâm đệ tử, có thể là Chân Tiên cảnh siêu cấp cường giả, tuỳ ý chém giết..."),t("br"),t("br"),n._v("Coi như mọi người tương đồng thực lực, cái tên này cũng có chút quá nghịch thiên đi!"),t("br"),t("br"),n._v("“Không thể không thừa nhận, cái tên này, là rất lợi hại, nhưng quá mức tự mãn, quá mức kiêu ngạo, liền như thế xông vào đám người... Ta không tin hắn có thể ngăn lại nhiều người như vậy đồng thời vây công!”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, nhìn thấy “Ta Rất Biết Điều” không biết sống chết xông vào đám người, Vương Kiệm Đông nói."),t("br"),t("br"),n._v("Hai quyền khó địch bốn tay, coi như lại nghịch thiên, cũng có cái hạn độ đi!"),t("br"),t("br"),n._v("Xông vào đám người, nhiều người như vậy đồng thời vây công, không phải đang tìm cái chết là làm gì?"),t("br"),t("br"),n._v("“Hẳn là không ngăn được...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt gật gật đầu, hiển nhiên, hắn đối với đối phương hành động này cũng không coi trọng."),t("br"),t("br"),n._v("“Lộ Kiệt sư huynh, người ở đây thật nhiều... Loạn như vậy, chúng ta có muốn hay không, nhân cơ hội đánh lén?”"),t("br"),t("br"),n._v("Con mắt chợt lóe, Vương Kiệm Đông làm cái chém đầu động tác."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1976"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1978"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);