(window.webpackJsonp=window.webpackJsonp||[]).push([[1101],{1456:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1965-moc-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1965-moc-truong-lao"}},[n._v("#")]),n._v(" Chương 1965: Mộc trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Không được!”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt lắc đầu: “Chúng ta vừa rồi đơn độc khiêu chiến đã thất bại, lần này lại đi vào Thông Thần điện, chỉ là quần chúng, không thể động thủ lần nữa! Nếu không, mỗi người bị giết phía sau, đều thay nhau bên trên, còn có gì công bằng mà nói?”"),t("br"),t("br"),n._v("Đối phương một người khiêu chiến toàn bộ nội môn, bọn họ bánh xe bên trên, đã rất mất mặt, nếu như hắn dạng này cường giả, chết rồi, thay ngựa giáp xông tới, chết đổi lại cái bí danh, bị những người khác biết, còn không bị chê cười chết?"),t("br"),t("br"),n._v("Thua cũng muốn thua có tôn nghiêm!"),t("br"),t("br"),n._v("Đây là bọn họ Lăng Vân trong Kiếm các môn đệ tử kiêu ngạo, cũng là trong lòng kiếm ý kiên trì!"),t("br"),t("br"),n._v("“Sư huynh dạy phải... Là ta càn rỡ...”"),t("br"),t("br"),n._v("Lui về sau một bước, Vương Kiệm Đông xấu hổ."),t("br"),t("br"),n._v("Là hắn quá muốn giết chết đối phương mới toát ra ý tưởng, giờ phút này suy nghĩ một chút quả thực không thích hợp."),t("br"),t("br"),n._v("Kiếm, vạn binh chi vương, muốn luyện kiếm, trước luyện tâm, nếu như ngay cả điểm ấy ngạo khí cùng kiên trì đều không có, còn có cái gì tư cách thu hoạch được kiếm ý công nhận, trở thành chân chính kiếm đạo cao thủ? Từ đó danh dự thiên hạ?"),t("br"),t("br"),n._v("“Ừm! Lần này là cùng sư tỷ tới, sư tỷ đã bị giết, chúng ta cũng nhanh chút trở về đi, nếu không, ta sợ nàng tức giận...”"),t("br"),t("br"),n._v("Thấy đối phương rõ ràng, Lưu Lộ Kiệt thở phào nhẹ nhõm, gật gật đầu, đang muốn rời đi Thông Thần điện, trở lại thực tế, đột nhiên cổ họng tê rần, muốn quay đầu, lại phát hiện, đầu lần nữa thấy được cái mông cùng gót chân..."),t("br"),t("br"),n._v("“Mẹ nó!”"),t("br"),t("br"),n._v("Con mắt nhìn qua nhìn lại, chỉ thấy vừa rồi xông vào đám người “Ta Rất Biết Điều”, không những không có bị chém giết, ngược lại cùng máy ủi đất đồng dạng, không ngừng đi thẳng về phía trước, mỗi đi về phía trước một bước, liền có một đám người lớn đầu bị chặt xuống."),t("br"),t("br"),n._v("Sở dĩ bị giết, chính là bởi vì dựa vào đối phương quá gần, tán dật mà ra kiếm khí, tiện tay liền đem đầu hắn chém xuống..."),t("br"),t("br"),n._v("Nhân gia giết những người khác, phát ra một đạo nhỏ yếu kiếm khí, liền có thể giết hắn... Còn muốn đánh lén!"),t("br"),t("br"),n._v("Lén cái rắm ah!"),t("br"),t("br"),n._v("Thế thì còn đánh như thế nào?"),t("br"),t("br"),n._v("Một mặt u oán, đầu tầng tầng lớp lớp rơi trên mặt đất, bóng da đồng dạng bắn vài cái, ngay sau đó nhìn thấy Vương Kiệm Đông đầu cũng cũng giống như mình, cũng trên mặt đất không ngừng bắn qua bắn lại..."),t("br"),t("br"),n._v("Nhìn đối phương biểu lộ, cũng đầy là bi thương..."),t("br"),t("br"),n._v("Thật sự là sang đây xem náo nhiệt... Vô tội..."),t("br"),t("br"),n._v("Người vô tội cũng muốn giết, còn biết xấu hổ hay không, có hay không thiên lý?"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ý thức lần nữa quay về, Lưu Lộ Kiệt càng thêm buồn bực."),t("br"),t("br"),n._v("Lần đầu tiên đụng nhân gia kiếm bên trên, bị tại chỗ chém thành hai khúc, lần thứ hai, đi qua ăn dưa, bị chém giết kiếm khí của người khác chém xuống đầu..."),t("br"),t("br"),n._v("Hắn nhưng là liên tục bảy năm nội môn đệ nhất nhân, dốc lòng muốn đuổi vượt Thập Lý Kiếm Thần tuyệt thế thiên tài, lúc nào, như vậy không có tồn tại cảm?"),t("br"),t("br"),n._v("Trong lòng từng cơn phát điên, ngẩng đầu nhìn lại, ngay sau đó nhìn thấy Bạch Nguyễn Khanh sư tỷ, thân thể mềm mại không ngừng run rẩy, trong phòng đã một mảnh hỗn độn."),t("br"),t("br"),n._v("Nhìn tới, không chỉ hắn không tiếp thụ được, mới xuất chiêu liền bị người chém xuống đầu, trước mắt vị này, càng thêm chịu không được, đang bắt hắn đồ dùng trong nhà trút giận."),t("br"),t("br"),n._v("May mắn những vật này, đều không đáng tiền, nếu không lần này, thua thiệt lớn hơn."),t("br"),t("br"),n._v("“Sư tỷ còn xin bớt giận...”"),t("br"),t("br"),n._v("Vội vàng đứng dậy ôm quyền."),t("br"),t("br"),n._v("“Bớt giận? Ta không có lửa giận, thế nào muốn tức?”"),t("br"),t("br"),n._v("Vẻ mặt như là hàn băng, Bạch Nguyễn Khanh răng ngà “Cắn” cục cục vang vọng, lạnh lùng nhìn lại."),t("br"),t("br"),n._v("Thấy nàng loại vẻ mặt này, Lưu Lộ Kiệt trong lòng chợt lạnh, vừa cho rằng không tốt, liền thấy thấy hoa mắt."),t("br"),t("br"),n._v("Đùng đùng!"),t("br"),t("br"),n._v("Một hồi quyền cước vào thịt âm thanh, trước đó cùng với nàng tới mấy vị nội môn đệ tử, bao quát Chu Ngôn Chi cùng Ngụy Tùy Phong, tất cả đều nằm trên mặt đất, từng cái mặt mũi bầm dập, thê thảm vô cùng."),t("br"),t("br"),n._v("“Tốt, ta thuận khí qua...”"),t("br"),t("br"),n._v("Đem những người này đánh xong, Bạch Nguyễn Khanh dường như cho rằng ngực, thông thuận không ít, lúc này mới thở phào nhẹ nhõm, thân thể mềm mại chuyển một cái: “Ta muốn ngươi hỗ trợ tìm người, ngươi tốn nhiều điểm tâm, mặt khác, ngày hôm nay ta tới qua sự tình, không hy vọng có người truyền đi, cho ta nghe đến tin tức... Các ngươi hẳn phải biết kết quả!”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt, Vương Kiệm Đông, Chu Ngôn Chi đám người đồng thời rụt cổ một cái, cho rằng dưới hông xiết chặt."),t("br"),t("br"),n._v("“Ta đi trước...”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Lời còn chưa dứt, bóng người lẻn ra ngoài, ngay sau đó rơi vào một đầu phi hành Thánh thú trên lưng, biến mất ở trước mặt mọi người."),t("br"),t("br"),n._v("Đường đường hạch tâm đệ tử chạy đến nội môn đệ tử Thông Thần điện đi dạy dỗ nội môn đệ tử, kết quả bị người ta đập phát chết luôn..."),t("br"),t("br"),n._v("Tiếp tục đợi ở chỗ này, sẽ chỉ càng mất mặt, còn không bằng rời đi!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Chúng ta làm sao bây giờ?”"),t("br"),t("br"),n._v("Gặp vị này nữ bạo long, xoay người rời đi, Vương Kiệm Đông cười khổ nhìn qua."),t("br"),t("br"),n._v("“Còn có thể làm sao? Chuyện này huyên náo như vậy lớn, đã giấy không thể gói được lửa, chỉ có thể trình báo trưởng lão!”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt nói."),t("br"),t("br"),n._v("Nếu như vừa rồi bọn họ đem cái này “Ta Rất Biết Điều” giết, coi như nghiêm trọng, cũng sẽ không huyên náo quá lớn, hiện tại, đều bị người giết vài trăm người, thậm chí hạch tâm đệ tử tự thân ra tay, đều bị làm... Lại không báo trưởng lão, bọn họ cũng đảm đương không nổi ah!"),t("br"),t("br"),n._v("“Đúng vậy a, muốn thông tri trưởng lão, để cho bọn họ tới xử lý...”"),t("br"),t("br"),n._v("Vương Kiệm Đông cũng gật gật đầu."),t("br"),t("br"),n._v("Thảo luận hết, hai người đang muốn hành động, liền nghe đến trên không xuất hiện âm thanh gào thét, vội vàng ra khỏi phòng, ngay sau đó nhìn thấy hơn mười vị lão giả, từ phi hành Thánh thú trên lưng, rơi xuống."),t("br"),t("br"),n._v("Chính là chịu trách nhiệm nội môn đệ tử mấy vị chủ yếu trưởng lão!"),t("br"),t("br"),n._v("“Lưu Lộ Kiệt, Vương Kiệm Đông, Thông Thần điện chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Đi đầu một cái lão giả, hơi khô gầy, màu xanh sợi râu, màu xám nhạt trường bào, nhìn thấy hai người, lông mày nâng lên."),t("br"),t("br"),n._v("Khu trong nội môn, thực lực mạnh nhất, quyền lợi lớn nhất Mộc trưởng lão!"),t("br"),t("br"),n._v("Cũng là Lục Vân trưởng lão lần trước đi trưởng lão điện gặp phải vị kia nói chuyện không quá lọt tai người quen biết cũ."),t("br"),t("br"),n._v("Nếu như Trương Huyền tại đây, khẳng định cũng có thể nhận ra, cái này hơn mười vị lão giả bên trong, Lục Vân trưởng lão thình lình xuất hiện."),t("br"),t("br"),n._v("Hắn vừa mới tấn thăng thành nội môn trưởng lão, dựa theo tình huống bình thường, là không có tư cách đi theo mọi người sau lưng, nhưng bởi vì chiêu thu một vị thiên tư mạnh mẽ hậu bối, bị tông môn coi trọng, mới có may mắn đi theo tới."),t("br"),t("br"),n._v("“Bẩm báo Mộc trưởng lão, có người khiêu chiến toàn bộ nội môn đệ tử...”"),t("br"),n._v("Nhìn thấy trưởng lão đi tới hắn sân nhỏ, Lưu Lộ Kiệt không dám do dự, đem sự tình vừa rồi, nói rõ chi tiết một lần."),t("br"),t("br"),n._v("Vị này “Ta Rất Biết Điều”, từ khiêu chiến bắt đầu, tất cả tất cả, hắn đều nhìn ở trong mắt, biết nguyên nhân hậu quả."),t("br"),t("br"),n._v("“Một người khiêu chiến các ngươi tất cả mọi người, giết vài trăm người, hai ngươi đều lên, vẫn như cũ không đỡ được?”"),t("br"),t("br"),n._v("Mộc trưởng lão lông mày giương lên, tràn đầy lửa giận: “Thiệt thòi ta giáo dục ngươi nhiều năm như vậy, làm sao rác rưởi như vậy?”"),t("br"),t("br"),n._v("“Chúng ta...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt hơi đỏ mặt, nói không ra lời."),t("br"),t("br"),n._v("“Mặc kệ cái tên này cụ thể thân phận, là nội môn đệ tử vẫn là hạch tâm bản thân, đi vào Thông Thần điện, chính là tương đồng cấp bậc, đều là Cổ Thánh nhất trọng sơ kỳ!”"),t("br"),t("br"),n._v("Mộc trưởng lão hừ lạnh: “Đồng dạng cấp bậc, một người khiêu chiến các ngươi vài trăm người... Quả thực mất mặt xấu hổ! Xem ra, ta bình thường truyền thụ cho các ngươi, một chút cũng không có ghi ở trong lòng, cũng không có học được trong lòng!”"),t("br"),t("br"),n._v("“Trưởng lão, không phải chúng ta yếu, là cái tên này quá mạnh...”"),t("br"),t("br"),n._v("Vương Kiệm Đông không phục."),t("br"),t("br"),n._v("“Nói láo! Mạnh hơn có thể mạnh bao nhiêu? Mạnh hơn thi triển cũng là Lăng Vân kiếm các kiếm thuật!” Mộc trưởng lão cắt đứt đối phương: “Thực lực thấp, chính là thực lực thấp, không muốn tìm cho mình lấy cớ nguỵ biện!”"),t("br"),t("br"),n._v("“Cái này... Là!”"),t("br"),t("br"),n._v("Vương Kiệm Đông còn muốn nói hai câu, bị Lưu Lộ Kiệt kéo góc áo, đành phải không cam lòng gật gật đầu."),t("br"),t("br"),n._v("Thật ra thì hắn cũng biết, vị này Mộc trưởng lão nói tuy là khó nghe, trên thực tế chưa hẳn chính là như vậy nghĩ... Có thể bị ở trước mặt châm chọc, vẫn còn có chút nhịn không được."),t("br"),t("br"),n._v("“Mộc trưởng lão, vậy chúng ta bây giờ làm sao bây giờ?”"),t("br"),t("br"),n._v("Chờ vị lão giả này mắng một lần, Lưu Lộ Kiệt nhìn lại."),t("br"),t("br"),n._v("Chỉ riêng chửi vô dụng, chuyện này nhất định phải cực nhanh xử lý, nếu không, mất mặt không riêng gì nội môn đệ tử, còn có những trưởng lão này."),t("br"),t("br"),n._v("Hưởng thụ tông môn nhiều như vậy tài nguyên, bồi dưỡng thế hệ sau, kết quả... Bị một người tận diệt... Truyền đi, cũng không tiện nghe ah!"),t("br"),t("br"),n._v("“Đã tới, chúng ta cũng đi qua nhìn một chút, cái tên này đến cùng từ nơi nào xuất hiện! Như vậy không theo quy củ làm việc?”"),t("br"),t("br"),n._v("Mộc trưởng lão xua tay."),t("br"),t("br"),n._v("Thực lực ngươi mạnh, thu hoạch được tài nguyên, tấn cấp hạch tâm là được, mạnh hơn, thậm chí có thể trở thành tông môn trưởng lão, thủ vệ một phương..."),t("br"),t("br"),n._v("Lợi hại như vậy, khiêu chiến nội môn đệ tử, quá không theo lẽ thường ra bài!"),t("br"),t("br"),n._v("Bọn họ cũng muốn nhìn một chút, đến cùng vâng vâng ai, như vậy không có có chừng có mực!"),t("br"),t("br"),n._v("“Xác định thân phận đối phương, sau đó lại đi tìm bản tôn, hỏi thăm rõ ràng tình huống... Tận lực chuyện này tiêu trừ hết, nếu không ảnh hưởng lớn, các ngươi đều sẽ bị ghi vào sử sách, để tiếng xấu muôn đời!”"),t("br"),t("br"),n._v("Mộc trưởng lão khẽ nói."),t("br"),t("br"),n._v("“Vâng!” Lưu Lộ Kiệt đám người gật đầu."),t("br"),t("br"),n._v("“Đi chuẩn bị chút nội môn đệ tử thông thần ngọc phù đến, chúng ta thân là trưởng lão, công khai đi qua không tốt lắm...” Mộc trưởng lão bàn giao."),t("br"),t("br"),n._v("Nội môn đệ tử Thông Thần điện, là nội môn đệ tử giao dịch cùng giao đấu nơi, dưới tình huống bình thường, trưởng lão là sẽ không tiến vào!"),t("br"),t("br"),n._v("Lại thêm sự tình lần này náo như vậy lớn, nếu như lấy thân phận trưởng lão đi vào, người khác thấy thế nào?"),t("br"),t("br"),n._v("“Trưởng lão chờ một lát...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt hai người quay người đi vào gian phòng, chỉ chốc lát lấy ra mười mấy viên thông thần ngọc phù đi ra."),t("br"),t("br"),n._v("Mới vừa rồi bị Bạch Nguyễn Khanh đánh một trận tơi bời phát tiết mấy cái nội môn đệ tử, đều là tại thị trường giao dịch bày quầy bán hàng, trong tay nắm giữ đại lượng ngọc phù, trưởng lão nhu cầu, không ai dám từ chối."),t("br"),t("br"),n._v("Nhỏ máu xác nhận, tinh thần khẽ động, ý niệm tiến vào bên trong."),t("br"),t("br"),n._v("Lần này đi vào Thông Thần điện, bọn họ đồng dạng không dám bày ra bản thân chân thực dung mạo, mà là ngụy trang thành phổ thông nội môn đệ tử hình dáng."),t("br"),t("br"),n._v("Mười mấy vị trưởng lão đi theo Lưu Lộ Kiệt đám người sau lưng, rất mau tới sân thi đấu lân cận."),t("br"),t("br"),n._v("Lúc này toàn bộ đấu trường, đều bị dìm ngập, kiếm khí gào thét, như là gió lốc, vô số đầu không ngừng rơi trên mặt đất, từng cái nội môn đệ tử, hóa thành quang ảnh tiêu tán."),t("br"),t("br"),n._v("Khoảng cách Lưu Lộ Kiệt đám người rời đi nơi đây, không đến mười phút đồng hồ thời gian, lại có ít nhất bảy, tám trăm người bị giết!"),t("br"),t("br"),n._v("Nói cách khác, ngắn ngủi một phút nhiều một chút thời gian, nội môn đệ tử đã bị giết một phần mười, hơn một ngàn người!"),t("br"),t("br"),n._v("Đây cũng quá hung tàn đi!"),t("br"),t("br"),n._v("“Thật giống chân khí trong cơ thể hắn cùng lực lượng, vẫn như cũ không có quá lớn tiêu hao...”"),t("br"),t("br"),n._v("Nhìn về phía giữa đám người, bị vây công “Ta Rất Biết Điều”, Lưu Lộ Kiệt đám người lông mày không ngừng nhảy loạn."),t("br"),t("br"),n._v("Tận mắt nhìn thấy, đều cảm giác muốn điên rồi."),t("br"),t("br"),n._v("Một người, liên tục chém giết hơn ngàn vị cùng cấp bậc cường giả, lực lượng hao tổn không lớn... Đổi lại trước đó, đánh chết đều không tin, nhưng bây giờ nhưng thực sự phát sinh!"),t("br"),t("br"),n._v("“Mộc trưởng lão, các ngươi cũng nhìn thấy, hiện tại quần tình xúc động phẫn nộ, thực sự không có cách nào...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt đi tới trước mặt."),t("br"),t("br"),n._v("Hiện tại không chỉ vị kia “Ta Rất Biết Điều” đại sát bát phương, không dừng được, tất cả nội môn đệ tử cũng triệt để thiêu đốt, không quan tâm, đừng nói là hắn, Mộc trưởng lão tự thân mở miệng, cũng chưa chắc khuyên can!"),t("br"),t("br"),n._v("“Ngươi không có cách nào, là bởi vì thực lực ngươi yếu, xem ta!”"),t("br"),t("br"),n._v("Trên mặt không vui, Mộc trưởng lão hừ lạnh một tiếng: “Chư vị trưởng lão đi theo đằng sau ta, ngăn cản cái tên này tiếp tục giết người!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hơn mười vị nội môn cường đại nhất trưởng lão, đồng thời gật gật đầu, đồng loạt hướng về phía trước."),t("br"),t("br"),n._v("Bọn họ tuy là cùng nội môn đệ tử đồng dạng trẻ tuổi dung mạo, nhưng trong cơ thể kiếm ý ở nơi đó bày biện, ngay tại vây công mọi người, thấy bọn họ đi tới nhao nhao tránh ra."),t("br"),t("br"),n._v("“Tốt, dừng tay đi...”"),t("br"),t("br"),n._v("Đi tới trước mặt, Mộc trưởng lão nhìn về phía Trương Huyền, trên tay trường kiếm vũ động, kiếm khí gào thét, đem bốn phía bao phủ."),t("br"),t("br"),n._v("“Lại tới một nhóm? Chẳng lẽ lại muốn đơn đả độc đấu? Ta thật không có nhiều thời gian như vậy lãng phí!”"),t("br"),t("br"),n._v("Gặp một chút toát ra nhiều như vậy, còn ngăn cản ở mọi người, Trương Huyền sững sờ, ngay sau đó lắc đầu, trường kiếm trong tay bỗng nhiên lắc một cái."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới cái tên này sẽ đối với bọn họ ra tay, ngay cả lời đều không cho nói xong, Mộc trưởng lão giận dữ, kiếm trong tay cũng giương lên, đang muốn xuất chiêu, cổ họng đột nhiên tê rần."),t("br"),t("br"),n._v("Ngay sau đó, đầu rơi trên mặt đất, còn không có biến mất ý thức, ngay sau đó nhìn thấy đi theo phía sau hắn tất cả trưởng lão, từng cái đồng loạt té ngã trên đất, đồng thời không còn đầu."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1977"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1979"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);