(window.webpackJsonp=window.webpackJsonp||[]).push([[1104],{1458:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1967-toan-bo-chem-giet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1967-toan-bo-chem-giet"}},[n._v("#")]),n._v(" Chương 1967: Toàn bộ chém giết")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Không có...” Lắc đầu, Bạch Nguyễn Khanh nhìn lại: “Phong lão cũng muốn đi xem?”"),t("br"),t("br"),n._v("“Trầm Tuyết kiếm pháp, ảo diệu vô tận, ta không tin sẽ bị người tuỳ tiện phá vỡ, ta xem một chút cái tên này, đến cùng dùng âm mưu quỷ kế gì! Coi như có thể phá giải ngươi, ta cũng có tự tin, để hắn không cách nào phá giải của ta!”"),t("br"),t("br"),n._v("Bạch Phong nói."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh mặc dù cố gắng, nhưng bị giới hạn tuổi tác, Trầm Tuyết kiếm pháp ảo diệu, lĩnh ngộ chỉ là da lông thôi!"),t("br"),t("br"),n._v("Từ hắn tự thân ra tay, thi triển bộ kiếm thuật này, khẳng định có thể tuỳ tiện đem hắn chém giết, mà không phải thất bại tan tác mà quay trở về."),t("br"),t("br"),n._v("“Hắn chỉ là cái nội môn đệ tử... Phong lão mặc dù không phải trưởng lão, có thể thực lực so với nhiều hạch tâm đệ tử trưởng lão, đều cường đại hơn... Tự thân ra tay, không quá phù hợp đi!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh một mặt xấu hổ."),t("br"),t("br"),n._v("Nàng ra tay liền rất mất mặt, Phong lão lại ra tay, mặc kệ thắng thua, đều đem mười phần khó chịu nổi."),t("br"),t("br"),n._v("“Ta chỉ là đi xem một chút cái tên này kiếm thuật, không nhất định ra tay!” Bạch Phong lắc đầu, nói: “Lại nói, ta chỉ là cái lão nô thôi, cũng không phải trưởng lão, không có gì phù hợp không thích hợp!”"),t("br"),t("br"),n._v("Hắn kiếm thuật lại cao hơn, cũng chỉ là lão gia hạ nhân."),t("br"),t("br"),t("br"),n._v("Không có nghĩa là Lăng Vân kiếm các."),t("br"),t("br"),n._v("“Cái này...” Thấy trước mắt vị này Phong lão, nhất định phải đi, biết khuyên can không được, Bạch Nguyễn Khanh dừng lại một chút, gật gật đầu: “Tốt!”"),t("br"),t("br"),n._v("Lần nữa trở lại Lưu Lộ Kiệt trụ sở."),t("br"),t("br"),n._v("Nhìn thấy Mộc trưởng lão bọn người mới đi, cái này nữ bạo long xuất hiện lần nữa, Lưu Lộ Kiệt con mắt ê ẩm, có chút muốn khóc."),t("br"),t("br"),n._v("Ngươi tới ta đi..."),t("br"),t("br"),n._v("Ta chỉ là trong đó môn đệ tử mà thôi, không phải mở khách sạn..."),t("br"),t("br"),n._v("“Ta vậy thì đi tìm...”"),t("br"),t("br"),n._v("Rõ ràng đối phương ý tứ, không dám nói nửa cái “Không” chữ, Lưu Lộ Kiệt lần nữa tìm đến nội môn đệ tử thông thần ngọc phù, tinh thần khẽ động, mấy người lần nữa đi vào trong điện."),t("br"),t("br"),n._v("Nói thật, hắn thực sự không muốn đi vào, có thể... Bạch Nguyễn Khanh nhìn chằm chằm, không đi vào liền có thể động thủ, cùng Vương Kiệm Đông hai người, đầy mặt bất đắc dĩ, lần nữa dẫn đường, đi tới tỷ thí trước đài."),t("br"),t("br"),n._v("Ngắn ngủi hơn mười phút, nội môn đệ tử lại bị giết hơn nghìn người."),t("br"),t("br"),n._v("Rất rõ ràng, vây tới càng ngày càng ít."),t("br"),t("br"),n._v("Nội môn đệ tử coi như lại oán giận, lại nổi giận, liên tục bị giết hơn hai ngàn người, cũng cảm thấy có chút sợ hãi."),t("br"),t("br"),n._v("Trong mắt bọn hắn, vị này “Ta Rất Biết Điều” liền cùng động cơ vĩnh cửu đồng dạng, không biết mệt mỏi, tới tại nhiều người, ở trước mặt hắn đều là uổng công."),t("br"),t("br"),n._v("“Cái này kiếm pháp...”"),t("br"),t("br"),n._v("Nhìn một hồi, Bạch Phong nhíu nhíu mày."),t("br"),t("br"),n._v("Trước mắt vị này kiếm pháp, đơn giản hiểu rõ, liếc mắt liền có thể nhìn ra không có bất kỳ cái gì kỹ xảo, cùng hài đồng vung vẩy đồng dạng, cho người ta một loại, không biết dùng kiếm cảm giác."),t("br"),t("br"),n._v("Có thể... Chẳng biết tại sao, uy lực nhưng cực lớn, chỉ cần thi triển đi ra, cũng làm người ta không cách nào ngăn cản, chỉ có thể trơ mắt bị giết."),t("br"),t("br"),n._v("Loại kiếm pháp này, cho dù là hắn, cũng nhìn không ra tốt xấu."),t("br"),t("br"),n._v("Đối chiến hai bên chênh lệch quá lớn, liền cùng mai tây khiêu chiến quốc túc đồng dạng, coi như chiến thắng, cũng nhìn không ra kỹ thuật bóng mạnh mẽ hay không, không phải không hảo hảo thi triển, mà là... Đối thủ quá yếu!"),t("br"),t("br"),n._v("Một quyền đánh khóc ba tuổi trẻ mới sinh, chứng minh ngươi rất mạnh ư?"),t("br"),t("br"),n._v("“Phong lão...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nhìn qua."),t("br"),t("br"),n._v("“Kiếm pháp của hắn, là rất mạnh... Bất quá, muốn chân chính biết mạnh yếu, cần tự thân tới tỷ thí!”"),t("br"),t("br"),n._v("Bạch Phong nói."),t("br"),t("br"),n._v("“Tự thân tỷ thí?” Lưu Lộ Kiệt lông mày nhảy dựng: “Phong lão, ta nhìn thôi được rồi, ta sợ...”"),t("br"),t("br"),n._v("“Sợ cái gì? Yên tâm, ta sẽ không giết hắn, cho các ngươi khó chịu...”"),t("br"),t("br"),n._v("Bạch Phong cau mày."),t("br"),t("br"),n._v("“Ta không phải sợ ngươi giết hắn, mà là sợ... Hắn đem ngươi giết, như thế càng khó chịu nổi...” Lưu Lộ Kiệt cười khổ."),t("br"),t("br"),n._v("“Làm càn! Phong lão thực lực gì ngươi cũng đã biết? Làm sao sẽ bị giết?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh giận dữ."),t("br"),t("br"),n._v("Phong lão mặc dù không phải tông môn trưởng lão, thực lực cũng không phải bình thường trưởng lão có thể so!"),t("br"),t("br"),n._v("Đối mặt dạng này cường giả, dám nói như thế, quả thực muốn bị đánh!"),t("br"),t("br"),n._v("“Ta... Chỉ là thuận miệng nói một chút, không tin, liền thử một chút đi...” Lưu Lộ Kiệt lắc đầu."),t("br"),t("br"),n._v("Vừa rồi Mộc trưởng lão đám người tới, đều bị chém... Vị này Phong lão coi như rất mạnh, cùng cấp bậc, cũng chưa chắc thắng được qua hơn mười vị nội môn trưởng lão đi!"),t("br"),t("br"),n._v("Bất quá, đối phương đã không tin, không cần thiết tiếp tục nhắc nhở."),t("br"),t("br"),n._v("Nếu không, cũng không tiện giải thích, tại sao lại như vậy xác nhận... Dù sao, Mộc trưởng lão trước khi đi đặc biệt bàn giao, đừng nói ra bọn họ tới qua tin tức."),t("br"),t("br"),n._v("“Đem ta giết? Cùng cấp bậc, loại trừ lão gia, cùng trong môn phái mấy vị kia trưởng lão, ta còn thực sự chưa hề sợ hãi qua những người khác!”"),t("br"),t("br"),n._v("Thấy cái này nội môn đệ tử nói như vậy, Bạch Phong nhịn không được cười lên."),t("br"),t("br"),n._v("Hắn bởi vì không phải trưởng lão, tại tông môn, danh khí cũng không lớn, nhưng... Không có nghĩa là thực lực liền rất yếu!"),t("br"),t("br"),n._v("Lão gia tự thân chỉ điểm, tu luyện hơn một trăm năm, đối kiếm đạo lý giải, sớm đã trong sáng thánh khiết, chưa từng sợ hãi qua bất luận người nào!"),t("br"),t("br"),n._v("Thân thể nhẹ nhàng run rẩy, trong cơ thể kiếm ý lan tràn ra, sải bước hướng về phía trước đi tới."),t("br"),t("br"),n._v("Hắn không cần hướng người khác chứng thực bản thân, chỉ là muốn xác nhận một chút, đối phương như thế nào phá giải hắn tu luyện Trầm Tuyết kiếm pháp thôi!"),t("br"),t("br"),n._v("Gặp hắn xông tới, tìm “Ta Rất Biết Điều”, Lưu Lộ Kiệt cùng Vương Kiệm Đông đồng thời khóe miệng co quắp thoáng cái, đồng loạt lui về phía sau mấy bước, giấu ở đại sảnh biên giới."),t("br"),t("br"),n._v("“Làm gì?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh cau mày."),t("br"),t("br"),n._v("“Không làm cái gì... Bạch sư tỷ, nếu không... Ngươi cũng đến đây đi, dù sao bằng vào chúng ta nhãn lực, đều có thể nhìn rất rõ ràng, không cần thiết gần như vậy...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt xấu hổ."),t("br"),t("br"),n._v("Liên tục bị chặt đầu ba lần, hắn cũng không muốn lại bị giết."),t("br"),t("br"),n._v("“Đồ hèn nhát, còn nội môn đệ nhất đây, mất mặt...”"),t("br"),t("br"),n._v("Nhìn ra ý nghĩ của đối phương, Bạch Nguyễn Khanh chẳng muốn tiếp tục chú ý, ngẩng đầu nhìn qua."),t("br"),t("br"),n._v("Cũng không biết Phong lão thi triển thủ đoạn gì, trước đó vây quanh ở “Ta Rất Biết Điều” mọi người chung quanh, giờ phút này tất cả đều tránh ra, xuất hiện một con đường."),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Người không đi tới trước mặt, Phong lão trường kiếm trong tay phát huy ra, kiếm khí như tiêu, mang theo nghẹn ngào vang lên, đầy trời lần nữa rơi ra kiếm khí hình thành gió tuyết."),t("br"),t("br"),n._v("Đối diện “Ta Rất Biết Điều”, nhìn thấy kiếm pháp này sửng sốt một chút, tiến về phía trước một bước, một kiếm đâm ra."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Phong lão đầu rơi trên mặt đất."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh đôi mắt thanh tú trợn tròn."),t("br"),t("br"),n._v("Thi triển ra mạnh nhất võ kỹ Phong lão cũng không có chặn lại?"),t("br"),t("br"),n._v("Xoa xoa con mắt, đang muốn xác nhận có phải thật vậy hay không, liền cảm thấy bản thân cổ họng đồng dạng tê rần..."),t("br"),t("br"),n._v("Đầu đồng dạng rơi trên mặt đất."),t("br"),t("br"),n._v("Sắp chết, cũng không dám tin tưởng."),t("br"),t("br"),n._v("Khoảng cách đối phương chừng hơn ba mươi mét... Liền như thế bị giết?"),t("br"),t("br"),n._v("Khó trách... Lưu Lộ Kiệt đám người trốn đến góc tường, xem ra hẳn là đã sớm đoán được có thể như vậy..."),t("br"),t("br"),n._v("Đầu trên không trung hạ xuống khe hở, nhịn không được hướng sau lưng hai người nhìn sang, ngay sau đó nhìn thấy lại một đạo kiếm mang bay lượn mà qua."),t("br"),t("br"),n._v("Phốc! Phốc!"),t("br"),t("br"),n._v("Hai người một mặt u oán trong ánh mắt, đầu đồng dạng rơi trên mặt đất."),t("br"),t("br"),n._v("Trốn đến góc tường cũng vô dụng... Đồng dạng bị giết!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Thật không biết xấu hổ...”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Liên tục giết nhiều người như vậy, hắn có chút mệt mỏi."),t("br"),t("br"),n._v("Thông Thần điện cho thân thể này, cùng hắn bản tôn có cách nhau một trời một vực, liên tục tiêu hao, chân khí trong cơ thể đã tổn thất tiếp cận một nửa, tinh thần cũng đầy là mệt mỏi."),t("br"),t("br"),n._v("Đơn độc chém giết những này, không cảm thấy cái gì, có thể... Có chút không biết xấu hổ nội môn đệ tử, bị giết hết, đổi cái ngọc phù, lần nữa chui vào, đổi cái ngọc phù lại chui vào..."),t("br"),t("br"),n._v("Một mực nếu như vậy, muốn giết tới lúc nào?"),t("br"),t("br"),n._v("Còn có hay không điểm công bằng đáng nói?"),t("br"),t("br"),n._v("Đặc biệt là cái kia hai cái không biết xấu hổ, đều giết bọn hắn ba lần, lại còn xung kích tới... Tuy là thay đổi dung mạo, nhưng thể nội kiếm ý, còn có thể phân biệt ra được!"),t("br"),t("br"),n._v("Còn có vừa giết chết nữ nhân kia, trước đó cũng từng giết đi!"),t("br"),t("br"),n._v("Quả thực không biết xấu hổ đến cực hạn!"),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Nhịn không được lắc đầu, đối phương không biết xấu hổ như vậy, nhưng hắn là chính nhân quân tử, biết điều kín đáo có đức tính, cùng lắm thì lần sau gặp được, trực tiếp giết chính là, không cần thiết tức giận."),t("br"),t("br"),n._v("Chỉ là... Một mực như vậy chơi tiếp, hắn thật hao không nổi, đây cũng không phải là giết người, mà là giết thông thần ngọc phù!"),t("br"),t("br"),n._v("“Chỉ có thể tốc chiến tốc thắng!”"),t("br"),t("br"),n._v("Lông mày nâng lên, Trương Huyền trong lòng có quyết định."),t("br"),t("br"),n._v("Từng cái giết, đối phương ra ngoài tìm mới ngọc phù trở lại, chỉ cần ngọc phù còn có, liền mãi mãi cũng giết không hết... Muốn giải quyết loại tình huống này, chỉ có một cái phương pháp, đó chính là dùng nhanh nhất phương thức động thủ!"),t("br"),t("br"),n._v("Có thể trong vòng một phút, đem tất cả mọi người giết chết, nên có thể giải quyết triệt để cái này tai hoạ ngầm!"),t("br"),t("br"),n._v("“Còn có tiếp cận ba ngàn người... Một phút đồng hồ làm sao có thể toàn bộ giết chết?”"),t("br"),t("br"),n._v("Hơn hai ngàn người, hắn liền giết gần tới nửa giờ, còn thừa lại ba ngàn, lại thêm lực lượng đã tiêu hao tiếp cận một nửa, căn bản không thể nào làm được ah!"),t("br"),t("br"),n._v("“Nhìn tới chỉ có thể thi triển chiêu kia...”"),t("br"),t("br"),n._v("Răng cắn chặt."),t("br"),t("br"),n._v("Đối phó quần công, hắn còn có một chiêu."),t("br"),t("br"),n._v("Nhưng chiêu này đối chân khí tiêu hao rất nhiều, một khi thi triển, toàn thân chân khí sẽ hao tổn không còn, bởi vì có người liên tục không ngừng đi vào, cho nên một mực không dám sử dụng."),t("br"),t("br"),n._v("Bất quá, đều bức đến mức này, bây giờ không có biện pháp khác!"),t("br"),t("br"),n._v("Không nhanh chóng giải quyết, đối phương sau khi chết liên tục không ngừng đi vào, vĩnh viễn cũng không có phần cuối ah!"),t("br"),t("br"),n._v("“Liều mạng!”"),t("br"),t("br"),n._v("Trong lòng tính toán một chút, tại không chần chờ, Trương Huyền trường kiếm trong tay rời khỏi tay."),t("br"),t("br"),n._v("Nhìn thấy hắn đột nhiên không cần trường kiếm, ngay tại vây công mọi người, tất cả đều sửng sốt một chút, quên mất tiếp tục tiến công."),t("br"),t("br"),n._v("Tinh thần nhanh chóng vận chuyển, Trương Huyền đầu ngón tay bỗng nhiên một điểm."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một đạo tinh thuần kiếm khí, lập tức từ chỉ điểm bắn ra, đi tới trên không, thời gian nháy mắt, phân tán nhìn tới, biến thành mấy ngàn đạo nhiều, từng đạo lơ lửng tại bốn phía, không nhúc nhích!"),t("br"),t("br"),n._v("Chiêu này, cũng không phải là vừa mới lĩnh ngộ Thần chi kiếm ý, mà là tại danh sư đại lục kiên trì học được Nhất Kiếm Phá Hải, Nhất Kiếm Uông Dương cùng Nhất Kiếm Lăng Thiên!"),t("br"),t("br"),n._v("Trước đó, hắn chỉ có thể thi triển ra một chiêu, giờ phút này lĩnh ngộ Thần chi kiếm ý phía sau, ba chiêu hợp nhất, hơn nữa trao cho lực lượng mới, đồng dạng là một chiêu kiếm pháp, uy lực nhưng so trước đó mạnh mẽ không biết gấp bao nhiêu lần!"),t("br"),t("br"),n._v("Lúc trước liền có thể mượn trên trăm thanh trường kiếm, mỗi một chuôi đều thi triển ra khác biệt kiếm thuật, đối với người tiến hành công kích, làm cho không người nào có thể phòng ngự, giờ phút này càng hơn một bậc, càng thêm cường đại!"),t("br"),t("br"),n._v("Dù vậy... Thân thể này, khống chế ba ngàn đạo kiếm khí, cũng đạt tới cực hạn, lại cao hơn có thể sẽ trực tiếp sụp đổ."),t("br"),t("br"),n._v("“Hắn có chút cổ quái, mọi người mau ra tay...”"),t("br"),t("br"),n._v("Nhìn thấy hắn thoáng cái thi triển ra nhiều như vậy kiếm khí, nhất đến gần nội môn đệ tử da đầu liền nổ tung, nhịn không được thở dài."),t("br"),t("br"),n._v("Tiếng la còn không có kết thúc, liền nghe đến trước mắt “Ta Rất Biết Điều”, quát khẽ một tiếng: “Đi!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Vô số kiếm khí, lập tức từ đầu ngón tay hắn bắn đi ra."),t("br"),t("br"),n._v("Phốc! Phốc! Phốc! Phốc!"),t("br"),t("br"),n._v("Ba ngàn người, đầu đồng thời rơi xuống đất, hóa thành điểm sáng biến mất, toàn bộ đại điện, trong nháy mắt... Rỗng xuống."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1979"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1981"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);