(window.webpackJsonp=window.webpackJsonp||[]).push([[1105],{1460:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1968-chien-đau-tinh-hinh-cu-the-va-ti-mi-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1968-chien-đau-tinh-hinh-cu-the-va-ti-mi-thuong"}},[n._v("#")]),n._v(" Chương 1968: Chiến đấu tình hình cụ thể và tỉ mỉ (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Phong lão, hắn... Thực lực thế nào?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được gian phòng yên tĩnh, Bạch Nguyễn Khanh nhìn lại."),t("br"),t("br"),n._v("Bọn họ từ Thông Thần điện rời đi đã có hơn mười phút, ý thức vừa trở về, Phong lão liền nhắm mắt lại, không biết nghĩ cái gì, mọi người ai cũng không dám nói chuyện."),t("br"),t("br"),n._v("“Diệu! Thật sự là diệu!”"),t("br"),t("br"),n._v("Từ từ mở mắt, Phong lão hai mắt sáng lên."),t("br"),t("br"),n._v("“Diệu?”"),t("br"),t("br"),n._v("Mọi người tất cả đều khóe miệng giật một cái."),t("br"),t("br"),n._v("Vị này... Chẳng lẽ bị sợ choáng váng đi!"),t("br"),t("br"),n._v("Bị chặt, không những không tức giận, còn hô to “Diệu” ?"),t("br"),t("br"),n._v("Là cho rằng chém đứt đầu rất hay ư?"),t("br"),t("br"),n._v("Lưu Lộ Kiệt cùng Vương Kiệm Đông nhìn nhau, kìm lòng không được sờ lên cái cổ, mặc dù biết là giả, liên tục bị chặt bốn lần, vẫn như cũ cảm thấy có chút hoảng sợ."),t("br"),t("br"),n._v("Sợ hãi cũng không kịp, “Diệu” cọng lông ah!"),t("br"),t("br"),n._v("“Trầm Tuyết kiếm pháp, ta tu luyện vượt qua một trăm năm, càng luyện càng khâm phục, không chỉ có tốc độ nhanh, càng quan trọng hơn là đem chân khí trong cơ thể, ẩn giấu ở kiếm trong cơ thể, khống chế thành thạo, mảy may đều không tiết ra ngoài! Một khi bắn ra, kiếm khí vào mây, lực lượng như hồng, quỷ thần khó lường!”"),t("br"),t("br"),n._v("Ánh mắt xa xăm, Bạch Phong thanh âm bên trong mang theo khâm phục cùng tôn sùng: “Cho tới nay, ta đều cho rằng, chiêu này là không có thiếu hụt, uy lực vô tận, không nghĩ tới, đối phương một chiêu mà phá đi! Hắn cái kia một kiếm... Thật diệu! Tuyệt không thể tả!”"),t("br"),t("br"),n._v("Tuy bị một kiếm đánh giết, nhưng cũng nhìn ra kiếm của đối phương thuật, quả thực cao minh, thành tâm khâm phục."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người nháy con mắt, Bạch Nguyễn Khanh càng là hơi đỏ mặt nhìn qua: “Phong lão... Ngươi không tức giận?”"),t("br"),t("br"),n._v("Bị một cái liền Hư Tiên đều không có đi đến tiểu nhân vật, vừa thấy mặt liền chém giết, ngươi dạng này đại cao thủ, chẳng lẽ không cảm thấy được trên mặt tối tăm, rất mất mặt, rất tức giận?"),t("br"),t("br"),n._v("“Nhìn thấy lợi hại như thế kiếm pháp, cảm ơn cũng không kịp, vì sao muốn tức giận?”"),t("br"),t("br"),n._v("Bạch Phong sững sờ, nhìn thấy vẻ mặt của mọi người, dường như biết bọn hắn ý nghĩ, nhẹ nhàng cười một tiếng: “Thân là tu luyện giả, nhất định phải khiêm tốn, mà không phải tự cho là đúng! Kiếm đạo bao la hạo viễn, không biết phần cuối, ngươi ta cố gắng cả đời, đều không thể thăm dò kết quả, cần gì vì một điểm không tồn tại mặt mũi, mà ảnh hưởng cảm xúc? Không muốn vì danh lợi ảnh hưởng, nếu như ta mua danh cầu lợi, hiện tại ít nhất cũng là hạch tâm trưởng lão!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người sắc mặt tất cả đều đỏ lên."),t("br"),t("br"),n._v("Dựa theo thực lực, vị này Bạch Phong, hoàn toàn có thể làm hạch tâm trưởng lão, nhưng hắn vẫn như cũ chăm sóc Bạch Diệp trưởng lão, thời khắc đều tại làm một vị người hầu bản phận, chưa từng vượt quá, phần này tâm tính, đáng giá được kính nể."),t("br"),t("br"),n._v("https://ngantruyen.com/"),t("br"),n._v("“Phong lão, vị này ‘Ta Rất Biết Điều’ đối với kiếm pháp lý giải... Rất cao? Chẳng lẽ... So ngươi còn lợi hại hơn?” Bạch Nguyễn Khanh tò mò nhìn qua."),t("br"),t("br"),n._v("“Ta?” Bạch Phong lắc đầu: “Ta không có tư cách tới đánh đồng! Tốt, tỷ thí lần này, thu hoạch tương đối khá, ta đi về trước... Nguyễn khanh, ngươi tiếp tục tìm kiếm vị kia bán thuốc bạn bè!”"),t("br"),t("br"),n._v("Nói xong, đứng dậy, nhẹ nhàng nhảy lên, bay ra ngoài, thời gian nháy mắt biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Không nghĩ tới Phong lão nói đi là đi, không chần chờ chút nào, Bạch Nguyễn Khanh sửng sốt một chút, nhìn về phía Lưu Lộ Kiệt: “Vị này ‘Ta Rất Biết Điều’, dù sao các ngươi cũng đánh không lại, cũng đừng quản, đi giúp ta thẩm tra một cái, vị này là ai!”"),t("br"),t("br"),n._v("Lần nữa đem chân dung lấy ra ngoài."),t("br"),t("br"),n._v("“Hiện tại các đệ tử, đều tại cùng ‘Ta Rất Biết Điều’ giao đấu, muốn thẩm tra cũng không rất dễ dàng... Đợi tỷ thí xong, ta lập tức tìm người hỏi thăm!” Lưu Lộ Kiệt đứng dậy."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nhíu nhíu mày: “Ta vừa rồi nhìn một chút, ít nhất còn có ba ngàn người, còn có không ít nội môn đệ tử liên tục không ngừng tiến vào bên trong, triệt để giao đấu xong cần rất lâu đi!”"),t("br"),t("br"),n._v("“Mặc kệ bao lâu, trong chúng ta môn các đệ tử, khẳng định đều sẽ kiên trì, chắc chắn sẽ không nhận thua! Đây là sự kiêu ngạo của chúng ta cùng kiên trì!”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt trong ánh mắt mang theo kiên định: “Ta tin tưởng, chỉ cần kiên trì, vị này ‘Ta Rất Biết Điều’ lợi hại hơn nữa, cũng sẽ bị hao hết lực lượng, tươi sống giết chết, mà thắng lợi cuối cùng, thuộc về chúng ta...”"),t("br"),t("br"),n._v("Ngay tại dõng dạc bảo đảm, chỉ thấy trước mắt Vương Kiệm Đông, cổ tay khẽ đảo, lòng bàn tay nhiều ra một cái ngọc bài, liếc mắt nhìn, da mặt không ngừng vặn vẹo, dường như có lời gì, muốn nói, còn nói không ra miệng."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Nhướng mày, Lưu Lộ Kiệt hỏi."),t("br"),t("br"),n._v("“Khụ khụ, Lưu sư huynh, tỷ thí kết thúc... ‘Ta Rất Biết Điều’, đem còn lại ba ngàn người, một kiếm chém giết... Hiện tại ngay tại quyết định thắng bại...”"),t("br"),t("br"),n._v("Vương Kiệm Đông nhịn không được nói."),t("br"),t("br"),n._v("“Một kiếm, một kiếm chém giết? Ba ngàn người?”"),t("br"),t("br"),n._v("Thân thể run rẩy, Lưu Lộ Kiệt suýt chút nữa không có ngã sấp xuống, tròng mắt trừng đến sắp rơi trên mặt đất: “Ngươi xác định?”"),t("br"),t("br"),n._v("Không chỉ hắn như vậy, một bên Bạch Nguyễn Khanh cũng choáng váng."),t("br"),t("br"),n._v("Một kiếm giết một cái, giết hai cái, thậm chí giết mười cái, đều có thể tiếp nhận... Giết cùng cấp bậc ba ngàn..."),t("br"),t("br"),n._v("Đây là tại đùa giỡn hay sao?"),t("br"),t("br"),n._v("“Tin tức đã xác định được, bây giờ bị chém giết mọi người, đều lần nữa trở lại Thông Thần điện, chờ phán quyết...” Vương Kiệm Đông nói."),t("br"),t("br"),n._v("Thân là nội môn xếp hạng thứ ba đệ tử, có bản thân trung thành thuộc hạ, nguồn tin tức, chuẩn xác không sai."),t("br"),t("br"),n._v("“Cái này...” Lông mày nhảy dựng, Lưu Lộ Kiệt vội vàng ôm quyền: “Bạch sư tỷ, tất cả mọi người đồng thời bị giết, chứng minh cá cược đã hoàn thành, chúng ta những này tới đánh cược người, cần đi vào Thông Thần điện, tiếp nhận phán quyết, mong được tha thứ...”"),t("br"),t("br"),n._v("“Đi đi!”"),t("br"),t("br"),n._v("Biết quy củ, Bạch Nguyễn Khanh không có nói nhiều mà là khoát tay áo."),t("br"),t("br"),n._v("Nói thật, đến bây giờ, nàng đều khó tin đây là sự thực."),t("br"),t("br"),n._v("Song quyền hoành gánh nội môn, một kiếm chém giết ba ngàn..."),t("br"),t("br"),n._v("Vị này “Ta Rất Biết Điều” đến cùng là ai?"),t("br"),n._v(" Quan trọng... Làm ra chuyện này, huyên náo toàn bộ nội môn đều gà chó không yên..."),t("br"),t("br"),n._v("Cùng “Biết điều” có nửa xu quan hệ?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Kiếm các đình, Mộc trưởng lão đám người cuối cùng từ khiếp sợ bên trong khôi phục lại."),t("br"),t("br"),n._v("Nơi này cho thấy thực lực của đối phương, chứng minh người này xác thực vì nội môn đệ tử, một cái nội môn đệ tử, khiêu chiến toàn bộ nội môn, liên tục giết hơn một ngàn người, không những không có bại, còn đem bọn họ đều giết... Quả thực biến thái tới cực điểm."),t("br"),t("br"),n._v("“Bất quá, lợi hại hơn nữa, cũng không có khả năng chiến thắng, nội môn đệ tử có hơn vạn khoảng cách, tin tức truyền ra, vì tôn nghiêm, tới sẽ chỉ càng ngày càng nhiều, chỉ cần không thể một cái đem đại điện phía trong người toàn bộ chém giết, liền không khả năng thành công!”"),t("br"),t("br"),n._v("Mộc trưởng lão lắc đầu: “Cho nên... Bất luận kiên trì bao lâu thời gian, cũng chỉ có thua hết một con đường có thể đi!”"),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("Một người khiêu chiến toàn bộ nội môn... Lại là cùng cấp bậc, thành công... Quá khó khăn!"),t("br"),t("br"),n._v("Dù là lại nghịch thiên."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngay tại cảm xúc, đưa tin ngọc bài phát ra âm thanh, Mộc trưởng lão cúi đầu nhìn lại, ngay sau đó ngốc tại chỗ: “Cái này, cái này... Liền như thế, có kết quả rồi? ‘Ta Rất Biết Điều’, một kiếm chém giết ba ngàn người, tranh tài kết thúc... Làm sao có thể?”"),t("br"),t("br"),n._v("“Một kiếm chém giết ba ngàn?”"),t("br"),t("br"),n._v("Lặng ngắt như tờ, tất cả trưởng lão, tất cả đều choáng váng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Thông Thần điện, lần lượt thân ảnh lại xuất hiện trong đó."),t("br"),t("br"),n._v("Tham dự giao đấu, sau đó bị giết, hầu như đều khi nghe đến tin tức phía sau, chạy về, nhìn ở chính giữa, có chút xụi lơ thanh niên, từng cái ánh mắt phức tạp."),t("br"),t("br"),n._v("Vừa nghe đối phương nói, muốn khiêu chiến tất cả mọi người thời điểm, đều cho rằng là ngạo mạn kinh khủng, bị hóa điên... Chân chính đã trải qua chiến đấu mới hiểu được... Đối phương thật có cả đoàn bị diệt toàn bộ nội môn thực lực!"),t("br"),t("br"),n._v("“Giao đấu kết thúc, vị này ‘Ta Rất Biết Điều’, khiêu chiến lúc ấy ở đây tất cả mọi người, lấy trên người mọi người kiếm các tệ làm tiền đặt cược, hiện tại đã có kết quả...”"),t("br"),t("br"),n._v("Chịu trách nhiệm công khai thi đấu tỷ thí một cái Thông Thần điện nhân viên quản lý, đi ra, âm thanh vang lên: “ ‘Ta Rất Biết Điều’ chiến thắng!”"),t("br"),t("br"),n._v("“Như vậy liền chiến thắng? Toàn bộ nội môn đều không có thắng được hắn một người?”"),t("br"),t("br"),n._v("“Cái này chỉ sợ là nội môn sỉ nhục nhất một ngày!”"),t("br"),t("br"),n._v("“Năm ngàn đệ tử, đều thua... Tại sao có thể như vậy?”"),t("br"),t("br"),n._v("Nghe được kết quả, ở đây không ít người, tâm tính mất cân bằng, có chút sụp đổ."),t("br"),t("br"),n._v("Thân là Lăng Vân trong Kiếm các môn đệ tử, đều có thuộc về mình kiêu ngạo, mà bây giờ những này kiêu ngạo, bị một người dễ như trở bàn tay đánh vỡ."),t("br"),t("br"),n._v("“Ta không phục, ta nghe được tin tức liền chạy tới, còn không có tham gia giao đấu, không tin một mình hắn có thể thắng được năm ngàn người! Coi như có thể thắng được, Lưu Lộ Kiệt sư huynh bọn họ những này đỉnh tiêm cao thủ, khẳng định cũng không có xuất hiện, nếu không, sao có thể dễ dàng như vậy!”"),t("br"),t("br"),n._v("Có người hô lên."),t("br"),t("br"),n._v("Nghe được nội môn đệ tử tranh đấu, không ít người liền hướng đám này đuổi đến, nhưng xuất phát từ đủ loại nguyên nhân, vẫn như cũ có hơn phân nửa nội môn đệ tử không có xuất hiện!"),t("br"),t("br"),n._v("Không ít người, vừa mới đi vào, liền nghe nói chiến đấu kết thúc, rõ ràng không có tham dự, nhưng muốn trên lưng sỉ nhục tội danh, mười phần không cam tâm."),t("br"),t("br"),n._v("“Lưu Lộ Kiệt sư huynh, liên tục trong vòng bảy năm môn đệ nhất, ta không tin hắn ra tay, vị này ‘Ta Rất Biết Điều’ có thể thắng!”"),t("br"),t("br"),n._v("Có người đón uống."),t("br"),t("br"),n._v("Trước đó Lưu Lộ Kiệt ra tay, chưa kịp nói xuất thân phần, liền bị chém giết, không ít người chẳng qua là cảm thấy kiếm pháp giống, nhưng kiếm pháp tương tự, liền nhất định đúng ư?"),t("br"),t("br"),n._v("Chưa hẳn!"),t("br"),t("br"),n._v("Nghe được như vậy kêu, không khỏi lên cao ra một tia hi vọng, có phải hay không vị này Lưu sư huynh thật không có xuất hiện?"),t("br"),t("br"),n._v("Mới để cho cái tên này thắng?"),t("br"),t("br"),n._v("“Các ngươi chẳng lẽ muốn không thừa nhận cá cược?” Thông Thần điện phụ trách người, lông mày giương lên."),t("br"),t("br"),n._v("“Không thừa nhận cá cược ngược lại không đến nỗi, thật sự là hắn rất lợi hại, nhưng cũng chỉ là thắng được tất cả mọi người ở đây, mà không phải thắng được chúng ta toàn bộ nội môn! Khu trong nội môn, những cái kia chân chính đỉnh tiêm cao thủ, thật muốn xuất thủ, chưa hẳn có thể kiên trì đến bây giờ!”"),t("br"),t("br"),n._v("Trước đó người nói chuyện, cao giọng nói."),t("br"),t("br"),n._v("Vô luận như thế nào nguỵ biện, cũng không che giấu được bị giết năm ngàn người thảm bại, đã như vậy, chỉ cần không thừa nhận thắng được toàn bộ nội môn, chí ít còn có thể mời ở lại chút mặt mũi."),t("br"),t("br"),n._v("“Nội môn hết thảy hơn một vạn người, hơn năm ngàn người bị giết, cái này còn không phải thắng được nội môn? Là thắng được cái gì? Nói ra lời này, không cảm thấy xấu hổ ư?”"),t("br"),t("br"),n._v("Thông Thần điện người phụ trách, sầm mặt lại."),t("br"),t("br"),n._v("Bọn họ là tông môn trưởng lão, không thuộc về nội môn, hạch tâm cùng ngoại môn, giải quyết việc chung, sẽ không làm việc thiên tư."),t("br"),t("br"),n._v("“Chúng ta nói là, trong đệ tử nội môn, lợi hại nhất mấy vị kia cũng không ra tay! Bọn họ không có ra tay, nói thắng được toàn bộ nội môn, liền không có căn cứ!” Lời mới vừa nói đệ tử nghiến răng."),t("br"),t("br"),n._v("“Ngươi thế nào phán định bọn họ không có ra tay?” Thông Thần điện người phụ trách nói."),t("br"),t("br"),n._v("“Ta không cách nào phán định, cho nên, còn xin Thông Thần điện công bố chiến đấu tình hình cụ thể và tỉ mỉ! Như vậy, coi như thua, cũng tâm phục khẩu phục!” Người này tiếp tục nói."),t("br"),t("br"),n._v("“Không sai, xin lấy ra chiến đấu tình hình cụ thể và tỉ mỉ, chúng ta đều muốn biết, đến cùng ai tham gia chiến đấu, cuối cùng dẫn đến thua, chúng ta chưa kịp tham gia, cũng không muốn cùng cõng nồi...”"),t("br"),t("br"),n._v("“Đưa ra cái này, mới có thể biểu hiện công bằng!”"),t("br"),t("br"),n._v("“Nếu như tham gia chiến đấu, đều là vừa tiến vào nội môn đệ tử, bản thân thực lực liền không mạnh, thua rất bình thường, thế nào để chúng ta tin tưởng và nghe theo?”"),t("br"),t("br"),n._v("Không ít người ngay sau đó nói."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1980"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1982"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);