(window.webpackJsonp=window.webpackJsonp||[]).push([[1108],{1462:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1971-la-han-la-han-chinh-la-han"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1971-la-han-la-han-chinh-la-han"}},[n._v("#")]),n._v(" Chương 1971: Là hắn là hắn chính là hắn!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thân là người trong cuộc Trương Huyền, không biết loại tình huống này, mà là một đường đi nhanh, thời gian không dài, đi tới đỉnh núi diễn võ trường."),t("br"),t("br"),n._v("Nơi này đã là người đông nghìn nghịt, hơn vạn tên nội môn đệ tử, tiếp vào tin tức phía sau, tất cả đều ngừng tu luyện, tụ đến."),t("br"),t("br"),n._v("Đi theo mọi người sau lưng, Trương Huyền tìm hẻo lánh, trốn đi, dù sao hiện tại nhiều người, cũng không có người chú ý hắn một người bình thường."),t("br"),t("br"),n._v("“Chư vị chắc hẳn đều đoán được, đem bọn ngươi tìm đến ý tứ!”"),t("br"),t("br"),n._v("Giữa đám người, một cái lão giả nhìn quanh một vòng, âm thanh truyền vào mỗi người lỗ tai."),t("br"),t("br"),n._v("Nội môn thứ hai trưởng lão, Hoàng Hữu!"),t("br"),t("br"),n._v("Tất cả nội môn đệ tử, nghe được trưởng lão lời nói, tất cả đều hơi đỏ mặt, cúi đầu xuống."),t("br"),t("br"),n._v("Vừa bị người tại Thông Thần điện lựa chọn, trưởng lão liền đem nó triệu tập tới, ngu nữa cũng rõ ràng chuyện gì xảy ra."),t("br"),t("br"),n._v("“Lăng Vân kiếm các nội môn, truyền thừa mấy ngàn năm, lần đầu tiên xuất hiện loại tình huống này! Mặc dù không biết, vị kia ‘Ta Rất Biết Điều’ là trong các ngươi ai, nhưng có thể làm lấy sức một người, chiến thắng cùng cấp bậc năm ngàn người, cũng coi như mở ra kiếm các tiền lệ! Hắn là đáng giá khen ngợi, nhưng những người khác, trọn năm ngàn vây công, liền một người đều giết không được... Ta chỉ muốn hỏi một chút, các ngươi những năm này đều học cái gì? Ngày ngày tại tông môn ăn không ngồi rồi ư?”"),t("br"),t("br"),n._v("Hướng mọi người nhìn lại, Hoàng Hữu trên mặt không có bất kỳ cái gì biểu lộ, cũng không biết là tức giận, vẫn là vui vẻ."),t("br"),t("br"),n._v("Nghe được quát tháo, mọi người tất cả đều vẻ mặt đỏ lên, khuôn mặt xấu hổ."),t("br"),t("br"),n._v("Năm ngàn người vây công, lại bị một người giết chết... Quả thực là vô cùng nhục nhã, vô luận như thế nào cãi lại, đều cãi lại không xong."),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Dường như, ngươi vụng trộm chạy tới cũng bị nhân gia giết, mọi người lớn ngốc không cười hai ngốc, năm mươi bước không cười trăm bước."),t("br"),t("br"),n._v("“Vị này ‘Ta Rất Biết Điều’, chúng ta thực sự không phải là đối thủ, chỉ là... Gay gắt như vậy, có phải hay không là hạch tâm đệ tử ngụy trang?”"),t("br"),t("br"),n._v("Một cái nội môn đệ tử, nhịn không được hỏi."),t("br"),t("br"),n._v("Lời này vừa ra, lập tức đưa tới không ít người cộng hưởng."),t("br"),t("br"),n._v("Nội môn đệ tử có bao nhiêu lợi hại, tất cả mọi người rất rõ ràng, một chút toát ra so với người bình thường mạnh mẽ một chút, để cho người ta tin tưởng, có thể... Mạnh mẽ nhiều như vậy, cũng có chút khó hiểu."),t("br"),t("br"),n._v("“Ta cùng Mộc trưởng lão đám người, đặc biệt đi kiếm các đình tra xét, thực lực của đối phương vì thực sự phá toái hư không đỉnh phong! Đây là thẩm tra kết quả...”"),t("br"),t("br"),n._v("Dường như biết mọi người có như vậy nghi ngờ, Hoàng Hữu vẻ mặt lãnh đạm, ngón tay một điểm."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một cái ghi chép hình ảnh hiện lên ở trên không, chính là trước đó mấy vị trưởng lão đi thăm dò hỏi ý kiến tình cảnh, rất nhanh bức tường hiện ra thực lực, cùng hắn nói giống nhau như đúc."),t("br"),t("br"),n._v("“Phá toái hư không đỉnh phong... Nội môn đệ tử!”"),t("br"),t("br"),n._v("https://ngantruyen.com/"),t("br"),n._v("“Nội môn thật có thể mạnh như vậy?”"),t("br"),t("br"),n._v("“Cùng cấp bậc có thể có cường đại như vậy?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Xem xong một màn này, mọi người rõ ràng, vị kia “Ta Rất Biết Điều”, là thực sự nội môn đệ tử, trước đó nghi ngờ, tất cả đều tan thành mây khói."),t("br"),t("br"),n._v("“Bây giờ còn có gì lời nói?”"),t("br"),t("br"),n._v("Đem hình ảnh phát ra xong xuôi, Hoàng Hữu trưởng lão lạnh lùng nhìn qua, thanh âm bên trong mang theo lạnh lùng: “Không sánh bằng, liền cảm thấy đối phương là hạch tâm... Các ngươi có thể tìm qua bản thân nguyên nhân?”"),t("br"),t("br"),n._v("Không cần mọi người trả lời, khí tức trên thân càng ngày càng lăng lệ, càng ngày càng dày tầng, ép người không thở nổi: “Cùng cấp bậc, vì cái gì, ‘Ta Rất Biết Điều’ có thể cường đại như vậy? Là bởi vì, hắn cùng mình ngoại hiệu đồng dạng, biết điều không có người biết, không có người chú ý! Chưa từng tham gia tỷ thí, chưa từng cùng người tranh phong đấu hung ác, thậm chí, đều không thế nào ra tay! Một lòng chỉ có tu luyện, tu luyện, tại tu luyện! Hao tốn người khác không biết gấp bao nhiêu lần tâm huyết cùng mồ hôi, mới đổi lấy thành tựu của ngày hôm nay! Mà các ngươi đâu?”"),t("br"),t("br"),n._v("Âm thanh càng thêm vang dội: “Cả ngày giao đấu, hiển lộ thân phận, lấy dẫn tới người khác chú ý là mục đích chủ yếu, tu vi nhưng chưa bao giờ như vậy để tâm... Làm sao có thể tu luyện ra tuyệt thế kiếm thuật? Trở thành cường giả chân chính?”"),t("br"),t("br"),n._v("Mọi người đồng thời xấu hổ xấu hổ vô cùng."),t("br"),t("br"),n._v("Trương Huyền vịn cái cằm."),t("br"),t("br"),n._v("Bản thân chưa từng tham gia trận đấu, chưa từng cùng người tranh phong đấu hung ác... Là bởi vì, mới đi đến kiếm các, không đến một ngày..."),t("br"),t("br"),n._v("Bất quá, tu luyện hao tốn người khác không biết gấp bao nhiêu lần tâm huyết cùng biết điều... Ngược lại là thật."),t("br"),t("br"),n._v("“Cho nên, thất bại không nên ôm oán, đó là bởi vì các ngươi bỏ ra còn chưa đủ, không có người khác thiên tài đồng thời, còn không có người khác cố gắng!” Hoàng trưởng lão âm thanh tiếp tục vang lên: “Từ hôm nay trở đi, ta hi vọng các ngươi có thể đem bản thân vứt bỏ sỉ nhục, lần nữa kiếm về, để chúng ta nội môn, trở nên càng thêm cường đại! Có thể làm được hay không? Có lòng tin hay không?”"),t("br"),t("br"),n._v("“Có thể!”"),t("br"),t("br"),n._v("“Có!”"),t("br"),t("br"),n._v("Từng cái nắm đấm xiết chặt, mọi người răng cắn chặt."),t("br"),t("br"),n._v("Thấy bọn họ khí thế như hồng, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Còn tưởng rằng tra ra thân phận của mình rồi, sợ tới mức vội vàng chạy tới, náo loạn nửa ngày, lấy chính mình làm nhân vật phản diện, khích lệ mọi người khí thế."),t("br"),t("br"),n._v("“Nói đến thế thôi, đã các ngươi có lòng tin, vậy ta liền mỏi mắt mong chờ!” Hoàng Hữu trưởng lão gật gật đầu, vung tay lên: “Mặt khác, các ngươi ai là ‘Ta Rất Biết Điều’, rất dễ có thể chủ động tới tìm ta...”"),t("br"),t("br"),n._v("“Bẩm báo đi...”"),t("br"),t("br"),n._v("Thấy không có tra được bản thân cụ thể thân phận, Trương Huyền thở phào nhẹ nhõm, từ ẩn thân chỗ đi ra, vừa nghĩ tới, có phải hay không phải đi mưa rơi đài, liền nghe đến một cái gấp gáp tiếng la vang lên: “Là hắn, là hắn, chính là hắn, ta tìm được...”"),t("br"),t("br"),n._v("Thanh âm này vang lên vô cùng bất ngờ, hơn nữa rất là sốt ruột, lập tức đưa tới vô số người chú ý."),t("br"),t("br"),n._v("Trương Huyền cũng quay đầu, xem xét phía dưới, lông tơ lập tức nổ lên, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Chỉ thấy hô lên âm thanh gia hỏa, đang chỉ mình, các đệ tử ánh mắt, cũng đều đồng loạt nhìn lại."),t("br"),t("br"),n._v("“Sát...”"),t("br"),t("br"),n._v("Da đầu nổ tung, Trương Huyền điên rồi."),t("br"),t("br"),n._v("Chẳng lẽ, liền như thế bị người nhận ra đi!"),t("br"),n._v(" Nếu thực như thế, còn thế nào biết điều?"),t("br"),t("br"),n._v("Trọng yếu nhất chính là, xác nhận thân phận, cũng không cần tu luyện, mỗi ngày chạy tới khiêu chiến liền không biết bao nhiêu, phiền đều có thể phiền chết..."),t("br"),t("br"),n._v("Dù sao, người người đều muốn dương danh lập vạn, trở nên nổi bật, chém giết năm ngàn người, không làm được, chỉ cần đem bản thân đánh bại, chẳng khác nào thu được cao hơn thành tựu..."),t("br"),t("br"),n._v("“Không phải ta, không phải ta, ngươi nhận lầm...”"),t("br"),t("br"),n._v("Trương Huyền quay đầu liền chạy, tốc độ nhanh tựa như ngựa hoang mất cương, thời gian nháy mắt liền mất đi tung tích."),t("br"),t("br"),n._v("“Đừng chạy...”"),t("br"),t("br"),n._v("Hô lên tiếng nội môn đệ tử, thấy cái tên này, trốn nhanh như vậy, cũng kìm lòng không được ngây người."),t("br"),t("br"),n._v("Vừa định đuổi theo, liền cảm thấy bên cạnh một cỗ nồng đậm áp lực kéo tới, Hoàng Hữu trưởng lão chẳng biết lúc nào đã đi tới trước mặt: “Ngươi mới vừa nói, là hắn, hắn là ai?”"),t("br"),t("br"),n._v("“Là một cái bán thuốc giả... A, không, bán thuốc thật!”"),t("br"),t("br"),n._v("Vị này đệ tử vội vàng khom người."),t("br"),t("br"),n._v("“...” Hoàng Hữu trưởng lão da mặt lay động, hận không thể đem trước mắt cái này hai hàng, một bàn tay quất chết."),t("br"),t("br"),n._v("Bản thân ngay tại nói vị kia “Ta Rất Biết Điều”, cái tên này liền “Là hắn, là hắn” loạn kêu, đối phương càng là sợ tới mức sốt ruột chạy trốn... Còn tưởng rằng tìm tới vị kia một chọi năm ngàn cường giả, không nghĩ tới, chỉ là cái bán thuốc..."),t("br"),t("br"),n._v("Gặp được bán thuốc, không đến mức kích động như vậy đi!"),t("br"),t("br"),n._v("“Vâng... Bạch Nguyễn Khanh sư tỷ tìm hắn, một mực tìm không thấy, vừa nhìn thấy, cho nên có chút kích động...” Nhìn thấy Hoàng trưởng lão sắc mặt không đúng, vị này đệ tử vội vàng nói."),t("br"),t("br"),n._v("Vị này, mặc dù không phải Trương Huyền bày quầy bán hàng lúc, ở gần nhất vị kia, nhưng cũng là lúc trước nội môn chợ giao dịch một thành viên, gặp qua Trương Huyền hình dáng."),t("br"),t("br"),n._v("Chỉ bất quá, Trương Huyền không chút chú ý đối phương thôi."),t("br"),t("br"),n._v("“Bạch Nguyễn Khanh tìm hắn?” Hoàng Hữu trưởng lão cau mày."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh cái này hạch tâm đệ tử, hắn xem như trưởng lão cũng có nghe nói, Bạch Diệp trưởng lão cháu gái ruột, tìm một cái nội môn đệ tử làm gì?"),t("br"),t("br"),n._v("“Vâng, hình như là mua hắn thuốc, hiệu quả cũng không tệ lắm, muốn tìm hắn lại mua một ít...” Vị này đệ tử trả lời."),t("br"),t("br"),n._v("“Đi giúp Bạch Nguyễn Khanh đi tìm, chớ chọc nàng tức giận...”"),t("br"),t("br"),n._v("Còn tưởng rằng là “Ta cũng biết điều”, đã không phải, Hoàng Hữu trưởng lão lại không còn hứng thú, bất đắc dĩ khoát tay áo."),t("br"),t("br"),n._v("“Ừm...” Vị này đệ tử liền vội vàng gật đầu, không kịp đi tìm Lưu Lộ Kiệt đám người, cấp tốc hướng dưới núi Trương Huyền biến mất phương hướng đuổi tới."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Nguy hiểm thật...”"),t("br"),t("br"),n._v("Chạy không biết bao xa, không có phát hiện có người đuổi, Trương Huyền lúc này mới ngừng lại, xoa xoa mi tâm."),t("br"),t("br"),n._v("Hắn chỉ muốn làm cái người bình thường, biết điều sống... Khó khăn như thế sao?"),t("br"),t("br"),n._v("Có thể... Ta là thật không muốn ưu tú như vậy ah!"),t("br"),t("br"),n._v("“Có người phát hiện, thân phận khẳng định bại lộ...” Trương Huyền suy tư."),t("br"),t("br"),n._v("Mặc dù không biết, là thế nào tiết lộ, nhưng chỉ cần bản thân không thừa nhận, không hiển lộ thực lực, đối phương hẳn là tìm không thấy chứng cứ, chứng minh chính mình là “Ta Rất Biết Điều”!"),t("br"),t("br"),n._v("“Nhất định phải đem ‘Ta Rất Biết Điều’ cái này viên thông thần ngọc phù hủy diệt...”"),t("br"),t("br"),n._v("Không hủy diệt, một khi bị đuổi kịp, chỉ cần thẩm tra đối chiếu, rất nhẹ nhàng liền có thể tìm tới mình đích thật bản thân phần, đến lúc đó thật sự hết đường chối cãi."),t("br"),t("br"),n._v("“May mắn, trước đó đánh cược kiếm kiếm các tệ, dùng chính là một trương không ký danh thẻ, đối phương thẩm tra không đến, không cần lo lắng thứ này không tìm về được...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đem “Ta Rất Biết Điều” thông thần ngọc phù lấy ra, tiện tay bóp nát, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thông thần ngọc phù phía trong, thường xuyên luận võ, bị giết là chuyện thường xảy ra, bởi vậy, không ít người đều đem trong đó giao dịch tiền bạc, đặt ở không ký danh trên thẻ, bất luận đổi bao nhiêu ngọc phù, tiến vào bên trong, đều có thể nhẹ nhõm tìm về."),t("br"),t("br"),n._v("Cho nên, ngọc phù cho dù hủy, tiền cũng sẽ không tổn thất."),t("br"),t("br"),n._v("Làm xong những này, Trương Huyền yên lòng, lúc này phát hiện, vừa rồi chỉ vào hắn người kia, đã đuổi theo."),t("br"),t("br"),n._v("Hướng đối phương sau lưng nhìn lại, cũng không phát hiện càng nhiều người, Trương Huyền thở phào nhẹ nhõm, tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Cái tên này đều hô lên bản thân là “Ta Rất Biết Điều”, vị kia Hoàng Hữu trưởng lão cùng những người khác hẳn là đều sẽ đuổi tới a?"),t("br"),t("br"),n._v("Vì sao chỉ có một người?"),t("br"),t("br"),n._v("Chẳng lẽ là sợ hù dọa bản thân?"),t("br"),t("br"),n._v("Bất quá, bất kể như thế nào, mặc kệ đối phương nói thế nào, không thừa nhận chính là!"),t("br"),t("br"),n._v("Dù sao ngọc phù đã bị bóp nát, không có chứng cứ..."),t("br"),t("br"),n._v("“Vị huynh đệ kia, ngươi chạy thật nhanh...”"),t("br"),t("br"),n._v("Vừa rồi vị kia đệ tử, đi tới trước mặt, miệng lớn thở hổn hển."),t("br"),t("br"),n._v("Tuy là Trương Huyền vừa rồi chạy trốn, vì phòng ngừa người khác nhìn ra dị thường, đã dùng rất chậm tốc độ, cũng không phải đối phương có thể tuỳ tiện đuổi theo."),t("br"),t("br"),n._v("“Còn tốt để cho ta cái thứ nhất nhận ra ngươi, hơn nữa đuổi tới, ha ha, lần này ta phát đạt...”"),t("br"),t("br"),n._v("Con mắt sáng lên, vị này đệ tử nhìn về phía trước mắt Trương Huyền, giống như là thấy được bảo tàng."),t("br"),t("br"),n._v("“Nhận ra ta? Ngươi cũng đừng nói bậy, ta không phải là ‘Ta Rất Biết Điều’ vị anh hùng kia vô cùng, mạnh mẽ vô song anh tuấn mỹ nam, hắn như là mặt trời đồng dạng chói mắt, quang minh... Mà ta chỉ là phổ thông nội môn đệ tử, không đáng giá nhắc tới... Nói lung tung, sẽ chết người đấy!” Trương Huyền vội vàng xua tay."),t("br"),t("br"),n._v("“ ‘Ta Rất Biết Điều’ ? Ta chưa nói... Ngươi là ‘Ta Rất Biết Điều’ a?”"),t("br"),t("br"),n._v("Vị này đệ tử sững sờ, có chút ngây người."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1983"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1985"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);