(window.webpackJsonp=window.webpackJsonp||[]).push([[1109],{1464:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1972-nhat-đinh-phai-ga-cho-han"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1972-nhat-đinh-phai-ga-cho-han"}},[n._v("#")]),n._v(" Chương 1972: Nhất định phải gả cho hắn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Nhất định phải gả cho hắn"),t("br"),t("br"),n._v("“Ách?” Trương Huyền cứng đờ: “Vậy ngươi đuổi ta làm gì?”"),t("br"),t("br"),n._v("Ngươi không phải nhận ra ta là “Ta Rất Biết Điều”, chạy nhanh như vậy đuổi tới làm gì?"),t("br"),t("br"),n._v("Sợ tới mức ta liền ngọc phù đều bóp nát?"),t("br"),t("br"),n._v("“Ngươi không phải bán một bình ngọc thuốc chữa thương, cho Bạch Nguyễn Khanh sư tỷ ư? Nàng để chúng ta tìm ngươi, nếu không, liền đánh tơi bời... Ngươi nhìn ta trên người tổn thương, đều là bị nàng đánh... Nhìn thấy ngươi, tự nhiên muốn đuổi tới...”"),t("br"),t("br"),n._v("Sửng sốt một chút, đệ tử này phản ứng lại, một chỉ khuôn mặt."),t("br"),t("br"),n._v("Liếc mắt nhìn, trên người đối phương quả nhiên có thương tích, Trương Huyền nước mắt sắp chảy ra."),t("br"),t("br"),n._v("Sớm biết, là chuyện như vậy, chạy cái rắm ah!"),t("br"),t("br"),n._v("Liền ngọc phù đều bị bóp nát... Đây chính là trọn20 kiếm các tệ đây!"),t("br"),t("br"),n._v("Sợ bóng sợ gió một hồi..."),t("br"),t("br"),n._v("May mắn tiền không có tổn thất, nếu không, thật muốn nhảy lầu tự sát."),t("br"),t("br"),n._v("“Nàng tìm ta làm gì?” Tràn đầy buồn bực, nhịn không được nói."),t("br"),t("br"),n._v("“Ta cũng không rõ ràng, hình như là ngươi thuốc đối chữa thương hữu dụng, muốn từ ngươi nơi này, tiếp tục mua sắm một ít...” Vị này đệ tử nói."),t("br"),t("br"),n._v("“Mua thuốc?” Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Chỉ cần không có bị phát hiện “Ta Rất Biết Điều” Cái thân phận này liền tốt... Còn dược vật, hồ lô nước tắm, đương nhiên là có hiệu, bằng không thì cũng không có khả năng, để thương thế của hắn tốt nhanh như vậy!"),t("br"),t("br"),n._v("“Đúng vậy a! Làm phiền ngươi đi với ta tìm Lưu Lộ Kiệt sư huynh, để hắn hỗ trợ liên hệ Bạch sư tỷ...” Vị này đệ tử liền vội vàng gật đầu."),t("br"),t("br"),n._v("Trương Huyền vốn không muốn đáp ứng, đột nhiên nghĩ tới một việc, tâm tư sinh động: “Vị này Bạch Nguyễn Khanh sư tỷ, hình như là Bạch Diệp trưởng lão cháu gái ruột, ở hạch tâm bên trong địa vị cũng rất cao đúng không?”"),t("br"),t("br"),n._v("Vị này nữ bạo long tin tức, trước hắn nghe qua."),t("br"),t("br"),n._v("Đã có địa vị, lại là hạch tâm đệ tử, nếu như có thể mượn thân phận của nàng, đi hạch tâm đệ tử tàng thư các một chuyến, chẳng phải không cần đi mưa rơi đài?"),t("br"),t("br"),n._v("Một cái tiết kiệm ít nhất hai bước."),t("br"),t("br"),n._v("“Đúng vậy a, địa vị xác thực rất cao, ngươi thuốc, nàng có thể công nhận, hơn nữa đặc biệt tới tìm, về sau khẳng định lại nhận coi trọng, một bước lên trời, ở trong tầm tay! Về sau đừng đem ta cái này tìm tới ngươi người quên thế là được...”"),t("br"),t("br"),n._v("Vị này đệ tử đầy mặt hâm mộ, cười nói."),t("br"),t("br"),n._v("Bọn họ gặp được Bạch sư tỷ, bị đánh hoàn toàn thay đổi, đối phương khẳng định không giống với lúc trước, nguyên nhân chính là như vậy, mới thái độ như vậy cung kính."),t("br"),t("br"),n._v("“Đi thôi...”"),t("br"),t("br"),n._v("Xác định điểm ấy, Trương Huyền ánh mắt sáng lên, gật đầu đồng ý."),t("br"),t("br"),n._v("Đi theo vị này đệ tử sau lưng, thời gian không dài đi tới Lưu Lộ Kiệt sân nhỏ."),t("br"),t("br"),n._v("“Ngươi chính là bán thuốc cho Bạch sư tỷ người đi đường?”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt cùng Vương Kiệm Đông nhìn thấy trước mắt vị thanh niên này, dường như so với chính mình còn nhỏ, hơn nữa tràn đầy lạ lẫm, chưa bao giờ thấy qua, cũng có chút khiếp sợ."),t("br"),t("br"),n._v("“Là ta trong lúc vô tình có được đồ vật, đã dùng không sai biệt lắm, đối chữa thương có không kém hiệu quả, bởi vì thiếu tiền dùng, mới được bức bách bán ra... Không nghĩ tới sẽ đối với Bạch sư tỷ cũng hữu dụng!” Trương Huyền vội nói."),t("br"),t("br"),n._v("Hắn biết thất phu vô tội, mang ngọc có tội ý tứ, trước đó bán ra hồ lô nước tắm, cấp tốc tại bất đắc dĩ, giờ phút này không thiếu tiền, không dám tiếp tục quá nhiều tiết lộ, trước thừa nhận số lượng không nhiều lắm lại nói, nếu không, người người đều đi cầu thuốc, ắt phải tiết lộ nhiều bí mật hơn."),t("br"),t("br"),n._v("“Ta đã báo tin Bạch sư tỷ, nàng một hồi liền đến... Cái loại này thuốc chữa thương, ngươi tốt nhất còn có một số, nếu không... Ta cũng không giữ được ngươi!”"),t("br"),t("br"),n._v("Nghĩ tới vị kia Bạch sư tỷ thô bạo, Lưu Lộ Kiệt nói."),t("br"),t("br"),n._v("“Là!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ở chỗ này chờ a, ta còn có việc...”"),t("br"),t("br"),n._v("Giao phó xong, Lưu Lộ Kiệt không nói thêm lời, hướng trong viện đi tới."),t("br"),t("br"),n._v("Bị “Ta Rất Biết Điều” Liên tục chém giết bốn lần, hắn cảm nhận được nồng đậm áp lực, cứ việc cũng có chút sợ hãi Bạch sư tỷ, nhưng giờ phút này một chút thời gian đều không muốn lãng phí."),t("br"),t("br"),n._v("Đi tới trong viện, cầm lấy trường kiếm, liền cùng đã đợi chờ Vương Kiệm Đông, nghiên cứu và thảo luận lên."),t("br"),t("br"),n._v("“Vị kia ‘Ta Rất Biết Điều’, là như thế này thi triển kiếm pháp...” Vương Kiệm Đông trường kiếm trong tay lắc một cái, kiếm khí gào thét."),t("br"),t("br"),n._v("“Không đúng, ta cảm thấy là như thế này thi triển...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt nhíu nhíu mày, thi triển ra khác biệt chiêu số."),t("br"),t("br"),n._v("Hai người ngươi tới ta đi, căn cứ nhớ lại, nghiên cứu “Ta Rất Biết Điều” Tuyệt chiêu, đồng thời thảo luận bản thân chiêu số bên trong thiếu hụt."),t("br"),t("br"),n._v("“Thiên phú cũng không tệ lắm...”"),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Hai người kia, thiên phú thật là không tệ, so Thiện Hiểu Thiên mạnh không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Chỉ bằng vào nhìn chiến đấu, liền phân tích ra nhiều như vậy, rất không dễ dàng."),t("br"),t("br"),n._v("Nhưng hắn kiếm pháp ảo diệu vô tận, Bạch Phong đều lý giải không được đầy đủ, đối phương hai cái nội môn đệ tử, làm sao có thể lĩnh ngộ tinh túy, trò chuyện một chút liền xuất hiện sai lầm, hơn nữa càng ngày càng xa."),t("br"),t("br"),n._v("Nhìn mí mắt run rẩy, cố nén muốn mắng người xúc động."),t("br"),t("br"),n._v("Làm lão sư làm quen thuộc, nhìn thấy hai người này, càng làm càng sai, thực sự có chút nhịn không được."),t("br"),t("br"),n._v("Chỉ khi nào mở miệng, thân phận nhất định tiết lộ, coi như, còn là nhịn một chút a..."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đúng lúc này, trên không một tiếng thú minh, Bạch Nguyễn Khanh đến."),t("br"),t("br"),n._v("Trương Huyền nhịn không được thở phào nhẹ nhõm."),t("br"),t("br"),n._v("May mắn tới, bằng không, thật sợ mình nhịn không được, chạy tới đổ ập xuống mắng một trận, sau đó lại mạnh mẽ dạy dỗ."),t("br"),t("br"),n._v("Bản thân gay gắt như vậy kiếm pháp, bị lý giải như vậy sai lầm, vặn vẹo thành bộ dáng này, cũng là say."),t("br"),n._v(" “Có thể tìm được ngươi... Nhưng còn có lần trước bán ta thuốc chữa thương?”"),t("br"),t("br"),n._v("Từ lưng thú bên trên nhảy xuống, Bạch Nguyễn Khanh nhận ra Trương Huyền, tràn đầy hưng phấn mà đi tới trước mặt."),t("br"),t("br"),n._v("Lần trước gia gia uống một bình nhỏ, liền có hiệu quả trị liệu, nếu như nhiều hơn nữa một ít, có lẽ có thể triệt để khôi phục."),t("br"),t("br"),n._v("“Ta cũng chỉ còn lại ba bình...” Trương Huyền cổ tay khẽ đảo, đem trước bày quầy bán hàng bán ra bình ngọc lấy ra ngoài."),t("br"),t("br"),n._v("“Ba bình ta muốn lấy hết...” Bạch Nguyễn Khanh liền vội vàng gật đầu, nhận lấy bình ngọc nhìn một chút, xác nhận cùng lần trước giống nhau như đúc, đưa hơi thở, một mặt mong đợi nhìn qua: “Không biết những thuốc này, là ngươi chế biến còn là... Từ nơi nào được?”"),t("br"),t("br"),n._v("“Ta trong lúc vô tình tại một chỗ di tích được, chỉ còn lại có cái này mấy bình, nếu không phải thiếu tiền, không có khả năng lấy ra...” Trương Huyền đem vừa rồi lời giải thích, lần nữa nói một lần."),t("br"),t("br"),n._v("Dù sao, hồ lô trong đan điền, chỉ cần không nấu, đối phương cũng không phát hiện được."),t("br"),t("br"),n._v("“Đáng tiếc...”"),t("br"),t("br"),n._v("Còn tưởng rằng có thể nhiều mua một ít, thấy đối phương nói như vậy, Bạch Nguyễn Khanh kìm lòng không được có chút thất lạc, lấy ra thông thần tệ đưa tới."),t("br"),t("br"),n._v("“Đây là sáu mươi kiếm các tệ, cho!”"),t("br"),t("br"),n._v("Không có đi nhận tiền, Trương Huyền nhìn lại: “Bạch sư tỷ, tiền này ta không được, chỉ muốn làm phiền ngươi một việc, không biết có thể!”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Bạch Nguyễn Khanh lại không còn vừa rồi vui vẻ, mơ hồ mang theo không vui: “Chuyện gì?”"),t("br"),t("br"),n._v("Thu tiền, là một cọc giao dịch, không lấy tiền, khẳng định mong muốn sự tình, là tiền không cách nào giải quyết."),t("br"),t("br"),n._v("Chẳng qua, đối phương loại thuốc này, cứu gia gia mạng, xem như ân nhân cứu mạng, không tốt lắm từ chối."),t("br"),t("br"),n._v("“Sư tỷ hẳn là đó có thể thấy được, tu vi của ta đã đi đến, Cổ Thánh tứ trọng đỉnh phong, khoảng cách đột phá, chỉ kém một bước cuối cùng...” Trương Huyền nói."),t("br"),t("br"),n._v("“Ngươi muốn cho ta giúp ngươi đột phá?” Bạch Nguyễn Khanh cau mày: “Cổ Thánh tứ trọng đến Hư Tiên, là nội môn cùng hạch tâm chênh lệch, giá trị, tuyệt không phải sáu mươi viên kiếm các tệ!”"),t("br"),t("br"),n._v("“Dĩ nhiên không phải chuyện này...” Trương Huyền cười cười: “Ta là kiến thức đạt đến bình cảnh, nội môn bên này tàng thư khố, tìm rất lâu, không tìm được muốn tìm nội dung, muốn hỏi một chút sư tỷ, có thể hay không mang ta đi một chuyến hạch tâm đệ tử tàng thư khố, không cần nhiều, chỉ nhìn ba canh giờ liền tốt...”"),t("br"),t("br"),n._v("“Đi tàng thư khố?”"),t("br"),t("br"),n._v("Còn tưởng rằng yêu cầu gì, nghe được là cái này, Bạch Nguyễn Khanh sững sờ."),t("br"),t("br"),n._v("Vừa rồi nàng tuy là nói, hỗ trợ đột phá, giá trị không chỉ sáu mươi kiếm các tệ, nhưng đối phương mãnh liệt yêu cầu, nàng khả năng vẫn như cũ sẽ hỗ trợ mua sắm phá chướng đan, để hắn đột phá, tỷ lệ lớn hơn một chút!"),t("br"),t("br"),n._v("Thậm chí, sẽ còn truyền thụ một ít đột phá kinh nghiệm..."),t("br"),t("br"),n._v("Đối phương những này đều không được tìm, chỉ là đi tàng thư khố đọc sách ba canh giờ?"),t("br"),t("br"),n._v("“Đổi một cái a, quá đơn giản!” Bạch Nguyễn Khanh lắc đầu."),t("br"),t("br"),n._v("“Mong rằng sư tỷ tác thành!” Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Đối với hắn mà nói, không có cái gì đọc sách bây giờ tới."),t("br"),t("br"),n._v("Chỉ cần có sách, đột phá tu vi, uống nước Bình thường đơn giản."),t("br"),t("br"),n._v("“Tốt!” Thấy hắn vẻ mặt thành thật, Bạch Nguyễn Khanh gật gật đầu, ánh mắt lộ ra vẻ tán thưởng."),t("br"),t("br"),n._v("Người người đều biết tu luyện, hiếm thấy người này có thể khu trừ xốc nổi, biết đọc sách, đúng là không đổi."),t("br"),t("br"),n._v("“Sư tỷ!”"),t("br"),t("br"),n._v("Thấy hai người nói xong, chuẩn bị rời đi, Lưu Lộ Kiệt hai người ngừng tu luyện, đi tới."),t("br"),t("br"),n._v("“Các ngươi đang nghiên cứu ‘Ta Rất Biết Điều’ kiếm chiêu?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("Nàng thua sau đó, trong lòng cũng yên lặng cân nhắc không biết bao nhiêu lần, phi hành Thánh thú hạ xuống xong, nhìn thấy hai người này ngay tại diễn luyện, chỗ nào không hiểu."),t("br"),t("br"),n._v("“Là!”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt gật đầu thừa nhận."),t("br"),t("br"),n._v("“Nhưng có thu hoạch gì?” Bạch Nguyễn Khanh cười cười."),t("br"),t("br"),n._v("“Có một chút thu hoạch, cũng có chút nghi ngờ, vừa định hướng Bạch sư tỷ lĩnh giáo!”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt vội nói."),t("br"),t("br"),n._v("Hắn mặc dù là nội môn đệ nhất nhân, nhưng trước mắt này vị, nắm giữ tông môn tam đại trưởng lão một trong Bạch Diệp trưởng lão làm gia gia, bản thân càng là nội môn đệ tử, tu vi càng có khả năng đạt đến Chân Tiên cảnh... Đối kiếm thuật lý giải, khẳng định mạnh hơn hắn không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Mời nói!” Bạch Nguyễn Khanh gật đầu."),t("br"),t("br"),n._v("“ ‘Ta Rất Biết Điều’ thi triển một chiêu này đẩy ngang, đơn giản suôn sẻ, thoạt nhìn tốc độ không phải rất nhanh, nhưng lại làm cho không người nào có thể tránh né, ta cùng Vương Kiệm Đông thương nghị rất lâu, đều không có lý giải đến cùng thế nào thi triển...”"),t("br"),t("br"),n._v("Lưu Lộ Kiệt trường kiếm trong tay lắc một cái, thi triển ra một động tác, liên tục thi triển ba lần, đều có chút dở dở ương ương, dường như, làm sao đều không nghĩ ra."),t("br"),t("br"),n._v("Trương Huyền tràn đầy im lặng."),t("br"),t("br"),n._v("Chiêu này cũng không phải là kiếm pháp, mà là hắn tiện tay thi triển ra chiêu số, trường kiếm đặt ngang, hướng về phía trước quét ngang, vừa vặn có thể đem dựng thẳng công kích tới đến kiếm khí, toàn bộ chém xuống, chiêu số mười phần đơn giản, có thể đem bị động chuyển hóa thành chủ động, đem phòng ngự biến thành tấn công."),t("br"),t("br"),n._v("Tiện tay thi triển ra, đối phương nghiên cứu thời gian dài như vậy... Còn không có hiểu rõ..."),t("br"),t("br"),n._v("Vừa rồi bản thân còn khen thiên tài đây..."),t("br"),t("br"),n._v("“Chiêu này tập hợp phòng ngự cùng tấn công làm một thể, là vô thượng cao minh kiếm thuật, ngươi lý giải không được cũng rất bình thường...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh mở miệng, nói “Đừng nói ngươi, cho dù là ta, đặc biệt cùng phong già thảo luận qua, đến bây giờ vẫn như cũ hiểu biết lơ mơ! Chẳng qua, cố hết sức, có thể thi triển đi ra!”"),t("br"),t("br"),n._v("Nói xong, cổ tay khẽ đảo, một thanh trường kiếm xuất hiện tại lòng bàn tay, nhẹ nhàng lắc một cái, đẩy ngang mà ra."),t("br"),t("br"),n._v("Tuy là không quá giống, nhưng cũng có ba, bốn phần hình dáng."),t("br"),t("br"),n._v("“Chiêu kiếm pháp này, cũng không phải là đặc biệt kiếm chiêu, rõ ràng là vị này ‘Ta Rất Biết Điều’ tiện tay thi triển ra!”"),t("br"),t("br"),n._v("Đem phong già nói, thuật lại một lần, Bạch Nguyễn Khanh trong mắt mang theo hưng phấn: “Tiện tay thi triển cứ như vậy lợi hại, để cho người ta khó mà đoán, thật không biết vị này ‘Ta Rất Biết Điều’, đến cùng đạt đến cảnh giới gì, trong các ngươi cửa nếu như điều tra ra cụ thể là ai, nhất định phải trước tiên nói cho ta biết... Loại thiên tài này, là ta tôn kính nhất cùng sùng bái thần tượng, cho nên, ta... Nhất định phải gả cho hắn!”."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1984"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1986"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);