(window.webpackJsonp=window.webpackJsonp||[]).push([[1110],{1465:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1973-bach-diep-truong-lao-muon-thu-đo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1973-bach-diep-truong-lao-muon-thu-đo"}},[n._v("#")]),n._v(" Chương 1973: Bạch Diệp trưởng lão muốn thu đồ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Gả?”"),t("br"),t("br"),n._v("Da mặt co lại, Trương Huyền kìm lòng không được lui về sau một bước, suýt chút nữa không có dọa chết tươi."),t("br"),t("br"),n._v("Đại tỷ, chuyện cười không phải lái như vậy!"),t("br"),t("br"),n._v("Ta trái tim chịu không được..."),t("br"),t("br"),n._v("Nói thật, vị này Bạch Nguyễn Khanh cũng không xấu, ngược lại rất đẹp, đặc biệt là cặp chân dài kia, thẳng đứng thon dài, có thể... Hắn là có bạn gái người, huống chi vị này chính là nữ bạo long, hơi có không hài lòng liền đánh một trận... Người sắt cũng gánh không được ah!"),t("br"),t("br"),n._v("Chí ít, hắn nhưng không có bị tra tấn khuynh hướng!"),t("br"),t("br"),n._v("“Ngươi làm sao vậy?”"),t("br"),t("br"),n._v("Gặp hắn ngạc nhiên biến sắc, giống như là giống như gặp quỷ, đang đắm chìm trong tôn sùng bên trong Bạch Nguyễn Khanh, nghi ngờ nhìn lại."),t("br"),t("br"),n._v("Ta muốn gả cho “Ta Rất Biết Điều”, ngươi ở đây lại lui lại kinh hoàng có ý tứ gì?"),t("br"),t("br"),n._v("“Không có gì...” Lau lau mồ hôi lạnh trên đầu, Trương Huyền xấu hổ cười một tiếng, trong lòng âm thầm quyết định, nhất định phải bảo vệ “Ta Rất Biết Điều” cái thân phận này, ngàn vạn không thể tiết lộ!"),t("br"),t("br"),n._v("“Chiêu kiếm pháp này, Phong lão cùng ta nói rõ chi tiết qua, hẳn là như vậy...”"),t("br"),t("br"),n._v("Không để ý tới hắn khiếp sợ, Bạch Nguyễn Khanh giải thích vài câu, dính dáng vừa rồi chiêu này bí ẩn, Lưu Lộ Kiệt đám người bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Nghe một hồi, Trương Huyền cố nén muốn phun ra xúc động, quay đầu đi, mắt nhìn mũi mũi nhìn tâm, giả bộ như cái gì đều nghe không được."),t("br"),t("br"),n._v("Bản thân tốt như vậy kiếm thuật, bị lý giải thành như vậy... Tiếp tục nghe tiếp, thật không biết có thể hay không không nhẫn nại được muốn đánh người!"),t("br"),t("br"),n._v("Nói thật, nếu không phải thực lực không đủ, nếu không, khẳng định sớm đã đem Lăng Vân kiếm các khai phái tổ sư vách quan tài cạy mở nhìn một chút, làm sao bồi dưỡng được như vậy một đám hậu nhân."),t("br"),t("br"),n._v("“Quên đi, thực sự không được, tìm cơ hội, cho bọn hắn hảo hảo chỉ điểm đi...”"),t("br"),t("br"),n._v("Đứng tại chỗ đợi một hồi, Bạch Nguyễn Khanh rốt cục giảng xong, một mặt thỏa mãn nhảy lên lưng thú, Trương Huyền theo sát tới."),t("br"),t("br"),n._v("Hạch tâm đệ tử sơn phong, trong khoảng cách môn không tính quá xa, nửa đoạn cắm vào trong mây trắng, tiên khí lượn lờ, cho người ta một loại thế ngoại đào nguyên cảm giác."),t("br"),t("br"),n._v("Ngồi tại lưng thú, Trương Huyền cảm thụ một chút, nơi này linh khí rõ ràng so nội môn sơn phong càng thêm mạnh mẽ, tu luyện càng gia sự hơn gấp rưỡi."),t("br"),t("br"),n._v("“Phía trước chính là tàng thư khố, đây là đại biểu thân phận ta lệnh bài, ngươi cầm liền có thể đi vào... Ta sau ba canh giờ, tới đón ngươi!”"),t("br"),t("br"),n._v("Thánh thú rơi vào một cái to lớn tàng thư các phía trước, Bạch Nguyễn Khanh bàn giao."),t("br"),t("br"),n._v("Nàng mệnh danh nữ bạo long, đọc sách loại chuyện này, từ trước tới nay không có gì kiên nhẫn, cùng hắn ở chỗ này chờ, còn không bằng mau chóng đem thuốc, cho gia gia cầm tới, cũng có thể để hắn ít nhận bị thương đau."),t("br"),t("br"),n._v("“Đa tạ!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đối phương không đi theo không còn gì tốt hơn, dù sao hắn đọc sách phương thức, cùng tất cả mọi người khác biệt."),t("br"),t("br"),n._v("Cùng nội môn đệ tử tàng thư khố đồng dạng, chỉ cần giao nộp đủ kiếm các tệ, liền có thể đi vào."),t("br"),t("br"),n._v("Nội môn là bốn cái kiếm các tệ một canh giờ, mà ở trong đó trọn vẹn tăng gấp mười lần, đạt đến bốn mươi kiếm các tệ một giờ."),t("br"),t("br"),n._v("Bất quá, hắn hiện tại tài đại khí thô, cũng không quan tâm điểm ấy, trực tiếp cho 120 viên, định cái ba canh giờ."),t("br"),t("br"),n._v("Hạch tâm đệ tử tàng thư khố, ghi lại công pháp và kiếm pháp, quả nhiên muốn so nội môn ảo diệu không biết bao nhiêu, Hư Tiên cảnh tu luyện công pháp, thình lình xuất hiện, Trương Huyền hai mắt sáng lên, từng hàng đảo qua đi, đem thư tịch thu sạch ghi chép tại thư viện."),t("br"),t("br"),n._v("Bất kể như thế nào, công pháp và tu vi mới là trọng yếu nhất."),t("br"),t("br"),n._v("Chỉ cần có thể hình thành Thiên đạo pháp quyết, dựa vào hiện tại của cải, tăng cao tu vi, sẽ mười phần đơn giản."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh trở lại cư trú sân nhỏ, đi tới tựa vào trên giường vẫn như cũ có chút hư nhược Bạch Diệp trưởng lão trước mặt."),t("br"),t("br"),n._v("“Gia gia, thuốc mua được...”"),t("br"),t("br"),n._v("Nói xong, đem vừa được ba cái bình ngọc đưa tới."),t("br"),t("br"),n._v("“Lần trước cho ta ăn chính là cái này thuốc?”"),t("br"),t("br"),n._v("Nhận lấy bình ngọc, Bạch Diệp trưởng lão mở ra nắp bình, nhìn kỹ một hồi, có chút mê man."),t("br"),t("br"),n._v("Tông môn vô số y sư, chế biến ra vô số trân quý đan dược, đều đối với hắn thương thế không có tác dụng gì, còn tưởng rằng có thể làm cho hắn thức tỉnh, sẽ là thứ càng quý giá, không nghĩ tới, vậy mà cùng nước sạch đồng dạng, một điểm linh khí đều không cảm ứng được!"),t("br"),t("br"),n._v("Thậm chí, liền một điểm dược lực đều phát giác không ra!"),t("br"),t("br"),n._v("Cái này... Thật hữu dụng?"),t("br"),t("br"),n._v("“Thoạt nhìn không đáng chú ý, lại có thể chữa khỏi thương thế của ngươi, nhanh ăn vào đi!” Gặp hắn bộ dáng này, Bạch Nguyễn Khanh cười cười."),t("br"),t("br"),n._v("Lúc trước, nàng cũng không nhận ra được, nếu không phải cái gì cũng có thể thử khi tuyệt vọng, chắc chắn sẽ không tin tưởng một cái nội môn đệ tử dược vật, có thể trị liệu trưởng lão trên người thương thế."),t("br"),t("br"),n._v("“Ta thử một chút!”"),t("br"),t("br"),n._v("Đã sớm nghe Bạch Phong cùng cháu gái nói qua, Bạch Diệp trưởng lão không chần chờ nữa, đem bình ngọc bưng lên, ngửa đầu uống một bình."),t("br"),t("br"),n._v("“Dược dịch” đi vào cổ họng, lập tức cảm thấy trong cơ thể một mực bất lực thương thế, xuất hiện giảm bớt, không ngừng hư thối nội tạng lực lượng, trở nên càng thêm mỏng manh."),t("br"),t("br"),n._v("“Thật lợi hại...”"),t("br"),t("br"),n._v("Con mắt sáng lên, Bạch Diệp trưởng lão lại không nghi ngờ, đem còn lại hai bình cũng mở ra, nuốt vào."),t("br"),t("br"),n._v("“Thế nào?” Nhìn gia gia liên tục nuốt vào ba bình, Bạch Nguyễn Khanh mang theo khẩn trương nhìn qua."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Không có trả lời, nằm ở trên giường Bạch Diệp trưởng lão bàn tay nhẹ nhàng nhấn một cái, cả người huyễn ảnh đồng dạng xuất hiện trong sân ở giữa, lòng bàn tay nhiều ra một thanh trường kiếm."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Kiếm khí gào thét, trong viện linh khí, tụ đến, tựa như gió lốc, một chiêu tiếp lấy một chiêu, trường kiếm tại Bạch Diệp trưởng lão trong tay, tựa như nước chảy mây trôi đồng dạng, nhìn dáng vẻ của hắn, chỗ nào giống bị thương!"),t("br"),t("br"),n._v("“Gia gia, ngươi khỏe hẳn?”"),t("br"),t("br"),n._v("Nhìn thấy vừa mới còn nằm ở trên giường không cách nào động đậy lão nhân, chạy đến trong viện luyện kiếm, Bạch Nguyễn Khanh hốc mắt; Lập tức đỏ lên."),t("br"),t("br"),n._v("“Khoảng cách khỏe hẳn, còn kém một ít, có điều, trước đó không cách nào loại bỏ lực lượng, đã triệt để loại bỏ, chỉ cần hảo hảo điều dưỡng, trong một tháng, hẳn là có thể khôi phục như lúc ban đầu!” Dừng lại, Bạch Diệp trưởng lão trong mắt khó mà kìm lại hưng phấn."),t("br"),t("br"),n._v("Bị thương nặng sau đó, bất cứ lúc nào cũng sẽ tử vong, vốn cho rằng kiếp này liền như thế xong, không nghĩ tới loại này thoạt nhìn không chút nào thu hút dược dịch, nắm giữ kỳ lạ như vậy công hiệu!"),t("br"),t("br"),n._v("“Chúc mừng gia gia!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh vội nói."),t("br"),n._v("“Bán ngươi thuốc người này, đối ta có ân cứu mạng! Không biết bây giờ, người ở nơi nào, ta muốn gặp mặt!” Lại tu luyện một hồi, cảm giác tinh khí thần phục hồi từ từ, Bạch Diệp trưởng lão lúc này mới dừng lại, quay đầu hỏi."),t("br"),t("br"),n._v("“Là cái nội môn đệ tử, ta vừa rồi đưa đến hạch tâm đệ tử tàng thư các!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Tàng thư các?”"),t("br"),t("br"),n._v("Thấy gia gia không hiểu, Bạch Nguyễn Khanh đem Trương Huyền yêu cầu nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Lấy ra lợi hại như vậy dược vật, chỉ yêu cầu đọc sách...” Bạch Diệp trưởng lão, có chút không dám tin tưởng: “Thực lực cao nhất niên đại, còn muốn đọc sách, không có nông nổi chi tâm... Còn có loại người này?”"),t("br"),t("br"),n._v("“Vâng!” Bạch Nguyễn Khanh gật đầu."),t("br"),t("br"),n._v("Đối phương chỉ yêu cầu đọc sách, nói thật, nàng cũng là rất khiếp sợ."),t("br"),t("br"),n._v("Tốt như vậy cơ hội, đổi lại người khác, tuỳ tiện nhắc tới điểm yêu cầu, đi vào hạch tâm, hẳn là dễ như trở bàn tay."),t("br"),t("br"),n._v("“Đi tàng thư khố đi xem một chút...” Lại tỉ mỉ hỏi thăm vài câu, Bạch Diệp trưởng lão gật gật đầu, vừa nói xong, đột nhiên dừng lại một chút: “Trước không vội, a phong!”"),t("br"),t("br"),n._v("“Lão gia!” Bạch Phong đi tới trước mặt."),t("br"),t("br"),n._v("“Nơi này có một cái dịch dung đan, sau khi phục dụng, có thể duy trì một canh giờ, để ngươi biến thành mặt khác một bức hình dáng, đem tu vi áp chế đến cùng vị bằng hữu kia đồng dạng, tìm một cơ hội thăm dò một chút...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Bạch Diệp trưởng lão trên tay nhiều ra một cái bình ngọc."),t("br"),t("br"),n._v("“Thăm dò?”"),t("br"),t("br"),n._v("Nhận lấy đan dược, Bạch Phong ánh mắt sáng lên: “Lão gia, đây là... Tính toán thu hắn làm đồ?”"),t("br"),t("br"),n._v("Thân là tông môn trưởng lão, không có khả năng vô duyên vô cớ đi dò xét đệ tử, huống chi đối phương có ân!"),t("br"),t("br"),n._v("Làm như vậy, chỉ có một cái mục đích, đó chính là... Tính toán thu đồ!"),t("br"),t("br"),n._v("“Biết nguyễn khanh mua sắm, không có nhân cơ hội vơ vét, đức hạnh không sai. Lại thêm, cứu mạng ta, chỉ là để hắn đi vào tàng thư khố, mà thờ ơ mà nói, mạng của ta, chẳng phải quá không đáng tiền?” Hai tay chắp sau lưng, Bạch Diệp trưởng lão thản nhiên nói."),t("br"),t("br"),n._v("Bất kể nói thế nào, hắn đều là Lăng Vân kiếm các tam đại trưởng lão một trong, toàn bộ đại lục đều tên tuổi lẫy lừng nhân vật đứng đầu."),t("br"),t("br"),n._v("Được người cứu tính mạng, chỉ làm điểm ấy, truyền đi, không đủ mất mặt."),t("br"),t("br"),n._v("“Lão gia ý tứ ta rõ ràng!”"),t("br"),t("br"),n._v("Bạch Phong cười cười, biết đối phương dụng tâm lương khổ: “Hãm Không chi thành thương thế, từ trước tới nay không cách nào trị liệu, hiện tại hắn bán ra thuốc đem lão gia chữa khỏi, coi như thật dược vật sử dụng hết, đối với những khác người cũng là hấp dẫn rất lớn, lão gia là sợ hắn bởi vậy gặp tai họa, mới thu làm đệ tử, cho cái danh phận! Xem như một loại che chở!”"),t("br"),t("br"),n._v("Hãm Không chi thành, bảo vật nhiều vô kể, là vô số cường giả hướng về chỗ, nhưng nguy hiểm cũng cực lớn, tiến vào bên trong, có thể sống đi ra, trăm không còn một, coi như sống sót, cũng cơ bản đều sẽ bị thương nặng không y, cuối cùng nuốt hận mà chết."),t("br"),t("br"),n._v("Mấy ngàn năm nay, Di Khí đại lục, vô số hạng người kinh tài tuyệt diễm đều thử qua, không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("Lúc này, một khi đối phương nắm giữ có thể chữa thương dược vật tin tức truyền đi, khẳng định sẽ trở thành vô số người mơ ước đối tượng, đến lúc đó nguy hiểm không nhỏ."),t("br"),t("br"),n._v("Thu làm đệ tử, người khác coi như muốn động, cũng phải nhìn Bạch trưởng lão mặt mũi, xem như đem một hồi tai họa, tiêu trừ trong vô hình."),t("br"),t("br"),n._v("Hít sâu một hơi, Bạch Diệp trưởng lão vẻ mặt xa xăm: “Tuy là trong Kiếm các tương đối an toàn, nhưng lòng dạ khó lường không ít, vẫn là muốn cẩn thận một chút! Như vậy, cũng coi như báo đáp ân cứu mạng, không đến mức làm cho đối phương bởi vậy hoạch tội! Đương nhiên, điều kiện tiên quyết là, thực lực đủ! Đối kiếm đạo lý giải, nếu như quá yếu, thu làm đệ tử, ta sẽ thật mất mặt.”"),t("br"),t("br"),n._v("“Vâng, ta vậy thì đi làm!”"),t("br"),t("br"),n._v("Gật gật đầu, Bạch Phong thân ảnh run rẩy, biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("“Gia gia, thật muốn thu hắn làm đồ?”"),t("br"),t("br"),n._v("Đem hối đoái nghe vào trong tai, Bạch Nguyễn Khanh đầy mặt rầu rĩ: “Như vậy... Ta chẳng phải so với hắn đến muộn bối phận?”"),t("br"),t("br"),n._v("“Ừm, cho tới nay, ta đều không có thu qua đệ tử, chỉ mong vị này, có thể đi qua Bạch Phong khảo hạch!” Nhìn thấy cháu gái ăn quả đắng, Bạch Diệp trưởng lão cười cười: “Chỉ cần thành công, liền là của ngươi sư thúc! Thân là vãn bối, đừng làm loạn, ngươi ở hạch tâm gây ra sự tình, ta nhưng mà đã nghe qua không ít!”"),t("br"),t("br"),n._v("“Sư thúc...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nghiến răng."),t("br"),t("br"),n._v("Vốn đang cho rằng gia hoả kia, có chút thật thà đáng yêu, vừa nghĩ tới tuổi tác so với chính mình nhỏ, muốn trở thành sư thúc, liền tức giận không đánh một chỗ tới."),t("br"),t("br"),n._v("“Gia gia, ngươi nếu không có chuyện gì, ta trước đi tu luyện...”"),t("br"),t("br"),n._v("Nhãn châu xoay động, Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Đi đi!”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão khoát tay áo."),t("br"),t("br"),n._v("Vừa mới khôi phục, thân thể của hắn vẫn còn có chút suy yếu, cần cẩn thận điều dưỡng."),t("br"),t("br"),n._v("Rời đi sân nhỏ, Bạch Nguyễn Khanh ngoan ngoãn hình dáng lập tức thay đổi, một đôi răng nanh từ bờ môi lộ ra, ánh mắt lộ ra hung dữ chi ý: “Trước đi giáo huấn hắn một trận... Cùng lắm thì trước mặt gia gia xưng hô sư thúc, lúc không có người, để hắn gọi ta sư tỷ!”"),t("br"),t("br"),n._v("Nàng được xưng là nữ bạo long, từ nhỏ đến lớn, chỗ nào thua thiệt qua, đối một cái so với chính mình tu vi thấp, so với chính mình nhỏ bé người kêu sư thúc... Thực sự nhẫn không dưới!"),t("br"),t("br"),n._v("Quản hắn là ai, trước dạy dỗ lại nói..."),t("br"),t("br"),n._v("Dù sao gia gia cũng muốn khảo hạch, coi như trước thời hạn..."),t("br"),t("br"),n._v("Phi hành Thánh thú lần nữa rơi vào tàng thư khố phía trước, dựa vào thân phận của nàng, không cần lệnh bài, liền có thể trực tiếp đi vào."),t("br"),t("br"),n._v("“Hắn không phải nói đang đọc sách ư?”"),t("br"),t("br"),n._v("Đi vào cửa phòng, Bạch Nguyễn Khanh nhíu nhíu mày."),t("br"),t("br"),n._v("Toàn bộ tàng thư khố, yên tĩnh dị thường, thật giống không người đến qua, không nghe được lật sách đọc sách âm thanh."),t("br"),t("br"),n._v("Dọc theo giá sách, đi vào trong, đi hơn phân nửa, lúc này mới phát hiện người thanh niên kia, đang đứng tại một hàng giá sách phía trước, trong tay tức không có cầm sách, cũng không nhìn tới, mà là theo thư tịch phương hướng, không ngừng đung đưa đầu."),t("br"),t("br"),n._v("“Ngươi đang làm gì?”"),t("br"),t("br"),n._v("Nhìn một hồi, thấy cái tên này, không có chút nào đem sách lấy ra ý tứ, Bạch Nguyễn Khanh nhịn không được."),t("br"),t("br"),n._v("“A, ngươi đã đến...”"),t("br"),t("br"),n._v("Thấy là nàng, Trương Huyền cười cười: “Ta đang tìm kiếm muốn tìm thư tịch...”"),t("br"),t("br"),n._v("Không hổ là hạch tâm đệ tử tàng thư khố, thư tịch số lượng rất nhiều, đều thời gian dài như vậy, cũng mới thu nhận một nửa không đến."),t("br"),t("br"),n._v("Xem ra, thu sạch chép xong, khả năng thật đúng là cần ba canh giờ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1985"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1987"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);