(window.webpackJsonp=window.webpackJsonp||[]).push([[1111],{1466:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1974-đap-choang-bach-nguyen-khanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1974-đap-choang-bach-nguyen-khanh"}},[n._v("#")]),n._v(" Chương 1974: Đập choáng Bạch Nguyễn Khanh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Cái này tàng thư các, ta tới qua nhiều lần lắm rồi, ngươi cần tìm dạng gì thư tịch, nói với ta, ta giúp ngươi tìm!”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("Ở đây không có cách nào động thủ, đối phương một mực tìm đi xuống, cũng không thể một mực đi theo đi!"),t("br"),t("br"),n._v("“Ta cũng là tuỳ ý chọn chọn, cũng chưa có xác định muốn tìm quyển sách nào, không cần phải để ý đến ta, tuỳ ý nhìn một chút, sau ba canh giờ, liền ra ngoài...”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("Thật vất vả tới đây một lần, đương nhiên là đem tất cả thư tịch thu sạch ghi chép đi, như vậy đối công pháp, võ kỹ, đều sẽ có tăng lên cực lớn."),t("br"),t("br"),n._v("“Nếu không... Ngươi tới đây một chút!”"),t("br"),t("br"),n._v("Thấy kẻ trước mắt này, đang khi nói chuyện con mắt đã quét qua trước mặt giá sách, đi vào tiếp theo hàng, như vậy toàn bộ tìm hết, không biết năm nào tháng nào, Bạch Nguyễn Khanh có chút kìm nén không được."),t("br"),t("br"),n._v("Nàng từ trước tới nay không có kiên nhẫn, tại bực này một, hai canh giờ, đầu đều sẽ nổ tung."),t("br"),t("br"),n._v("“Đi đâu?”"),t("br"),t("br"),n._v("Thấy nàng biểu lộ cổ quái, Trương Huyền hơi nghi hoặc một chút nhìn qua: “Chuyện gì?”"),t("br"),t("br"),n._v("“Liền đến bên kia tĩnh thất!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh một chỉ trong Tàng Thư các dùng để đọc sách, tu luyện tĩnh thất."),t("br"),t("br"),n._v("“Tĩnh thất?” Trương Huyền rầu rĩ một chút: “Cô nam quả nữ... Không tốt lắm đâu?”"),t("br"),t("br"),n._v("Lông mày nhảy dựng, Bạch Nguyễn Khanh có loại muốn làm tràng đem cái này hai hàng bóp chết xúc động: “Chỉ là hỏi thăm ngươi một ít trong vấn đề tu luyện...”"),t("br"),t("br"),n._v("“Ah!” Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Hắn nhưng là rất chú ý tiết tháo, ngàn vạn không thể ở đây mất đi."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Gặp hắn như trút được gánh nặng, Bạch Nguyễn Khanh suýt chút nữa không có khống chế lại, tại chỗ ra tay."),t("br"),t("br"),n._v("Bất kể nói thế nào, nàng đều là trong trăm có một tuyệt đỉnh mỹ nữ, toàn bộ hạch tâm, không biết bao nhiêu người muốn âu yếm, đơn độc ở chung... Bản thân hẹn đối phương đi tĩnh thất, cái tên này lại còn bộ dáng này..."),t("br"),t("br"),n._v("Ngươi có ý tứ gì?"),t("br"),t("br"),n._v("Cho rằng ta sẽ ăn ngươi ư?"),t("br"),t("br"),n._v("Sợ bị đối phương nhìn ra cái gì, cố nén sắp bùng nổ ngực, gấp rút hít thở hai cái, ép lại nội tâm lửa giận, đi đầu đi thẳng về phía trước."),t("br"),t("br"),n._v("Tuy là cùng cái tên này tiếp xúc ít, nhưng cũng coi như kiến thức, mỗi nói một câu, liền có thể làm người ta tức chết, đáng đời độc thân!"),t("br"),t("br"),n._v("Đi vào tĩnh thất, đợi Trương Huyền cũng sau khi tiến vào, “Kẹt kẹt!” Một tiếng đóng cửa lại."),t("br"),t("br"),n._v("“Làm gì?”"),t("br"),t("br"),n._v("Che ngực cùng cổ áo, Trương Huyền một mặt cảnh giác."),t("br"),t("br"),n._v("“...” Mắt tối sầm lại, Bạch Nguyễn Khanh nghiến răng nhịn xuống, đem bên trong căn phòng cách âm cấm chế kích hoạt, cái này mới nhìn lại: “Ta muốn hỏi hỏi, ngươi tên là gì, cụ thể tu vi cao bao nhiêu?”"),t("br"),t("br"),n._v("“Trên đường ta không phải đã nói rồi sao? Ta tên Trương Huyền! Tu vi hiện tại là phá toái hư không đỉnh phong!” Trương Huyền giải thích nói."),t("br"),t("br"),n._v("Tới hạch tâm sơn mạch thời điểm, đối phương hỏi thăm qua, nghĩ đến cũng không có gì có thể che giấu, liền đem tên thật nói."),t("br"),t("br"),n._v("Còn tu vi, tu vi cao một cái liền có thể nhìn thấu, càng không cần tận lực giấu diếm."),t("br"),t("br"),n._v("“Phá toái hư không đỉnh phong đúng không...”"),t("br"),t("br"),n._v("Răng ngà cắn chặt, Bạch Nguyễn Khanh toàn thân tu vi, lập tức như thủy triều hạ xuống, sau một khắc đã giống như hắn, đều là phá toái hư không đỉnh phong: “Ta hiện tại đang áp chế tu vi giống như ngươi, cùng ngươi tỷ thí một trận! Thua đối phương, từ hôm nay trở đi, muốn đối người thắng, nói gì nghe nấy, không cho phép nuốt lời!”"),t("br"),t("br"),n._v("“Tỷ thí?” Trương Huyền trượng nhị hòa thượng không hiểu rõ nổi."),t("br"),t("br"),n._v("Còn tưởng rằng là thèm nhỏ dãi tại sắc đẹp của hắn, không nghĩ tới muốn tỷ võ."),t("br"),t("br"),n._v("Chỉ là... Cái này nữ bạo long, lại tái phát cái gì thần kinh?"),t("br"),t("br"),n._v("“Không sai!” Bạch Nguyễn Khanh gật đầu."),t("br"),t("br"),n._v("“Nói gì nghe nấy... Chẳng lẽ ngươi muốn ta tự sát, ta cũng phải tuân thủ?” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Tuy là cùng cấp bậc chắc thắng, loại này cược cũng không muốn đánh."),t("br"),t("br"),n._v("Biết điều mới là bổn phận của hắn, là vương đạo!"),t("br"),t("br"),n._v("“Yên tâm, ta sẽ không để cho ngươi tự sát, muốn ngươi mặc kệ về sau thân phận gì... Đều muốn gọi ta là sư tỷ! Giống cái khác sư đệ đồng dạng, đối ta tôn trọng, nghe mệnh lệnh của ta!” Hoạt động một chút, Bạch Nguyễn Khanh toàn thân xương cốt “Kẽo kẹt! Kẽo kẹt” vang vọng."),t("br"),t("br"),n._v("Không hiểu ra sao, Trương Huyền lắc đầu: “Ta hiện tại liền xưng hô ngươi là sư tỷ ah... Ta thật muốn xem sách...”"),t("br"),t("br"),n._v("đọｃ ngantruyen.com"),t("br"),n._v("Nói xong xoay người muốn đi ra gian phòng."),t("br"),t("br"),n._v("Hắn hiện tại là “Nội môn đệ tử”, nhìn thấy hạch tâm đệ tử, khẳng định muốn xưng hô sư tỷ!"),t("br"),t("br"),n._v("“Chạy đi đâu!”"),t("br"),t("br"),n._v("Một tiếng khẽ kêu, chẳng muốn phí lời, Bạch Nguyễn Khanh trường kiếm xuất hiện tại lòng bàn tay, nhẹ nhàng lắc một cái, thẳng tắp đối Trương Huyền phía sau lưng liền đâm tới."),t("br"),t("br"),n._v("Quản hắn thừa nhận không thừa nhận, trước thu phục lại nói!"),t("br"),t("br"),n._v("Kiếm khí không ngừng phụt ra hút vào, đem tu vi áp chế đến phá toái hư không đỉnh phong, so Cổ Thánh nhất trọng, có thể thi triển ra càng nhiều kiếm pháp, lực lượng cũng càng thuận buồm xuôi gió, kiếm chiêu vừa ra, so Thông Thần điện lúc, mạnh mẽ không biết gấp bao nhiêu lần, trong nháy mắt liền đem Trương Huyền tất cả đường lui, toàn bộ đóng kín."),t("br"),t("br"),n._v("Nàng cũng không muốn giết người, mà là muốn đánh đối phương thần phục, về sau không dám lấy “Sư thúc” thân phận mệnh lệnh bản thân."),t("br"),t("br"),n._v("Không nghĩ tới nàng, trực tiếp ra tay, Trương Huyền tràn đầy im lặng."),t("br"),t("br"),n._v("Khó trách được người xưng là nữ bạo long, quả nhiên không theo đạo lý nào."),t("br"),t("br"),n._v("Nhẹ nhàng run rẩy, từ tất cả trong kiếm mang né tránh ra đến, Trương Huyền lắc đầu: “Sư tỷ, ta thật không phải là đối thủ của ngươi, không cần tiếp tục so!”"),t("br"),t("br"),n._v("“Ừm?” Thấy mình dùng ra hung mãnh như vậy kiếm chiêu, nhẹ nhõm tránh thoát, chẳng những không có mảy may sợ hãi, còn có nhàn tâm nói chuyện, Bạch Nguyễn Khanh sầm mặt lại: “Đem kiếm của ngươi lấy ra, bảo ta xem một chút thực lực chân chính của ngươi, nếu không... Đừng trách ta không khách khí!”"),t("br"),t("br"),n._v("Sưu sưu sưu!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, liên tục ba kiếm, một chiêu so một chiêu nhanh, toàn bộ tĩnh thất, lập tức đều bị kiếm khí che kín."),t("br"),t("br"),n._v("Xây dựng tàng thư khố thời điểm, sợ hãi tu luyện giả xem xong sách, có lĩnh ngộ, thả ra lực lượng quá mạnh, hủy tàng thư, cố ý đem tĩnh thất xây dựng mười phần chắc chắn, bên trong trận pháp, cho dù là hạch tâm đệ tử, đều không thể tổn thương, bởi vậy, Bạch Nguyễn Khanh kiếm khí, mặc dù lợi hại, cũng không đâm rách mặt tường."),t("br"),t("br"),n._v("Trương Huyền lấp lóe, đem ba kiếm tránh thoát, một mặt chân thành: “Ngươi nhìn, ta thật không phải là đối thủ...”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Thấy nhanh như vậy kiếm pháp, đối phương nhẹ nhõm tránh thoát, còn bộ dáng này, Bạch Nguyễn Khanh có chút phát điên: “Nhanh lên một chút xuất kiếm, nếu không, ta động thủ thật...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Kiếm khí gào thét, trường kiếm tốc độ tấn công càng nhanh."),t("br"),t("br"),n._v("Nàng có thể tu luyện Trầm Tuyết kiếm pháp, chứng minh đối tốc độ khống chế đã đạt đến vô cùng cao thâm tình trạng, giờ phút này toàn lực thi triển, tĩnh thất xuất hiện từng đạo huyễn ảnh, dường như rơi vào trong đó, lại người mạnh đều chạy trốn không xong."),t("br"),t("br"),n._v("Nàng kiếm nhanh, bóng người càng nhanh, bất kể như thế nào công kích, đều giống như cùng đối phương kém một tia khoảng cách, bất luận trường kiếm vẫn là kiếm khí, đều không đả thương được mảy may."),t("br"),t("br"),n._v("“Ta không tin...”"),t("br"),n._v("Sắc mặt dần dần xanh lại, động tác càng nhanh, vốn cho rằng vị này Trương Huyền, sẽ trở nên lại không giống vừa rồi thong dong như vậy, lại thấy hắn ngáp một cái, bàn tay vỗ vỗ miệng ba, tiếp tục mở miệng: “Ta là thật đánh không lại ngươi ah, ngươi bảo làm sao rút kiếm? Đây không phải bức ta tự sát ư?”"),t("br"),t("br"),n._v("“Ah ah ah...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh muốn điên rồi."),t("br"),t("br"),n._v("Nếu như ngươi chật vật không chịu nổi, đầu đầy mồ hôi, nói đánh không lại, ta tin!"),t("br"),t("br"),n._v("Một bên ngáp, một bàn tay đập miệng, một tay gãi ngứa ngứa, thậm chí trên mặt còn mang theo ta có muốn hay không ngụy trang một chút sầu khổ... Nói như vậy, là đùa ta chơi ư?"),t("br"),t("br"),n._v("Tròng mắt đỏ hoe, kiếm pháp càng lúc càng nhanh, đối phương ngoài miệng nói không phải là đối thủ, thân ảnh lại giống như là bọt sóng bên trong thuyền, bất luận sóng lớn ngập trời, ta tự theo gió nhấp nhô, nước thủy chung không cách nào bao phủ."),t("br"),t("br"),n._v("“Xuất kiếm...”"),t("br"),t("br"),n._v("Liên tục hơn hai mươi chiêu, liền đối phương góc áo đều không có đụng phải, lại nhịn không được, Bạch Nguyễn Khanh triệt để đánh tơi bời: “Nếu không, chết đừng trách ta...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trường kiếm lắc một cái, mạnh nhất Trầm Tuyết kiếm pháp, lần nữa thi triển đi ra."),t("br"),t("br"),n._v("Nàng vốn là không có quá nhiều kiên nhẫn, nếu không, cũng không có khả năng liên tục đem mấy vị hạch tâm đệ tử đều đá bể."),t("br"),t("br"),n._v("Liên tục thi triển ra nhiều như vậy công kích, đều không có làm bị thương mảy may, vốn là không nhiều kiên nhẫn, bị triệt để tiêu hao hầu như không còn."),t("br"),t("br"),n._v("Đầy trời kiếm khí hình thành bông tuyết, bên trong căn phòng không khí, lập tức lạnh lẽo xuống, cho người ta một loại thấm nhuần tận xương cảm giác."),t("br"),t("br"),n._v("“Ai...”"),t("br"),t("br"),n._v("Dạo bước tại kiếm của đối phương chiêu bên trong, Trương Huyền xoa xoa mi tâm, nhịn không được thở dài một tiếng: “Ta thật rất muốn biết điều... Có thể tại sao phải bức ta?”"),t("br"),t("br"),n._v("Hắn chỉ muốn an tĩnh đọc sách, yên tĩnh tấn cấp, yên tĩnh làm một cái mỹ nam tử..."),t("br"),t("br"),n._v("Khó khăn như thế sao?"),t("br"),t("br"),n._v("Nhìn cái sách, chạy tới luận võ..."),t("br"),t("br"),n._v("Ta chỗ nào trêu chọc ngươi?"),t("br"),t("br"),n._v("Không phải muốn như vậy?"),t("br"),t("br"),n._v("Không phải phải làm như vậy?"),t("br"),t("br"),n._v("“Ngươi nói cái gì...”"),t("br"),t("br"),n._v("Gặp hắn lầm bầm lầu bầu, ngay cả mình mạnh nhất tuyệt chiêu, đều không thèm để ý chút nào, nhẹ nhõm tránh né, Bạch Nguyễn Khanh trái tim bỗng nhiên co rút lại, một cái ý nghĩ xông ra: “Chẳng lẽ ngươi là...”"),t("br"),t("br"),n._v("Cùng cấp bậc, nàng thi triển ra Trầm Tuyết kiếm pháp, Lưu Lộ Kiệt đều không chống đỡ được!"),t("br"),t("br"),n._v("Một cái nội môn không biết tên gia hỏa, không những không đả thương được, còn đi bộ nhàn nhã, thành thạo..."),t("br"),t("br"),n._v("Bảo nàng không tự chủ được nhớ tới, vị kia đưa nàng liên tục chém giết hai lần “Ta Rất Biết Điều”!"),t("br"),t("br"),n._v("Chẳng lẽ vị thần bí nhân kia, chính là trước mắt cái này a?"),t("br"),t("br"),n._v("Đang đang khiếp sợ, vừa định đem trong lòng suy đoán nói ra, liền nghe đến thanh niên trước mắt, lần nữa than thở: “Đã ẩn không che giấu nổi, chỉ có thể xấu hổ...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tiếng nói còn không có kết thúc, bàn tay lăng không đè ép xuống."),t("br"),t("br"),n._v("Cánh tay của hắn không dài, nhưng trong nháy mắt đã đột phá kiếm khí phong tỏa, đi tới phía trước, cảm giác áp bách mạnh mẽ, bảo Bạch Nguyễn Khanh hô hấp dồn dập, có chút không kịp thở."),t("br"),t("br"),n._v("“Không...”"),t("br"),t("br"),n._v("Không nghĩ tới phá toái hư không có thể thi triển ra lực lượng như vậy, Bạch Nguyễn Khanh đồng tử co rụt lại, lại không áp chế trong cơ thể tu vi."),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Một cái chớp mắt, tu vi liền từ phá toái hư không khôi phục lại Hư Tiên cảnh, nhanh chóng bay lên!"),t("br"),t("br"),n._v("Hư Tiên cảnh sơ kỳ, trung kỳ, hậu kỳ, đỉnh phong!"),t("br"),t("br"),n._v("Chân Tiên cảnh sơ kỳ!"),t("br"),t("br"),n._v("Cùng đoán đồng dạng, nàng quả nhưng đã đột phá Hư Tiên cảnh, đạt đến Chân Tiên cảnh!"),t("br"),t("br"),n._v("Chân Tiên, chân khí liên tục không ngừng, coi như tại Lăng Vân kiếm các, cũng có thể trở thành nội môn trưởng lão, được cho một phương cường giả."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tu vi của nàng khôi phục như lúc ban đầu, không để ý tới tiếp tục tiến công, thân ảnh nhanh chóng lui về phía sau, nhưng chẳng biết tại sao, bàn tay của đối phương, giống như là đã biết hướng đi của nàng đồng dạng, bất kể như thế nào né tránh, đều nghiền ép mà đến, để cho người ta không kịp trách né."),t("br"),t("br"),n._v("“Làm sao có thể? Ngươi thật chỉ là... Phá toái hư không đỉnh phong?”"),t("br"),t("br"),n._v("Da mặt không ngừng lay động, Bạch Nguyễn Khanh nhanh muốn điên rồi."),t("br"),t("br"),n._v("Thực lực của đối phương, nàng có thể rõ ràng cảm nhận được, chỉ có phá toái hư không đỉnh phong, cũng không tăng trưởng, có thể... Khôi phục Chân Tiên sơ kỳ cảnh giới nàng, vậy mà làm sao đều tránh không khỏi một chưởng này!"),t("br"),t("br"),n._v("Liền giống như, nàng tất cả chiêu số cùng thân pháp, đối phương rõ như lòng bàn tay!"),t("br"),t("br"),n._v("Tại sao có thể như vậy?"),t("br"),t("br"),n._v("Lăng Vân kiếm các, thiên tài vô số, quả thực có không ít người có thể vượt cấp khiêu chiến, có thể... Vượt qua một cái, hai cái tiểu cấp bậc, hiếm khi thấy, dù sao tất cả mọi người là cao thủ, tu luyện cũng là tương đồng công pháp và kiếm pháp!"),t("br"),t("br"),n._v("Phá toái hư không đối chiến Chân Tiên, bảo trong lòng mình sinh ra cảm giác bất lực..."),t("br"),t("br"),n._v("Rốt cục mạnh đến mức nào?"),t("br"),t("br"),n._v("“Ta không tin...”"),t("br"),t("br"),n._v("Trong cơ thể lực lượng lần nữa bạo phát, cả người sắp bùng nổ, Chân Tiên cảnh chân khí, kích động, tĩnh thất trận pháp tựa hồ cũng có chút không chịu nổi."),t("br"),t("br"),n._v("“Chớ phản kháng, ngoan...”"),t("br"),t("br"),n._v("Vang lên bên tai thanh niên thanh âm nhàn nhạt, bàn tay “Hô!” Một chút biến mất, Bạch Nguyễn Khanh chỉ cảm thấy mắt tối sầm lại."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Té ngã trên đất, ngất đi."),t("br"),t("br"),n._v("“Làm sao bây giờ?”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Trương Huyền thu tay lại mà đứng."),t("br"),t("br"),n._v("Đối phương một mực bức bách, không thi triển thi triển chiêu số, khẳng định sẽ bị đối phương yêu cầu làm một ít bản thân chuyện không muốn làm!"),t("br"),t("br"),n._v("Có thể xuất thủ, lại sẽ bị phát giác thân phận..."),t("br"),t("br"),n._v("Nếu là phổ thông đệ tử, giết chính là... Nhưng trước mắt này vị, là tam đại trưởng lão một trong cháu gái ruột, có cái sơ xuất, căn bản trốn không thoát ah!"),t("br"),t("br"),n._v("Chẳng lẽ, mới đi đến Lăng Vân kiếm các không đến một ngày, liền muốn biến thành toàn bộ kiếm các truy nã tồn tại?"),t("br"),t("br"),n._v("“Ưu tú người, cuối cùng có đếm không hết phiền não, ai...”"),t("br"),t("br"),n._v("Trương Huyền trong mắt tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1986"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1988"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);