(window.webpackJsonp=window.webpackJsonp||[]).push([[1112],{1467:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1975-nguoi-qua-yeu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1975-nguoi-qua-yeu"}},[n._v("#")]),n._v(" Chương 1975: Ngươi quá yếu!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nếu như không phải mạnh như vậy, nhận khuất nhục, liền bị, chỉ có thể chịu đựng."),t("br"),t("br"),n._v("Nhưng đối phương rõ ràng không phải là đối thủ, còn muốn để hắn chịu nhục, khẳng định không được ah!"),t("br"),t("br"),n._v("Nhưng bây giờ đánh ngất xỉu, hơi xử lý không tốt, phiền phức càng lớn!"),t("br"),t("br"),n._v("“Hai con đường, thứ nhất, giết, hủy thi diệt tích... Bất quá, đối phương đi vào tàng thư khố, lại cùng ta cùng một chỗ đi vào tĩnh thất, thật muốn biến mất, rất dễ dàng bị người tra ra!”"),t("br"),t("br"),n._v("“Thứ hai, ép nàng lập xuống khế ước, không cho phép nói ra bí mật! Nhìn tới chỉ có thể đi loại thứ hai...”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền ngón tay một điểm, một đạo Thiên đạo chân khí chui vào đối phương trong cơ thể, lần nữa nhẹ nhàng phất một cái, hôn mê nữ hài lập tức chậm rãi tỉnh lại."),t("br"),t("br"),n._v("“Ngươi là hạch tâm đệ tử, ta không muốn giết ngươi, nhưng ngươi phải thề, không thể...”"),t("br"),t("br"),n._v("Thấy nàng mở mắt, Trương Huyền đang muốn khống chế chui vào trong cơ thể đạo kia Thiên đạo chân khí, để hắn ngoan ngoãn nghe lời, chỉ thấy nữ hài trong mắt lóe lên vẻ hưng phấn, một ùng ục bò lên, quỳ mọp xuống đất: “Trương sư huynh... A, không đúng, Trương sư thúc, ngươi có thể hay không dạy ta kiếm pháp? Chỉ cần ngươi truyền thụ cho ta kiếm pháp, để cho ta làm cái gì cũng được...”"),t("br"),t("br"),n._v("“Sư thúc?” Trương Huyền ngẩn ngơ, muốn nói nuốt đến trong miệng."),t("br"),t("br"),n._v("Là cẩu a?"),t("br"),t("br"),n._v("Mặt trở nên có phần quá nhanh!"),t("br"),t("br"),n._v("Mới vừa rồi còn chém chém giết giết, nhất định muốn giết chết bản thân không thể, thời gian nháy mắt liền như thế..."),t("br"),t("br"),n._v("Làm cái gì?"),t("br"),t("br"),n._v("Lại nói... Sư thúc danh xưng này, ngươi từ nơi nào luận?"),t("br"),t("br"),n._v("Bất quá, mục đích của hắn là làm cho đối phương không nói ra bí mật của mình, chỉ cần có thể giữ miệng giữ mồm, xưng hô như thế nào cũng không đáng kể."),t("br"),t("br"),n._v("Trầm tư một chút, nói: “Ngươi thật muốn cho ta truyền thụ cho ngươi kiếm pháp?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh liên tục gật đầu."),t("br"),t("br"),n._v("Bị đập choáng trong nháy mắt, nàng liền xác nhận, trước mắt cái này, khẳng định là vị kia “Ta Rất Biết Điều”, cũng chỉ có vị này, tuỳ tiện phá giải nàng Trầm Tuyết kiếm pháp, thậm chí liền tu vi bạo tăng, đều không thèm quan tâm!"),t("br"),t("br"),n._v("Người này kiếm pháp, so Phong lão đều cường đại hơn không biết gấp bao nhiêu lần, có thể cùng hắn học tập, kiếm thuật nhất định biến chuyển từng ngày, không cần tiếp tục lo lắng, hạch tâm mấy vị biến thái, thậm chí vượt qua cũng chưa biết chừng!"),t("br"),t("br"),n._v("Loại cơ hội này, sao có thể bỏ lỡ!"),t("br"),t("br"),n._v("“Muốn cho ta truyền thụ cho ngươi kiếm chiêu cũng không phải không thể, nhưng ta có một điều kiện!”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển, thấy đối phương nói chân tâm thật ý, không có chút nào ngụy trang, Trương Huyền thở phào nhẹ nhõm, nói: “Ta làm người biết điều, không muốn đem thân phận chân thật của mình tiết lộ ra ngoài, ngươi nếu có thể đem bí mật này bảo vệ, ta có thể bảo đảm, để ngươi kiếm thuật, trong thời gian ngắn đại tăng, trở thành hạch tâm đệ nhất nhân, dễ như trở bàn tay!”"),t("br"),t("br"),n._v("“Sư thúc yên tâm...”"),t("br"),t("br"),n._v("Thấy bị nhìn thấu tâm tư, Bạch Nguyễn Khanh khuôn mặt đỏ lên, vội vàng đem sống lưng thẳng tắp, bàn tay giơ lên, ánh mắt lộ ra vẻ mặt ngưng trọng: “Ta Bạch Nguyễn Khanh ở đây đối Thần Linh thề, tuyệt không tiết lộ sư thúc nửa điểm tin tức, như có làm trái, nguyện bị vạn tiễn xuyên tâm mà chết!”"),t("br"),t("br"),n._v("Thấy nàng chủ động thề, hơn nữa như vậy chính thức, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Đi tới Thượng Thương trong khoảng thời gian này, cũng coi như biết rõ."),t("br"),t("br"),n._v("Đối Thần Linh thề, là không ai dám làm trái, một khi vứt bỏ, vô cùng có khả năng bị Thần Linh thăm dò, từ đó hạ xuống trừng phạt."),t("br"),t("br"),n._v("Đương nhiên, chỉ là như vậy nói, cụ thể thế nào, liền không được biết rồi."),t("br"),t("br"),n._v("“Trước đó, ngươi cho Lưu Lộ Kiệt giảng giải chiêu kia trường kiếm quét ngang không đúng, ta hiện tại nói với ngươi một chút...”"),t("br"),t("br"),n._v("Trương Huyền đem trước kìm nén khó chịu lời nói, nói một lần."),t("br"),t("br"),n._v("Nghe xong giải thích, Bạch Nguyễn Khanh kích động thân thể mềm mại run rẩy."),t("br"),t("br"),n._v("Nằm mơ cũng không dám muốn, kiếm pháp lại còn có thể như vậy dùng."),t("br"),t("br"),n._v("Đơn giản như vậy, lại phát huy ra vượt quá tưởng tượng lực lượng."),t("br"),t("br"),n._v("“Tốt, ngươi ở đây từ từ lĩnh ngộ a, ta tiếp tục xem sách đi...”"),t("br"),t("br"),n._v("Thấy đối phương rơi vào suy nghĩ, từng chiêu không ngừng thí nghiệm, Trương Huyền xoay người đi ra ngoài."),t("br"),t("br"),n._v("Lần nữa trở lại trước kệ sách, tiếp tục xem sách, một canh giờ sau, tất cả thư tịch toàn bộ khắc sâu vào trong óc."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Hô nhỏ một tiếng, liên quan tới Hư Tiên cảnh tu luyện công pháp lập tức hội tụ vào một chỗ, hình thành một quyển sách."),t("br"),t("br"),n._v("[ truyen ｃua"),t("br"),n._v("tui đốt net ]  Mở ra nhìn một lần, thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Cùng đoán đồng dạng, nơi này thư tịch, nhẹ nhõm tạo thành hoàn chỉnh Hư Tiên cảnh thiên đạo công pháp, nói cách khác, chỉ cần Tiên Nguyên đan đủ, có thể trực tiếp đột phá, trở thành Hư Tiên cảnh cường giả!"),t("br"),t("br"),n._v("Thậm chí Chân Tiên cảnh công pháp, cũng tạo thành một nửa."),t("br"),t("br"),n._v("“Trở về lại đột phá đi...”"),t("br"),t("br"),n._v("Nhìn đồng hồ, đi vào thư viện đến bây giờ, đã tiếp cận ba canh giờ, Trương Huyền duỗi lưng một cái, lần nữa tới đến tĩnh thất trước mặt, đẩy cửa đi vào."),t("br"),t("br"),n._v("Mới vừa vào đi, liền thấy kiếm khí gào thét, Bạch Nguyễn Khanh ngay tại thí nghiệm trước đó truyền thụ cho kiếm chiêu."),t("br"),t("br"),n._v("Trường kiếm quét ngang, thoạt nhìn chậm chạp, lại tạo thành một cái đặc thù bình chướng, đem trước mặt không khí phong tỏa ngăn cản, làm cho không người nào có thể tới gần, cùng thời khắc đó, trường kiếm bản thân kiếm khí, đột nhiên nổ lên, bình lấy hướng về phía trước cắt chém."),t("br"),t("br"),n._v("Lực lượng tựa như giao long."),t("br"),t("br"),n._v("“Không sai...”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Mới luyện hơn một canh giờ, cô bé này, cũng đã ra dáng, tuy là cùng hắn so, kém rất lớn một đoạn, nhưng cũng có sáu, bảy phần uy lực."),t("br"),t("br"),n._v("“Sư thúc...”"),t("br"),t("br"),n._v("Gặp hắn đi vào, Bạch Nguyễn Khanh dừng lại tu luyện, vội vàng ôm quyền."),t("br"),t("br"),n._v("Kiếm pháp của đối phương, càng luyện càng biết mạnh mẽ, giờ phút này đã đối vị này tuổi tác so với nàng còn nhỏ thanh niên, phục sát đất."),t("br"),t("br"),n._v("Trước đó còn muốn lấy, muốn gả cho đối phương, giờ phút này hoàn toàn mất hết ý nghĩ."),t("br"),t("br"),n._v("Loại này ưu tú người, căn bản là nàng, xứng với."),t("br"),t("br"),n._v("“Ừm, ta xem xong sách, muốn trở về tu luyện...”"),t("br"),t("br"),n._v("Trương Huyền bàn giao: “Ta sự tình, hi vọng liền gia gia ngươi, còn có cái kia Phong lão cũng không được nói cho...”"),t("br"),t("br"),n._v("“Vâng!” Bạch Nguyễn Khanh liên tục gật đầu."),t("br"),t("br"),n._v("Bí mật này đương nhiên là bản thân nàng biết tốt nhất rồi, nếu không, người người đều chạy tới bái sư, còn làm sao có thể một tiếng hót lên làm kinh người, trở nên nổi bật?"),t("br"),n._v("“Vậy là tốt rồi...” Thấy nàng đáp ứng, Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Dù sao đối phương trong cơ thể còn có bản thân một đạo chân khí, cũng không sợ lặng lẽ làm loạn."),t("br"),t("br"),n._v("“Sư thúc, không vội đi, không có gì bất ngờ xảy ra, Phong lão hẳn là đang canh giữ ở ngươi trên đường trở về, tính toán thăm dò thực lực của ngươi cùng kiếm pháp!”"),t("br"),t("br"),n._v("Thấy sư thúc muốn đi, Bạch Nguyễn Khanh cắn môi, vội vàng nói."),t("br"),t("br"),n._v("“Thăm dò ta?” Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("Chẳng lẽ tiết lộ thân phận?"),t("br"),t("br"),n._v("Coi như tiết lộ, tự do nội môn trưởng lão tìm kiếm, không cần đến Phong lão ra tay đi!"),t("br"),t("br"),n._v("“Vâng... Ngươi thuốc chữa khỏi gia gia thương thế, lão nhân gia ông ta, tính toán thu ngươi làm đồ, lại sợ thực lực ngươi quá yếu, cho nên... Để Phong lão tới thăm dò một chút!” Bạch Nguyễn Khanh vội nói."),t("br"),t("br"),n._v("Trương Huyền giờ mới hiểu được tới."),t("br"),t("br"),n._v("Khó trách nữ nhân này, vừa đến liền muốn động thủ, lại muốn ép bản thân xưng hô sư tỷ, là sợ nàng gia gia thu bản thân làm đồ đệ phía sau, chẳng biết tại sao nhiều một vị sư thúc..."),t("br"),t("br"),n._v("Như vậy cũng liền giải thích, muốn cho bản thân truyền thụ kiếm thuật, nhưng vì sao không xưng hô sư phụ, mà là sư thúc."),t("br"),t("br"),n._v("“Phong lão... Tu vi gì?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Trước mắt vị này, coi như khôi phục thực lực, dựa vào hắn vượt cấp khiêu chiến năng lực, chỉ cần chiếm được tiên cơ mà nói, đồng dạng có thể nhẹ nhõm đánh bại, vị kia Phong lão thực lực, cụ thể cao bao nhiêu, cũng không rõ ràng, chỉ sợ muốn làm như vậy, gần như không có khả năng làm được."),t("br"),t("br"),n._v("“Hắn cùng gia gia đồng dạng... Cũng là Tiên Quân cảnh cường giả!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Tiên Quân?” Trương Huyền khóe miệng co quắp một chút: “Hắn nhất định sẽ ra tay?”"),t("br"),t("br"),n._v("Tiên Quân cảnh, tiên chi quân vương, hạch tâm trưởng lão mới có thực lực, hắn mặc dù tu vi tiến nhanh, gặp được loại cường giả cấp bậc này, cũng là có thể có bao xa liền chạy bao xa, căn bản không có khả năng chiến thắng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh gật đầu: “Gia gia tự thân bàn giao, Phong lão không có khả năng từ chối!”"),t("br"),t("br"),n._v("Trương Huyền suy nghĩ."),t("br"),t("br"),n._v("Đè thấp tu vi thăm dò, cùng cấp bậc bản thân thắng được, khẳng định sẽ bại lộ “Ta Rất Biết Điều” thân phận, tu vi hiện tại, lại không đủ để làm cho đối phương im miệng không nói."),t("br"),t("br"),n._v("Mà thắng không nổi, Bạch Diệp trưởng lão lại vô cùng có khả năng thu làm đệ tử..."),t("br"),t("br"),n._v("Nhưng hắn, không nguyện ý trở thành người khác học sinh."),t("br"),t("br"),n._v("Thật muốn muốn làm như vậy, danh sư đại lục, cũng không biết bái bao nhiêu nhân vi sư."),t("br"),t("br"),n._v("“Như vậy đi, ta cần ngươi phối hợp ta diễn một tuồng kịch!”"),t("br"),t("br"),n._v("Dừng lại một chút, nói."),t("br"),t("br"),n._v("“Tốt!” Không chần chờ chút nào, Bạch Nguyễn Khanh gật đầu đáp ứng."),t("br"),t("br"),n._v("Thảo luận xong xuôi, Trương Huyền một thân một mình đi ra tàng thư các, một đường phi nhanh, từ hạch tâm đệ tử sơn mạch, hướng về nội môn sơn mạch chạy như bay."),t("br"),t("br"),n._v("Không mượn phi hành Thánh thú, chỉ bằng vào đi đường mà nói, đại khái muốn đi một canh giờ."),t("br"),t("br"),n._v("Một đường tiến lên, Trương Huyền tận lực đem tốc độ khống chế tại bình thường phá toái hư không đỉnh phong."),t("br"),t("br"),n._v("Đi gần nửa canh giờ, đi tới một chỗ không có bóng người vị trí, đột nhiên cảm thấy bốn phía một cỗ nồng đậm lực lượng áp bức mà tới, vội vàng dừng bước, ngay sau đó nhìn thấy một cái tuổi trẻ bóng người, chẳng biết lúc nào xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Chính là chờ ở chỗ này Bạch Phong!"),t("br"),t("br"),n._v("Đã sử dụng dịch dung đan, đem dung mạo hoàn toàn thay đổi."),t("br"),t("br"),n._v("Bất quá, tại Minh Lý Chi Nhãn chiếu xuống, bản thân tướng mạo có thể thấy rõ ràng."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Không có bất kỳ cái gì lời nói, Bạch Phong cánh tay vừa nhấc, một thanh trường kiếm đâm tới."),t("br"),t("br"),n._v("Tu vi sớm đã áp chế cùng hắn tương đồng, đều là phá toái hư không đỉnh phong."),t("br"),t("br"),n._v("“Ah... Ngươi là ai, tại sao muốn động thủ với ta? Nơi này là Lăng Vân kiếm các...”"),t("br"),t("br"),n._v("Dường như không nghĩ tới đối phương sẽ trực tiếp ra tay, Trương Huyền sợ tới mức sắc mặt trắng nhợt, liên tiếp lui về phía sau, dưới chân mềm nhũn, té ngã trên đất."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Bạch Phong trường kiếm, đã đáp lên trên cổ của hắn."),t("br"),t("br"),n._v("Mày nhăn lại, Bạch Phong vẻ mặt khó coi."),t("br"),t("br"),n._v("Thật vất vả lão gia muốn nhận đệ tử, không nghĩ tới vị này thực lực, kém như vậy!"),t("br"),t("br"),n._v("Cùng cấp bậc dưới tình huống, lực lượng dùng không đến một thành, liền bị đánh bại dễ dàng... Loại người này, coi như thật thành lão gia đệ tử, cũng khẳng định sẽ mất mặt xấu hổ."),t("br"),t("br"),n._v("“Cầm lấy kiếm của ngươi, không ngăn được chiêu kiếm của ta, ngày hôm nay chỉ có một con đường chết!”"),t("br"),t("br"),n._v("Lui về sau một bước, Bạch Phong lạnh lùng nói."),t("br"),t("br"),n._v("“Phong lão, vẫn là ta tới đi...”"),t("br"),t("br"),n._v("Ngay tại chỉ là, một người mặc áo trắng, che mặt nữ tử thân ảnh xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Nghe được truyền âm, Bạch Phong biết là tiểu thư, nhíu nhíu mày, vẫn là lui về sau một bước."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh tiến về phía trước một bước, ném ra một thanh kiếm, rơi vào Trương Huyền trước mặt, ngay sau đó một kiếm đâm tới."),t("br"),t("br"),n._v("Trương Huyền vội vàng phía dưới, cầm lấy trường kiếm, rung ra một đạo kiếm hoa, kiếm chiêu thi triển giống thật mà là giả, vừa nhìn liền biết không có khắc khổ tu luyện qua."),t("br"),t("br"),n._v("Cũng không chỉ là thực lực không mạnh, vẫn là chân khí quá yếu, trường kiếm trong tay của hắn, bị nữ hài trường kiếm đụng một cái, lập tức rời khỏi tay, cắm vào cách đó không xa trên mặt đất, phát ra “Vo ve!” Vang lên."),t("br"),t("br"),n._v("Trường kiếm rời tay, lại không còn sức phản kháng, Trương Huyền thân thể cứng đờ, lần nữa bị trường kiếm để ở cái cổ."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Lắc đầu, Bạch Nguyễn Khanh trường kiếm thu lại, xoay người lại đến Bạch Phong trước mặt, ánh mắt lộ ra nhàn nhạt thất lạc, âm thanh để lộ ra nồng đậm thất vọng: “... Quá yếu!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1987"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1989"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);