(window.webpackJsonp=window.webpackJsonp||[]).push([[1115],{1470:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1977-bach-nguyen-khanh-khieu-chien-hach-tam-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1977-bach-nguyen-khanh-khieu-chien-hach-tam-thuong"}},[n._v("#")]),n._v(" Chương 1977: Bạch Nguyễn Khanh khiêu chiến hạch tâm (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Vừa bước một bước, Tào Thành Lập quay đầu hướng nữ hài: “Mong rằng vị sư tỷ này thứ lỗi, bước qua khung cửa thời điểm, còn xin dùng chân phải!”"),t("br"),t("br"),n._v("“???” Bạch Nguyễn Khanh."),t("br"),t("br"),n._v("“Đây là thiếu gia của chúng ta quyết định quy củ, mong được tha thứ”"),t("br"),t("br"),n._v("Tào Thành Lập tràn đầy chân thành."),t("br"),t("br"),n._v("Nhíu nhíu mày, Bạch Nguyễn Khanh suy nghĩ một chút, vẫn là không dám làm trái, chân phải vượt qua ngưỡng cửa, đi vào."),t("br"),t("br"),n._v("Vừa tiến vào sân nhỏ, lập tức nhìn thấy một thiếu niên đang luyện kiếm, trên đầu tràn đầy mồ hôi, hẳn là luyện quá lâu."),t("br"),t("br"),n._v("“Cái này kiếm pháp...”"),t("br"),t("br"),n._v("Đứng tại chỗ nhìn một hồi, Bạch Nguyễn Khanh thân thể cứng nhắc, sau lưng toát ra mồ hôi lạnh."),t("br"),t("br"),n._v("Trước mắt vị này, chỉ có mười, sáu bảy tuổi, hơn nữa tu vi chỉ là Cổ Thánh nhị trọng, hết sức bình thường, có thể kiếm pháp, liên miên không ngớt, chiêu số đơn giản, lại ẩn chứa đại đạo, tang thương cổ điển, thật giống đắm chìm trong kiếm thuật bên trên, ít nhất lâu đến mấy chục năm."),t("br"),t("br"),n._v("Thậm chí, một ít chiêu số, so Phong lão thi triển đều càng lão luyện hơn, huyền diệu!"),t("br"),t("br"),n._v("Mười mấy tuổi thiếu niên, thi triển ra so có thể so với gia gia hắn loại này cường giả kiếm chiêu, kiếm ý, nàng tu luyện nhiều năm như vậy kiếm pháp, cảm giác ở trước mặt đối phương, em bé đồng dạng ngây thơ buồn cười!"),t("br"),t("br"),n._v("“Hắn... Chính là Thiện Hiểu Thiên?”"),t("br"),t("br"),n._v("Bờ môi trắng bệch, hỏi."),t("br"),t("br"),n._v("“Ừm, hắn chính là Hiểu Thiên tiểu thiếu gia...” Tào Thành Lập gật đầu."),t("br"),t("br"),n._v("“Hắn cùng Trương sư... Đệ, học tập bao lâu kiếm thuật?” Nhịn không được nhìn qua."),t("br"),t("br"),n._v("Cao minh như vậy kiếm thuật, chẳng lẽ cái tên này vừa ra đời liền cùng Trương sư thúc tại học tập đi!"),t("br"),t("br"),n._v("“Bao lâu?” Bẻ ngón tay, Tào Thành Lập coi như nửa ngày, duỗi ra năm ngón tay, dừng lại một chút, lại rụt về lại hai cái: “Đại khái thời gian dài như vậy đi!”"),t("br"),t("br"),n._v("“Ba năm? Mới ba năm cứ như vậy lợi hại?”"),t("br"),t("br"),n._v("Đồng tử co rút lại, Bạch Nguyễn Khanh giật nảy mình."),t("br"),t("br"),n._v("Nàng từ ba tuổi liền bắt đầu luyện kiếm, đến bây giờ đã tu luyện tiếp cận hai mươi năm!"),t("br"),t("br"),n._v("Trong khoảng thời gian này, gia gia cùng Phong lão, tay nắm tay dạy dỗ, dùng vô số trân quý dược vật, mới có thành tựu như thế, đối phương mới tiếp nhận chỉ điểm ba năm, liền thoạt nhìn so với chính mình càng thêm cường đại?"),t("br"),t("br"),n._v("Làm sao làm được?"),t("br"),t("br"),n._v("“Ba năm?” Tào Thành Lập sửng sốt một chút: “Không phải ah, ba canh giờ!”"),t("br"),t("br"),n._v("Thiếu gia, mỗi ngày đều rất bận, tiểu thiếu gia mặc dù là hắn học sinh, chỉ điểm thời gian tu luyện cũng không lâu, bình thường đều là nói ra trong tu luyện vấn đề, sau đó cho cái ngọc bài bản thân học tập... Bởi vậy, bái sư hơn mười ngày, chân chính chỉ điểm, tính toán đâu ra đấy, đều không cao hơn ba canh giờ!"),t("br"),t("br"),n._v("“... Giờ?”"),t("br"),t("br"),n._v("Lắc lư một cái, Bạch Nguyễn Khanh thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Học được ba canh giờ, kiếm thuật cứ như vậy cao minh?"),t("br"),t("br"),n._v("Miệng há mở, sắp điên cuồng!"),t("br"),t("br"),n._v("Bên này khiếp sợ nói không ra lời, cách đó không xa Thiện Hiểu Thiên, nhìn thấy khách tới, mỉm cười, ngừng lại, giải thích nói: “Thật ra thì lão sư... Chân chính chỉ cho ta chút thời gian, cũng liền hơn một canh giờ... Ba canh giờ, Tào quản gia nói nhiều!”"),t("br"),t("br"),n._v("Đối phương có thể tìm tới nơi này, hơn nữa biết mình, chứng minh đã hiểu thân phận của hắn, không có gì có thể giấu diếm."),t("br"),t("br"),n._v("“Một cái...”"),t("br"),t("br"),n._v("Khiếp sợ đều có chút mất cảm giác, Bạch Nguyễn Khanh qua một hồi thật lâu, mới mở miệng lần nữa: “Ta có thể hay không cùng ngươi tỷ thí một chút kiếm pháp?”"),t("br"),t("br"),n._v("“Đương nhiên có thể!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên liền vội vàng gật đầu."),t("br"),t("br"),n._v("Hắn vẫn luôn là tự mình tu luyện, có người có thể tỷ thí, không thể tốt hơn!"),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Đem tu vi áp chế đến Cổ Thánh nhị trọng sơ kỳ, cùng đối phương tương đồng, Bạch Nguyễn Khanh đem trường kiếm lấy ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thân thể mềm mại khẽ động, hướng về phía trước đâm tới."),t("br"),t("br"),n._v("Vừa rồi gặp qua đối phương luyện kiếm, biết ảo diệu vô tận, không dám khinh thường, vừa ra tay liền thi triển ra mạnh nhất chiêu số, tốc độ nhanh như thiểm điện."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngay tại nàng cho rằng, hẳn là có thể chiến thắng đối phương thời điểm, trước mắt Thiện Hiểu Thiên đột nhiên xuất thủ, ánh mắt hoa lên, sau đó..."),t("br"),t("br"),n._v("Sau đó... Trên đầu chẳng biết lúc nào, nhiều ra một thanh kiếm tới!"),t("br"),t("br"),n._v("Vẻ mặt trắng bệch, không dám tiếp tục áp chế tu vi, tu vi thời gian nháy mắt liền khôi phục lại Chân Tiên cảnh, hộ thể chân khí trùng kích vào, trường kiếm bị kích đống ra ngoài!"),t("br"),t("br"),n._v("Nếu không phải phá giải tu vi tốc độ nhanh, khả năng đầu đã bị xuyên thủng."),t("br"),t("br"),n._v("Tất cả đều là mồ hôi lạnh, Bạch Nguyễn Khanh thân thể cứng ngắc."),t("br"),t("br"),n._v("Trong tay đối phương kiếm thế nào bắn ra, lại như thế nào đi tới đỉnh đầu của nàng... Vậy mà mảy may cũng không thấy!"),t("br"),t("br"),n._v("Nói cách khác, nếu quả thật cùng cấp bậc mà nói, người... Đã sớm chết!"),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("Tràn đầy hoảng sợ nhìn sang."),t("br"),t("br"),n._v("Còn tưởng rằng, vừa rồi đối phương tu luyện kiếm pháp, rất lợi hại, nằm mộng cũng nghĩ không ra, chiêu này phi kiếm, càng thêm cường đại!"),t("br"),t("br"),n._v("Cùng cấp bậc, một chiêu thuấn sát!"),t("br"),t("br"),n._v("“Ta luyện lâu như vậy, chưa hề cùng người khác chiến đấu qua, không biết lực lượng mạnh yếu, lại thêm không thể tự do khống chế, thực sự xin lỗi...” Thiện Hiểu Thiên vò đầu."),t("br"),t("br"),n._v("Hắn vốn nghĩ, trường kiếm đâm qua đi, liền dừng tay, không nghĩ tới đối với kiếm pháp lý giải vẫn là quá yếu, căn bản không dừng được."),t("br"),t("br"),n._v("May mắn nàng phản ứng nhanh, hộ thể chân khí ngăn lại, nếu không, mới đi đến khách nhân, trên đầu cắm cái kiếm đi gặp lão sư... Chắc là phải bị mắng chết!"),t("br"),t("br"),n._v("“Học được một canh giờ, cùng cấp bậc ta, không đỡ được...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Bạch Nguyễn Khanh nhớ tới một việc: “Ta cũng cùng Trương sư thúc, học được một hồi, hắn cũng truyền thụ ta giơ kiếm phương pháp sử dụng... Tuy là ta luyện được qua loa, không được đến tinh túy, nhưng chiêu này uy lực lớn bao nhiêu?”"),t("br"),t("br"),n._v("Mặc dù biết, chiêu này giơ kiếm phương pháp rất lợi hại, có thể... Chưa hề thí nghiệm qua, cũng không cùng người khác giao thủ, có muốn thử một chút hay không?"),t("br"),n._v("Nghĩ đến cái này, liếc mắt nhìn trước mắt Thiện Hiểu Thiên, cuối cùng lắc đầu."),t("br"),t("br"),n._v("Nàng cũng không muốn lại bị kiếm của đối phương cắm vào trên đầu, vừa rồi phản ứng nhanh, xem như không có việc gì, ngộ nhỡ thất thần, khả năng giờ phút này, đã biến thành thi thể."),t("br"),t("br"),n._v("“Nơi này nhưng có yên tĩnh gian phòng, an bài cho ta một gian?”"),t("br"),t("br"),n._v("Ngẩng đầu hỏi."),t("br"),t("br"),n._v("“Cái kia không người ở, ngươi trước ở lại chính là, lão sư đang lúc bế quan, chờ hắn xuất quan, ta sẽ gọi ngươi!” Thiện Hiểu Thiên một chỉ."),t("br"),t("br"),n._v("Nội môn đệ tử, bởi vì cần hạ nhân chăm sóc, cư trú sân nhỏ, bình thường đều có mười cái gian phòng, mười phần rộng rãi."),t("br"),t("br"),n._v("Không nói thêm lời, Bạch Nguyễn Khanh đi vào gian phòng, chuẩn bị ổn thoả, cổ tay khẽ đảo, lấy ra một cái thông thần ngọc phù."),t("br"),t("br"),n._v("Hạch tâm đệ tử thông thần ngọc phù!"),t("br"),t("br"),n._v("Hơi chuyển động ý nghĩ một chút, tiến vào bên trong."),t("br"),t("br"),n._v("“Hạch tâm đệ tử khiêu chiến thi đấu, cùng nội môn khác biệt, có thể công khai, cũng có thể tự mình tỷ thí... Ta lần trước hạch tâm giải thi đấu, chỉ xếp tại tên thứ mười bảy, mười sáu tên Vương Hạo, ta không phải là đối thủ... Giờ phút này, có thể thử một chút!”"),t("br"),t("br"),n._v("Đứng tại Thông Thần điện phía trong, Bạch Nguyễn Khanh mắt sáng lên."),t("br"),t("br"),n._v("Nàng ở hạch tâm đệ tử bên trong, danh khí rất lớn, người người kính sợ, là bởi vì gia gia Bạch Diệp trưởng lão duyên cớ, trên thực tế, thực lực chân thật, liền mười vị trí đầu đều không xếp hạng tới."),t("br"),t("br"),n._v("Đặc biệt là trong trung tâm, trước mấy biến thái, rất nhiều trưởng lão, đều không nhất định là đối thủ!"),t("br"),t("br"),n._v("Lần trước hạch tâm đệ tử đại bỉ, nàng thua ở người thứ mười sáu Vương Hạo trong tay, cuối cùng chỉ là mười bảy tên, vừa vặn có thể mượn hắn tay, đi thử một chút Trương sư thúc truyền thụ cho kiếm thuật, đến cùng thế nào!"),t("br"),t("br"),n._v("Tinh thần khẽ động, đi tới trước đài: “Giúp ta phát ra tỷ thí mời, ta muốn khiêu chiến người thứ mười sáu, Vương Hạo!”"),t("br"),t("br"),n._v("Thời gian không dài, tên là Vương Hạo đệ tử, đi tới nơi này, khóe miệng nâng lên: “Làm sao đột nhiên muốn cùng ta tỷ thí? Chẳng lẽ mới học cái gì kiếm chiêu, cho rằng có thể thắng được ta? Không cần thử, nửa tháng trước, mới so qua, ngươi không phải là đối thủ...”"),t("br"),t("br"),n._v("“Ta là học được mới kiếm chiêu, muốn thử xem...” Bạch Nguyễn Khanh cũng không có không thừa nhận: “Thua cùng lắm thì, lần sau lại đến!”"),t("br"),t("br"),n._v("Trương sư thúc giơ kiếm chiêu số, rốt cục mạnh đến mức nào, nàng chỉ là đơn độc tu luyện, không cùng người khác thử qua, tìm Vương Hạo, cũng chỉ là thí nghiệm một chút, còn có thể hay không thắng, trong lòng một điểm đáy đều không có."),t("br"),t("br"),n._v("“Nếu ngươi không quan tâm, vậy thì bắt đầu đi!”"),t("br"),t("br"),n._v("Thấy nàng bộ dáng này, Vương Hạo nhẹ nhàng cười một tiếng, hai người tìm cái tư mật tỷ thí tràng, tiến vào bên trong."),t("br"),t("br"),n._v("Hạch tâm đệ tử, mỗi một vị đều là tông môn chói mắt nhất tồn tại, nắm giữ địa vị cực cao cùng vô số cầm giữ độn, bởi vậy, nội môn đệ tử sử dụng công khai thi đấu, đối bọn hắn cũng không áp dụng."),t("br"),t("br"),n._v("Dù sao, một khi thất bại, sẽ tạo thành ảnh hưởng rất lớn."),t("br"),t("br"),n._v("Cho nên, không ít người, cũng sẽ ở Thông Thần điện, mở việc riêng tư tranh tài, còn kết quả... Chỉ có song phương giao chiến biết."),t("br"),t("br"),n._v("Tại tỷ thí Ðài điếm định, Vương Hạo nhìn lại: “Bắt đầu đi!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trong khi nói chuyện, trường kiếm lay động, một đạo kiếm mang bổ xuống."),t("br"),t("br"),n._v("Trước mắt vị này, mặc dù một mực thua bởi hắn, nhưng dù sao cũng là Bạch trưởng lão cháu gái ruột, thực lực cực mạnh, hắn không dám khinh thường, vừa ra tay liền thi triển ra mạnh nhất chiêu số."),t("br"),t("br"),n._v("“Bổ tới kiếm mang?”"),t("br"),t("br"),n._v("Nhìn thấy đối phương thi triển ra chiêu này, Bạch Nguyễn Khanh sững sờ."),t("br"),t("br"),n._v("Nàng học tập giơ kiếm, chính là nhằm vào loại này chiêu số, giờ phút này thi triển, thật đúng là ngủ gật đưa gối..."),t("br"),t("br"),n._v("Không có đảm nhiệm Hà Trì nghi, trước đó tu luyện mấy chục lần giơ kiếm, hướng về phía trước vạch ra."),t("br"),t("br"),n._v("“Ngươi cẩn thận, ta chiêu này, tuy là còn không có rèn luyện, nhưng uy lực phải rất khá...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, trường kiếm bay tứ tung, đang muốn nhắc nhở đối phương một câu, lời còn chưa nói hết, liền cảm thấy phía trước bóng người run rẩy, trên tay lực lượng buông lỏng..."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Một cái đầu rơi tại cách đó không xa dưới chân, ngay sau đó Vương Hạo thi thể, nằm ở trên mặt đất."),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh giật nảy mình."),t("br"),t("br"),n._v("Nàng chiêu này còn không có rèn luyện, mấu chốt nhất là, đều không có thi triển xong cả... Trước đó vẫn luôn chiến thắng chẳng qua Vương Hạo, đầu liền mất..."),t("br"),t("br"),n._v("“Ngươi... Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Đang khó tin, bị chém giết Vương Hạo, dùng mặt khác một cái thông thần ngọc phù đi tới trước mặt."),t("br"),t("br"),n._v("Nói thật, hắn cũng bối rối."),t("br"),t("br"),n._v("Đối phương từ trước tới nay không phải là đối thủ, lần này mới một chiêu liền bị chém đầu... Tự mình trải qua, đều có chút phát điên."),t("br"),t("br"),n._v("“Ta cũng không biết...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh đờ đẫn."),t("br"),t("br"),n._v("Nàng là thật không biết, chỉ là dần dần từng bước thi triển Trương sư thúc truyền thụ cho kiếm chiêu, nằm mơ nghĩ không ra, chiêu số không dùng hết, đối phương đầu liền mất."),t("br"),t("br"),n._v("“Lại đến... Lần này ta sẽ không thả lỏng, sẽ vừa ra tay liền dùng ra công kích mạnh nhất...”"),t("br"),t("br"),n._v("Thấy nàng bộ dáng này, còn tưởng rằng đang cố ý nhục nhã, Vương Hạo khí cao giọng gào thét, lần nữa lấy ra trường kiếm lao đến."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Kiếm khí như gió, kiếm mang như nước."),t("br"),t("br"),n._v("Có thể trở thành hạch tâm đệ tử bên trong, xếp hạng thứ mười sáu, đối kiếm thuật lý giải cực mạnh, vừa ra tay, cũng làm người ta cảm thấy tuyệt vọng."),t("br"),t("br"),n._v("Biết đối phương thi triển ra toàn lực, dùng trước chiêu số, khẳng định đồng dạng không cách nào chiến thắng, Bạch Nguyễn Khanh cắn răng một cái, lần nữa đem Trương sư thúc truyền thụ cho giơ kiếm, phát huy ra."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Chiêu số đồng dạng không dùng hết, Vương Hạo đầu, lại rớt xuống, lần này bóng da đồng dạng, lăn được càng xa."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh toàn toàn sững sờ."),t("br"),t("br"),n._v("Dường như, học chiêu này, đầu của đối phương, cũng không làm sao kiên cố, tuỳ ý một chém, liền rơi mất..."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1989"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1991"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);