(window.webpackJsonp=window.webpackJsonp||[]).push([[1116],{1471:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1978-bach-nguyen-khanh-khieu-chien-hach-tam-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1978-bach-nguyen-khanh-khieu-chien-hach-tam-ha"}},[n._v("#")]),n._v(" Chương 1978: Bạch Nguyễn Khanh khiêu chiến hạch tâm (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Hạch tâm đệ tử sơn phong."),t("br"),t("br"),n._v("Đường Viêm ở trong đó, tên tuổi lẫy lừng, lần trước đại bỉ xếp hạng thứ bảy, Vô Trần kiếm pháp, tại ngoại giới, càng là rất có danh khí, để vô số người kính sợ."),t("br"),t("br"),n._v("Đã từng một người một kiếm, đuổi một vị Chân Tiên cảnh đỉnh phong đạo tặc, ngang dọc nam bắc năm mươi vạn dặm, đem hắn sau lưng sư môn, thế lực, đều cả gốc chém trừ, từ đó danh chấn thiên hạ."),t("br"),t("br"),n._v("Năm nay năm mươi mốt tuổi, nhưng dung mạo thoạt nhìn, cùng hai mươi tuổi không có cái gì khác nhau, chân khí tinh thuần viên mãn, kiếm khí bất cứ lúc nào cũng sẽ phá thể mà ra, cho người ta một loại nồng đậm uy áp."),t("br"),t("br"),n._v("Danh khí, địa vị, lực lượng cái gì cần có đều có."),t("br"),t("br"),n._v("Hắn giờ phút này, đang đứng ở trong viện, trường kiếm trong tay vạch ra từng đạo tốt đẹp dấu vết, tựa như cao minh thư họa sư, đang tiến hành vẽ tranh, cho người ta một loại đẹp hưởng thụ."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Kiếm ngừng, thu kiếm mà đứng."),t("br"),t("br"),n._v("Dấu vết vẫn tại trên không bồng bềnh, tựa như khói xanh."),t("br"),t("br"),n._v("Oa oa!"),t("br"),t("br"),n._v("Một đầu chim rừng trong lúc vô tình bay vào trong đó."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Giống như là đi vào cối xay thịt, máu tươi bắn tung toé, thời gian nháy mắt liền bị vỡ thành bột phấn, máu tươi chảy xuôi đầy đất."),t("br"),t("br"),n._v("“Chúc mừng sư huynh, Vô Trần kiếm pháp rốt cục đạt đến đại thành cấp bậc!”"),t("br"),t("br"),n._v("Vỗ tay âm thanh vang lên, một thanh niên cười đi tới."),t("br"),t("br"),n._v("Hạch tâm xếp thứ tám đệ tử, Quách Nghị!"),t("br"),t("br"),n._v("Vị này thoạt nhìn chừng hai mươi tuổi, trên thực tế, số tuổi thật sự cũng vượt qua năm mươi."),t("br"),t("br"),n._v("Đi đến Hư Tiên phía sau, tuổi thọ liền vượt qua ba trăm, Chân Tiên cảnh, tuổi thọ mặc dù không có tăng thêm, lực lượng lại mạnh hơn, lực lượng cũng duy trì càng lâu, bởi vậy, năm mươi, xem như vô cùng còn tấm bé tuổi tác."),t("br"),t("br"),n._v("“Khổ tu ba mươi năm, hôm nay, rốt cục có thành tựu... Lần sau hạch tâm đệ tử khảo hạch, nhất định có thể xung kích năm vị trí đầu!”"),t("br"),t("br"),n._v("Cười cười, Đường Viêm một mặt ngạo nghễ."),t("br"),t("br"),n._v("Lần trước khảo hạch, hắn một mực không có đem chiêu kiếm pháp này, luyện đến đỉnh phong, mới cái thứ bảy, giờ phút này đạt đến đại thành cấp bậc, năm vị trí đầu không khó lắm!"),t("br"),t("br"),n._v("Lăng Vân kiếm các lấy chiến đấu xưng, có thể trở thành hắn hạch tâm năm vị trí đầu, toàn bộ đại lục, chỉ sợ đều có thể hàng tiến lên hai mươi."),t("br"),t("br"),n._v("Thế hệ trẻ tuổi, xem như chân chân chính chính đỉnh cao cường giả!"),t("br"),t("br"),n._v("“Hạch tâm năm vị trí đầu, mỗi một vị đều có cơ hội trở thành Lăng Vân kiếm các hạch tâm trưởng lão, ta ở đây liền sớm chúc mừng Đường sư huynh...”"),t("br"),t("br"),n._v("Quách Nghị ôm quyền."),t("br"),t("br"),n._v("Khống chế Lăng Vân kiếm các hạch tâm trưởng lão, tiền thân đều là hạch tâm đệ tử năm người đứng đầu."),t("br"),t("br"),n._v("Liền giống như Bạch Diệp trưởng lão, một trăm năm trước, ở hạch tâm đệ tử bên trong, một mực xếp hạng trước ba, cũng chỉ so gì Thiên trưởng lão hơi yếu một tia thôi!"),t("br"),t("br"),n._v("“Ta trở thành hạch tâm trưởng lão, khẳng định có chỗ tốt của ngươi...”"),t("br"),t("br"),n._v("Mỉm cười, Đường Viêm nhìn lại: “Thế nào, ngày hôm nay làm sao có thời gian tới tìm ta?”"),t("br"),t("br"),n._v("“Là như vậy, sư huynh không phải một mực đối Bạch Nguyễn Khanh, ưa thích không rời ư?” Quách Nghị nói."),t("br"),t("br"),n._v(" “Ừm...” Đường Viêm cũng không phủ nhận."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh mặc dù tính tình không tốt, nhưng xuất thân tốt, người lại xinh đẹp, nếu như có thể lấy về nhà, đối với hắn về sau phát triển, hoàn toàn có sự giúp đỡ to lớn."),t("br"),t("br"),n._v("Có lẽ, sớm liền có thể tiếp quản kiếm các, thực sự trở thành đại lục đỉnh phong nhất một đám người một trong."),t("br"),t("br"),n._v("“Nàng xếp hạng hạch tâm đệ tử người thứ mười bảy, ngày hôm nay không biết làm sao vậy, đột nhiên khiêu chiến Vương Hạo...”"),t("br"),t("br"),n._v("“Xếp hạng thứ mười sáu Vương Hạo?”"),t("br"),t("br"),n._v("Đường Viêm cười nhạt một tiếng, cấp cho phê bình: “Thực lực của người này không tính quá mạnh, nhưng một chiêu , vẫn là cực kỳ đáng sợ, kiếm ý như nước, phòng ngự vô địch, liền xem như ta, muốn phá giải, cũng cần tiêu phí một ít công phu, khiêu chiến hắn... Tự mình chuốc lấy cực khổ!”"),t("br"),t("br"),n._v("“Vương Hạo là rất mạnh... Nhưng, vừa mới truyền đến tin tức, liên tục hai lần đều bị Bạch Nguyễn Khanh chém xuống đầu, tổng cộng dùng... Không cao hơn ba chiêu!”"),t("br"),t("br"),n._v("Quách Nghị nói."),t("br"),t("br"),n._v("“Không cao hơn ba chiêu, chém giết Vương Hạo hai lần?”"),t("br"),t("br"),n._v("Sửng sốt một cái, Đường Viêm khó tin."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh cùng Vương Hạo thực lực, hắn biết rất rõ ràng, dưới tình huống bình thường, người sau chắc thắng... Làm sao có thể không đến ba chiêu, liền bị giết hai lần?"),t("br"),t("br"),n._v("Lúc nào, vị này nữ bạo long trở nên cường đại như vậy?"),t("br"),t("br"),n._v("“Giết Vương Hạo phía sau, Bạch Nguyễn Khanh không có đình chỉ, tiếp tục khiêu chiến, ta trước khi đến, nghe được tin tức là, đồng dạng một chiêu chém giết xếp hạng thứ mười lăm Tôn Kiện! Thứ mười bốn Dương Hổ, thứ mười ba Đỗ Nhược!”"),t("br"),t("br"),n._v("Cười khổ một tiếng, Quách Nghị nói tiếp."),t("br"),t("br"),n._v("“Chỉ dùng một chiêu, từ mười sáu tên, giết tới tên thứ mười ba?”"),t("br"),t("br"),n._v("“Cái này sao có thể?” Đường Viêm khó có thể tin: “Cùng đi nhìn một chút!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Quách Nghị lên tiếng."),t("br"),t("br"),n._v("Đến tìm sư huynh mục đích, chính là cái này, hai người lấy ra thông thần ngọc phù, đồng thời đi vào Thông Thần điện."),t("br"),t("br"),n._v("Rất nhanh, đi tới tỷ thí gian phòng, ngoài cửa phòng thấy được mấy người, tất cả đều một mặt ủ rũ."),t("br"),t("br"),n._v("“Hồng Thanh, ngươi... Làm sao cũng tại đây?”"),t("br"),t("br"),n._v("Ngay phía trước một thanh niên, kêu Hồng Thanh, hạch tâm đệ tử xếp hạng thứ mười, mặc dù là mười vị trí đầu một tên sau cùng, có thể đi vào trong đó, nhưng cũng không tầm thường."),t("br"),t("br"),n._v("Dù sao, mười vị trí đầu đại biểu ý nghĩa khác biệt, cạnh tranh cũng phải so phía sau càng thêm quyết liệt."),t("br"),t("br"),n._v("Tình huống bình thường, đệ nhất một cái cấp bậc, hai, ba một cái, bốn, năm, sáu một cái, bảy, tám, chín, mười một cái, mười một đến hai mươi, lại là một cái!"),t("br"),t("br"),n._v("Tất cả cấp bậc, mỗi một cấp độ, đều đại biểu cường giả khác nhau."),t("br"),t("br"),n._v("Hắn xếp hạng thứ bảy, tương đương với hạch tâm cái thứ tư cầu thang, đủ để cho vô số người tuyệt vọng."),t("br"),t("br"),n._v("Dù vậy, cũng chỉ cùng vị này Hồng Thanh cùng một cái cấp độ, không phải mới vừa nói, Bạch Nguyễn Khanh chỉ giết đến tên thứ mười ba ư?"),t("br"),t("br"),n._v("Xếp hạng thứ mười đứng tại cổng, bộ dáng này làm gì?"),t("br"),t("br"),n._v("“Ta vừa bị... Bạch sư tỷ một kiếm chém xuống đầu...”"),t("br"),t("br"),n._v("Hồng Thanh cười khổ."),t("br"),t("br"),n._v("Bất luận hạch tâm vẫn là nội môn, thực lực thấp, muốn xưng hô thực lực cao là sư huynh, sư tỷ, cái này sớm đã thành quy định."),t("br"),t("br"),n._v("“Nàng khiêu chiến ngươi, hơn nữa chiến thắng?” Quách Nghị lông mày nhảy dựng."),t("br"),t("br"),n._v("“Đúng vậy a, một đường khiêu chiến tới, ta bị một chiêu chém xuống đầu!”"),t("br"),t("br"),n._v("“Ta cũng thế...”"),t("br"),t("br"),n._v("Phía trước mấy cái thanh niên, đồng thời nói, Đường Viêm nhìn sang, ngay sau đó nhìn thấy xếp hạng thứ mười hai, mười một cũng đều đứng tại trong đó, khắp khuôn mặt là bất đắc dĩ."),t("br"),t("br"),n._v("Hiển nhiên, bọn họ đồng dạng không có chặn lại Bạch Nguyễn Khanh kiếm pháp."),t("br"),t("br"),n._v("“Hạch tâm đệ tử Thông Thần điện, mọi người tu vi cũng như nhau, là giả tiên cảnh sơ kỳ... Một mình nàng một kiếm, từ mười sáu giết tới hạng mười... Nói cách khác, liên tục giết bảy người, còn có khí lực?”"),t("br"),t("br"),n._v("Đường Viêm khó có thể tin."),t("br"),n._v("Thông Thần điện phía trong, tất cả mọi người là Hư Tiên cảnh sơ kỳ, lực lượng có hạn, liên tục giết bảy cái trước đó so với chính mình kiếm pháp mạnh, lại còn có thể tiếp tục..."),t("br"),t("br"),n._v("Làm sao mấy ngày không thấy, Bạch Nguyễn Khanh kiếm pháp cao minh đến loại tình trạng này?"),t("br"),t("br"),n._v("“Các ngươi thủ tại chỗ này, chẳng lẽ nàng...”"),t("br"),t("br"),n._v("Cùng hắn khiếp sợ khác biệt, nghĩ tới điều gì, Quách Nghị có chút nhớ nhung khóc."),t("br"),t("br"),n._v("“Đúng vậy a, nàng đang khiêu chiến xếp hạng thứ chín lỗ càng! Một khi thành công, hẳn là sẽ đến lượt ngươi...”"),t("br"),t("br"),n._v("Gật gật đầu, Hồng Thanh còn chưa nói xong, liền thấy cửa phòng “Kẹt kẹt!” Một tiếng mở ra, một cô gái lộ ra đầu, hướng ra phía ngoài nhìn nhìn: “Quách Nghị, ngươi tới vừa vặn, lượt ngươi...”"),t("br"),t("br"),n._v("“...” Quách Nghị."),t("br"),t("br"),n._v("Nghe đối phương hơi thở, làm sao cảm giác, khiêu chiến giống như đùa giỡn?"),t("br"),t("br"),n._v("Ngươi đây là khiêu chiến hạch tâm đệ tử, không phải nấu cơm, đến phiên ta... Liên tục khiêu chiến đều không cần nghỉ ngơi sao?"),t("br"),t("br"),n._v("“Đừng đi vào, ngay ở chỗ này tỷ thí đi!”"),t("br"),t("br"),n._v("Đường Viêm đột nhiên mở miệng."),t("br"),t("br"),n._v("“Không sai, mặc kệ thắng thua, công khai so a, như vậy mọi người cũng có thể nhìn thấy, dù sao chỉ chúng ta mấy người, không có gì có thể giấu diếm!”"),t("br"),t("br"),n._v("Lại có người nói."),t("br"),t("br"),n._v("Đều bị một người trực tiếp mở lựa chọn, còn có cái gì có thể giấu diếm?"),t("br"),t("br"),n._v("Cùng hắn bí mật tranh tài, còn không bằng tất cả mọi người nhìn thấy, cũng được có cái đề phòng."),t("br"),t("br"),n._v("“Ngươi ý tứ, công khai so?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh nhìn qua."),t("br"),t("br"),n._v("“Đường sư huynh ý tứ chính là ta, ta cũng chủ trương công khai!”"),t("br"),t("br"),n._v("Cắn răng một cái, Quách Nghị gật đầu."),t("br"),t("br"),n._v("“Vậy được!”"),t("br"),t("br"),n._v("Gặp hắn đáp ứng, Bạch Nguyễn Khanh không nói thêm lời, xách theo trường kiếm đi ra."),t("br"),t("br"),n._v("Quách Nghị nói: “Đi tỷ thí đài đi...”"),t("br"),t("br"),n._v("“Không cần phiền toái như vậy...”"),t("br"),t("br"),n._v("Trường kiếm trong tay nhấc lên, Bạch Nguyễn Khanh nhẹ nhàng quét ngang, cả người đã đi tới trước mặt."),t("br"),t("br"),n._v("“Ở đây động thủ?”"),t("br"),t("br"),n._v("Không nghĩ tới, nàng trực tiếp ra tay, căn bản không quan tâm lôi đài các loại, giật nảy mình, Quách Nghị sầm mặt lại, trường kiếm nâng lên."),t("br"),t("br"),n._v("Bất quá, kiếm còn chưa tới đến đối phương trước mặt, cổ họng đau xót."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Đầu rơi trên mặt đất, con mắt trừng tròn xoe, chết không nhắm mắt."),t("br"),t("br"),n._v("Cho đến bị giết, cũng không dám tin tưởng đây là sự thực."),t("br"),t("br"),n._v("“Tốt, lượt ngươi...”"),t("br"),t("br"),n._v("Một kiếm chém giết Quách Nghị, Bạch Nguyễn Khanh nhìn về phía cách đó không xa Đường Viêm."),t("br"),t("br"),n._v("Vừa mới bắt đầu, đối Trương sư thúc truyền thụ cho giơ kiếm, còn có chút lạ lẫm, không biết uy lực thế nào, liên tục chém giết mấy vị xếp tại trước mặt hạch tâm đệ tử, hơn nữa đều là thuấn sát, liền lòng tự tin đại tăng!"),t("br"),t("br"),n._v("Sư thúc chỉ chỉ điểm nàng vài phút, cứ như vậy mạnh..."),t("br"),t("br"),n._v("Khó trách thắng không nổi Thiện Hiểu Thiên..."),t("br"),t("br"),n._v("Tiếp nhận chỉ điểm hơn hai canh giờ, về đi đến loại nào cấp bậc, không dám tưởng tượng!"),t("br"),t("br"),n._v("“Ta... Cũng ở nơi đây?”"),t("br"),t("br"),n._v("Đường Viêm khuôn mặt trầm xuống."),t("br"),t("br"),n._v("“Ngay ở chỗ này đi... Tránh khỏi phiền phức!” Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Không nên quá ngạo mạn...” Đường Viêm lông mày nhảy loạn."),t("br"),t("br"),n._v("Đổi lại trước kia, hắn so Quách Nghị coi như mạnh, cũng mạnh không đến chỗ nào, hiện tại đem Vô Trần kiếm pháp tu luyện tới đại thành, sớm đã không phải trước đó tên ngố."),t("br"),t("br"),n._v("“Ta không có thời gian, còn muốn khiêu chiến năm vị trí đầu, cũng đừng chậm trễ...” Bạch Nguyễn Khanh khoát tay áo."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Vẻ mặt đỏ lên, Đường Viêm lông mày nâng lên: “Đã như vậy, cũng đừng trách ta không khách khí!”"),t("br"),t("br"),n._v("Lòng bàn tay trường kiếm lắc một cái, từng đạo vết kiếm xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("“Vô Trần kiếm đại thành?”"),t("br"),t("br"),n._v("“Thật lợi hại...”"),t("br"),t("br"),n._v("“Loại kiếm pháp này, chỉ sợ muốn xếp hạng đến năm vị trí đầu đi!”"),t("br"),t("br"),n._v("“Đúng vậy a...”"),t("br"),t("br"),n._v("Tất cả mọi người là thức kiếm người, vừa nhìn thấy chiêu này, tất cả đều tràn đầy kích động."),t("br"),t("br"),n._v("Không nghĩ tới không chỉ Bạch Nguyễn Khanh tiến bộ, Đường Viêm cũng tiến bộ như vậy lớn."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Nhìn thấy chiêu kiếm pháp này, Bạch Nguyễn Khanh ngừng lại."),t("br"),t("br"),n._v("Vô Trần kiếm, đâu đâu cũng có vết kiếm, xông tới liền sẽ bị thương, nàng giơ kiếm mặc dù không tệ, lại vừa vặn không đối ứng, hoàn toàn không cần võ chi địa."),t("br"),t("br"),n._v("“Không nghĩ tới ngươi đem Vô Trần kiếm pháp tu luyện tới loại tình trạng này... Đừng có gấp, ngươi chờ một chút, ta đi ra ngoài một chút, trở về tiếp tục cùng ngươi tỷ thí!”"),t("br"),t("br"),n._v("Nói xong, thân thể mềm mại run rẩy, hóa thành quang ảnh, từ biến mất tại chỗ."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người ngươi nhìn ta, ta nhìn ngươi, tất cả đều không hiểu ra sao."),t("br"),t("br"),n._v("Đường Viêm cũng ngây người."),t("br"),t("br"),n._v("Còn tưởng rằng đối phương cùng giải quyết dạng xông lại, đem hắn đầu chém xuống, thật muốn như vậy, hắn cũng có thể đem hắn chém giết, liền xem ai động tác càng nhanh, càng nhanh chóng..."),t("br"),t("br"),n._v("Nằm mơ đều không có đi tới, đối phương không theo lẽ thường ra bài, chuyển thân đi!"),t("br"),t("br"),n._v("Còn để hắn chờ đợi..."),t("br"),t("br"),n._v("“Sư huynh...”"),t("br"),t("br"),n._v("Dùng cái khác thông thần ngọc phù lần nữa đi vào Quách Nghị, cũng một mặt choáng váng."),t("br"),t("br"),n._v("“Ở chỗ này chờ a, ta không tin chờ một chút, nàng liền có thể chiến thắng...”"),t("br"),t("br"),n._v("Đường Viêm cắn răng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1990"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1992"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);