(window.webpackJsonp=window.webpackJsonp||[]).push([[1118],{1473:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1980-truong-huyen-xuat-quan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1980-truong-huyen-xuat-quan"}},[n._v("#")]),n._v(" Chương 1980: Trương Huyền xuất quan")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được lão gia xác nhận, Bạch Phong nói không ra lời."),t("br"),t("br"),n._v("Vừa nghĩ như thế mà nói, quả thực hoàn toàn phù hợp!"),t("br"),t("br"),n._v("Tiểu thư rõ ràng đi qua tìm phiền toái, kết quả, lại cam tâm tình nguyện phối hợp vị này Trương Huyền diễn kịch, sau khi trở về, liền học được “Ta Rất Biết Điều” kiếm thuật, ngang dọc hạch tâm, không người có thể địch!"),t("br"),t("br"),n._v("Vị này “Trương Huyền”, còn lấy ra có thể cứu chữa lão gia dược vật..."),t("br"),t("br"),n._v("Đi vào Lăng Vân kiếm các, không đến một ngày thời gian, mà trong ngày này, có người lĩnh ngộ Thần chi kiếm ý, có người khiêu chiến toàn bộ nội môn..."),t("br"),t("br"),n._v("Suy nghĩ kỹ một chút, tất cả mũi nhọn, đều chỉ hướng đối phương, không thừa nhận đều không được!"),t("br"),t("br"),n._v("“Trước không vội làm ầm lên, có phải là hắn hay không, còn cần đi dò xét...”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão truyền âm dừng lại một chút: “Ta tự mình đi!”"),t("br"),t("br"),n._v("“Vâng, lão gia!” Bạch Phong gật đầu."),t("br"),t("br"),n._v("Hai người vừa rồi giao lưu, đều là truyền âm, bởi vậy, lão giả đối diện, cũng không hiểu rõ tình hình."),t("br"),t("br"),n._v("“Các ngươi không muốn quản?”"),t("br"),t("br"),n._v("Thấy hai cái này, một mực truyền âm, không để ý tới mình, lão giả khí càng là sắp nổ."),t("br"),t("br"),n._v("“Đương nhiên muốn xen vào, chúng ta trước đi qua nhìn một chút...”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão gọi Bạch Phong một tiếng, người sau lấy tới hai cái hạch tâm đệ tử thông thần ngọc phù, tinh thần khẽ động, đồng thời đi vào Thông Thần điện."),t("br"),t("br"),n._v("Hạch tâm trưởng lão đi vào hạch tâm đệ tử Thông Thần điện, cũng cùng trước đó Mộc trưởng lão đám người đồng dạng, dùng tới ngụy trang, người ngoài nhìn không ra."),t("br"),t("br"),n._v("Lúc này, giao đấu địa phương đã đổi, đến công khai thi đấu tỷ thí tràng trước."),t("br"),t("br"),n._v("Hạch tâm đệ tử cũng nhận được tin tức, tích tụ càng ngày càng nhiều, dưới đài đã có hơn mười vị khoảng cách, lại thêm lặng lẽ tới quan sát trưởng lão, tên khác nhau, ai cũng không biết cụ thể là ai."),t("br"),t("br"),n._v("Giờ phút này, Bạch Nguyễn Khanh đang cùng xếp hạng thứ năm Mặc Hương Vân giao đấu!"),t("br"),t("br"),n._v("Mặc Hương Vân, giống như nàng, cũng là nữ đệ tử, chẳng qua dung mạo hơi kém một tia, cũng lôi thôi lếch thếch, cả người thân hình cao lớn, so với bình thường nam sĩ đều phải uy mãnh."),t("br"),t("br"),n._v("Không nghe được tên, đều cho là nam."),t("br"),t("br"),n._v("Không chỉ dung mạo như vậy, chiêu số cũng cùng nam sĩ có chút tương tự, kiếm pháp đại khai đại hợp, cho người ta một loại rìu đục lực uy hiếp."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Hai bên đứng vững, Mặc Hương Vân chẳng muốn quá nhiều phí lời, thân thể giống như xe tăng hướng về phía trước nghiền ép, cùng thời khắc đó trường kiếm trong tay, cuồng chém mà tới!"),t("br"),t("br"),n._v("Kiếm của nàng, cùng người bình thường không giống, chừng bảy thước, không biết nặng bao nhiêu, hướng phía dưới vừa rơi xuống, không khí phát ra âm bạo, tựa như bất cứ lúc nào cũng sẽ bị xé nứt."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh không dám đón đỡ, trường kiếm trong tay quét ngang, chặn lại chiêu thứ nhất thế công, đồng thời lật tay lại, phá không mà ra, thẳng tắp hướng về phía đối phương bắn tới."),t("br"),t("br"),n._v("Nàng hiện tại mạnh nhất chính là cái này hai chiêu, cái khác chiêu số đến năm vị trí đầu loại cảnh giới này, tiện tay liền có thể ngăn cản, thậm chí còn có thể bởi vậy rơi vào bị động, bởi vậy, trực tiếp sử dụng, không dám bảo lưu."),t("br"),t("br"),n._v("“Ha ha, chiêu này đối ta vô dụng!”"),t("br"),t("br"),n._v("Mặc Hương Vân trường kiếm trong tay bỗng nhiên nâng lên."),t("br"),t("br"),n._v("Leng keng!"),t("br"),t("br"),n._v("Bắn ra kiếm liền bị cản lại, Bạch Nguyễn Khanh nhanh chóng lui về phía sau, miệng lớn thở hổn hển."),t("br"),t("br"),n._v("Không hổ là vọt tới năm vị trí đầu cường giả, mỗi một cái đều có thuộc về mình tuyệt chiêu, không phải nàng học được chút da lông, liền có thể đánh bại."),t("br"),t("br"),n._v("Huống chi, đối phương còn nhìn nàng trước đó giao chiến cảnh tượng, sớm có đề phòng."),t("br"),t("br"),n._v("“Có dám hay không ở đây chờ một lát ta một hồi... Ta một lát nữa cùng ngươi lại chiến?” Biết cái này hai chiêu không đủ để đem hắn đánh bại, dừng lại một chút, Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Mặc Hương Vân gật đầu."),t("br"),t("br"),n._v("Trước đó, đối phương cũng từng có loại kinh nghiệm này, vài phút mà thôi, chờ được."),t("br"),t("br"),n._v("Ôm quyền khom người, Bạch Nguyễn Khanh tác phẩm hội họa điểm sáng, biến mất tại tỷ thí đài, sau một khắc, lần nữa trở lại sân nhỏ."),t("br"),t("br"),n._v("“Hiểu Thiên sư đệ, nhanh lên một chút truyền thụ cho ta hai chiêu...”"),t("br"),t("br"),n._v("Vừa ra cửa phòng liền hô lên, ngay sau đó nhìn thấy một bóng người, vẻ mặt một hồi xấu hổ: “Sư thúc!”"),t("br"),t("br"),n._v("Xuất hiện ở trước mắt, không phải người khác, chính là Trương Huyền!"),t("br"),t("br"),n._v("Tu vi thành công vừa tấn cấp đến Hư Tiên cảnh đỉnh phong... Thuận lợi xuất quan!"),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Nữ nhân này, làm sao chạy đến bản thân trong sân? Hơn nữa còn để Thiện Hiểu Thiên truyền thụ nàng kiếm thuật?"),t("br"),t("br"),n._v("“Ta dùng sư thúc truyền thụ cho kiếm pháp... Ở hạch tâm Thông Thần điện cùng người so kiếm...” Hơi đỏ mặt, Bạch Nguyễn Khanh đem sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("Nghe nàng nói xong, Trương Huyền nhìn qua: “Có hay không đánh cược?”"),t("br"),t("br"),n._v("“Không có ah...”"),t("br"),t("br"),n._v("Một mặt mê man, Bạch Nguyễn Khanh lắc đầu."),t("br"),t("br"),n._v("Ta đang nói, dùng ngươi truyền thụ cho kiếm chiêu cùng người tỷ thí, ngươi hỏi ta đánh cược... Cái quỷ gì?"),t("br"),t("br"),n._v("“Không có đánh cược, có gì có thể so?” Trương Huyền chỉ tiếc mài sắt không nên kim."),t("br"),t("br"),n._v("Vốn nghĩ, tìm cơ hội đi vào hạch tâm, lại đi làm thịt một bút... Cái tên này ngược lại tốt, thậm chí ngay cả cược đều không đánh!"),t("br"),t("br"),n._v("Còn có thể vui vẻ vui đùa ư?"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Gặp hắn bộ biểu tình này, Bạch Nguyễn Khanh da mặt run rẩy."),t("br"),t("br"),n._v("Một mực đến nay, đều cho rằng “Ta Rất Biết Điều” là thật biết điều, thật cao nhân, xem danh lợi như mây trôi... Náo loạn nửa ngày, như vậy tham tiền!"),t("br"),t("br"),n._v("Ta tỷ thí, chỉ là vì nghiệm chứng kiếm pháp, làm sao làm, tại trong miệng ngươi, theo tới đoạt tiền giống như?"),t("br"),t("br"),n._v("Đường đường trưởng lão cháu gái, cần đoạt tiền?"),t("br"),t("br"),n._v("“Còn có hạch tâm thông thần ngọc phù ư? Cho ta một cái, ta đi qua khiêu chiến một chút... Đúng, hạch tâm đệ tử, có phải hay không từng cái đều rất có tiền?”"),t("br"),t("br"),n._v("Không để ý tới đối phương buồn bực, Trương Huyền hỏi."),t("br"),t("br"),n._v("Vẫn là sớm hỏi rõ ràng tốt hơn."),t("br"),t("br"),n._v("“Vâng...” Bạch Nguyễn Khanh che cái trán."),t("br"),t("br"),n._v("Làm Lăng Vân kiếm các hạch tâm, tu vi kém cỏi nhất đều đạt đến Hư Tiên cảnh, sung túc mức độ, khẳng định không phải nội môn đệ tử có thể so sánh được."),t("br"),t("br"),n._v("“Vậy là tốt rồi, thông thần ngọc phù đâu?”"),t("br"),t("br"),n._v("Trương Huyền đưa tay."),t("br"),n._v(" “Hạch tâm đệ tử Thông Thần điện, yêu cầu thấp nhất là Hư Tiên cảnh sơ kỳ, nói cách khác... Không đến cấp bậc này, là không vào được...”"),t("br"),t("br"),n._v("Gặp hắn muốn ngọc phù, Bạch Nguyễn Khanh do dự một chút."),t("br"),t("br"),n._v("Tu vi của đối phương, hơn hai canh giờ phía trước, lúc tỷ đấu, mới phá toái hư không đỉnh phong, mà muốn đi vào hạch tâm đệ tử Thông Thần điện, cần Hư Tiên cảnh sơ kỳ, rõ ràng còn chưa đủ!"),t("br"),t("br"),n._v("“Không có việc gì, ta đã đột phá...”"),t("br"),t("br"),n._v("Lung lay thân thể, Trương Huyền khí tức trong người, bừng lên."),t("br"),t("br"),n._v("“Hư Tiên cảnh... Đỉnh phong?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Chân núi Phong lão vây chặt thời điểm, thật giống cũng là phá toái hư không đỉnh phong a? Ngắn ngủi hơn một canh giờ điểm, liền biến thành Hư Tiên cảnh đỉnh phong, tăng lên trọn một cái đại cấp bậc... Làm sao làm được?"),t("br"),t("br"),n._v("Cắn thuốc cũng không có nhanh như vậy đi!"),t("br"),t("br"),n._v("“Thiếu gia uy vũ!”"),t("br"),t("br"),n._v("Một bên Tào Thành Lập con mắt sáng lên, Thiện Hiểu Thiên cũng nắm đấm xiết chặt."),t("br"),t("br"),n._v("Sớm biết thiếu gia (lão sư) rất mạnh, thật không nghĩ đến, mạnh đến loại tình trạng này."),t("br"),t("br"),n._v("“Thiếu gia, tiến bộ nhanh như vậy, kiếm pháp cũng cao minh, nhất định có thể đánh bại ‘Ta Rất Biết Điều’ cái kia hai hàng!”"),t("br"),t("br"),n._v("Kích động một hồi, Tào Thành Lập nói."),t("br"),t("br"),n._v("Hắn một mực nghe nói, vị kia “Ta Rất Biết Điều” bao nhiêu lợi hại, bao nhiêu lợi hại, có thể lợi hại hơn nữa, lợi hại quá ít gia ư?"),t("br"),t("br"),n._v("Ngắn ngủi không tới một ngày, từ phá toái hư không đỉnh phong, tấn cấp đến Hư Tiên cảnh đỉnh phong..."),t("br"),t("br"),n._v("Lăng Vân kiếm các, cũng không có cường đại như vậy tồn tại đi!"),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Vừa cảm xúc hết, đột nhiên cảm thấy trên mặt lần nữa tê rần, lại bị đánh tại chỗ chuyển hai quyền, vội vàng ngẩng đầu ngay sau đó nhìn thấy thiếu gia, mặt đen như đáy nồi: “Vừa rồi ta nhìn thấy ngươi, sau khi vào cửa dùng chân phải, phá hủy quy củ của ta, cho nên cái kia đánh!”"),t("br"),t("br"),n._v("“Chân trái làm hư quy củ, chân phải cũng hư?”"),t("br"),t("br"),n._v("Con mắt tối sầm lại, Tào Thành Lập suýt chút nữa không có khóc."),t("br"),t("br"),n._v("Chân trái không thể vào môn, chân phải cũng không thể vào, cái này còn thế nào vào cửa..."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Không thèm để ý cái này đầy trong đầu đều là nữ nhân, không muốn chính sự sơn tặc, Trương Huyền cùng Bạch Nguyễn Khanh về đến phòng, ngón tay một điểm, huyết dịch đi vào ngọc phù, ý thức tại nguyên chỗ biến mất, sau một khắc đi tới hạch tâm đệ tử Thông Thần điện."),t("br"),t("br"),n._v("“Lần trước kêu ‘Ta Rất Biết Điều’, huyên náo động tĩnh rất lớn, Lăng Vân kiếm các khẳng định tra xét, cái tên này lại không có thể sử dụng...” Đứng tại chỗ dừng lại một chút, Trương Huyền suy nghĩ."),t("br"),t("br"),n._v("Ta Rất Biết Điều, một người khiêu chiến toàn bộ nội môn, huyên náo người ngã ngựa đổ, tên này khẳng định không thể lại xuất hiện, bởi vậy, nhất định phải còn muốn cái mới ngoại hiệu mới được."),t("br"),t("br"),n._v("“Ngoại hiệu không tốt lấy, ‘Biết điều’ không thể dùng, xem ra, chỉ có thể mượn ta bản thân ưu thế...”"),t("br"),t("br"),n._v("Rầu rĩ lão đại một hồi, không biết nên lấy vật gì tên tốt, một lát sau, làm ra quyết định: “Liền kêu ‘Ta Rất Tuấn Tú’... Tuy là không có điệu thấp như vậy, nhưng cũng ăn ngay nói thật, không tính nói dối!”"),t("br"),t("br"),n._v("Thoả mãn gật gật đầu."),t("br"),t("br"),n._v("“Ta Rất Biết Điều” cái tên này, hắn là hài lòng nhất, hoàn mỹ biểu đạt tâm lý của hắn cùng nguyện vọng, chẳng qua cái này “Ta Rất Tuấn Tú” cũng phù hợp khí chất, xem như nói còn nghe được."),t("br"),t("br"),n._v("Một bên Bạch Nguyễn Khanh nhìn thấy hắn lấy cái ngoại hiệu này, lần nữa che cái trán..."),t("br"),t("br"),n._v("Đại ca... Ta có thể lấy được êm tai một ít, khiêm tốn một ít ư?"),t("br"),t("br"),n._v("Có thể hay không đừng không biết xấu hổ như vậy..."),t("br"),t("br"),n._v("Một mặt xấu hổ, đi vào Thông Thần điện, đi tới tỷ thí địa phương."),t("br"),t("br"),n._v("“Ta hiện tại đang cùng Mặc Hương Vân tỷ thí, nên làm cái gì?”"),t("br"),t("br"),n._v("Vừa đi, Bạch Nguyễn Khanh một bên truyền âm."),t("br"),t("br"),n._v("Cái gì đều không có học, liền trở lại, khẳng định không phải là đối thủ."),t("br"),t("br"),n._v("“Rất đơn giản, cùng đối phương đánh cược, thua, đem tất cả kiếm các tệ đều thua ngươi... Sau đó, ta chỉ điểm ngươi thế nào thi triển kiếm pháp, từ đó chiến thắng!”"),t("br"),t("br"),n._v("Trương Huyền truyền âm qua."),t("br"),t("br"),n._v("“Chỉ điểm? Lúc tỷ đấu chỉ điểm?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh khóe miệng giật một cái."),t("br"),t("br"),n._v("Chiến đấu thay đổi trong nháy mắt, lúc này đang chỉ điểm, đã chậm đi!"),t("br"),t("br"),n._v("Gia gia loại cấp bậc này siêu cấp cao thủ, cũng không dám làm như vậy, ngươi xác định làm như vậy... Ta sẽ không bị giết?"),t("br"),t("br"),n._v("Vốn, giết không sao, dù sao, bản thân cũng giết không ít... Chỉ khi nào đánh cược thua, là được nghèo rớt mồng tơi!"),t("br"),t("br"),n._v("“Không sai!” Trương Huyền gật đầu: “Đi đi, sẽ không thua...”"),t("br"),t("br"),n._v("“... Tốt a!”"),t("br"),t("br"),n._v("Cũng không biết nghe đối phương chính là đúng là sai, Bạch Nguyễn Khanh đành phải cắn răng, nhấc chân đi trở về tỷ thí đài."),t("br"),t("br"),n._v("“Đã trở về, vậy thì tiếp tục đi...”"),t("br"),t("br"),n._v("Mặc Hương Vân gật gật đầu, trường kiếm trong tay lần nữa phát ra nghẹn ngào thanh âm."),t("br"),t("br"),n._v("“Trước không vội...”"),t("br"),t("br"),n._v("Thấy dưới đài sư thúc kiên định ánh mắt, Bạch Nguyễn Khanh bờ môi cắn chặt: “Đã tỷ thí, ta muốn đánh cược!”"),t("br"),t("br"),n._v("“Đánh cược?”"),t("br"),t("br"),n._v("Mặc Hương Vân nhíu nhíu mày."),t("br"),t("br"),n._v("“Không sai, chính là... Ta thắng, ngươi đem trên người tất cả kiếm các tệ, đều bại bởi ta! Ta thua... Ta đem trên người tất cả kiếm các tệ đều cho ngươi!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh vẻ mặt thấu hồng."),t("br"),t("br"),n._v("Tuy là sư thúc lời nói không dám vi phạm, có thể... Thật nói ra, thật tốt ngượng ngùng đây!"),t("br"),t("br"),n._v("“Cược tất cả tiền?” Mặc Hương Vân con mắt trợn tròn: “Ngươi đây là muốn ăn cướp?”"),t("br"),t("br"),n._v("“Ừm...” Bạch Nguyễn Khanh gật đầu: “Xem như thế đi!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Tôn nữ của ngươi... Đây là thiếu tiền thiếu điên rồi sao?”"),t("br"),t("br"),n._v("Cùng Bạch Diệp trưởng lão cùng đi vị lão giả kia, quay đầu nhìn về phía bên cạnh vị này toàn bộ tông môn xếp hạng trước ba trưởng lão."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão cũng ngây người."),t("br"),t("br"),n._v("Tỷ thí đặt cược đánh bạc, còn tưởng là lấy... Nhiều người như vậy mặt... Đây thật là bản thân cái kia nghe lời, có hiếu tâm cháu gái?"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1992"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1994"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);