(window.webpackJsonp=window.webpackJsonp||[]).push([[1123],{1478:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1985-gap-tong-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1985-gap-tong-chu"}},[n._v("#")]),n._v(" Chương 1985: Gặp tông chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thật, ta là thật muốn học đao, đao pháp đại khai đại hợp, lực lượng cuồng bạo, phù hợp tính cách và khí chất...” Còn tưởng rằng đối phương tin tưởng, Trương Huyền liền vội vàng gật đầu."),t("br"),t("br"),n._v("Kiếm pháp nhẹ nhàng, đao pháp nặng nề, dưới tình huống bình thường, cả hai không thể đều chiếm được."),t("br"),t("br"),n._v("Đã đối phương, vẫn muốn để cho mình cùng hắn học kiếm, nói không am hiểu, ưa thích đao pháp thì tốt rồi... Dù sao cũng không phải Lăng Vân kiếm các đệ tử, không có gì làm trái nguyên tắc."),t("br"),t("br"),n._v("Chỉ cần bày ra, mình am hiểu đao pháp, đối phương khẳng định liền sẽ từ bỏ thu đồ tính toán!"),t("br"),t("br"),n._v("“Khụ khụ... Không biết ngươi đao pháp thế nào? Có thể hay không bày ra cho chúng ta nhìn một chút?”"),t("br"),t("br"),n._v("Cảm giác trong lòng có một vạn đầu Thần thú bay qua, Bạch Diệp trưởng lão cố nén buồn bực hỏi."),t("br"),t("br"),n._v("Ngươi mạnh như vậy kiếm pháp, không muốn trở thành đệ tử ta, có thể lý giải, nhưng nói muốn muốn học đao... Ngược lại muốn xem xem, làm sao thi triển!"),t("br"),t("br"),n._v("Không thi triển ra được, hoặc là đao pháp bên trong mang theo kiếm ý, nhìn ngươi làm sao xuống đài!"),t("br"),t("br"),n._v("Bị người cự tuyệt như vậy, thân là tam đại trưởng lão một trong, ở sâu trong nội tâm cũng là có chút không vui."),t("br"),t("br"),n._v("“Ta không có đao...” Trương Huyền xua tay, nói còn chưa dứt lời, chỉ thấy Bạch Diệp trưởng lão đưa tới một thanh trường đao: “Ta chỗ này có!”"),t("br"),t("br"),n._v("Mặc dù hắn là Lăng Vân kiếm các trưởng lão, kiếm bên ngoài vũ khí, cũng đều có mang theo."),t("br"),t("br"),n._v("Đá ở núi khác có thể công ngọc."),t("br"),t("br"),n._v("Muốn cho kiếm pháp càng cao minh hơn, nhất định phải biết những binh khí khác tốt thiếu sót."),t("br"),t("br"),n._v("Liền giống như Độc Cô cửu kiếm bên trong, có Phá Đao thức, Phá Tiên thức, Phá Tiễn thức, Phá Chưởng thức..."),t("br"),t("br"),n._v("Muốn phá giải, đương nhiên cần càng hiểu hơn."),t("br"),t("br"),n._v("“Tốt thôi!”"),t("br"),t("br"),n._v("Không còn đường lui, nhận lấy trường đao, Trương Huyền nhẹ nhàng lắc một cái, một cỗ cường đại đao ý, lập tức từ trên người lan ra mà ra, ngay sau đó cả người giống như là biến thành một thanh sắc bén vô song trường đao, bất cứ lúc nào cũng sẽ đem thương thiên bổ ra cái lỗ thủng."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, Bạch Diệp trưởng lão cùng Bạch Phong từng người đồng tử co rút lại: “Cái này... Mẹ nó cũng được?”"),t("br"),t("br"),n._v("Bọn họ trong nháy mắt, cảm nhận được, trong cơ thể mình kiếm ý, bị người trước mắt này đao ý, chèn ép không ngẩng đầu được lên!"),t("br"),t("br"),n._v("Nói cách khác..."),t("br"),t("br"),n._v("Đối phương đối đao ý lý giải, tới một mức độ nào đó, so với bọn hắn kiếm ý, càng thêm tinh thuần, mạnh mẽ!"),t("br"),t("br"),n._v("Trước mắt vị này, nhưng mà lĩnh ngộ “Thần chi kiếm ý”, có khả năng trở thành kiếm thần siêu cấp nhân vật..."),t("br"),t("br"),n._v("Vậy mà đối đao ý lĩnh ngộ mạnh như vậy..."),t("br"),t("br"),n._v("Làm sao cảm giác bản thân những năm này khổ tu, đều học được thân chó bên trên?"),t("br"),t("br"),n._v("“Ta có phải hay không tương đối am hiểu đao pháp...”"),t("br"),t("br"),n._v("Lần nữa đem đao run rẩy một chút, Trương Huyền nhẹ nhàng run rẩy, thu về lực lượng trong cơ thể, nói."),t("br"),t("br"),n._v("Nhìn đối phương bộ dáng khiếp sợ, hẳn là tin tưởng..."),t("br"),t("br"),n._v("Vì không làm đối phương đệ tử, cũng thật sự là liều mạng..."),t("br"),t("br"),n._v("“Là am hiểu đao pháp...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Bạch Diệp trưởng lão áp chế lại khiếp sợ trong lòng, lại không muốn giấu diếm: “Thật ra thì, ta lần này tới, cũng không phải là muốn thu ngươi làm đồ đệ! Mà là... Xác nhận thân phận của ngươi, muốn mang ngươi đi trưởng lão điện!”"),t("br"),t("br"),n._v("“Thân phận?”"),t("br"),t("br"),n._v("Trương Huyền cứng đờ: “Thân phận gì?”"),t("br"),t("br"),n._v("  “Không có đoán sai, ngươi hẳn là ‘Ta Rất Biết Điều’, ‘Ta Rất Tuấn Tú’ a?”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão nói."),t("br"),t("br"),n._v("“Cái gì ‘Ta Rất Biết Điều’, ‘Ta Rất Tuấn Tú’ ?” Trương Huyền da đầu nổ tung, trên mặt nhưng không có bất kỳ biểu lộ gì, vội vàng xua tay: “Ta không biết các ngươi nói cái gì ý tứ...”"),t("br"),t("br"),n._v("“Không cần phủ nhận! Ta cháu gái này, tình huống gì, ta chẳng lẽ không rõ ràng? Tùy tiện, học kiếm pháp không có chút nào chăm chú... Nếu không, cũng sẽ không chỉ có hiện tại trình độ! Đi theo phía sau ngươi, ngắn ngủi nửa canh giờ, liền có khiêu chiến hạch tâm đệ nhất năng lực, loại trừ ‘Ta Rất Biết Điều’ vị này lĩnh ngộ Thần chi kiếm ý siêu cấp thiên tài, thực sự nghĩ không ra những người khác!”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão nói."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Trương Huyền nổi giận."),t("br"),t("br"),n._v("Bà mẹ ngươi ah!"),t("br"),t("br"),n._v("Nếu đã xác định, vừa đến liền nói ah..."),t("br"),t("br"),n._v("Để cho ta lại thi triển đao pháp, lại diễn kịch, đầu đầy mồ hôi... Khỉ làm xiếc ư?"),t("br"),t("br"),n._v("“Ta không có ác ý, nếu như có, đã sớm động thủ, mặc dù ngươi lĩnh ngộ Thần chi kiếm ý... Không có gì bất ngờ xảy ra, cũng mới Hư Tiên cảnh sơ kỳ a, hẳn là còn lâu mới là đối thủ của ta!”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão nói."),t("br"),t("br"),n._v("Đối phương mặc dù không biết dùng cái gì bí pháp áp chế tu vi, thoạt nhìn chỉ có phá toái hư không đỉnh phong, trên thực tế có thể đi vào hạch tâm đệ tử Thông Thần điện, chứng minh thực lực, đã đạt đến Hư Tiên cảnh."),t("br"),t("br"),n._v("“Tốt thôi... Ta thừa nhận!”"),t("br"),t("br"),n._v("Thấy đối phương không nhìn ra tu vi của mình, Trương Huyền cũng không giải thích, đành phải gật gật đầu."),t("br"),t("br"),n._v("Đều bị nhìn đi ra, tiếp tục giấu diếm cũng không có ý nghĩa gì, còn không bằng sảng khoái một chút."),t("br"),t("br"),n._v("Đối phương nói không sai, mặc kệ chính mình là Hư Tiên cảnh sơ kỳ, vẫn là đỉnh phong... Tại Tiên Quân cường giả phía trước, không có một tia năng lực phản kháng, thật muốn ra tay, căn bản trốn không thoát!"),t("br"),t("br"),n._v("“Quả nhiên...”"),t("br"),t("br"),n._v("Gặp hắn thừa nhận, Bạch Diệp trưởng lão nắm đấm xiết chặt."),t("br"),t("br"),n._v("Trong lòng mặc dù đã xác nhận, nghe hắn thừa nhận, vẫn như cũ tràn đầy kích động."),t("br"),t("br"),n._v("Thần chi kiếm ý..."),t("br"),t("br"),n._v("Lăng Vân kiếm các thành lập mấy ngàn năm, loại trừ tổ sư bên ngoài người thứ hai!"),t("br"),t("br"),n._v("“Nếu là ngươi, có thể hay không cùng đi với ta trưởng lão điện... Lĩnh ngộ Thần chi kiếm ý, đối kiếm các tới nói, mười phần quan trọng... Không có gì bất ngờ xảy ra, tương lai sẽ trở thành tông chủ của chúng ta!” Bạch Diệp trưởng lão ôm quyền."),t("br"),t("br"),n._v("Lăng Vân kiếm các lựa chọn tông chủ phương pháp rất đơn giản, căn cứ kiếm pháp cao thấp quyết định."),t("br"),t("br"),n._v("Lĩnh ngộ Thần chi kiếm ý, chỉ cần tu vi đủ, trở thành tông chủ, chắc chắn, không có bất kỳ cái gì dị nghị."),t("br"),t("br"),n._v("“Trở thành tông chủ?” Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Còn tưởng rằng, đối phương nhận ra mình, lại bởi vì nội môn cùng hạch tâm sự tình, tiến hành trừng phạt, không nghĩ tới, muốn tìm bản thân đi qua làm tông chủ..."),t("br"),t("br"),n._v("Thật muốn có loại thân phận này, không chỉ an toàn không ít, càng quan trọng hơn... Đứng ở Thượng Thương chân chính đỉnh phong, đang tìm kiếm Lạc Nhược Hi tung tích, cũng liền đơn giản!"),t("br"),t("br"),n._v("“Ừm!” Bạch Diệp trưởng lão gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, ta đi theo ngươi trưởng lão điện... Bất quá, không hy vọng quá nhiều người biết được!” Dừng lại một chút, Trương Huyền nói."),t("br"),n._v(" Mặc dù tin tưởng trước mắt vị lão giả này, vẫn là muốn cẩn thận một chút."),t("br"),t("br"),n._v("Cây có mọc thành rừng gió vẫn thổi bật rễ đạo lý, hắn hiểu."),t("br"),t("br"),n._v("Nếu không, cũng sẽ không cho tới nay, đều biết điều như vậy."),t("br"),t("br"),n._v("“Điểm ấy yên tâm, chúng ta, chắc chắn sẽ không truyền ra ngoài!”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão cũng gật gật đầu."),t("br"),t("br"),n._v("Không cần hắn nói, thân là đại lục đỉnh phong nhất cường đại nhân vật, cũng rõ ràng điểm ấy."),t("br"),t("br"),n._v("“Nguyễn Khanh, chuyện ngày hôm nay, không muốn truyền ra ngoài, ta cùng vị này Trương huynh đệ, đi một lát sẽ trở lại!”"),t("br"),t("br"),n._v("Bàn giao Bạch Nguyễn Khanh một câu, Bạch Diệp trưởng lão lúc này mới lần nữa nhìn tới: “Đi thôi!”"),t("br"),t("br"),n._v("Nói xong, nhẹ nhàng vồ một cái, lực lượng đem Trương Huyền bọc, thời gian nháy mắt, bay lên, biến mất ở chân trời."),t("br"),t("br"),n._v("“Lão sư (thiếu gia)... Có khả năng trở thành Lăng Vân kiếm các tông chủ?”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Thiện Hiểu Thiên cùng Tào Thành Lập tất cả đều bối rối."),t("br"),t("br"),n._v("Bọn họ tuy là trong lòng, một mực đánh giá cao vị thanh niên này, cho rằng hắn thiên phú cực cao, thực lực cực mạnh..."),t("br"),t("br"),n._v("Có thể làm giấc mơ đều không có nghĩ đến, vậy mà lĩnh ngộ Thần chi kiếm ý!"),t("br"),t("br"),n._v("Tuổi còn trẻ liền có cơ hội trở thành sáu đại tông môn một trong Lăng Vân kiếm các người cầm lái!"),t("br"),t("br"),n._v("Đặc biệt là người sau, thân thể run rẩy, không tự chủ được bại liệt trên mặt đất."),t("br"),t("br"),n._v("Khó trách, thiếu gia “Quy củ” kỳ quái như thế, không để cho chân trái vào cửa, cũng không để cho chân phải vào cửa..."),t("br"),t("br"),n._v("Mình nói hai lần “Ta Rất Biết Điều” cái này hai hàng... Không phải tương đương với mở miệng mắng hắn ư?"),t("br"),t("br"),n._v("Không có tại chỗ đánh chết, coi như kềm chế cực cao."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Kiếm các đình."),t("br"),t("br"),n._v("Tông chủ, Hà trưởng lão đám người, đang ngồi ở mặt tường phía trước, trông mòn con mắt."),t("br"),t("br"),n._v("Lộc cộc lộc cộc lộc cộc lộc cộc!"),t("br"),t("br"),n._v("Một cái tiếng bước chân dồn dập vang lên."),t("br"),t("br"),n._v("“Vị kia... ‘Ta Rất Biết Điều’ xuất hiện?”"),t("br"),t("br"),n._v("Trong tu luyện Hà trưởng lão, vội vàng mở mắt ra, nhìn về phía đi tới một vị lão giả."),t("br"),t("br"),n._v("“Không phải... Là có người một người khiêu chiến tất cả hạch tâm đệ tử...” Lão giả vội vàng nói."),t("br"),t("br"),n._v("Vị này, chính là trước đó tìm Bạch Diệp vị trưởng lão kia."),t("br"),t("br"),n._v("“Khiêu chiến tất cả hạch tâm đệ tử? Chẳng lẽ là... Ta Rất Biết Điều?” Đột nhiên đứng dậy, Hà trưởng lão đồng tử co rụt lại: “Không đúng, hắn bất tài phá toái hư không đỉnh phong ư?”"),t("br"),t("br"),n._v("“Người kia gọi... ‘Ta Rất Tuấn Tú’! Một hơi khiêu chiến hơn mười vị hạch tâm đệ tử, tất cả đều tại chỗ chém giết, ta cùng Bạch Diệp trưởng lão trôi qua lặng lẽ... Cũng bị chém xuống đầu...”"),t("br"),t("br"),n._v("Lão giả hơi đỏ mặt."),t("br"),t("br"),n._v("“Chém giết hơn mười vị hạch tâm đệ tử, đem ngươi cùng Bạch Diệp cũng chém giết...” Hàn tông chủ mở miệng: “Khẳng định là vị nào lĩnh ngộ Thần chi kiếm ý thiên tài, thẩm tra ‘Ta Rất Tuấn Tú’ ở đâu...”"),t("br"),t("br"),n._v("Đơn thuần chém giết hơn mười vị hạch tâm đệ tử, hắn có thể làm được dễ dàng, nhưng tuỳ tiện chém giết Bạch Diệp trưởng lão cùng trước mắt vị này, liền xem như hắn, cũng không dễ dàng như vậy!"),t("br"),t("br"),n._v("Không cần nghĩ, vị này “Ta Rất Tuấn Tú” cùng “Ta Rất Biết Điều” hẳn là cùng là một người!"),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Nghe được dặn dò mặt tường lắc lư một cái, cũng không biểu hiện nội dung."),t("br"),t("br"),n._v("“Bẩm báo tông chủ, hắn... Đem tất cả hạch tâm đệ tử chém giết hết, liền rời đi Thông Thần điện!”"),t("br"),t("br"),n._v("Lão giả vội vàng giải thích: “Bất quá... Bạch Diệp trưởng lão nói đã xác định là ai, đi qua tìm, cho các ngươi trước về trưởng lão điện đợi chờ, không có gì bất ngờ xảy ra, rất nhanh liền có thể đem mang về!”"),t("br"),t("br"),n._v("“Bạch Diệp xác định?”"),t("br"),t("br"),n._v("Nhìn nhau, mấy người khó có thể tin."),t("br"),t("br"),n._v("Bạch Diệp trưởng lão một mực trọng thương, liền trưởng lão hội nghị đều không có tham gia, làm sao có thể so với bọn hắn trước xác định?"),t("br"),t("br"),n._v("“Là như vậy...”"),t("br"),t("br"),n._v("Lão giả vội vàng giải thích."),t("br"),t("br"),n._v("Bạch Nguyễn Khanh mua thuốc sự tình, hắn cũng không biết, có điều, lại biết cái trước dựa vào hai chiêu khiêu chiến hạch tâm sự tình."),t("br"),t("br"),n._v("Có lẽ, Bạch Diệp trưởng lão chính là căn cứ cái này, xác định thân phận đối phương."),t("br"),t("br"),n._v("“Bạch Nguyễn Khanh, chỉ học được hai chiêu, từ hạch tâm tên thứ mười bảy một hơi giết tới hạng nhất, đem cháu của ta đều đánh ngang?”"),t("br"),t("br"),n._v("Hà trưởng lão trợn mắt lên."),t("br"),t("br"),n._v("Nếu như nói trước đó còn có chút nghi ngờ, hiện tại cơ bản đã xác định."),t("br"),t("br"),n._v("“Nhanh lên một chút trở về đi...”"),t("br"),t("br"),n._v("Biết những này, mọi người không nói thêm lời, vội vã trở lại trưởng lão điện."),t("br"),t("br"),n._v("Vừa mới ngồi xuống, liền thấy Bạch Diệp trưởng lão mang theo một thanh niên bay tới."),t("br"),t("br"),n._v("“Tông chủ, ngươi trở về...”"),t("br"),t("br"),n._v("Nhìn thấy chủ vị người đang ngồi, Bạch Diệp trưởng lão ánh mắt sáng lên, nói một tiếng, vội vàng giới thiệu: “Vị này chính là... Lĩnh ngộ Thần chi kiếm ý vị kia siêu cấp thiên tài, Trương Huyền!”"),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Mọi người tràn đầy tò mò nhìn lại."),t("br"),t("br"),n._v("Lúc này mới phát hiện, người trước mắt này, chỉ có chừng hai mươi tuổi, trẻ tuổi không tưởng nổi."),t("br"),t("br"),n._v("“Trương Huyền, vị này chính là chúng ta Lăng Vân kiếm các đương thời Hàn tông chủ, Hàn Kiếm Thu!”"),t("br"),t("br"),n._v("Bạch Diệp trưởng lão giới thiệu."),t("br"),t("br"),n._v("“Tông chủ?”"),t("br"),t("br"),n._v("Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Vị này Hàn Kiếm Thu, là cái hơn sáu mươi tuổi lão giả, một thân áo xanh, trên người kiếm ý nồng đậm, khí tức càng là mạnh mẽ như trụ, để cho người ta một cái nhìn không ra sâu cạn."),t("br"),t("br"),n._v("Mặc dù không biết tu vi gì, nhưng cũng biết, vượt xa Bạch Diệp trưởng lão cùng cách đó không xa Hà trưởng lão!"),t("br"),t("br"),n._v("“Chẳng lẽ... Là một vị vượt qua Tiên Quân cường giả?”"),t("br"),t("br"),n._v("Trong lòng hơi động."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1997"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1999"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);