(window.webpackJsonp=window.webpackJsonp||[]).push([[1124],{1479:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1986-treo-len-đanh-truong-lao-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1986-treo-len-đanh-truong-lao-đien"}},[n._v("#")]),n._v(" Chương 1986: Treo lên đánh trưởng lão điện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Treo lên đánh trưởng lão điện (bên trên)"),t("br"),t("br"),n._v("Vượt qua Tiên Quân cụ thể là cấp bậc gì, nhìn qua trong thư tịch không có ghi chép, Lục Vân trưởng lão cũng không nói qua, nhưng trước mắt vị này, Rõ ràng đã đi đến."),t("br"),t("br"),n._v("Loại người này, sâu không lường được, thật muốn động thủ với hắn, nhất niệm liền có thể xoá bỏ."),t("br"),t("br"),n._v("“Trương Huyền tiểu hữu, không biết ngươi có tiện hay không, biểu diễn một điểm kiếm pháp, cho chúng ta nhìn một chút?”"),t("br"),t("br"),n._v("Ngay tại khiếp sợ, Hàn Kiếm Thu tông chủ tiếng cười vang lên."),t("br"),t("br"),n._v("“Không biết tông chủ, để cho ta thế nào bày ra?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Tu vi của ngươi, chỉ có Hư Tiên cảnh sơ kỳ... Không cách nào đi vào trưởng lão đặc thù Thông Thần điện, như vậy, chúng ta cùng một chỗ đi vào hạch tâm đệ tử Thông Thần điện, mọi người tương đồng lực lượng, tương đồng tu vi, tỷ thí một trận, như vậy, cũng có thể tốt hơn bày ra kiếm pháp!”"),t("br"),t("br"),n._v("Hàn Kiếm Thu cười cười nói."),t("br"),t("br"),n._v("Trưởng lão, có thuộc về trưởng lão Thông Thần điện, hạch tâm có thuộc về hạch tâm, tu vi của đối phương, Rõ ràng còn chưa đủ tư cách tiến vào trưởng lão cấp bậc này, chỉ có thể tiến vào thấp một cái cấp bậc."),t("br"),t("br"),n._v("“Đi vào Thông Thần điện... Coi như, không bằng các ngươi đè thấp tu vi, ngay ở chỗ này so với ta a!”"),t("br"),t("br"),n._v("Thấy đối phương cũng không nhìn ra bản thân cố ý áp chế tu vi, Trương Huyền mỉm cười, nói."),t("br"),t("br"),n._v("“Ở đây? Ngươi xác định?” Hàn tông chủ còn chưa lên tiếng, Hà trưởng lão nhíu nhíu mày, mở miệng nói: “Tại Thông Thần điện, mọi người tu vi tương đồng, chân khí số lượng dự trữ tương đồng, là công bình nhất... Mà ở đây, chúng ta cho dù đè thấp tu vi, thân thể cường độ, linh hồn cường độ, chân khí độ tinh thuần cùng số lượng dự trữ, cũng là không cách nào khống chế...”"),t("br"),t("br"),n._v("Thông Thần điện, chỉ là một tia ý thức tiến vào bên trong, phân phối thân thể, thuộc tính tất cả đều giống nhau như đúc, cùng khôi lỗi phụ thể có chút tương tự, giao đấu, mới có thể chân chính biểu hiện ra kiếm pháp mạnh yếu."),t("br"),t("br"),n._v("Mà ở đây, cho dù đè thấp tu vi, Tiên Quân cảnh thân thể, linh hồn, cũng là mười phần đáng sợ, căn bản không phải một cái Hư Tiên cảnh sơ kỳ, có thể chống lại."),t("br"),t("br"),n._v("“Không sao!” Trương Huyền nói."),t("br"),t("br"),n._v("Đi vào Thông Thần điện, cứ việc thư viện có thể nhìn thấu thân phận, lại cũng chỉ có thể xác nhận thi triển kiếm pháp các loại, mà ở đây, biết càng thêm tỉ mỉ, thậm chí tổ tông mười tám đời, đều có thể đào ra... Vạn nhất xuất hiện biến cố, những vật này, có lẽ liền có thể trở thành bảo vệ tính mạng dựa dẫm!"),t("br"),t("br"),n._v("“Hừ, nếu ngươi quyết định như vậy, vừa vặn chúng ta cũng muốn nhìn một chút, Thần chi kiếm ý, đến cùng mạnh cỡ bao nhiêu!”"),t("br"),t("br"),n._v("Một vị trưởng lão đi đầu đứng lên."),t("br"),t("br"),n._v("Thần chi kiếm ý, khai phái tổ sư lĩnh ngộ, vừa tiến vào kiếm các, liền biết cao không thể chạm... Có thể Một mực không có người lĩnh ngộ, cho dù bọn họ là tông môn lợi hại nhất, cũng không biết loại này kiếm ý, đến cùng lợi hại ở nơi nào, lại đến cùng mạnh bao nhiêu!"),t("br"),t("br"),n._v("Trước mắt vị này, đã muốn ở chỗ này tỷ thí, liền không còn gì tốt hơn!"),t("br"),t("br"),n._v("Cũng có thể tự mình cảm thụ một chút."),t("br"),t("br"),n._v("Dù sao lấy thực lực của bọn hắn, cho dù đè thấp tu vi, đối phương muốn làm bị thương, cũng không có khả năng!"),t("br"),t("br"),n._v("“Tại hạ Cừu Hỏa, kiếm các hạch tâm đệ tử trưởng lão viện đại trưởng lão, Tiên Quân cảnh đỉnh phong thực lực, đi thử một chút chiêu kiếm của ngươi!”"),t("br"),t("br"),n._v("Tên là Cừu Hỏa trưởng lão đi tới trước mặt, rất nhanh liền đem tu vi áp chế đến cùng Trương Huyền đồng dạng, trường kiếm trong tay lắc một cái, phát ra thanh minh, thản nhiên nhìn tới."),t("br"),t("br"),n._v("Kiếm của bọn hắn, đều là trưởng lão điện thống nhất chế biến, tương đối cùng một cái lượt, thuộc tính cũng như nhau, không tồn tại dựa dẫm vũ khí sắc bén, mà chiếm được tiên cơ tình huống."),t("br"),t("br"),t("span",[t("br"),n._v("[ truyen cua t"),t("br"),n._v("ui . nｅt ] //ngantruyen.com/"),t("br")]),n._v(" “Một mình ngươi không đủ, các ngươi những người khác cũng cùng một chỗ xuống đây đi!”"),t("br"),t("br"),n._v("Liếc mắt nhìn, Trương Huyền cũng không rút kiếm, mà là nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Không đủ?”"),t("br"),t("br"),n._v("Bên trong căn phòng mấy vị trưởng lão, tất cả đều lông mày nhảy dựng."),t("br"),t("br"),n._v("Đặc biệt là Bạch Diệp trưởng lão, che cái trán."),t("br"),t("br"),n._v("Cái tên này lấy ngoại hiệu... “Ta Rất Biết Điều”, cái này mẹ nó chỗ nào khiêm tốn?"),t("br"),t("br"),n._v("Ở đây, không riêng gì Lăng Vân kiếm các đỉnh phong nhất cường giả, mỗi một cái, trên đại lục cũng đều tên tuổi lẫy lừng, ngươi coi như kiếm thuật rất mạnh, khiêu chiến một cái thăm dò sâu cạn, thì cũng thôi đi... Một cái muốn tất cả mọi người xuống, bao quát tông chủ..."),t("br"),t("br"),n._v("Không mang theo phách lối như vậy!"),t("br"),t("br"),n._v("“Làm sao, không dám?”"),t("br"),t("br"),n._v("Trương Huyền lông mày giương lên."),t("br"),t("br"),n._v("“Thần chi kiếm ý, là mạnh mẽ, nhưng ta có thể trở thành hạch tâm đệ tử trưởng lão điện đại trưởng lão, cũng không phải chỉ là hư danh, huống chi, chỉ là đè thấp tu vi, cũng không phải là Thông Thần điện... Muốn khiêu chiến những người khác, trước đem ta thắng rồi nói sau!”"),t("br"),t("br"),n._v("Cừu Hỏa vẻ mặt khó coi."),t("br"),t("br"),n._v("Hắn đi đầu hạ tràng, đè thấp tu vi khiêu chiến một cái Hư Tiên cảnh sơ kỳ, vốn là có chút mất phần, đối phương... Lại còn khinh thường cùng hắn tỷ thí..."),t("br"),t("br"),n._v("Quả thực chính là tại chỗ đánh mặt!"),t("br"),t("br"),n._v("Hơn nữa còn là “Đùng! Đùng! Đùng! Đùng!” Qua lại cuồng quất cái chủng loại kia!"),t("br"),t("br"),n._v("“Thắng ngươi lại nói? Cũng được!”"),t("br"),t("br"),n._v("Thấy những người khác không có kết quả ý tứ, tựa hồ đối với trước mắt vị này rất có lòng tin, Trương Huyền cười nhạt một tiếng, duỗi ra một ngón tay."),t("br"),t("br"),n._v("“Có ý tứ gì? Ngươi một chiêu đánh bại ta?”"),t("br"),t("br"),n._v("Cừu Hỏa sững sờ, giận quá mà cười: “Có phải hay không có chút quá cuồng vọng?”"),t("br"),t("br"),n._v("“Một chiêu? Ngươi cả nghĩ quá rồi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Ta nói là, không sử dụng kiếm, không cần thân thể những bộ phận khác, một ngón tay, một cái hô hấp phía trong, đem ngươi đánh bại!”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Cừu Hỏa sắp nổ tung."),t("br"),t("br"),n._v("Thân là hạch tâm đệ tử trưởng lão điện đại trưởng lão, bình thường tu tâm dưỡng tính, sớm đã đi đến không có chút rung động nào tình trạng, có thể... Đối phương nói chuyện thực sự quá khinh người!"),t("br"),t("br"),n._v("Quan trọng ánh mắt cùng động tác, nhất cử nhất động... Đều giống như tại nhìn một con kiến hôi..."),t("br"),t("br"),n._v("Hắn nhưng là Tiên Quân đỉnh phong!"),t("br"),t("br"),n._v("Đại lục người mạnh nhất một trong..."),t("br"),t("br"),n._v("Làm sao cảm giác tại trong mắt đối phương trái ngược?"),t("br"),t("br"),n._v("“Tốt, đã như vậy, ta liền lĩnh hội một cái ngươi cao chiêu, để cho ta nhìn một chút, một cái hô hấp phía trong, dựa vào một ngón tay, thế nào đem ta đánh bại...”"),t("br"),t("br"),n._v("Tràn đầy tức giận, Cừu Hỏa trưởng lão còn là nhịn xuống."),t("br"),t("br"),n._v("Thực lực đi đến hắn loại tình trạng này, sớm đã có thể tùy ý khống chế tâm tính, sẽ không bị cảm xúc chi phối."),t("br"),t("br"),n._v("“Có chút cuồng ah...”"),t("br"),t("br"),n._v("“Nào chỉ là cuồng, quả thực liền cuồng không giới hạn...”"),t("br"),n._v("Hai người thảo luận, phía trên Hàn Kiếm Thu cùng Hà trưởng lão liếc mắt nhìn nhau, từng người cười khổ."),t("br"),t("br"),n._v("Một cái Hư Tiên cảnh tiểu tử, nhìn thấy nhiều như vậy Tiên Quân đỉnh phong, không những không có sợ hãi, còn ngạo mạn, kiêu ngạo thành như vậy..."),t("br"),t("br"),n._v("Là đầu óc thiếu sợi dây, còn là kẻ tài cao gan cũng lớn?"),t("br"),t("br"),n._v("“Thần chi kiếm ý, có thể đối kháng Thần Linh... Chỉ mong thực lực thật sự có thể để chúng ta kinh diễm!”"),t("br"),t("br"),n._v("“Xem một chút đi!”"),t("br"),t("br"),n._v("Lần nữa hướng về phía trước nhìn lại."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Đáng tiếc...”"),t("br"),t("br"),n._v("Thấy lời nói đều nói thành như vậy, vẫn như cũ không có người xuống, Trương Huyền có chút thất vọng."),t("br"),t("br"),n._v("Không phải hắn cuồng, mà là... Cố ý làm như vậy."),t("br"),t("br"),n._v("Chỉ cần cùng một chỗ xuống, thi triển võ kỹ, thư viện liền có thể đem tất cả mọi người thiếu hụt các loại, toàn bộ điều tra rõ ràng, từng cái xuống, ngộ nhỡ... Biết mình kiếm pháp mạnh mẽ, còn lại lo lắng do dự mặt mũi, không ra sân, chẳng phải lại không cách nào quan sát?"),t("br"),t("br"),n._v("Loại sự tình này, cũng không phải sẽ không phát sinh."),t("br"),t("br"),n._v("“Coi như, trước giải quyết đi cái này một cái, tiếp tục khiêu chiến tất cả mọi người, hẳn là đều sẽ hạ tràng...”"),t("br"),t("br"),n._v("Thấy mọi người từng cái ngồi yên lặng, Trương Huyền biết, lại để cho hắn xuống, gần như không có khả năng, lúc này cười nhạt một tiếng: “Bắt đầu đi!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, trước mắt Cừu Hỏa động."),t("br"),t("br"),n._v("Cứ việc áp chế đến Hư Tiên cảnh sơ kỳ, tốc độ vẫn như cũ nhanh tựa như gió lốc, sấm sét, đặc biệt là trước người trường kiếm, phá vỡ không gian ràng buộc, trong tích tắc, liền đến đến trước mắt."),t("br"),t("br"),n._v("Mặc dù là cùng cấp bậc, nhưng so trước đó vị kia gì yên tĩnh hiên, mạnh mẽ không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Ngay tại đối phương trường kiếm, lập tức tới đến cổ họng thời điểm, Trương Huyền ngón tay nhấc lên, hướng về phía trước một điểm."),t("br"),t("br"),n._v("Động tác không vui, nhưng cho người ta một loại càng lúc càng lớn, càng ngày càng nặng nặng cảm giác, giống như là sao băng hạ xuống, trong nháy mắt, ngay tại Cừu Hỏa trong lòng, đốt lên một loại bất khả kháng hoành ảo giác."),t("br"),t("br"),n._v("“Làm sao có thể?”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, Cừu Hỏa con mắt trợn tròn."),t("br"),t("br"),n._v("Hắn là Tiên Quân cường giả đỉnh phong, cứ việc đè thấp tu vi, nhưng tâm lý tố chất ở nơi đó bày biện, tuyệt không có khả năng bị tức tức áp chế xuất hiện ảo giác, có thể... Tay của đối phương chỉ thật cho hắn một loại không cách nào chống lại lực lượng."),t("br"),t("br"),n._v("Đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Trong lòng gào thét, đối phương đầu ngón tay mang tới cảm giác áp bách, nhào tới trước mặt, Hư Tiên cảnh chân khí, cũng lại không chịu nổi, trường kiếm không tự chủ được uốn lượn, thời gian nháy mắt xuất hiện vết rách, gãy thành vô số mảnh vỡ."),t("br"),t("br"),n._v("Mà hắn tiến lên thân thể, cũng giống là bị giam cầm ở địa phương, lại không cách nào động đậy, chỉ có thể trơ mắt nhìn đối phương ngón tay, chút hướng mình mi tâm."),t("br"),t("br"),n._v("“Cái tên này... Có ý tứ!”"),t("br"),t("br"),n._v("Hắn tràn đầy khiếp sợ, khó tin, trên đài Hàn Kiếm Thu, đột nhiên sững sờ, ngay sau đó mỉm cười."),t("br"),t("br"),n._v("“Tông chủ, cái này...”"),t("br"),t("br"),n._v("Thấy hắn hiểu được, Hà trưởng lão vẫn như cũ tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Mọi người tương đồng thực lực, trước mắt vị này Trương Huyền, một ngón tay, không chỉ đem kiếm áp đứt, lực lượng của đối phương, cũng giống là bị phong bế... Đến cùng nguyên lý gì?"),t("br"),t("br"),n._v("“Không nhìn lầm, hắn không chỉ tinh thông kiếm thuật, đối với trận pháp cũng biết rất nhiều!”"),t("br"),t("br"),n._v("Hàn Kiếm Thu giải thích."),t("br"),t("br"),n._v("“Trận pháp?” Hà trưởng lão càng mơ hồ hơn."),t("br"),t("br"),n._v("“Ừm, hắn một chỉ này, không chỉ dùng kiếm, còn mượn toàn bộ trưởng lão điện trận pháp, lấy trận pháp thai nghén kiếm ý, mới để cho lực lượng cường đại như vậy...” Hàn Kiếm Thu tông chủ nói “Vừa rồi cố ý dùng ngôn ngữ khí đối phương, trên thực tế là đang trì hoãn thời gian, đem kiếm ý ẩn giấu ở trong trận pháp...”"),t("br"),t("br"),n._v("Nói đến đây, nhịn không được cảm xúc: “Khó trách có thể lĩnh ngộ Thần chi kiếm ý, loại này đối hoàn cảnh vận dụng, quả thực nghe rợn cả người!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hà trưởng lão da mặt lắc một cái."),t("br"),t("br"),n._v("Hắn mới đến đây cái đại điện bao lâu thời gian?"),t("br"),t("br"),n._v("Nhiều nhất không cao hơn mười phút đồng hồ..."),t("br"),t("br"),n._v("Không chỉ nhận ra ẩn chứa trong đó trận pháp, còn đem kiếm ý ẩn chứa trong đó, mượn trận pháp đại thế..."),t("br"),t("br"),n._v("Cái này... Quá nghịch thiên đi!"),t("br"),t("br"),n._v("Trước đó, còn muốn lấy, cùng cấp bậc, đối phương một người không có khả năng thắng được bọn họ nhiều như vậy, hiện tại xem ra, còn thật sự có khả năng!"),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Bên này kinh ngạc nói không ra lời, phía trước chiến đấu hai người, đã có kết quả, Trương Huyền một chỉ rơi xuống, Cừu Hỏa bay ngược mà ra, người đi đường trên không trung, liền đem tu vi giải phong."),t("br"),t("br"),n._v("Nếu không, khẳng định muốn nhận nội thương nghiêm trọng."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Thu tay lại mà đứng, Trương Huyền vẻ mặt ngạo nghễ."),t("br"),t("br"),n._v("Từ ra chỉ, đến đối phương bị đánh bay, không nhiều không ít, vừa vặn một cái hô hấp."),t("br"),t("br"),n._v("“Ta... Thua!”"),t("br"),t("br"),n._v("Ngực đau rát đau, Cừu Hỏa cứ việc cho rằng đối phương chiêu này không phải đơn thuần kiếm pháp, nhưng cũng không thật nhiều nói."),t("br"),t("br"),n._v("Bị chỉ có Hư Tiên cảnh người đi đường, một chỉ đánh bại, đã rất mất thể diện, đang nói chuyện, thực sự không ngẩng đầu được lên."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Thấy hắn thừa nhận, Trương Huyền nhẹ nhàng cười một tiếng, đột nhiên nhìn quanh một vòng, thanh âm nhàn nhạt, tại mỗi một vị trưởng lão trong tai nổ vang."),t("br"),t("br"),n._v("“Không biết chư vị có thể hay không nể mặt, cùng một chỗ hạ tràng... Để cho ta một người một kiếm, lần lượt đánh bên trên một hồi... Được chứ?”"),t("br"),t("br"),n._v("(Tấu chương xong)."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1998"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2000"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);