(window.webpackJsonp=window.webpackJsonp||[]).push([[1127],{1483:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-191-truong-huyen-truyen-thuyet-thuong"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-191-truong-huyen-truyen-thuyet-thuong"}},[n._v("#")]),n._v(" Chương 191: Trương Huyền truyền thuyết (thượng)")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v(" “Ta Lục Tầm tại Thiên Huyền Vương quốc có thể xông ra mỏng manh tên tuổi, điểm ấy tự tin vẫn có, còn Dương sư có thu hay không ta, ba trăm vạn có thể hay không trả, không nhọc tiên sinh quan tâm!” "),g("br"),g("br"),n._v(" Thấy bệ hạ ba sư đều không mở miệng, một cái rực rỡ mũ lông chồn thương nhân hỏi dò, Lục Tầm không nhịn được lông mày vừa nhíu."),g("br"),g("br"),n._v(" Đối phương có thể cùng ba sư, bệ hạ ngồi cùng một chỗ, nói rõ địa vị không thấp, bất quá, hắn Lục Tầm cũng có sự kiêu ngạo của chính mình, không nói Đế sư gia đình, chỉ nói riêng tuổi còn trẻ liền trở thành Thiên Huyền Vương quốc nổi danh nhất minh tinh giáo sư này một cái, liền có tư cách cùng một ít tộc trưởng của đại gia tộc đứng ngang hàng."),g("br"),g("br"),n._v(" Lại như trước Vương gia tộc trưởng Vương Hoằng, nhìn thấy hắn cũng phải xưng hô một tiếng Lục lão sư, không dám mảy may trang đại."),g("br"),g("br"),n._v(" Trước mắt cái này ngược lại tốt, nhìn chút chính là nhà giàu mới nổi, không có một chút nào phong độ, coi như có chút thân phận thì lại làm sao? Ta tại cùng Thẩm Truy bệ hạ nói chuyện, còn chưa tới phiên ngươi đến quơ tay múa chân, làm thêm bình luận!"),g("br"),g("br"),n._v(" Ngươi là cái thá gì?"),g("br"),g("br"),n._v(" Thấy hắn như thế tự tin, ba sư mấy người suýt chút nữa bị nước bọt sặc."),g("br"),g("br"),n._v(" Ba người chúng ta chân chính danh sư, cũng không dám không chút nào cung kính... Một mình ngươi liền học đồ cũng không bằng minh tinh giáo sư ra vẻ cái gì?"),g("br"),g("br"),n._v(" “Ta không phải quan tâm, chẳng qua là cảm thấy một khi không trả, tiền khẳng định không trả nổi, đến thời điểm làm sao cùng bệ hạ bàn giao, đem ngươi bán cũng không đáng cái này tiền đi!” "),g("br"),g("br"),n._v(" Trương Huyền lắc đầu."),g("br"),g("br"),n._v(" Lòng tốt nhắc nhở, lại bị đối phương nói như vậy, cũng thực sự là bất đắc dĩ."),g("br"),g("br"),n._v(" “Vị tiên sinh này xin chú ý của ngươi ngôn từ!” "),g("br"),g("br"),n._v(" Lần này Lục Tầm còn chưa mở miệng, một bên Vương Siêu híp mắt lại: “Lục Tầm lão sư là Hồng Thiên học viện xếp hạng thứ nhất minh tinh giáo sư, sớm muộn đều sẽ trở thành danh sư, chỉ là ba trăm vạn kim tệ sẽ quỵt nợ? Đùa gì thế? Lời nói không êm tai mà nói, số tiền này, coi như người khác muốn đưa tới, cũng chưa chắc thu, ngươi cho rằng một cái tương lai danh sư ân tình, là tốt như vậy nợ?” "),g("br"),g("br"),n._v(" “Được rồi!” "),g("br"),g("br"),n._v(" Lục Tầm đánh gãy bạn tốt mà nói, eo lưng thẳng tắp, giống như một cây cây lao, không tiếp tục để ý Trương Huyền, lần thứ hai hướng Thẩm Truy ôm quyền: “Bệ hạ, ta Lục Tầm cái gì người ngươi cũng biết, coi như số tiền này Dương sư không trả, chỉ cần ta đồng ý, cũng có tự tin trong thời gian ngắn trả lại, chút tiền này, còn không đến mức để ta thất tín dự quỵt nợ!” "),g("br"),g("br"),n._v(" Minh tinh giáo sư tuy rằng không bằng danh sư, nhưng chỉ điểm người đột phá, chỉ điểm tu vi, vẫn có thể làm được, ba trăm vạn tuy lớn, chỉ cần đồng ý, trong thời gian ngắn vẫn như cũ có thể kiếm được."),g("br"),g("br"),n._v(" “Chuyện này...” "),g("br"),g("br"),n._v(" Thẩm Truy bệ hạ do dự."),g("br"),g("br"),n._v(" Ba trăm vạn kim tệ đối với một cái Vương quốc tới nói, không tính là gì, sở dĩ do dự là lo lắng trêu chọc cách đó không xa Dương sư không cao hứng."),g("br"),g("br"),n._v(" “Ta thật giống nghe nói, Dương sư hiện tại đã không tiếp tục gọi gặp người ngoài, coi như có tiền cũng chưa chắc có thể nhìn thấy!” Không thèm để ý đối phương vô lễ thái độ, Trương Huyền tiếp tục mở miệng."),g("br"),g("br"),n._v(" Cái tên này là Lục Trầm đại sư con trai, chạy đến nơi đây vay tiền, người sau khẳng định là không biết, làm sao đều muốn khuyên can một lần."),g("br"),g("br"),n._v(" Tuy rằng muốn cùng hắn tiến hành sư giả bình trắc, thuộc về quan hệ thù địch, nhưng một mã quy một mã, Lục Trầm đại sư cho không ít trợ giúp, con trai phạm hồ đồ, làm trưởng bối, vẫn có tư cách nhắc nhở."),g("br"),g("br"),n._v(" “Có thể hay không nhìn thấy, cái kia là ta chuyện của chính mình, vị tiên sinh này không cảm thấy có chút bao biện làm thay, quản quá rộng?” "),g("br"),g("br"),n._v(" Thấy cái tên này vẫn không thức thời mở miệng, Lục Tầm rốt cục có chút không cao hứng: “Ta tại hướng bệ hạ vay tiền, lại không phải tìm ngươi, mượn cùng không cho mượn, bệ hạ tự có phán đoán, còn không cần ngươi ở đây nhọc lòng.” "),g("br"),g("br"),n._v(" Nói xong quăng một cái ống tay áo."),g("br"),g("br"),n._v(" Ngươi có bị bệnh không, ta cùng Thẩm Truy bệ hạ vay tiền, vẫn đến cản trở, lại là không trả nổi, lại là vay tiền vô dụng, có ý gì?"),g("br"),g("br"),n._v(" Cho rằng ta Lục Tầm dễ ức hiếp sao?"),g("br"),g("br"),n._v(" Đường đường minh tinh giáo sư, nếu như ngay cả một điểm uy nghiêm tính khí đều không có, mặc người nói lung tung, sau đó còn làm sao tại Thiên Huyền Vương quốc hỗn? Lại nói, trên đài ba sư còn nhìn đây, nếu như nhìn thấy bị người chỉ trích, bảo sao nghe vậy, liền câu nói cũng không dám nói, sau đó còn có tư cách gì xung kích danh sư học đồ?"),g("br"),g("br"),n._v(" “Vị tiên sinh này, mặc kệ ngươi thân phận gì, đây là Lục Tầm lão sư cùng bệ hạ sự việc của nhau, vẫn xen mồm, chẳng lẽ không cảm thấy được nhiều chuyện?” "),g("br"),g("br"),n._v(" Vương Siêu cũng hừ lạnh."),g("br"),g("br"),n._v(" Ngươi ai vậy, không thấy ba sư đều không mở miệng sao?"),g("br"),g("br"),n._v(" Không nói lời nào, sẽ chết không?"),g("br"),g("br"),n._v(" “Nhiều chuyện?” "),g("br"),g("br"),n._v(" Trương Huyền chỉ là lòng tốt khuyên can, không hy vọng đối phương hoa vô dụng tiền, không nghĩ tới còn bị khinh bỉ, bất đắc dĩ lắc đầu."),g("br"),g("br"),n._v(" Hắn cái này “Dương sư” thân phận, chẳng mấy chốc sẽ biến mất, ngươi muốn trở thành của hắn học đồ, rất rõ ràng liền đánh nhầm rồi chủ ý, vốn tưởng rằng gặp mặt, nói hai câu, biết đánh nhau tiêu hắn ý nghĩ này, nằm mơ đều không nghĩ tới, lòng tốt xem là lòng lang dạ thú."),g("br"),g("br"),n._v(" Đối phương không chỉ không cảm kích, còn biến thành lắm miệng."),g("br"),g("br"),n._v(" Bất quá, đối phương loại thái độ này, cũng không cái gì có thể tức giận, đổi làm bản thân, nếu như bản thân hỏi người khác vay tiền, chủ nhà không mở miệng, bên cạnh vẫn có người nói chen vào, cũng khẳng định không cao hứng."),g("br"),g("br"),n._v(" “Ngươi gọi Lục Tầm đúng không, ta không có tới trước liền nghe nói qua tên của ngươi, ta cảm thấy dương... Hắn nói rất đúng, ngươi nên suy tính một chút!” "),g("br"),g("br"),n._v(" Trương Huyền không để ý tới đối phương trào phúng, Lưu Lăng thực sự nhẫn không xuống."),g("br"),g("br"),n._v(" Vốn là hắn đối với Lục Tầm cảm quan rất tốt, còn dự định sát hạch một phen thu làm học đồ, không nghĩ tới lần thứ nhất gặp mặt, liền thấy cảnh này, cùng Dương sư nói chuyện như vậy, quả thực coi trời bằng vung, không biết trời cao đất rộng, cái kia tia hiếm hoi còn sót lại hảo cảm cũng lập tức không còn sót lại chút gì."),g("br"),g("br"),n._v(" “Đa tạ Lưu sư đề điểm, ta đã cân nhắc tốt...” "),g("br"),g("br"),n._v(" Nghe được Lưu sư lên tiếng, Lục Tầm không dám trang lớn, vội vã ôm quyền, thái độ nói chuyện với Trương Huyền, hoàn toàn hai khái niệm."),g("br"),g("br"),n._v(" Thấy hắn không lĩnh ngộ ý của chính mình, Lưu Lăng lắc đầu không ở nhiều lời."),g("br"),g("br"),n._v(" Dương sư ngăn cản Thẩm Truy bệ hạ nói xuất thân phần, rất rõ ràng không muốn để cho đối phương biết, đã như vậy, hắn cũng sẽ không lắm miệng nói ra."),g("br"),g("br"),n._v(" “Ba trăm vạn mà thôi, cũng đừng nói cái gì có cho mượn hay không, Lục Trầm đại sư là giáo viên của ta, trực tiếp đưa cho ngươi cũng không sao!” "),g("br"),g("br"),n._v(" Thấy Dương sư cũng không trách tội ý tứ, Thẩm Truy bệ hạ yên lòng, mở miệng nói."),g("br"),g("br"),n._v(" Số tiền này đối với một vài gia tộc lớn cũng không tính là cái gì, một cái Vương quốc tự nhiên lại xem thường, làm con trai của lão sư, đừng nói mượn, tặng không cũng không tính là gì."),g("br"),g("br"),n._v(" “Đa tạ bệ hạ! Bất quá, nếu nói rồi mượn, vậy thì là mượn, của hắn ân tình ta không muốn dính!” Lục Tầm bàn tay lớn vẫy một cái, khắp khuôn mặt là tự kiêu."),g("br"),g("br"),n._v(" Đã cùng trong nhà lão gia tử làm lộn tung lên, hắn là hắn, bản thân là bản thân."),g("br"),n._v(" Làm sao có khả năng chiếm đối phương tiện nghi?"),g("br"),g("br"),n._v(" “Đi lấy ba trăm vạn kim tệ!” "),g("br"),g("br"),n._v(" Biết phụ tử ở giữa có chút mâu thuẫn, Thẩm Truy bệ hạ cũng không nói thêm nữa, đối với một bên thái giám khoát tay áo một cái."),g("br"),g("br"),n._v(" Thái giám xoay người rời đi, thời gian không lâu, đem ra một xấp kim phiếu."),g("br"),g("br"),n._v(" “Đa tạ bệ hạ, ta Lục Tầm chẳng mấy chốc sẽ trả lại, nói được là làm được!” "),g("br"),g("br"),n._v(" Tiếp nhận kim phiếu, Lục Trầm lần thứ hai ôm quyền."),g("br"),g("br"),n._v(" “Ừm!” Thẩm Truy bệ hạ xua tay."),g("br"),g("br"),n._v(" Có thể trả lại liền còn, không trả nổi thì thôi, hắn cũng không để ý."),g("br"),g("br"),n._v(" Hiện tại lão tổ đột phá, đạt đến Tông Sư cảnh, sau đó Thiên Huyền Vương quốc khẳng định quốc lực càng ngày càng cường thịnh, một khi có thể hấp dẫn danh sư trấn thủ, xung kích nhị đẳng Vương quốc cũng chưa biết chừng, chỉ là ba trăm vạn, không tính là gì."),g("br"),g("br"),n._v(" “Lục Tầm (Vương Siêu) liền không quấy rầy bệ hạ, cáo từ!” "),g("br"),g("br"),n._v(" Tiền tới tay, Lục Tầm không ở dừng lại, xoay người liền muốn rời khỏi."),g("br"),g("br"),n._v(" Mới vừa đi mấy bước, ngừng lại, ngẩng đầu nhìn hướng cách đó không xa Trương Huyền."),g("br"),g("br"),n._v(" “Ta không biết thân phận của ngươi là cái gì, có thể ở đây cùng ba sư, bệ hạ nghị sự, chắc là cũng không đơn giản, bất quá, ta vẫn có lời nói muốn nhắc nhở ngươi một câu!” "),g("br"),g("br"),n._v(" “Ồ?” Trương Huyền nhìn sang."),g("br"),g("br"),n._v(" “Người khác trò chuyện, tốt nhất không nên tùy tiện nói chen vào, làm như vậy, rất làm cho người ta chán ghét!” Lục Tầm hơi vung tay cánh tay, vẻ mặt ngạo nghễ, như là một đóa sen trong bùn mà không nhiễm bùn hoa sen, thánh khiết kiêu ngạo, dưới chân cũng không dừng lại, đi ra ngoài."),g("br"),g("br"),n._v(" Nhìn Trương Huyền một chút, Vương Siêu cười nhạo một tiếng, cũng theo sát đuổi theo."),g("br"),g("br"),n._v(" Hai người vừa rời đi, gian phòng lập tức yên tĩnh lại, ba sư từng cái từng cái hai mặt nhìn nhau, tràn đầy không nói gì."),g("br"),g("br"),n._v(" “Cái tên này... Đến cùng từ đâu tới tự tin?” "),g("br"),g("br"),n._v(" Qua không biết bao lâu, Trang sư không nhịn được nói."),g("br"),g("br"),n._v(" Hắn xem như là nhịn gần chết."),g("br"),g("br"),n._v(" Một cái nho nhỏ minh tinh giáo sư, liền học đồ cũng không tính, lại có thể mở miệng chỉ trích liền bọn họ đều cẩn thận từng li từng tí một Dương sư..."),g("br"),g("br"),n._v(" Lá gan cũng quá béo tốt đi!"),g("br"),g("br"),n._v(" “Vốn là ta còn dự định thu hắn làm học đồ, bây giờ nhìn lại, vẫn là quên đi, không có lễ phép, không coi bề trên ra gì...” "),g("br"),g("br"),n._v(" Lưu Lăng cũng lắc đầu."),g("br"),g("br"),n._v(" Lục Tầm tiếng tăm rất lớn, thậm chí đã không hạn chế Thiên Huyền Vương quốc, Lưu Lăng mấy người đã sớm nghe, không nghĩ tới lần thứ nhất gặp mặt liền như vậy lúng túng."),g("br"),g("br"),n._v(" “Dương sư, xin lỗi, hắn chắc là... Không biết thân phận của ngươi, thuộc về vô ý mạo phạm...” "),g("br"),g("br"),n._v(" Thấy ba sư đều có chút bất mãn, Thẩm Truy bệ hạ vội vàng nhìn về phía làm người trong cuộc Dương Huyền, hi vọng hắn không nên tức giận."),g("br"),g("br"),n._v(" “Không có gì, tuổi còn trẻ liền lấy được thành tựu như vậy, có chút ngạo khí cũng là khó tránh khỏi!” Trương Huyền lắc đầu, đứng dậy: “Được rồi, Thẩm Hồng đột phá, ta cũng nên đi rồi, mong rằng bệ hạ cố gắng nhanh đem Thông Huyền cảnh bí tịch sưu tập chỉnh tề.” "),g("br"),g("br"),n._v(" “Vâng!” Thẩm Truy bệ hạ liền vội vàng gật đầu."),g("br"),g("br"),n._v(" “Cáo từ!” "),g("br"),g("br"),n._v(" Cùng cái khác ba vị danh sư lần thứ hai bắt chuyện một lần, Trương Huyền không lưu lại cần thiết, liền ôm quyền, bồng bềnh rồi biến mất, xem ba sư, Thẩm Truy bệ hạ lần thứ hai nhiệt huyết sôi trào."),g("br"),g("br"),n._v(" Xem đến chưa?"),g("br"),g("br"),n._v(" Dựa theo những người khác, giúp người đột phá, khẳng định nhân cơ hội vơ vét, đại giết đặc biệt giết, mà hắn, nhẹ như mây gió, không mang đi một áng mây..."),g("br"),g("br"),n._v(" Thậm chí bị tiểu bối ngay mặt nghi vấn, nói móc, cũng không có một chút nào tức giận cùng tức giận."),g("br"),g("br"),n._v(" “Đây mới thực sự là danh sư nên có lòng dạ cùng khí độ!” "),g("br"),g("br"),n._v(" Lưu Lăng ca ngợi."),g("br"),g("br"),n._v(" “Đúng đấy! Vị này Dương sư, ta phục, nếu như có thể bái hắn làm thầy, thực sự là có phúc ba đời!” Trịnh Phi cũng liền vội vàng gật đầu."),g("br"),g("br"),n._v(" “Trước liền nghe nói Lục Tầm tên tuổi, không nghĩ tới gặp mặt sau đó, dáng vẻ ấy!” Cảm khái xong, nhớ tới trước Lục Tầm biểu hiện, Lưu Lăng lắc lắc đầu, tiếp theo lại nghĩ tới cái gì, hỏi: “Bệ hạ lần trước nói, hắn qua mấy ngày muốn cùng một người tên là Trương Huyền lão sư sư giả bình trắc, không biết có phải là thật hay không?” "),g("br"),g("br"),n._v(" Tại Thiên Huyền Vương quốc lưu lại một quãng thời gian, cũng chuyên môn chú ý qua Hồng Thiên học viện, chuyện này không phải bí mật gì, hơi hơi hỏi thăm liền có thể rõ ràng."),g("br"),g("br"),n._v(" “Thật có việc này!” Thẩm Truy bệ hạ gật đầu."),g("br"),g("br"),n._v(" Trở lại Vương cung, liền nhận được Tiền Các Lão tấu, cũng biết cái này định tính vì tam tinh sự kiện."),g("br"),g("br"),n._v(" Hai đại giáo sư giao đấu, tuy rằng không đuổi kịp Dương Huyền danh sư ngày sau huyền Vương quốc như thế chấn động, nhưng cũng khuấy lên phong vân, hấp dẫn vô số ánh mắt."),g("br"),g("br"),n._v(" “Có thể cùng hắn giao đấu, cái này Trương Huyền chắc cũng là Hồng Thiên học viện minh tinh giáo sư chứ? Ta làm sao trước chưa từng nghe nói?” "),g("br"),g("br"),n._v(" Lưu sư mang theo nghi hoặc nhìn sang."),g("br"),g("br"),n._v(" Binh đối với binh tướng đối tướng, Lục Tầm là Hồng Thiên học viện đệ nhất minh tinh giáo sư, vị này Trương lão sư nếu dám cùng đối chiến, chắc là cũng không kém quá nhiều."),g("br"),g("br"),n._v(" Nhưng mình nghe qua không ít tuổi trẻ tài cao giáo sư, làm sao liền chưa từng nghe tới danh tự này?"),g("br"),g("br"),n._v(" (Quyển sách giữ gốc hai chương, bắt đầu từ ngày mai, thờì gian đổi mới vì buổi sáng 10 điểm, buổi tối 7 điểm, như rất nhiều lại, mười giờ tối! Nói cách khác, một ngày canh ba mà nói, 10 điểm, 1 9 giờ cùng 22 giờ, như có chương thứ tư hoặc là đại bạo phát, sẽ sớm báo cho. Mọi người định điểm tới đọc sách liền có thể, không cần liên tục xoạt, các tâm tiêu. Sớm một chút lại xong, mọi người cũng không cần thức đêm. Mặt khác, vé tháng, phiếu đề cử loại hình đừng quên ném a!) "),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-199"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-201"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);