(window.webpackJsonp=window.webpackJsonp||[]).push([[1129],{1484:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1988-ta-con-khong-phai-kiem-cac-đe-tu-【-canh-thu-hai-】"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1988-ta-con-khong-phai-kiem-cac-đe-tu-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1988: Ta còn không phải kiếm các đệ tử 【 canh thứ hai 】")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Gặp tông chủ nhận thua, Trương Huyền thở phào nhẹ nhõm, đem trên ánh mắt miếng vải đen hiểu xuống dưới."),i("br"),i("br"),n._v("Cùng năm người này chiến đấu, thoạt nhìn đơn giản, thực tế so chém giết nội môn năm ngàn người, đều muốn phức tạp, hơi không cẩn thận, liền có thể thất bại, từ đó thất bại trong gang tấc!"),i("br"),i("br"),n._v("May mắn, thành công!"),i("br"),i("br"),n._v("“Thần chi kiếm ý, quả nhiên mạnh mẽ!”"),i("br"),i("br"),n._v("Lần nữa trở lại trên chỗ ngồi ngồi xuống, Hàn Kiếm Thu tông chủ cười nhạt một tiếng, trên mặt không có chút rung động nào, dường như vừa rồi thất bại căn bản không có để hắn sinh ra nôn nóng, thất lạc chờ tâm tình tiêu cực."),i("br"),i("br"),n._v("“Là chư vị đa tạ...”"),i("br"),i("br"),n._v("Trương Huyền ôm quyền."),i("br"),i("br"),n._v("“Thua thì thua...” Hàn Kiếm Thu lắc đầu: “Muốn hỏi cái gì, nói thẳng đi!”"),i("br"),i("br"),n._v("“Ta muốn hỏi thăm một chút Hàn tông chủ, có thể hay không nghe qua... Linh Thần Cung!”"),i("br"),i("br"),n._v("Hít sâu một hơi, Trương Huyền cả người đều có chút căng thẳng."),i("br"),i("br"),n._v("Trước mắt vị này, là Lăng Vân kiếm các tông chủ, Thượng Thương đứng tại đỉnh phong nhất một trong những nhân vật, biết tin tức, khẳng định so Lục Vân trưởng lão nhiều không biết bao nhiêu."),i("br"),i("br"),n._v("Người sau không rõ ràng, có lẽ hắn có thể nhẹ nhõm giải đáp."),i("br"),i("br"),n._v("“Linh Thần Cung?”"),i("br"),i("br"),n._v("Lông mày nhíu một cái, Hàn Kiếm Thu ngay sau đó lắc đầu: “Chưa từng nghe qua...”"),i("br"),i("br"),n._v("Trương Huyền sững sờ."),i("br"),i("br"),n._v("Hàn Kiếm Thu nói: “Đại lục sáu đại tông môn, theo thứ tự là: Lăng Vân kiếm các, Trục Tinh cung, Vạn Thú môn, Thất Tinh lâu, Huyền Kính môn cùng với Phiêu Miễu tiên tông! Trong đó, có thể có tư cách sử dụng ‘Thần’ chữ, chỉ có chúng ta Lăng Vân kiếm các kiếm thần đài! Mặt khác năm đại tông môn, đều không cho phép sử dụng.”"),i("br"),i("br"),n._v("“Vượt qua sáu đại tông môn, bao trùm ở phía trên... Chỉ có Thông Thần điện! Là hơn bốn ngàn năm trước, Khổng điện chủ thành lập, lúc ấy hắn chỉ bằng một người, đi vào Thần điện, đoạt tới một cái ‘Thần’ chữ.”"),i("br"),i("br"),n._v("“Nguyên nhân chính là như vậy, Thông Thần điện mới có thể trải rộng thiên hạ, sáu đại thực lực, cũng không dám tới là địch! Sau đó... Mấy ngàn năm qua, lại không người có thể thông qua Thông Thiên kiều, chớ nói chi là đi vào Thần điện, cướp đoạt chữ Phong! Không có người đi vào, đương nhiên cũng liền không ai dám dùng ‘Thần’ chữ. Ngươi nói cái này Linh Thần Cung, hoặc là không tồn tại, hoặc là, cũng không tại Di Khí đại lục!”"),i("br"),i("br"),n._v("Hàn Kiếm Thu nhìn qua."),i("br"),i("br"),n._v("Đại lục, bởi vì là gặp phải Thần Linh vứt bỏ, đối “Thần” chữ, sử dụng hết sức cẩn thận, không được đến Thần điện cho phép, bất kỳ cái gì thế lực cũng không dám sử dụng."),i("br"),i("br"),n._v("Linh Thần Cung nếu quả như thật tồn tại, không có khả năng chưa từng nghe qua."),i("br"),i("br"),n._v("Trương Huyền sắc mặt trắng nhợt."),i("br"),i("br"),n._v("Chẳng lẽ... Lúc trước triệu hoán tới Linh Thần, nói, là giả?"),i("br"),i("br"),n._v("Căn bản cũng không có Linh Thần Cung?"),i("br"),i("br"),n._v("“Ngươi... Ở nơi nào nghe nơi này?”"),i("br"),i("br"),n._v("Gặp trước mắt vị này, đối cái này cái gọi là “Linh Thần Cung”, mười phần để tâm, Hàn Kiếm Thu nhìn lại."),i("br"),i("br"),n._v("“Ta... Trong lúc vô tình nghe được...”"),i("br"),i("br"),n._v("Tràn đầy thất lạc, Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Trước mắt vị này, như vậy thân phận, đều không có nghe qua, chỉ sợ cả thương, nghe qua người, hầu như không có!"),i("br"),i("br"),n._v(" Cái kia... Linh Thần Cung, đến cùng ở đâu?"),i("br"),i("br"),n._v("Lạc Nhược Hi, lại đến cùng đi nơi nào?"),i("br"),i("br"),n._v("“Nếu như ngươi nghe được tin tức, không phải giả, như lời ngươi nói cái này Linh Thần Cung, chân chính tồn tại, có lẽ... Đi hỏi thăm Đỗ Thanh Diên, nàng có thể biết!”"),i("br"),i("br"),n._v("Dừng lại một chút, Hàn Kiếm Thu nói."),i("br"),i("br"),n._v("“Đỗ Thanh Diên?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("“Nàng là sáu đại tông môn, Trục Tinh cung cung chủ! Đối Thần điện tìm hiểu rất nhiều, thậm chí còn đã từng cùng chân chính Thần Linh câu thông qua, từng chiếm được khen thưởng!” Hàn Kiếm Thu gật đầu."),i("br"),i("br"),n._v("“Cùng... Chân chính Thần Linh câu thông?”"),i("br"),i("br"),n._v("Trương Huyền một chút cứng đờ, trong lòng một thanh âm vang lên: “Lạc Nhược Hi không tại Thượng Thương... Có phải hay không là một vị chân chính Thần Linh?”"),i("br"),i("br"),n._v("Vị này bạn gái thần bí, có thể biết hắn nắm giữ thư viện, thậm chí còn có thể đem Khổng sư lưu lại Xuân Thu đại điển cùng hắn dung hợp... Thủ đoạn cao, chỉ sợ trước mắt vị này Hàn tông chủ đều không làm được!"),i("br"),i("br"),n._v("Đã như vậy... Có phải hay không là chân chính Thần Linh!"),i("br"),i("br"),n._v("“Bất quá, Đỗ cung chủ... Tính tình cũng không tính quá tốt! Lại thêm dính dáng Thần Linh bí ẩn, đối phương chưa chắc sẽ nói...”"),i("br"),i("br"),n._v("Hàn Kiếm Thu lắc đầu: “Hơn nữa, đã Thần Linh đem chúng ta khu trừ, nhất mất hứng chính là, tỉ mỉ tìm hiểu... Một khi tin tức tiết lộ, ta sợ Thần điện người, sẽ tới tìm phiền toái!”"),i("br"),i("br"),n._v("Trương Huyền cau mày."),i("br"),i("br"),n._v("Đi qua đọc sách, đối với cái này cái gọi là Di Khí đại lục cũng coi như tìm hiểu một chút."),i("br"),i("br"),n._v("Truyền thuyết, khối đại lục này, vi phạm với Thần Linh quy tắc, bị lưu đày xuống, tất cả sinh mệnh, đều là như vậy."),i("br"),i("br"),n._v("Hậu bối người, đều cố gắng tu luyện, tranh thủ khôi phục trước đó vinh quang, quay về Thần giới!"),i("br"),i("br"),n._v("Chỉ tiếc... Kết nối Thần giới Thông Thiên kiều đứt mất, trăm năm mới có thể kết nối một lần, hơn nữa, cầu phía sau, trấn áp một tòa Thần Điện."),i("br"),i("br"),n._v("Không ít thủ hộ giả, bảo vệ trong đó."),i("br"),i("br"),n._v("Lúc trước Khổng điện chủ, cùng Lăng Vân kiếm các khai phái tổ sư, chính là ở đây tranh đoạt một chữ, cùng nửa chữ!"),i("br"),i("br"),n._v("“Thần điện, phong tỏa thế giới này, phong tỏa chúng ta muốn trở thành Chân Thần thông đạo! Không chỉ có như vậy, nếu có người tìm hiểu Thần Linh bí ẩn, bị đối phương phát giác, liền sẽ gặp được nguy hiểm! Thậm chí...”"),i("br"),i("br"),n._v("Hàn Kiếm Thu thấp giọng: “Giống như ngươi lĩnh ngộ Thần chi kiếm ý người, cũng sẽ gặp phải kiêng kị, có khả năng dẫn tới bọn họ, sớm ra tay!”"),i("br"),i("br"),n._v("“Vì cái gì?” Trương Huyền nhìn tới."),i("br"),i("br"),n._v("“Ta không biết, cũng có thể là là, Thần điện quyền uy không cho phép khiêu chiến đi! Lĩnh ngộ Thần chi kiếm ý, mặc cho trưởng thành, liền có khiêu chiến tư cách của bọn hắn, bọn họ, đương nhiên không cho phép loại chuyện này phát sinh... Tóm lại, liên quan tới Thần Linh sự tình, tốt nhất không nói không nói, không hỏi thăm cũng đừng đi tìm hiểu!”"),i("br"),i("br"),n._v("Hàn Kiếm Thu nhìn qua, mang theo giao phó mùi vị: “Bởi vậy... Thân phận của ngươi, vẫn như cũ là bí mật lớn nhất, lại không có đi đến nắm giữ chân chính tự vệ thực lực phía trước, không thể nói ra đi!”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Không nghĩ tới Thần điện vậy mà lại làm như vậy, Trương Huyền nắm đấm xiết chặt, một lát sau, thở ra một hơi: “Ta chắc chắn sẽ không nhiều lời!”"),i("br"),i("br"),n._v("“Không chỉ không cần nhiều lời, còn tốt nhất đừng tuỳ tiện tiết lộ thực lực, không thể quá kiêu căng...”"),i("br"),i("br"),n._v("Hàn Kiếm Thu tiếp tục nói."),i("br"),i("br"),n._v("Ngươi có thể không nói, nhưng ở bên ngoài thi triển ra Thần chi kiếm ý, cũng là rất dễ dàng bị người phát hiện!"),i("br"),i("br"),n._v("Đồng dạng sẽ dẫn tới phiền phức."),i("br"),n._v("“Yên tâm đi, ta người này từ trước tới nay biết điều!” Trương Huyền nói."),i("br"),i("br"),n._v("“... Khụ khụ!”"),i("br"),i("br"),n._v("Nghe nói như thế, Hàn Kiếm Thu kìm nén sắc mặt đỏ lên, không biết trả lời như thế nào."),i("br"),i("br"),n._v("Một ngày phía trong, đầu tiên là làm sơn môn sụp đổ, giấy không gói được lửa... Ngay sau đó, đơn đấu toàn bộ nội môn cùng hạch tâm, hiện tại lại đơn đấu trưởng lão điện, đem tông chủ và đại trưởng lão đám người mặt mũi, đè xuống đất cọ xát..."),i("br"),i("br"),n._v("Một ngày làm sự tình, so người khác cả một đời đều nhiều..."),i("br"),i("br"),n._v("Vậy mà không biết xấu hổ nói, biết điều?"),i("br"),i("br"),n._v("Mặt đâu?"),i("br"),i("br"),n._v("“Đây là Thông Thần điện Khổng điện chủ, biết có người lĩnh ngộ Thần chi kiếm ý, đưa tặng lệnh bài! Sử dụng thứ này, Thông Thần điện phía trong mua sắm tác dụng gì tới tấn cấp tài nguyên, đều không nhận bất kỳ chi phí!”"),i("br"),i("br"),n._v("Biết nói tiếp, sẽ bị tức ngất đi, Hàn Kiếm Thu cổ tay khẽ đảo, lấy ra một cái lệnh bài."),i("br"),i("br"),n._v("Chính là trước đó, Khổng điện chủ đưa tặng viên kia."),i("br"),i("br"),n._v("“Khổng điện chủ đưa tặng?”"),i("br"),i("br"),n._v("Trương Huyền thân thể xiết chặt: “Nhưng mà... Khổng sư? Hắn... Hắn còn tại đại lục?”"),i("br"),i("br"),n._v("Hư Tiên cảnh tuổi thọ, chỉ có ba trăm, coi như Tiên Quân cảnh, thậm chí Ngụy Thần cảnh cường giả, tuổi thọ cũng sẽ không tăng thêm bao nhiêu."),i("br"),i("br"),n._v("Khổng sư rời khỏi danh sư đại lục vài vạn năm, dựa theo Thượng Thương mười so một thời gian lưu tốc, cũng chính là tới chí ít mấy ngàn năm, chẳng lẽ... Còn sống?"),i("br"),i("br"),n._v("Hơn nữa còn sống ở đại lục này, cũng không rời khỏi?"),i("br"),i("br"),n._v("“Khổng sư? Trước kia là có người xưng hô như vậy qua!”"),i("br"),i("br"),n._v("Hàn Kiếm Thu tông chủ cười cười: “Hắn đương nhiên còn tại đại lục... Còn ở nơi nào, ta cũng không rõ ràng, chỉ biết là hắn, hành tung lơ lửng không cố định, mấy ngàn năm qua, không chút xuất hiện qua, cũng là cái này mấy chục năm, mới một lần nữa đi ra... Tất cả mọi người cũng mới biết, cũng không từ trần!”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Trương Huyền con mắt sáng lên."),i("br"),i("br"),n._v("Khổng sư không có chết, hơn nữa ở trên thương, xem như ngày hôm nay nghe được tin tức tốt nhất."),i("br"),i("br"),n._v("Chỉ cần còn sống, gặp mặt, hẳn là chuyện sớm hay muộn."),i("br"),i("br"),n._v("Đưa tay nhận lấy lệnh bài, nhẹ nhàng vuốt ve một chút, cảm thấy một cỗ dịu dàng dòng nước ấm tràn vào trong cơ thể."),i("br"),i("br"),n._v("“Là hắn...”"),i("br"),i("br"),n._v("Nắm đấm xiết chặt."),i("br"),i("br"),n._v("Nếu như trước đó còn nghi ngờ, Khổng điện chủ có phải hay không Khổng sư, chạm đến đạo lệnh bài trong nháy mắt, liền hiểu."),i("br"),i("br"),n._v("Tuyệt đối là cùng là một người!"),i("br"),i("br"),n._v("Cùng hắn đều là trời nhận danh sư, lại cùng từ danh sư đại lục đi ra, trong lòng đã sớm có đặc thù nào đó ăn ý."),i("br"),i("br"),n._v("“Thông Thần điện điện chủ, đối với thiên tài, đều có như thế hậu tặng, ta thân là tông chủ, nếu như không có biểu thị, chẳng phải nhận người chê cười?”"),i("br"),i("br"),n._v("Gặp hắn nhận lấy lệnh bài, Hàn Kiếm Thu cười cười, một thanh trường kiếm xuất hiện tại lòng bàn tay: “Chuôi kiếm này, tên là 'Đồng Thường', tuy là không bằng ta sử dụng Nguyễn Thiên', nhưng cũng đạt đến Tiên Quân đỉnh phong, hiện tại đưa tặng cho ngươi, cẩn thận rèn luyện, một khi luyện hóa, đối ngươi tu vi cùng thực lực, sẽ có cực lớn trợ giúp!”"),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Đồng Thường kiếm, nhẹ nhàng trôi lơ lửng ở phía trước, mang theo nồng đậm cảm giác áp bách."),i("br"),i("br"),n._v("Tựa như một vị Tiên Quân cường giả, xuất hiện ở trước mắt."),i("br"),i("br"),n._v("“Đa tạ tông chủ...”"),i("br"),i("br"),n._v("Trương Huyền hành lễ."),i("br"),i("br"),n._v("Trước đó còn lo lắng, tu vi không đủ, thật muốn có người giết hắn, ai cũng cứu không được, có chuôi kiếm này, tương đương với một vị Tiên Quân cường giả bảo vệ, không cần tiếp tục lo lắng!"),i("br"),i("br"),n._v("Chỉ là..."),i("br"),i("br"),n._v("Vũ khí đi đến loại này cấp bậc, nhất định là có bản thân linh tính, thực lực bây giờ, muốn thuần phục, chỉ sợ không dễ dàng như vậy."),i("br"),i("br"),n._v("Bất quá, có Thiên đạo thư viện tại, ép đối phương ngoan ngoãn nghe lời, không khó lắm."),i("br"),i("br"),n._v("Tinh thần khẽ động, thu vào trữ vật giới chỉ."),i("br"),i("br"),n._v("Đối phương hẳn là gia trì đặc thù nào đó cấm chế, nếu không, loại cấp bậc này vũ khí, tinh thần lực của hắn, coi như muốn nhận vào trữ vật giới chỉ, cũng là không làm được."),i("br"),i("br"),n._v("“Đúng rồi, ngươi bây giờ ở đâu cái phong? Vị nào trưởng lão chịu trách nhiệm?”"),i("br"),i("br"),n._v("Gặp hắn thanh kiếm nhận lấy, Hàn Kiếm Thu gật gật đầu, đột nhiên nhớ tới một việc, tò mò hỏi."),i("br"),i("br"),n._v("Có thể bồi dưỡng ra như vậy một vị đệ tử thiên tài, vị trưởng lão này công lao không nhỏ, nhất định phải hảo hảo ban thưởng."),i("br"),i("br"),n._v("“Ta tại... Nội môn đệ tử cư trú đỉnh núi... Trưởng lão nha... Là Lục Vân trưởng lão!”"),i("br"),i("br"),n._v("Dừng lại một chút, Trương Huyền nói."),i("br"),i("br"),n._v("“Lục Vân...” Hàn Kiếm Thu nhíu nhíu mày."),i("br"),i("br"),n._v("“Trước đó là cái ngoại môn trưởng lão, tiến cử thiên tài thế hệ sau có công, nhận ban thưởng, ngày hôm nay mới đột phá tu vi, đi đến Chân Tiên cảnh, đã được mời làm nội môn trưởng lão...”"),i("br"),i("br"),n._v("Gặp hắn chưa từng nghe qua, Hà Thiên trưởng lão vội vàng trả lời."),i("br"),i("br"),n._v("Ngoại môn trưởng lão tồn tại cảm cực thấp, thân là tông chủ, không biết rất bình thường."),i("br"),i("br"),n._v("“Ngoại môn trưởng lão, vừa đột phá? Nói như vậy... Trước ngươi là ngoại môn đệ tử?” Hàn Kiếm Thu tràn đầy không tin nhìn về phía thanh niên trước mắt."),i("br"),i("br"),n._v("Ngoại môn đệ tử, lĩnh ngộ Thần chi kiếm ý?"),i("br"),i("br"),n._v("Thật hay giả?"),i("br"),i("br"),n._v("Lúc nào, Lăng Vân kiếm các ngoại môn lợi hại như vậy?"),i("br"),i("br"),n._v("Khó trách muốn treo lên đánh nội môn, hạch tâm, thậm chí trưởng lão... Nguyên lai là không được trọng dụng!"),i("br"),i("br"),n._v("“Ngoại môn?”"),i("br"),i("br"),n._v("Trương Huyền một mặt xấu hổ: “Ta còn không phải kiếm các đệ tử...”"),i("br"),i("br"),n._v("Nói xong, thấy mọi người một mặt dại ra, rầu rĩ một chút, cắn răng một cái: “Nếu không, lệnh bài này cùng Đồng Thường kiếm, ta từ bỏ, đều trả lại ngươi?”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2000"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2002"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);