(window.webpackJsonp=window.webpackJsonp||[]).push([[1130],{1485:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1989-đong-thuong-kiem-tan-cap-đieu-kien-【-canh-3-minh-chu-đong-đuong-tang-them-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1989-đong-thuong-kiem-tan-cap-đieu-kien-【-canh-3-minh-chu-đong-đuong-tang-them-】"}},[n._v("#")]),n._v(" Chương 1989: Đồng Thường kiếm tấn cấp điều kiện 【 Canh [3] minh chủ đồng đường tăng thêm 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Bạch Diệp trưởng lão biết Trương Huyền chân thực thân phận, còn chưa kịp báo, liền muốn thử kiếm, sau đó liền phát sinh những sự tình này."),t("br"),t("br"),n._v("Bởi vậy, ở đây ai cũng không nghĩ tới, vị này lĩnh ngộ Thần chi kiếm ý siêu cấp thiên tài, không tại hạch tâm, không tại nội môn, không tại ngoại môn... Thậm chí, liền Lăng Vân kiếm các đệ tử, cũng không phải!"),t("br"),t("br"),n._v("Vậy thì xấu hổ."),t("br"),t("br"),n._v("Một cái liền đệ tử cũng không phải gia hỏa, lĩnh ngộ khai phái tổ sư kiếm ý không nói, còn đem bọn họ từ trong cửa đến tông chủ... Tất cả đều đánh cho một trận!"),t("br"),t("br"),n._v("Nghe xong giới thiệu, Hàn Kiếm Thu cùng Hà trưởng lão đám người, từng cái vẻ mặt kìm nén đỏ lên, hô hấp không quá thông thuận, có chút nhớ nhung muốn tự sát."),t("br"),t("br"),n._v("Chuyện này là sao..."),t("br"),t("br"),n._v("Truyền đi, đều không đủ mất mặt!"),t("br"),t("br"),n._v("“Ngươi... Thật chỉ tới kiếm các chỉ có một ngày? Hơn nữa, là đi ngang qua sơn môn thời điểm, cảm ngộ đến kiếm ý?”"),t("br"),t("br"),n._v("Hà trưởng lão muốn khóc."),t("br"),t("br"),n._v("Hắn đứng tại sơn môn, liên tục cảm ngộ hơn ba mươi năm, nóng lạnh không ngừng, kết quả... Cọng lông đều không có cảm ngộ một cái, người ta ngược lại tốt, đi ngang qua liếc mắt nhìn liền lĩnh ngộ..."),t("br"),t("br"),n._v("Không mang theo như vậy đả kích người!"),t("br"),t("br"),n._v("“Đúng vậy a, đại khái dùng mấy hơi thở đi!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“...” Hà trưởng lão con mắt biến thành màu đen."),t("br"),t("br"),n._v("“Xem ra, Thần chi kiếm ý, cũng không phải là đợi đến thời gian càng lâu, càng có thể thành công cảm ngộ!” Gặp Hà trưởng lão một bộ sống không thể yêu hình dáng, Hàn Kiếm Thu tông chủ an ủi một câu, nhìn lại: “Lĩnh ngộ tiên tổ kiếm ý, chẳng khác nào thành đời tiếp theo tông chủ, không người nào có thể thay thế... Không phải đệ tử vừa vặn! Từ giờ trở đi, chính là tông môn hạch tâm trưởng lão, hưởng thụ trưởng lão tất cả đặc quyền!”"),t("br"),t("br"),n._v("Mấy ngàn năm qua, vị thứ nhất lĩnh ngộ tiên tổ kiếm ý người, Lăng Vân kiếm các có thể hay không biến thành Lăng Vân Kiếm Thần các, khả năng còn muốn dựa vào trước mắt vị này, khẳng định không thể để cho hắn rời khỏi."),t("br"),t("br"),n._v("Không phải là đệ tử, lại thu nhận vì nội môn, hạch tâm đều không thích hợp... Dù sao, một mình đấu tông chủ, đại trưởng lão đám người, toàn bộ tông môn, đều không có người có tư cách truyền thụ cho hắn kiếm pháp!"),t("br"),t("br"),n._v("Cùng hắn như vậy, còn không bằng trở thành trưởng lão."),t("br"),t("br"),n._v("Mọi người ngồi ngang hàng, về sau kế vị tông chủ, cũng đơn giản không ít."),t("br"),t("br"),n._v("“Trưởng lão?”"),t("br"),t("br"),n._v("Trương Huyền ngẩn ngơ."),t("br"),t("br"),n._v("Như vậy liền trưởng thành già?"),t("br"),t("br"),n._v("“Không sai, bất quá... Cái này thân phận trưởng lão, tạm thời đồng dạng không thể công bố, đợi ngươi... Tu vi đi đến Tiên Quân phía sau, mới có thể nói đi ra!” Hàn Kiếm Thu nói."),t("br"),t("br"),n._v("Thần chi kiếm ý bị người lĩnh ngộ tin tức đã bị tiết lộ, một chút nhiều ra một vị, trẻ tuổi như vậy trưởng lão, không khiến người ta nghi ngờ đều là giả."),t("br"),t("br"),n._v("Lý do an toàn, vẫn là biết điều một ít cho thỏa đáng."),t("br"),t("br"),n._v("“Cái kia... Ta có thể hay không tới quan sát trưởng lão tàng thư khố?”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền hỏi."),t("br"),t("br"),n._v("Công không công bố, cũng không đáng kể, dù sao hắn không thích nói toạc móng heo, chỉ cần có sách nhìn, mặt khác đều là việc nhỏ."),t("br"),t("br"),n._v("“Đương nhiên...” Hàn Kiếm Thu gật đầu, cổ tay khẽ đảo: “Đây là đại biểu thân phận trưởng lão lệnh bài, cũng không ghi chú tên của ngươi, nhưng có ta đặc thù ấn ký ở bên trong, hạch tâm trưởng lão có thể đi địa phương, cũng có thể đi qua! Cũng có thể mượn tông môn tài nguyên, cho mình sử dụng, nhưng... Không thể quá nhiều!.”"),t("br"),t("br"),n._v("“Đa tạ tông chủ!”"),t("br"),t("br"),n._v("Trương Huyền nhận lấy lệnh bài."),t("br"),t("br"),n._v("Có thứ này, liền có thể đi sưu tập Chân Tiên cảnh công pháp bí tịch, thành công đột phá!"),t("br"),t("br"),n._v("Chỉ cần đi đến Chân Tiên cảnh đỉnh phong, dựa vào hắn vượt cấp năng lực chiến đấu, Tiên Quân cảnh cường giả, cũng có thể một trận chiến, coi như tại toàn bộ đại lục, cũng coi như được an toàn."),t("br"),t("br"),n._v("“Ừm, dựa theo bình thường đạo lý, mỗi cái trưởng lão, đều có thuộc về mình biệt viện, bất quá... Vì an toàn, trước tạm thời không cho ngươi chuẩn bị, vẫn như cũ ở tại nội môn liền tốt...”"),t("br"),t("br"),n._v("Dừng lại một chút, Hàn Kiếm Thu tông chủ mang theo lúng túng nói."),t("br"),t("br"),n._v("Đều là tương lai tông chủ... Đãi ngộ thực sự có chút không cách nào mở miệng."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền mảy may đều không thèm để ý."),t("br"),t("br"),n._v("Nếu như bây giờ cho trưởng lão biệt viện, chẳng khác nào công bố thân phận, không làm cho người khác chú ý, cũng khó khăn."),t("br"),t("br"),n._v("Tất cả giao phó xong, Hàn tông chủ dừng lại phút chốc: “Ngươi bây giờ đã là trưởng lão, hạch tâm, nội môn Thông Thần điện, cũng đừng lại đi...”"),t("br"),t("br"),n._v("Trương Huyền đáp ứng."),t("br"),t("br"),n._v("Có Khổng sư đưa tặng viên kia lệnh bài, tài nguyên tu luyện không thiếu, khẳng định cũng liền chẳng muốn lại đi chơi đùa hạch tâm, nội môn đệ tử."),t("br"),t("br"),n._v("“Cáo từ...”"),t("br"),t("br"),n._v("Nên hỏi thì hỏi hết, đối phương cũng xác nhận thân phận của mình, Trương Huyền không nói thêm lời, xoay người đi ra trưởng lão điện."),t("br"),t("br"),n._v("Bạch Diệp trưởng lão đi theo ra ngoài, đem hắn đưa về."),t("br"),t("br"),n._v("Hắn vừa rời đi, gian phòng liền an tĩnh lại."),t("br"),t("br"),n._v("Qua không biết bao lâu, Hà trưởng lão nhìn về phía lão giả trước mắt: “Tông chủ, lĩnh ngộ Thần chi kiếm ý, tốt nhất là trực tiếp đi vào Kiếm Ý cung tu luyện, như vậy tiến bộ sẽ nhanh hơn một ít, hắn hiện tại cũng có tư cách này, vì sao ngươi...”"),t("br"),t("br"),n._v("Kiếm Ý cung, Lăng Vân kiếm các, nơi thần bí nhất, trở thành tông chủ phía sau, sẽ tiến vào bên trong rèn luyện kiếm ý, từ đó đột phá đến Ngụy Thần cảnh, tu vi đại tăng!"),t("br"),t("br"),n._v("Vị này Trương Huyền, đã lĩnh ngộ Thần chi kiếm ý, trở thành tông chủ, ván đã đóng thuyền, tại không dị nghị, vì có thể để cho cấp tốc tấn cấp, đi vào Kiếm Ý cung là lựa chọn tốt nhất, vì sao tông chủ, nâng đều không có nâng?"),t("br"),t("br"),n._v("“Kiếm Ý cung, lưu giữ Lăng Vân kiếm các vô số tiên tổ quý giá kiếm ý, hậu nhân tiến vào bên trong, có thể chiêm ngưỡng, học tập, hơn nữa rèn luyện bản thân lực lượng, quả thực có thể khiến người ta cấp tốc tiến bộ, nhưng...”"),t("br"),t("br"),n._v("Hàn Kiếm Thu tông chủ cười khổ một tiếng: “Vị này Trương Huyền, đã lĩnh ngộ Thần chi kiếm ý, ý cảnh so tất cả tiên tổ đều cao... Ta sợ, tiến vào bên trong, không những không tạo nên rèn luyện tác dụng, sẽ còn đem trọn cái Kiếm Ý cung, đều hủy diệt... Dù sao, trong một ngày... Nội môn, hạch tâm, thậm chí chúng ta trưởng lão điện, đều bị đơn đấu, vẫn là... Cho tiên tổ lưu chút mặt mũi đi!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Hà trưởng lão lông mày nhảy dựng."),t("br"),t("br"),n._v("Cũng đúng."),t("br"),t("br"),n._v("Cái tên này làm ầm ĩ vô cùng, nội môn, hạch tâm đều bị lựa chọn, ngộ nhỡ chạy đến Kiếm Ý cung, không phải đi học tập, mà là đơn đấu, để tất cả tiên tổ còn mặt mũi nào mà tồn tại?"),t("br"),t("br"),n._v("Vẫn là tông chủ suy tính dường như chu toàn, nếu không... Làm hậu bối, mất mặt đều mất chết rồi."),t("br"),t("br"),n._v("“Hà trưởng lão, Cừu trưởng lão, trong Kiếm các, ta sợ cũng không có như vậy an toàn, liền làm phiền hai người các ngươi, tốn nhiều điểm tâm, chăm sóc một chút vị này Trương Huyền! Mới đi đến tông môn một ngày... Liền lĩnh ngộ Thần chi kiếm ý, có lẽ, chúng ta Lăng Vân kiếm các, không chỉ có thể khôi phục tiên tổ lúc vinh quang, càng có thể cùng Thông Thần điện đồng dạng, bao trùm tại cái khác năm đại tông môn phía trên!”"),t("br"),t("br"),n._v("Hàn Kiếm Thu trong mắt tràn đầy kích động."),t("br"),n._v("Tuy là vị này gọi Trương Huyền gia hỏa, thoạt nhìn không quá đáng tin cậy, nhưng thực lực, nhưng là thực sự mạnh mẽ!"),t("br"),t("br"),n._v("Đặc biệt là đối với kiếm pháp lý giải, cao minh hơn bọn hắn không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Chỉ cần hắn trưởng thành đến cùng mình tương đồng cảnh giới, Lăng Vân kiếm các, lần nữa đoạt lại nửa chữ, trở thành sáu đại tông môn đứng đầu, thậm chí vượt qua Thông Thần điện, cũng chưa biết chừng!"),t("br"),t("br"),n._v("“Yên tâm đi tông chủ!”"),t("br"),t("br"),n._v("Hà trưởng lão đám người gật gật đầu."),t("br"),t("br"),n._v("Vì tông môn, không thể đổ trách nhiệm cho người khác!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trở lại Thiện Hiểu Thiên sân nhỏ, chỉ điểm Bạch Nguyễn Khanh đám người một hồi, Trương Huyền trở lại gian phòng của mình."),t("br"),t("br"),n._v("Bốn phía bố trí xong cấm chế, lúc này mới cổ tay khẽ đảo, Khổng sư vị kia lệnh bài xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Máu tươi nhỏ lên đi, đem luyện hóa, rất nhanh hiểu rõ công dụng."),t("br"),t("br"),n._v("Đó cũng không phải thông thần ngọc phù, cũng không cách nào đi vào Thông Thần điện, mà là một loại tín vật."),t("br"),t("br"),n._v("Chỉ cần luyện hóa, đi vào ngoại giới Thông Thần điện, liền có thể tùy ý mua sắm tăng cao tu vi bảo vật, tuỳ ý bao nhiêu, đều không cần dùng tiền!"),t("br"),t("br"),n._v("Có thể nói, đối phương vừa ra tay, liền cho một trương vô tận tiêu hao thẻ vàng, tuỳ ý cà!"),t("br"),t("br"),n._v("Không hổ là Khổng sư... Thật là hào phóng!"),t("br"),t("br"),n._v("“Bây giờ tại Lăng Vân trong Kiếm các, chờ rời đi nơi này lại nói...”"),t("br"),t("br"),n._v("Lưu luyến không rời quân lệnh bài thu về nhẫn."),t("br"),t("br"),n._v("Hiện tại hắn tại Lăng Vân trong Kiếm các, cho dù có thông thần ngọc phù, tiến vào cũng là “Mạng máy tính cục bộ”, muốn đi vào ngoại giới Thông Thần điện, hưởng thụ vô tận kim tệ hứng thú, nhất định phải rời khỏi đặc biệt phạm vi."),t("br"),t("br"),n._v("Luyện hóa lệnh bài, biết hiện tại không thể dùng, Trương Huyền lần nữa lật tay một cái chưởng, Hàn Kiếm Thu cho chuôi này Đồng Thường kiếm xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Trường kiếm vừa xuất hiện, không gian bốn phía lập tức xuất hiện vết rách, dường như không chịu nổi trên thân kiếm toả ra lực lượng, bất cứ lúc nào cũng sẽ bị xé nứt."),t("br"),t("br"),n._v("Không hổ là Tiên Quân đỉnh phong cấp bậc vũ khí, uy lực lớn kinh người."),t("br"),t("br"),n._v("“Tiểu tử, ta là nhận mệnh lệnh, đi theo bên cạnh ngươi, muốn luyện hóa ta... Thực lực ngươi bây giờ, còn kém xa lắm! Khuyên ngươi đừng phí công phu, mặt khác, không có việc gì tốt nhất đừng đem ta lấy ra, nếu không, ta sợ sẽ không khống chế được, đánh ngươi một chầu!”"),t("br"),t("br"),n._v("Vặn vẹo một chút, Đồng Thường kiếm khẽ nói."),t("br"),t("br"),n._v("Thân là Tiên Quân cảnh đỉnh phong vũ khí, muốn nhận một cái Hư Tiên cảnh tiểu tử làm chủ, quả thực chính là vô cùng nhục nhã."),t("br"),t("br"),n._v("“Đánh ta?” Trương Huyền cười cười, ngón tay tại trên kiếm phong nhẹ nhàng vạch một cái: “Nếu như không nhìn lầm, ngươi hẳn là, đại lục xếp hạng thứ ba đúc kiếm sư, Âu Dương Thanh Phong rèn đúc mà thành đi!”"),t("br"),t("br"),n._v("“Không sai!” Đồng Thường kiếm lắc lư một cái."),t("br"),t("br"),n._v("Âu Dương Thanh Phong, là đại lục nổi danh đúc kiếm sư, danh khí cực lớn, cả đời Tiên Quân cảnh trường kiếm rèn đúc trọn vẹn bảy chuôi, trở thành một đời truyền kỳ."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, một mực dùng cái này thân phận tự ngạo."),t("br"),t("br"),n._v("“Âu Dương Thanh Phong đại sư đúc kiếm chi thuật, quả thực rất mạnh, dùng Huyền Dương thiết cùng Thâm Hải huyền tinh, hai loại bảo vật, lại phối hợp cao minh rèn đúc phương pháp, cấp bậc của ngươi, bản năng đủ tiến thêm một bước, lại chỉ là Tiên Quân đỉnh phong... Có biết vì sao?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Trước mắt trường kiếm, sử dụng chất liệu, đều là đại lục ở bên trên hi hữu hiếm thấy, mười phần trân quý, rèn đúc thủ pháp cũng rất cao minh, tình huống bình thường, vận khí tốt, có cơ hội tấn cấp Ngụy Thần cảnh, mà bây giờ chỉ là Tiên Quân đỉnh phong, không thể không nói, mười phần đáng tiếc."),t("br"),t("br"),n._v("“Tiến thêm một bước? Ngụy Thần cảnh vũ khí, toàn bộ đại lục đều không có mấy món, muốn thành công sao mà khó khăn, ít dùng những lời này tới đối ta mê hoặc...”"),t("br"),t("br"),n._v("Đồng Thường kiếm lay động một cái: “Nếu như ngươi muốn dựa vào mấy câu liền để ta thần phục, vẫn là tỉnh lại đi...”"),t("br"),t("br"),n._v("Đi đến nó loại cấp bậc này vũ khí, không có khả năng tuỳ tiện nhận chủ."),t("br"),t("br"),n._v("Cho dù nó trước đó chủ nhân, cũng là hao tốn không biết bao nhiêu tâm huyết, mới thành công thu hoạch được công nhận, cuối cùng luyện hóa."),t("br"),t("br"),n._v("“Mê hoặc? Ngươi cả nghĩ quá rồi, ta chỉ là trình bày một sự thật mà thôi! Dùng như ngươi loại này chất liệu, phối hợp thêm phương pháp luyện khí, tình huống bình thường, tấn cấp Ngụy Thần cảnh khả năng chỉ có một phần mười! Hơn nữa, còn muốn có đặc thù lực lượng gia trì...”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng, nhìn lại: “Bất quá... Tại ta chỗ này, chỉ cần ta nguyện ý, có thể để cho ngươi trăm phần trăm đi đến Ngụy Thần cảnh giới!”"),t("br"),t("br"),n._v("“Ồ? Ngươi vì sao có tự tin như vậy? Nói ra nghe một chút!”"),t("br"),t("br"),n._v("Một bộ bộ dáng lười biếng, Đồng Thường kiếm căn bản không tin."),t("br"),t("br"),n._v("Ngụy Thần cảnh giới vũ khí, nếu như dễ dàng như vậy tấn cấp, đại lục ở bên trên cũng sẽ không ít như vậy!"),t("br"),t("br"),n._v("Hàn Kiếm Thu đều không làm được, không tin một cái Hư Tiên cảnh tiểu gia hỏa có thể hoàn thành."),t("br"),t("br"),n._v("“Rất đơn giản...”"),t("br"),t("br"),n._v("Cũng không phí lời, Trương Huyền chập ngón tay làm kiếm, nhẹ nhàng điểm một cái, một đạo kiếm ý, lập tức tản mát ra."),t("br"),t("br"),n._v("Chính là trước đó lĩnh ngộ Thần chi kiếm ý!"),t("br"),t("br"),n._v("Cảm thụ kiếm ý, trùng trùng điệp điệp, như là Thần Linh tự thân thi triển, ẩn chứa vô cùng vô tận huyền diệu, Đồng Thường kiếm cứng đờ: “Ngươi lĩnh ngộ... Thần chi kiếm ý?”"),t("br"),t("br"),n._v("“Không sai, ngươi hẳn là rõ ràng, Thần chi kiếm ý, Thần Linh mới có thể thi triển, ta dùng loại này kiếm ý đối ngươi tẩm bổ, ngươi tấn cấp Ngụy Thần cảnh giới cơ hội có bao lớn!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Tiên Quân cảnh trường kiếm, muốn tấn cấp cảnh giới cao hơn, kiếm ý tẩm bổ là tốt nhất, mà hắn Thần chi kiếm ý, chính là tốt nhất đồ vật, không làm loại thứ hai nghĩ."),t("br"),t("br"),n._v("“Là tỷ lệ lớn, có thể ngươi bây giờ tu vi quá thấp, ta vẫn như cũ không biết đối ngươi thần phục...”"),t("br"),t("br"),n._v("Dừng lại một chút, Đồng Thường kiếm vẫn là trực tiếp từ chối."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đúng lúc này, một cái hồ lô bất ngờ xuất hiện tại phía trước, nhẹ nhàng run rẩy, liền đem trường kiếm nuốt xuống."),t("br"),t("br"),n._v("“Cùng nó phí nhiều lời như vậy làm gì, không thần phục, nuốt là được...”"),t("br"),t("br"),n._v("Hồ lô âm thanh vang lên."),t("br"),t("br"),n._v("P/S: Hồ lô hố cha =) ) ) ) ) ) ) ) ) ) ) ) ) ) ) Còn 1 chương"),t("br"),t("br"),n._v("(Cảm ơn thư hữu  trở thành quyển sách thứ 82 vị minh chủ. Yên tâm), có Canh [4]."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2001"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2003"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);