(window.webpackJsonp=window.webpackJsonp||[]).push([[1131],{1487:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1990-truong-lao-tang-thu-kho-【-canh-4-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1990-truong-lao-tang-thu-kho-【-canh-4-】"}},[n._v("#")]),n._v(" Chương 1990: Trưởng lão tàng thư khố 【 Canh [4] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới cái này hồ lô như vậy hung tàn, há miệng ra đem Tiên Quân đỉnh phong cấp bậc trường kiếm đều nuốt xuống, Trương Huyền tức giận vội vàng tiến lên, lấy ra một nắm búa, mạnh mẽ bổ tới: “Nhanh phun ra...”"),t("br"),t("br"),n._v("Chuôi kiếm này, là hắn dùng để bảo vệ tính mạng tốt nhất đồ vật, thật muốn bị nuốt, tổn thất lớn, không gì sánh kịp!"),t("br"),t("br"),n._v("Còn cái tên này... Không có chút nào đáng tin cậy, căn bản không trông cậy được vào!"),t("br"),t("br"),n._v("“Một cái phá kiếm, tại đây kéo ngồi chém gió tự kỷ giống như, vẫn là cho ta ăn đi...”"),t("br"),t("br"),n._v("Hồ lô giãy dụa."),t("br"),t("br"),n._v("“Nhanh lên một chút nhổ, nếu không, có tin ta hay không nơi này ngươi cũng lại lấy không được một chút đồ ăn...” Trương Huyền tức giận sắp nổ tung."),t("br"),t("br"),n._v("Cái gì ngoạn ý đi!"),t("br"),t("br"),n._v("Một ngày đến muộn, chỉ có biết ăn!"),t("br"),t("br"),n._v("Bất quá, tuy là buồn bực, nhưng trong lòng cũng mười phần khiếp sợ."),t("br"),t("br"),n._v("Trước đó vẫn cảm thấy cái hồ lô này, mười phần không đáng tin cậy, nhiều nhất nước tắm có thể dùng tới chữa thương, mặt khác không có quá tác dụng lớn chỗ, không nghĩ tới, Tiên Quân cảnh vũ khí, đều một cái có thể nuốt!"),t("br"),t("br"),n._v("Có thể làm được điểm ấy, chẳng phải chứng minh... Là cái so Tiên Quân cảnh đều cường đại hơn vũ khí?"),t("br"),t("br"),n._v("Gặp chủ nhân nói quyết tuyệt như vậy, chần chờ một chút, hồ lô nhẹ nhàng run rẩy, đầy vẻ không muốn đem Đồng Thường kiếm phun ra."),t("br"),t("br"),n._v("Không nhổ không được, trước mắt vị chủ nhân này về sau đưa cho đồ vật, khẳng định muốn so thôn phệ một thanh kiếm tới nhiều."),t("br"),t("br"),t("br"),n._v("Thế nào chọn lựa, nó trong lòng hiểu rõ."),t("br"),t("br"),n._v("Không nói mặt khác, chỉ nói có thể để cho nó khôi phục nhanh chóng chân khí, cũng không phải là một thanh kiếm có thể so sánh được."),t("br"),t("br"),n._v("“Ngươi là ai...”"),t("br"),t("br"),n._v("Bị phun ra Đồng Thường kiếm sắp sợ choáng váng."),t("br"),t("br"),n._v("Đường đường Tiên Quân đỉnh phong cấp bậc trường kiếm, thiên hạ nổi danh lợi khí, lại bị... Một cái hồ lô nuốt!"),t("br"),t("br"),n._v("Suy nghĩ một chút liền không rét mà run."),t("br"),t("br"),n._v("“Không nhận chủ, tin hay không, lại đem ngươi ăn, biến thành một đống phân kéo ra tới...”"),t("br"),t("br"),n._v("Hồ lô khó chịu."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Run run phút chốc, Đồng Thường kiếm đột nhiên đầy người chính khí: “Ta lần đầu tiên nhìn thấy chủ nhân, liền biết hắn uy vũ bất phàm, trực tiếp muốn nhận chủ, vừa rồi sở dĩ làm như vậy, là muốn khảo nghiệm một chút mà thôi!”"),t("br"),t("br"),n._v("“...” Trương Huyền che cái trán."),t("br"),t("br"),n._v("Không nghĩ tới Tiên Quân cấp bậc vũ khí, cũng không biết xấu hổ như vậy!"),t("br"),t("br"),n._v("Rất nhanh, nhận chủ xong xuôi, cảm giác trường kiếm triệt để nhận khống chế của mình, Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Nhìn tới... Cái này hồ lô, cũng không phải chỉ ăn cơm trắng, vẫn là có nhất định tác dụng!"),t("br"),t("br"),n._v("Tuy là, dựa vào thư viện nhìn ra thiếu hụt, đem chuôi này Đồng Thường kiếm, sớm muộn cũng có thể luyện hóa, nhưng khẳng định không có thuận lợi như vậy!"),t("br"),t("br"),n._v("“Tiên Quân cảnh kiếm, uy lực vẫn là quá lớn, cho dù nắm giữ Thần chi kiếm ý, cũng không cách nào bình thường khởi động...”"),t("br"),t("br"),n._v("Luyện hóa trường kiếm, rốt cuộc minh bạch, cái này Đồng Thường kiếm, đến cùng có bao nhiêu đáng sợ."),t("br"),t("br"),n._v("Khó trách không muốn nhận bản thân làm chủ."),t("br"),t("br"),n._v("Hư Tiên cảnh thực lực, ở trước mặt đối phương, không thể so với sâu kiến mạnh mẽ quá nhiều."),t("br"),t("br"),n._v("Có thể nói, thực lực bây giờ, liền đối phương một phần trăm uy lực, đều phóng thích không đi ra."),t("br"),t("br"),n._v("Dù vậy, dùng tới chuôi kiếm này, đánh bất ngờ mà nói, Tiên Quân cường giả, cũng có thể chém giết!"),t("br"),t("br"),n._v("Có thể nói, Tiên Quân trở xuống, muốn giết hắn gần như không có khả năng! Thậm chí Tiên Quân cường giả, cũng không cần lo lắng quá mức."),t("br"),t("br"),n._v("Chân chính có năng lực bảo vệ tính mạng."),t("br"),t("br"),n._v("“Thu!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, kiếm bị bắt vào nhẫn."),t("br"),t("br"),n._v("“Lần này làm không tệ, ta sẽ lại mua một ít đan dược cho ngươi đồ ăn, xem như ban thưởng!”"),t("br"),t("br"),n._v("Thoả mãn nhìn một chút hồ lô."),t("br"),t("br"),n._v("Nếu không phải cái tên này, không có khả năng nhanh như vậy luyện hóa, lần này xem như dựng lên đầu công."),t("br"),t("br"),n._v("Trấn an hồ lô vài câu, lần nữa thu về đan điền, Trương Huyền lần nữa ra khỏi phòng."),t("br"),t("br"),n._v("Biết Lạc Nhược Hi có thể là Thần Linh, áp lực của hắn trở nên càng lớn, nhất định phải nhanh tăng cao tu vi."),t("br"),t("br"),n._v("Giờ khắc này đột phá Hư Tiên đỉnh phong đại khái hơn một canh giờ... Cũng coi như có một đoạn thời gian, nên đi tìm sách nhìn, tranh thủ nhanh lên một chút đột phá!"),t("br"),t("br"),n._v("Chỉ cần đi đến Chân Tiên cảnh, dựa vào Thiên đạo chân khí tinh thuần, Thần chi kiếm ý cùng với Đồng Thường kiếm, Tiên Quân cũng có thể một trận chiến."),t("br"),t("br"),n._v("Đi ra sân nhỏ, hướng Bạch Nguyễn Khanh mượn phi hành Thánh thú, đối trưởng lão vị trí đỉnh núi bay đi."),t("br"),t("br"),n._v("Nội môn đệ tử tàng thư khố thư tịch, phần lớn đều là Hư Tiên cảnh trở xuống, hạch tâm đệ tử, hầu như đều là Chân Tiên cảnh trở xuống, muốn xem Chân Tiên cảnh, thậm chí Tiên Quân cảnh công pháp, chỉ có thể đi trưởng lão vị trí tàng thư khố."),t("br"),t("br"),n._v("Đi vào trước mặt, lấy ra Hàn Kiếm Thu đưa cho thân phận lệnh bài, quả nhiên thuận lợi tiến vào bên trong."),t("br"),t("br"),n._v("Trưởng lão tàng thư khố, đồng thời không có hạch tâm đệ tử tàng thư khố như vậy hùng vĩ, chỉ là cái hai tầng lầu nhỏ, rất thưa thớt, mấy cái giá sách trưng bày trong đó."),t("br"),t("br"),n._v("Cau mày, Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Chân Tiên cảnh giới tu luyện bí pháp, quả nhiên ở bên trong, chẳng qua số lượng cũng không phải là rất nhiều."),t("br"),t("br"),n._v("Rất nhanh thu vào thư viện."),t("br"),t("br"),n._v("“Chỉ có hơn sáu trăm bản?”"),t("br"),t("br"),n._v("Trương Huyền dừng lại."),t("br"),t("br"),n._v("Trưởng lão tàng thư khố, liên quan tới Chân Tiên cảnh tu luyện công pháp, chỉ có hơn sáu trăm bản, thiếu xa một ngàn bản số lượng."),t("br"),t("br"),n._v("Đi đến lầu hai."),t("br"),t("br"),n._v("Là Tiên Quân tu luyện công pháp, có điều, số lượng càng ít... Chỉ có hơn mười bản."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền rầu rĩ."),t("br"),n._v(" Người khác tu luyện, chỉ cần quyết định một môn công pháp, cố gắng nghiên cứu là được, mà hắn, cần càng nhiều thư tịch, tấn cấp suốt ngày đạo pháp quyết, mới có thể tiếp tục..."),t("br"),t("br"),n._v("Nói thật, hơn sáu trăm bản Chân Tiên cảnh công pháp, loại trừ Lăng Vân kiếm các loại này đại tông môn, địa phương khác căn bản không có khả năng tìm đến, số lượng đã rất nhiều... Nhưng đối với hắn tới nói, vẫn như cũ còn thiếu rất nhiều!"),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, Chân Tiên cảnh công pháp hội tụ vào một chỗ, tạo thành một bản bí tịch, mở ra xem xét, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cùng đoán đồng dạng, sai lầm thực sự quá nhiều, chừng bảy chỗ!"),t("br"),t("br"),n._v("Chỉ có bảy chỗ tu luyện công pháp, đối với người khác tới nói, đã được cho tuyệt thế bí tịch, hắn lại khó mà tu luyện."),t("br"),t("br"),n._v("“Xem ra còn muốn tìm kiếm đủ bí tịch...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm."),t("br"),t("br"),n._v("Muốn đem cái này bảy chỗ thiếu hụt toàn bộ loại bỏ, chỉ sợ ít nhất phải ba, bốn trăm bản Chân Tiên cảnh công pháp, mà loại này pháp quyết, mua, giá trị không biết bao nhiêu, vừa mới kiếm điểm này kiếm các tệ, khẳng định không đủ."),t("br"),t("br"),n._v("Lại chuyển vài vòng, đem trọn cái tàng thư khố tất cả thư tịch thu sạch vào trong óc, phát hiện quả thực không cách nào hình thành Thiên đạo công pháp, Trương Huyền đành phải bất đắc dĩ đi ra."),t("br"),t("br"),n._v("Một bên hướng về bay, một bên suy tư."),t("br"),t("br"),n._v("Chân Tiên, tại Lăng Vân kiếm các loại địa phương này, cũng đủ làm nội môn trưởng lão, trong trung tâm, có thể đi đến loại cấp bậc này cũng không nhiều... Có thể nói, tại toàn bộ đại lục, đều được cho một phương cường hào!"),t("br"),t("br"),n._v("Đổi lại danh sư đại lục, đã có thể cùng Đại Thánh so sánh."),t("br"),t("br"),n._v("Đi đến loại này cấp bậc, công pháp thưa thớt, mười phần bình thường... Có thể tìm tới hơn sáu trăm bản, đủ để chứng minh Lăng Vân kiếm các mạnh mẽ."),t("br"),t("br"),n._v("Có thể... Thu thập không đủ, liền không có cách nào tu luyện."),t("br"),t("br"),n._v("Để hắn tu luyện nắm giữ bảy chỗ thiếu hụt công pháp, còn không bằng giết được rồi!"),t("br"),t("br"),n._v("“Khổng sư cho cái lệnh bài kia, có thể tuỳ ý mua sắm dùng để tấn cấp đan dược... Không biết công pháp có thể hay không mua!”"),t("br"),t("br"),n._v("Trong lòng hơi động, một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Khổng sư cho lệnh bài, căn cứ Hàn Kiếm Thu tông chủ nói, có thể tùy ý mua sắm dùng để tấn cấp đan dược, vô tận tiêu hao... Đã như vậy, có thể hay không dùng để mua sắm Chân Tiên cảnh công pháp?"),t("br"),t("br"),n._v("Trước đó mua không nổi, nhưng... Có thứ này, hoàn toàn có thể tuỳ ý mua ah!"),t("br"),t("br"),n._v("Nghĩ đến cái này, con mắt sáng lên, có điều, rất nhanh đầy mặt cười khổ: “Cần rời khỏi Lăng Vân kiếm các mới được...”"),t("br"),t("br"),n._v("Cái lệnh bài này, tại Lăng Vân Kiếm các nội bộ, không thể sử dụng, muốn chân chính phát huy hiệu quả, phải đi kiếm các bên ngoài Thông Thần điện."),t("br"),t("br"),n._v("“Trước đi hỏi một chút...”"),t("br"),t("br"),n._v("Trở lại sân nhỏ, gặp Bạch Nguyễn Khanh còn không có rời khỏi, Trương Huyền đi vào trước mặt: “Không biết... Cách kiếm các gần nhất thành trấn tại phương hướng nào? Nơi đó nhưng có Thông Thần điện?”"),t("br"),t("br"),n._v("“Ngươi muốn đi ra ngoài?”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh sững sờ."),t("br"),t("br"),n._v("“Có quyết định này...”"),t("br"),t("br"),n._v("Trương Huyền cũng không phủ nhận."),t("br"),t("br"),n._v("“Khoảng cách kiếm các gần nhất thành trấn gọi Ô Hải thành, cách đây đại khái hơn ba ngàn dặm, bởi vì tới gần Ô hải mà đặt tên, thành thị rất lớn, coi là nhất lưu, có Thông Thần điện!” Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Không xa lắm...”"),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Khoảng cách ba ngàn dặm, phi hành Thánh thú hơn một canh giờ liền có thể đến, xem như rất gần."),t("br"),t("br"),n._v("“Là không xa, chỉ là... Kiếm các không cho phép đệ tử tùy tiện rời đi, thật muốn ra ngoài, có thể muốn tiêu phí công phu rất lớn, thậm chí còn muốn trưởng lão điện xem xét, không có thời gian vài ngày, không làm được!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh giải thích."),t("br"),t("br"),n._v("“Mấy ngày?” Trương Huyền nhíu nhíu mày, thời gian dài như vậy, quả thực thật phiền toái: “Đệ tử ra ngoài, phiền toái như vậy, trưởng lão kia đâu? Có hay không có thể tùy ý rời khỏi?”"),t("br"),t("br"),n._v("“Cũng không được, trưởng lão không có đặc thù sự tình, cũng là không cho phép tùy ý rời khỏi tông môn...” Bạch Nguyễn Khanh giải thích: “Trừ phi...”"),t("br"),t("br"),n._v("Gia gia của nàng chính là tam đại trưởng lão một trong Bạch Diệp trưởng lão, đối với môn nội quy củ, biết rất rõ ràng."),t("br"),t("br"),n._v("“Trừ phi cái gì?”"),t("br"),t("br"),n._v("“Trừ phi, ra ngoài làm nhiệm vụ! Lời như vậy, mặc kệ là trưởng lão vẫn là đệ tử, cũng có thể rất dễ dàng rời khỏi, sẽ không nhận làm khó!” Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“Nhiệm vụ?”"),t("br"),t("br"),n._v("“Bất luận nội môn đệ tử, vẫn là hạch tâm đệ tử, không có kiếm các tệ sử dụng, đều sẽ đi nhận chức vụ điện nhận lấy, có nhiệm vụ bài, có thể tùy thời rời khỏi tông môn, mà sẽ không bị người hạn chế!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh giải thích."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Làm nhiệm vụ kiếm tiền, trước đó liền nghe nói qua, ngại tới tiền quá chậm, mới không có đi làm."),t("br"),t("br"),n._v("“Sư thúc, ngươi thật muốn đi ra ngoài, ta có thể đi lĩnh nhiệm vụ, sau đó chúng ta cùng một chỗ đến Ô Hải thành!”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh cười cười."),t("br"),t("br"),n._v("Nàng đã sớm muốn rời đi sơn môn ra ngoài xông xáo, đáng tiếc gia gia một mực không cho phép, rời khỏi tông môn nhiệm vụ, cứ việc dẫn qua không ít, nhưng cũng đi không bao xa, nếu như lần này, có thể cùng sư thúc cùng một chỗ, chắc hẳn gia gia, chắc chắn sẽ không ngăn cản."),t("br"),t("br"),n._v("Ô Hải thành, là Lăng Vân kiếm các lân cận, thành thị phồn hoa nhất, nàng đã sớm nghe tiếng đã lâu, muốn qua."),t("br"),t("br"),n._v("“Vậy ngươi nhận lấy nhiệm vụ a, chúng ta cùng đi xem nhìn...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu đồng ý."),t("br"),t("br"),n._v("Thân phận của hắn nhận lấy nhiệm vụ cũng không tiện, Bạch Nguyễn Khanh mà nói, vừa vặn có thể giải quyết vấn đề này."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Bạch Nguyễn Khanh nhảy lên lưng thú, hai người liền thẳng tắp hướng nhiệm vụ điện phương hướng bay đi."),t("br"),t("br"),n._v("Nhiệm vụ điện, là Lăng Vân kiếm các phân phối nhiệm vụ địa phương, sẽ căn cứ nhiệm vụ độ khó khác biệt, thiết trí đẳng cấp cùng kiếm các tệ số lượng."),t("br"),t("br"),n._v("Đi vào trong đó, tìm một hồi, phát hiện không ít Ô Hải thành nhiệm vụ."),t("br"),t("br"),n._v("Ô Hải thành nhiệm vụ, bình thường tới nói, đều tương đối khó, nhưng thanh toán thù lao cũng đều rất mê người."),t("br"),t("br"),n._v("“Ô Hải thành Ô hải thương hội, giám bảo nhiệm vụ, giá trị ba mươi kiếm các tệ! Liền cái này đi...”"),t("br"),t("br"),n._v("Tuyển một hồi, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Những nhiệm vụ khác, đều cần tiêu phí không ít thời gian cùng tinh lực, nhiệm vụ này, chỉ là giám bảo, hẳn là mười phần nhẹ nhõm, đi qua hoàn thành nhiệm vụ, cũng không chậm trễ hắn đi vào Thông Thần điện."),t("br"),t("br"),n._v("(Cảm ơn thư hữu trở thành quyển sách thứ 8 vị minh chủ! Bạo phát xong xuôi.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2002"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2004"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);