(window.webpackJsonp=window.webpackJsonp||[]).push([[1134],{1489:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1992-thu-đo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1992-thu-đo"}},[n._v("#")]),n._v(" Chương 1992: Thu đồ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Bạch Nguyễn Khanh"),t("br"),t("br"),n._v("Chương trước trở về mục lục chương sau trở về trang sách"),t("br"),t("br"),n._v("“Mua thông thần ngọc phù?”"),t("br"),t("br"),n._v("Không nghĩ tới sư thúc gấp gáp như vậy, Bạch Nguyễn Khanh dừng lại một chút, lên tiếng: “Tốt!”"),t("br"),t("br"),n._v("Dù sao nhiệm vụ, trong ba ngày hoàn thành liền tốt."),t("br"),t("br"),n._v("Lại nói, lần này là đi ra mở mang kiến thức một chút, có thể hay không hoàn thành, không quá quan trọng."),t("br"),t("br"),n._v("Lấy ra chuẩn dọn xong bản đồ, tìm một vòng, Bạch Nguyễn Khanh xác định phương vị, điều khiển phi hành Thánh thú, phi hành tiếp cận nửa giờ, hai người tới một cái cao lớn cung điện."),t("br"),t("br"),n._v("Chính là Thông Thần điện thực thể."),t("br"),t("br"),n._v("“Ô Hải thành mua sắm đồ vật, dùng chính là cũng là đặc thù thông thần tệ, cùng trước kia Huyền Giang thành khác biệt, giá trị cao hơn! Đại khái cùng kiếm các tệ, có thể làm được một so một hối đoái! Bởi vậy, ở đây, có thể trực tiếp dùng kiếm các tệ tiến hành mua sắm...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh cười cười."),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Vốn đang lo lắng có muốn hay không lại nghĩ biện pháp kiếm tiền, hiện tại xem ra, không cần."),t("br"),t("br"),n._v("Hắn kiếm các tệ rất nhiều, mua cái ngọc phù mà thôi, hẳn là mười phần đơn giản."),t("br"),t("br"),n._v("Đi tới trước đài, tìm tới nhân viên phục vụ."),t("br"),t("br"),n._v("“Thông thần ngọc phù, khác biệt cảnh giới, giá cả khác biệt, Cổ Thánh tứ trọng, hai mươi thông thần tệ, Hư Tiên cảnh hai trăm, Chân Tiên cảnh hai ngàn! Hiện gấp mười lần tăng lên!”"),t("br"),t("br"),n._v("Phục vụ viên giải thích."),t("br"),t("br"),n._v("Cùng Lăng Vân kiếm các giá cả không kém bao nhiêu."),t("br"),t("br"),n._v("“Mua hai cái Hư Tiên cảnh thông thần ngọc phù...” Trương Huyền đem kiếm các tệ đưa tới."),t("br"),t("br"),n._v("Có Lăng Vân kiếm các thông thần ngọc phù, có thể tùy thời đem kiếm các tệ lấy ra."),t("br"),t("br"),n._v("Chỉ chốc lát, nhân viên phục vụ đưa tới hai cái ngọc phù: “Bên kia có tĩnh thất, tuyệt đối không có người quấy rầy, một canh giờ một cái thông thần tệ!”"),t("br"),t("br"),n._v("Trương Huyền muốn một gian, cùng Bạch Nguyễn Khanh tiến vào bên trong."),t("br"),t("br"),n._v("Thông thần ngọc phù xe nhẹ đường quen, hai người rất mau tiến vào trong đó."),t("br"),t("br"),n._v("Ngọc phù phía trong Thông Thần điện kết cấu, cùng ngoại giới giống nhau như đúc, để cho người ta sinh ra hoảng hốt cảm giác, Trương Huyền lần nữa đi tới trước đài, tìm được việc làm nhân viên, thuận tiện đem Khổng sư ban cho viên kia lệnh bài, lấy ra ngoài."),t("br"),t("br"),n._v("“Giúp ta nhìn một chút, có thể mua gì!”"),t("br"),t("br"),n._v("Phục vụ viên nhận lấy lệnh bài, trong mắt tràn đầy khiếp sợ nhìn lại: “Tiền bối, mời tới bên này, cái lệnh bài này quy định thực sự quá cao, ta không có quyền hạn, cần chúng ta trưởng lão, tự thân tới cho ngươi phục vụ!”"),t("br"),t("br"),n._v("Mang theo hai người đi vào một cái rộng lớn gian phòng, đợi một hồi, một cái lão giả vội vã đi tới trước mặt."),t("br"),t("br"),n._v("“Tại hạ, Ô Hải thành Thông Thần điện trưởng lão, tiền bối ngươi tốt, ngươi cái này viên lệnh bài ta xem, là điện chủ tự thân ban tặng, có thể mua sắm bất kỳ tấn cấp cần bảo vật, dược vật, không cần thu bất kỳ chi phí!”"),t("br"),t("br"),n._v("Khom người đến cùng, lão giả quân lệnh bài cung kính đưa tới."),t("br"),t("br"),n._v("Nhìn thấy cái này viên lệnh bài, như nhìn thấy điện chủ bản thân, cho dù là hắn, đều mười phần khiếp sợ."),t("br"),t("br"),n._v("Bởi vậy, người trước mắt này, mặc dù là người tuổi trẻ hình dáng, hắn không chút nào không dám thất lễ."),t("br"),t("br"),n._v("“Ừm!” Thấy lệnh bài là thật, Trương Huyền thở phào nhẹ nhõm, mỉm cười: “Cho ta lấy trước một trăm viên thượng phẩm Tiên Nguyên đan!”"),t("br"),t("br"),n._v("Đột phá đến Hư Tiên cảnh đỉnh phong, trung phẩm Tiên Nguyên đan hiệu quả đã yếu đi, muốn tiếp tục, nhất định phải lên phẩm Tiên Nguyên đan mới được."),t("br"),t("br"),n._v("Mà tại Lăng Vân kiếm các mua, một cái thượng phẩm Tiên Nguyên đan, liền muốn hai vạn kiếm các tệ, hắn tuy là khiêu chiến nội môn cùng hạch tâm kiếm lời không ít, cũng không mua được mấy cái."),t("br"),t("br"),n._v("“Một trăm?”"),t("br"),t("br"),n._v("Lão giả lông mày nhảy dựng: “Thượng phẩm Tiên Nguyên đan, đối Tiên Quân cường giả, đều có công hiệu, chúng ta Ô Hải thành Thông Thần điện... Tạm thời còn không bỏ ra nổi nhiều như vậy!”"),t("br"),t("br"),n._v("“Cái kia có thể cầm bao nhiêu đi ra?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Đại khái... Hai mươi viên trái phải!” Lão giả dừng lại một chút, nói tiếp: “Nếu như tiền bối không sốt ruột lời nói, trong ba ngày, hẳn là có thể gom góp...”"),t("br"),t("br"),n._v("“Trước đem hai mươi viên cho ta đi, còn lại mau chóng tiếp cận!” Trương Huyền dặn dò một tiếng."),t("br"),t("br"),n._v("Tuy vô công bất thụ lộc, nhưng hắn hiện tại nhu cầu cấp bách tăng thực lực lên, cùng lắm thì, thực lực đi đến Tiên Quân, hoặc là cao hơn, nhìn thấy Khổng sư sau, khiến cho dùng đồ vật, trả lại liền có thể."),t("br"),t("br"),n._v("“Là!”"),t("br"),t("br"),n._v("Thời gian không dài, lão giả lần nữa trở về, đã đem đan dược chuẩn bị hoàn toàn."),t("br"),t("br"),n._v("“Không biết tiền bối, còn có cái gì cần?”"),t("br"),t("br"),n._v("“Ta muốn thấy Chân Tiên cảnh tu luyện công pháp, không biết... Cái này viên lệnh bài, có thể hay không có thể mua sắm?” Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này viên lệnh bài, chỉ có thể mua sắm dùng để tấn cấp đan dược các loại, thư tịch là không có cách nào mua sắm...” Dừng lại một chút, lão giả nói tiếp: “Hơn nữa, mua sắm sau đan dược, không thể dùng bỏ ra bán, chỉ có thể dùng tại bản thân trên việc tu luyện, nếu không, Một khi bị phát hiện, liền sẽ bị đông cứng, về sau lại không cách nào sử dụng!”"),t("br"),t("br"),n._v("Trương Huyền cười khổ."),t("br"),t("br"),n._v("Trước đó liền đoán ra, khả năng không được, chính tai nghe được xác nhận, vẫn như cũ không khỏi có chút thất lạc."),t("br"),t("br"),n._v("Không thể mua sắm thư tịch, lại không có cần mua sắm tu luyện vật tư, Trương Huyền đành phải lui ra Thông Thần điện, đi qua ngọc phù bên trên truyền tống trận, đem dược vật lấy ra."),t("br"),t("br"),n._v("Thượng phẩm Tiên Nguyên đan quả nhiên linh khí nồng đậm, vừa lấy ra, liền cho người ta một loại dâng trào cảm giác, phổ thông trung phẩm đan dược, tới tỷ thí, kém không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Cho!”"),t("br"),t("br"),n._v("Tiện tay ném cho Bạch Nguyễn Khanh một cái."),t("br"),t("br"),n._v("“Đa tạ sư thúc...”"),t("br"),t("br"),n._v("Nữ hài hơi đỏ mặt."),t("br"),t("br"),n._v("Loại cấp bậc này dược vật, chỉ ở gia gia nơi đó nhìn thấy qua, bởi vì quá mức quý giá, chưa hề sử dụng qua."),t("br"),t("br"),n._v("Trong lòng đã sớm kích động."),t("br"),t("br"),n._v("Há miệng nuốt vào trong bụng."),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),n._v(" To lớn linh khí phóng thích ra, cả người đồng tử co rụt lại, thân thể mềm mại không ngừng run rẩy, mồ hôi cuồn cuộn chảy xuôi."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Thấy nàng bộ dạng không đúng, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("“Sư thúc, viên đan dược này linh khí quá mức nồng đậm, ta... Không chịu nổi...” Vẻ mặt trắng bệch, Bạch Nguyễn Khanh vội nói."),t("br"),t("br"),n._v("Nàng dùng trung phẩm Tiên Nguyên đan, mỗi một viên, đều cần tiêu hóa rất lâu mới có thể hoàn thành, thượng phẩm ẩn chứa linh khí quá cường đại, căn bản chịu không được."),t("br"),t("br"),n._v("Liền giống như đem một xe đồ vật, cứ thế mà nhét vào ếch trong miệng đồng dạng, hơi không cẩn thận, liền có thể bạo thể mà chết!"),t("br"),t("br"),n._v("Lúc này, nàng rốt cuộc minh bạch, vì sao gia gia, không cho nàng sử dụng loại cấp bậc này dược vật, không phải không bỏ đến, mà là... Nàng Chân Tiên cảnh sơ kỳ thực lực, cho, cũng là lãng phí, làm không cẩn thận, sẽ còn xuất hiện tai hoạ!"),t("br"),t("br"),n._v("“Ah...” Lời còn chưa dứt, một tiếng kêu thảm."),t("br"),t("br"),n._v("Lực lượng quá mức hung mãnh, trong cơ thể lực lượng tàn phá bừa bãi từ huyệt đạo phun ra, Bạch Nguyễn Khanh toàn thân ửng hồng, thân thể bóng bay giống như bành trướng, bất cứ lúc nào cũng sẽ nổ tung."),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy dựng."),t("br"),t("br"),n._v("Loại tình huống này, hắn cũng đoán không kịp."),t("br"),t("br"),n._v("Vốn chỉ nghĩ, đối phương giúp như vậy đại ân, có đan dược cho bên trên một cái, cũng coi như báo đáp... Không nghĩ tới, sẽ xuất hiện loại tình huống này."),t("br"),t("br"),n._v("Thật muốn bị đan dược trướng chết... Trở về thế nào cùng Bạch Diệp trưởng lão bàn giao?"),t("br"),t("br"),n._v("“Đánh một bộ võ kỹ cho ta xem một chút...”"),t("br"),t("br"),n._v("Thấy đối phương sắp không chịu nổi, Trương Huyền vội vàng nói."),t("br"),t("br"),n._v("Mặc dù không biết đối phương mục đích làm như vậy, nhưng tình cảnh nguy cấp, bây giờ không có những biện pháp khác, Bạch Nguyễn Khanh cắn răng một cái, một đạo kiếm pháp thi triển đi ra."),t("br"),t("br"),n._v("Vội vàng nhìn về phía vừa mới hình thành thư tịch, Trương Huyền lông mày ngay sau đó nhăn lại."),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, thượng phẩm Tiên Nguyên đan linh khí thực sự quá nồng nặc, hiện tại nữ hài, liền cùng trong lúc vô tình nuốt pha nhảy mảnh đồng dạng, trong cơ thể linh lực quá mức mạnh mẽ, không nghĩ biện pháp khơi thông, vô cùng có khả năng tại chỗ nổ tung, cuối cùng dẫn đến vẫn lạc."),t("br"),t("br"),n._v("“Duy nhất phương pháp, chính là tu luyện, đem những này bạo ngược linh khí, hình thành tiên lực, tụ hợp vào đan điền...”"),t("br"),t("br"),n._v("Cùng vỡ đê hồng thủy đồng dạng, muốn giải quyết, lấp không bằng khai thông, vạn chảy về biển, liền có thể loại bỏ tai hoạ ngầm."),t("br"),t("br"),n._v("Chỉ là... Làm như vậy, đối công pháp có rất lớn hạn chế."),t("br"),t("br"),n._v("Phổ thông công pháp, cùng ống hút đồng dạng, đối mặt như đại dương Giang Hà, nhiều tới mấy chục cây cũng không có tác dụng gì."),t("br"),t("br"),n._v("Hiện tại muốn làm chính là, tìm kiếm một loại mạnh mẽ công pháp, cấp tốc đem không cách nào khống chế linh khí, hội tụ về chảy!"),t("br"),t("br"),n._v("“Nhìn tới... Chỉ có thể thu làm học sinh!”"),t("br"),t("br"),n._v("Ánh mắt lộ ra bất đắc dĩ, Trương Huyền than thở một tiếng."),t("br"),t("br"),n._v("Truyền thụ đối phương kiếm thuật, một chiêu hai chiêu, tiết lộ ra ngoài, cũng không khẩn yếu, không bái sư cũng được, có thể Thiên đạo công pháp, liền không giống với lúc trước, liên lụy đến bản thân bí mật lớn nhất, tuyệt không thể có một tia sơ sẩy, nhất định phải thu làm đệ tử, mới có thể truyền thụ."),t("br"),t("br"),n._v("Nếu không, Một khi xảy ra vấn đề, bản thân khẳng định sẽ trở thành tất cả mọi người nghiên cứu đối tượng, sinh tử cũng không khỏi bản thân điều khiển."),t("br"),t("br"),n._v("“Chân Tiên cảnh Thiên đạo công pháp, cứ việc có bảy chỗ thiếu hụt, đối với ta mà nói, khó mà tu luyện, đối với nàng, hẳn là so trước đó tu luyện, cường đại hơn không biết gấp bao nhiêu lần...”"),t("br"),t("br"),n._v("Tại Lăng Vân kiếm các sửa sang lại Chân Tiên cảnh thiên đạo công pháp, có bảy chỗ thiếu hụt, đã phù hợp bản rút gọn Thiên đạo công pháp tiêu chuẩn."),t("br"),t("br"),n._v("Dù sao, chân chính hoàn mỹ pháp quyết, loại trừ hắn, bất luận người nào đều không thể luyện thành."),t("br"),t("br"),n._v("Nghĩ tới những thứ này, tại không do dự, ngẩng đầu nhìn về phía trước mắt bất cứ lúc nào cũng sẽ không chịu nổi nữ hài, truyền âm qua: “Ta có thể giải quyết ngươi bây giờ tai hoạ ngầm, nhưng nhất định phải bái ta làm thầy, truyền thụ cho ngươi độc môn công pháp mới được!”"),t("br"),t("br"),n._v("“Đồ nhi bái kiến lão sư...”"),t("br"),t("br"),n._v("Không chần chờ chút nào, Bạch Nguyễn Khanh lập tức quỳ rạp xuống đất."),t("br"),t("br"),n._v("Nàng đã sớm muốn bái sư, bất quá đối phương không có ý tứ này, Một mực không dám có chỗ động tác, lúc này mở miệng, nào dám lo nghĩ."),t("br"),t("br"),n._v("Nói thật, vui vẻ cũng không kịp."),t("br"),t("br"),n._v("“Ừm, đây là công pháp của ta...”"),t("br"),t("br"),n._v("Thừa nhận nàng quỳ lạy, Trương Huyền ngón tay một điểm, trước đó sưu tập Thiên đạo công pháp, lập tức truyền vào đối phương trong óc, cùng thời khắc đó, cong ngón tay búng một cái, vô số châm bạc rơi vào đối phương huyệt đạo bên trên, Thiên đạo chân khí, theo kinh mạch truyền vào mà vào."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Bạo ngược linh khí, tại thiên đạo chân khí dẫn dắt bên dưới, lập tức trở nên suôn sẻ lên, lại không còn trước đó sắp bùng nổ giống như bành trướng cảm giác."),t("br"),t("br"),n._v("“Công pháp này...”"),t("br"),t("br"),n._v("Lúc này, Bạch Nguyễn Khanh cũng xem xong đối phương vừa mới truyền lại mà đến pháp quyết, đồng tử nhịn không được co rút lại."),t("br"),t("br"),n._v("Bộ công pháp này, thoạt nhìn cùng hắn học tập Lăng Vân kiếm các công pháp, đồng căn đồng nguyên, có chút tương tự, nhưng cao minh không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Trước đó pháp quyết, di động chân khí, nhiều nhất chỉ là dòng suối, mà bây giờ, giang hải Bình thường rộng lớn, không thể so sánh nổi."),t("br"),t("br"),n._v("Biết công pháp trân quý, không dám do dự, vội vàng nhắm mắt lại, dựa theo phía trên yêu cầu phương pháp vận chuyển lực lượng."),t("br"),t("br"),n._v("Tu vi trong nháy mắt cấp tốc tăng thêm."),t("br"),t("br"),n._v("Chân Tiên cảnh sơ kỳ!"),t("br"),t("br"),n._v("Chân Tiên cảnh trung kỳ!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không đến ba canh giờ, không có bất kỳ cái gì ràng buộc, trực tiếp đạt đến Chân Tiên cảnh đỉnh phong!"),t("br"),t("br"),n._v("Tu vi đi đến loại cảnh giới này, viên kia thượng phẩm Tiên Nguyên đan lực lượng cũng gần như bị tiêu hao hầu như không còn."),t("br"),t("br"),n._v("“Thực lực của ta...”"),t("br"),t("br"),n._v("Cảm nhận được trong cơ thể dâng trào muốn ra lực lượng, bất cứ lúc nào cũng sẽ bùng nổ mà ra, Bạch Nguyễn Khanh lần nữa nhìn về phía thanh niên trước mắt, đè nén kích động trong lòng, cũng nhịn không được nữa, trực tiếp quỳ rạp xuống đất: “Đồ nhi, bái kiến lão sư, Tạ lão sư ân cứu mạng...”"),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Nàng bên này quỳ xuống, thư viện nhẹ nhàng chấn động, trong đầu lập tức nhiều ra một bản thư tịch màu vàng."),t("br"),t("br"),n._v("Thiên đạo chi sách!."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2005"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2007"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);