(window.webpackJsonp=window.webpackJsonp||[]).push([[1137],{1492:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1995-truong-huyen-giai-thich"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1995-truong-huyen-giai-thich"}},[n._v("#")]),n._v(" Chương 1995: Trương Huyền giải thích")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("【 Canh [3]】"),t("br"),t("br"),n._v("Không chỉ hắn không hiểu, gian phòng những người khác cũng đều choáng váng."),t("br"),t("br"),n._v("Vương Hành Chủ cứ việc không phải giám bảo sư, đối giám bảo ngành nghề biết không nhiều, khả năng đủ mua sắm pho tượng này, chứng minh đối tĩnh tâm thạch tìm hiểu không ít, tự nhiên cũng là có thể phân biệt ra được, tới đối ứng huyết tinh thạch."),t("br"),t("br"),n._v("Mua tĩnh tâm thạch, lại trở thành thứ này, quan trọng... Hạc lão còn nói là chính xác!"),t("br"),t("br"),n._v("Thật muốn không có gặp được vị thanh niên này, lấy về... Sợ thời gian không dài, thực biết bị oan hồn quấn quanh, tâm tình uất ức, rất có thể sẽ cùng cái kia thỏ đồng dạng, tại chỗ tự sát!"),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra? Hạc lão, ngươi... Đây là ngươi giám định không có sai lầm?”"),t("br"),t("br"),n._v("Một cơn lửa giận tuôn hướng trong lòng, quay đầu nhìn trước lão giả trước mắt, khí sắp nổ tung."),t("br"),t("br"),n._v("Bình thường, lấy thân phận của hắn, khẳng định không dám như vậy cùng đối phương nói chuyện, có thể... Giờ phút này suýt chút nữa chết, đổi lại ai, đều sẽ không tiếp thụ được."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thân thể run rẩy, Hạc lão không biết trả lời như thế nào, ngồi xổm xuống, đem tượng mảnh vỡ nhặt lên, bất luận nhìn thế nào, đều là huyết tinh thạch, cùng tĩnh tâm thạch không có nửa xu quan hệ."),t("br"),t("br"),n._v("Trong nháy mắt giống như là già đi mười tuổi, run run phút chốc, nhìn về phía Trương Huyền, nói “Không biết... Các hạ xưng hô như thế nào?”"),t("br"),t("br"),n._v("“Lăng Vân kiếm các hạch tâm đệ tử, Trương Huyền!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Thân phận trưởng lão tạm thời vẫn là đừng nói nữa, nếu không, sợ đối phương tại chỗ dọa chết đi qua."),t("br"),t("br"),n._v("“Trương tiểu huynh đệ, pho tượng này, ta rõ ràng thấy là tĩnh tâm thạch, có thể làm sao...”"),t("br"),t("br"),n._v("Lại không còn trước đó cao ngạo, xưng hô cũng biến thành “Tiểu huynh đệ”, Hạc lão khom người đến cùng."),t("br"),t("br"),n._v("Hắn thoạt nhìn không có bất kỳ sai lầm nào, kết quả nhưng biến thành như vậy, đối phương có thể sớm biết, khẳng định là nhìn ra vấn đề."),t("br"),t("br"),n._v("Trước đó cũng đã gặp qua Lăng Vân kiếm các tiếp nhiệm vụ tới đụng đại vận nội môn đệ tử, kết quả cái gì cũng không biết, còn làm ra không ít chê cười, thậm chí còn đem một kiện giá trị cực cao bảo vật tiêu hủy, làm hại hắn chịu phạt."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới đối kiếm các đệ tử cái gọi là nhiệm vụ, có chút ác cảm."),t("br"),t("br"),n._v("Nhìn thấy cái tên này tuổi tác cũng không lớn, nghĩ đến giống như những người khác, cũng là tới đụng đại vận, lừa gạt nhiệm vụ, không nghĩ tới... Lại là chân chính giám bảo sư!"),t("br"),t("br"),n._v("So với hắn nhãn lực cao minh không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Thấy hắn lại không còn trước đó vênh váo tự đắc, Trương Huyền chẳng muốn nói nhiều, tiện tay nhặt lên một khối đá vụn, cầm tại lòng bàn tay: “Tĩnh tâm thạch cùng huyết tinh thạch, cả hai trái lại, lại đều có thể toả ra lực lượng, ảnh hưởng tinh thần của người khác cùng tâm trí! Điểm ấy, không cần ta nói nhiều, các ngươi đều rõ ràng a!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Tĩnh tâm thạch cùng huyết tinh thạch, đều thuộc về linh thạch một loại, phổ thông linh thạch, có thể dự trữ linh khí, cung cấp cho người ta tu luyện năng lượng, mà thứ này, dự trữ chính là ảnh hưởng người tinh thần đồ vật!"),t("br"),t("br"),n._v("“Hạc lão giám định không sai, quả thực là Đỗ Hà Tiên quân lưu lại, cũng là hắn tự thân điêu khắc, thậm chí phía ngoài màu sắc, cũng là hắn miêu hội...” Trương Huyền nói tiếp."),t("br"),t("br"),n._v("“Là hắn lưu lại, cái kia...” Hạc lão kì quái."),t("br"),t("br"),n._v("Nói như vậy, bản thân không có lầm ah!"),t("br"),t("br"),n._v("Có thể kết quả, vì sao cùng suy đoán có cách nhau một trời một vực?"),t("br"),t("br"),n._v("“Các ngươi ai biết vị này Đỗ Hà Tiên quân là ai?”"),t("br"),t("br"),n._v("Không có trả lời nghi vấn của hắn, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Ta biết...” Một bên Viễn Thanh nhịn không được mở miệng: “Đỗ Hà Tiên quân, là trăm năm trước y đạo thánh thủ, làm người lương thiện, cả đời cứu người vô số, nguyên nhân chính là như vậy, không ít người đều thờ phụng hắn tượng, thứ nhất đền ơn, thứ hai van xin bình yên! Loại người này, làm sao sẽ làm ra huyết tinh thạch đồ vật, độc hại hậu nhân?”"),t("br"),t("br"),n._v("Đỗ Hà Tiên quân, là Ô Hải thành trăm năm trước xuất hiện một vị siêu cấp cường giả, y sư, nắm giữ rất lớn danh vọng, cứu người vô số, làm sao sẽ cố ý hại người?"),t("br"),t("br"),n._v("Dùng huyết tinh thạch điêu khắc chân dung của hắn, hơn nữa cho người ta một loại an thần tĩnh tâm tác dụng, Rõ ràng lừa dối!"),t("br"),t("br"),n._v("“Độc hại? Ngươi nói sai!” Trương Huyền lắc đầu: “Hắn làm ra thứ này, cũng không phải là hại người, cùng ngươi mới vừa nói đồng dạng, là đang cứu người!”"),t("br"),t("br"),n._v("“Cứu người?”"),t("br"),t("br"),n._v("Mọi người càng thêm mê hoặc."),t("br"),t("br"),n._v("“Thứ này, là huyết tinh thạch điêu khắc mà thành, bên ngoài dùng đặc thù lực lượng vẽ tranh, mặt ngoài là tăng thêm màu sắc, thoạt nhìn để tảng đá trở nên sinh động như thật, trên thực tế, cùng ta vừa rồi vẽ tranh đồng dạng, dùng bút lông buộc vòng quanh đặc thù trận pháp, áp chế trong đó sát lục chi khí! Không chỉ có như vậy, còn có thể cung cấp lực lượng, không ngừng toả ra tĩnh tâm khí tức, làm cho tâm thần người an bình.”"),t("br"),t("br"),n._v("Thấy mọi người không hiểu, Trương Huyền giải thích: “Tình huống bình thường, những này sát lục chi khí, sẽ vẫn luôn nhìn không ra, cho nên, Hạc lão mới có thể cho rằng đây là sự thực!”"),t("br"),t("br"),n._v("Hạc lão gật đầu."),t("br"),n._v("Thật sự là hắn không có nhìn ra, còn có sát lục chi khí."),t("br"),t("br"),n._v("“Tình huống bình thường, sát lục chi khí sẽ không toả ra, có thể mượn trợ trận pháp, đem những này để cho người ta tĩnh tâm khí tức thả ra ngoài, lại không được... Trận pháp dung hợp, tương đương phá hủy tượng bên trên vẽ ra tới trận đồ, vừa mới bắt đầu vẫn không cảm giác được đến cái gì, kèm theo thời gian trôi qua, sát lục chi khí sẽ từ từ thâm nhập vào tu luyện giả trong cơ thể. Nếu như vừa rồi Vương Hành Chủ, trở về sử dụng cái này đồ vật, không chỉ bản thân sẽ mất đi ý thức mà tự sát, làm không cẩn thận sẽ còn đem thân hữu, người nhà toàn bộ giết chết, cùng một chỗ chôn cùng!”"),t("br"),t("br"),n._v("Vương Hành Chủ run rụt lại, trên đầu tràn đầy mồ hôi."),t("br"),t("br"),n._v("Có chút bảo vật, dùng tốt, có thể khiến người ta cấp tốc tiến bộ, dùng không tốt... Chết cũng không biết chết như thế nào!"),t("br"),t("br"),n._v("Liền giống như cái này!"),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, giám bảo sư chức nghiệp, mới nắm giữ cực cao địa vị, kiếm tiền cũng cực nhanh."),t("br"),t("br"),n._v("“Ta mặc dù đối vị này Đỗ Hà Tiên quân cuộc đời không tính là am hiểu, nhưng... Hắn làm ra thứ này, nếu như không có đoán sai, cũng không phải là dùng để hại người, mà là muốn cứu trị, rơi vào ngủ mê mệt bệnh nhân!”"),t("br"),t("br"),n._v("Không để ý tới hắn sợ hãi, Trương Huyền nói tiếp: “Rơi vào ngủ mê mệt bệnh nhân, bất luận bất kỳ thủ đoạn nào đều không gọi tỉnh lời nói, kích thích tinh thần, là biện pháp tốt nhất! Pho tượng này, vừa mới bắt đầu phóng thích an thần tĩnh tâm lực lượng, để cho người ta không có bất kỳ cái gì đề phòng, sau đó... Sát lục chi khí lặng lẽ xâm nhập, liền có thể thuận lợi kích thích hồn phách, để hắn cấp tốc tỉnh táo!”"),t("br"),t("br"),n._v("Lúc trước Ngụy Như Yên bởi vì không khống chế được Tiên Thiên độc thể lực lượng, rơi vào hôn mê, bất kể như thế nào đều không gọi tỉnh, cuối cùng dùng đặc thù dược vật mới lấy thành công."),t("br"),t("br"),n._v("Nếu như Lúc đó liền có thứ này, hẳn là có thể đưa đến tác dụng."),t("br"),t("br"),n._v("Thân thể coi như hôn mê, cũng có bản thân bảo vệ cơ chế, sẽ đối với kẻ xâm nhập, tiến hành phản kháng, tỷ như thân thể của hắn, vu hồn rời thân thể, cảm nhận được nguy hiểm, cũng có thể một mình chiến đấu!"),t("br"),t("br"),n._v("Nước ấm nấu ếch."),t("br"),t("br"),n._v("Vừa mới bắt đầu tỏa ra làm cho tâm thần người yên tĩnh khí tức, thân thể sẽ dần dần mất đi đề phòng, lúc này, lại tiến hành kích thích, hiệu quả càng tốt!"),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, hắn mới nói, đối phương làm ra cái này, là vì cứu người, cũng không phải là hại người."),t("br"),t("br"),n._v("“Cái này... Đỗ Hà Tiên quân cuộc đời, ta nghiên cứu qua, tiểu nữ nhi của hắn, một lần ngoài ý muốn, hôn mê bất tỉnh, ngủ trọn nửa năm, đủ loại thủ đoạn đều là dùng qua, chính là không có hiệu quả, về sau... Không biết dùng phương pháp gì, tỉnh lại, hắn cũng không có truyền ra ngoài qua, trở thành một đại bí ẩn... Chẳng lẽ, chính là cái này?”"),t("br"),t("br"),n._v("Hạc lão sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Làm giám bảo sư, biết sự tình muốn bao nhiêu, Đỗ Hà Tiên quân cuộc đời, đặc biệt nhìn qua liên quan thư tịch, biết hắn con gái nhỏ sự tình."),t("br"),t("br"),n._v("Vốn không nghĩ tới, nghe được trước mắt vị này Trương Huyền lời nói, bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("“Có lẽ vậy, đồ vật không có thiện ác chi phân, phải xem có biết dùng hay không, dùng thứ này, cứu chữa hôn mê bất tỉnh người, hiệu quả rất tốt, có thể dùng để tu luyện... Rất dễ dàng tẩu hỏa nhập ma!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Hạc lão hiểu được, lần nữa nhìn về phía thanh niên trước mắt ánh mắt lộ ra phức tạp biểu lộ, một lát sau, nhịn không được gật đầu: “Ta thua... Vương Hành Chủ, ta sẽ đối ngươi tiến hành bồi thường!”"),t("br"),t("br"),n._v("“Hạc lão khách khí!”"),t("br"),t("br"),n._v("Vương Hành Chủ vội vàng đáp lễ."),t("br"),t("br"),n._v("Hành lý xong xuôi, nhìn về phía Trương Huyền, ánh mắt mang theo cảm ơn: “Trương huynh, không chỉ cứu Vương Hành Chủ, cũng chờ tại đã cứu ta!”"),t("br"),t("br"),n._v("Đối phương thời khắc mấu chốt, ngăn lại Vương Hành Chủ, cứ việc vừa rồi mười phần tức giận, suy nghĩ cẩn thận, nhưng tương đương cứu hắn."),t("br"),t("br"),n._v("Nếu không, đối phương thật muốn tu luyện, xuất hiện biến cố, làm giám bảo người, khẳng định cũng sẽ nhận phán quyết!"),t("br"),t("br"),n._v("“Không biết, ta hiện tại nhưng có tư cách, đi hoàn thành tông môn cho ra nhiệm vụ?”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền nói."),t("br"),t("br"),n._v("“Đương nhiên là có...”"),t("br"),t("br"),n._v("Hạc lão liền vội vàng gật đầu: “Mời tới bên này!”"),t("br"),t("br"),n._v("Hắn đều không có nhìn ra được đồ vật, đối phương liếc mắt liền nhận ra, nhãn lực mạnh mẽ, lợi hại không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Có lẽ... Thật có thể giải quyết, hắn một mực bất lực sự kiện kia..."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền đi theo đối phương sau lưng, đi ra ngoài.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2008"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2010"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);