(window.webpackJsonp=window.webpackJsonp||[]).push([[1138],{1493:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-192-truong-huyen-truyen-thuyet-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-192-truong-huyen-truyen-thuyet-ha"}},[n._v("#")]),n._v(" Chương 192: Trương huyền truyền thuyết (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Ta cũng là lần đầu tiên nghe nói vị này Trương Huyền lão sư, chuyên môn tìm người nghe mới biết được thân phận... Hắn chẳng những không là minh tinh giáo sư, còn vẫn bị người lên án...” "),t("br"),t("br"),n._v(" Nhớ tới trước thấy nội dung, Thẩm Truy bệ hạ đến bây giờ cũng có chút không dám tin tưởng, cười khổ lắc đầu, nhịn không được hỏi: “Các ngươi biết, vị này Trương lão sư trước ở học viện danh tiếng là cái gì không?” "),t("br"),t("br"),n._v(" “Còn có danh tiếng? Là cái gì?” "),t("br"),t("br"),n._v(" Ba vị danh sư thấy hứng thú, nhịn không được nhìn lại."),t("br"),t("br"),n._v(" “Là... Phế vật!” Thẩm Truy bệ hạ nói."),t("br"),t("br"),n._v(" “Phế vật?” "),t("br"),t("br"),n._v(" Tam sư hai mặt nhìn nhau."),t("br"),t("br"),n._v(" “Đúng vậy, có người nói vị này Trương Huyền thầy giáo khảo hạch được linh phân, còn dạy một vị học sinh tẩu hỏa nhập ma, được xưng là Hồng Thiên học viện từ trước tới nay kém nhất lão sư!” Thẩm Truy bệ hạ nói."),t("br"),t("br"),n._v(" “Thầy giáo khảo hạch được linh phân?” "),t("br"),t("br"),n._v(" “Dạy học sinh tẩu hỏa nhập ma?” "),t("br"),t("br"),n._v(" “Người như thế thế nào còn có thể ở lại giáo sư đội ngũ? Hơn nữa... Nếu kém như vậy, Lục Tầm cùng hắn tỷ thí làm gì?” "),t("br"),t("br"),n._v(" Tam sư đều có chút hôn mê."),t("br"),t("br"),n._v(" Trước không nói cái này Trương Huyền có bao nhiêu kém, chỉ nói Lục Tầm, ngươi đường đường minh tinh giáo sư, Hồng Thiên học viện đệ nhất nhân, cùng ai đọ không tốt, cùng một cái thầy giáo khảo hạch được linh phân lão sư đọ..."),t("br"),t("br"),n._v(" Này cùng với người què đọ thi chạy, người điếc đọ thính lực có cái gì khác nhau?"),t("br"),t("br"),n._v(" Thắng cũng không vẻ vang a!"),t("br"),t("br"),n._v(" Huống chi vạn nhất không thắng được đây?"),t("br"),t("br"),n._v(" “Ba vị hãy nghe ta nói hết, đây chỉ là trước hắn xưng hô, ở hơn mười ngày tiền, xuất hiện chuyển ngoặt!” Thẩm Truy bệ hạ nói tiếp: “Một vị kêu Tào Hùng lão sư bởi vì cùng hắn tranh đoạt một vị học viên, tiến hành học tâm khảo vấn, vốn tưởng rằng nắm chắc, kết quả lại xuất hiện làm cho trợn mắt hốc mồm kết cục.” "),t("br"),t("br"),n._v(" “Vị này Trương Huyền lão sư, không chỉ có không phải là cái gì đều không biết phế vật lão sư, còn là một vị cao thủ, tu vi đạt tới Ích Huyệt cảnh, càng làm cho học sinh đối với hắn đạt tới sáu mươi bốn điểm độ tín nhiệm...” "),t("br"),t("br"),n._v(" Học tâm khảo vấn mặc dù bị Hồng Thiên học viện cố ý đè lại tin tức, nhưng làm Thiên Huyền vương quốc quốc vương bệ hạ, muốn thẩm tra, còn là rất đơn giản, ngày đó chi tiết cùng nội dung, biết đến rất rõ ràng, một năm một mười nói ra."),t("br"),t("br"),n._v(" “Mới tuyển nhận học sinh, độ tín nhiệm sáu mươi bốn? Đã đạt được danh sư tiêu chuẩn? Này... Điều này sao có thể?” "),t("br"),t("br"),n._v(" “Không được hai mươi tuổi, Ích Huyệt cảnh tu vi? Thật là lợi hại thiên phú, thì là danh sư chỉ điểm học viên, cũng không gì hơn cái này đi!” "),t("br"),t("br"),n._v(" “Lợi dụng tẩu hỏa nhập ma, giải trừ trời sinh tỏa mạch tai hoạ ngầm? Cũng để học sinh hơn mười phút tựu từ võ giả nhất trọng đột phá đến nhị trọng...” "),t("br"),t("br"),n._v(" Ba vị danh sư ngươi xem ta, ta xem ngươi, thế nào cảm giác cân đang nằm mơ như nhau? Không quá tin tưởng đây?"),t("br"),t("br"),n._v(" Lão sư trẻ tuổi vì không phá phôi học viện danh tiếng, chịu nhục, cam nguyện cõng phế vật danh tiếng, kết quả còn bị người vẽ mặt, lúc này mới thuận thế phản kích..."),t("br"),t("br"),n._v(" “Nếu như mấy tin tức này đều là thật... Vị này Trương Huyền lão sư phẩm chất rất cao thượng đi!” "),t("br"),t("br"),n._v(" Trang Hiền nhịn không được cảm khái."),t("br"),t("br"),n._v(" Bị người vu hãm không phản bác, đơn giản là cố kỵ học viện mặt, loại này lòng dạ cùng chức nghiệp hành vi thường ngày, coi như là bọn họ, đều khó khăn dĩ làm được, huống chi một cái không đủ hai mươi thanh niên."),t("br"),t("br"),n._v(" “Ta chuyên môn phái người đi hỏi thăm giáo sư nghiệp đoàn, chiếm được chính xác trả lời!” "),t("br"),t("br"),n._v(" Thẩm Truy bệ hạ cũng biết mọi người không tin, ngoắc tay, từ thái giám trong sách nhận được một phong thư: “Đây là nghiệp đoàn Mạc trưởng lão tự tay viết viết, trong đó nội dung cùng ta mới vừa nói giống nhau như đúc, chư vị mời xem!” "),t("br"),t("br"),n._v(" Nói đem giấy viết thư đưa tới, Lưu Lăng đám người tiếp nhận, nhìn thoáng qua, đồng thời gật đầu."),t("br"),t("br"),n._v(" Này là giáo sư nghiệp đoàn xuất cụ giấy viết thư, mặt trên có công hội con dấu, không có khả năng có sai, bằng không, Mạc trưởng lão chức nghiệp đều sẽ phải chịu liên lụy."),t("br"),t("br"),n._v(" “Không nghĩ tới Hồng Thiên học viện, còn ẩn tàng một nhân tài như vậy!” "),t("br"),t("br"),n._v(" Lưu Lăng nhãn tình sáng lên."),t("br"),t("br"),n._v(" Vốn có hắn dự định tuyển nhận Lục Tầm tố học đồ, nhìn thấy bản thân hậu, hơi có chút thất vọng, lúc này nghe thế vị Trương lão sư truyền kỳ sự tích, nhất thời lần thứ hai thấy hứng thú."),t("br"),t("br"),n._v(" “Phẩm đức cao thượng, bất kể giác được mất, vì học sinh khổ tâm cô nghệ, thậm chí không tiếc trên lưng bêu danh... Loại này lão sư, ta cũng muốn thấy tận mắt kiến!” Trang Hiền cũng không nhịn được cảm khái."),t("br"),t("br"),n._v(" Danh sư mong muốn chiêu đến tốt học đồ, không riêng dạy học tốt, danh khí đại, phẩm chất cũng đã chiếm rất lớn một mặt."),t("br"),t("br"),n._v(" Không có tốt đẹp phẩm chất, chiêu đến một vị bạch nhãn lang, thì là thiên phú cho dù tốt thì có ích lợi gì?"),t("br"),t("br"),n._v(" “Bẩm báo bệ hạ, bên ngoài có người cầu kiến!” "),t("br"),t("br"),n._v(" Mấy người đang ở cảm khái, một tên hộ vệ lần thứ hai sãi bước đi tiến đến."),t("br"),t("br"),n._v(" “Người nào?” "),t("br"),t("br"),n._v(" “Là Bạch Ngọc thành Triệu thành chủ quản gia, Diêu Hàn!” Hộ vệ nói."),t("br"),t("br"),n._v(" “Để hắn tiến đến!” Thẩm Truy bệ hạ gật đầu."),t("br"),t("br"),n._v(" Triệu Phong thành chủ là hắn tín nhiệm nhất một phương quan to, Diêu Hàn là của hắn dòng chính, lẫn nhau nhận thức."),t("br"),t("br"),n._v(" “Diêu Hàn gặp qua bệ hạ!” Không lâu sau, một trung niên nhân sãi bước đi tiến đến, quỳ mọp xuống đất."),t("br"),t("br"),n._v(" Chính là Diêu Hàn."),t("br"),t("br"),n._v(" “Diêu tướng quân xin đứng lên!” Thẩm Truy bệ hạ bắt chuyện."),t("br"),t("br"),n._v(" Diêu Hàn mặc dù bây giờ chỉ là một quản gia, trên thực tế trước đây từng tùy Triệu Phong thành chủ nam chinh bắc chiến, từng làm được tướng quân vị trí."),t("br"),t("br"),n._v(" “Tướng quân lúc này qua đây, thế nhưng Triệu Phong thành chủ có chuyện gì bẩm báo?” "),t("br"),t("br"),n._v(" Biết đối phương không có khả năng không có việc gì chạy đến vương cung nói chuyện phiếm, Thẩm Truy bệ hạ hỏi."),t("br"),t("br"),n._v(" “Hồi bẩm bệ hạ, chúng ta thành chủ dự định mấy ngày nữa tới vương thành một chuyến, đặc biệt để ta hướng bệ hạ xin chỉ thị.” Diêu Hàn nói."),t("br"),t("br"),n._v(" Triệu Phong làm như nhất phương quan to, không thể nào cùng người thường như nhau dễ dàng, nói đi là đi, thì là nghĩ đến vương thành cũng khẳng định cần sớm xin chỉ thị, không phải, len lén qua đây, một ngày cấp bệ hạ biết được, hay đại nghịch bất đạo."),t("br"),t("br"),n._v(" “Tới vương thành?” Thẩm Truy sửng sốt."),t("br"),n._v(" “Là, tiểu thư bái sư Trương Huyền lão sư, Trương lão sư vì giúp nàng mở ra thể chất đặc thù, chuyên môn khảo hạch luyện đan sư, tìm kiếm trân quý (Hàn Dương mẫu thảo), cũng mua tam phẩm đan dược (Phá Âm đan) vì nàng dùng, ân tình thực sự quá lớn, thành chủ quyết định qua đây ngay mặt cảm kích!” Diêu Hàn trả lời."),t("br"),t("br"),n._v(" “Bái sư Trương Huyền? Hàn Dương mẫu thảo? Phá Âm đan?” "),t("br"),t("br"),n._v(" Mọi người cương thảo luận đến Trương Huyền, lúc này nghe nói như thế, tất cả đều sửng sốt, Thẩm Truy bệ hạ cũng không nhịn được nói: “Rốt cuộc chuyện gì, nói tường tận một lần!” "),t("br"),t("br"),n._v(" “Là!” Diêu Hàn gật đầu, liền đem Trương lão sư làm sao thu đồ đệ Triệu Nhã, thì như thế nào nhìn ra của nàng thể chất đặc thù, cũng khảo hạch luyện đan sư chờ chứa nhiều sự tình, kể lại giảng giải một lần."),t("br"),t("br"),n._v(" Người khác khả năng đối việc này biết đến kiến thức nửa vời, hắn bị đối phương ấu đả dường như đầu heo, chuyên môn kể lại điều tra, biết đến nhất thanh nhị sở, nói đứng lên không có sai lầm chút nào."),t("br"),t("br"),n._v(" “Ngươi nói... Cái này Trương Huyền lão sư khảo hạch nhất tinh luyện đan sư thành công?” "),t("br"),t("br"),n._v(" “Không chỉ có như vậy, vì giúp học sinh đột phá, bỏ ra số tiền lớn mua đan dược linh dịch? Thậm chí... Chuyên môn vì bọn họ lượng thân làm theo yêu cầu sáng tạo công pháp... Không cầu hồi báo?” "),t("br"),t("br"),n._v(" “Dốc hết tâm huyết sáng tạo vũ kỹ, nhiều ngày không chợp mắt, thế cho nên nằm ở ghế trên tựu ngủ thiếp đi?” "),t("br"),t("br"),n._v(" Nghe xong Diêu Hàn nói, tam đại danh sư đám mở to hai mắt nhìn, nắm tay xiết chặt."),t("br"),t("br"),n._v(" Lão sư, căn bản nhất nhiệm vụ, còn là truyền đạo, thụ nghiệp, giải thích nghi hoặc, nếu như ngay cả học sinh đều không coi trọng, chắc chắn sẽ không là tốt sư người."),t("br"),t("br"),n._v(" Vị này Trương Huyền lão sư, vì học sinh của mình, nỗ lực nhiều như vậy... Thậm chí còn không cho học sinh biết được, loại này phẩm chất, coi như là bọn họ chính tai nghe được, đều khiếp sợ tột đỉnh."),t("br"),t("br"),n._v(" Mấu chốt nhất là... Dĩ nhiên thông qua luyện đan sư khảo hạch, thành một vị chính thức luyện đan sư!"),t("br"),t("br"),n._v(" “Khảo hạch danh sư, cần những nghề nghiệp khác phụ trợ, trở thành chính thức luyện đan sư, nói cách khác, nếu như chiêu hắn vi học đồ nói, chỉ cần hơi chút điều giáo, sẽ có khảo hạch danh sư tư cách?” "),t("br"),t("br"),n._v(" Tam đại danh sư mắt càng ngày càng sáng."),t("br"),t("br"),n._v(" Danh sư có thể vì người chỉ điểm, tri thức lượng trước hết muốn rất kinh khủng mới được, thứ nhì, sẽ đối những nghề nghiệp khác cũng phi thường tinh thông, thậm chí có thể trở thành là chính thức một thành viên, không phải cũng không có khả năng làm cho chỉ điểm."),t("br"),t("br"),n._v(" Luyện đan sư là thượng cửu lưu giữa tột cùng nhất một trong những nghề, trở thành chính thức luyện đan sư, nếu như lại phối hợp tốt dạy học chất lượng cùng tri thức hệ thống, chẳng khác nào có tiến nhập danh sư điện phủ vào bàn khoán."),t("br"),t("br"),n._v(" Loại này giáo sư, thì là ở nhị đẳng, thậm chí nhất đẳng vương quốc, cũng là không ít danh sư tranh đoạt đối tượng."),t("br"),t("br"),n._v(" Dù sao học sinh cũng là danh sư, đối tự thân danh khí cũng là có trợ giúp rất lớn."),t("br"),t("br"),n._v(" “Có thực lực, bị người vu hãm, vi lấy đại cục làm trọng mà cam tâm chịu đựng; Truyền thụ học sinh, có can đảm nếm thử tân pháp, bị người hiểu sai, không kiêu không nóng nảy; Tuổi còn trẻ, tri thức uyên bác, lại tình nguyện ẩn dấu, không hào nhoáng, không làm bộ; Vì học viên, càng bỏ qua tất cả... Đây thật là danh sư tốt nhất mầm!” "),t("br"),t("br"),n._v(" Lưu Lăng càng nói mắt càng sáng: “Trang sư, Trịnh sư, nếu Hồng Thiên học viện có tốt như vậy lão sư, có thể hay không theo ta đi xem?” "),t("br"),t("br"),n._v(" “Lưu sư nói đâu nói, làm sao có thể là cùng ngươi nhìn, hẳn là theo ta nhìn, nhân vật như vậy, thế nhưng ta vẫn muốn tìm kiếm trợ giáo học đồ.” Trang sư cười khanh khách nói."),t("br"),t("br"),n._v(" “Ta đối luyện đan lý giải rất nhiều, cũng là chính thức luyện đan sư, hắn trở thành ta học đồ mới là điều kiện tốt nhất phối hợp...” Trịnh Phi chen vào nói."),t("br"),t("br"),n._v(" Nghe được Trương Huyền nhiều như vậy ưu tú sự tích, tam đại danh sư cũng không nhịn được nữa."),t("br"),t("br"),n._v(" Lão sư muốn cùng danh sư làm tốt quan hệ, danh sư cũng hy vọng có thể thu được tốt lão sư tố vì mình học đồ."),t("br"),t("br"),n._v(" Vị này Trương Huyền, phẩm chất cao thượng, vì học sinh không tiếc tất cả, tiềm lực so với kia một Lục Tầm mạnh hơn nhiều lắm, tam đại danh sư làm sao không động tâm?"),t("br"),t("br"),n._v(" “Này...” "),t("br"),t("br"),n._v(" Thấy tam sư tranh đoạt, không ai nhường ai, Thẩm Truy bệ hạ, Diêu Hàn toàn bộ đều ngẩn ở đây tại chỗ."),t("br"),t("br"),n._v(" Người khác đều là tễ phá đầu muốn có được danh sư đa liếc mắt nhìn, kết quả vị này Trương lão sư khen ngược, tam đại danh sư còn không người, tựu cướp túi bụi."),t("br"),t("br"),n._v(" Bất quá, ngẫm lại hắn chứa nhiều sự tích, loại thiên tài này, cũng xác thực đáng giá danh sư cướp giật."),t("br"),t("br"),n._v(" “Xem ra phải sớm ta cùng vị này Trương Huyền lão sư giao hảo...” "),t("br"),t("br"),n._v(" Thẩm Truy bệ hạ trong lòng âm thầm có quyết định."),t("br"),t("br"),n._v(" Một vị rất có thể trở thành danh sư tiềm lực bắp đùi, làm như quốc vương bệ hạ, làm sao có thể buông tha?"),t("br"),t("br"),n._v(" “Diêu tướng quân, ngươi trở lại thông tri Triệu Phong, để hắn cứ qua đây, không cần cố kỵ!” "),t("br"),t("br"),n._v(" Nghĩ vậy, lập tức gật đầu đồng ý Diêu Hàn thỉnh cầu."),t("br"),t("br"),n._v(" Vô luận đối vương quốc còn là với hắn mà nói, đây đều là chuyện tốt."),t("br"),t("br"),n._v(" Đi qua Triệu Nhã cái này ràng buộc, là có thể rất nhẹ nhàng bả vị này Trương Huyền lão sư bảng định ở Thiên Huyền vương quốc trên chiến xa."),t("br"),t("br"),n._v(" Một ngày vương quốc có một vị chân chính danh sư, thực lực của một nước nhất định phát triển không ngừng, tấn cấp nhị đẳng vương quốc, cũng ở trong tầm tay."),t("br"),t("br"),n._v(" “Thẩm Truy bệ hạ, chúng ta quyết định đi Hồng Thiên học viện nhìn, tiên không quấy rầy!” "),t("br"),t("br"),n._v(" Tam đại danh sư tranh đoạt một hồi, còn là quyết định xem trước một chút vị này Trương lão sư rốt cuộc là phủ cùng trong truyền thuyết như nhau anh minh thần võ."),t("br"),t("br"),n._v(" Tai nghe là giả, mắt thấy là thật, chỉ có chính mắt thấy được, tài năng làm ra phán đoán chính xác nhất."),t("br"),t("br"),n._v(" Về phần đến lúc đó thùy thu làm học đồ, còn phải xem ý của đối phương, ở chỗ này thì là cướp đầu rơi máu chảy, cũng không dùng."),t("br"),t("br"),n._v(" “Tam sư xin cứ tự nhiên!” "),t("br"),t("br"),n._v(" Thẩm Truy bệ hạ không dám ngăn trở, gật đầu đồng ý."),t("br"),t("br"),n._v(" Lưu Lăng, Trang Hiền, Trịnh Phi tam đại danh sư cũng không nhiều nói, bước nhanh đi ra ngoài, thẳng đến Hồng Thiên học viện đi."),t("br"),t("br"),n._v(" “Lục Tầm...” "),t("br"),t("br"),n._v(" Gặp tam sư vì Trương Huyền rời đi, nhớ tới vừa mới rời đi Lục Tầm, Thẩm Truy bệ hạ lắc đầu."),t("br"),t("br"),n._v(" Vô luận từ tu vi, học thức, phong độ, giáo dục học sinh xoay ngang, cấp tam sư cảm quan, truyền lại mà đến danh tiếng..."),t("br"),t("br"),n._v(" Vị này Hồng Thiên học viện đệ nhất minh tinh giáo sư, đều thua..."),t("br"),t("br"),n._v(" Thất bại thảm hại!"),t("br"),t("br"),n._v(" (Hơn ba ngàn tự đại chương tiết!) "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-200"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-202"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);