(window.webpackJsonp=window.webpackJsonp||[]).push([[114],{467:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1080-ta-muon-phong-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1080-ta-muon-phong-thanh"}},[n._v("#")]),n._v(" Chương 1080: Ta muốn phong Thánh!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1080: Ta muốn phong Thánh!"),t("br"),t("br"),n._v("Bên ngoài động tĩnh làm cho lớn như vậy, tất cả mọi người tưởng rằng Trương sư bị Thiên Diệp Vương phát hiện, phát sinh chiến đấu."),t("br"),t("br"),n._v("Kết quả lại chạy tới hai cái quái vật, không một cái có nhân dạng, làm sao cái tình huống?"),t("br"),t("br"),n._v("Cái này Phong Thánh các, không phải loại trừ bọn họ không còn người khác ư?"),t("br"),t("br"),n._v("“Cái này... Không phải thứ gì, là... Trương sư!” Hàn hội trưởng giống như là phát hiện cái gì, nuốt ngụm nước bọt: “Ngươi xem quần áo, cùng vừa rồi Trương sư giống nhau như đúc, chỉ bất quá hình như... Bị sét đánh qua!”"),t("br"),t("br"),n._v("“Không sai, quả thực là Trương sư, chỉ là... Phía trước cái kia đĩa bánh mặt chính là ai?” Ngô sư cũng nhận ra được, nhịn không được gật đầu, đồng thời có chút kỳ quái."),t("br"),t("br"),n._v("Phía trước cái kia đĩa bánh mặt, cũng cùng Trương sư quần áo đồng dạng quần áo, hình thể dáng người bình thường không hai, chỉ bất quá, mặt đều dẹp thành như vậy, đổi lại những người khác đã sớm chết đi, làm sao còn có thể chạy?"),t("br"),t("br"),n._v("Hơn nữa chạy nhanh như vậy? Thậm chí Trương sư đều có chút đuổi không kịp..."),t("br"),t("br"),n._v("“Ta muốn các ngươi chết...”"),t("br"),t("br"),n._v("Đang tại kỳ quái, chỉ thấy hai người sau lưng khúc quanh, một cái cao lớn nguyên thần bay tới, vừa đuổi bên cạnh công kích, mặt đất từng cơn lay động, bị đánh ra một cái tiếp một cái cực lớn chưởng ấn."),t("br"),t("br"),n._v("“Thiên Diệp Vương? Hắn... Nguyên thần xuất khiếu, nguy rồi, nhanh cứu người!”"),t("br"),t("br"),n._v("Thấy cảnh này, mọi người lại không để ý tới hai người này là ai, tất cả đều đồng loạt đem binh khí lấy ra ngoài."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("“Lên!”"),t("br"),t("br"),n._v("Nương theo Ngô sư một tiếng la lên, mọi người binh khí trong tay lập tức liên hợp cùng một chỗ, tạo thành một đạo huy hoàng mưa kiếm, hướng Thiên Diệp Vương đâm tới."),t("br"),t("br"),n._v("“Dám ngăn trở ta, các ngươi đều phải chết...”"),t("br"),t("br"),n._v("Lần nữa gào thét, trên không ở Thiên Diệp Vương nguyên thần, bàn tay đưa tay về phía trước, đem mưa kiếm ngăn tại trước mặt, trở tay chụp lại."),t("br"),t("br"),n._v("Lộc cộc lộc cộc lộc cộc lộc cộc!"),t("br"),t("br"),n._v("Ngô sư đám người nhất thời liên tục lui về sau bảy, tám bước, khí huyết sôi trào, nguyên một đám vẻ mặt trở nên trắng."),t("br"),t("br"),n._v("Trước đó, đối phương không có xuất khiếu, bọn họ cũng không phải là đối thủ, hiện tại nổi giận, chỉ còn lại có nguyên thần, lực lượng mạnh hơn, làm sao ngăn cản được?"),t("br"),t("br"),n._v("Bất quá, mặc dù không phải là đối thủ, lần này tiến công, cũng chậm lại Thiên Diệp Vương tốc độ, để Trương Huyền cùng phân thân thành công đi tới mọi người trước mặt."),t("br"),t("br"),n._v("“Nhanh tiến vào trận pháp...”"),t("br"),t("br"),n._v("Biết mọi người tại nơi này đáp chặn lời nói, làm không cẩn thận đều sẽ bị chém giết, Trương Huyền một tiếng la lên, tiếp tục hướng phía trước phóng đi."),t("br"),t("br"),n._v("Mọi người cũng hiểu chuyện gì xảy ra, theo sát trên đó, đi vào chỗ ở phía ngoài trong trận pháp."),t("br"),t("br"),n._v("Vừa tiến đến, Trương Huyền lúc này mới thở phào nhẹ nhõm, nhìn về phía cách đó không xa đĩa bánh mặt, tức giận đến lông mày nhảy loạn."),t("br"),t("br"),n._v("Nếu không phải đánh không lại hắn, khẳng định đã sớm giáo huấn một lần."),t("br"),t("br"),n._v("Làm một cái phân thân, không đi cứu bản tôn, kết quả so bản tôn chạy còn nhanh hơn, thật sự là quá không muốn mặt."),t("br"),t("br"),n._v("“Ngươi về trước tổ ong, lát nữa lại tìm ngươi tính sổ...”"),t("br"),t("br"),n._v("Biết bây giờ không phải là nói những này thời điểm, khoát tay áo, tinh thần khẽ động, đem hắn thu vào tổ ong."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Mới vừa làm xong những này, liền nghe đến một tiếng kêu khẽ, Kê Nham phó viện trưởng đã đem trận pháp vận chuyển, một đạo quang mang, bao phủ bốn phía."),t("br"),t("br"),n._v("Trận pháp này, là bọn họ những này người còn sống sót bố trí, chuyên môn dùng để ngăn cản Thiên Diệp Vương đánh lén cùng tiến công, sớm đã hết sức quen thuộc, trong nháy mắt liền có thể khởi động."),t("br"),t("br"),n._v("“Đáng ghét, đáng ghét...”"),t("br"),t("br"),n._v("Bị trận pháp ngăn cản, Thiên Diệp Vương một tiếng tức giận gào thét, ngay sau đó mọi người liền cảm thấy trận pháp kịch liệt nổ vang, lực lượng cuồng bạo không ngừng đánh tới, tựa như lúc nào cũng sẽ bị đánh nát."),t("br"),t("br"),n._v("“Tiếp tục như vậy, trận pháp không kiên trì được bao lâu...”"),t("br"),t("br"),n._v("Bị liên tục công kích mấy lần, chủ trì trận pháp Kê Nham sắc mặt trắng nhợt, trên đầu mồ hôi lạnh ứa ra."),t("br"),t("br"),n._v("Trước kia Thiên Diệp Vương mặc dù cũng tiến công qua, lại không điên cuồng như vậy bất chấp hậu quả, sớm làm như vậy, trận pháp khẳng định đã sớm phá!"),t("br"),t("br"),n._v("“Nơi này chỉ có chúng ta mấy cái, Thiên Diệp Vương lại không tìm được xác thực đường ra phía trước, chắc chắn sẽ không giết chúng ta, nếu không, một người tại đây, thực sự quá cô độc. Hiện tại như vậy khác thường... Trương sư, ngươi đến cùng làm cái gì?”"),t("br"),t("br"),n._v("Đàm Thanh lắc đầu, nhịn không được nhìn về phía cách đó không xa mặt mũi cháy đen thanh niên."),t("br"),t("br"),n._v("Đây rốt cuộc là làm nhiều yêu nghiệt sự tình, mới có thể để cho Thiên Diệp Vương nguyên thần xuất khiếu, bất kể sinh tử xông lại, hận không thể giết người ăn thịt?"),t("br"),t("br"),n._v("“Ta... Chỉ là đem hắn thân thể đánh nổ...” Trương Huyền xấu hổ."),t("br"),t("br"),n._v("Phân thân làm cục diện rối rắm, cái tên này trốn đến tổ ong liền xong rồi, bản thân nhưng muốn thu thập cục diện, càng nghĩ càng thấy đến buồn bực."),t("br"),t("br"),n._v("“Thân thể đánh nổ?”"),t("br"),t("br"),n._v("“Ngươi đem hắn... Giết? Chỉ còn lại có nguyên thần?”"),t("br"),t("br"),n._v("Mọi người tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Vừa rồi liền suy nghĩ, chỉ là cầm cái Đoạn Tục thảo, không đến mức nguyên thần xuất khiếu, liều mạng như vậy đi... Nằm mơ đều không có nghĩ đến, trực tiếp đem người ta thân thể đều đánh chết."),t("br"),t("br"),n._v("Xuất Khiếu cảnh cường giả, đối thân thể bảo vệ rất tốt, có thể nói là điểm chí mạng, ra ngoài một hồi, đem thứ này đánh phế... Đừng nói là đầu này Dị Linh tộc vương giả, liền xem như nhóm người mình, như thế thê, khẳng định cũng sẽ làm chuyện điên rồ ah!"),t("br"),t("br"),n._v("“Ta cũng không chú ý, không cẩn thận... Không cẩn thận đánh!”"),t("br"),t("br"),n._v("Thấy mọi người gặp quỷ đồng dạng biểu lộ, Trương Huyền vội vàng giải thích."),t("br"),t("br"),n._v("Nếu là hắn có thể tại ước thúc một chút, phân thân chắc chắn sẽ không như thế lỗ mãng, cái này thật xem như gây ra đại hoạ."),t("br"),t("br"),n._v("“Không cẩn thận...”"),t("br"),t("br"),n._v("Mọi người kìm nén sắc mặt đỏ lên, đều hận không thể cuồng quất kẻ trước mắt này một bữa."),t("br"),t("br"),n._v("Ngươi một cái Tòng Thánh, không cẩn thận làm hỏng Thánh vực ngũ trọng cường giả thân thể... Liền đủ giật, vẫn là không cẩn thận làm!"),t("br"),n._v("Một khi cẩn thận một chút, Khổng sư sợ rằng cũng phải bị ngươi tươi sống chơi đùa chết."),t("br"),t("br"),n._v("“Trước đừng quản nhiều như vậy, nhanh ổn định trận pháp, nếu không, chúng ta đều sẽ chết ở chỗ này...”"),t("br"),t("br"),n._v("Hít sâu vài khẩu khí, hóa giải trong lồng ngực buồn bực, Hàn hội trưởng vẻ mặt nghiêm túc mà nói."),t("br"),t("br"),n._v("Hắn là Thanh Nguyên đế quốc trận pháp sư công hội hội trưởng, đối với trận pháp lý giải cực sâu, biết trước mắt cái này, mặc dù tạm thời có thể chặn lại đối phương, muốn kéo dài vẫn là rất khó, nhất định phải toàn viên điều động."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Mọi người cũng biết bây giờ không phải là truy cứu những này thời điểm, không nói thêm lời, đồng loạt đi tới sau lưng, nghe theo phân phó."),t("br"),t("br"),n._v("“Ngô sư, nhưng càn vị trông coi, Lạc sư đi chấn vị...”"),t("br"),t("br"),n._v("Hàn hội trưởng vội vàng phân phó."),t("br"),t("br"),n._v("Hắn đi tới liền chuyên môn nhìn trận pháp này, biết yếu kém vị trí ở đâu, Trương Huyền thấy hắn an bài không có sai lầm chút nào, cũng không nói thêm lời, vội vàng đi tới vị trí chỉ định đứng vững."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mọi người chân khí tuôn ra, trận pháp quả nhiên vững chắc không ít, lại không trước đó lay động."),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Cảm nhận được trận pháp biến hóa, phía ngoài Thiên Diệp Vương biết cho dù đem hết toàn lực, trong thời gian ngắn cũng phá giải không được, lần nữa gào thét: “Đã các ngươi làm rùa đen rút đầu, ta liền đem cái kia tượng đá đánh nát!”"),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Tiếng rống kết thúc, mọi người nhất thời cảm thấy trên thân buông lỏng, trận pháp áp lực lập tức biến mất, hình như Thiên Diệp Vương đi."),t("br"),t("br"),n._v("“Hắn muốn đi đối phó viện trưởng, làm sao bây giờ?”"),t("br"),t("br"),n._v("Đi ra trận pháp, nhìn một cái, quả nhiên phát hiện Thiên Diệp Vương đã rời đi, Kê Nham đám người tràn đầy cuống cuồng."),t("br"),t("br"),n._v("Cái tên này nói muốn đi đánh nát tượng đá, tự nhiên chỉ là Phong Thánh đài trước mặt lão viện trưởng."),t("br"),t("br"),n._v("Hiện tại hắn hồn phách bị khóa ở bên trong, trốn không thoát cũng ra không được, một khi bị đánh nát, cũng chỉ có một con đường chết!"),t("br"),t("br"),n._v("Cứ việc giờ phút này hắn chỉ là cái người chết sống lại, nhưng có rõ ràng ý thức, có thể tự chủ đối thoại nói chuyện phiếm, cũng không thể trơ mắt nhìn xem, cứ như vậy bị giết."),t("br"),t("br"),n._v("“Còn có thể làm sao? Cứu người!” Ngô sư cắn răng."),t("br"),t("br"),n._v("“Nhưng... Chúng ta căn bản ngăn cản không nổi Thiên Diệp Vương!”"),t("br"),t("br"),n._v("Đàm Thanh vội vàng nói."),t("br"),t("br"),n._v("Thiên Diệp Vương quá mạnh, bọn họ nhiều người như vậy liên thủ, đều ngăn cản không nổi, tiến lên, không những không thể đem người cứu, làm không cẩn thận còn có thể toàn quân bị diệt."),t("br"),t("br"),n._v("“Cái kia còn có thể làm sao? Trơ mắt nhìn đồng bạn chết tại trước mặt, nhưng không có bất kỳ động tác gì... Ta làm không được! Danh sư, chưa từng bỏ qua bằng hữu!”"),t("br"),t("br"),n._v("Ngô sư nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Chúng ta cũng làm không được... Nhưng có biện pháp gì?” Đàm Thanh lắc đầu: “Viện trưởng là vì cứu ta, mới thành như thế, nếu như chúng ta lại vì cứu hắn rơi vào nguy cơ, hắn cho dù chết, khẳng định cũng sẽ không tha thứ chúng ta!”"),t("br"),t("br"),n._v("Đi theo Chương Dẫn Khâu viện trưởng sau lưng nhiều năm, biết tính tình của hắn cùng bản tính."),t("br"),t("br"),n._v("Một đời chưa lập gia đình, đem tất cả tinh lực đều hiến tặng cho học viện, hiến tặng cho danh sư chức nghiệp, nhóm người mình tiến lên cứu người lời nói, một khi xuất hiện tổn hại, cho dù cứu, đối phương cũng tất nhiên sẽ tràn đầy tự trách mình, tràn đầy hối hận."),t("br"),t("br"),n._v("“Chẳng lẽ không có cái gì biện pháp tốt ư?” Lạc Nhược Hi nhịn không được nói."),t("br"),t("br"),n._v("“Phương pháp là có, chính là ta trước đó nói như vậy... Có người có thể tại Phong Thánh đài đột phá! Một khi như vậy, mượn nhờ Phong Thánh đài lực lượng, trong thời gian ngắn, sẽ xuất hiện vô địch trạng thái... Thiên Diệp Vương mặc dù mạnh mẽ, cũng khẳng định không phải là đối thủ!”"),t("br"),t("br"),n._v("Kê Nham nói."),t("br"),t("br"),n._v("“Vô địch?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Không sai, hai năm này chúng ta cũng một mực quan sát lầu một bia đá, căn cứ Khổng sư lưu lại ghi chép, một khi thành công, sẽ trời giáng thánh khí, đến lúc đó, Phong Thánh đài toả ra ánh sáng chói lọi, ẩn chứa trong đó lực lượng, sẽ truyền bá toàn thân, ẩn chứa thiên địa uy nghiêm, cửu tinh danh sư đều ngăn cản không nổi!”"),t("br"),t("br"),n._v("Kê Nham gật đầu."),t("br"),t("br"),n._v("Hai năm này bị vây ở cái này, loại trừ cùng Thiên Diệp Vương đấu trí đấu dũng, bọn họ cũng một mực nghiên cứu, làm sao trốn đi, đối Phong Thánh đài ghi chép biết đến rất kỹ càng."),t("br"),t("br"),n._v("Hiểu muốn ra ngoài, chỉ có hai cái phương pháp: Thứ nhất, nhận được Khưu Ngô cổ thánh truyền thừa. Thứ hai, Phong Thánh đài phong Thánh!"),t("br"),t("br"),n._v("Hiện tại cái thứ nhất đã không trông cậy vào, cổ thánh ý niệm đều bị đả kích tán loạn, cho dù muốn truyền thừa, cũng không thể nào làm được... Nói cách khác, muốn rời khỏi, giải trừ thời khắc này nguy hiểm, trừ phi cái thứ hai!"),t("br"),t("br"),n._v("“Đây là cái phương pháp, thật ra thì cùng không nói đồng dạng... Phong Thánh? Nào có dễ dàng như vậy!” Ngô sư lắc đầu."),t("br"),t("br"),n._v("Lời còn chưa dứt, chỉ thấy cách đó không xa thanh niên đi lên phía trước, trong mắt mang theo kiên định: “Như vậy đi, các ngươi đi qua nghĩ biện pháp ngăn chặn Thiên Diệp Vương, để hắn đừng đối lão viện trưởng ra tay, ta đi Phong Thánh đài, đột phá Thánh vực, tiến hành phong Thánh!”"),t("br"),t("br"),n._v("Hiện tại Thất Thải Địa Quỳnh dịch đã tới tay, còn kém một bước cuối cùng có thể đột phá."),t("br"),t("br"),n._v("Một khi đột phá, nhờ vào hắn tích lũy, cùng đối Phong Thánh giải lý giải, tám chín phần mười có thể làm tràng phong Thánh!"),t("br"),t("br"),n._v("Nhìn tới, hiện nay chỉ có loại này một loại phương pháp đi cứu lão viện trưởng lưu lại hồn phách, đi ngăn cản Thiên Diệp Vương."),t("br"),t("br"),n._v("“Phong Thánh... So với ngươi tưởng tượng muốn khó, không phải nói phong liền phong, chuyện này tốt nhất đừng đi suy nghĩ...”"),t("br"),t("br"),n._v("Thấy cái tên này lần nữa nhấc lên việc này, Kê Nham nhướng mày, trước đó đã nói một lần, mọi người mới quen, chưa khỏi ý tứ quát tháo, kết quả lại tới... Ngươi cho rằng phong Thánh là mua cải trắng a? Tùy tiện một cái chợ bán thức ăn đều có thể tìm tới?"),t("br"),t("br"),n._v("Thật sự là không biết tự lượng sức mình!"),t("br"),t("br"),n._v("Hừ một tiếng, đang nghĩ nói tiếp, liền cảm thấy thanh niên trước mắt thân thể nhoáng một cái, một cỗ đặc thù khí tức, tuôn trào ra."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Cỗ khí tức này, để toàn thân hắn tản mát ra ôn nhuận ánh sáng, phụ trợ tựa như thần chỉ."),t("br"),t("br"),n._v("“Đây là... Đây là... Trời nhận danh sư?”"),t("br"),t("br"),n._v("Cảm nhận được cỗ khí tức này, Kê Nham lời nói thoáng cái câm tại trong miệng, con ngươi co rụt lại, thân thể cứng ngắc."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1088"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1090"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);