(window.webpackJsonp=window.webpackJsonp||[]).push([[1140],{1495:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1997-chan-tien-canh-thien-đao-cong-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1997-chan-tien-canh-thien-đao-cong-phap"}},[n._v("#")]),n._v(" Chương 1997: Chân Tiên cảnh thiên đạo công pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chân Tiên cảnh thiên đạo công pháp!"),t("br"),t("br"),n._v("Tuy là không biết trước mắt thứ này thuộc tính, nhưng hắn đối Thánh thú, tiên thú các loại hiểu rõ rất nhiều."),t("br"),t("br"),n._v("Tảng đá kia đã có “Thần huyết” Hai chữ, nhất định cùng Thần Linh huyết dịch liên quan đến, cường đại tới đâu tiên thú gặp lên, chỉ sợ đều muốn nhượng bộ lui binh."),t("br"),t("br"),n._v("Loại phương pháp này, còn là từ Lăng Vân kiếm các truyền tới."),t("br"),t("br"),n._v("Trong đó Kiếm Thần đường, lợi hại hơn nữa tiên thú, cũng không dám tới gần."),t("br"),t("br"),n._v("Chính là bởi vì, trong đó có khai phái tổ sư lưu lại “Kiếm thần” Hai chữ."),t("br"),t("br"),n._v("“Cái này... Ta hiện tại liền đi tìm!”"),t("br"),t("br"),n._v("Phương pháp không khó, Hồ phường chủ cùng Hạc lão nhìn nhau, cái trước gật gật đầu, vội vã rời đi."),t("br"),t("br"),n._v("Hắn rất muốn xác định, đến cùng phải hay không Thần Huyết thạch."),t("br"),t("br"),n._v("Nếu như là thật, lần này coi như thua thiệt lớn, cũng không dám đấu giá bán."),t("br"),t("br"),n._v("Dù sao, tiền cùng mạng so ra, người sau Rõ ràng quan trọng hơn một ít."),t("br"),t("br"),n._v("Không đến nửa canh giờ, Hồ phường chủ cầm mấy cái bình ngọc lần nữa đi tới."),t("br"),t("br"),n._v("“Mấy cái này đều là Tiên Quân cảnh cấp bậc tiên thú huyết dịch...”"),t("br"),t("br"),n._v("Tiên Quân cảnh tiên thú, số lượng không nhiều, tìm tới một đầu, hơn nữa mang tới rất khó, nhưng số ít huyết dịch, cũng không phức tạp."),t("br"),t("br"),n._v("Tiên thú huyết dịch ẩn chứa năng lượng to lớn, đối tăng cao tu vi, cũng có rất lớn trợ giúp, chỉ cần nguyện ý bỏ tiền, Thông Thần điện, còn là rất dễ dàng có thể đem thứ này mua được."),t("br"),t("br"),n._v("“Bình này là Mặc Hổ tiên thú huyết dịch, trưởng thành đi đến Tiên Quân đỉnh phong...”"),t("br"),t("br"),n._v("Cầm qua một cái bình ngọc, đem một giọt máu hướng trước mắt tảng đá đổ tới."),t("br"),t("br"),n._v("Mặc dù chỉ là một giọt máu, nhưng ẩn chứa nồng đậm linh lực, hoàng kim đồng dạng nặng nề, lăng không nhỏ xuống, ép không khí phát ra vù vù vang vọng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Mắt thấy huyết dịch liền muốn rơi vào trước mắt trên tảng đá, đột nhiên nhẹ nhàng run rẩy, bay đến một bên, giống như là thấy được cái gì để hắn sợ hãi đồ vật đồng dạng, trốn ở một bên, phát ra nghẹn ngào thanh âm."),t("br"),t("br"),n._v("Hồ phường chủ da mặt cứng đờ."),t("br"),t("br"),n._v("Nếu như nói trước đó còn có điều nghi ngờ, giờ phút này trong lòng đã tin tưởng bảy, tám phần."),t("br"),t("br"),n._v("Hắn mua sắm mấy loại huyết dịch, đầu này Mặc Hổ tiên thú cường đại nhất, cái khác huyết dịch gặp lên, đều run lẩy bẩy không dám trước mắt, nhưng bây giờ, còn chưa tới đến tảng đá phía trước, liền bộ dáng này..."),t("br"),t("br"),n._v("Chứng minh trong viên đá nhất định tỏa ra một loại để hắn kính sợ lực lượng!"),t("br"),t("br"),n._v("Loại trừ... Thần Linh, thật đúng là không tưởng tượng ra được!"),t("br"),t("br"),n._v("Đem còn lại trong bình ngọc huyết dịch nhỏ đi lên, tất cả đều cùng cái này giống nhau như đúc, sợ hãi không dám trước mắt."),t("br"),t("br"),n._v("“Quả nhiên là Thần Huyết thạch...”"),t("br"),t("br"),n._v("Trong lòng tại không thể nghi ngờ hoặc, nhìn về phía thanh niên trước mắt, tràn đầy kính sợ."),t("br"),t("br"),n._v("Cái gì công cụ cũng không cần, liếc mắt liền nhìn ra cùng thần huyết liên quan đến, đối phương nhãn lực, quả thực mạnh mẽ đáng sợ."),t("br"),t("br"),n._v("“Thứ này... Ngươi có biết, lúc nào đạt được, vị trí cụ thể, lại tại nơi nào?”"),t("br"),t("br"),n._v("Trương Huyền không nói thêm lời, lúc này cũng dẹp loạn kích động trong lòng, nhìn lại."),t("br"),t("br"),n._v("“Ta lúc mua, người kia chỉ nói, Trục Tinh chi hải, cái khác cũng không tỉ mỉ tiết lộ...” Hồ phường chủ lắc đầu."),t("br"),t("br"),n._v("“Cái kia... Hướng ngươi bán ra tảng đá này người, bây giờ còn có thể tìm tới ư? Ta có chút nghi vấn muốn hỏi thăm!” Trương Huyền nói."),t("br"),t("br"),n._v("“Ta thử một chút!”"),t("br"),t("br"),n._v("Lên tiếng, Hồ phường chủ lấy ra đưa tin ngọc bài, cho đối phương đưa tin, đợi một hồi, cũng không khôi phục tin tức, đành phải bất đắc dĩ lắc đầu: “Hắn đem tảng đá bán cho ta, liền biến mất, hẳn là... Sẽ liên lạc lại không lên!”"),t("br"),t("br"),n._v("Trương Huyền than thở."),t("br"),t("br"),n._v("Còn tưởng rằng, có thể tìm tới đối phương, hỏi thăm một cái, tìm tới thứ này vị trí cụ thể, dễ chịu đi tự thân thăm dò, hiện tại xem ra, không thể nào."),t("br"),t("br"),n._v("Lại hỏi thăm vài câu, cũng không đạt được đầu mối hữu dụng, Trương Huyền kìm lòng không được có chút thất lạc."),t("br"),t("br"),n._v("“Cái kia... Ngươi cũng đã biết, cái này tảng đá, đối phương đạt được, đại khái thời gian dài bao lâu?”"),t("br"),t("br"),n._v("Cuối cùng hỏi."),t("br"),t("br"),n._v("“Ta mua lại, tiếp cận một tháng, Lúc đó hắn nói, đạt được hơn một tháng... Nói cách khác, cái này tảng đá, đến trong tay hắn, đến bây giờ, đã có hai tháng thời gian!”"),t("br"),t("br"),n._v("“Hai tháng?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền nắm đấm lần nữa xiết chặt."),t("br"),t("br"),n._v("Lạc Nhược Hi, tại danh sư đại lục, đợi thời gian hơn một năm."),t("br"),t("br"),n._v("Nàng rời đi sau, bản thân lại đợi ba bốn tháng, mới đi vào Thượng Thương."),t("br"),t("br"),n._v("Dựa theo Thượng Thương cùng danh sư đại lục mười so một thời gian quan hệ, một năm rưỡi, hơn năm trăm trời, cũng liền tương đương với Thượng Thương hơn năm mươi trời... Đi tới Thượng Thương cũng qua hơn mười ngày, tính toán đâu ra đấy cộng lại, vừa vặn hơn hai tháng!"),t("br"),t("br"),n._v("Thoạt nhìn..."),t("br"),t("br"),n._v("Thứ này, vô cùng có khả năng cùng hắn liên quan đến."),t("br"),t("br"),n._v("Đương nhiên, phải hay không phải, còn cần tự mình đi cái gọi là Trục Tinh chi hải nhìn một chút."),t("br"),t("br"),n._v("Rõ ràng những này, thở ra một hơi, Trương Huyền tiếp tục hỏi: “Hồ phường chủ, ngươi nơi này nhưng có Chân Tiên cảnh tu luyện công pháp? Số lượng càng nhiều càng tốt, mượn ta lật xem một cái? Ta chỉ nhìn liếc mắt liền có thể, sẽ không sao chép, cũng sẽ không mang đi!”"),t("br"),t("br"),n._v("Tới hỗ trợ giám định bảo vật mục đích, liền sẽ vì cái này, mà muốn đi Trục Tinh chi hải, tìm kiếm Lạc Nhược Hi tung tích... Tu vi mười phần quan trọng!"),t("br"),t("br"),n._v("Chỉ có đi đến Chân Tiên, mới coi như có năng lực tự vệ."),t("br"),t("br"),n._v("“Chân Tiên cảnh tu luyện công pháp?”"),t("br"),t("br"),n._v("Thấy hắn đưa ra yêu cầu này, Hồ phường chủ dừng lại một chút, gật gật đầu: “Thứ này, ta chỗ này có một ít, đều là phòng đấu giá bán ra ghi chép phần chuẩn bị... Nếu như Trương huynh muốn nhìn, vui vẻ cũng không kịp!”"),t("br"),t("br"),n._v("Trước mắt vị này, liền như vậy ít thấy đồ vật đều có thể nhận ra, giám định thuật mạnh mẽ, làm người ta líu lưỡi."),t("br"),t("br"),n._v("Nếu như có thể lôi kéo đến bọn họ thương hội, về sau bất kỳ bảo vật, cũng sẽ không xuất hiện chỗ sơ suất!"),t("br"),t("br"),n._v("Bởi vậy, đối phương nói chuyện, lập tức đáp ứng."),t("br"),t("br"),n._v("Huống chi, đối phương chỉ là nhìn một chút cũng không phải là sao chép... Cũng vui vẻ nhân tình."),t("br"),t("br"),n._v("“Đa tạ phường chủ!”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, Trương Huyền thở phào nhẹ nhõm, ngay sau đó nhìn về phía cách đó không xa tảng đá: “Cái này Thần Huyết thạch, dính dáng Thần Linh, phường chủ tốt nhất vẫn là không được đấu giá, tìm đặc thù trận pháp phong tồn lên, tuyệt đối không nên để tin tức tiết lộ! Nếu như về sau, ta xác thực biết luyện chế phương pháp hoặc là phương pháp sử dụng, sẽ cùng phường chủ nói tỉ mỉ.”"),t("br"),n._v(" “Làm phiền Trương huynh...”"),t("br"),t("br"),n._v("Hồ phường chủ liền vội vàng gật đầu."),t("br"),t("br"),n._v("Thật ra thì không cần đối phương nói, hắn cũng đã sớm quyết định."),t("br"),t("br"),n._v("Dính dáng Thần Linh đồ vật, cho hắn mười cái lá gan, cũng không dám tiết lộ mảy may."),t("br"),t("br"),n._v("Tuy là tu vi đạt đến nửa bước Tiên Quân, nhưng cùng Thần điện cường giả so... Kém không biết bao nhiêu, căn bản không tại cùng một cái cấp bậc."),t("br"),t("br"),n._v("Mấu chốt nhất là... Thần điện coi như mặc kệ, sáu đại tông môn cường giả, biết tin tức, khẳng định cũng sẽ tới."),t("br"),t("br"),n._v("Hắn chỉ là cái thương nhân, đối diện với mấy cái này, khẳng định chống lại không được."),t("br"),t("br"),n._v("Cùng hắn phiền phức, không bằng đem tin tức đè xuống, cái gì đều không nói."),t("br"),t("br"),n._v("Sắp xếp người đem Thần Huyết thạch thả lên, Hồ phường chủ mang theo Trương Huyền, đi tới một cái phòng."),t("br"),t("br"),n._v("Bên trong quả nhiên rậm rạp chằng chịt bày không biết bao nhiêu thư tịch, số lượng tuy là so ra kém trưởng lão điện, nhưng cũng rất nhiều."),t("br"),t("br"),n._v("“Chỉ có hơn bảy mươi bản...”"),t("br"),t("br"),n._v("Rất nhanh thu nhận trong óc, Trương Huyền lần nữa lắc đầu."),t("br"),t("br"),n._v("Thư tịch rất nhiều, nhưng đi đến Chân Tiên cảnh, chỉ có hơn bảy mươi bản."),t("br"),t("br"),n._v("Hơn sáu trăm bản cũng còn còn lại bảy chỗ thiếu hụt, coi như nhiều hơn nữa bảy mươi tới bản, khả năng tác dụng cũng sẽ không quá lớn."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Một tiếng hô khẽ, thư viện lay động, cái này hơn bảy mươi bản cùng trước đó sưu tập hơn sáu trăm bản dung hợp lại cùng nhau."),t("br"),t("br"),n._v("Không ôm hy vọng quá lớn đem thư tịch cầm lấy, liếc mắt nhìn, con mắt kìm lòng không được sáng lên."),t("br"),t("br"),n._v("Trước đó còn thừa lại bảy chỗ thiếu hụt, vậy mà toàn bộ bổ sung xong xuôi, trong tay Chân Tiên cảnh Thiên đạo công pháp, vậy mà hoàn mỹ vô khuyết, lại không sơ hở!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền có chút không dám tin tưởng."),t("br"),t("br"),n._v("Vốn nghĩ, hơn bảy mươi bản, có thể bổ sung một hai nơi là tốt lắm rồi, toàn bộ bổ sung xong xuôi, đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Vội vàng trong đầu, đem vừa mới thu nhận hơn bảy mươi bản đi ngủ lật xem một lần, lập tức bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Phòng đấu giá sưu tập những này, cứ việc không so kiếm các mạnh mẽ, nhưng... Không ít đều là thưa thớt, kỳ quái pháp quyết, loại này, dùng chính là chênh lệch tả đạo, đi cũng không phải là bình thường tu luyện con đường."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới có thể bán ra giá cao, để rất nhiều tu luyện giả vây đỡ."),t("br"),t("br"),n._v("Kiếm các hơn sáu trăm bản, thiên hướng chính thống, cái này hơn bảy mươi bản, kiếm tẩu thiên phong... Cả hai bổ sung, Thiên đạo công pháp, cũng liền triệt để hình thành."),t("br"),t("br"),n._v("“Không biết Trương huynh, phải xem phương diện nào thư tịch, ta có thể lấy đi ra...”"),t("br"),t("br"),n._v("Hồ phường chủ cười nói."),t("br"),t("br"),n._v("“Ta đã xem xong, đa tạ!”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Xem xong?”"),t("br"),t("br"),n._v("Hồ phường chủ ngẩn ngơ."),t("br"),t("br"),n._v("Hai người vừa mới đi vào, trước mắt thanh niên này, cũng chỉ là nhìn lướt qua trước mặt thư tịch..."),t("br"),t("br"),n._v("Liền giống như, mang đối phương đi một ít không thể miêu tả nơi, bên này quần còn không có cởi ra, bên kia liền đứng dậy, nói cho người khác biết, đã tốt..."),t("br"),t("br"),n._v("Quá nhanh đi!"),t("br"),t("br"),n._v("Ngươi xác định là tới chơi đùa nghịch... A, không, là đến xem sách?"),t("br"),t("br"),n._v("Có muốn hay không nhanh như vậy?"),t("br"),t("br"),n._v("“Ta chỉ là có chút trên việc tu luyện nghi vấn khó xử lý, không nghĩ thông, muốn tới đây tìm kiếm thư tịch, nói bóng nói gió một cái, nhìn một chút có thể hay không dung hội quán thông... Nhìn thấy ngươi những sách này, phát hiện cùng ta đều không thích hợp, cùng hắn nhìn, nhiễu loạn đầu mối tư tưởng, còn không bằng không nhìn!”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("“Cái này... Cũng đối, ta những công pháp này cùng kiếm các chính thống pháp quyết, có không ít khác biệt.”"),t("br"),t("br"),n._v("Nghe được lý do này, Hồ phường chủ gật đầu."),t("br"),t("br"),n._v("Tu luyện, cũng không phải là học càng nhiều càng tốt, tham thì thâm, không ít nhìn sách nhiều, ngược lại không cách nào phân biệt thật giả, cuối cùng dẫn đến tẩu hỏa nhập ma."),t("br"),t("br"),n._v("“Trương huynh, lần này nhờ có ngươi phân biệt ra Thần Huyết thạch, để cho ta miễn trừ một hồi tai hoạ, chỉ là một điểm tâm ý, mong rằng không được từ chối...”"),t("br"),t("br"),n._v("Rời phòng, Hồ phường chủ đưa tới một túi thông thần tệ, xem ra chừng trên trăm."),t("br"),t("br"),n._v("Một hơi lấy ra nhiều như vậy, vì lôi kéo Trương Huyền, cũng coi như bỏ hết cả tiền vốn."),t("br"),t("br"),n._v("“Phường chủ khách tức giận, ta cũng là vì hoàn thành nhiệm vụ...”"),t("br"),t("br"),n._v("Khoát tay áo, Trương Huyền cũng không tiếp thu."),t("br"),t("br"),n._v("Quân tử yêu tiền, ăn cướp liền tốt."),t("br"),t("br"),n._v("Điểm ấy thông thần tệ, còn chướng mắt."),t("br"),t("br"),n._v("Đương nhiên, chủ yếu là không muốn cùng đối phương lo lắng quá sâu."),t("br"),t("br"),n._v("“Cáo từ, về sau có cơ hội gặp lại!”"),t("br"),t("br"),n._v("Thoái thác đối phương thịnh tình chiêu đãi, Trương Huyền xoay người rời đi thương hội."),t("br"),t("br"),n._v("Lần này tới mục đích đúng là tìm kiếm công pháp, tiếp cận Tề Chân tiên cảnh Thiên đạo pháp quyết, hiện tại hoàn thành, nên trở về đi tìm tới Bạch Nguyễn Khanh, đồng thời mau chóng tăng cao tu vi!"),t("br"),t("br"),n._v("Chỉ cần đi đến Chân Tiên cảnh đỉnh phong, phối hợp đồng váy kiếm, Tiên Quân cường giả đỉnh phong đều có thể một trận chiến, lại đi Trục Tinh chi hải, tìm kiếm Lạc Nhược Hi tung tích, cũng không cần phải lo lắng."),t("br"),t("br"),n._v("Rời đi thương hội lúc này mới phát hiện, trời bên ngoài đã toàn bộ màu đen, Nguyệt Lượng huyền phù, Tinh Thần muốn ngã, bốn phía yên tâm tĩnh mịch."),t("br"),t("br"),n._v("Dọc theo đường phố, hướng Thông Thần điện đi tới, mới đi không có mấy bước, đồng tử đột nhiên co rụt lại."),t("br"),t("br"),n._v("“Người nào?”"),t("br"),t("br"),n._v("Vội vàng quay đầu, ngay sau đó nhìn thấy trước mắt nguyên bản bằng phẳng đường phố, một hồi vặn vẹo, tất cả kiến trúc, giống như là trong nháy mắt biến mất đồng dạng, xuất hiện tại một mảnh vùng bỏ hoang, ngay sau đó, vài bóng người, bất ngờ xuất hiện tại phía trước."),t("br"),t("br"),n._v("(Tấu chương xong)."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2010"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2012"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);