(window.webpackJsonp=window.webpackJsonp||[]).push([[1145],{1500:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2002-lang-van-than-kiem-quyet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2002-lang-van-than-kiem-quyet"}},[n._v("#")]),n._v(" Chương 2002: Lăng Vân thần kiếm quyết")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Lăng Vân kiếm thần tuyệt"),t("br"),t("br"),n._v("Hắn thực sự nói thật."),t("br"),t("br"),n._v("Vốn đi theo là muốn bảo vệ vị này Trương Huyền an toàn, kết quả, ba vị Chân Tiên cảnh đỉnh phong người áo đen, đối phương một mình giết chết một vị, thừa dịp cùng mình chiến đấu, đâm chết một vị, còn lại một cái, cũng là bị hắn trường kiếm cứ thế mà chặt chết."),t("br"),t("br"),n._v("Còn Tiên Quân cảnh cường giả, càng là bị kẻ trước mắt này, một sách đập chết."),t("br"),t("br"),n._v("Cẩn thận nói đến, thật, không có giúp quá lớn vội vàng."),t("br"),t("br"),n._v("Nhiều nhất chính là lôi kéo kỳ quái, hấp dẫn bộ phận thù hận."),t("br"),t("br"),n._v("Đương nhiên, trang bị cũng không có cầm lên, đều bị đối phương lấy đi."),t("br"),t("br"),n._v("Vừa rồi một mực khiếp sợ, đối phương tốc độ tiến bộ, thực sự quá nhanh, làm sao... Cái này thời gian một cái nháy mắt, lại từ sơ kỳ biến đỉnh phong?"),t("br"),t("br"),n._v("“Trên đường tới, cảm giác thực lực của mình quá thấp, liền ăn một chút Tiên Nguyên đan, đột phá!”"),t("br"),t("br"),n._v("Thấy đem tu vi thật sự triển lộ ra, đối phương sợ đến như vậy, Trương Huyền giải thích."),t("br"),t("br"),n._v("Nếu như vừa rồi liền đi đến Chân Tiên cảnh đỉnh phong, cái kia ba vị Thần điện võ sĩ, liên hợp cùng một chỗ, cũng chưa chắc là đối thủ của mình, cũng sẽ không cần phiền phức như vậy."),t("br"),t("br"),n._v("Cho nên, vì bản thân bảo vệ, trên đường tới, không ngừng nuốt Tiên Nguyên đan, có Thiên đạo công pháp, lại thêm vừa rồi chiến đấu rèn luyện, thời gian không dài, liền từ sơ kỳ đạt đến đỉnh phong."),t("br"),t("br"),n._v("Trước đó không có bày ra, giờ phút này đi tới trưởng lão điện, đã an toàn, cho rằng không cần thiết tiếp tục giấu diếm, không nghĩ tới, làm cho đối phương kinh ngạc như vậy."),t("br"),t("br"),n._v("“Ăn một chút... Tiên Nguyên đan, đã đột phá?”"),t("br"),t("br"),n._v("Không chỉ Cừu Hỏa trưởng lão phát điên, trước mặt Hàn Kiếm Thu, Hà Thiên trưởng lão mấy người cũng lắc lư một cái, suýt chút nữa không có hộc máu."),t("br"),t("br"),n._v("Trước đó, còn cho rằng đối phương không cố gắng tu luyện, có chút mất hứng, biết đối phương một ngày công phu, tấn cấp tiếp cận hai cái đại cấp bậc, khiếp sợ sắp đã hôn mê, cái này cũng chưa hết... Mới ra ngoài bao lâu thời gian? Hai canh giờ? Ba canh giờ?"),t("br"),t("br"),n._v("Liền Chân Tiên cũng đột phá, còn đi đến đỉnh phong?"),t("br"),t("br"),n._v("Mấu chốt là đối phương thái độ..."),t("br"),t("br"),n._v("Ăn một chút Tiên Nguyên đan, đã đột phá!"),t("br"),t("br"),n._v("Tới tới tới, ta chỗ này còn có một chậu, ta ăn, vì cái gì một điểm động tĩnh đều không có?"),t("br"),t("br"),n._v("“Không đúng, ngươi mới vừa nói... Trương Huyền trưởng lão, mượn Đồng Thường kiếm... Kiếm này hắn luyện hóa?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, nghĩ tới càng thêm khó tin sự tình, Hàn tông chủ lần nữa nhìn tới."),t("br"),t("br"),n._v("Đồng Thường kiếm là hắn được bảo vật, tính cách có bao nhiêu cương quyết, biết rõ ràng nhất."),t("br"),t("br"),n._v("Hắn muốn luyện hóa, đều cần tiêu phí cực lớn công phu, ngại phiền phức mới không có đi làm, đối phương cầm tới tay thời điểm, mới là cái Hư Tiên, liền thành công?"),t("br"),t("br"),n._v("Quan trọng... Mới bao lâu?"),t("br"),t("br"),n._v("“Ừm, ta gặp được thời điểm, cũng đã luyện hóa...” Cừu Hỏa trưởng lão cười khổ gật đầu."),t("br"),t("br"),n._v("Đi theo cái tên này sau lưng, tất cả tự tin, đều sẽ bị đả kích, không còn tồn tại."),t("br"),t("br"),n._v("Rất nhanh, đem vừa rồi tự mình trải qua sự tình, tỉ mỉ nói một lần."),t("br"),t("br"),n._v("Nghe được Thần điện vị kia Tiên Quân cường giả, một chỉ liền đánh bại Cừu Hỏa trưởng lão, tất cả mọi người khiếp sợ tột đỉnh, được nghe lại bị trước mắt vị này, một sách đập chết, tất cả đều nói không ra lời."),t("br"),t("br"),n._v("“Cái này... Thư tịch, là lão sư ta cho ta hộ thân pháp bảo, sử dụng hết liền không có...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thư viện sự tình, không có cách nào giải thích, chỉ có thể lần nữa cho mình làm cái cũng không tồn tại “Lão sư”."),t("br"),t("br"),n._v("Dù sao Lăng Vân kiếm các, cũng không truy cứu môn hạ đệ tử, học tập môn phái khác công pháp."),t("br"),t("br"),n._v("Mọi người từng cái lộ ra nghiêm mặt."),t("br"),t("br"),n._v("Trước đó, một mực kỳ lạ, trước mắt vị thanh niên này, vì sao chỉ có hai mươi tuổi, liền nắm giữ tu vi như thế cùng đúng kiếm đạo lý giải, nghe được có lão sư, đồng thời thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Khẳng định có siêu cấp cường giả bồi dưỡng, mới nắm giữ loại này bản lĩnh."),t("br"),t("br"),n._v("Không có sư thừa, ai cũng không tin."),t("br"),t("br"),n._v("“Thần điện từ trước tới nay bá đạo, chẳng qua... Bình thường cũng sẽ đợi đến tu luyện giả, đi đến Tiên Quân cảnh, mới có thể tới, một cái Hư Tiên liền phái ra Tiên Quân, Chân Tiên tới, thậm chí còn phong tỏa không gian... Liền xem như ta, trước đó đều không có nghe qua!”"),t("br"),t("br"),n._v("Giải thích xong, Trương Huyền lại đem trên đường hỏi thăm sự tình, hỏi thăm trước mắt Hàn tông chủ, người sau cũng mày nhăn lại, tràn đầy nghi ngờ."),t("br"),t("br"),n._v("“Càng quan trọng hơn là, biết thân phận của ngươi, đều tại đây cái đại điện, không có người rời đi, đối phương thế nào biết? Thần điện, cho dù cao cao tại thượng, cũng không phải toàn trí toàn năng!”"),t("br"),t("br"),n._v("Lại thương nghị một hồi, xác thực không biết chuyện gì xảy ra, đành phải thôi."),t("br"),t("br"),n._v("“Nếu như... Thần điện phái người tới, kiếm các có thể hay không chống đỡ được?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("Hắn hiện tại dường như quan tâm cái này, ngộ nhỡ kiếm các không ngăn được, lúc này lại không bảo vệ tính mạng Thiên đạo chi sách, đến lúc đó, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("“Hẳn là sẽ không!” Hàn tông chủ lắc đầu: “Chúng ta là bị Thần điện trục xuất, cũng chính là cái gọi là thần khí chi tộc, Thần miếu cao cao tại thượng, lặng lẽ động thủ, ngược lại cũng thôi, quang minh chính đại xông vào tông môn, tất nhiên sẽ dẫn tới tất cả mọi người bắn ngược, đến lúc đó, cho dù Thần miếu mạnh mẽ, cũng không khống chế được!”"),t("br"),t("br"),n._v("“Lại nói... Lăng Vân kiếm các, vô số tiên tổ lực lượng gia trì, cho dù là Thần điện Tiên Quân cường giả, tiến vào bên trong, cũng không cách nào làm mưa làm gió, chỉ có một con đường chết!”"),t("br"),t("br"),n._v("Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("An toàn liền tốt."),t("br"),t("br"),n._v("Suy nghĩ một chút cũng đối, nếu như đối phương thật muốn công khai, cũng sẽ không trước đem bản thân kéo vào không gian đặc thù, lại đi động thủ!"),t("br"),t("br"),n._v("“Không biết... Loại trừ trưởng lão điện, chúng ta nơi này, nhưng còn có Tiên Quân cảnh tu luyện công pháp, càng nhiều càng tốt, ta đúng tu luyện còn có chút không hiểu địa phương, muốn nhìn nhiều nhìn...”"),t("br"),t("br"),n._v("Lại hỏi thăm vài câu, Trương Huyền hỏi."),t("br"),t("br"),n._v("Thực lực, còn là nắm giữ ở trong tay chính mình tốt nhất."),t("br"),t("br"),n._v("Nếu như đi đến Tiên Quân, Thần điện cường giả tới nhiều hơn nữa, cũng không cần lo lắng."),t("br"),t("br"),n._v("Cho nên, việc cấp bách, còn là nghĩ biện pháp đột phá."),t("br"),t("br"),n._v("“Tiên Quân cảnh tu luyện công pháp, đều tại trưởng lão điện...”"),t("br"),t("br"),n._v("Hàn Kiếm Thu tông chủ lắc đầu."),t("br"),t("br"),n._v("Tiên Quân, đã đứng tại Di Khí đại lục đỉnh phong nhất, mỗi một bộ công pháp, đều thưa thớt vô cùng, làm sao có thể có rất nhiều?"),t("br"),t("br"),n._v("“Chỉ có mười mấy bản?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Trưởng lão điện tàng thư khố, hắn đi qua, đặc biệt nhìn qua Tiên Quân cảnh pháp quyết tu luyện, chỉ có hơn mười bản mà thôi, Thiên đạo công pháp, căn bản là không có cách hình thành... Như vậy, còn thế nào tu luyện?"),t("br"),n._v("“Ừm!” Hàn Kiếm Thu gật đầu: “Loại trừ một bộ tông chủ mới có tư cách tu luyện Lăng Vân kiếm thần quyết, cái khác đều tại tàng thư khố!”"),t("br"),t("br"),n._v("“Lăng Vân kiếm thần quyết?” Trương Huyền ánh mắt sáng lên: “Ta... Nhưng có tư cách học tập?”"),t("br"),t("br"),n._v("Hàn tông chủ nói “Ngươi lĩnh ngộ Thần chi kiếm ý, chính là kiếm các tương lai tông chủ, tự nhiên có tư cách! Chẳng qua, bộ công pháp này, không được bí tịch... Muốn học tập, chỉ có tự mình cảm thụ, đi theo ta!”"),t("br"),t("br"),n._v("Theo sau lưng, đi ra đại điện."),t("br"),t("br"),n._v("Hiện tại tăng thực lực lên, mới là vương đạo, cái khác đều là giả."),t("br"),t("br"),n._v("Rất mau tới đến một cái phòng kín mít, lấy ra tông chủ mới có lệnh bài, mở ra phong cấm, tiến vào bên trong."),t("br"),t("br"),n._v("Gian phòng bên trong, là một hàng tượng, đếm, chừng mười hai cái, mỗi một cái đều cầm trong tay trường kiếm, thi triển ra khác biệt kiếm chiêu."),t("br"),t("br"),n._v("“Đây là Lăng Vân kiếm thần quyết?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Là, bộ này kiếm thần quyết, là lĩnh ngộ Thần chi kiếm ý quan trọng, công pháp cũng ẩn chứa trong đó!” Hàn Kiếm Thu nói."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, trước mắt tượng, hình dáng khác nhau, động tác toàn bộ không giống nhau, đã không có ghi chú tiên lực vận chuyển tình huống, cũng không có lực lượng hướng đi, thoạt nhìn cùng công pháp không có bất cứ quan hệ nào."),t("br"),t("br"),n._v("“Cái này mười hai cái, đều là kiếm chiêu, nhưng mỗi một chiêu đều hết sức kỳ quái, vi phạm với thân thể học cấu tạo, chỉ cần nghiêm ngặt dựa theo những động tác này tiến hành tu luyện, trong cơ thể sẽ tự nhiên hình thành tiên lực vận chuyển đường đi... Mà cái này, không riêng gì kiếm các, cao nhất Thần Tiên Quân đột phá pháp quyết, càng là đột phá Ngụy Thần quan trọng!”"),t("br"),t("br"),n._v("Hàn Kiếm Thu giải thích."),t("br"),t("br"),n._v("Trương Huyền nhìn kỹ lại, quả nhiên thấy những kiếm chiêu này động tác mười phần cổ quái."),t("br"),t("br"),n._v("Mỗi một cái, đều giống như vi phạm với thân thể bản năng uốn lượn, hiện ra khó tin khúc độ."),t("br"),t("br"),n._v("“Ngươi có thể từ cái này chiêu thứ nhất bắt đầu thí nghiệm, một chiêu so một chiêu khó, Lúc trước ta vì hoàn thành chiêu thứ nhất, hao tốn trọn ba ngày thời gian!” Hàn Kiếm Thu tông chủ cười khổ."),t("br"),t("br"),n._v("“Ba ngày?”"),t("br"),t("br"),n._v("Trương Huyền nhìn về phía cái động tác thứ nhất, sống lưng sau bẻ, trường kiếm từ trên đầu đâm ra, hết sức không được tự nhiên."),t("br"),t("br"),n._v("“Đúng vậy a, đây đã là đơn giản nhất, cái thứ hai động tác, ta dùng mười ngày, cái thứ ba, dùng một tháng... Trước mắt mười hai cái tượng, ta toàn bộ học xong, dùng trọn mười lăm năm!”"),t("br"),t("br"),n._v("Hàn Kiếm Thu tông chủ giải thích."),t("br"),t("br"),n._v("Lăng Vân kiếm các cao thâm nhất công pháp, năm đó khai phái tổ sư lưu lại ảo diệu võ học, hắn có thể mười lăm năm đi học xong, xem như cực nhanh, vô cùng có thiên phú."),t("br"),t("br"),n._v("Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("“Không nên xem thường cái tốc độ này, các đời kiếm các tông chủ bên trong, mười lăm năm liền có thể toàn bộ luyện qua, ta tuyệt đối hàng tiến lên mười...”"),t("br"),t("br"),n._v("Thấy hắn biểu lộ không đúng, Hàn Kiếm Thu nghiêm mặt nói."),t("br"),t("br"),n._v("Có thể trở thành một tông chi chủ, thiên phú tự nhiên không kém, nếu không, cũng không có khả năng thắng được những người khác, đi tới nơi này!"),t("br"),t("br"),n._v("Mười lăm năm, nghe rất chậm, nhưng đối với tu luyện Lăng Vân kiếm thần quyết tới nói, đã được cho cực nhanh."),t("br"),t("br"),n._v("“Có đúng không?”"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, Trương Huyền cổ tay khẽ đảo, Đồng Thường kiếm xuất hiện tại lòng bàn tay, sống lưng uốn cong, một đạo kiếm khí, từ mũi kiếm lan ra mà ra, động tác cùng trước mắt cái thứ nhất tượng, giống nhau như đúc."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Hàn Kiếm Thu lông mày nhảy dựng."),t("br"),t("br"),n._v("Vừa nói xong học được ba ngày mới hoàn thành, đối phương một cái sử dụng ra... Để hắn có loại linh cảm không lành."),t("br"),t("br"),n._v("“Chiêu thứ nhất tương đối đơn giản... Ngươi lĩnh ngộ Thần chi kiếm ý, thi triển đi ra không khó lắm, phía sau hẳn là liền không dễ dàng như vậy...”"),t("br"),t("br"),n._v("Tràn đầy xấu hổ, vội vàng nói."),t("br"),t("br"),n._v("“Ta thử một chút...”"),t("br"),t("br"),n._v("Trương Huyền thân thể chuyển một cái, thân thể lần nữa vặn vẹo thành một cái khó tin khúc độ, chiêu thứ hai hoàn thành!"),t("br"),t("br"),n._v("Sau đó, chiêu thứ ba!"),t("br"),t("br"),n._v("Chiêu thứ tư!"),t("br"),t("br"),n._v("Chiêu thứ năm!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thời gian nháy mắt, liền đi tới chiêu thứ mười."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Hàn Kiếm Thu thật cho rằng muốn điên rồi."),t("br"),t("br"),n._v("Đối phương hoàn thành chiêu thứ nhất, có thể nói là chiêu này đơn giản, còn lại chiêu số, một hơi làm được, cái quỷ gì?"),t("br"),t("br"),n._v("Chẳng lẽ trước đó liền luyện qua?"),t("br"),t("br"),n._v("Tuyệt không có khả năng này!"),t("br"),t("br"),n._v("Nơi này loại trừ tông chủ, tuyệt đối không có những người khác tới qua."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Chính khiếp sợ nói không ra lời, chỉ thấy thanh niên trước mắt, đem còn lại hai cái động tác cũng thi triển xong, tràn đầy nghi ngờ nhìn qua: “Cái này rất đơn giản ah... Ngươi thật tu luyện mười lăm năm?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Hàn Kiếm Thu hận không thể có đất khe hở chui vào."),t("br"),t("br"),n._v("Bản thân hao tốn mười lăm năm, còn ba hoa chích choè, dương dương tự đắc, cho rằng xếp hạng mười vị trí đầu... Kết quả đối phương chỉ dùng không đến mười lăm cái hô hấp..."),t("br"),t("br"),n._v("Người so với người, tức chết người!"),t("br"),t("br"),n._v("Chính không biết như thế nào cho phải, liền nghe đến thanh niên trước mắt, tràn đầy giọng nghi ngờ tiếp tục vang lên."),t("br"),t("br"),n._v("“Chẳng qua... Ngươi tu luyện mười lăm năm, chẳng lẽ không có phát hiện, những kiếm chiêu này trình tự là sai?”"),t("br"),t("br"),n._v("“Sai?”"),t("br"),t("br"),n._v("Hàn Kiếm Thu ngẩn ở tại chỗ."),t("br"),t("br"),n._v("(Tấu chương xong)."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2015"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2017"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);