(window.webpackJsonp=window.webpackJsonp||[]).push([[1146],{1501:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2003-phi-hanh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2003-phi-hanh"}},[n._v("#")]),n._v(" Chương 2003: Phi hành")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Mười hai chiêu kiếm pháp, dựa theo độ khó theo thứ tự tăng lên, lúc tu luyện, chân khí trong cơ thể vận chuyển phương hướng cũng liền miên không ngừng, chưa từng xuất hiện khó chịu loại hình, mấu chốt nhất, tu luyện sau đó, đối kiếm đạo lý giải cùng tu vi, thực sự đột nhiên tăng mạnh, trình tự... Làm sao sẽ sai?"),i("br"),i("br"),n._v("“Chiêu thứ nhất cùng chiêu thứ bảy, đổi một chút lời nói, sẽ để cho chân khí hội tụ tốc độ càng nhanh, chiêu thứ tám cùng chiêu thứ mười một đổi một chút, có thể để cho kiếm ý càng thêm mạnh mẽ! Chiêu thứ sáu cùng chiêu thứ ba đổi, lại càng dễ để tinh thần chuyên tâm...”"),i("br"),i("br"),n._v("Trương Huyền tiện tay chỉ đi."),i("br"),i("br"),n._v("Tràn đầy nghi ngờ, Hàn Kiếm Thu dựa theo đối phương nói tới, thay đổi tu luyện trình tự."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Trong cơ thể lực lượng sôi trào, trước đó không cách nào tu luyện tới địa phương, thời gian nháy mắt dung hội quán thông, đối kiếm ý lĩnh ngộ, cũng lập tức càng hơn một bậc, tựa như lúc nào cũng sẽ xé rách không gian."),i("br"),i("br"),n._v("“Đúng thật...”"),i("br"),i("br"),n._v("Ngốc tại chỗ, Hàn Kiếm Thu dừng lại, triệt để bối rối."),i("br"),i("br"),n._v("Tu luyện bộ công pháp này, vượt qua tám mươi năm, nhưng lại chưa bao giờ nghĩ tới, trình tự sẽ không đúng, càng không nghĩ tới, đổi một chút, liền sẽ nắm giữ lợi hại như thế công hiệu, thậm chí, để hắn giam giữ lại thật lâu lực lượng, đều có chút buông lỏng!"),i("br"),i("br"),n._v("Cứ việc những này buông lỏng mười phần nhỏ yếu, nhưng kèm theo thời gian trôi qua, khẳng định sẽ càng lúc càng lớn, cuối cùng hình thành biến chất, cùng trước mắt vị này đồng dạng, lĩnh ngộ Thần chi kiếm ý, cũng không phải là không có khả năng!"),i("br"),i("br"),n._v("Sau khi hết khiếp sợ, lần nữa nhìn về phía cách đó không xa thanh niên, trong mắt hiện ra nồng đậm khó tin."),i("br"),i("br"),n._v("Hắn tiêu phí 15 năm, mới học được kiếm chiêu, đối phương một cái nháy mắt liền luyện thành... Mấu chốt nhất, còn có thể nhìn ra trong đó sai lầm cùng sơ hở..."),i("br"),i("br"),n._v("Cái này... Không phải người a?"),i("br"),i("br"),n._v("Trước đó còn muốn lấy, đối phương lĩnh ngộ Thần chi kiếm ý, cùng vận khí liên quan đến, hiện tại xem ra, cùng vận khí không có nửa xu quan hệ, mà là đối phương thiên tư, quá mạnh mẽ!"),i("br"),i("br"),n._v("Thậm chí... Đã có thể cùng vị kia Thông Thần điện điện chủ, cùng so sánh!"),i("br"),i("br"),n._v("Truyền thuyết, vài ngàn năm trước, Thông Thần điện điện chủ, ngang trời xuất hiện, ngắn ngủi mấy tháng không đến thời gian, liền quét ngang thiên hạ, không người có thể địch, càng là lẻ loi một mình xông vào Thần điện, cướp đoạt một chữ..."),i("br"),i("br"),n._v("Loại này bản lĩnh, mấy ngàn năm chỉ này một người, không nghĩ tới, vị thứ hai tại bọn họ tông môn xuất hiện!"),i("br"),i("br"),n._v("“Như vậy trải qua... Thông Thiên kiều mở ra, sáu đại tông môn thiên tài hội tụ, chúng ta Lăng Vân kiếm các, nhất định có thể rút đến thứ nhất, một kiếm trấn áp mặt khác...”"),i("br"),i("br"),n._v("Trong mắt lóe ra vẻ hưng phấn, Hàn Kiếm Thu tông chủ nắm đấm xiết chặt."),i("br"),i("br"),n._v("Trước kia, Thông Thiên kiều mở ra, bọn họ Lăng Vân kiếm các, cứ việc nắm giữ nửa cái Thần chữ, nhận hạn chế thiên tài không đủ cường đại, thủy chung không cách nào thống soái toàn cục, giờ phút này có vị này, nhất định có thể một tiếng hót lên làm kinh người, chân chính nắm giữ quyền nói chuyện!"),i("br"),i("br"),n._v("Tràn đầy hưng phấn, nhìn về phía cách đó không xa thanh niên, chỉ thấy hắn yên tĩnh đứng tại chỗ, dường như có cảm giác ngộ."),i("br"),i("br"),n._v("Biết loại này cảm ngộ có thể ngộ nhưng không thể cầu, Hàn Kiếm Thu không nói thêm gì nữa, xoay người lui ra ngoài."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Cùng hắn đoán đồng dạng, Trương Huyền quả thực tại cảm ngộ."),i("br"),i("br"),n._v("Bộ này Lăng Vân Kiếm Thần quyết, đối với hắn đột phá Tiên Quân không có quá lớn trợ giúp, nhưng lại để hắn có thể càng thêm dễ dàng khống chế trước đó lĩnh ngộ Thần chi kiếm ý."),i("br"),i("br"),n._v("Thu đồ Thiện Hiểu Thiên hình thành Thiên đạo chi sách, ngăn lại xông tới đạo kiếm ý kia, để hắn đối Thần chi kiếm ý, có hoàn mỹ lý giải, nhưng cho tới nay, bị giới hạn thực lực, cùng lực lượng linh hồn, đạo kiếm ý này, đều không thể thi triển ra toàn bộ uy lực."),i("br"),i("br"),n._v("Giờ phút này, đem trước mắt mười hai cái động tác nối liền cùng một chỗ, đạo kiếm ý kia, thế mà đột phá Thiên đạo chi sách phong cấm, cùng thân thể hoàn mỹ dung hợp."),i("br"),i("br"),n._v("Nói cách khác, đạo kiếm ý này, triệt để dung hợp tại trong cơ thể, thành hắn một bộ phận, dung hội quán thông, có thể tùy ý thi triển, lại không nhận ràng buộc."),i("br"),i("br"),n._v("“Nguyên lai bộ kiếm pháp kia, mới là mở ra Thần chi kiếm ý chìa khoá...”"),i("br"),i("br"),n._v("Cảm nhận được thân thể biến hóa, Trương Huyền giật mình."),i("br"),i("br"),n._v("Cùng cái kia ba vị Chân Tiên cảnh người áo đen thời điểm chiến đấu, Thần chi kiếm ý, một mực không cách nào thi triển xong cả, mới có hơi bị động, một khi có thể hoàn mỹ thi triển, cho dù không cần đột phá đến Chân Tiên, cũng có thể đem hắn chém giết."),i("br"),i("br"),n._v("Cô cô cô cục cục!"),i("br"),i("br"),n._v("Hòa tan vào thân thể, kiếm ý ở trong người chảy xuôi, cả người tựa hồ cũng trở nên sắc bén, thật giống bất cứ lúc nào cũng sẽ đâm thủng bầu trời."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Kiếm ý như sóng lớn, Trương Huyền trong tích tắc, hóa thân thành một thanh trường kiếm, từ tại chỗ lơ lửng."),i("br"),i("br"),n._v("“Có thể bay đi...”"),i("br"),i("br"),n._v("Mắt sáng lên, Trương Huyền tràn đầy kích động."),i("br"),i("br"),n._v("Thượng Thương không gian áp lực, so danh sư đại lục lớn không biết bao nhiêu, chỉ có đi đến Tiên Quân cấp bậc mới có thể phi hành!"),i("br"),i("br"),n._v("Hắn cứ việc chỉ là Chân Tiên cảnh đỉnh phong, có thể mượn giúp trong cơ thể Thần chi kiếm ý, hoàn toàn có thể hóa thân trường kiếm, tung hoành ngang dọc, tốc độ thậm chí so Cừu Hỏa trưởng lão đều muốn mau hơn không ít!"),i("br"),i("br"),n._v("“Có thể phi hành, lại thêm tu vi đi đến Chân Tiên cảnh đỉnh phong, hoàn toàn khống chế Thần chi kiếm ý... Tiên Quân đỉnh phong, cũng có thể một trận chiến! Trước đó vị kia Tiên Quân cảnh người áo đen, muốn lưu lại ta, cũng không dễ dàng như vậy...”"),i("br"),i("br"),n._v("Cảm nhận được trong cơ thể lực lượng biến hóa, Trương Huyền thở phào nhẹ nhõm."),i("br"),i("br"),n._v("Tuy là không có sưu tập thành Tiên quân cảnh Thiên đạo công pháp, nhưng cái này mười hai cái động tác, để hắn đối Thần chi kiếm ý, có càng sâu hiểu rõ, sức chiến đấu lần nữa bạo tăng."),i("br"),i("br"),n._v("Trước đó, để hắn kính sợ Bạch Diệp trưởng lão đám người, chân chính đối chiến, cũng chưa chắc có thể là đối thủ."),i("br"),i("br"),n._v("“Đúng rồi, hồ lô, ngươi nuốt những cái kia trường kiếm đây? Không cho ta lưu hai thanh?”"),i("br"),i("br"),n._v("Đem suy nghĩ từ tu vi bên trên khôi phục lại, vội vàng nhìn về phía trong đan điền hồ lô."),i("br"),i("br"),n._v("Bản thân để cái tên này, thôn phệ đối thủ trường kiếm, giải quyết lúc đó nguy cục... Nó vừa ra tay, liền Cừu Hỏa trưởng lão đều không có bỏ qua... Vừa rồi tình cảnh nguy cấp, không kịp hỏi kỹ, vừa vặn hiện tại có thời gian, vừa vặn nhìn một chút đến cùng thế nào rồi."),i("br"),i("br"),n._v("Hồ lô lộ ra cảnh giác chi ý: “Ngươi không phải nói, không để cho ta phun ra ư? Chẳng lẽ muốn đổi ý?”"),i("br"),i("br"),n._v("“Dĩ nhiên không phải, chỉ là muốn hỏi một chút, nuốt nhiều như vậy, no không ăn không tiêu...” Trương Huyền vẻ mặt không hồng."),i("br"),i("br"),n._v("“Không no, vừa tiêu hóa xong...” Hồ lô giãy dụa một chút."),i("br"),i("br"),n._v("“Tiêu hóa xong?”"),i("br"),i("br"),n._v("Lông mày nhảy dựng, Trương Huyền vội vàng nhìn lại, quả nhiên thấy hồ lô lần nữa lười biếng nằm trong đan điền, mảy may đều không có chống đỡ hình dáng."),i("br"),i("br"),n._v("“Được rồi...”"),i("br"),i("br"),n._v("Biết bị đối phương nuốt vào, muốn đòi về, đã không thể nào, Trương Huyền đầy mặt im lặng cổ tay khẽ đảo, mấy cái thi thể xuất hiện tại phía trước."),i("br"),i("br"),n._v("Chính là trước đó vây giết hắn cái kia Tiên Quân, cùng ba vị Chân Tiên cảnh đỉnh phong."),i("br"),i("br"),n._v("Vù!"),i("br"),n._v("Thi thể mới vừa xuất hiện, một thanh trường kiếm trực tiếp đối Trương Huyền mi tâm đâm tới."),i("br"),i("br"),n._v("Chính là vị kia Tiên Quân người áo đen sử dụng Tiên Quân cấp bậc vũ khí, chủ nhân bị một sách đánh chết, không có phản ứng lại, liền bị thu vào trữ vật giới chỉ, giờ phút này gặp kẻ thù xuất hiện, cũng lại kìm nén không được."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Duỗi ra ngón tay, nhẹ nhàng bóp một cái."),i("br"),i("br"),n._v("Trường kiếm lập tức bị bóp tại đầu ngón tay."),i("br"),i("br"),n._v("Tiên Quân cảnh cấp bậc vũ khí, Hư Tiên cảnh lúc, không thể làm gì, hiện tại Chân Tiên đỉnh phong, càng đem Thần chi kiếm ý triệt để lĩnh ngộ, chân chính Tiên Quân đỉnh phong cũng chưa chắc là đối thủ, một thanh kiếm mà thôi, không thành vấn đề."),i("br"),i("br"),n._v("“Ngươi, ngươi...”"),i("br"),i("br"),n._v("Trường kiếm dường như khó tin."),i("br"),i("br"),n._v("Cũng liền không đến nửa giờ, cái tên này, làm sao một chút mạnh mẽ nhiều như vậy?"),i("br"),i("br"),n._v("Không thèm để ý đối phương, nắm lại trường kiếm, Trương Huyền nhìn đúng mấy chỗ, bấm đốt ngón tay gảy ra."),i("br"),i("br"),n._v("Vù vù!"),i("br"),i("br"),n._v("Gặp Đạo kiếm ý, lập tức dọc theo thân kiếm tràn vào trường kiếm trong cơ thể, trường kiếm liền phát ra một tiếng nghẹn ngào tiếng khóc, ngay sau đó linh tính tiêu tán."),i("br"),i("br"),n._v("Thanh trường kiếm này linh tính, là bị đối phương luyện hóa, coi như thuần phục, cũng có khả năng phản bội, hắn cũng không muốn tại cùng người thời điểm chiến đấu, binh khí thuộc về mình, quay giáo một đòn, đem bản thân giết chết."),i("br"),i("br"),n._v("Cho nên, vì để tránh cho phiền phức, trực tiếp liền đem linh tính, dùng thần chi kiếm ý mẫn diệt."),i("br"),i("br"),n._v("“Ngươi... Làm như vậy, chuôi kiếm này, chẳng khác nào phế...”"),i("br"),i("br"),n._v("Nhìn thấy cử động của hắn, vừa rồi lấy ra Đồng Thường kiếm, giật nảy mình, không ngừng run rẩy."),i("br"),i("br"),n._v("Nó cùng thực lực của đối phương không kém bao nhiêu, một cái nháy mắt, chủ nhân liền đem nó linh tính ma diệt, chẳng phải cho thấy, bản thân cũng sẽ bị trực tiếp đánh giết?"),i("br"),i("br"),n._v("Chỉ là..."),i("br"),i("br"),n._v("Không còn linh tính, kiếm chẳng khác nào không còn hồn phách, nhiều nhất chỉ là phẩm chất cứng rắn một ít, rất nhiều kiếm pháp đều không thể thi triển, lại không còn Tiên Quân cảnh binh khí uy lực."),i("br"),i("br"),n._v("“Yên tâm, sẽ không phế...”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền ngón tay lần nữa tại trên thân kiếm nhẹ nhàng bắn ra, hai cái hô hấp sau đó, trường kiếm bỗng nhiên nhảy dựng, lần nữa phát ra thanh minh."),i("br"),i("br"),n._v("Lần này là tựa như chim tước ca hát, mang theo hưng phấn."),i("br"),i("br"),n._v("“Lần nữa có linh tính?”"),i("br"),i("br"),n._v("Đồng Thường kiếm run rẩy, suýt chút nữa từ không trung rơi xuống."),i("br"),i("br"),n._v("Một thanh kiếm linh tính, cần không biết bao nhiêu năm ôn dưỡng, mới có thể xuất hiện, đối phương ngón tay một vệt, hủy diệt trước đó, lần nữa một vệt, liền xuất hiện mới..."),i("br"),i("br"),n._v("Không ngừng run rẩy, Đồng Thường kiếm không dám nói nhiều nửa câu."),i("br"),i("br"),n._v("Trước đó, nó có ngạo khí, là bởi vì, kiếm, không còn nó, chẳng khác nào phế, chỉ cần là luyện kiếm người, không có khả năng đoạn tuyệt đường lui."),i("br"),i("br"),n._v("Nhìn thấy cử động của đối phương, mới hiểu được..."),i("br"),i("br"),n._v("Cái này nha, không thể dùng người bình thường phương thức, để cân nhắc ah!"),i("br"),i("br"),n._v("Tiện tay xoá bỏ linh tính... Quan trọng, xóa xong, còn có thể làm ra một cái tới..."),i("br"),i("br"),n._v("Nói cách khác, nó kiêu ngạo nhất dựa dẫm, ở trước mặt đối phương, cái gì cũng không bằng..."),i("br"),i("br"),n._v("Xem ra sau này, ngàn vạn không thể trang bức, nếu không... Chết cũng không biết chết như thế nào."),i("br"),i("br"),n._v("Giờ này khắc này, Đồng Thường kiếm hoàn toàn thần phục, cũng không dám có đặc thù tâm tư."),i("br"),i("br"),n._v("“Từ hôm nay trở đi, ngươi gọi... Trường kiếm!”"),i("br"),i("br"),n._v("Nhẹ nhõm luyện hóa vừa mới mở ra linh tính, Trương Huyền thoả mãn nhìn binh khí trong tay."),i("br"),i("br"),n._v("Cùng Đồng Thường kiếm đồng dạng, cũng là một thanh đi đến Tiên Quân đỉnh phong cấp bậc vũ khí."),i("br"),i("br"),n._v("“Trường kiếm?”"),i("br"),i("br"),n._v("Run rẩy một chút, trường kiếm suýt chút nữa không có từ không trung rơi xuống."),i("br"),i("br"),n._v("Có muốn hay không như vậy tùy ý?"),i("br"),i("br"),n._v("Tốt xấu cũng là Tiên Quân cảnh cấp bậc vũ khí, toàn bộ đại lục, đều phải kể đến siêu cấp cường giả..."),i("br"),i("br"),n._v("Liền lấy cái danh tự như vậy?"),i("br"),i("br"),n._v("“Chủ nhân, có muốn hay không... Đổi lại tên? Cái này... Thực sự quá tùy ý!”"),i("br"),i("br"),n._v("Thực sự nhịn không được, trường kiếm nói."),i("br"),i("br"),n._v("“Tùy ý? Cái này...”"),i("br"),i("br"),n._v("Trương Huyền nhíu nhíu mày: “Nếu không để... Tiểu kiếm kiếm đi!”"),i("br"),i("br"),n._v("“Phù phù!”"),i("br"),i("br"),n._v("Trường kiếm không có ổn định, rơi trên mặt đất, khóc ra thành tiếng."),i("br"),i("br"),n._v("Ma đản, còn không bằng tên thứ nhất êm tai đây..."),i("br"),i("br"),n._v("Bất quá, lúc này cũng rõ ràng, trước mắt chủ nhân, là cái lấy tên cặn bã, nói nhiều hơn nữa cũng vô dụng, đành phải rưng rưng nhịn xuống."),i("br"),i("br"),n._v("“Nhìn một chút đối phương trong giới chỉ, có cái gì tốt đồ vật...”"),i("br"),i("br"),n._v("Đem trường kiếm thu vào trữ vật giới chỉ, Trương Huyền không tiếp tục để ý, mà là ngẩng đầu nhìn về phía cách đó không xa bốn cái thi thể."),i("br"),i("br"),n._v("Đối phương là Thần điện cường giả, hơn nữa tu vi đều mạnh như vậy, trữ vật giới chỉ bên trong, sẽ có hay không có bảo vật gì?"),i("br"),i("br"),n._v("Ngón tay ngoắc ngoắc, bốn chiếc nhẫn lập tức bay tới, rơi vào lòng bàn tay của hắn."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2016"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2018"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);