(window.webpackJsonp=window.webpackJsonp||[]).push([[1148],{1503:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2005-truong-huyen-lam-tong-chu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2005-truong-huyen-lam-tong-chu"}},[n._v("#")]),n._v(" Chương 2005: Trương Huyền làm tông chủ")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Quá nguy hiểm...”"),i("br"),i("br"),n._v("Hàn Kiếm Thu sốt ruột."),i("br"),i("br"),n._v("Trương Huyền nói: “Ta lĩnh ngộ Thần chi kiếm ý, sớm muộn đều sẽ trở thành kiếm các tông chủ, hướng đi càng xa, một mực ẩn nấp tại các ngươi che chở bên dưới, như thế nào mới có thể trưởng thành? Huống chi, luyện kiếm người, muốn có khí thế một đi không trở lại, bởi vì sợ hãi, mà rụt rè, kiếm ý cũng duy trì không được!”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Hàn Kiếm Thu yên lặng."),i("br"),i("br"),n._v("Đối phương nói không sai."),i("br"),i("br"),n._v("Kiếm ý, đại biểu một cái luyện kiếm người tinh, khí, thần, ý chí vị trí, mũi kiếm nhất định đến thẳng!"),i("br"),i("br"),n._v("Một khi làm bất cứ chuyện gì, bó tay bó chân, mất đi tinh thần, mạnh mẽ kiếm ý, đồng dạng sẽ ảm đạm xuống."),i("br"),i("br"),n._v("Lăng Vân kiếm các, liền xuất hiện qua loại này ví dụ, một ngàn năm trước, một vị tông chủ, kiếm ý thông huyền, thiên hạ hiếm có đối thủ, kết quả, một chuyện nhỏ, giết lầm hảo hữu chí giao, cực độ tự trách mình bên dưới, đối tu luyện sinh ra nghi ngờ, cuối cùng dẫn đến trong cơ thể kiếm ý sụp đổ, tu vi giảm nhiều, cuối cùng buồn bực sầu não mà chết."),i("br"),i("br"),n._v("Trước mắt vị này, lĩnh ngộ là Thần chi kiếm ý, khẳng định có lấy khí thế một đi không trở lại, thiên hạ không ai cản nổi quyết tâm, không thể có bất kỳ sợ hãi cùng ràng buộc."),i("br"),i("br"),n._v("Sát người bảo vệ, là vì an toàn của hắn, chỉ khi nào bị hư phần này kiên trì, ngược lại được không bù mất."),i("br"),i("br"),n._v("“Cái kia... Ta đem trong tông môn tốc độ nhanh nhất phi hành tiên thú cho ngươi!”"),i("br"),i("br"),n._v("Không tại nhiều nói, Hàn Kiếm Thu nói."),i("br"),i("br"),n._v("“Không cần, làm như vậy, đồng dạng làm cho người chú ý!”"),i("br"),i("br"),n._v("  Chần chờ một chút, Trương Huyền hỏi: “Không biết, lân cận nơi nào có am hiểu phi hành tiên thú ẩn hiện? Không được, ta đi thuần phục một đầu!”"),i("br"),i("br"),n._v("Thần điện cường giả, không biết ẩn giấu ở nơi nào, lúc này ngồi tông môn nhanh nhất phi hành tiên thú đi ra, không phải là trở thành bia sống?"),i("br"),i("br"),n._v("Còn không bằng thay đổi hình dáng, lặng lẽ rời khỏi, tuỳ ý tìm địa phương thuần phục một đầu, đồng dạng tốc độ không chậm."),i("br"),i("br"),n._v("“Thuần phục?” Hàn Kiếm Thu lông mày nhảy dựng: “Tiên thú bình thường đều mười phần cương quyết, thuần thú, khả năng cần tiêu phí thời gian rất lâu, không tưởng tượng dễ dàng như vậy...”"),i("br"),i("br"),n._v("Lăng Vân kiếm các nuôi nhốt thú sủng, cơ bản đều là từ Vạn Thú môn mua sắm tới, bọn họ chỉ am hiểu kiếm pháp, đối với thuần thú, hầu như chưa hề tiếp xúc qua."),i("br"),i("br"),n._v("Bất quá, dù vậy, cũng biết độ khó."),i("br"),i("br"),n._v("Rất nhiều am hiểu thuần thú Vạn Thú môn đệ tử, thuần phục một đầu, đều cần tiêu phí không biết bao lâu, huống chi trước mắt vị này."),i("br"),i("br"),n._v("Tuy ngồi mài đao cũng không làm mất kỹ thuật đốn củi, có thể... Tiền đề muốn có đao đi mài ah!"),i("br"),i("br"),n._v("Trước đi đào quặng sắt, nghĩ biện pháp luyện chế búa, cuối cùng mới đi đốn củi... Đoán chừng đã sớm chết đói!"),i("br"),i("br"),n._v("“Ta biết, chỉ là đi thử xem!” Trương Huyền mỉm cười, cũng không giải thích."),i("br"),i("br"),n._v("“Cái này... Nếu ngươi làm quyết định, ta cũng không ngăn trở, đây là ta luyện chế phòng thân ngọc phù, nguy hiểm lúc, chân khí kích hoạt, có thể hình thành phòng ngự màng ánh sáng, chống lại Ngụy Thần cảnh cường giả một đòn!”"),i("br"),i("br"),n._v("Thấy đối phương ý chí kiên quyết, Hàn Kiếm Thu bất đắc dĩ lắc đầu, lấy ra một viên ngọc bài đưa tới."),i("br"),i("br"),n._v("“Đa tạ...”"),i("br"),i("br"),n._v("Biết là bảo vệ tính mạng bảo vật, Trương Huyền cũng không từ chối, đưa tay nhận lấy."),i("br"),i("br"),n._v("“Ngươi vừa rồi hỏi thăm phi hành tiên thú, khoảng cách cái này năm ngàn cây số Lăng Vân trên núi, ở vài đầu Tiên Quân cấp bậc, cũng có Chân Tiên cảnh... Vạn Thú môn từng mấy lần phái người đi thuần phục, đều không có thành công, tóm lại... Ngươi thật muốn đi qua nhìn một chút, nhất định cẩn thận!”"),i("br"),i("br"),n._v("Hàn Kiếm Thu tiếp tục bàn giao."),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Cẩn thận hỏi thăm một chút Trục Tinh cung vị trí phương hướng, đối phương từng cái trả lời."),i("br"),i("br"),n._v("Đem muốn biết, hỏi rõ, Trương Huyền lại không ở lâu, trở lại Thiện Hiểu Thiên vị trí sân nhỏ."),i("br"),i("br"),n._v("Bạch Nguyễn Khanh lúc này đã nhận được báo tin, từ Ô Hải thành trở lại nơi này."),i("br"),i("br"),n._v("“Hiểu Thiên, ta đã thu Nguyễn Khanh làm đồ đệ, từ hôm nay trở đi, nàng liền là của ngươi sư tỷ!”"),i("br"),i("br"),n._v("“Gặp qua sư tỷ!” Thiện Hiểu Thiên ôm quyền."),i("br"),i("br"),n._v("“Đây là hai người các ngươi tu luyện về sau công pháp và kiếm thuật, có không hiểu hỏi thăm, ta sẽ cho các ngươi giải đáp!”"),i("br"),i("br"),n._v("Trương Huyền cong ngón tay búng một cái, hai cái ngọc bài, rơi vào hai người phía trước."),i("br"),i("br"),n._v("Bên trong ẩn chứa, hai người tu luyện về sau công pháp và phương hướng."),i("br"),i("br"),n._v("Thiện Hiểu Thiên cùng Bạch Nguyễn Khanh, vội vàng nhìn lại, càng xem càng là say mê."),i("br"),i("br"),n._v("Bên trong ghi chép công pháp và kiếm thuật, thực sự quá mức ảo diệu, chỉ cần chăm chú tu luyện, có thể đoán được, thực lực nhất định cấp tốc tiến bộ."),i("br"),i("br"),n._v("Rất mau đem nội dung xem xong, từng người đem không hiểu nghi ngờ hỏi thăm đi ra."),i("br"),i("br"),n._v("Trương Huyền từng cái chỉ điểm."),i("br"),i("br"),n._v("“Đây là các ngươi sau này tu luyện cần có Tiên Nguyên đan, mỗi người hai bình, ta hi vọng, lần sau gặp được, tu vi đều đạt đến ta dự trù!”"),i("br"),i("br"),n._v("Đi vào kiếm các Thông Thần điện, lấy ra hai bình trung phẩm Tiên Nguyên đan cùng hai bình thượng phẩm, phân biệt cho hai cái đệ tử."),i("br"),i("br"),n._v("Tất cả an bài ổn thoả, trời dần dần sáng lên."),i("br"),i("br"),n._v("Bất tri bất giác, đã đi tới Lăng Vân kiếm các, hai ngày hai đêm, thời gian không tính ngắn."),i("br"),i("br"),n._v("Bất quá, tu vi cũng từ phá toái hư không sơ kỳ, tấn cấp đến Chân Tiên cảnh đỉnh phong, càng là lĩnh ngộ Thần chi kiếm ý, luyện hóa hai thanh Tiên Quân cảnh đỉnh phong vũ khí, ba đầu Chân Tiên cảnh đỉnh phong Vô Hồn Kim Nhân... Thu hoạch không nhỏ."),i("br"),i("br"),n._v("“Tuyệt không phụ lòng lão sư mong đợi!”"),i("br"),i("br"),n._v("Biết lão sư muốn ra cửa, Bạch Nguyễn Khanh, Thiện Hiểu Thiên từng cái hốc mắt ửng hồng, đồng thời quỳ gối."),i("br"),i("br"),n._v("Trương Huyền thoả mãn gật đầu."),i("br"),i("br"),n._v("Thượng Thương đối với hắn mà nói, thủy chung chỉ là đi ngang qua."),i("br"),i("br"),n._v("Hắn muốn làm, là tìm kiếm Lạc Nhược Hi."),i("br"),i("br"),n._v("Hai cái này đệ tử, cứ việc cũng coi là thân truyền, lại không có khả năng giống Triệu Nhã đám người như thế, mang theo trên người, cẩn thận dạy dỗ."),i("br"),i("br"),n._v("Còn về sau, cuối cùng có thể đi tới loại tình trạng nào, chỉ có thể dựa vào chính bọn hắn."),i("br"),i("br"),n._v("“Tào Thành Lập, sau khi ta rời đi, chiếu cố thật tốt tiểu thiếu gia cùng tiểu thư, đây là ngươi tu luyện về sau pháp quyết, cùng đan dược! Đừng già nghĩ đến nữ nhân, hi vọng lần sau gặp được, cũng có thể có chỗ tiến bộ, mà không phải, cho ta khoe khoang cái gì mười người chém, trăm người chém!”"),i("br"),i("br"),n._v("Đem sơn tặc Tào Thành Lập hô tới."),i("br"),i("br"),n._v("“Khụ khụ, nói thật... Ta đã Thiên Nhân trảm!” Tào Thành Lập xấu hổ gãi đầu một cái, một lát sau, nghi ngờ nhìn qua: “Đúng rồi, thiếu gia, ngươi bây giờ là...”"),i("br"),n._v("Lông mày nhảy dựng, vẻ mặt tối sầm."),i("br"),i("br"),n._v("Cái tên này thật đúng là được đà lấn tới, hừ lạnh một tiếng, Trương Huyền nói: “Nghe nói, ngươi gần nhất nhảy vào cửa, còn thể thống gì? Về sau cho ta biết, cắt đứt chân chó của ngươi!”"),i("br"),i("br"),n._v("“Không thể nhảy?”"),i("br"),i("br"),n._v("Tào Thành Lập khóc."),i("br"),i("br"),n._v("Chân trái không thể vào cửa, chân phải không thể vào, hiện tại không thể nhảy... Chẳng lẽ, ta ra không được, cũng không cách nào đi vào?"),i("br"),i("br"),n._v("Không thèm để ý cái này hết chuyện để nói gia hỏa, Trương Huyền lại bàn giao vài câu, rời khỏi sân nhỏ, hướng ngoài sơn môn đi tới."),i("br"),i("br"),n._v("Ngày mới mới vừa sáng, cũng đã đi ra sơn môn, biến mất tại mênh mông rừng núi, không thấy tung tích."),i("br"),i("br"),n._v("Hắn vừa biến mất chỉ chốc lát, sau lưng hai cái lão giả bất ngờ xuất hiện, hai mặt nhìn nhau, một mặt mê hoặc."),i("br"),i("br"),n._v("Chính là Hà trưởng lão cùng Cừu Hỏa trưởng lão."),i("br"),i("br"),n._v("Hàn Kiếm Thu tuy là đáp ứng Trương Huyền, để hắn một mình rời khỏi, lại thật không dám làm như vậy, vừa trở về liền an bài hai người này theo tới, lặng lẽ bảo vệ, phòng ngừa Thần điện đánh lén, ai biết, mới đi ra khỏi tông môn liền mất dấu..."),i("br"),i("br"),n._v("“Nhanh lên một chút tìm...”"),i("br"),i("br"),n._v("Phóng lên trời, hai người tới trên không, tìm kiếm khắp nơi."),i("br"),i("br"),n._v("Trống không, đâu còn có Trương Huyền hình bóng."),i("br"),i("br"),n._v("Sau hai canh giờ, hai người đầy mặt xấu hổ trở lại trưởng lão điện."),i("br"),i("br"),n._v("Theo dõi một cái Chân Tiên, mất dấu..."),i("br"),i("br"),n._v("Thật không biết làm sao cùng tông chủ bàn giao."),i("br"),i("br"),n._v("“Không có đuổi theo?”"),i("br"),i("br"),n._v("Thấy hắn hai người trở về, Hàn Kiếm Thu dường như đã sớm biết đồng dạng, cười khổ nói."),i("br"),i("br"),n._v("“Vâng...”"),i("br"),i("br"),n._v("Hà trưởng lão, Cừu Hỏa trưởng lão không dám ngẩng đầu."),i("br"),i("br"),n._v("“Hắn đã đoán ra, các ngươi sẽ theo dõi...”"),i("br"),i("br"),n._v("Hàn Kiếm Thu nói."),i("br"),i("br"),n._v("“Đoán được?”"),i("br"),i("br"),n._v("Hai người tràn đầy nghi ngờ, đồng thời ngẩng đầu."),i("br"),i("br"),n._v("“Ừm, đây là của các ngươi...”"),i("br"),i("br"),n._v("Hàn Kiếm Thu cong ngón tay búng một cái, hai cái ngọc bài xuất hiện tại hai người lòng bàn tay."),i("br"),i("br"),n._v("Chân khí hướng bên trong truyền vào, ngay sau đó Trương Huyền thanh âm nhàn nhạt vang lên: “Hai vị trưởng lão đừng đi theo, ta có thể tự mình bảo vệ mình an toàn! Đây là ta nhằm vào các ngươi kiếm pháp bên trong thiếu hụt, lần nữa cho các ngươi sáng tạo kiếm pháp, dựa theo cái này tu luyện, có thể để cho các ngươi đối kiếm đạo lý giải càng cao thâm hơn, đi càng xa...”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Vội vàng hướng ngọc bài phía trong nhìn lại, rất mau nhìn hết, hai đại trưởng lão, tất cả đều khiếp sợ nói không ra lời."),i("br"),i("br"),n._v("Ngọc bài phía trong kiếm pháp, vừa vặn nhằm vào bọn họ kiếm chiêu thiếu hụt, dựa theo cái này tu luyện, kiếm thuật nhất định có thể lần nữa càng hơn một bậc! Thậm chí tu vi, đều có thể tiến thêm một bước!"),i("br"),i("br"),n._v("“Hắn, hắn làm sao nhìn ra được?”"),i("br"),i("br"),n._v("Con mắt trợn tròn, hai người nói không ra lời."),i("br"),i("br"),n._v("Bọn họ trong tu luyện thiếu hụt, chỉ có bản thân biết, người thân nhất đều không biết, đối phương chỉ cùng bọn hắn tỷ thí qua một lần, liền rõ ràng, viết không kém chút nào không nói, còn toàn bộ tiến hành sửa lại, hoàn mỹ phù hợp bản thân tu luyện pháp quyết..."),i("br"),i("br"),n._v("Cái này muốn đối tu luyện, rất cao thâm lý giải, mới có thể hoàn thành?"),i("br"),i("br"),n._v("Công pháp, kiếm chiêu, hỗ trợ lẫn nhau, rõ ràng chỉ là sửa đổi một chỗ thiếu hụt, có thể chỉ cần hơi có sai lầm, liền có thể mang đến càng lớn tai hoạ."),i("br"),i("br"),n._v("Cho nên, bọn họ biết rõ thiếu hụt mà không đi thay đổi, cũng không phải là không muốn thay đổi, mà là không thể thay đổi!"),i("br"),i("br"),n._v("Đối phương dễ dàng hoàn thành..."),i("br"),i("br"),n._v("“Cái này... Ân tình thực sự quá lớn!”"),i("br"),i("br"),n._v("Hốc mắt đồng thời đỏ lên."),i("br"),i("br"),n._v("Vốn nghĩ đến bảo vệ đối phương, kết quả người ta không những không cần, đưa cho nặng như vậy lễ vật, thực sự xấu hổ tiếp nhận."),i("br"),i("br"),n._v("“Không riêng gì các ngươi, chúng ta mỗi người đều có...” Hàn Kiếm Thu cười khổ, ánh mắt nhìn về phía ngoài điện, dường như muốn truy tìm người thanh niên kia thân ảnh, nhưng thủy chung tìm không đến."),i("br"),i("br"),n._v("Đối phương tựa như giáng lâm thế gian Thần Linh, lắc người một cái, lại biến mất."),i("br"),i("br"),n._v("Hai người lúc này mới phát hiện, tông chủ và các trưởng lão khác trong tay cũng đều từng người có cái ngọc bài, phía trên cũng đều ghi chép liên quan tới bản thân thiếu hụt, cùng thay đổi phương pháp."),i("br"),i("br"),n._v("“Hảo hảo tu luyện, đừng phụ lòng hắn ý tốt!”"),i("br"),i("br"),n._v("Hàn Kiếm Thu đứng dậy: “Lĩnh ngộ Thần chi kiếm ý, một cái nhìn ra chúng ta thiếu hụt, đều trợ giúp thay đổi công pháp... Tin tưởng, Lăng Vân kiếm các, về sau tại dưới sự hướng dẫn của hắn, sẽ càng đi càng xa!”"),i("br"),i("br"),n._v("“Đúng vậy a!”"),i("br"),i("br"),n._v("Mọi người gật đầu, từng cái tràn đầy kích động."),i("br"),i("br"),n._v("Vốn nghĩ, đối phương lĩnh ngộ Thần chi kiếm ý, chỉ là bản thân mạnh mẽ, không nghĩ tới... Không chỉ có bản thân sẽ tu luyện, còn có thể đem lĩnh ngộ đồ vật, truyền thụ đi ra!"),i("br"),i("br"),n._v("Vậy thì đáng sợ!"),i("br"),i("br"),n._v("Một người thiên phú cao, sẽ tu luyện, nhiều nhất là cái dấu hiệu, để cho người ta kiêng kị... Có thể chỉ điểm người khác, tương đương với có thể đem toàn bộ tông môn, dẫn dắt đến càng thêm huy hoàng tình trạng!"),i("br"),i("br"),n._v("“Ta quyết định...”"),i("br"),i("br"),n._v("Thấy mọi người bộ dáng này, Hàn Kiếm Thu cười nhạt một tiếng."),i("br"),i("br"),n._v("“Tông chủ, ngươi... Xác định?”"),i("br"),i("br"),n._v("Biết hắn muốn làm gì, Hà trưởng lão tràn đầy khiếp sợ nhìn qua."),i("br"),i("br"),n._v("“Không sai!” Hàn Kiếm Thu gật đầu: “Trước kia, ta còn lo lắng hắn biết hay không biết quá mức ngang tàng, có muốn hay không rèn luyện một chút, bây giờ thấy, trực tiếp đem bản thân đối kiếm đạo lĩnh ngộ, không giữ lại chút nào truyền thụ cho chúng ta... Đủ để chứng minh nhân phẩm, không thể nghi ngờ!”"),i("br"),i("br"),n._v("Cảm xúc một tiếng, Hàn Kiếm Thu bàn tay nhẹ nhàng vồ một cái, một cái đại biểu chưởng môn thân phận lệnh bài, lập tức lơ lửng giữa không trung."),i("br"),i("br"),n._v("Ngay sau đó hắn thanh âm hùng hậu, thời gian nháy mắt, vang vọng toàn bộ Lăng Vân kiếm các."),i("br"),i("br"),n._v("“Từ hôm nay, Trương Huyền là ta kiếm các tông chủ!”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2018"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2020"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);