(window.webpackJsonp=window.webpackJsonp||[]).push([[1158],{1513:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2014-tan-nguyen-mon-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2014-tan-nguyen-mon-chu"}},[n._v("#")]),n._v(" Chương 2014: Tần Nguyên môn chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Hồ lô thực lực không thể nghi ngờ, hiện tại bất kể như thế nào, trước hết để cho nó phá vỡ phong ấn lại nói."),t("br"),t("br"),n._v("Khổng sư cho viên kia lệnh bài, khẳng định liền không quan tâm hắn sử dụng bao nhiêu."),t("br"),t("br"),n._v("Đã như vậy, thượng phẩm Tiên Nguyên đan coi như thưa thớt, chỉ cần mở miệng, Thông Thần điện nên vẫn như cũ có thể cầm ra được."),t("br"),t("br"),n._v("Đoán chừng cũng chính là phiền toái một chút thôi!"),t("br"),t("br"),n._v("“Bất quá, bây giờ tại Vạn Thú môn bên trong, thông thần ngọc phù cũng không có thể sử dụng...”"),t("br"),t("br"),n._v("Vạn Thú môn có lẽ cùng Lăng Vân kiếm các tương đồng, nắm giữ thuộc về mình mạng máy tính cục bộ, Thông Thần điện thông thần ngọc phù, nên tác dụng không lớn."),t("br"),t("br"),n._v("Lại nói, thực lực của hắn bây giờ, đã đạt đến Chân Tiên cảnh, muốn mua càng nhiều dược liệu, cũng cần sử dụng cảnh giới này thông thần ngọc phù, hiện nay còn không có."),t("br"),t("br"),n._v("Nhìn tới chỉ có thể rời khỏi Vạn Thú môn, tìm lớn một chút thành trấn lại nói."),t("br"),t("br"),n._v("“Cho nên, phải nhanh một chút xem xong sách, rời đi nơi này...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền bên này hỏi thăm hồ lô, Liêu trưởng lão đám người đã đi vào Vạn Thú môn trưởng lão điện."),t("br"),t("br"),n._v("“Môn chủ!”"),t("br"),t("br"),n._v("Bọn họ ngay phía trước, là cái thoạt nhìn chỉ có khoảng bốn mươi tuổi trung niên nhân."),t("br"),t("br"),n._v("Tuy là thoạt nhìn tuổi không lớn lắm, nhưng khí tức trong người, đã rõ ràng xuất hiện già yếu, thật giống bất cứ lúc nào cũng sẽ tán loạn."),t("br"),t("br"),n._v("“Trường Thanh thú thú đan, cứ việc có thể làm cho người thoạt nhìn vĩnh viễn giữ gìn trẻ tuổi, nhưng... Thân thể là thành thật, ta nhận qua trọng thương, tuổi thọ có hại, nhiều nhất chỉ có năm năm tuổi thọ, không thể kiên trì được nữa!”"),t("br"),t("br"),n._v("Lắc đầu, trung niên nhân than thở một tiếng, xoay người nhìn về phía Liêu trưởng lão đám người: “Thế nào? Có kết quả ư? Nhanh như vậy trở về, là Viên Châu thành công, vẫn là Lưu Nhiên?”"),t("br"),t("br"),n._v("Viên Châu chính là lấy ra Bắc Hải Long châu, bị Bạch Nhĩ Hồ trêu đùa Viên trưởng lão, Lưu Nhiên thì là bị giao long trước đó vây công đánh chết vị kia."),t("br"),t("br"),n._v("Nếu không phải tuổi thọ gần tới, cũng không có khả năng gấp gáp như vậy tìm kiếm truyền nhân, thậm chí để môn nhân nằm ở trong nguy hiểm."),t("br"),t("br"),n._v("“Bẩm báo môn chủ... Hai người bọn họ đều không có thành công!”"),t("br"),t("br"),n._v("Liêu trưởng lão hướng về phía trước đem sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Ngươi nói... Vị này gọi Trịnh Dương tán tu, ngắn ngủi hai phút đồng hồ phía trong thuần phục, giao long, Cửu Đầu Hỏa Phượng thú cùng Thất Vĩ Kiếm Xỉ Hổ, ba đầu Tiên Quân cảnh đỉnh phong Tiên thú, mà hắn bản thân tu vi... Chỉ có Chân Tiên cảnh đỉnh phong?”"),t("br"),t("br"),n._v("Trung niên nhân môn chủ, sửng sốt một chút, nhịn không được nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Liêu trưởng lão gật đầu: “Viên trưởng lão, Lưu trưởng lão tự thân xuất mã, chúng ta lại nhiều người như vậy giúp đỡ, đều không có thành công, ngược lại thương vong tiếp cận một nửa... Một khi tin tức tiết lộ, đối Vạn Thú môn, nhất định là cái đả kích trí mạng, cho nên... Ta đem vị này Trịnh Dương mời tới, chỉ cần hắn thừa nhận là chúng ta tông môn trưởng lão, đối với tông môn thanh danh tới nói, chẳng những không có tổn thương, còn có thể bởi vậy đại thịnh!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Gật gật đầu, trung niên nhân đứng dậy: “Ta bởi vì tuổi thọ gần tới, Vạn Thú môn, tại sáu đại trong tông môn, một mực nằm ở yếu thế, vị này Trịnh Dương, thật muốn cùng các ngươi nói lợi hại như vậy, kế thừa tông môn, cũng là có thể! Lăng Vân kiếm các truyền đến tin tức, Hàn Kiếm Thu tông chủ chi vị, vừa mới nhường cho một cái gọi Trương Huyền người trẻ tuổi! Chỉ vì hắn lĩnh ngộ Thần chi kiếm ý... Chỉ cần có người thích hợp, thoái vị nhượng chức, ta cũng có thể làm ra được!”"),t("br"),t("br"),n._v("“Bất quá... Còn cần khảo nghiệm một chút!”"),t("br"),t("br"),n._v("“Môn chủ sợ hắn là... Thần điện người?” Liêu trưởng lão vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“Thần điện từ trước tới nay cao cao tại thượng, không để ý tới khói lửa nhân gian, nhưng chẳng biết tại sao, những năm gần đây, có loại muốn từng bước xâm chiếm sáu đại tông môn xu thế, không thể không phòng!” Trung niên nhân nói."),t("br"),t("br"),n._v("“Làm sao khảo nghiệm?”"),t("br"),t("br"),n._v("Liên quan tới Thần điện, Liêu trưởng lão không dám nói chuyện nhiều, hỏi."),t("br"),t("br"),n._v("“Đem hắn đưa đến Vạn Thú sảnh, mời ra Thanh Minh thú lão tổ, nó đối Thần điện người, trời sinh mẫn cảm, nếu như là, khẳng định có thể một cái nhìn ra!” Trung niên nhân gật đầu."),t("br"),t("br"),n._v("“Tốt!” Liêu trưởng lão lên tiếng."),t("br"),t("br"),n._v("Thanh Minh thú lão tổ, là Vạn Thú môn đời thứ mười bảy môn chủ thú sủng, thực lực cứ việc không tính mạnh nhất, có thể linh tính nhưng là không ai bằng."),t("br"),t("br"),n._v("Đối phương thật muốn từ Thần điện mà đến, trên người nhất định có đặc thù khí tức, có thể dễ dàng điều tra đi ra."),t("br"),t("br"),n._v("“Chỉ cần không phải Thần điện người, loại này thuần thú bản lĩnh, đủ có thể khiến hắn trở thành tông chủ, như vậy năng lực, chỉ cần đột phá, có lẽ liền có thể thuần phục, mạnh hơn thú sủng, ta Vạn Thú môn, lo gì không vượt qua được Lăng Vân kiếm các? Lần sau gặp được Hàn Kiếm Thu, cũng có thể tự hào nói cho hắn biết... Không chỉ bọn họ có thiên tài, chúng ta cũng có!”"),t("br"),t("br"),n._v("Trung niên nhân nắm đấm xiết chặt."),t("br"),t("br"),n._v("Liêu trưởng lão lui xuống, một canh giờ sau, một mặt phức tạp đi tới."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Trung niên nhân nhìn qua."),t("br"),t("br"),n._v("“Dựa theo môn chủ dặn dò, chúng ta đem Trịnh Dương dẫn tới Vạn Thú sảnh, kết quả...” Liêu trưởng lão da mặt co rúm."),t("br"),t("br"),n._v("“Kết quả làm sao? Thanh Minh thú lão tổ, nhưng có nhìn ra hắn... Có phải hay không Thần điện người?”"),t("br"),t("br"),n._v("“Nhìn không nhìn ra không biết, chỉ biết là... Thanh Minh thú lão tổ... Đòi muốn nhận chủ, cứng rắn bị chúng ta kéo lại...”"),t("br"),t("br"),n._v("Liêu trưởng lão xấu hổ."),t("br"),t("br"),n._v("“Đòi nhận chủ?”"),t("br"),t("br"),n._v("Trung niên nhân ngẩn ngơ."),t("br"),t("br"),n._v("“Không chỉ có như vậy... Vạn Thú sảnh, là tông môn các đệ tử Thánh thú, Tiên thú tụ tập địa phương, vị này Trịnh Dương đi một vòng, đã có hơn ba trăm đầu Thánh thú, mười hai đầu Tiên thú muốn nhận chủ, hiện tại toàn bộ tông môn đều sắp nổ, không ít trưởng lão, đều có ý kiến...”"),t("br"),t("br"),n._v("Liêu trưởng lão vội nói."),t("br"),t("br"),n._v("“Nhận chủ? Đến cùng chuyện gì xảy ra? Ta chỉ bảo ngươi dẫn hắn đi Vạn Thú sảnh, trêu chọc những này Thánh thú, Tiên thú làm cái gì?”"),t("br"),t("br"),n._v("Trung niên nhân ngẩn ngơ, sắp điên rồi."),t("br"),t("br"),n._v("Hắn chỉ là làm cho đối phương, xác nhận người sau thân phận, làm sao mới một canh giờ, nhiều như vậy thú, muốn nhận chủ?"),t("br"),t("br"),n._v("Vạn Thú môn, sở dĩ mạnh mẽ, là bởi vì trong đó hạch tâm đệ tử, nội môn đệ tử, trưởng lão, hầu như người người đều có thú sủng, bản thân tu vi phối hợp sủng vật, cùng cấp bậc cơ bản không người có thể vượt qua..."),t("br"),t("br"),n._v("Nếu như những này thú sủng, đều bị một người thuần phục, toàn bộ tông môn, còn không trực tiếp sụp đổ?"),t("br"),t("br"),n._v("Quy củ loại hình, tất cả đều muốn sụp đổ!"),t("br"),t("br"),n._v("“Là Thanh Minh thú lão tổ, nghe nói Trịnh Dương, có thể trong thời gian ngắn thuần phục giao long các thú, không quá tin tưởng, đem tin tức truyền lại cho rất nhiều Thánh thú, Tiên thú, bọn chúng mới đối với hắn tiến hành vây công, sau đó... Ta nhất thời không có ngăn cản, liền biến thành như vậy!”"),t("br"),n._v(" Nhớ tới chuyện lúc trước, Liêu trưởng lão cảm thấy mình sắp khóc."),t("br"),t("br"),n._v("Không phải hắn không muốn ngăn cản, mà là căn bản không ngăn cản được!"),t("br"),t("br"),n._v("Nhiều như vậy Thánh thú, Tiên thú xông lên, không đến ba phút liền bị vị này Trịnh huynh đánh ngã... Vốn cho rằng đánh rồi thì thôi... Ai có thể nghĩ tới, chỉ cần là bị đánh thú, tất cả đều vọt thẳng đi qua, chó xù đồng dạng, muốn nhận chủ..."),t("br"),t("br"),n._v("Thanh Minh thú lão tổ đều không ngoại lệ..."),t("br"),t("br"),n._v("Nó nhưng mà lão tổ cấp bậc Tiên thú... Nhận đối phương làm chủ, bọn họ vai vế làm sao bây giờ?"),t("br"),t("br"),n._v("Chẳng phải thoáng cái, muốn thấp thật lớn một đoạn?"),t("br"),t("br"),n._v("Vốn Trịnh huynh kêu rất tốt, một chút đổi giọng gọi lão tổ... Chịu không nổi ah!"),t("br"),t("br"),n._v("Không nghĩ tới ngắn ngủi một canh giờ, xuất hiện nhiều chuyện như vậy, trung niên nhân con mắt trợn tròn: “Dẫn ta đi gặp vị này Trịnh Dương!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Thật khó chịu...”"),t("br"),t("br"),n._v("Nhìn trước mắt quỳ đầy đất Thánh thú, Tiên thú, Trương Huyền đầy mặt rầu rĩ."),t("br"),t("br"),n._v("Liêu trưởng lão rời khỏi không lâu, đến tìm hắn, nói muốn dẫn hắn đi cái gì Vạn Thú sảnh, ai biết mới đi đến nơi này, liền bị rất nhiều Thánh thú công kích."),t("br"),t("br"),n._v("Vì tự vệ, đành phải đem những người này, tất cả đều đánh cho một trận..."),t("br"),t("br"),n._v("Sau đó... Bọn chúng liền thần phục!"),t("br"),t("br"),n._v("Khiêm tốn thật là khó!"),t("br"),t("br"),n._v("Chỉ là, hiện tại đến bây giờ đều không hiểu, Liêu trưởng lão mục đích là cái gì?"),t("br"),t("br"),n._v("Chẳng lẽ chỉ là vì để cho mình tới thuần thú?"),t("br"),t("br"),n._v("Có thể... Hắn có giao long các thú là được, không cần nhiều như vậy ah!"),t("br"),t("br"),n._v("“Các ngươi đều đi thôi, ta đã có thú sủng, sẽ không đi thu nhiều!”"),t("br"),t("br"),n._v("Nhiều như vậy gia hỏa, thật muốn mang đi ra ngoài, đều sắp đuổi kịp một cái tông môn, còn thế nào lặng yên không một tiếng động?"),t("br"),t("br"),n._v("Chỉ sợ còn chưa đi xa, liền bị Thần điện phát hiện, trực tiếp tru sát."),t("br"),t("br"),n._v("“Không nhận bọn họ, nhận lấy ta đi, thực lực của ta, đã đi đến Tiên Quân cảnh, mặc dù không bằng giao long bọn họ, nhưng khứu giác nhạy bén, trời sinh mẫn cảm, có thể cảm thấy được nguy hiểm, sớm dự phòng!”"),t("br"),t("br"),n._v("Bò lổm ngổm đi tới, Thanh Minh thú trong mắt mỉm cười."),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Đầu này Thanh Minh thú, thực lực cứ việc chỉ là Tiên Quân sơ kỳ, nhưng cùng nó nói đồng dạng, quả thực trời sinh mẫn cảm, đối với nguy hiểm, có thể sớm dự phòng."),t("br"),t("br"),n._v("“Vậy thì tốt, ngươi liền làm ta thú sủng...”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền gật gật đầu, vừa định hoàn thành nhận chủ nghi thức, chỉ thấy một người trung niên vội vã bay tới: “Trịnh Dương tiểu huynh đệ, xin chậm!”"),t("br"),t("br"),n._v("Trương Huyền ngừng lại."),t("br"),t("br"),n._v("“Trịnh huynh, vị này ta Vạn Thú môn Tần Nguyên môn chủ!”"),t("br"),t("br"),n._v("Theo ở phía sau Liêu trưởng lão vội vàng giới thiệu."),t("br"),t("br"),n._v("“Môn chủ?” Trương Huyền sững sờ, vội vàng ôm quyền: “Gặp qua Tần môn chủ!”"),t("br"),t("br"),n._v("“Tiểu huynh đệ không cần khách khí...” Tần Nguyên môn chủ cười khổ: “Ngươi thuần thú bản lĩnh, ta có thể tính phục...”"),t("br"),t("br"),n._v("Thân là Vạn Thú môn tông chủ, cả thương, thuần thú có thể vượt qua hắn, hầu như không, vốn cho rằng đã đi đến cực hạn, nhìn thấy trước mắt vị này, mới hiểu được... Kém quá nhiều!"),t("br"),t("br"),n._v("Vẫn lấy làm kiêu ngạo thuần thú chi thuật, ở trước mặt đối phương, cái gì cũng không bằng... Không đáng giá nhắc tới!"),t("br"),t("br"),n._v("Vốn nghĩ, Liêu trưởng lão ít nhiều có chút khoa trương, tận mắt thấy một màn trước mắt, mới hiểu được, không những không có khoa trương, còn nói mất!"),t("br"),t("br"),n._v("Mấy trăm Thánh thú, Tiên thú đồng thời thần phục một người..."),t("br"),t("br"),n._v("Nhìn chung Vạn Thú môn lịch sử, đều chưa bao giờ có."),t("br"),t("br"),n._v("“Thuần thú, thật ra thì cũng không tưởng tượng bên trong phức tạp như vậy!” Trương Huyền nói."),t("br"),t("br"),n._v("“Nhưng lắng tai nghe...” Tần Nguyên môn chủ ôm quyền."),t("br"),t("br"),n._v("“Rất đơn giản, thú cùng người đồng dạng, cũng cần tôn trọng, cũng có tôn nghiêm...” Trương Huyền chậm rãi giải thích."),t("br"),t("br"),n._v("Danh sư đại lục, hắn thuần thú liền đạt đến đỉnh phong, không ai bằng, thấy hai người muốn nghe, lập tức êm tai nói."),t("br"),t("br"),n._v("Nghe một hồi, Tần Nguyên môn chủ thân thể chấn động, trong mắt tràn đầy khó tin."),t("br"),t("br"),n._v("Hắn nghiên cứu thuần thú nhiều năm, không nghĩ tới, còn có cao thâm như vậy lý luận."),t("br"),t("br"),n._v("Nghe một hồi, cảm giác hiểu ra, đối thuần thú lý giải, tăng tiến một bước không chỉ!"),t("br"),t("br"),n._v("“Môn chủ, hắn... Nhưng có tư cách, trở thành người nhậm chức môn chủ kế tiếp?”"),t("br"),t("br"),n._v("Qua không biết bao lâu, Liêu trưởng lão nhịn không được, truyền âm tới."),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Tần Nguyên môn chủ tràn đầy kích động."),t("br"),t("br"),n._v("Có thể để cho Thanh Minh thú lão tổ, cam tâm tình nguyện nhận chủ, chứng minh khẳng định không phải Thần điện người, hiện tại lại có cao thâm như vậy thuần thú phương pháp, sửa sang lại truyền thừa cho tông môn lời nói, toàn bộ Vạn Thú môn thuần thú trình độ, đều đem tăng lên một đoạn dài!"),t("br"),t("br"),n._v("Có thực lực, thuần thú mạnh, cũng không phải Thần điện người... Tuyệt đối là trở thành tông chủ người chọn lựa thích hợp nhất!"),t("br"),t("br"),n._v("Nghĩ đến cái này, cũng nhịn không được nữa, nhìn về phía thanh niên trước mắt, âm thanh có chút phát run: “Trịnh Dương tiểu huynh đệ, ngươi có hứng thú hay không... Trở thành Vạn Thú môn tông chủ, quản lý toàn bộ Vạn Thú môn?”"),t("br"),t("br"),n._v("“Trở thành tông chủ?”"),t("br"),t("br"),n._v("Trương Huyền ngẩn ngơ."),t("br"),t("br"),n._v("Đi vào mới một canh giờ, liền để tự mình làm tông chủ... Thượng Thương tông môn, đều như vậy trò đùa ư?"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2027"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2029"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);