(window.webpackJsonp=window.webpackJsonp||[]).push([[1160],{1515:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-194-ba-su-khiep-so-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-194-ba-su-khiep-so-thuong"}},[n._v("#")]),n._v(" Chương 194: Ba sư khiếp sợ (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Nói cách khác, này đạo hắc khí, không thuộc về hắn biết đến bất luận một loại nào kịch độc."),t("br"),t("br"),n._v(" Cái kia rốt cuộc là thứ gì?"),t("br"),t("br"),n._v(" “Thử xem có thể hay không bức ra ngoài thân thể!” "),t("br"),t("br"),n._v(" Ánh mắt nghiêm nghị, Trương Huyền điều động chân khí trong cơ thể."),t("br"),t("br"),n._v(" Tuy rằng không biết độc khí nguy hại, nhưng mặc cho nó vẫn ở lại trong cơ thể, sớm muộn là kẻ gây họa, trước không phát giác ngược lại cũng thôi, hiện tại biết, phải nghĩ biện pháp mau chóng xử lý."),t("br"),t("br"),n._v(" Bằng không, một cây đao đặt ở trong lòng, bất luận người nào đều sẽ không thoải mái."),t("br"),t("br"),n._v(" Dùng chân khí bức độc, tại một quyển sách bên trong có ghi chép, học tập lên đến không hề phức tạp, tâm tư hơi động, dĩ nhiên học được."),t("br"),t("br"),n._v(" Ục ục ục!"),t("br"),t("br"),n._v(" Nhìn thấy chân khí vọt tới, hắc khí như là nhìn thấy khắc tinh giống như vậy, nhẹ nhàng loáng một cái, chui vào kinh mạch ở giữa, ẩn giấu đi."),t("br"),t("br"),n._v(" “Không được...” "),t("br"),t("br"),n._v(" Sầm mặt lại."),t("br"),t("br"),n._v(" Này cỗ độc khí thực sự quá lợi hại, lại có thể tránh né chân khí vây giết, giờ khắc này chỉ là chui vào kinh mạch ở giữa, không quá quan trọng, một khi phát rồ tiến vào vào thân thể trọng yếu bộ phận, e sợ không kịp phản kháng, sẽ bị tại chỗ độc giết."),t("br"),t("br"),n._v(" Có thể tránh né chân khí, cùng có trí tuệ như thế, đây là cái gì độc?"),t("br"),t("br"),n._v(" Lại là khi nào trúng?"),t("br"),t("br"),n._v(" Tìm khắp thư viện liên quan với độc thư tịch, đều không có vật này bất luận cái gì ghi chép, nói rõ này độc cấp bậc cao hơn Thiên Huyền Vương quốc!"),t("br"),t("br"),n._v(" Loại này cấp bậc độc khí... Lại ở trong cơ thể mình phát giác."),t("br"),t("br"),n._v(" Trương Huyền càng ngày càng mơ hồ."),t("br"),t("br"),n._v(" Rất nhiều thủ pháp giải độc đều dùng một lần, phát giác không có một chút tác dụng nào, không thể làm gì khác hơn là tạm thời từ bỏ."),t("br"),t("br"),n._v(" Nhìn dáng dấp này đạo hắc khí, đối với chân khí của hắn vô cùng sợ hãi, tạm thời còn không dám quấy rối, bằng không, cũng không thể sống lâu như thế đều không phát hiện."),t("br"),t("br"),n._v(" Bất quá, tuy rằng tạm thời sẽ không có nguy hiểm đến tính mạng, nhưng như thế nào trúng độc, là cái vấn đề lớn, trong lúc vô tình ngược lại cũng thôi, bị người hạ độc nhưng lại không biết vậy thì đáng sợ."),t("br"),t("br"),n._v(" Đối phương một đòn không được, khẳng định còn có thể đến lần thứ hai."),t("br"),t("br"),n._v(" Không sợ tặc trộm, chỉ sợ tặc nhớ nhung a!"),t("br"),t("br"),n._v(" “Trước xác định là sống lại trước trúng độc vẫn là sau khi sống lại, là do người khác hay là vô tình, cũng có thể sớm có cái phòng bị!” "),t("br"),t("br"),n._v(" Sinh ra cảnh giác, Trương Huyền lần thứ hai nhìn về phía hắc khí, nghiên cứu nửa ngày... Kết quả, cái rắm đều không nghiên cứu ra."),t("br"),t("br"),n._v(" Không phải hắn quá ngốc mà là... Chất độc này thực sự quá cao cấp, vượt qua Thiên Huyền Vương quốc có thể hiểu được phạm trù, dấu vết nào cũng không thấy, chớ nói chi là nghiên cứu khi nào trúng, rốt cuộc là thứ gì."),t("br"),t("br"),n._v(" “Thử xem Thiên Đạo thư viện!” "),t("br"),t("br"),n._v(" Băn khoăn nửa ngày cũng không hiểu rõ, đang định từ bỏ, đột nhiên trong lòng hơi động."),t("br"),t("br"),n._v(" Hắn nghiên cứu không ra, không có nghĩa là thư viện cũng không thể ra sức!"),t("br"),t("br"),n._v(" Thư viện nhất định phải ngón tay chạm tới hoặc là đối phương đánh quyền, mới có thể hình thành thư tịch, biết thiếu hụt, này đạo độc khí ẩn giấu ở kinh mạch ở giữa, ngón tay chạm đến khẳng định không được, để nó đánh quyền... Khặc khặc, càng là nằm mơ."),t("br"),t("br"),n._v(" Bất quá, chỉ là xác nhận một ít đơn giản vấn đề, trái lại không làm khó được Trương Huyền."),t("br"),t("br"),n._v(" Tiện tay lấy ra một cái trống không sách vở, cầm lấy bút lông viết đi: “Trong cơ thể độc khí là sau khi sống lại trúng độc, có người hãm hại!” "),t("br"),t("br"),n._v(" Ngón tay xẹt qua, đầu óc lập tức xuất hiện một quyển giống như đúc thư tịch."),t("br"),t("br"),n._v(" Tiện tay mở ra, quả nhiên xuất hiện câu nói này thiếu hụt."),t("br"),t("br"),n._v(" Xem xong nội dung, sắc mặt tái xanh."),t("br"),t("br"),n._v(" Căn cứ Thiên Đạo thư viện tặng lại đến nội dung, chất độc này là sống lại trước trúng, cũng không phải là bản thân vô ý, là bị người hạ xuống hắc thủ!"),t("br"),t("br"),n._v(" Nói cách khác... Có người muốn giết hắn."),t("br"),t("br"),n._v(" “Chẳng lẽ... Tiền thân tử vong, không hề là uống rượu phiền muộn mà chết, mà là... Trúng chất độc này?” "),t("br"),t("br"),n._v(" Trước liền kỳ quái, tiền thân cũng quá yếu, đường đường Chân Khí cảnh đỉnh phong cường giả, uống cái rượu có thể uống chết, bây giờ nhìn lại, căn bản không phải có chuyện như vậy."),t("br"),t("br"),n._v(" Nghĩ đến liền làm, ở trên thư bổn đem đủ loại suy đoán viết đi ra."),t("br"),t("br"),n._v(" Không biết qua bao lâu, Trương Huyền ngừng lại."),t("br"),t("br"),n._v(" Thông qua này nửa ngày suy đoán, đã biết một cái đại thể hình dáng."),t("br"),t("br"),n._v(" Tiền thân quả nhiên là bị người độc chết, chính vì như thế, mới có bản thân xuyên qua."),t("br"),t("br"),n._v(" Sau khi sống lại bản thân sở dĩ không có chuyện gì, là bởi vì tu luyện Thiên Đạo chân khí quá mức tinh khiết, này cỗ độc khí trời sinh sợ hãi, bị áp chế không cách nào động đậy, bằng không, cũng không thể sống đến hiện tại."),t("br"),t("br"),n._v(" Nói cách khác, có thể sống sót, thiệt thòi vẫn tính chăm chỉ, tu luyện không ngừng, bằng không, một khi chân khí không đủ, bắt được hắc khí sẽ lập tức phản phệ, đem độc giết."),t("br"),t("br"),n._v(" “Lại là tiền thân lưu lại hỗn loạn...” "),t("br"),t("br"),n._v(" Vốn tưởng rằng giải quyết xong giáo viên sát hạch sự tình, đã có thể sống ra bản thân, trời cao mặc chim bay, kết quả mới phát giác cả nghĩ quá rồi."),t("br"),t("br"),n._v(" Này đạo hắc khí không giải quyết triệt để, sẽ vẫn nằm ở nguy cơ bên trong."),t("br"),t("br"),n._v(" “Phải nghĩ biện pháp tìm ra người muốn giết ta mới được.” "),t("br"),t("br"),n._v(" Muốn giết hắn người, ẩn giấu đi, không thể tìm tới, nhưng chỉ cần lần sau còn dám động thủ, dựa vào Thiên Đạo thư viện, liền nhất định có thể liền hắn tổ tông tám đời đều tra đến đi ra."),t("br"),t("br"),n._v(" Vì lẽ đó, không hề đủ sợ, chỉ là trong lòng nghi hoặc."),t("br"),t("br"),n._v(" Tiền thân chỉ là cái cô nhi, liền cái thân thích đều không có, bản thân lại là rác rưởi, không đắc tội người nào, đến cùng là ai, có thể sử dụng Thiên Huyền Vương quốc đều không có độc đến giết hắn?"),t("br"),n._v("  Có thể đem loại độc chất này đi tìm đến, thực lực khẳng định không đơn giản, phí lớn như vậy tay chân, làm gì không một cái tát đập chết?"),t("br"),t("br"),n._v(" Này không phải cởi quần nói láo tự gây phiền phức sao?"),t("br"),t("br"),n._v(" Nghĩ đến một hồi không nghĩ ra, Trương Huyền không nghĩ nhiều nữa, hạ độc người tạm thời không xuất hiện, muốn nhiều hơn nữa vô dụng, chỉ có thể trước nghĩ biện pháp giải quyết trong cơ thể vấn đề."),t("br"),t("br"),n._v(" “Muốn giải độc, trước hết biết đây là cái gì độc, Thiên Huyền Vương quốc liên quan với độc thư tịch vẫn là quá thiếu, nhìn dáng dấp xin đi thư tịch càng nhiều địa phương mới được...” "),t("br"),t("br"),n._v(" Nắm giữ Thiên Đạo thư viện, chỉ cần có đủ để nhốt tại độc thư tịch, chính hắn liền có thể giải."),t("br"),t("br"),n._v(" Bất quá, những sách này tịch cũng không dễ tìm, vương thất tàng thư khố đều xem xong, không có bất luận cái gì thu hoạch, những nơi khác thư tịch, không cần nhìn cũng biết, khẳng định cũng không có."),t("br"),t("br"),n._v(" Chung quy chất độc này quá cao cấp, cao cấp Thiên Huyền Vương quốc cũng không tìm tới."),t("br"),t("br"),n._v(" Liền độc đều không có, lại làm sao có khả năng có phương pháp giải quyết."),t("br"),t("br"),n._v(" Chân chính muốn tiêu trừ mầm họa, e sợ phải đi cấp bậc càng cao hơn Vương quốc, tìm kiếm nắm giữ loại này thư tịch nhiều địa phương."),t("br"),t("br"),n._v(" “Quay lại tìm ba vị danh sư hỏi thăm một chút...” "),t("br"),t("br"),n._v(" Nơi như thế này hắn cùng không biết chuyện, có lẽ tam đại danh sư có thể biết một, hai."),t("br"),t("br"),n._v(" Bọn họ theo cấp bậc càng cao hơn Vương quốc lại đây, du lịch rất rộng, khẳng định so tự mình biết nhiều lắm."),t("br"),t("br"),n._v(" “Cũng không vội vã, trước đem chuyện nơi đây giải quyết đi lại nói.” "),t("br"),t("br"),n._v(" Ngược lại có Thiên Đạo chân khí áp chế, hắc khí trong thời gian ngắn cũng không dám làm ác, Trương Huyền không thể làm gì khác hơn là lắc đầu một cái không nghĩ nhiều nữa, đưa tay ra mời lười eo tiếp tục giải quyết lừa gạt tiền để lại vấn đề."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Lưu sư, chúng ta làm gì thay quần áo?” "),t("br"),t("br"),n._v(" Ba vị danh sư đi ở Hồng Thiên học viện trên đường phố, Trịnh Phi nhìn trên người thường phục, không nhịn được nói."),t("br"),t("br"),n._v(" Bình thường đều ăn mặc danh sư đặc hữu trang phục, đi đến bất kỳ địa phương nào đều được người tôn kính cao cao tại thượng, một lần đổi thành người bình thường y phục, còn có chút không thích ứng."),t("br"),t("br"),n._v(" “Danh sư trang phục quá dễ thấy, thật ăn mặc lại đây, Trương Huyền lão sư nhất định sẽ trực tiếp chạy tới, nói gì nghe nấy, cung kính đến cực điểm, thật muốn như vậy, còn làm thế nào thấy được của hắn chân thực phẩm chất?” "),t("br"),t("br"),n._v(" Lưu Lăng nói."),t("br"),t("br"),n._v(" “Đúng đấy, đổi thành thường phục tốt, ai cũng không nhận ra, vừa vặn thử thách nhân phẩm!” Trang Hiền theo sát gật đầu."),t("br"),t("br"),n._v(" Trịnh Phi hiểu được."),t("br"),t("br"),n._v(" Đổi người bình thường y phục, lại cố ý đè thấp tu vi, ai cũng đoán không ra ba người bọn hắn là danh sư, đều sẽ coi bọn họ là thành một cái phổ thông ông lão."),t("br"),t("br"),n._v(" Tình huống như thế mới có thể nhìn thấy gần gũi nhất sự thực chân tướng."),t("br"),t("br"),n._v(" Một đường hỏi thăm, thời gian không lâu, liền tìm đến Trương Huyền lớp học."),t("br"),t("br"),n._v(" “Này phòng học cũng quá nhỏ đi!” "),t("br"),t("br"),n._v(" Nhìn thấy trước mắt lớp học dáng dấp, Lưu Lăng mấy người cau mày."),t("br"),t("br"),n._v(" Hồng Thiên học viện quy mô, cùng nhau đi tới bọn họ đều nhìn thấy, coi như nhất bình thường lão sư dùng để giảng bài phòng học, đều có mấy trăm mét vuông, mà trước mắt cái này liền một trăm đều không có, bất luận kiến trúc vẫn là trang trí, đều biểu hiện đến mức dị thường đơn sơ."),t("br"),t("br"),n._v(" Nhìn chút liền biết là kém cỏi nhất nhất loại."),t("br"),t("br"),n._v(" “Xem ra vị này Trương lão sư tại học viện đãi ngộ xác thực không ra sao!” "),t("br"),t("br"),n._v(" Như thế kém giảng bài hoàn cảnh, thật không biết, vị này Trương lão sư là làm sao nhịn được xuống đến, đổi làm bọn họ, khẳng định đã sớm cùng học viện đại náo, phẩy tay áo bỏ đi."),t("br"),t("br"),n._v(" “Ở trong môi trường này giảng bài, còn có thể duy trì bình tĩnh, thắng không kiêu bại không nản, xem ra đã quên mất sủng nhục tình cảm, khiến cho người kính nể.” "),t("br"),t("br"),n._v(" Lưu Lăng cảm khái."),t("br"),t("br"),n._v(" Chịu đến không công bằng đãi ngộ, đáy lòng nhưng không có lời oán hận, không bởi vậy qua loa học sinh, chỉ loại này không quan tâm hơn thua phẩm chất, liền làm người khâm phục."),t("br"),t("br"),n._v(" “Vương Đào anh họ, Trương lão sư giảng, Ngưng Khí tụ hợp, ta đến hiện tại vẫn để ý giải không được, ngươi có thể cho ta lại giảng một lần sao?” "),t("br"),t("br"),n._v(" Hướng phía trong đi mấy bước, còn không tiến vào lớp học, liền nghe đến gian phòng phía trước trên sân cỏ, truyền đến hỏi dò âm thanh."),t("br"),t("br"),n._v(" Ba người dừng bước."),t("br"),t("br"),n._v(" “Tốt, bất quá, đối với đoạn này, Trương lão sư giảng vô cùng huyền diệu, liền ngay cả ta cũng chỉ lĩnh ngộ không đủ một phần mười, như vậy đi, Trương lão sư giảng giải thời điểm, ta làm bút ký, chúng ta cùng nhau nghiên cứu!” Gọi là Vương Đào học trưởng, do dự một chút nói."),t("br"),t("br"),n._v(" “Quá tốt rồi!” Thanh âm đầu tiên tràn đầy hưng phấn."),t("br"),t("br"),n._v(" “Ngươi xem, Trương lão sư là nói như vậy, Tụ Tức cảnh, Ngưng Khí tụ hợp, đem thiên địa linh khí xem là thân thể một phần, mà không phải ép buộc hấp thu, như vậy tới nay, khí tức sẽ đem người xem như vật dẫn, do đó hội tụ linh khí tốc độ tăng cường; Lúc đó Trương lão sư nói rồi mười cái phương pháp, ta chỉ ghi chép ba cái, chúng ta có thể một khối nghiên cứu, ngươi xem này cái thứ nhất...” "),t("br"),t("br"),n._v(" Vương Đào âm thanh tiếp theo truyền đến."),t("br"),t("br"),n._v(" “Đem thiên địa linh khí xem là thân thể một phần?” "),t("br"),t("br"),n._v(" Đem hai người đối thoại nghe vào trong tai, tam đại danh sư đồng thời chấn động, từng cái từng cái lộ ra vẻ kinh hãi."),t("br"),t("br"),n._v(" “Cái này lý luận ta cũng là theo lão sư ta trong miệng nghe nói qua... Cái này Trương Huyền lão sư, lại có thể giảng huyền diệu như vậy?” "),t("br"),t("br"),n._v(" Lưu Lăng miệng mở ra."),t("br"),t("br"),n._v(" Người trong nghề vừa ra tay, đã biết có hay không, bọn họ là danh sư, đứng ở giáo sư nghề nghiệp này cao nhất, tự nhiên am hiểu sâu giảng bài chi đạo, cũng biết giảng giải những cái kia là chính xác, những cái kia là sai lầm."),t("br"),t("br"),n._v(" Tuy rằng không nghe giảng bài, quang theo hai cái học viên đối thoại, bọn họ liền có thể nghe ra cái này Trương lão sư lý luận, không những mới mẻ độc đáo, còn hết sức chính xác, nhắm thẳng vào tu luyện hạch tâm."),t("br"),t("br"),n._v(" Trình độ loại này giảng bài nội dung, coi như là bọn họ, đều rất khó giảng đi ra."),t("br"),t("br"),n._v(" Phía trước hai người càng thảo luận càng nhiều, nói tới lý luận không phải cỡ nào huyền ảo, lại sâu sắc vào bên trong, như đại dương mênh mông, một chút không nhìn thấy phần cuối bao la cùng thâm thúy, nghe càng nhiều, đối với tu luyện vượt có trợ giúp, được ích lợi không nhỏ."),t("br"),t("br"),n._v(" Tam đại danh sư càng nghe càng chấn động, từng cái từng cái kích động toàn thân run rẩy, đứng tại chỗ đều đi không nổi."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-202"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-204"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);