(window.webpackJsonp=window.webpackJsonp||[]).push([[1172],{1526:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2026-co-bang-long-hay-khong-tro-thanh-truong-lao"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2026-co-bang-long-hay-khong-tro-thanh-truong-lao"}},[n._v("#")]),n._v(" Chương 2026: Có bằng lòng hay không trở thành trưởng lão?")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Hiện thực Thông Thần điện."),i("br"),i("br"),n._v("“Nhanh tra một chút, vị này đến cùng là ai?”"),i("br"),i("br"),n._v("Vẻ mặt trắng bệch, một vị lão giả nhìn về phía bên người phục vụ viên."),i("br"),i("br"),n._v("Hắn chính là đáp ứng Trương Huyền hơn nữa yêu cầu hai vạn thông thần tệ vị kia Thông Thần điện trưởng lão, vừa rồi nghe được tin tức, đi vào giả lập Thông Thần điện, tận mắt thấy đối phương một người một chưởng, thắng liền mười sáu tràng, một điểm tổn thương đều không có nhận!"),i("br"),i("br"),n._v("Mặc dù không biết, đằng sau tới mấy người, là Thất Tinh lâu đại trưởng lão cùng tông chủ, nhưng căn cứ đối phương thi triển võ kỹ, cùng cùng cấp bậc tại chiến đấu lực, vẫn có thể đoán ra một ít."),i("br"),i("br"),n._v("Nói cách khác, có người tại Bích Viễn thành Thông Thần điện, một người quét ngang toàn bộ Thất Tinh lâu!"),i("br"),i("br"),n._v("Ngụy Thần cảnh tông chủ, cũng không là đối thủ..."),i("br"),i("br"),n._v("Truyền đi, tuyệt đối sẽ lập tức nháo lật trời!"),i("br"),i("br"),n._v("“Trưởng lão, đối phương nên dùng một loại nào đó trận pháp, ngăn cách tin tức, chúng ta chỉ tra được, có khả năng tại Thông Thần điện trong tĩnh thất, cụ thể là ai, không thể nào biết được!” Phục vụ viên dừng lại một chút, hồi đáp."),i("br"),i("br"),n._v("Bắt đầu khiêu chiến, bọn họ liền dò xét, chỉ tiếc, đối phương phảng phất trước thời hạn biết cử động của bọn hắn đồng dạng, dùng ngăn cách khí tức trận pháp, phong tỏa thông thần ngọc phù, chỉ có thể tra ra cái đại khái phạm vi."),i("br"),i("br"),n._v("“Nơi này, hết thảy có một vạn ba ngàn hơn bảy trăm cái tĩnh thất, hiện tại tra cho ta, người ở bên trong đều là lúc nào đi vào, đi đến Chân Tiên cảnh trở lên, lại có bao nhiêu vị, trong đó có hay không gọi Lưu Dương...”"),i("br"),i("br"),n._v("Trưởng lão tiếp tục nói."),i("br"),i("br"),n._v("Nhiều người như vậy, cứ việc điều tra ra khả năng rất nhỏ, nhưng có thể rất nhanh thu nhỏ phạm vi."),i("br"),i("br"),n._v("“Tìm tới về sau, hảo hảo tiếp đãi, tuyệt đối không nên để hắn rời khỏi, ta sẽ lập tức tới ngay bái kiến...” Trưởng lão nói tiếp."),i("br"),i("br"),n._v("Có thể đem cùng cấp bậc Thất Tinh lâu tông chủ đánh bại, người này nhất định không đơn giản, tuyệt không phải hắn một cái nho nhỏ Thông Thần điện người phụ trách, có thể đắc tội."),i("br"),i("br"),n._v("Phục vụ viên gật đầu, vừa định đi làm, liền nghe đến trước đó tại trước đài phục vụ viên âm thanh vang lên: “Trưởng lão, vừa rồi vị kia muốn đi Trục Tinh chi hải bằng hữu, vội vã đến tìm ngươi...”"),i("br"),i("br"),n._v("“Tìm ta?” Nhướng mày, trưởng lão xua tay, mang theo không kiên nhẫn: “Nói cho hắn biết, hiện tại không rảnh! Không có tiền không được qua đây phiền ta!”"),i("br"),i("br"),n._v("Việc cấp bách là nhanh điểm tìm tới vị kia cao thủ, một cái Chân Tiên cảnh tiểu tử mà thôi, không đủ tiền, ai cùng ngươi chơi đùa?"),i("br"),i("br"),n._v("“Hắn nói...” Trước đài phục vụ viên âm thanh tiếp tục vang lên: “Bẩm báo trưởng lão, hắn nói đã gom góp đủ thông thần tệ, hiện tại tới giao tiền...”"),i("br"),i("br"),n._v("“Gom góp? Nhanh như vậy?” Trưởng lão sững sờ, đột nhiên nghĩ đến cái gì, cảm thấy đầu óc trong nháy mắt nổ tung, con mắt trợn tròn: “Chẳng lẽ là hắn? Nhanh để hắn đi vào...”"),i("br"),i("br"),n._v("Chân Tiên cảnh, phù hợp điều kiện!"),i("br"),i("br"),n._v("Hắn nói muốn kiếm tiền, để cho mình chờ một chút, sau đó... Giả lập Thông Thần điện, liền có người tiến hành công khai thi đấu, thắng liền mười mấy tràng... Bên kia đánh xong, bên này chạy tới nói tiền gom góp đủ! Thời gian đối với bên trên!"),i("br"),i("br"),n._v("Mấu chốt nhất là, trên đài người kia, thắng liền 15 tràng về sau, sau đó nói... Tiền đủ rồi, không đánh!"),i("br"),i("br"),n._v("Căn cứ tính toán, lúc ấy trong tay, vừa vặn có hơn hai vạn thông thần tệ!"),i("br"),i("br"),n._v("Tiền số lượng cũng có thể đối bên trên!"),i("br"),i("br"),n._v("Trong lòng đang suy tính, ngay sau đó nhìn thấy vừa rồi người thanh niên kia đi vào, vừa vào gian phòng, đưa tới một cái thẻ."),i("br"),i("br"),n._v("“Trong này có hai vạn thông thần tệ...”"),i("br"),i("br"),n._v("Vội vàng nhận lấy, trưởng lão tra xét một phen, quả nhiên có hai vạn, thân thể xiết chặt, hỏi: “Không biết... Các hạ xưng hô như thế nào? Là như vậy... Đã muốn ngồi Thất Tinh lâu phi hành Thánh thú, khẳng định muốn biết họ tên lai lịch loại hình...”"),i("br"),i("br"),n._v("“Nên!” Trương Huyền gật gật đầu: “Ta tên Lưu Dương!”"),i("br"),i("br"),n._v("Phù phù!"),i("br"),i("br"),n._v("Trưởng lão từ dưới đất lần nữa bò dậy, con mắt chớp chớp, có chút muốn khóc."),i("br"),i("br"),n._v("Sớm biết cái tên này lợi hại như vậy, đánh chết cũng không kiếm tiền của hắn ah!"),i("br"),i("br"),n._v("Thoạt nhìn hết sức bình thường, tuổi tác cũng không lớn... Lại một người hoành khiêu toàn bộ Thất Tinh lâu, không người có thể chống lại..."),i("br"),i("br"),n._v("“Vừa rồi... Công khai thi đấu chính là ngươi?” Cũng nhịn không được nữa hỏi."),i("br"),i("br"),n._v("“Ngươi nói muốn dùng hai vạn, trong lúc nhất thời, cũng nghĩ không ra cái gì kiếm tiền tốt con đường, liền khiêu chiến thoáng cái... May mắn, đối thủ đều không mạnh lắm, nếu không, khả năng còn thu thập không đủ đây!”"),i("br"),i("br"),n._v("Trương Huyền cười gật đầu."),i("br"),i("br"),n._v("Vừa rồi đã không có thi triển Thần chi kiếm ý, cũng không có cho thấy Vạn Thú môn tông chủ thân phận... Cho nên, cũng không có gì có thể phủ nhận."),i("br"),i("br"),n._v("Cũng không thể, vì tránh né Thần điện, liền một chút xíu năng lực đều không sử dụng đi!"),i("br"),i("br"),n._v("“Không mạnh lắm?” Lung lay thoáng cái, trưởng lão suýt chút nữa không có hộc máu."),i("br"),i("br"),n._v("Thất Tinh các đại trưởng lão, tông chủ... Còn không gọi mạnh?"),i("br"),i("br"),n._v("Vậy cái gì gọi là cường giả?"),i("br"),i("br"),n._v("“Sớm biết ngươi có lợi hại như vậy thực lực, tiền khẳng định không thu... Yên tâm, ta hiện tại liền liên hệ Thất Tinh lâu!” Xác nhận đối phương, chính là vị kia vừa đem Thất Tinh lâu cao thủ khiêu chiến một lần gia hỏa, vội vàng đem thẻ trả đi qua."),i("br"),i("br"),n._v("Không phải là không muốn thu, mà là không dám thu!"),i("br"),i("br"),n._v("Nhân vật lợi hại như thế, đã không phải là hắn loại này cấp bậc có thể đắc tội."),i("br"),i("br"),n._v("Lấy ra đưa tin ngọc phù, một lát sau, nhìn lại: “Tiền bối, Thất Tinh lâu Khôi Hiểu tông chủ, muốn đích thân gặp ngươi, chúng ta đi qua đi...”"),i("br"),i("br"),n._v("Báo chuyện vừa rồi, đối phương lập tức phát tới tin tức, để hắn đem người dẫn đi."),i("br"),i("br"),n._v("Lúc này chỗ nào không hiểu, trước mắt vị này, tại trong mắt đối phương trọng lượng, không dám tiếp tục phí lời, xưng hô cũng biến thành tiền bối."),i("br"),i("br"),n._v("“Được...”"),i("br"),i("br"),n._v("Trương Huyền đi theo."),i("br"),i("br"),n._v("Thất Tinh lâu khoảng cách Thông Thần điện không xa, không đến mười phút đồng hồ, hai người liền tiến vào đại biểu chủ điện Thiên Xu trong điện."),i("br"),i("br"),n._v("Trong phòng ở giữa, Khôi Hiểu tông chủ, đại trưởng lão, Hồng Vũ trưởng lão ngồi ngay ngắn trong đó, thấy hai người đi tới, tất cả đều đem ánh mắt tập trung ở Trương Huyền trên người."),i("br"),n._v(" Giả lập Thông Thần điện phía trong thân ảnh trẻ tuổi, phía trước vị này, thoạt nhìn càng trẻ tuổi, tính toán đâu ra đấy, không cao hơn ba mươi tuổi."),i("br"),i("br"),n._v("Không đến ba mươi tuổi Chân Tiên cảnh đỉnh phong, hơn nữa cùng cấp bậc, bọn họ cũng không là đối thủ..."),i("br"),i("br"),n._v("“Ngươi... Chính là vừa rồi Thông Thần điện, thắng liền mười sáu tràng vị kia Lưu Dương?”"),i("br"),i("br"),n._v("Khôi Hiểu nhịn không được nói."),i("br"),i("br"),n._v("Thông Thần điện phía trong hình dáng cùng bản thân là khác biệt, cho dù tận mắt thấy, đều nếu không xác nhận."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu: “Ta có chút sự tình, muốn đi Trục Tinh chi hải, khôi tông chủ, có thể hay không tiện thể đoạn đường? Ta sẽ dựa theo giá thị trường cấp cho thù lao...”"),i("br"),i("br"),n._v("Trên đường tới, trước đó tại trưởng lão đã cùng hắn nói rồi, trước mắt vị này, chính là Thất Tinh lâu khôi tông chủ!"),i("br"),i("br"),n._v("Đổi lại trước kia, nhìn thấy Ngụy Thần cảnh cường giả, sẽ còn cảm thấy kính sợ, gặp qua Hàn Kiếm Thu cùng Tần Nguyên môn chủ, lại thêm liên tục chém giết bốn vị Thần điện Tiên Quân, gà con càng là nuốt một viên loại cấp bậc này vũ khí, sớm đã không còn tâm mang sợ hãi."),i("br"),i("br"),n._v("“Ngươi cũng muốn đi Trục Tinh chi hải?” Nhíu nhíu mày, Khôi Hiểu tông chủ nhịn không được nhìn tới: “Không biết Lưu huynh, là cái nào tông môn thiên tài? Xin lỗi ta kiến thức nông cạn, trước đó thực sự chưa từng nghe qua cái tên này!”"),i("br"),i("br"),n._v("Trục Tinh chi hải, sợ làm cho mâu thuẫn, bình thường cao thủ rất ít đi qua, đối phương lại chủ động đi, liên tưởng đến cây cầu thông thiên bất cứ lúc nào cũng sẽ mở ra, vị này, sẽ không phải là... Một cái nào đó tông môn muốn đi khiêu chiến thiên tài đi!"),i("br"),i("br"),n._v("Thật muốn như vậy, phía bên mình Hồng Vũ trưởng lão, muốn chiến thắng, chỉ sợ rất khó!"),i("br"),i("br"),n._v("Tiên Quân... Cứ việc tấn cấp không quá dễ dàng, nhưng tập hợp một tông lực lượng, để hắn đột phá, có lẽ còn là không khó!"),i("br"),i("br"),n._v("“Ta... Chính là tán tu!” Trương Huyền vội vàng ôm quyền."),i("br"),i("br"),n._v("Nói là Lăng Vân kiếm các, rất dễ dàng bị người liên tưởng đến Thần chi kiếm ý vị kia, nói là Vạn Thú môn... Lại dễ dàng bị nghĩ đến là vị kia tân nhiệm tông chủ..."),i("br"),i("br"),n._v("Còn không bằng nói tiếp là tán tu, tận lực khiêm tốn một ít."),i("br"),i("br"),n._v("Kiếm các gây ra động tĩnh, bị đuổi giết, Vạn Thú môn gây ra động tĩnh, xuất hiện đồng dạng một màn..."),i("br"),i("br"),n._v("Hiện tại muốn làm, chính là che kín ánh sáng, không để cho người khác phát giác."),i("br"),i("br"),n._v("“Tán tu?” Khôi Hiểu tông chủ ánh mắt sáng lên, ngay sau đó lông mày nhíu một cái, hỏi: “Không biết... Lưu huynh đi Trục Tinh chi hải, vì chuyện gì?”"),i("br"),i("br"),n._v("“Là chút việc tư...” Trương Huyền lắc đầu."),i("br"),i("br"),n._v("“Việc tư...” Khôi Hiểu tông chủ lúc này mới thở phào nhẹ nhõm."),i("br"),i("br"),n._v("Nếu là việc tư, liền cùng Thông Thiên kiều không quan hệ, hẳn không phải là những tông môn khác người."),i("br"),i("br"),n._v("“Đã Lưu huynh, là tán tu, không biết... Có hứng thú hay không, gia nhập chúng ta Thất Tinh lâu?”"),i("br"),i("br"),n._v("Dừng lại một chút, Khôi Hiểu tông chủ ánh mắt lấp lánh nhìn qua."),i("br"),i("br"),n._v("“Gia nhập Thất Tinh lâu?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("Làm sao đối phương cũng phải hắn gia nhập?"),i("br"),i("br"),n._v("Đi vào Bích Viễn thành về sau, một mực rất biết điều ah!"),i("br"),i("br"),n._v("Cũng liền vừa rồi liên tục chiến đấu hơn mười tràng, đánh thắng một ít thực lực không mạnh tiểu nhân vật mà thôi... Loại trình độ này chiến đấu, hẳn là sẽ không dẫn tới một vị tông chủ coi trọng đi!"),i("br"),i("br"),n._v("“Không sai, chỉ cần ngươi trở thành chúng ta Thất Tinh lâu trưởng lão, thay thế Thất Tinh lâu cùng người giao đấu một hồi, ta có thể tập hợp tất cả lực lượng, để ngươi trong mười ngày, đột phá đến Tiên Quân đỉnh phong!”"),i("br"),i("br"),n._v("Khôi Hiểu tông chủ cười nói."),i("br"),i("br"),n._v("“Giúp ta đột phá Tiên Quân đỉnh phong?”"),i("br"),i("br"),n._v("Trương Huyền đồng tử co rụt lại."),i("br"),i("br"),n._v("Hắn gấp gáp như vậy, đi Trục Tinh chi hải, thứ nhất, vì dò xét Thần Huyết thạch sự tình, thứ hai, chính là muốn tìm đến tăng lên vu hồn công pháp, mau chóng đột phá Tiên Quân!"),i("br"),i("br"),n._v("Liên tục bị Thần điện vây giết hai lần, liền xem như hắn, cũng cảm nhận được nồng đậm áp lực, nhất định phải nhanh tăng thực lực lên."),i("br"),i("br"),n._v("Đi vào Thượng Thương hơn hai mươi ngày, mới từ phá toái hư không sơ kỳ, tấn cấp đến Chân Tiên đỉnh phong, đối với người khác tới nói, nhanh không gì sánh kịp, đối với hắn mà nói, vẫn còn có chút quá chậm!"),i("br"),i("br"),n._v("Dù sao, thời gian cùng danh sư đại lục khác biệt, nơi này hơn hai mươi ngày, người sau, đã qua tiếp cận một năm."),i("br"),i("br"),n._v("“Không sai!” Khôi Hiểu tông chủ gật đầu."),i("br"),i("br"),n._v("Đột phá Tiên Quân, đối bất kỳ một cái nào Chân Tiên cảnh, đều có hay không có thể ngăn cản lực hấp dẫn."),i("br"),i("br"),n._v("“Ngươi nói giao đấu, là cái gì? Có hay không nguy hiểm tính mạng?”"),i("br"),i("br"),n._v("Dừng lại một chút, Trương Huyền nói."),i("br"),i("br"),n._v("Thiên hạ không có cơm trưa miễn phí."),i("br"),i("br"),n._v("Để hắn đột phá Tiên Quân, khẳng định không dễ dàng như vậy, đã đối phương đáp ứng, làm điều kiện trao đổi, khẳng định cũng không có đơn giản như vậy."),i("br"),i("br"),n._v("“Yên tâm đi, chỉ là sáu đại tông môn liên hợp cùng một chỗ giao đấu, tất cả mọi người là tương đồng cấp bậc, không có nguy hiểm tính mạng! Chỉ cần nhận thua, không có người sẽ đuổi tận giết tuyệt!” Khôi Hiểu tông chủ nói: “Hơn nữa, nếu như chuyện này làm rất tốt, vô cùng có khả năng kế thừa tông chủ chi vị, trở thành sáu đại tông môn một trong, Thất Tinh lâu chân chính người cầm lái!”"),i("br"),i("br"),n._v("Sáu đại tông môn cường hãn nhất hậu bối, tuyển chọn ra đi vào Thông Thiên kiều làm chủ nhân viên, cơ bản đều là chạm đến là thôi, bị thương cũng sẽ không! Chớ nói chi là nguy hiểm!"),i("br"),i("br"),n._v("Còn, đi vào Thông Thiên kiều khiêu chiến Thần điện võ sĩ..."),i("br"),i("br"),n._v("Có rất nhiều đột phá Ngụy Thần cơ hội, tuy là nguy hiểm, chỉ khi nào thành công, kết quả lại là không có gì sánh kịp."),i("br"),i("br"),n._v("Tựa như hắn, cùng Vạn Thú môn Tần Nguyên môn chủ đồng dạng, đi vào nơi này, mới đạt tới cảnh giới bây giờ."),i("br"),i("br"),n._v("Chỉ cần đột phá Ngụy Thần, Thất Tinh lâu tông chủ chi vị khẳng định chạy không thoát."),i("br"),i("br"),n._v("“Kế thừa tông chủ chi vị?”"),i("br"),i("br"),n._v("Lắc đầu, Trương Huyền nhớ tới một việc, mỉm cười, nhìn lại: “Cái này liền thôi... Chỉ cần ngươi nói giao đấu không nguy hiểm, ta trở thành Thất Tinh lâu trưởng lão cũng không sao... Mặt khác, trở thành trưởng lão, Thất Tinh trong lâu tàng thư, nên cũng có thể xem đi?”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2039"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2041"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);