(window.webpackJsonp=window.webpackJsonp||[]).push([[1178],{1533:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2032-nguy-than-canh-bi-an"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2032-nguy-than-canh-bi-an"}},[n._v("#")]),n._v(" Chương 2032: Ngụy Thần cảnh bí ẩn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Thần điện trợ giúp?”"),t("br"),t("br"),n._v("Trương Huyền không hiểu."),t("br"),t("br"),n._v("“Muốn trở thành Thần Linh, cần Thần Linh công nhận! Đây cũng là sáu đại tông môn, đều muốn tiến vào Thần điện, cướp đoạt ‘Thần’ chữ nguyên nhân! Có chữ, dù là chỉ có nửa cái, cũng chờ tại có tấn cấp Ngụy Thần vốn liếng...”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ giải thích nói."),t("br"),t("br"),n._v("Trương Huyền hiểu được."),t("br"),t("br"),n._v("Khó trách, sáu đại tông môn đều nghĩ đến có thể cướp đoạt “Thần” chữ, vốn cho rằng, chỉ là vì êm tai, hiện tại xem ra, là vì trở nên càng thêm cường đại."),t("br"),t("br"),n._v("Cũng coi như hiểu, vì sao hắn Vạn Thú môn tông chủ, Lăng Vân kiếm các cùng Thất Tinh lâu trưởng lão tới vì sao dễ dàng như vậy."),t("br"),t("br"),n._v("Chỉ sợ tất cả mọi người gửi hi vọng cùng hắn, muốn cho hắn, cướp đoạt “Thần” chữ, một khi thành công, trong tông môn, không cách nào đột phá Tiên Quân đỉnh phong, cũng có thể đột phá, bất luận tuổi thọ, còn là tu vi, đều sẽ gia tăng thật lớn."),t("br"),t("br"),n._v("Hắn như thành công không được, đối phương nhất định cũng không cách nào hoàn thành."),t("br"),t("br"),n._v("“Lăng Vân kiếm các, chỉ lấy được nửa chữ, không cách nào quan danh tông môn, chỉ có thể làm ra một cái Kiếm Thần đường...”"),t("br"),t("br"),n._v("Nhịn không được hỏi: “Nửa chữ, cũng có thể để cho người ta đột phá Tiên Quân?”"),t("br"),t("br"),n._v("“Có thể, có điều, trình độ khó khăn so một chữ, lớn rất nhiều! Toàn bộ đại lục, cũng chỉ có Thông Thần điện, đạt được trọn một chữ, mới có thể chân chính sinh ra Ngụy Thần, cho nên, sáu đại tông môn, cũng không dám phản bác, lấy đứng đầu!”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ nói."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Sáu đại tông môn, đều có phạm vi, đều tự phạm vi bên trong, những tông môn khác, không thể nhúng tay, có thể thông Thần điện, lại trải rộng cả thương, nguyên lai một cái “Thần” chữ, đáng sợ như vậy."),t("br"),t("br"),n._v("“Không có chữ, liền không cách nào đột phá, nhưng ta quan sát, tông chủ tu vi, có vẻ như sớm đã đạt đến Ngụy Thần cảnh...”"),t("br"),t("br"),n._v("Trương Huyền không hiểu."),t("br"),t("br"),n._v("Sáu đại tông môn tông chủ, hắn gặp qua ba cái, đều là Ngụy Thần cảnh cường giả."),t("br"),t("br"),n._v("  Nếu là không có chữ không cách nào đột phá, bọn họ lại là làm sao đi đến loại cảnh giới này?"),t("br"),t("br"),n._v("“Vậy thì dính dáng chúng ta muốn tìm ngươi nguyên nhân!” Khôi Hiểu tông chủ mỉm cười."),t("br"),t("br"),n._v("“Tìm ta?”"),t("br"),t("br"),n._v("“Mỗi hơn trăm năm, Thần điện, đều sẽ xuất hiện một tòa Thông Thiên kiều, Di Khí đại lục người, thông qua đạo này cầu, có thể đi vào Thần điện, từ đó có cơ hội cướp đoạt Thần chữ... Chiến thắng một hồi, liền có liền sẽ hấp thu Thần Linh chi khí, đột phá Ngụy Thần... Ta chính là trăm năm trước, tiến vào bên trong người khiêu chiến!”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Chiến thắng một hồi, có cơ hội hấp thu Thần Linh chi khí, hơn nữa đột phá, cái kia... Người thất bại đâu?”"),t("br"),t("br"),n._v("“Người thất bại... Cơ bản tại chỗ liền sẽ tử vong!”"),t("br"),t("br"),n._v("“Tử vong?” Trương Huyền ngẩn ngơ: “Cuối cùng sẽ không, sáu đại tông môn tông chủ, đều chiến thắng qua một hồi đi...”"),t("br"),t("br"),n._v("Tần Nguyên môn chủ là Ngụy Thần, Hàn Kiếm Thu là Ngụy Thần, trước mắt vị này cũng là Ngụy Thần, còn lại ba đại tông môn tông chủ, cho dù chưa thấy qua, tu vi khẳng định cũng không thấp, chẳng lẽ, bọn họ đều chiến thắng qua?"),t("br"),t("br"),n._v("Thần điện võ sĩ, hắn tới giao thủ qua, biết đáng sợ."),t("br"),t("br"),n._v("Cùng cấp bậc, có thể xưng vô địch, tuyệt không phải người bình thường có thể đối phó, coi như Hàn Kiếm Thu, trước mắt vị này Khôi tông chủ cũng không được, một cái chiến thắng, vận khí tốt, sáu cái đều chiến thắng qua, rất không có khả năng đi!"),t("br"),t("br"),n._v("“Sáu đại tông môn tông chủ, là đều chiến thắng qua một hồi, nhưng... Cũng không phải cùng một đám người khiêu chiến! Giống Trục Tinh cung Đỗ Thanh Diên, là ngàn năm trước thành tựu Ngụy Thần, đằng sau Trục Tinh cung nhiều lần xung kích, tất cả đều là thất bại!”"),t("br"),t("br"),n._v("“Ngàn năm trước?”"),t("br"),t("br"),n._v("Trương Huyền đồng tử co rút lại: “Ngụy Thần có thể sống lâu như vậy?”"),t("br"),t("br"),n._v("Bình thường Tiên Quân, cùng Hư Tiên đồng dạng, ba trăm tuổi thọ là cực hạn, vị này Đỗ Thanh Diên lại là ngàn năm trước thành tựu Ngụy Thần, chẳng phải cho thấy, sống hơn một ngàn năm?"),t("br"),t("br"),n._v("Đổi lại danh sư đại lục, chính là hơn một vạn tuổi!"),t("br"),t("br"),n._v("Quả thực đáng sợ."),t("br"),t("br"),n._v("“Ngụy Thần bình thường là 500 tuổi thọ mạng, sống không được lâu như vậy, Trục Tinh cung người, thể chất đặc thù, cho nên, mới có thể sống càng lâu, dù vậy, cũng gần như tuổi thọ nhanh đến cuối cùng, không có gì bất ngờ xảy ra, sống không được mấy năm... Đồng dạng còn có Tần Nguyên môn chủ, tuổi thọ cũng sắp đến cực hạn!”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ lắc đầu: “Cho nên, bọn họ đều hi vọng lần này Thông Thiên kiều, có thể lần nữa sinh ra mới Thần Linh, trấn áp tông môn, nếu không, sáu đại tông môn lịch sử tất nhiên sẽ sẽ sửa chữa...”"),t("br"),t("br"),n._v("Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("Khó trách Tần Nguyên môn chủ như vậy quả quyết để mình làm tông chủ, nguyên lai là nguyên nhân này."),t("br"),t("br"),n._v("Tuổi thọ gần tới, chỉ có thể ký thác vào bản thân vị này “Thiên tài” trên người, hy vọng có thể thông qua Thông Thiên kiều, thành tựu Ngụy Thần."),t("br"),t("br"),n._v("Dù sao, không còn Ngụy Thần cảnh trấn áp, địa bàn sớm muộn đều sẽ bị người khác cướp đoạt, thiên tài cũng sẽ bị cướp đi, dần dà, coi như những tông môn khác không ra tay đối phó, môn phái này cũng sẽ biến thành nhị lưu thế lực, từ đó biến mất tại dòng sông lịch sử."),t("br"),t("br"),n._v("“Khôi tông chủ trước đó nói, cùng những tông môn khác tỷ thí, hơn nữa không có nguy hiểm, đây là ý gì?”"),t("br"),t("br"),n._v("Trong lòng cảm xúc, hỏi lần nữa."),t("br"),t("br"),n._v("Trước đó đối phương, rõ ràng nói qua, để hắn tham gia chính là cùng những tông môn khác tỷ thí, mà không phải Thông Thiên kiều."),t("br"),t("br"),n._v("“Ngươi không phải mới vừa kỳ lạ, vì sao sáu đại tông môn, đều có Ngụy Thần cảnh cường giả ư?” Khôi Hiểu tông chủ giải thích nói: “Trấn thủ Thông Thiên kiều, là năm vị Thần điện võ sĩ, nếu như ngươi có thể tại sáu đại tông môn trong tỉ thí, trổ hết tài năng, trở thành những tông môn khác tôn sùng thủ lĩnh, liền có cơ hội lựa chọn... Để năm cái khác người đối phó võ sĩ, bản thân đi vào Thần điện, cũng chính là không cần chiến đấu, liền chiến thắng một hồi... Từ đó có cơ hội thành tựu Ngụy Thần...”"),t("br"),t("br"),n._v("“Bản thân đi vào? Luân không? Cái kia...” Trương Huyền giật mình: “Năm người khác?”"),t("br"),n._v("“Bọn họ cơ bản đều sẽ tử vong!” Khôi Hiểu tông chủ nói: “Cho nên, sáu đại tông môn hậu bối, nếu như thực lực, không kém nhiều, bình thường cũng không cần luận võ, dựa theo trình tự, chọn lựa một cái là được, nói cách khác, năm nay Lăng Vân kiếm các, sang năm chính là Vạn Thú môn, năm sau chính là chúng ta Thất Tinh lâu... Nguyên nhân chính là đều có cơ hội, mọi người mới có thể giúp đỡ cho nhau, cùng đối kháng Thần điện... Sáu đại tông môn cũng có thể duy trì một cái tiềm ẩn cân bằng!”"),t("br"),t("br"),n._v("“Đương nhiên, nếu là thực lực chênh lệch rất lớn, trình tự tự nhiên muốn bị đánh loạn! Liền giống như lúc trước Khổng điện chủ, cùng với Lăng Vân kiếm các khai phái tổ sư, vượt qua những người khác quá nhiều, cho dù không cần thủ lĩnh danh ngạch, đồng dạng có thể xông qua Thông Thiên kiều, đi vào Thần điện! Loại tình huống này, thành tựu Ngụy Thần cơ hội sẽ trở nên rất lớn, coi như sáu người không có khả năng đều thành công, chí ít cũng có thể thành tựu hai, ba vị!”"),t("br"),t("br"),n._v("Trương Huyền lần nữa gật đầu."),t("br"),t("br"),n._v("Những tông môn khác người nhìn thấy bản thân, kích động như vậy, trực tiếp ban tặng thân phận trưởng lão, đủ loại tài nguyên không keo kiệt cung cấp, hiển nhiên đều là muốn cho bản thân, thay bọn họ tranh đoạt danh ngạch, thành tựu Ngụy Thần."),t("br"),t("br"),n._v("Bảo vệ một tông trăm năm an toàn."),t("br"),t("br"),n._v("Loại cảm giác này, thật giống kiếp trước nhìn qua một quyển sách, tuyển chọn tông chủ đi hiệp khách đảo."),t("br"),t("br"),n._v("Chỉ cần có người nguyện ý uống cháo mồng 8 tháng chạp, liền có thể làm tông chủ, căn bản không quản ngươi phẩm hạnh làm sao, có hay không trung thành..."),t("br"),t("br"),n._v("“Lưu trưởng lão, đến lúc đó nhất định muốn dùng hết toàn lực, chúng ta cũng sẽ toàn lực ủng hộ, giúp ngươi mau chóng đột phá Tiên Quân đỉnh phong!” Đem nên nói toàn bộ nói rõ ràng, Khôi Hiểu tông chủ một mặt chờ mong nhìn qua."),t("br"),t("br"),n._v("Tương đồng thực lực, đơn đấu hắn, đại trưởng lão đám người, mảy may mỏi mệt đều không có, một chiêu liền có thể chiến thắng, một khi tham gia tỷ thí lần này, nghiền ép những tông môn khác đã thành kết cục đã định."),t("br"),t("br"),n._v("Đến lúc đó, thành tựu Ngụy Thần, thậm chí còn có khả năng vì tông môn cướp tới nửa chữ, vậy thì thật ghi tên sử sách, thiên thu trăm đời."),t("br"),t("br"),n._v("“Yên tâm đi!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nếu không phải đối phương vô tư dâng hiến lực lượng, hắn không có khả năng đột phá tu vi hiện tại, trở thành chân chính Tiên Quân."),t("br"),t("br"),n._v("Phần ân tình này, đủ có thể khiến hắn toàn lực xuất thủ."),t("br"),t("br"),n._v("Lời nói rõ, không tại nhiều nói, phi hành Tiên thú toàn lực tăng tốc, không đến nửa ngày, một mảnh to lớn đại dương xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("“Trục Tinh chi hải đến...”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ đứng dậy."),t("br"),t("br"),n._v("Trương Huyền cũng tới đến phía trước cửa sổ."),t("br"),t("br"),n._v("Chỉ thấy một mảng lớn màu xanh thẳm mặt nước hiện lên ở trước mắt, từ xa nhìn lại, cùng bầu trời giao hòa cùng một chỗ, không phân khác biệt, mà bọn họ đang phi hành tại vũ trụ cùng đại dương ở giữa, ánh nắng phản xạ gợn sóng, đủ loại cá chim cùng bay lượn, đẹp không sao tả xiết."),t("br"),t("br"),n._v("“Đây là Trục Tinh chi hải?”"),t("br"),t("br"),n._v("Trương Huyền kinh ngạc."),t("br"),t("br"),n._v("Như vậy cảnh đẹp, đại lục ở bên trên nhiều cường giả như vậy, vậy mà đều không nguyện ý tới."),t("br"),t("br"),n._v("“Đúng vậy a, nếu như đến buổi tối, lái thuyền dạo chơi tại mặt biển, liền sẽ phát hiện, Tinh Thần tựa như rơi xuống, lái thuyền tốc độ nhanh lời nói, tùy thời cũng có thể đuổi theo, để cho người ta có loại truy đuổi xúc động, bởi vậy, mới gọi Trục Tinh chi hải...”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ gật đầu: “Có điều, càng mỹ lệ hơn địa phương, càng là nguy hiểm, chúng ta ngồi cái này, mang theo Thất Tinh lâu tiêu chí, lại thêm nhiều như vậy Tiên Quân cấp bậc Tiên thú lôi kéo, không có 【. 】 Tiên thú, giặc cướp đánh lén, đổi lại người bình thường, lại không được, khả năng mới đi vào Trục Tinh chi Hải Phạm vây, liền sẽ gặp được vô số nguy hiểm, sống đều sống không nổi, ai còn quan tâm cảnh đẹp?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("Trong hải dương, nguy cơ trùng trùng, ban đầu ở danh sư đại lục liền biết."),t("br"),t("br"),n._v("Phía dưới mặt biển thoạt nhìn yên tĩnh, trên thực tế ẩn chứa trong đó không biết bao nhiêu mạnh mẽ Thánh thú, Tiên thú, tùy tiện đi vào bọn chúng phạm vi, quả thực mười phần nguy hiểm."),t("br"),t("br"),n._v("“Trục Tinh cung ở nơi nào?”"),t("br"),t("br"),n._v("Nhìn một vòng, thấy Tiên thú càng bay đi càng xa, dần dần đi vào đại dương chỗ sâu, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Tại trong hải dương một tòa cực lớn hòn đảo, có một tòa Trục Tinh đảo, Trục Tinh cung cùng cái gọi là Di Khí đại lục thổ dân người, đều ở chỗ này...”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ giải thích."),t("br"),t("br"),n._v("Tiên thú tiếp tục phi hành, đi vào đại dương không đến một ngàn cây số, Trương Huyền quả nhiên thấy được không ít Tiên Quân cảnh Tiên thú, dạo chơi tại trong hải dương, hướng về phía trên không xe bay, rục rà rục rịch, bất cứ lúc nào cũng sẽ công kích."),t("br"),t("br"),n._v("Nếu không phải Khôi Hiểu tông chủ, thỉnh thoảng tỏa ra Ngụy Thần cảnh lực lượng tiến hành chấn nhiếp, chỉ sợ sớm đã có Tiên thú xông tới."),t("br"),t("br"),n._v("Khó trách không ai dám tới gần, còn không có nhìn thấy Trục Tinh cung người, chỉ những này Tiên thú, liền có thể để vô số người ngắm mà lùi bước."),t("br"),t("br"),n._v("Lại nửa ngày về sau, quả nhiên một tòa cực lớn hòn đảo xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("Đường kính chừng mấy ngàn cây số, phía trên đứng sừng sững lấy một tòa cực lớn thành trì, không biết bao nhiêu người sinh sống trong đó."),t("br"),t("br"),n._v("Từ xa nhìn lại, hòn đảo bên trên một cỗ đặc thù lực lượng lan tràn ra, tựa như tạo thành một cái đặc thù từ trường, cho người ta một loại người lạ chớ quấy rầy cảm giác."),t("br"),t("br"),n._v("“Loại khí tức này có chút quen thuộc...”"),t("br"),t("br"),n._v("Liếc mắt nhìn, Trương Huyền nhịn không được sửng sốt."),t("br"),t("br"),n._v("Loại này đặc thù từ trường, trước hắn ở nơi nào nhìn thấy qua, hết sức quen thuộc."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, tập trung tinh thần."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trương Huyền đồng tử đột nhiên co rút lại, thân thể xiết chặt."),t("br"),t("br"),n._v("“Đây là... Dị Linh tộc nhân khí tức?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2045"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2047"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);