(window.webpackJsonp=window.webpackJsonp||[]).push([[118],{471:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1083-phong-thanh-kiep"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1083-phong-thanh-kiep"}},[n._v("#")]),n._v(" Chương 1083: Phong thánh kiếp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1083: Phong thánh kiếp"),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Trực tiếp nhảy lên, Chương Dẫn Khâu thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Thất khiếu chảy máu, trên trái tim bị đâm một kiếm, đều có thể lý giải, khả năng thiên phú dị bẩm, trái tim không tại vốn vị trí... Có thể bị đập thành như vậy một đống, đến cùng làm sao sống được?"),t("br"),t("br"),n._v("Mấu chốt... Còn cản trở ngươi!"),t("br"),t("br"),n._v("Trương sư trang bức, ah phi, Trương sư đột phá, ngươi là đến cùng có bao nhiêu quan tâm a?"),t("br"),t("br"),n._v("Đang không biết nên trả lời như thế nào, nên rời đi hay là nên làm sao bây giờ, liền thấy trước mắt lần nữa một đạo kiếm quang lướt qua."),t("br"),t("br"),n._v("Trên không Thiên Diệp Vương dường như đã phát hiện cái này gia hỏa không có chết, tức giận đến lần nữa một đạo kiếm mang đâm tới."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Không có bất kỳ cái gì ngoài ý muốn, trước mắt cái này đống, từ đó bị cắt ra, lập tức biến thành hai đống, trên mặt đất không ngừng nhúc nhích."),t("br"),t("br"),n._v("“Ngươi, ngươi... Lần này chết a?”"),t("br"),t("br"),n._v("Bờ môi run rẩy, nhịn không được hỏi."),t("br"),t("br"),n._v("“Ngươi mới chết rồi, cả nhà ngươi đều đã chết...”"),t("br"),t("br"),n._v("Trên đất hai đống, tức giận đến oa oa kêu loạn, phía trên hai cái con ngươi, lập tức nhìn về phía trên không Thiên Diệp Vương: “Lão tử ở đây giả chết, xem như nể mặt ngươi, một lần một lần lại một lần, làm sao? Cho thể diện mà không cần phải không?”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trên đất hai đống, tựa như dung dịch, lần nữa dính chung một chỗ, thời gian nháy mắt lần nữa khôi phục trước đó bộ dáng, hình như một chút cũng không bị tổn thương, gào thét một tiếng, trực tiếp hướng trên không Thiên Diệp Vương vọt tới."),t("br"),t("br"),n._v("Thời gian nháy mắt, chiến đấu cùng một chỗ."),t("br"),t("br"),n._v("Bất quá, thực lực của hắn, cùng Thiên Diệp Vương dường như còn có chút chênh lệch, mới vừa bay đi lên không lâu, lần nữa bị một bàn tay quất xuống, kề sát trên mặt đất, như là bùn nhão."),t("br"),t("br"),n._v("Chương Dẫn Khâu còn chưa kịp bi thương, bùn nhão lại xông tới, tiếp tục cùng Thiên Diệp Vương đánh nhau."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không chỉ hắn ngẩn người, Ngô sư cùng Hàn hội trưởng mấy người cũng choáng váng."),t("br"),t("br"),n._v("Trương sư vị bằng hữu này, hình như đột nhiên có chút quá đáng, thân thể liền cùng cao su đồng dạng, đánh như thế nào đều vô sự... Đến cùng tu luyện thế nào đi ra?"),t("br"),t("br"),n._v("“Nhanh lên một chút động thủ, chỉ cần ngăn chặn hắn là được...”"),t("br"),t("br"),n._v("Giật mình một hồi, Ngô sư lần nữa la lên."),t("br"),t("br"),n._v("Giờ phút này không phải khiếp sợ ngẩn người thời điểm, nhất định phải ngăn cản Thiên Diệp Vương khống chế đồ cùng thủy tinh dung hợp, một khi thành công, tất cả mọi người muốn chết."),t("br"),t("br"),n._v("Mấy đại cao thủ liên hợp công kích, kiếm khí như cầu vồng, chân khí như sóng, cuồng bạo khí tức không ngừng cuồn cuộn, dường như đem trọn cái bầu trời đều tịch quyển."),t("br"),t("br"),n._v("Thấy có cái này đánh không chết quái nhân gia nhập, Ngô sư đám người tạm thời không có nguy hiểm, Chương Dẫn Khâu lần nữa hướng cách đó không xa Trương sư nhìn lại, chỉ thấy trong cơ thể hắn lực lượng nhất trọng tiếp lấy nhất trọng, vẫn như cũ không ngừng tăng lên, hình như không có cùng tận đồng dạng."),t("br"),t("br"),n._v("“Tốt hùng hậu...”"),t("br"),t("br"),n._v("Nhịn không được líu lưỡi."),t("br"),t("br"),n._v("Đổi lại người khác, đừng nói đột phá, riêng này đoạn thời gian chân khí tiêu hao, đều khẳng định sẽ đem bản thân ép thành người khô, kẻ trước mắt này, nhưng thật giống như người không việc gì, không thèm quan tâm."),t("br"),t("br"),n._v("Chỉ loại này chân khí dự trữ, mười cái hắn, đều xa xa không bằng."),t("br"),t("br"),n._v("Nhìn một hồi nhịn không được thở phào nhẹ nhõm, đang nghĩ lần nữa ngẩng đầu nhìn về phía trên không chiến đấu, đột nhiên nhìn thấy một thân ảnh, lông mày nhíu một cái: “Lạc sư, ngươi vì cái gì không đi chiến đấu?”"),t("br"),t("br"),n._v("Cùng Trương sư đám người cùng đi vị mỹ nữ kia Lạc sư, đang đứng tại cách đó không xa, không nhúc nhích, hình như trên không chiến đấu, việc không liên quan đến mình đồng dạng."),t("br"),t("br"),n._v("“Ta phải bảo vệ Trương Huyền!”"),t("br"),t("br"),n._v("Lạc sư thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Hắn chỉ là đột phá, có Phong Thánh đài thủ hộ, cũng không có vấn đề đi...”"),t("br"),t("br"),n._v("Chương Dẫn Khâu cau mày."),t("br"),t("br"),n._v("Mặc kệ thực lực ngươi làm sao, tất cả mọi người đang liều mạng, ngươi không động thủ ngược lại cũng thôi, còn nói cái gì bảo hộ Trương Huyền... Trương sư tại Phong Thánh đài đột phá, làm sao có thể có chuyện xảy ra?"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Tiếng nói của hắn còn không có kết thúc, liền nghe đến tiếng sấm vang rền, Phong Thánh đài bầu trời, đột nhiên xuất hiện đen kịt một màu mây đen, trong đó lôi điện cuồn cuộn, cho người ta một loại hủy diệt cảm giác."),t("br"),t("br"),n._v("“Đây là... Xuất khiếu kiếp? Tại sao lại ở chỗ này xuất hiện?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Chương Dẫn Khâu suýt chút nữa không dọa ngất đi qua."),t("br"),t("br"),n._v("Trong truyền thuyết, Nguyên Thần cảnh đột phá đến Xuất Khiếu cảnh, cần trải qua lôi kiếp, chỉ có như vậy, mới có thể cứng cáp hơn, rời khỏi thân thể cũng có thể đơn độc sống sót, không đến mức tử vong... Đây là một loại lột xác, cũng là một loại sống chết khảo nghiệm."),t("br"),t("br"),n._v("Rất nhiều Nguyên Thần cảnh cường giả, áp chế tu vi mấy trăm năm, cũng không dám xung kích cảnh giới cao hơn, chính là sợ bị lôi đình đánh hồn phi phách tán."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Nguyên Thần cảnh cùng Xuất Khiếu cảnh, cứ việc chỉ có một cái cấp bậc, sức chiến đấu nhưng lại có cách nhau một trời một vực, thậm chí cường giả số lượng cũng hoàn toàn khác biệt."),t("br"),t("br"),n._v("Chỉ có đạt tới xuất khiếu mới tính được là bên trên đại lục chân chính cường giả đỉnh phong."),t("br"),t("br"),n._v("Cái này cửu tử nhất sinh trải qua, được người xưng là xuất khiếu kiếp!"),t("br"),t("br"),n._v("Trương sư chỉ là Tòng Thánh xung kích Thánh vực, tại sao lại trải qua lợi hại như vậy thiên kiếp?"),t("br"),t("br"),n._v("“Người người đều muốn phong Thánh, nếu như không có đầy đủ thực lực, làm sao có thể giành được thiên địa công nhận?”"),t("br"),t("br"),n._v("Hừ một tiếng, Lạc sư một bước hướng về phía trước, nhìn lên trên trời mây đen, tú quyền xiết chặt, không có tiếp tục nói chuyện."),t("br"),t("br"),n._v("“Phong Thánh cần trước thời hạn trải qua... Xuất khiếu kiếp?”"),t("br"),t("br"),n._v("Nghe được đối phương, Chương Dẫn Khâu nuốt ngụm nước bọt, mặc dù hắn là hồn phách, không có khả năng chảy mồ hôi, vẫn như cũ cảm thấy sống lưng ẩm ướt thạp thạp."),t("br"),t("br"),n._v("Xuất khiếu kiếp, vô số nửa bước Xuất Khiếu cảnh cường giả đều không chịu nổi, trước mắt vị này chỉ là Tòng Thánh, làm sao ngăn cản?"),t("br"),t("br"),n._v("Không nói cái khác, ánh sáng trên không lôi đình, hắn gặp gỡ, liền sẽ hồn phi phách tán, tại chỗ tử vong."),t("br"),t("br"),n._v("“Đây không phải xuất khiếu kiếp, mà là phong thánh kiếp! Muốn phong Thánh, trước phải nhận kiếp...” Lạc Nhược Hi ánh mắt mang theo lo lắng: “Loại thiên kiếp này, chỉ có thể dựa vào chính hắn vượt qua, chỉ mong không nên gặp chuyện xấu...”"),t("br"),t("br"),n._v("“Phong thánh kiếp?”"),t("br"),n._v("Mọi người khiếp sợ tột đỉnh, Phong Thánh đài bên trên Trương Huyền, cũng hiểu được, con ngươi thu hẹp."),t("br"),t("br"),n._v("Khó trách phong Thánh khó như vậy, không phải không người không thể lĩnh ngộ Khổng sư tu luyện bí ẩn, mà là muốn mang hắn quan nhất định nhận hắn nặng, muốn phong Thánh, nhất định phải chống đỡ phong thánh kiếp!"),t("br"),t("br"),n._v("Cái này lôi kiếp, cũng không phải là dễ dàng như vậy vượt qua."),t("br"),t("br"),n._v("“Tới đi!”"),t("br"),t("br"),n._v("Biết chắc trốn không mất, chỉ có đối diện chống lại, mới có thể thành công, Trương Huyền cắn răng một cái, toàn thân huyệt đạo mở ra, vô số linh khí, lập tức như nước chảy tràn vào trong cơ thể, ngẩng đầu hướng trời cao nhìn sang."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Dường như cảm nhận được hắn chiến ý, trên bầu trời lôi vân cũng lại kìm nén không được, nhất đạo lớn bằng cánh tay lôi điện, trực tiếp bổ xuống."),t("br"),t("br"),n._v("Nếu như dùng để so sánh lời nói, trước đó lôi đình chi hải điện mang, cùng cái này so sánh, liền cùng mầm non gặp cổ thụ che trời, căn bản không tại cùng một cái cấp bậc bên trên."),t("br"),t("br"),n._v("Đạo này lôi điện còn chưa tới đến trước mặt, liền cho người ta một loại nghiền ép thân thể, tiêu diệt linh hồn cảm giác, hình như chỉ cần tu vi hơi yếu, liền có thể chống lại không được, liền hồn phách đều sẽ bị tại chỗ biến mất."),t("br"),t("br"),n._v("“Phong Thánh chia làm hồn kiếp, thể kiếp cùng tâm kiếp, một cái so một cái khó...”"),t("br"),t("br"),n._v("Một bên Lạc Nhược Hi, tú quyền xiết chặt."),t("br"),t("br"),n._v("Loại này kiếp số chỉ có thể dựa vào bản thân, những người khác không thể thay thay, nếu không, chẳng những không có chỗ tốt, còn có thể dẫn tới thiên địa tức giận hạ xuống càng lớn tai nạn."),t("br"),t("br"),n._v("“Hồn kiếp?” Nghe được nàng tự lẩm bẩm, Chương Dẫn Khâu hơi nghi hoặc một chút."),t("br"),t("br"),n._v("Đối với phong Thánh, hắn chỉ ở Khổng sư lưu lại văn tự bên trên thấy qua, vốn cho rằng đã hoàn toàn biết, hiện tại mới hiểu được, cùng trước mắt cô gái này kém rất xa."),t("br"),t("br"),n._v("“Ừm, hồn kiếp liền là linh hồn cần trải qua điện mang rèn luyện cùng tẩy lễ, hơi không cẩn thận liền sẽ hồn phi phách tán, là phong thánh kiếp cửa thứ nhất!”"),t("br"),t("br"),n._v("Lạc Nhược Hi giải thích."),t("br"),t("br"),n._v("“Linh hồn...”"),t("br"),t("br"),n._v("Chương Dẫn Khâu run rụt lại."),t("br"),t("br"),n._v("Hắn hiện tại là linh hồn trạng thái, biết linh hồn đối lôi điện có bao nhiêu e ngại, có thể nói là tự nhiên khắc tinh, đừng nói như vậy thô to lôi điện, cho dù nhất đạo nhỏ bé, đều không chịu nổi."),t("br"),t("br"),n._v("“Trương sư... Chỉ mong không có việc gì...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, vội vàng nhìn lại."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Lôi điện đi vào trong cơ thể, Trương Huyền lập tức cảm giác linh hồn của mình, giống như là bị ngọn lửa thiêu đốt, trước đó thật vất vả ngưng tụ hồn phách, một hồi lay động, hình như bất cứ lúc nào cũng sẽ tán loạn."),t("br"),t("br"),n._v("“Đau quá...”"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Loại này đau đớn lúc tới tự sâu trong linh hồn, thật giống như cả người bất cứ lúc nào cũng sẽ không chịu nổi."),t("br"),t("br"),n._v("Nếu không phải trên đường tới, chuyên môn tại dung nham, lôi đình chi hải, rèn luyện một phen, chỉ sợ lần này, đã hồn phi phách tán, chết không có chỗ chôn."),t("br"),t("br"),n._v("“Nhìn tới Khưu Ngô ngoài cung mặt dung nham cùng lôi đình chi hải, cũng không phải là cố ý hành động, mà là Khổng sư chuyên môn thiết kế tốt, lưu cho hậu nhân tôi luyện...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, liều mạng kiên trì, Trương Huyền giờ phút này rốt cục bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Bắt đầu, còn không hiểu, vì sao phải có dung nham cùng lôi đình chi hải như vậy trận pháp, tưởng là đối phương là cố ý, làm khó kẻ đến sau, hiện tại xem ra, liền là để cho người ta rèn luyện hồn phách, tốt chống đỡ cái này phong Thánh cửa thứ nhất."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Lôi điện ở trong người tàn phá bừa bãi, linh hồn bị đánh nát, sau đó gây dựng lại."),t("br"),t("br"),n._v("May mắn ở trong người, nếu là xuất khiếu, đừng nói ngưng tụ, chỉ sợ sớm đã tiêu tán giữa thiên địa."),t("br"),t("br"),n._v("Thân thể cứng ngắc, đau đớn kịch liệt bên dưới, Trương Huyền cảm giác ý thức có chút hôn mê."),t("br"),t("br"),n._v("Mặc dù sớm biết phong Thánh không đơn giản như vậy, nhưng tự mình trải qua, mới biết được đến cùng có bao nhiêu đáng sợ."),t("br"),t("br"),n._v("Loại này kịch liệt xé rách, thật giống như đem hắn linh hồn mỗi một cái sợi, đều từng chút một cắt ra, lần nữa tiến hành luyện chế."),t("br"),t("br"),n._v("“Chịu đựng! Nhất định phải chịu đựng... Khổng sư đều có thể kiên trì, ta vì cái gì không thể?”"),t("br"),t("br"),n._v("Đột nhiên, một cái tâm niệm lên cao."),t("br"),t("br"),n._v("Khổng sư là người, hắn cũng là người, dựa vào cái gì Khổng sư có thể làm được, hắn liền không chịu nổi?"),t("br"),t("br"),n._v("Tu luyện lâu như vậy Phong Thánh giải, giữ vững được lâu như vậy, nếu như nếu là bởi vì điểm ấy liền không kiên trì nổi, làm sao xứng đáng lâu như vậy cố gắng!"),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Tín niệm sinh ra, lôi điện vẫn như cũ vỡ vụn đau đớn, dĩ nhiên đã không có trước đó đáng sợ như vậy."),t("br"),t("br"),n._v("“Cửa thứ nhất... Xem như qua...”"),t("br"),t("br"),n._v("Thấy cảnh này, Lạc Nhược Hi thở phào nhẹ nhõm, lần nữa sinh ra lo lắng: “Cửa thứ hai thể kiếp, không riêng gì thân thể, còn có chân khí cùng đối tu luyện cảm ngộ, chỉ mong không có việc gì!”"),t("br"),t("br"),n._v("Thể kiếp, trên thân thể kiếp nạn."),t("br"),t("br"),n._v("Bao quát thân thể, chân khí, đối võ kỹ lý giải, chân khí có hay không tinh khiết... Cái này so vừa rồi hồn kiếp càng khó."),t("br"),t("br"),n._v("Cũng càng không dễ dàng chống đỡ."),t("br"),t("br"),n._v("Hồn kiếp mặc dù thống khổ, chỉ cần không xuất khiếu, tính nguy hiểm sẽ không quá lớn, nhưng thể kiếp không giống, một khi không chịu nổi, vô cùng có khả năng tại chỗ nổ thành thịt nát."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Sầu lo bên trong, trên bầu trời tích súc hồi lâu đạo thứ hai lôi điện, lần nữa lan tràn mà ra, bất cứ lúc nào cũng sẽ đánh xuống."),t("br"),t("br"),n._v("Lần này so vừa rồi càng to lớn hơn, một cái chừng lớn bằng bắp đùi, còn chưa tới đến trước mặt, liền mang theo kịch liệt nghiền ép cảm giác, hình như liền Phong Thánh đài đều có thể chém nát."),t("br"),t("br"),n._v("“Có thể đưa tới lôi kiếp, ngươi muốn phong Thánh nào có dễ dàng như vậy...”"),t("br"),t("br"),n._v("Trên bầu trời Thiên Diệp Vương, lúc này rốt cục đem Ngô sư đám người áp chế, nhìn thấy Trương Huyền thành công vượt qua đạo thứ nhất lôi kiếp, sầm mặt lại, nhịn không được lên tiếng rống to."),t("br"),t("br"),n._v("Một chưởng liền chụp tới!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1091"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1093"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);