(window.webpackJsonp=window.webpackJsonp||[]).push([[1180],{1535:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2034-phong-thiem-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2034-phong-thiem-truong-lao"}},[n._v("#")]),n._v(" Chương 2034: Phong Thiêm trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Lưu trưởng lão, vị này là tông môn Phong Thiêm trưởng lão, Phong trưởng lão, vị này là Lưu Dương trưởng lão, Thông Thiên kiều, do hắn ra tay!”"),t("br"),t("br"),n._v("Đi vào thuyền con bên trên, Khôi Hiểu tông chủ lẫn nhau giới thiệu một chút."),t("br"),t("br"),n._v("“Lưu Dương trưởng lão!”"),t("br"),t("br"),n._v("Nhìn về phía Trương Huyền, Phong Thiêm trưởng lão khẽ nhíu mày."),t("br"),t("br"),n._v("Vốn cho rằng là tông chủ đệ tử, không nghĩ tới muốn tham gia Thông Thiên kiều."),t("br"),t("br"),n._v("Quyết định như vậy, cũng liền cho thấy, trước mắt vị này là trong tông môn trăm tuổi phía dưới đệ nhất nhân?"),t("br"),t("br"),n._v("Chừng hai mươi tuổi đệ nhất nhân?"),t("br"),t("br"),n._v("Lúc nào, Thất Tinh lâu như vậy trò đùa?"),t("br"),t("br"),n._v("“Đã quyết định để ngươi tham gia Thông Thiên kiều, nhất định nổi bật, để cho người ta khâm phục, tại hạ vừa vặn có chút trên việc tu luyện không hiểu, không biết Lưu trưởng lão có thể hay không giải đáp thoáng cái!”"),t("br"),t("br"),n._v("Tầm mắt buông xuống, hai tay ôm quyền."),t("br"),t("br"),n._v("Khôi Hiểu tông chủ cũng không nói chuyện."),t("br"),t("br"),n._v("Tham gia Thông Thiên kiều, chỉ cần không ngã xuống, về sau liền sẽ là Thất Tinh lâu về sau tông chủ, Phong Thiêm làm tông chủ bí ẩn thực lực người cầm lái, nhất định phải hoàn toàn thần phục mới được."),t("br"),t("br"),n._v("Cho nên, đây là đối phương đối Lưu trưởng lão khảo giáo, cũng là Lưu trưởng lão xác lập uy tín thời cơ tốt nhất."),t("br"),t("br"),n._v("Một khi nắm chắc không được, Thất Tinh lâu tông chủ cấp dưới lực lượng cường đại nhất, sẽ đem khống chế không được, tông chủ chi vị, cũng liền không dễ dàng như vậy đạt được."),t("br"),t("br"),n._v("“Mời nói!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Phong Thiêm trưởng lão nói: “Thuộc hạ tu luyện Thất Tinh chưởng, luyện đến tầng thứ ba, liền khó mà tiếp tục, không biết Lưu trưởng lão nhưng có cái gì tốt đề nghị?”"),t("br"),t("br"),n._v("Thất Tinh chưởng, là Thất Tinh lâu trấn tông võ kỹ một trong, uy lực cực lớn, không ít trưởng lão đều tu luyện qua."),t("br"),t("br"),n._v("“Vấn đề này, có chút quá sơ lược...”"),t("br"),t("br"),n._v("Trương Huyền vẫn chưa trả lời, Khôi Hiểu tông chủ nhịn không được mở miệng."),t("br"),t("br"),n._v("Thuộc hạ muốn thần phục, khảo hạch thoáng cái, không gì đáng trách, có thể ít nhất cũng phải đem vấn đề nói rõ đi."),t("br"),t("br"),n._v("Tu luyện Thất Tinh chưởng, chỉ tới tầng thứ ba, liền không cách nào tiếp tục, nguyên nhân ít nhất có mấy trăm loại, bản thân tình huống loại hình cái gì đều không có nói, để cho người ta trả lời như thế nào?"),t("br"),t("br"),n._v("Cho dù là bản thân, đều không hiểu ra sao."),t("br"),t("br"),n._v("“Không sao cả!” Trương Huyền cắt đứt Khôi Hiểu tông chủ lời nói, mỉm cười: “Không nhìn lầm, Phong Thiêm trưởng lão tại tám mươi mốt năm trước, từng nội tức bị tổn thương, gần như tử vong đi!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão gật đầu, liếc mắt nhìn Khôi Hiểu tông chủ."),t("br"),t("br"),n._v("Tám mươi mốt năm trước, hắn bị kẻ thù vây công, suýt chút nữa tử vong, là trước mắt vị này cứu hắn, mới hoàn toàn hiệu trung, cam nguyện ẩn cư phía sau màn, vì đối phương vuốt lên tất cả gian nan khổ cực."),t("br"),t("br"),n._v("Chuyện này vốn là cơ mật, không nghĩ tới, Khôi tông chủ vậy mà nói cho đối phương biết."),t("br"),t("br"),n._v("“Ta... Chưa nói...” Biết ý tứ trong ánh mắt của hắn, Khôi Hiểu tông chủ vội vàng xua tay."),t("br"),t("br"),n._v("Trước khi đến, trước mắt vị này, liền ngươi là ai cũng không biết... Tại sao lại biết ngươi tám mươi mốt năm trước nhận qua trọng thương, ta cũng rất mê hoặc ah..."),t("br"),t("br"),n._v("Đang muốn giải thích cặn kẽ, một bên Trương Huyền liền ngắt lời hắn: “Không cần người khác nói, nhìn liền có thể nhìn ra, lúc ấy hao tốn không ít bảo vật, cho dù thực lực của ngươi cùng tu vi đều đã khôi phục, trên thực tế, lại chôn giấu cực lớn tai hoạ ngầm! Nếu như không nhìn lầm, ngươi tiên lực chạy thật **, cùng giang dũng huyệt lúc, sẽ cảm thấy toàn thân tê liệt, mà Thất Tinh chưởng, cái này hai nơi huyệt đạo rất là trọng yếu, thân thể tê liệt, kinh mạch tắc nghẽn, tự nhiên không cách nào luyện thành tầng thứ tư!”"),t("br"),t("br"),n._v("“Cái này...” Phong Thiêm trưởng lão sững sờ."),t("br"),t("br"),n._v("Đối phương nói những này là thuộc về chính hắn cơ mật, liền Khôi tông chủ đều không có nói qua, đối phương vậy mà mở miệng nói ra... Thật chẳng lẽ có thể nhìn ra?"),t("br"),t("br"),n._v("“Không chỉ có như vậy, ngươi bên trái cánh tay, hẳn là cũng không có trước đây ít năm tốt như vậy dùng a, có phải hay không sẽ xuất hiện cứng nhắc, không khỏi bản thân khống chế?” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Phong Thiêm trưởng lão thần sắc cứng lại."),t("br"),t("br"),n._v("Đối phương vẫn như cũ nói rất đúng."),t("br"),t("br"),n._v("Hắn tay trái hai năm này chẳng biết tại sao, thường xuyên không tự chủ run rẩy, trở nên cứng nhắc không gì sánh được, thủ đoạn không biết dùng bao nhiêu, một chút hiệu quả đều không có, thậm chí cũng tìm không ít danh y nhìn, đều nói không ra như thế về sau, đối phương làm sao biết được?"),t("br"),t("br"),n._v("“Ngươi vừa bắt đầu tu luyện công pháp vì Bích Ba công, chính là tiền bối quan sát sóng biếc sóng biển sáng tạo mà thành, nội tức bị tổn thương về sau, ngươi cảm thấy bộ công pháp này, uy lực không đủ, lại thêm chân khí trong cơ thể, tiêu hao hầu như không còn, cưỡng ép sửa chữa pháp quyết, chuyển thành hiện tại tu luyện Liệt Dương quyết... Uy lực là tăng cường, nhưng từ tu luyện bắt đầu, kinh mạch liền bị sóng biếc tẩm bổ, đột nhiên đổi thành Liệt Dương, như thoải mái đất đai phì nhiêu, bị mặt trời chói chang, hơn nữa không cách nào trợ cấp lượng nước... Ngươi cảm thấy sẽ xuất hiện loại tình huống nào?”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão đồng tử co rụt lại: “Sẽ dẫn đến đất đai khô nứt, phì nhiêu biến mất...”"),t("br"),t("br"),n._v("“Phì nhiêu biến mất là đơn giản nhất, thời gian lâu dài, sẽ biến thành tử địa, không có một ngọn cỏ!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Không có một ngọn cỏ? Sẽ không như thế nghiêm trọng đi!” Khôi Hiểu tông chủ cau mày."),t("br"),t("br"),n._v("Đối phương thay đổi công pháp, hắn là biết, lúc trước trước mắt vị này Phong trưởng lão lòng như tro nguội, chỉ cần có thể báo thù, cái gì đều nguyện ý làm."),t("br"),t("br"),n._v("Cho nên hắn mới mạo hiểm ban cho Liệt Dương quyết, trong quá trình tu luyện, là xuất hiện qua không đúng, nhưng hắn đi đến Ngụy Thần về sau, đặc biệt cho đối phương điều dưỡng qua, cũng hao tốn không biết bao nhiêu bảo vật, vốn cho rằng tất cả đều giải quyết, không nghĩ tới, còn có tai hoạ ngầm tồn tại."),t("br"),t("br"),n._v("“Thân thể luôn luôn cái có thể tiếp nhận hạn độ!” Trương Huyền hai tay chắp sau lưng: “Hắn nội tức toàn bộ bị phá hủy, bản thân liền là đại thương tích, loại tình huống này, không cố gắng ôn dưỡng, để thực lực phục hồi từ từ, lại cứng rắn tu sửa luyện pháp quyết, không có bị mất mạng tại chỗ, đã chứng minh vận khí tốt, các ngươi tiêu phí bảo vật nhiều!”"),t("br"),t("br"),n._v("“Nói thật cho ngươi biết a, cánh tay cứng nhắc, chỉ là vừa bắt đầu triệu chứng, trong vòng hai năm, hai chân của ngươi cũng sẽ cứng nhắc, xương cột sống cũng sẽ biến hình, đi bộ đều khó khăn, không nhanh chóng nghĩ biện pháp giải quyết, trong vòng năm năm, kinh mạch toàn thân đều sẽ không chịu nổi lực lượng, toàn bộ rách nát! Một thân huyền công, hủy hoại chỉ trong chốc lát...”"),t("br"),n._v("“Ngươi... Nói nhưng mà thật?”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão mày nhăn lại."),t("br"),t("br"),n._v("Hắn tuy là cánh tay trái xuất hiện cứng nhắc, nhưng cũng không tin tưởng đối phương nói là sự thật."),t("br"),t("br"),n._v("Quá mức nghe rợn cả người."),t("br"),t("br"),n._v("Trong vòng năm năm tu vi hủy hết... Bản thân vì sao một điểm cảm giác đều không có?"),t("br"),t("br"),n._v("“Không tin, ta có thể cho ngươi thử một chút!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền chập ngón tay làm kiếm, đột nhiên đâm tới."),t("br"),t("br"),n._v("Hắn chỉ là phổ thông chiêu số, không có kiếm khí, cũng không có tiên lực, chỉ là nhẹ nhàng điểm một cái, rơi vào Phong Thiêm trưởng lão nơi bả vai, người sau liền cảm thấy toàn thân cứng ngắc, thật giống bị định trụ một m giống như, trên đầu toát ra mồ hôi lạnh, nhịn không được lui về phía sau một bước, muốn né tránh, chỉ thấy tay của đối phương chỉ theo sát mà đến, hướng về ngực mặt khác một chỗ."),t("br"),t("br"),n._v("Vừa định động đậy, trong cơ thể khí tức tựa như bị kiềm chế, kinh mạch một hồi như tê liệt đau đớn."),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Một ngụm máu tươi phun ra, vẻ mặt có chút uể oải."),t("br"),t("br"),n._v("“Hiện tại cảm giác làm sao?” Thu tay lại mà đứng, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Ta... Cảm thấy kinh mạch toàn thân bất cứ lúc nào cũng sẽ rách nát...” Hít thở mấy ngụm, Phong Thiêm trưởng lão lần nữa nhìn về phía thanh niên trước mắt, ánh mắt lộ ra nồng đậm khâm phục: “Còn xin Lưu trưởng lão chỉ điểm sai lầm, giúp ta giải quyết tai hoạ ngầm, nếu như thành công, ta cam tâm tình nguyện, phụng ngươi vì tông chủ, lại không hai lòng!”"),t("br"),t("br"),n._v("Chỉ nhìn một cái, liền nhìn ra trong cơ thể hắn nhiều vấn đề như vậy, hơn nữa tìm tới nguyên nhân, bất luận thực lực còn là nhãn lực, đều đáng kính nể."),t("br"),t("br"),n._v("“Rất đơn giản, đem ngươi Bích Ba công nhặt lên, lại tu luyện từ đầu!”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("“Lại tu luyện từ đầu? Cái này... Làm sao có thể?” Lần này không chỉ Phong Thiêm trưởng lão không hiểu, Khôi Hiểu tông chủ cũng sửng sốt."),t("br"),t("br"),n._v("Hắn hiện tại tu luyện Liệt Dương quyết, lại tu luyện Bích Ba công, rất dễ dàng dẫn tới hai loại công pháp xung đột, hơi không cẩn thận, đừng nói trị liệu thương thế, rất có thể, tại chỗ tẩu hỏa nhập ma, trực tiếp tử vong."),t("br"),t("br"),n._v("“Dĩ nhiên không phải tu luyện trước ngươi Bích Ba công, mà là ta sửa chữa sau...”"),t("br"),t("br"),n._v("Trương Huyền cổ tay khẽ đảo, lấy ra một cái ngọc bài, ngón tay ở phía trên nhẹ nhàng điểm một cái, một đạo ý niệm tiến vào bên trong, ghi chép một bộ hoàn chỉnh pháp quyết."),t("br"),t("br"),n._v("Phong Thiêm trưởng lão nhận lấy, liếc mắt nhìn, trong cơ thể tiên lực kìm lòng không được dựa theo vận chuyển, một lát sau, phun ra ba ngụm máu đen, cảm thấy toàn thân một hồi nhẹ nhõm, lại không còn trước đó ràng buộc, cánh tay cũng không tại cứng ngắc lại."),t("br"),t("br"),n._v("“Cái này...” Nắm đấm xiết chặt, lần nữa nhìn qua, đã không phải là bội phục, mà là kinh hoàng cùng tôn sùng."),t("br"),t("br"),n._v("Tu vi đánh tới hắn loại thực lực này, đối với mình thân thân thể biết rất rõ ràng, đối phương ban cho công pháp, chỉ tu luyện một hồi, liền có loại hiệu quả này, thật muốn tuân thủ một cách nghiêm chỉnh lời nói, trong cơ thể tai hoạ ngầm, có thể bảo đảm toàn bộ giải trừ."),t("br"),t("br"),n._v("Quấy nhiễu hắn hơn tám mươi năm tai hoạ ngầm, hao tốn tông môn không biết bao nhiêu bảo vật, đều không có giải quyết, đối phương chỉ nhìn một cái, cho bộ pháp quyết liền triệt để loại bỏ..."),t("br"),t("br"),n._v("Phần này năng lực quả thực nghe rợn cả người!"),t("br"),t("br"),n._v("Không chỉ có hắn bộ dáng này, một bên Khôi Hiểu tông chủ cũng triệt để bối rối."),t("br"),t("br"),n._v("Trước đó còn lo lắng đối phương còn quá trẻ, không cách nào phục chúng, không nghĩ tới vừa ra tay cứ như vậy lợi hại."),t("br"),t("br"),n._v("Không chỉ tu luyện võ kỹ thiên phú cao mạnh, đối y đạo cùng tu luyện hiểu, vậy mà cũng đạt tới để cho người ta khó có thể tưởng tượng tình trạng, thậm chí hắn đều xa xa không kịp."),t("br"),t("br"),n._v("“Tốt, nghiêm ngặt dựa theo bộ công pháp này yêu cầu tu luyện, trong vòng nửa năm liền có thể giải quyết trong cơ thể toàn bộ tai hoạ ngầm, thậm chí có thể để cho tu vi của ngươi cao hơn một tầng!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Đối với hắn mà nói, tiện tay mà thôi mà thôi."),t("br"),t("br"),n._v("Đối phương điều động thuyền con đi lại, liền vận dụng võ kỹ, cho nên, liếc mắt một cái thấy ngay tiềm ẩn tai hoạ ngầm, kết hợp những ngày này nhìn qua bí tịch, cho ra một cái lúc nào phương pháp giải quyết, cũng không khó."),t("br"),t("br"),n._v("“Đa tạ Lưu trưởng lão!”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão khom người."),t("br"),t("br"),n._v("“Phía trước liền hẳn là ngươi lưu lại trận pháp cùng phong ấn a?”"),t("br"),t("br"),n._v("Biết trong lòng đối phương đã khâm phục vô cùng, nói tiếp, cũng không có cái gì ý nghĩa, Trương Huyền cười nhạt một tiếng, nhìn về phía trước."),t("br"),t("br"),n._v("Hai người nói chuyện công phu, thuyền con đã tại mặt biển du tẩu trên trăm cây số, một mảnh tựa như kính bộ dáng mặt nước, xuất hiện tại phía trước."),t("br"),t("br"),n._v("“Đây là Tiểu Kính hải, ta trận pháp cùng phong ấn, quả thực mai phục tại nơi này...”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão gật đầu."),t("br"),t("br"),n._v("Tiểu Kính hải, dưới nước ẩn chứa đặc thù bèo rong, tôn lên toàn bộ mặt nước tựa như kính, sắp đặt xuất trận pháp, phong ấn, Ngụy Thần cảnh cường giả đều không phát hiện được, trước mắt vị này, làm sao có thể nhìn ra?"),t("br"),t("br"),n._v("Ngay tại nghi ngờ, muốn hỏi thăm, thanh niên trước mắt âm thanh tiếp tục vang lên."),t("br"),t("br"),n._v("“Nhất Thủy Nhị Ba trận, đặt ở trong hải dương, quả thực có thể vàng thau lẫn lộn, để cho người ta không phát hiện được, uy lực cũng rất mạnh, nhưng muốn vây khốn Ngụy Thần cảnh cường giả, còn là yếu đi chút! Mấu chốt nhất là... Đối phương là quanh năm sinh hoạt ở trong nước sinh mệnh, đối dòng nước khống chế rất mạnh, trận pháp này, một khi bị đối phương phát giác, chỉ sợ không phải vây khốn, ngược lại có thể mượn trong đó sóng nước uy lực, thuận lợi chạy trốn, lại không cách nào đuổi kịp!”"),t("br"),t("br"),n._v("“Ngươi có thể nhìn ra ta bố trí trận pháp là cái gì...”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão ngẩn ngơ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2047"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2049"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);