(window.webpackJsonp=window.webpackJsonp||[]).push([[1181],{1536:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2035-tieu-kinh-hai-bi-an"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2035-tieu-kinh-hai-bi-an"}},[n._v("#")]),n._v(" Chương 2035: Tiểu Kính hải bí ẩn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Nhất Thủy Nhị Ba trận, lấy đặc chế giọt nước vì trận kỳ, sắp đặt tại trong hải dương, theo sóng nước, không ngừng nhảy, Ngụy Thần cảnh cường giả, dùng thần thức đặc biệt liếc nhìn, đều không phát hiện được."),t("br"),t("br"),n._v("Trước mắt vị này, mới đi đến trước mặt, liền nhìn ra... Hơn nữa nói ra trận pháp thiếu hụt, cái này nhãn lực... Quả thực kinh khủng!"),t("br"),t("br"),n._v("“Lưu trưởng lão, tại lân cận dạo chơi Ngụy Thần cảnh Tiên thú là một đầu Huyền Bối quy, không am hiểu tốc độ, Nhất Thủy Nhị Ba trận, lực lượng chấn động, vừa vặn có thể vây khốn đối phương...”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ xen vào nói."),t("br"),t("br"),n._v("Đã muốn bắt, trong hải dương Tiên thú, đối phương mượn thế nước, khẳng định đã sớm cân nhắc đến, sở dĩ, vẫn như cũ sử dụng trận pháp này, là bởi vì muốn bắt đối tượng là Huyền Bối quy, loại này Tiên thú, bản thân liền không quen dài tốc độ, cho dù có thế nước, khó mà mượn không nói, chỉ cần khống chế trong đó gợn sóng dày đặc, ngược lại có thể hình thành gông xiềng, đem hắn thân thể khổng lồ triệt để vây khốn."),t("br"),t("br"),n._v("“Huyền Bối quy hình thể to lớn, am hiểu phòng ngự, tốc độ không tốt, dùng Nhất Thủy Nhị Ba trận, quả thực rất tốt, nhưng... Không nên quên, nơi này là Tiểu Kính hải, tối nay là đêm trăng tròn, ánh trăng như vậy sáng rực...”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhìn nhau, Khôi Hiểu tông chủ và Phong Thiêm trưởng lão tất cả đều ngây người."),t("br"),t("br"),n._v("Nói trận pháp cùng Huyền Bối quy sự tình, ngươi nói Tiểu Kính hải cùng mặt trăng làm gì?"),t("br"),t("br"),n._v("“Các ngươi có suy nghĩ hay không, đầu này Huyền Bối quy, cũng không phải là cảnh giác, mà là... Đã sớm phát hiện ngươi bố trí trận pháp, chờ đợi đêm nay trăng tròn?” Trương Huyền nói."),t("br"),t("br"),n._v("Trước đó, Phong Thiêm trưởng lão nói, đầu này Ngụy Thần cảnh Tiên thú, vô cùng cảnh giác, hắn cũng có chút kỳ lạ."),t("br"),t("br"),n._v("Đối phương đều Ngụy Thần, muốn không bị bắt, ai có thể tổn thương nó?"),t("br"),t("br"),n._v("Như vậy tình huống còn đang do dự, khẳng định không phải cảnh giác!"),t("br"),t("br"),n._v("Tận mắt thấy trận pháp cùng mặt biển cảnh tượng, giờ mới hiểu được tới."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?” Khôi Hiểu tông chủ cũng nhịn không được nữa."),t("br"),t("br"),n._v("Đối phương nói chưa dứt lời, nói chuyện, càng thêm mê mang."),t("br"),t("br"),n._v("“Tiểu Kính hải, tựa như mặt kính, các ngươi hẳn là đều biết nguyên nhân, cũng không phải là tảo biển, mà là mảnh này mặt nước, sinh tồn một loại sinh vật, gọi !”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Hai người gật đầu."),t("br"),t("br"),n._v("Ngân diệp phù du, bọn họ đã sớm nghe nói qua, cái đầu vô cùng nhỏ, nhỏ bé cùng bụi bặm đồng dạng, toàn thân màu bạc, lúc này mới đem mặt biển tôn lên như là kính."),t("br"),t("br"),n._v("“Loại này phù du đồ ăn, cũng không phải là thực vật, cũng không phải động vật, mà là một loại đặc thù khoáng thạch năng lượng, vùng biển này phía dưới tất cả đều là loại này khoáng thạch, mới dẫn tới phù du một mực không rời đi, sáng tạo ra Tiểu Kính hải chỗ này kỳ lạ cảnh tượng!”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp."),t("br"),t("br"),n._v("Lần này chỉ có Phong Thiêm trưởng lão gật đầu."),t("br"),t("br"),n._v("Hắn đến địa phương này thời gian rất lâu, đặc biệt dò xét qua, quả thực cùng đối phương nói đồng dạng."),t("br"),t("br"),n._v("[ truyen cuа tui ʘʘ v"),t("br"),n._v("n ]  “Những này phù du, bình thường không có cái gì, nhưng một khi phía dưới khoáng thạch xuất hiện chấn động hoặc là biến cố, các ngươi cảm thấy sẽ xuất hiện tình huống gì?”"),t("br"),t("br"),n._v("“Cái này... Khoáng thạch xuất hiện biến cố, phù du khẳng định sẽ nghĩ biện pháp cấp cứu...”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ nói."),t("br"),t("br"),n._v("Bọn chúng sinh tồn căn cơ dao động, tự nhiên muốn bảo vệ."),t("br"),t("br"),n._v("“Không sai, khẳng định sẽ đoạt cứu, đại lượng phù du một khi toàn bộ động, ngươi xác định ngươi Nhất Thủy Nhị Ba trận, còn có tác dụng chỗ?”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("Phong Thiêm á khẩu không trả lời được."),t("br"),t("br"),n._v("Nhiều như vậy phù du, mỗi một cái đều cùng bụi bặm đồng dạng, một hai cái không quan trọng, vô số số lượng đồng thời động, cường đại tới đâu trận pháp đều sẽ trong nháy mắt sụp đổ, làm không cẩn thận còn biết trở thành bản thân ràng buộc, đừng nói bắt Tiên thú, làm không cẩn thận chính mình cũng sẽ được giam ở trong đó, lại không cách nào chạy trốn."),t("br"),t("br"),n._v("“Những quáng thạch này chôn sâu ở đáy biển không biết đã bao nhiêu năm, trước đến giờ không động tới, hẳn là sẽ không xuất hiện biến cố đi! Lại nói... Cùng trăng tròn có quan hệ gì?”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ hỏi."),t("br"),t("br"),n._v("Nếu như những quáng thạch này dễ dàng biến cố, khẳng định đã sớm xuất hiện, một mực không, chứng minh vô cùng ổn định."),t("br"),t("br"),n._v("“Trước kia là không có xuất hiện qua, không có nghĩa là hiện tại sẽ không xuất hiện, hơn nữa... Xuất hiện nguyên nhân, cùng các ngươi liên quan đến!” Trương Huyền nói."),t("br"),t("br"),n._v("“Cùng chúng ta liên quan đến?”"),t("br"),t("br"),n._v("Hai người nhìn nhau, lần nữa mê hoặc."),t("br"),t("br"),n._v("Cái này đều cái nào cùng cái nào ah, tại sao lại cùng chúng ta có liên quan rồi?"),t("br"),t("br"),n._v("“Huyền Bối quy, thích nhất chính là lóe sáng đồ vật, không có đoán sai, các ngươi đặt ở đáy biển, dùng để hấp dẫn nó, hẳn là một ít vô cùng trân quý ngân toản tinh thạch đi!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Loại này tinh thạch, đặt ở biển sâu, so dạ minh châu còn chói mắt hơn mấy trăm lần, dù là cách nhau vạn dặm, đều có thể thu hút Huyền Bối quy đến đây, nhưng mà... Các ngươi có bao giờ nghĩ tới, chỉ cần là năng lượng, cũng sẽ không bỗng dưng xuất hiện! Dạ minh châu cũng cần ban ngày hấp thu Thái Dương quang mang, mới có thể tại buổi tối thả ra quang minh! Ngân toản tinh thạch, như vậy chói mắt, năng lượng từ đâu mà đến? Không muốn nói với ta, là những này ngân diệp phù du, ngươi dám hấp thu những vật này năng lượng trong cơ thể, nó tuyệt đối sẽ tại chỗ đem ngươi nuốt lấy!”"),t("br"),t("br"),n._v("“Cái này...” Hai người ngẩn ngơ."),t("br"),t("br"),n._v("Bọn họ vẫn cho là, là từ phù du bên trong hấp thu năng lượng, chẳng lẽ không phải?"),t("br"),t("br"),n._v("Dường như nhìn ra bọn hắn ý nghĩ, Trương Huyền nói tiếp: “Cái này đồ vật, là từ khoáng thạch bên trong hấp thu lực lượng, mà đêm trăng tròn, trăng tròn giữa trời, chính là ngân diệp phù du, phản xạ ánh trăng mãnh liệt nhất thời điểm, lúc này, bọn chúng suy yếu nhất, đối năng lượng hấp thu, cũng lớn nhất! Lúc này, phía dưới lại toát ra một cái ngân toản tinh thạch, cũng đang hấp thu... Khoáng thạch năng lượng cung cấp có hạn, rất dễ dàng xuất hiện biến cố! Nhẹ thì, mặt đất lay động, nặng thì, Tiểu Kính hải sẽ xuất hiện vòng xoáy khổng lồ, thôn phệ lui tới thuyền sinh mệnh!”"),t("br"),t("br"),n._v("“Nếu là như vậy, ta trận pháp, quả thực sẽ mất đi tác dụng, nhưng... Huyền Bối quy khẳng định cũng sẽ nhận hạn chế, làm không cẩn thận, đồng dạng rơi vào trong đó không thể chạy trốn...” Phong Thiêm trưởng lão hỏi."),t("br"),t("br"),n._v("Những này phù du, một khi bạo động, đối với hắn trận pháp là có chỗ xấu, nhưng đối với Huyền Bối quy cũng chưa khỏi chỗ ah, vì sao nhất định muốn đợi đến đêm trăng tròn?"),t("br"),t("br"),n._v("“Không có cách nào chạy trốn?” Trương Huyền cười nhạo: “Ngươi quá coi thường Ngụy Thần cảnh Tiên thú! Nó phòng ngự vô địch, một khi xuất hiện bạo động, chỉ cần ba cái hô hấp, đối phương liền có thể nuốt lấy ngươi ngân toản tinh thạch, tinh thạch đi vào trong cơ thể nó, lại không phương pháp tiêu hóa, nó liền thành thôn phệ năng lượng cội nguồn, các ngươi cảm thấy những cái kia phù du sẽ làm sao?”"),t("br"),t("br"),n._v("“Khẳng định sẽ công kích nó...”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão nói một câu, đồng tử đột nhiên co rút lại: “Ngươi ý tứ... Huyền Bối quy, đã sớm phát hiện kế hoạch của ta, cố ý đợi đến đêm trăng tròn, không chỉ muốn phá đi trận pháp, còn muốn mượn cơ hội nuốt lấy những này ngân diệp phù du, thậm chí... Còn có ta?”"),t("br"),t("br"),n._v("Phù du đối Huyền Bối quy công kích, đương nhiên biết bị thôn phệ, lúc này trận pháp đã phá hoại, mình bị vây ở phù du bên trong, không thể chạy trốn... Vốn muốn đi săn, lúc này vô cùng có khả năng ngược lại biến thành con mồi!"),t("br"),t("br"),n._v("Nếu thực như thế, liền đáng sợ!"),t("br"),n._v("Bản thân vẫn cảm thấy, nắm giữ toàn cục, đối phương nhất định sẽ được lúc, không nghĩ tới, dĩ nhiên thẳng đến tại đối phương trong thiết kế."),t("br"),t("br"),n._v("“Ngươi? Thực lực của ngươi, hẳn là nó còn không để vào mắt, thật muốn ăn ngươi, đã sớm động thủ, nó càng nghĩ tới hơn có thể là... Khôi tông chủ!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Khôi Hiểu sững sờ."),t("br"),t("br"),n._v("“Nếu như ta không tại, ngươi bây giờ chỉ sợ đã đi vào đáy biển, chờ Huyền Bối quy mắc câu rồi đi!” Trương Huyền cười nói: “Các ngươi đợi đối phương, nếu như, lúc này, ngân diệp phù du toàn bộ vọt tới, phong tỏa ngươi toàn thân huyệt đạo cùng kinh mạch đâu?”"),t("br"),t("br"),n._v("“Cái này...” Khôi Hiểu tông chủ toàn thân cứng đờ."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Nếu như Lưu trưởng lão không đến, hắn rất có thể sẽ cùng Phong Thiêm trưởng lão đồng dạng, chờ ở trong biển, đến lúc đó, một khi phù du phát sinh rung chuyển, lấy bọn chúng hạt bụi nhỏ giống như thân thể, phủ kín ở huyệt đạo, để hắn tiên lực trong thời gian ngắn không cách nào vận chuyển, là vô cùng bình thường!"),t("br"),t("br"),n._v("Dù sao hắn không có Huyền Bối quy mạnh mẽ như vậy phòng ngự."),t("br"),t("br"),n._v("Lúc này, đầu này Ngụy Thần cảnh Tiên thú, nhân cơ hội đánh lén... Thực lực bản thân coi như rất mạnh, khẳng định cũng sẽ trúng chiêu!"),t("br"),t("br"),n._v("Đem đối phương nói tình huống, toàn bộ trong đầu thôi diễn một lần, Khôi Hiểu tông chủ thân thể càng ngày càng lạnh."),t("br"),t("br"),n._v("Nếu thực như thế, hắn bị giết, bị đối phương nuốt lấy tỷ lệ, có thể đủ đi đến tám thành!"),t("br"),t("br"),n._v("Nói cách khác..."),t("br"),t("br"),n._v("Cùng trước mắt vị này Lưu trưởng lão nói đồng dạng, thiết kế Huyền Bối quy đồng thời, đối phương cũng tại thiết kế bản thân... Cho rằng người ta là đơn giản con mồi, nằm mộng cũng nghĩ không ra, chân chính biến thành con mồi chính là mình!"),t("br"),t("br"),n._v("Một khi không có phát hiện, ngày hôm nay chết, vô cùng có khả năng chính là mình, coi như dựa dẫm bảo vật có thể chạy thoát, khẳng định cũng nguyên khí tổn hao nhiều!"),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("Khó trách có thể trở thành Ngụy Thần cảnh cường giả, những đại dương này sinh tồn Tiên thú, quả nhiên không thể coi thường..."),t("br"),t("br"),n._v("“Lưu trưởng lão, cái kia... Bây giờ nên làm gì?”"),t("br"),t("br"),n._v("Trầm tĩnh phút chốc, Phong Thiêm trưởng lão nhịn không được nhìn qua."),t("br"),t("br"),n._v("Giờ phút này hắn đối vị này Lưu trưởng lão phục sát đất, thậm chí đều vượt qua Khôi Hiểu tông chủ."),t("br"),t("br"),n._v("Không biết làm sao thời điểm, trực tiếp hỏi đi ra."),t("br"),t("br"),n._v("“Rất đơn giản, ngươi sắp đặt Nhất Thủy Nhị Ba trận giọt nước trận kỳ, nhưng còn có?” Trương Huyền cười nói."),t("br"),t("br"),n._v("“Có...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lòng bàn tay lơ lửng ra mấy chục giọt nước."),t("br"),t("br"),n._v("Thoạt nhìn cùng nước giống nhau như đúc, nhưng thật ra là đặc biệt luyện chế trận kỳ, có thể hòa vào trong nước, hình thành đặc thù lực lượng trận đồ, tùy thời kích phát ra trận pháp lực lượng."),t("br"),t("br"),n._v("“Có thứ này liền tốt, nó muốn thiết kế chúng ta, chúng ta vừa vặn thay đổi trận pháp, đối với nó tiến hành thiết kế...”"),t("br"),t("br"),n._v("Trương Huyền cười nhạt một tiếng, ngón tay ngoắc ngoắc, Phong Thiêm trưởng lão trong tay giọt nước lập tức bay đi."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền chân khí run lên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Mấy chục giọt nước lập tức rơi lả tả tại Tiểu Kính hải sóng gợn lăn tăn trong mặt nước, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hắn thoáng cái liền đem tất cả dịch giọt bắn đi ra, không chần chờ chút nào cùng dừng lại, Phong Thiêm trưởng lão ngẩn ngơ."),t("br"),t("br"),n._v("Còn là lần đầu tiên nhìn thấy như vậy bày trận!"),t("br"),t("br"),n._v("Tốc độ có phần quá nhanh!"),t("br"),t("br"),n._v("“Đi xuống chờ xem...”"),t("br"),t("br"),n._v("Làm xong những này, Trương Huyền cũng không nhiều lời, nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Liền như thế chờ lấy?”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ đều có chút khó tin."),t("br"),t("br"),n._v("“Vâng, các Huyền Bối quy xuất hiện, tất cả cùng trước đó ngươi thiết kế đồng dạng, đừng lại có mặt khác động tác...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Dựa theo hắn nói làm đi!”"),t("br"),t("br"),n._v("Thấy hắn một mặt tự tin, trước đó lại đem sự tình nói rõ chi tiết đi ra, Khôi Hiểu tông chủ dừng lại một chút, nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão không tại nhiều nói, điều khiển lấy thuyền nhỏ, thẳng tắp hướng Tiểu Kính hải ở giữa chạy tới."),t("br"),t("br"),n._v("Rất mau tới đến ở giữa."),t("br"),t("br"),n._v("“Ngay ở chỗ này...”"),t("br"),t("br"),n._v("Gật gật đầu, Phong Thiêm trưởng lão tay cầm một trảo, mặt nước một cái vòng xoáy xuất hiện tại phía trước, đi đầu bay vào, thuyền nhỏ cũng bị hắn thu vào nhẫn."),t("br"),t("br"),n._v("Trương Huyền cùng Khôi Hiểu tông chủ theo sát trên đó, cũng hướng bên trong bay đi, thời gian không dài, liền đến đến có chút tràn đầy đáy biển u ám."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2048"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2050"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);