(window.webpackJsonp=window.webpackJsonp||[]).push([[1183],{1538:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2036-thong-minh-huyen-boi-quy"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2036-thong-minh-huyen-boi-quy"}},[n._v("#")]),n._v(" Chương 2036: Thông minh Huyền Bối quy")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Mặt biển sóng nước lấp loáng, trọc lãng ngập trời, đáy biển lại gió êm sóng lặng, không có một tia gợn sóng, yên tĩnh tựa như tĩnh thất."),t("br"),t("br"),n._v("Tiếp tục hướng xuống, thời gian không dài, chói mắt quang mang xuất hiện tại tầm mắt, chính là đối phương bố trí tốt ngân toản tinh thạch."),t("br"),t("br"),n._v("Phong Thiêm trưởng lão thiết kế vô cùng tinh diệu, để thứ này thoạt nhìn, cùng tự nhiên sinh trưởng ở địa mạch bên trong đồng dạng, sở dĩ hiển lộ, là bởi vì động đất nổ tung, cho dù là bản thân, không phải trước thời hạn biết vì đối phương sắp đặt, đều cho là tự nhiên hình thành, từ đó mất đi lòng cảnh giác."),t("br"),t("br"),n._v("“Chúng ta trốn ở chỗ này là được...”"),t("br"),t("br"),n._v("Đi vào ngân toản tinh thạch chỗ không xa, Phong Thiêm trưởng lão bàn tay lớn vồ một cái, một cái không lớn vòng xoáy xuất hiện, đi đầu chui vào, hai người theo sát phía sau."),t("br"),t("br"),n._v("Vừa mới đi vào, vòng xoáy biến mất, liền giống như ba người chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("“Không gian trận pháp?”"),t("br"),t("br"),n._v("Khó trách Phong Thiêm trưởng lão tự tin như vậy, đối phương không phát hiện được nhóm người mình, nguyên lai đã sớm chuẩn bị xong không gian trận pháp, tiến vào bên trong, chỉ cần tìm không thấy trận cơ, không phá hư trận pháp kết cấu, sẽ rất khó phát hiện."),t("br"),t("br"),n._v("Người ngoài không nhìn thấy bọn họ, bọn họ lại có thể rõ ràng nhìn thấy ngân toản tinh thạch, với bên ngoài tình huống như lòng bàn tay."),t("br"),t("br"),n._v("“Thứ này, không dễ tìm cho lắm đi!”"),t("br"),t("br"),n._v("Nhìn cách đó không xa chói mắt, tỏa ra khiến người ta say mê ánh sáng tinh thạch, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Loại này tinh thạch, không cách nào luyện chế vũ khí, cũng không có cách nào hòa tan, nhưng là cả thương, xinh đẹp nhất đồ vật một trong, chỉ riêng một viên giá trị, liền vượt qua Tiên Quân đỉnh phong cấp bậc vũ khí, hơn nữa còn có giá cả không thành phố."),t("br"),t("br"),n._v("Thượng Thương đại lục ghi lại trong lịch sử, thứ này, tổng cộng chỉ xuất hiện qua ba cái, hơn nữa, lớn nhất chỉ có long nhãn trái phải, trước mắt cái này, so nắm đấm đều lớn hơn, giá trị cao, không thể đánh giá."),t("br"),t("br"),n._v("Vì đầu này Ngụy Thần cảnh Tiên thú, Thất Tinh lâu thật đúng là bỏ ra đủ vốn liếng."),t("br"),t("br"),n._v("“Vì cái này, Thất Tinh lâu vận dụng tiếp cận một vạn người, hao tốn vô số bảo vật, mới lấy thành công...” Khôi Hiểu tông chủ gật đầu."),t("br"),t("br"),n._v("Chuyện này, vốn là lấy hắn làm chủ đạo."),t("br"),t("br"),n._v("Cho dù tiêu phí to lớn, nhưng mà nếu có thể nắm lấy hoặc là đánh giết một đầu Ngụy Thần cảnh Tiên thú, tuyệt đối cũng đáng!"),t("br"),t("br"),n._v("Không nói mặt khác, chỉ nói huyết dịch, tinh hạch, da thú, khung xương... Có thể chế ra bảo vật, do Thất Tinh lâu đấu giá, tuyệt đối có thể để cho trước đó tổn thất, vượt lên ít nhất gấp mười lần!"),t("br"),t("br"),n._v("Mấu chốt nhất là, trước kia chỉ có Thần điện, mới xuất hiện qua Ngụy Thần cảnh Thần thú, địa phương khác không có phát hiện qua, bởi vậy, cũng chưa từng có người săn giết thành công, máu của nó, thân thể, sẽ đối với bản thân có ảnh hưởng hay không?"),t("br"),t("br"),n._v("Có thể hay không để cho tu vi tiến thêm một bước?"),t("br"),t("br"),n._v("Một khi có ích, tiêu phí lại lớn, đều đáng giá."),t("br"),t("br"),n._v("Cho nên, nghe tới có đầu này Tiên thú tin tức về sau, liền bắt đầu chuẩn bị, trước mắt Lưu trưởng lão, chẳng qua vừa lúc mà gặp thôi."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Không cần đối phương giải thích, hắn cũng hiểu vì cái này viên ngân toản tinh thạch, khẳng định hao tốn không biết bao nhiêu, đoán chừng, toàn bộ đại lục, cũng chỉ có Thất Tinh lâu như vậy có tiền thế lực bá chủ, mới có thể làm đến."),t("br"),t("br"),n._v("Đương nhiên, Thông Thần điện, khẳng định cũng có thể làm được, chỉ bất quá, đối phương càng thêm thần bí, rõ ràng nhị lưu trở lên thành thị, đều có phần bộ tồn tại, nhưng tổng bộ ở đâu, lại có bao nhiêu lợi hại cường giả, hầu như không người biết được."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngay tại trò chuyện, kèm theo trăng tròn bay lên, mặt biển quả nhiên xuất hiện khuấy động, động đất đồng dạng."),t("br"),t("br"),n._v("Tựa như kính đồng dạng mặt nước, đang sôi trào bên trong, không ngừng vặn vẹo, chiếu rọi bốn phía, kỳ quái, tựa như đầy trời tinh đấu, toàn bộ rơi xuống ở đây, ở trong nước chơi đùa."),t("br"),t("br"),n._v("“Bắt đầu...”"),t("br"),t("br"),n._v("Trương Huyền lông mày giương lên."),t("br"),t("br"),n._v("Khôi Hiểu tông chủ đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Quả nhiên cùng trước hắn nói đồng dạng, trăng tròn bay lên, mặt biển xuất hiện biến cố, những cái kia phù du, không ngừng lay động, Nhất Thủy Nhị Ba trận thật muốn tiếp tục sử dụng lời nói, vô cùng có khả năng mất đi hiệu quả."),t("br"),t("br"),n._v("Phong Thiêm trưởng lão âm thanh vang lên."),t("br"),t("br"),n._v("“Tới...”"),t("br"),t("br"),n._v("Đám người ngẩng đầu, ngay sau đó nhìn thấy một đầu to lớn rùa biển, từ đằng xa chậm chạp bơi tới."),t("br"),t("br"),n._v("Đầu này rùa biển, hình thể to lớn, đường kính vượt qua trăm mét, dạo chơi tại trong hải dương, tựa như một chiếc to lớn tàu chiến, đem Tiểu Kính hải phía trên phù du phản xạ ánh sáng, đều che chắn xuống dưới."),t("br"),t("br"),n._v("“Quả nhiên là Ngụy Thần cảnh...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước mắt đầu này đại gia hỏa, quả nhiên là Ngụy Thần cảnh cường giả, thoạt nhìn cồng kềnh, nhưng chỉ nói tới sức mạnh, so với Khôi Hiểu tông chủ, đều chỉ mạnh không yếu, nếu như đang đối mặt đụng, đối phương không nói mặt khác, mạnh mẽ đâm tới, ba người bọn họ liền có thể ngăn cản không nổi!"),t("br"),t("br"),n._v("Hắn tu vi hiện tại, cho dù đột phá Tiên Quân, có thể bảo đảm cái này tu vi cùng cấp bậc vô địch, có thể cùng Ngụy Thần vẫn như cũ chênh lệch quá lớn, cả hai đã là hai loại hoàn toàn khác biệt sinh mạng thể, không thể cùng ngày mà nói."),t("br"),t("br"),n._v("Cho nên, gặp được loại cường giả cấp bậc này, vẫn như cũ là có thể chạy được bao xa, chạy bao xa, ngàn vạn không thể mạnh mẽ chống đỡ!"),t("br"),t("br"),n._v("“Chuẩn bị khởi động trận pháp đi...”"),t("br"),t("br"),n._v("Thấy Huyền Bối quy càng ngày càng gần, Khôi Hiểu tông chủ vẻ mặt nghiêm túc, lực lượng toàn thân vận chuyển, bất cứ lúc nào cũng sẽ phối hợp trận pháp, cho trước mắt đầu này đại gia hỏa một kích trí mạng."),t("br"),t("br"),n._v("Loại cấp bậc này Tiên thú, thuần phục là không thể nào, cùng hắn bỏ phí phí công phu, còn không bằng tại chỗ bắn chết, thu hoạch được chỗ tốt càng lớn."),t("br"),t("br"),n._v("“Có chút không đúng...”"),t("br"),t("br"),n._v("Trương Huyền đột nhiên lông mày nhíu một cái."),t("br"),t("br"),n._v("“Không đúng?”"),t("br"),t("br"),n._v("Hai người nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Huyền Bối quy giờ phút này khoảng cách đã tới gần, nếu như không khởi động trận pháp lời nói, đối phương vô cùng có khả năng một cái xung kích, liền đem ngân toản tinh thạch cướp đi, chỉ cần trốn đi trận pháp bao phủ khu vực, trước đó bố cục, sẽ hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("Trương Huyền nói: “Huyền Bối quy, đã mục tiêu là ngân toản tinh thạch, giờ phút này mặt biển rung chuyển, chính là thiết kế tốt, tình huống bình thường, vừa đến nên xông lại cướp đi, giờ phút này lại một mực quanh quẩn một chỗ, các ngươi không cảm thấy kỳ quái sao?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão sững sờ, đồng thời nhìn lại, quả nhiên cũng nhìn ra không đúng."),t("br"),n._v("To lớn Huyền Bối quy, rõ ràng đã đi tới tinh thạch trước mặt, nhưng lại chưa thôn phệ, mà là vòng quanh xoay quanh, thật giống không nhìn thấy đồng dạng."),t("br"),t("br"),n._v("Đối phương đều thiết kế tốt, trăng tròn phù du náo động thời điểm, cướp đoạt tinh thạch, thuận tiện đem Phong Thiêm trưởng lão đám người đánh giết, cơ hội đang ở trước mắt, lại không động đậy, khẳng định có ý nghĩ của mình."),t("br"),t("br"),n._v("“Khà khà, khó trách một mực hướng bên này đi, nguyên lai là phát hiện thứ này, người chết vì tiền, chim chết vì ăn, súc sinh chính là súc sinh, vì một viên tinh thạch mất mạng, đừng trách ta không khách khí...”"),t("br"),t("br"),n._v("Ba người tràn đầy kỳ lạ, một cái thanh âm nhàn nhạt, từ đằng xa truyền đến, ngay sau đó ánh sáng lấp lánh, một bóng người phá vỡ mặt nước, thẳng tắp đi vào Huyền Bối quy phía trên."),t("br"),t("br"),n._v("“Ngụy Thần cảnh cường giả?”"),t("br"),t("br"),n._v("Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Xuất hiện ở trước mắt vị này, cùng Khôi Hiểu tông chủ và Huyền Bối quy đồng dạng, vậy mà cũng là một vị Ngụy Thần cảnh cường giả!"),t("br"),t("br"),n._v("Khó trách người sau một mực xoay quanh, lại không thôn phệ tinh thạch, nguyên lai tại bị đối phương truy lùng."),t("br"),t("br"),n._v("“Là Huyền Kính môn tông chủ, Bạch Huyền Sanh!”"),t("br"),t("br"),n._v("Sửng sốt một chút, Khôi Hiểu tông chủ truyền âm tới."),t("br"),t("br"),n._v("Trương Huyền quay đầu: “Huyền Kính môn tông chủ?”"),t("br"),t("br"),n._v("Sáu đại tông môn Huyền Kính môn, am hiểu luyện chế vũ khí, Đồng Thường kiếm chính là xuất từ tay đối phương, trước đó chưa hề tiếp xúc qua, không nghĩ tới, vậy mà tại nơi này trực tiếp gặp đối phương môn chủ."),t("br"),t("br"),n._v("“Hắn tại sao lại ở chỗ này?”"),t("br"),t("br"),n._v("Phong Thiêm trưởng lão không hiểu."),t("br"),t("br"),n._v("“Ngụy Thần cảnh Tiên thú xuất hiện, chúng ta có thể được đến tin tức, đối phương khẳng định cũng có thể...”"),t("br"),t("br"),n._v("Khôi Hiểu tông chủ lắc đầu."),t("br"),t("br"),n._v("Loại này cấp bậc Tiên thú trân quý, người người đều biết, hắn muốn chém giết, đối phương khẳng định cũng muốn, dù sao không phải hắn vật phẩm tư nhân, không có quyền một mình chiếm hữu."),t("br"),t("br"),n._v("“Trận pháp khởi động ư?”"),t("br"),t("br"),n._v("Không nghĩ tới nửa đường giết ra cái Trình Giảo Kim, đem trước bố cục toàn bộ xáo trộn, Phong Thiêm trưởng lão mặt sắc khó coi."),t("br"),t("br"),n._v("“Trước không cần...” Trương Huyền xua tay: “Đổi lại trước kia Nhất Thủy Nhị Ba trận, phù du xúc động phía dưới, khả năng đã kích hoạt, thậm chí mất đi tác dụng! Bị ta sửa lại thoáng cái, càng thêm củng cố, ta không tự thân kích động, rất khó vận chuyển! Như vậy chẳng khác nào cho chúng ta lưu lại cuối cùng át chủ bài!”"),t("br"),t("br"),n._v("“Át chủ bài?”"),t("br"),t("br"),n._v("“Trước đó, ta một mực cảm giác, Huyền Bối quy là muốn mượn đêm trăng tròn, thiết kế Khôi tông chủ, hiện tại xem ra, cũng không phải là như vậy! Khôi tông chủ tới hay không, nó không biết rõ tình hình, nhưng... Vị này Bạch Huyền Sanh tông chủ, hẳn là theo dõi rất lâu, nó đã sớm muốn đem hắn giết chết!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền hiểu được."),t("br"),t("br"),n._v("Trên đường thời điểm, hắn đã biết Thông Thiên kiều trước thời hạn mở ra tin tức, tình huống bình thường, Khôi Hiểu tông chủ cho dù cũng tới nơi này, khẳng định không có gấp gáp như vậy, cho nên... Đầu này Huyền Bối quy, chưa chắc là đang chờ hắn!"),t("br"),t("br"),n._v("Giờ phút này nhìn thấy vị này Bạch Tông chủ, lập tức giật mình."),t("br"),t("br"),n._v("Đoán chừng cái tên này, đã sớm thiết kế tốt, đối phương sẽ đuổi tới nơi này, sau đó cố ý chờ đợi, tốt mượn phù du lực lượng, đem chém giết."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trong quá trình nói chuyện, mặt đất lay động càng thêm lợi hại, ngân toản tinh thạch hấp thu lực lượng càng ngày càng mạnh, phía trên phù du càng thêm sôi trào, đại địa xuất hiện lần nữa khe hở."),t("br"),t("br"),n._v("Huyền Bối quy không để ý tới trên không lơ lửng Bạch Huyền Sanh tông chủ, nhìn quanh hai bên một vòng, phát hiện Nhất Thủy Nhị Ba trận cũng không kích động, ánh mắt bên trong lộ ra vẻ lo lắng."),t("br"),t("br"),n._v("Bất quá, giờ phút này cũng biết, lại không cách nào chờ đợi, cắn răng một cái, thân thể to lớn, bỗng nhiên xông về trước ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thuấn di đồng dạng, xuất hiện tại ngân toản tinh thạch phía trước, há miệng nuốt xuống."),t("br"),t("br"),n._v("“Cái này gọi tốc độ... Không nhanh?”"),t("br"),t("br"),n._v("Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Trước đó, Khôi Hiểu tông chủ nói, đầu này đại hải quy bởi vì hình thể to lớn, chỉ là lực phòng ngự mạnh, tốc độ đồng dạng, hắn đến về sau, cũng cảm thấy rất tán thành... Nằm mơ đều không có nghĩ đến, nhanh như vậy!"),t("br"),t("br"),n._v("So thi triển ra toàn bộ lực lượng hắn, đều muốn mau hơn không ít!"),t("br"),t("br"),n._v("Thật muốn chiến đấu, coi như đối phương không có bất kỳ cái gì động tác, chỉ lấy vừa rồi tốc độ va chạm, đều chịu không được!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Một mặt xấu hổ, Khôi Hiểu tông chủ quay đầu nhìn về phía cách đó không xa Phong Thiêm trưởng lão."),t("br"),t("br"),n._v("“Ta... Lần trước thấy nó, tốc độ chính là không nhanh, cùng ta gần như...” Phong Thiêm trưởng lão vội vàng giải thích."),t("br"),t("br"),n._v("Sắp đặt chỗ này cạm bẫy, chính là vì đối phó đầu này đại gia hỏa, khẳng định đã sớm biết tốc độ của nó đến cùng có bao nhanh."),t("br"),t("br"),n._v("Chỉ là không nghĩ tới, đối phương căn bản không có phát huy ra thực lực chân chính!"),t("br"),t("br"),n._v("“Nhìn tới, nó so trong tưởng tượng thông minh nhiều lắm...”"),t("br"),t("br"),n._v("Trương Huyền cười khổ."),t("br"),t("br"),n._v("Biết Đạo Tạng kém cỏi, thu hút người khác cắn câu, sau đó mượn trăng tròn cùng Phong Thiêm trưởng lão trận pháp, đối phó Bạch Huyền Sanh tông chủ... Đầu này Huyền Bối quy, thoạt nhìn vụng về, trên thực tế lại lộ ra trí tuệ."),t("br"),t("br"),n._v("Khó trách có thể đi đến Ngụy Thần cảnh, quả nhiên không có một cái nào đơn giản!"),t("br"),t("br"),n._v("“Làm sao bây giờ?”"),t("br"),t("br"),n._v("Hai người cũng hiểu được, Khôi Hiểu tông chủ nói."),t("br"),t("br"),n._v("“Đã Huyền Bối quy đã ra tay, liền để hai người bọn họ đánh nhau, chúng ta tốt ngư ông đắc lợi!” Trương Huyền cười nhạt một tiếng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2049"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2051"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);