(window.webpackJsonp=window.webpackJsonp||[]).push([[1191],{1546:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2044-thuan-phuc-giao-sa"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2044-thuan-phuc-giao-sa"}},[n._v("#")]),n._v(" Chương 2044: Thuần phục Giao Sa")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Chủ nhân?”"),t("br"),t("br"),n._v("“Thuộc hạ?”"),t("br"),t("br"),n._v("Huyền Bối quy cùng Trương Huyền liếc mắt nhìn nhau, tất cả đều ngây người."),t("br"),t("br"),n._v("Mới vừa rồi còn không chết không thôi, lúc nào thành thuộc hạ?"),t("br"),t("br"),n._v("Không riêng gì hai người bọn họ, bốn phía rất nhiều Tiên thú, Thánh thú cũng bối rối."),t("br"),t("br"),n._v("Bọn họ vương giả, đối với nhân loại căm thù đến tận xương tuỷ, hận không thể đem hắn đuổi tận giết tuyệt, mới vừa rồi còn định ra dạy dỗ sáu đại tông môn kế hoạch... Thế nào trong nháy mắt liền làm phản rồi?"),t("br"),t("br"),n._v("Thân thể cung cùng rang chín giống như, xưng hô một nhân loại làm chủ người?"),t("br"),t("br"),n._v("“Lão đại, ngươi làm sao nhận như vậy một tiểu nhân vật làm chủ...”"),t("br"),t("br"),n._v("Còn lại hai đầu Giao Sa, cũng không dám tin tưởng."),t("br"),t("br"),n._v("“Ngậm miệng!”"),t("br"),t("br"),n._v("Lời mới vừa nói Giao Sa một tiếng gầm thét: “Còn không nhanh chút tới, gặp qua chủ nhân!”"),t("br"),t("br"),n._v("Hai đầu Giao Sa không biết lão đại đến cùng là dụng ý gì, có điều, không dám vi phạm mệnh lệnh, đuôi vặn vẹo uốn éo, bơi tới Trương Huyền phía trước, liếc mắt nhìn, đồng dạng đồng tử co rụt lại, đồng loạt quỳ gối: “Thuộc hạ, gặp qua chủ nhân!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền không biết đối phương là dụng ý gì, chần chờ một chút, mở miệng nói: “Nhận chủ đi!”"),t("br"),t("br"),n._v("Mặc kệ thành tâm đóng kịch, chỉ cần nhận chủ, liền lại không cách nào thay đổi."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Không chần chờ chút nào, ba đại Giao Sa tất cả đều tế ra tinh huyết của mình."),t("br"),t("br"),n._v("Tiếp nhận tinh huyết, hoàn thành nhận chủ nghi thức, trong đầu một hồi nổ vang, lúc này mới phát hiện, đối phương cũng không có chút nào quỷ kế, chân tâm thật ý, nhận hắn làm chủ."),t("br"),t("br"),n._v("“Các ngươi đây là...”"),t("br"),t("br"),n._v("Mới vừa rồi còn đánh khó bỏ khó phân, hận không thể đem hắn xé thành đập tan, sau một khắc biến thành bản thân thú sủng, cam tâm tình nguyện, Trương Huyền có chút không hiểu rõ nổi."),t("br"),t("br"),n._v("Cái này tính tình cũng thực sự rất cổ quái, chẳng lẽ... Bản thân thật rất có mị lực, chỉ nhìn bên trên một cái, liền có thể để bọn chúng cúi đầu xưng thần?"),t("br"),t("br"),n._v("Quan trọng... Cũng không có toả ra qua cái gì vương bá chi khí ah!"),t("br"),t("br"),n._v("“Chúng ta sở dĩ có thể đột phá Ngụy Thần cảnh, là bởi vì nuốt Thần Linh huyết dịch, trên người chủ nhân, mang theo Thần Linh tín vật, chúng ta tự nhiên muốn phụng ngươi làm chủ!”"),t("br"),t("br"),n._v("Thực lực mạnh nhất Giao Sa, nhìn ra hắn nghi hoặc, giải thích nói."),t("br"),t("br"),n._v("“Tín vật?”"),t("br"),t("br"),n._v("Trương Huyền cúi đầu nhìn về phía trước ngực mặt dây chuyền, trước đó là đặt ở trong quần áo, bởi vì cảm nhận được thần huyết, sinh ra khô nóng, lấy ra ngoài, giờ phút này đang treo chếch tại trên cổ."),t("br"),t("br"),n._v("“Chính là!” Giao Sa cung kính gật đầu."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Trước đó liền suy đoán ra, thần huyết cùng Lạc Nhược Hi liên quan đến, hiện tại xem ra, đã xác định."),t("br"),t("br"),n._v("Giao Sa nuốt vào nàng rơi vãi máu tươi, mới đột phá đến Ngụy Thần, nhìn thấy tín vật của nàng, tự nhiên có cảm ứng, không còn dám tiến hành công kích."),t("br"),t("br"),n._v("Còn Huyền Bối quy, lúc trước vì sao không nhận ra được, là bởi vì, trước đó vẫn giấu kín tại quần áo phía dưới, đối phương căn bản là không có nhìn thấy."),t("br"),t("br"),n._v("“Để bọn chúng trước tản đi đi! Ta có chuyện muốn hỏi ngươi!”"),t("br"),t("br"),n._v("Đã nhận chủ, lại không còn nguy hiểm, Trương Huyền thở phào nhẹ nhõm, dặn dò một tiếng về sau, uống một bình ngọc tiểu hoàng kê nước tắm, lúc này mới cảm thấy thương thế khôi phục lại."),t("br"),t("br"),n._v("Giao Sa dặn dò vài câu, vây chung quanh rất nhiều Tiên Quân Tiên thú, cho dù không cam lòng, còn là nghe theo mệnh lệnh, lui ra ngoài."),t("br"),t("br"),n._v("Tất cả Tiên thú rời khỏi, Trương Huyền lúc này mới nhìn về phía trước mắt ba đầu Giao Sa: “Đã thần phục ta, lấy cái tên, cũng có thể thuận tiện ghi nhớ... Ngươi gọi Đại Sa, ngươi gọi Nhị Sa, ngươi gọi Tam Sa!”"),t("br"),t("br"),n._v("“???” Giao Sa ba huynh đệ."),t("br"),t("br"),n._v("“Ngươi đem Trục Tinh cung cung chủ làm sao tế tự Thượng Thương, Thần Linh làm sao giáng thế, cuối cùng lại đi nơi nào, tỉ mỉ cùng ta nói một lần...”"),t("br"),t("br"),n._v("Không để ý tới buồn bực Tam Sa huynh đệ, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Đúng hơn hai tháng trước một ngày, Trục Tinh cung cung chủ đột nhiên xuất hiện ở đây, lúc ấy chúng ta cũng không quen biết, thấy có nhân loại đi vào khu không người, tất cả đều vô cùng tức giận, chạy tới tìm nàng phiền phức, kết quả... Đều bị đả thương!”"),t("br"),t("br"),n._v("Nhớ tới hôm đó tình huống, Đại Sa nói."),t("br"),t("br"),n._v("Lúc đó nó chỉ là Tiên Quân đỉnh phong, tự nhiên không phải Ngụy Thần cảnh cường giả đối thủ."),t("br"),t("br"),n._v("“Sau đó, nàng liền lấy chúng ta rất nhiều Tiên thú máu tươi làm tế tự, xé rách không trung, tiếp đón Thần Linh. Lần đầu tiên không thành công, lại thử một lần, bởi vì hao hết lực lượng toàn thân, trong nháy mắt biến thành bà lão. Chúng ta vốn cho rằng sẽ chết đi như thế, kết quả... Thần Linh xuất hiện!”"),t("br"),t("br"),n._v("Nói đến Thần Linh, Đại Sa run lẩy bẩy, cũng không chỉ là kích động còn là sợ hãi."),t("br"),t("br"),n._v("“Bởi vì Trục Tinh cung cung chủ lực lượng không đủ, cho dù thi triển hai lần tế tự, không gian bích lũy còn là quá cường đại, Thần Linh không cách nào xuyên qua tới, bất quá... Vị này Thần Linh vô cùng quả quyết, liều mạng bị không gian bích lũy trọng thương, mạnh mẽ đi tới nơi này... Không gian phong bạo phản phệ rất cường đại, mới để cho nàng chảy xuống không ít máu tươi...”"),t("br"),t("br"),n._v("“Huynh đệ chúng ta ba người vốn là bị thương, nhìn thấy Thần Linh nhỏ xuống huyết dịch, nghĩ đến dù sao cũng là một lần chết, liền mỗi cái nuốt một giọt, sau đó... Lâm vào hôn mê, chuyện về sau, cũng không rõ ràng!”"),t("br"),t("br"),n._v("“Không rõ ràng? Ngươi không phải mới vừa nói... Vị kia Thần Linh rời đi ư?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Đúng ta nhìn thấy...” Tam Sa bơi tới: “Ta lá gan tương đối nhỏ, thôn phệ huyết dịch tương đối trễ, loáng thoáng nhìn thấy vị kia Thần Linh, tay cầm tại Trục Tinh cung cung chủ trên người vỗ vỗ, người sau lập tức từ lão giả biến trở về trẻ tuổi hình dáng! Nói một câu nói cái gì, theo sau dường như lại có người tiến hành tế tự, xé rách không gian, chui vào...”"),t("br"),t("br"),n._v("“Lại có người tế tự?”"),t("br"),t("br"),n._v("Trương Huyền suy nghĩ."),t("br"),t("br"),n._v("Hẳn là Thần Dung Hoàng tiến hành tế tự, sau đó Lạc Nhược Hi xuyên qua hàng rào, đi vào danh sư đại lục."),t("br"),t("br"),n._v("Có lẽ chính là bởi vì, cái này liên tục xuyên qua hai nơi giới vực hàng rào, thương thế nghiêm trọng, lúc này mới ngụy trang thành lục tinh danh sư..."),t("br"),t("br"),n._v("Đương nhiên, đây chỉ là suy đoán, cụ thể tình huống gì, chỉ có thể nhìn thấy vị kia Trục Tinh cung cung chủ, mới có thể tỉ mỉ hỏi thăm."),t("br"),n._v(" Lần nữa lấy ra Lạc Nhược Hi hình ảnh, để ba thú nhận ra, kết quả bọn chúng cùng Huyền Bối quy đồng dạng, chuyện khi đó có thể ghi nhớ, nói đến dung mạo hoặc là cụ thể chi tiết, cũng toàn bộ quên."),t("br"),t("br"),n._v("“Các ngươi làm sao nhận ra thứ này, là Thần Linh tín vật?”"),t("br"),t("br"),n._v("Cái này ba đầu Giao Sa, đều không nhớ ra được vị kia Thần Linh cụ thể hình dáng, lại vì sao có thể nhận ra cái này mặt dây chuyền?"),t("br"),t("br"),n._v("“Thần Linh có thể phá vỡ không gian bích lũy, đi tới nơi này, chính là dùng cái này mặt dây chuyền! Thứ này chói mắt như là Thái Dương, chúng ta toàn bộ nhìn ở trong mắt, không có khả năng không nhớ được...”"),t("br"),t("br"),n._v("Đại Sa nói."),t("br"),t("br"),n._v("Hơn hai tháng trước cảnh tượng, chi tiết coi như quên, sự kiện lớn chắc chắn sẽ không quên."),t("br"),t("br"),n._v("Trục Tinh cung cung chủ bởi vì lực lượng hao hết, tế tự không có triệt để hoàn thành, dưới tình huống bình thường, vị kia Thần Linh là không cách nào xuyên qua tới, lúc ấy chính là mượn cái này viên mặt dây chuyền, đánh nát không gian bích lũy, mới lấy thành công."),t("br"),t("br"),n._v("Bởi vì ghi nhớ, nhìn thấy trước mắt vị thanh niên này trước ngực đeo, mới cảm thấy kinh hoàng, không dám tiếp tục động thủ!"),t("br"),t("br"),n._v("Thần Linh một giọt máu, liền để bọn chúng đột phá, trước mắt vị này cho dù không phải Thần Linh, nhất định cùng Thần Linh cũng có cái này cực lớn liên luỵ cùng quan hệ, nhận hắn làm chủ, tuyệt đối không sai."),t("br"),t("br"),n._v("Cương quyết, có cốt khí, uy vũ không khuất phục?"),t("br"),t("br"),n._v("Tại Thần Linh phía trước, không tồn tại."),t("br"),t("br"),n._v("“Cái này mặt dây chuyền, có thể phá vỡ không gian bích lũy?”"),t("br"),t("br"),n._v("Nắm tại lòng bàn tay, Trương Huyền cảm nhận được phía trên vẫn như cũ tràn đầy khô nóng, tinh thần khẽ động, tiên lực rót vào trong đó, lại phát hiện không có bất kỳ biến hóa nào."),t("br"),t("br"),n._v("Thần thạch, Minh Lý Chi Nhãn, tiên lực, tất cả đều sử dụng một lần, trước mắt thứ này cùng phổ thông mặt dây chuyền đồng dạng, cũng không biểu hiện ra bất kỳ uy lực."),t("br"),t("br"),n._v("“Có lẽ là tu vi của ta không đủ đi...”"),t("br"),t("br"),n._v("Nhớ tới trước đó tẩu hỏa nhập ma một màn, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lạc Nhược Hi đã thận trọng đem thứ này cho mình, khẳng định bất phàm, bằng không thì cũng không có khả năng, liền thư viện sinh ra tâm ma, đều có thể áp chế xuống."),t("br"),t("br"),n._v("“Đã các ngươi thần phục với ta, liền đi theo ta đi! Vừa vặn cũng không ít sự tình, cần các ngươi!”"),t("br"),t("br"),n._v("Đem mặt dây chuyền lần nữa giấu kỹ trong người, Trương Huyền nói."),t("br"),t("br"),n._v("Có cái này ba đầu Giao Sa hiệu trung, chẳng khác nào nắm giữ bốn vị Ngụy Thần cảnh Tiên thú, lại thêm xích sắt vũ khí, tương đương với có năm đại Ngụy Thần bảo vệ."),t("br"),t("br"),n._v("Bởi như vậy, coi như Thần điện còn muốn đánh lén, khẳng định cũng sẽ cân nhắc thoáng cái."),t("br"),t("br"),n._v("Lấy ra tiểu hoàng kê nước tắm, cho bị thương Giao Sa chữa thương."),t("br"),t("br"),n._v("Làm xong những này, lần nữa hỏi thăm thần huyết sự tình."),t("br"),t("br"),n._v("Khi biết, chỉ có bọn chúng ba cá cùng Huyền Bối quy thuận lợi tấn cấp, còn lại huyết dịch, hòa vào đại dương tiêu tán ra, chỉ bảo tồn bên dưới tiểu hoàng kê nuốt giọt kia về sau, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Còn có tồn tại huyết dịch, có lẽ có thể nghiên cứu một chút, không còn, chỉ có thể như vậy coi như thôi."),t("br"),t("br"),n._v("Tuy là tiếc nuối, có thể nghĩ để gà con phun ra... Hẳn là rất khó làm được."),t("br"),t("br"),n._v("Từ ba đầu Giao Sa trong cơ thể rút lấy không ít huyết dịch, cho giao long các thú dùng, người sau vẫn không có đột phá."),t("br"),t("br"),n._v("Nhìn tới, Ngụy Thần quả thực không phải dễ dàng như vậy thành công, dù là có Ngụy Thần cảnh cường giả làm chỗ dựa, đồng dạng khó mà hoàn thành."),t("br"),t("br"),n._v("Xử lý xong chuyện bên này, lại không tiếp tục chờ lâu, đem Nhị Sa, Tam Sa cùng Huyền Bối quy thu vào thú sủng túi, ngồi Đại Sa cấp tốc hướng Trục Tinh đảo bay lượn mà đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền bên này đi vào Vô Nhân hải, ngay tại ngồi phi hành Tiên thú, chạy tới Vạn Thú môn đám người, cũng nhận được đến từ Thất Tinh lâu đưa tin."),t("br"),t("br"),n._v("“Tần Nguyên trưởng lão, chuyện gì?” Liêu trưởng lão nói."),t("br"),t("br"),n._v("“Thất Tinh lâu cũng xác lập mới tông chủ...”"),t("br"),t("br"),n._v("Tần Nguyên trưởng lão nhìn lòng bàn tay đưa tin ngọc phù."),t("br"),t("br"),n._v("Liêu trưởng lão sửng sốt: “Đúng ai?”"),t("br"),t("br"),n._v("“Đúng một vị gọi Lưu Dương người trẻ tuổi, Tiên Quân sơ kỳ tu vi, lại đem Thất Tinh lâu tất cả võ kỹ dung hội quán thông, thiên tư vô song, tu vi cực mạnh!”"),t("br"),t("br"),n._v("Tần Nguyên trưởng lão nói."),t("br"),t("br"),n._v("Không nghĩ tới bọn họ Vạn Thú môn, vừa tìm tới một vị thiên tư trác tuyệt tông chủ, Thất Tinh lâu cũng đồng dạng tìm được một vị!"),t("br"),t("br"),n._v("Đại loạn chi thế ra anh hùng, nhìn tới thế giới, không có lấy trước như vậy thái bình!"),t("br"),t("br"),n._v("“Lưu Dương... Chưa từng nghe qua!”"),t("br"),t("br"),n._v("Liêu trưởng lão gật đầu."),t("br"),t("br"),n._v("Đồng dạng tin tức, cũng truyền đến đang chạy tới Lăng Vân kiếm các trong tai mọi người."),t("br"),t("br"),n._v("Lần này Lăng Vân kiếm các, chỉ có Hàn Kiếm Thu, đại trưởng lão gì trời các rải rác mấy người."),t("br"),t("br"),n._v("Bất quá, vị kia Trục Tinh cung tới tìm kiếm tông chủ đồng tử Ngột Thần, bất ngờ theo ở phía sau."),t("br"),t("br"),n._v("“Chúng ta vừa xác lập mới tông chủ Trương Huyền, Vạn Thú môn, Thất Tinh lâu liền nhao nhao bắt chước...” Nhìn thấy tin tức mới vừa nhận được, Hàn Kiếm Thu cười khổ lắc đầu."),t("br"),t("br"),n._v("“Bắt chước? Có ý tứ gì?”"),t("br"),t("br"),n._v("Một bên đại trưởng lão gì trời, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Vạn Thú môn, mấy ngày trước đây vừa mới xác lập một vị trẻ tuổi tông chủ, gọi... Trịnh Dương! Không phải sao, Thất Tinh lâu cũng theo sau, cũng tuyên bố tin tức, xác lập một vị đồng dạng người trẻ tuổi vì tông chủ... Gọi Lưu Dương!”"),t("br"),t("br"),n._v("Hàn Kiếm Thu giải thích nói."),t("br"),t("br"),n._v("“Trịnh Dương? Lưu Dương? Trương Huyền...”"),t("br"),t("br"),n._v("Đi theo đám người phía sau Ngột Thần, vốn tràn đầy tâm sự, giờ phút này nghe được tin tức này, khóe miệng co giật thoáng cái, trên mặt lộ ra biểu tình cổ quái: “Tại sao ta cảm giác... Giống như là cùng là một người?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2057"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2059"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);