(window.webpackJsonp=window.webpackJsonp||[]).push([[1193],{1548:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-197-đa-lau-khong-gap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-197-đa-lau-khong-gap"}},[n._v("#")]),n._v(" Chương 197: Đã lâu không gặp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Như vậy khẳng định không được...” "),t("br"),t("br"),n._v(" Trương Huyền vội vàng lắc đầu."),t("br"),t("br"),n._v(" Thật muốn như vậy, hắn khả năng chẳng mấy chốc sẽ tinh thần phân liệt."),t("br"),t("br"),n._v(" Bất quá, không bái một người trong đó vi sư, liền không chiếm được trợ giáo tư cách, không thể sát hạch chân chính danh sư."),t("br"),t("br"),n._v(" Ngẫm lại cũng có chút đau đầu."),t("br"),t("br"),n._v(" “Quên đi, trước không muốn, bất quá, lấy thân phận của Trương Huyền gặp gỡ ba vị này danh sư cũng tốt, đệ nhất có thể hỗn cái mặt quen; Thứ hai, cũng vừa hay hỏi một chút quan ở trong cơ thể độc khí sự tình!” "),t("br"),t("br"),n._v(" Băn khoăn một hồi, có quyết định."),t("br"),t("br"),n._v(" Trong cơ thể hắn đạo kia hắc khí tuy rằng tạm thời không nguy hiểm, nhưng nhất định phải thanh trừ, “Dương Huyền” đi hỏi dò mà nói, có sai lầm cao nhân thân phận, bản thân đi vào trái lại vừa vặn."),t("br"),t("br"),n._v(" Nếu như hắn ý nghĩ này cho Lục Tầm, Vương Siêu mấy người biết, nhất định sẽ tươi sống khóc chết."),t("br"),t("br"),n._v(" Hai người kia thấy bái Dương Huyền vi sư không thể nào, ngày hôm qua chuyên môn bái phỏng ba sư, kết quả thấy đều chưa thấy... Cái tên này ngược lại tốt, ba sư muốn gặp hắn, hắn còn muốn suy nghĩ một chút, một mặt không tình nguyện..."),t("br"),t("br"),n._v(" Không trang bức có thể chết không?"),t("br"),t("br"),n._v(" “Ngươi nói tiệc mừng thọ khi nào thì bắt đầu? Cái kia Điền lão lại là người nào, liền ba sư, Thẩm Truy bệ hạ đều đi vào bái phỏng?” "),t("br"),t("br"),n._v(" Trương Huyền vẫn còn có chút kỳ quái."),t("br"),t("br"),n._v(" Có thể làm cho tam đại danh sư, Thẩm Truy bệ hạ tự mình chúc thọ, cái này Điền lão khẳng định không đơn giản, nếu như đúng là cái gì nhân vật lợi hại, tiền thân cần phải đã sớm biết a, làm sao một chút ấn tượng đều không có?"),t("br"),t("br"),n._v(" ngantruyen.com"),t("br"),n._v(" “Tiệc mừng thọ ngay hôm nay, hiện tại chạy tới vừa vặn tới kịp.” "),t("br"),t("br"),n._v(" Thấy hỏi như vậy, Hoàng Ngữ biết hắn có quyết định, thở phào nhẹ nhõm, giải thích: “Điền lão đã từng là Bắc Vũ học viện lão sư, Lưu sư, Trang sư, Trịnh sư đều cùng hắn học tập qua, tuy rằng không chủ dạy tu vi, nhưng cũng có nửa sư tình nghĩa, lần này qua chín mươi đại thọ, ba sư nếu tại Thiên Huyền Vương quốc, nhất định phải đi chúc mừng...” "),t("br"),t("br"),n._v(" Vị này Điền lão, mặc dù là Thiên Huyền Vương quốc người, nhưng từng tại Bắc Vũ học viện đảm nhiệm dạy học qua, ba sư đều từng tại môn hạ học tập, nắm giữ nửa sư tình nghĩa, Lưu sư để Hoàng Ngữ trước về đến, kỳ thực chính là vì làm cho nàng tham gia tiệc mừng thọ."),t("br"),t("br"),n._v(" Kết quả, bởi vì Thẩm"),t("br"),t("br"),n._v(" Hồng sự tình, ba sư tụ hội, nếu đều đến Thiên Huyền Vương quốc, đương nhiên phải tham gia tiệc mừng thọ, bọn họ đều tham gia, Thẩm Truy bệ hạ lại làm sao có khả năng không đi?"),t("br"),t("br"),n._v(" Cho tới Bắc Vũ học viện, là Bắc Vũ Vương quốc to lớn nhất học viện, Hoàng Ngữ, Bạch Tốn trước đều ở trong đó đi học."),t("br"),t("br"),n._v(" Trương Huyền lật xem qua toàn bộ Vương quốc tàng thư khố, những cái này đơn giản đất vực phân chia, tinh thần hơi động, liền chơi rõ rõ ràng ràng."),t("br"),t("br"),n._v(" Bắc Vũ Vương quốc là tới gần Thiên Huyền Vương quốc một cái nhị đẳng Vương quốc, trấn thủ danh sư chính là Lưu Lăng, Trang Hiền."),t("br"),t("br"),n._v(" Vương quốc chia làm: Phong hào, nhất đẳng, nhị đẳng cùng với Thiên Huyền Vương quốc loại này liền đẳng cấp đều không xếp hạng tới."),t("br"),t("br"),n._v(" Vương quốc đẳng cấp càng cao càng phát triển, giao dịch tài nguyên cũng là càng phong phú, võ giả cấp bậc cũng là càng cao."),t("br"),t("br"),n._v(" “Vị này Điền lão không dạy người tu luyện, lẽ nào cũng là lão sư?” Trương Huyền kỳ quái."),t("br"),t("br"),n._v(" Hồng Thiên học viện hết thảy lão sư đều là truyền thụ tu vi, coi như lấy thương pháp nổi danh Vương Siêu lão sư, không những truyền thụ thương pháp, cũng phụ trách chỉ điểm học sinh thực lực."),t("br"),t("br"),n._v(" Không dạy tu luyện dạy cái gì?"),t("br"),t("br"),n._v(" Làm sao cũng sẽ là lão sư?"),t("br"),t("br"),n._v(" “Bắc Vũ học viện quy mô so Hồng Thiên học viện muốn lớn hơn nhiều, trong đó lão sư phân loại cũng nhiều, tu luyện chỉ là một người trong đó, cũng không có thiếu truyền thụ đặc thù nghề nghiệp lão sư. Tỷ như luyện đan lão sư truyền thụ thuật luyện đan, thư họa lão sư truyền thụ thư họa... Những nghề nghiệp này, học sinh học tập có thể xem là cả đời phấn đấu mục tiêu, cũng có thể phụ tu, tăng cường tri thức mặt, thả lỏng tâm cảnh. Lưu sư mấy người, vì sát hạch danh sư, chỉ học tập một quãng thời gian, cũng không thuộc về chân chính học sinh, vì lẽ đó chỉ có nửa sư tình nghĩa.” "),t("br"),t("br"),n._v(" Hoàng Ngữ kiên trì giải thích."),t("br"),t("br"),n._v(" Nếu như lão sư nghề nghiệp chỉ một, chỉ truyền thụ tu vi, luyện đan sư, luyện khí sư loại hình rất nhiều nghề nghiệp lại là làm sao truyền thừa xuống? Luôn không khả năng cũng làm cho danh sư làm giúp đi!"),t("br"),t("br"),n._v(" Thật muốn như vậy, danh sư còn không tươi sống mệt chết?"),t("br"),t("br"),n._v(" Rất hiển nhiên, cũng có loại nghề nghiệp này chuyên môn lão sư."),t("br"),t("br"),n._v(" Vị này Điền lão, liền đã từng là loại này đặc thù nghề nghiệp lão sư."),t("br"),t("br"),n._v(" “Vậy hắn dạy cái gì?” Hiểu được, Trương Huyền gật gật đầu."),t("br"),t("br"),n._v(" Vậy thì cùng kiếp trước trường học gần như, có dạy thể dục, tiếng Anh, cũng có dạy toán học, vật lý, lão sư không thể toàn tài, tự nhiên cũng không thể hết thảy môn học đều dạy."),t("br"),t("br"),n._v(" “Điền lão dạy chính là trà đạo, là một vị chính thức trà đạo sư!” Hoàng Ngữ cười cợt."),t("br"),t("br"),n._v(" “Trà đạo sư?” "),t("br"),t("br"),n._v(" Vương quốc tàng thư khố có liên quan với trà đạo sư giới thiệu."),t("br"),t("br"),n._v(" Là một loại hạ cửu lưu nghề nghiệp, nghe tới cũng không đáng chú ý, bất quá, tại một ít trong vương quốc lại gặp phải rất lớn tung hô."),t("br"),t("br"),n._v(" Pha trà, có thể thả lỏng tâm cảnh, khiến lòng người hình dạng ôn hòa, một ít lợi hại trà đạo đại sư, càng là có thể ngâm ra giảm bớt nhân tâm cảnh nước trà, để người dùng để uống phía sau, triệt để thả lỏng, đối với tu luyện có cực giúp đỡ lớn, công hiệu không thua gì Tĩnh Tâm đan."),t("br"),t("br"),n._v(" Mấu chốt nhất chính là, trà đạo so luyện đan, luyện khí, thư họa loại hình học lên đến dễ dàng nhiều lắm, tuy rằng muốn học cao thâm trở thành chính thức trà đạo sư khó, nhưng nhập môn lên đến phi thường dễ dàng, một ít cao đẳng Vương quốc, không ít con cháu thế gia, vương hầu tướng lĩnh, cơ bản đều sẽ bên trên mấy tay."),t("br"),t("br"),n._v(" “Tốt, ta và các ngươi đi qua.” "),t("br"),t("br"),n._v(" Hỏi rõ ràng xảy ra chuyện gì, Trương Huyền không chậm trễ nữa, nếu tiệc mừng thọ sắp bắt đầu, trực tiếp đi theo Hoàng Ngữ, Bạch Tốn phía sau, lên xe ngựa."),t("br"),t("br"),n._v(" Điền lão chỗ ở, tại Vương thành vùng ngoại thành, khoảng cách Hồng Thiên học viện có một khoảng cách nhỏ, xe ngựa đi rồi tiếp cận một canh giờ, ở một tòa to lớn trang viên trước mặt ngừng lại."),t("br"),t("br"),n._v(" Đi ra xe ngựa lập tức nhìn thấy xung quanh người đông nghìn nghịt."),t("br"),n._v("  Điền lão từng là Bắc Vũ học viện lão sư, chỉ đạo qua danh sư, chỉ này một cái, liền có thể hấp dẫn vô số thế lực lớn đến đây bái chúc mừng."),t("br"),t("br"),n._v(" “Trương đại sư, đi theo ta...” "),t("br"),t("br"),n._v(" Đi xuống xe ngựa, Hoàng Ngữ phía trước dẫn đường, đến tới cửa, lấy ra bái thiếp, ung dung tiến vào sân."),t("br"),t("br"),n._v(" Không hổ là liền ba sư đều muốn bái phỏng phủ đệ, rộng lớn bao la, đường tắt ngang dọc, chưa từng tới người lần thứ nhất lại đây, làm không cẩn thận còn có thể lạc đường."),t("br"),t("br"),n._v(" “Lưu sư bọn họ cần phải còn chưa tới, chúng ta có thể ở chỗ này Thiên điện chờ.” "),t("br"),t("br"),n._v(" Hoàng Ngữ tựa hồ đã từng tới nơi này, xe nhẹ chạy đường quen, ba người một đường tiến lên, thời gian không lâu đi tới một cái rộng rãi đại điện."),t("br"),t("br"),n._v(" Còn không tiến vào bên trong, liền nhìn thấy đầu người phun trào, đã đến không ít người."),t("br"),t("br"),n._v(" “Tiểu Ngữ, ngươi rốt cục đến rồi...” "),t("br"),t("br"),n._v(" Mới đi vào cửa đại điện, liền nghe đến một niềm vui bất ngờ âm thanh âm vang lên, lập tức một người thanh niên một mặt mừng rỡ tiến lên đón."),t("br"),t("br"),n._v(" Người thanh niên này, hai mươi ba, bốn tuổi dáng dấp, toàn thân áo trắng, hình dạng cực kỳ anh tuấn, Trương Huyền cùng với so sánh, đều kém không ít."),t("br"),t("br"),n._v(" Nhìn thấy người này, Bạch Tốn sầm mặt lại, che ở Hoàng Ngữ trước mặt."),t("br"),t("br"),n._v(" “Bạch Tốn, ngươi muốn làm gì?” Thanh niên nhếch miệng lên."),t("br"),t("br"),n._v(" “Tiểu Ngữ cũng là ngươi kêu? Ta khuyên ngươi cách xa nàng điểm, không phải vậy, đừng trách ta không khách khí.” Bạch Tốn nhìn chằm chằm đối phương, nổi giận đùng đùng."),t("br"),t("br"),n._v(" “Không khách khí? Chúng ta lại không phải không đánh qua, ngươi thắng được ta sao?” "),t("br"),t("br"),n._v(" Đối mặt sự uy hiếp của hắn, thanh niên không cần thiết chút nào: “Lại nói, đây là nhà ta, tiểu Ngữ tới nhà của ta làm khách, đương nhiên muốn tận tình địa chủ, ngươi đây cũng muốn quản? Ngươi Bạch tiểu vương gia, quản cũng không tránh khỏi quá rộng đi!” "),t("br"),t("br"),n._v(" “Ngươi...” Bạch Tốn nhướng mày, liền muốn động thủ."),t("br"),t("br"),n._v(" “Được rồi!” Tựa hồ sớm biết hai người sẽ cãi nhau, Hoàng Ngữ đôi mi thanh tú một túc, nhìn về phía hai người: “Chúng ta là đến cho Điền lão mừng thọ, muốn đánh các ngươi quay đầu lại lại đánh!” "),t("br"),t("br"),n._v(" “Tiểu Ngữ yên tâm, ta chẳng muốn cùng cái tên này chấp nhặt, một cái cả ngày chỉ biết là đánh đánh giết giết vũ phu!” "),t("br"),t("br"),n._v(" Nói xong, thanh niên nhẹ nhàng nở nụ cười: “Tiểu Ngữ, ta mới vừa học được một bộ pha trà thủ pháp, đi, ta mang ngươi thưởng thức, tuyệt đối an thần Ngưng Khí, uống nhiều mấy lần, bảo đảm ngươi có thể sớm ngày bước vào tâm như chỉ thủy...” "),t("br"),t("br"),n._v(" “Không được, ta còn phải ở chỗ này chờ Lưu sư!” "),t("br"),t("br"),n._v(" Đối với đối phương nhiệt tình, Hoàng Ngữ cũng không để ý, xua tay từ chối."),t("br"),t("br"),n._v(" “Không đi cũng không quan trọng lắm, ta vừa vặn cũng muốn bái kiến Lưu sư, liền ở ngay đây cùng ngươi cùng nhau chờ đi...” "),t("br"),t("br"),n._v(" Đối với nàng từ chối, thanh niên không để ý chút nào, cười khanh khách nói."),t("br"),t("br"),n._v(" Hắn không đi, Hoàng Ngữ cũng không có cách nào."),t("br"),t("br"),n._v(" “Không biết xấu hổ!” Bạch Tốn nổi giận đùng đùng, lại không tiếp tục nhấc lên động thủ."),t("br"),t("br"),n._v(" Xem ra cần phải không phải cái tên này đối thủ, bằng không, lấy của hắn bạo phát cáu, nơi nào quản cái gì mừng thọ không mừng thọ."),t("br"),t("br"),n._v(" Biết vị này Tiểu vương gia bản tính phát cáu, thanh niên không thèm để ý, lúc này mới phát giác Trương Huyền, nghi hoặc nhìn lại: “Tiểu Ngữ, vị này chính là...” "),t("br"),t("br"),n._v(" “Há, ta đến giới thiệu cho các ngươi một chút!” Hoàng Ngữ nhìn sang: “Trương đại sư, vị này Điền Long chính là chủ nhân của nơi này, Điền lão cháu trai duy nhất; Điền Long, vị này chính là Hồng Thiên học viện Trương Huyền lão sư.” "),t("br"),t("br"),n._v(" “Trương Huyền? Danh tự này thật quen thuộc, thật giống ở đâu nghe qua...” "),t("br"),t("br"),n._v(" Thanh niên Điền Long nghe được tên hơi nhướng mày, suy tư chốc lát, ánh mắt sáng lên: “Hồng Thiên học viện, ngươi là cùng Lục Tầm lão sư, sư giả bình trắc Trương Huyền?” "),t("br"),t("br"),n._v(" Sư giả bình trắc huyên náo sôi sùng sục, muốn không biết cũng khó."),t("br"),t("br"),n._v(" “Lục Tầm là Hồng Thiên học viện lợi hại nhất minh tinh giáo sư, dám cùng hắn tiến hành sư giả bình trắc, ngươi lá gan thật là lớn, khâm phục, khâm phục!” "),t("br"),t("br"),n._v(" Đối phương trong miệng nói khâm phục, trên mặt lại không có một chút nào khâm phục tâm ý, trái lại tràn đầy cười trên sự đau khổ của người khác."),t("br"),t("br"),n._v(" Lục Tầm thành danh thật nhiều năm, lại thêm lên là Lục Trầm đại sư con trai, tiếng tăm rất lớn, hầu như tất cả mọi người đều cảm thấy tỷ thí lần này, là nghiêng về một phía cục diện."),t("br"),t("br"),n._v(" Nhìn thấy hắn dáng vẻ ấy, Trương Huyền lắc đầu."),t("br"),t("br"),n._v(" Lại một cái tự cho là đúng tiểu nhân vật mà thôi."),t("br"),t("br"),n._v(" “Điền Long, ngươi có ý gì?” Bạch Tốn không nhìn nổi."),t("br"),t("br"),n._v(" “Không có ý gì, người quý có tự mình biết mình, không biết tự lượng sức mình, chỉ có thể càng sống càng buồn tẻ.” Điền Long lắc đầu một cái."),t("br"),t("br"),n._v(" “Ngươi đem lời nói rõ ràng ra, ai không biết tự lượng sức mình? Trương đại sư, chỉ là là người điệu thấp không muốn hiển lộ thôi...” Bạch Tốn nổi giận đùng đùng."),t("br"),t("br"),n._v(" Hắn tuy rằng cũng nhận thức Lục Tầm, nhưng cũng chưa quen thuộc, ngược lại đối với Trương Huyền phục sát đất, nghe đối phương nói như vậy, lập tức không liên quan."),t("br"),t("br"),n._v(" “Đại sư? Cái gì đại sư? Đại sư danh xưng này là tùy tiện kêu sao?” "),t("br"),t("br"),n._v(" Điền Long cười nhạo, bàn tay lớn quăng một cái, mang theo kiêu ngạo lãnh khốc tâm ý: “Cái kia là đối với một cái nghề nghiệp đạt đến nhất định trình độ mới có xưng hô, lại như ông nội ta, tại trà đạo bên trên theo đuổi cực hạn, mới có tư cách xưng là trà đạo đại sư, một cái Hồng Thiên học viện lão sư, chừng hai mươi tuổi, gọi tiếng lão sư, coi như nể tình, cũng dám hô đại sư? Thực sự là không biết trời cao đất rộng...” "),t("br"),t("br"),n._v(" Cười lạnh, chính muốn nói tiếp, liền nhìn thấy một ông già đi tới, Điền Long lập tức đóng chặt miệng, một mực cung kính ôm quyền, tiến lên nghênh tiếp: “Điền Long gặp qua Nguyên Ngữ đại sư!” "),t("br"),t("br"),n._v(" Đến chính là Thiên Huyền Vương quốc đệ nhất y sư, Nguyên Ngữ."),t("br"),t("br"),n._v(" Đối với của hắn cung kính, Nguyên Ngữ đại sư gật gù, trên mặt không có chút rung động nào, chính muốn tiếp tục hướng phía trước, đột nhiên nhìn thấy một bên Trương Huyền, ánh mắt sáng lên, lập tức vọt tới, đầy mặt nhiệt tình."),t("br"),t("br"),n._v(" “Trương đại sư, ngươi cũng tại đây... Đã lâu không gặp!” "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-205"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-207"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);