(window.webpackJsonp=window.webpackJsonp||[]).push([[1196],{1551:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2048-thong-than-đien-cuong-gia"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2048-thong-than-đien-cuong-gia"}},[n._v("#")]),n._v(" Chương 2048: Thông Thần điện cường giả")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("“Đưa các nàng đều bắt!”"),i("br"),i("br"),n._v("Giải thích xong, trong thần điện niên nhân không nói thêm lời, vẫy tay một cái."),i("br"),i("br"),n._v("Sau lưng bốn vị Tiên Quân cường giả đỉnh phong, đồng loạt đi tới."),i("br"),i("br"),n._v("“Bảo vệ đại trưởng lão...”"),i("br"),i("br"),n._v("Gian phòng xông ra hơn mười vị Tiên Quân đỉnh phong cấp bậc trưởng lão."),i("br"),i("br"),n._v("Sau ba phút, đám người liền toàn bộ nằm trên mặt đất, từng cái bị trọng thương, trong thời gian ngắn mất đi sức chiến đấu."),i("br"),i("br"),n._v("Đều là Tiên Quân, Thần điện võ sĩ so phổ thông tông môn trưởng lão mạnh mẽ quá nhiều, căn bản không tại một cái lượng cấp."),i("br"),i("br"),n._v("Tất cả trưởng lão bị đánh bại, Sở Âm cùng đại trưởng lão bị bắt."),i("br"),i("br"),n._v("“Mang về, bắt được Đỗ cung chủ thì cũng thôi đi, không có bắt được, để nàng mang đồ vật tới đổi người!”"),i("br"),i("br"),n._v("Hừ lạnh một tiếng, trong thần điện niên nhân xoay người đi ra ngoài."),i("br"),i("br"),n._v("Trương Huyền chân khí trong cơ thể chậm chạp vận chuyển, định từ Thần điện trong tay đem người cứu được."),i("br"),i("br"),n._v("Mặc dù đến bây giờ đều không có thấy rõ, Thần điện đến cùng muốn cái gì, nhưng nếu như tùy ý đối phương đem người bắt đi, Đỗ Thanh Diên nhất định rơi vào nguy cơ, đến lúc đó, muốn hỏi thăm sự tình, cũng sẽ trở nên vô cùng phiền phức."),i("br"),i("br"),n._v("Huống chi, hắn cùng Thần điện cũng có cừu oán, lần nữa nhìn thấy, sao có thể để hắn yên tâm rời khỏi."),i("br"),i("br"),n._v("Đồng Thường kiếm cùng mặt khác bốn chuôi Tiên Quân đỉnh phong trường kiếm, đồng thời xuất hiện tại lòng bàn tay, đang định trước đem đối phương bốn đại Tiên Quân, đánh lén giết chết, lại tập hợp toàn bộ lực lượng đối phó vị kia Ngụy Thần, liền nghe đến trên không một cái thanh âm dồn dập vang lên, ngay sau đó một cỗ cường đại uy áp, từ đằng xa lan ra mà tới."),i("br"),i("br"),n._v("“Lại là Ngụy Thần?”"),i("br"),i("br"),n._v("Trương Huyền lông mày giương lên, vận chuyển chân khí chậm rãi ngừng lại, tiếp tục che giấu khí tức."),i("br"),i("br"),n._v("Đột nhiên xuất hiện cảm giác áp bách, tuyệt không phải Tiên Quân cường giả có thể thi triển, nói cách khác, lại có Ngụy Thần cảnh cường giả xuất hiện!"),i("br"),i("br"),n._v("“Người nào?”"),i("br"),i("br"),n._v("Trong thần điện niên nhân giờ phút này cũng phát hiện đối phương, ngừng lại, ngắm nhìn bốn phía."),i("br"),i("br"),n._v("“Vị bằng hữu này, tìm chỗ khoan dung mà độ lượng, Trục Tinh cung người, còn là đừng mang đi, lưu lại đi!”"),i("br"),i("br"),n._v("Thanh âm nhàn nhạt vang lên, cho người ta một loại dịu dàng cảm giác, một người mặc thanh y lão giả, xuất hiện tại cửa đại điện, chậm rãi đi tới."),i("br"),i("br"),n._v("Hơn sáu mươi tuổi hình dáng, ba sợi sợi râu, cho người ta một loại tiên khí mờ mịt cảm giác."),i("br"),i("br"),n._v("Tu vi cùng vừa rồi Trương Huyền cảm ứng đồng dạng, đã đạt đến Ngụy Thần cảnh, hơn nữa, cho người ta một loại nhìn không thấu tĩnh mịch cảm giác, chỉ sợ so với Hàn Kiếm Thu, Tần Nguyên tông chủ đám người, đều chỉ mạnh không yếu!"),i("br"),i("br"),n._v("“Cái này... Lại từ đâu bên trong xuất hiện?”"),i("br"),i("br"),n._v("Trương Huyền cau mày."),i("br"),i("br"),n._v("Sáu đại tông môn tông chủ, hắn đã gặp bốn cái, chỉ còn lại có Đỗ cung chủ cùng Phiêu Miễu tiên tông vị tông chủ kia vốn không che mặt."),i("br"),i("br"),n._v("Căn cứ trước đó biết tin tức, trong thiên hạ Ngụy Thần, cũng liền cái này sáu vị... Đại Sa, Nhị Sa, Huyền Bối quy các thú, là bởi vì thần huyết xuất hiện biến cố, không tính ở trong đó, Thần điện nắm giữ Ngụy Thần, không gì đáng trách..."),i("br"),i("br"),n._v("Nhưng vị này, chỗ nào đi ra?"),i("br"),i("br"),n._v("Cùng Thần điện, hiển nhiên không thuộc về cùng một phe thế lực."),i("br"),i("br"),n._v("Sở Âm cùng vị đại trưởng lão kia, cũng không có làm rõ, trong mắt đồng thời lộ ra nghi hoặc."),i("br"),i("br"),n._v("“Lưu lại? Liền nhìn ngươi có hay không thực lực này...”"),i("br"),i("br"),n._v("Cũng không nghĩ tới đột nhiên toát ra một vị, trong thần điện niên nhân hừ lạnh."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Lời còn chưa dứt, hai vị Tiên Quân đỉnh phong Thần điện võ sĩ, thân thể đồng thời run rẩy, trường kiếm trong tay nâng lên, thẳng tắp đối vị này mới vừa tới đến trước mặt Ngụy Thần đâm tới."),i("br"),i("br"),n._v("Hai cái này võ sĩ, cho dù không có tận lực phối hợp, nhưng tu vi đi đến bọn họ loại cảnh giới này, nhất cử nhất động kèm theo bố cục, hai đạo kiếm ý thời gian nháy mắt dung hợp lại cùng nhau, trước mặt không gian, xuất hiện đen kịt vết rách."),i("br"),i("br"),n._v("Kiếm khí như rồng, kiếm ý như biển."),i("br"),i("br"),n._v("Hai người đối kiếm đạo hiểu, so với Lăng Vân kiếm các đại trưởng lão Diệp Thiên đám người, đều không kém chút nào."),i("br"),i("br"),n._v("Xà nhà phía sau Trương Huyền, cảm nhận được hai người mang tới áp lực, cũng không chịu được nắm đấm xiết chặt."),i("br"),i("br"),n._v("Gần nhất tuy là tiến bộ rất lớn, có thể xưng Tiên Quân cùng cấp bậc vô địch, nhưng đột nhiên gặp được loại này tiến công, nhất định cũng sẽ rơi vào hoảng loạn, không biết như thế nào cho phải."),i("br"),i("br"),n._v("Thậm chí, liền xem như Huyền Bối quy các Ngụy Thần cảnh Tiên thú, cũng rất khó chống đỡ."),i("br"),i("br"),n._v("Tập trung tinh thần, hướng lão giả nhìn lại, muốn nhìn một chút người này ứng đối ra sao."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Nhìn chăm chú bên trong, lão giả di động, trên mặt hiện ra cười nhạt ý, tay cầm nâng lên, xuyên qua bụi hoa đồng dạng, nhẹ nhàng phất một cái."),i("br"),i("br"),n._v("  Bành! Bành!"),i("br"),i("br"),n._v("Hai vị Thần điện Tiên Quân cường giả đỉnh phong, trong nháy mắt bay ngược ra ngoài, tầng tầng lớp lớp ngã xuống đất, từng cái khuôn mặt trắng bệch, máu tươi phun mạnh."),i("br"),i("br"),n._v("Chỉ một chiêu, liền bị trọng thương!"),i("br"),i("br"),n._v("Trương Huyền thân thể chấn động."),i("br"),i("br"),n._v("Vốn cho rằng, vị này nhiều nhất cùng Hàn Kiếm Thu thực lực tương đương, hiện tại xem ra, chỉ sợ càng thêm đáng sợ!"),i("br"),i("br"),n._v("Thoạt nhìn bằng phẳng không có gì lạ một chưởng, nằm mộng cũng nghĩ không ra, nắm giữ loại này uy lực khó mà tin nổi."),i("br"),i("br"),n._v("Đánh cho bị thương hai vị võ sĩ, lão giả cũng không ngừng nghỉ, trong chốc lát đi vào áp lấy Sở Âm cùng đại trưởng lão võ sĩ trước mặt, lần nữa phất một cái."),i("br"),i("br"),n._v("Chiêu số đơn giản, lại ẩn chứa không biết bao nhiêu loại biến hóa, đem người sau tất cả ứng đối đều bao phủ ở bên trong."),i("br"),i("br"),n._v("Bành! Bành!"),i("br"),i("br"),n._v("Đồng dạng hai tiếng kêu rên, hai đại võ sĩ cùng trước đó đồng dạng, bay ra ngoài, té ngã trên đất, từng cái thương thế không cạn, xem ra, trong thời gian ngắn, không cách nào tiếp tục chiến đấu."),i("br"),i("br"),n._v("“Hai vị bị sợ hãi...”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, lão giả một tay vạch một cái, phong tỏa tại Sở Âm, đại trưởng lão hai người trên người lực lượng loại bỏ sạch sẽ."),i("br"),i("br"),n._v("“Đa tạ tiền bối ân cứu mạng...”"),i("br"),i("br"),n._v("Hai người vội vàng khom người."),i("br"),i("br"),n._v("“Không sao...”"),i("br"),i("br"),n._v("Cười nhạt một tiếng, lão giả tiến về phía trước một bước, nhìn về phía trước mắt trong thần điện niên nhân: “Ra tay đi!”"),i("br"),i("br"),n._v("“Thực lực không tệ...”"),i("br"),i("br"),n._v("Trung niên nhân híp mắt lại."),i("br"),i("br"),n._v("Không nghĩ tới trước mắt vị này, nắm giữ loại thực lực này, bốn vị Tiên Quân đỉnh phong, cho dù hắn loại thực lực này, muốn vượt qua, cũng không có dễ dàng như vậy."),i("br"),n._v(" Lão giả bàn chân một bước, lao đến."),i("br"),i("br"),n._v("Trung niên nhân nghênh đón mà lên."),i("br"),i("br"),n._v("Rầm rầm rầm!"),i("br"),i("br"),n._v("Hai đại Ngụy Thần cảnh cường giả chiến đấu, xé rách thiên địa, may mắn đại điện bên trong có trận pháp bảo vệ, nếu không, chỉ sợ toàn bộ Trục Tinh đảo, đều sẽ bởi vì giao thủ, mà dìm vào đáy biển."),i("br"),i("br"),n._v("Giấu ở phía trên Trương Huyền, biết loại này cấp bậc cường giả giao chiến, có thể ngộ nhưng không thể cầu, vận chuyển Minh Lý Chi Nhãn, nhìn kỹ lại."),i("br"),i("br"),n._v("Trung niên nhân lực lượng cương mãnh, lão giả chiêu số xảo diệu."),i("br"),i("br"),n._v("Trong nháy mắt, hai người đánh khó bỏ khó phân, chẳng phân biệt được cao thấp."),i("br"),i("br"),n._v("“Lão giả này chiêu số, vì sao cuối cùng cho ta một loại cảm giác quen thuộc?”"),i("br"),i("br"),n._v("Nhìn một hồi, Trương Huyền mày nhăn lại."),i("br"),i("br"),n._v("Vị lão giả này chiêu số, thoạt nhìn đơn giản, lại có thể thẳng vào chỗ yếu hại, công kích người khác không thể không phòng địa phương, tình huống bình thường, lực lượng của hắn cùng chân khí độ tinh thuần, kém xa đối diện trung niên nhân, có thể chiến đấu xuống, người sau lại ngay cả liền lui về phía sau, tựa hồ có chút không kiên trì nổi."),i("br"),i("br"),n._v("“Vị lão giả này võ kỹ, cùng Thiên đạo tinh giản võ kỹ, có chút giống nhau, có thể hóa phức tạp thành đơn giản, thi triển ra cường đại hơn sức chiến đấu...”"),i("br"),i("br"),n._v("Âm thầm gật đầu."),i("br"),i("br"),n._v("Khó trách có loại cảm giác quen thuộc, nguyên lai vị lão giả này võ kỹ, cùng hắn Thiên đạo công pháp, có chút giống nhau."),i("br"),i("br"),n._v("Đều nhắm thẳng vào đại đạo, công kích người khác không thể không phòng địa phương, để cho người ta mệt mỏi ứng đối."),i("br"),i("br"),n._v("“Nhìn tới hắn muốn thắng...”"),i("br"),i("br"),n._v("Ý tưởng này vừa xuất hiện, chỉ thấy trong thần điện niên nhân sắc mặt trắng nhợt, bị một chưởng đánh trúng ngực, “Lộc cộc lộc cộc đạp!” Liên tục lui về phía sau mấy bước."),i("br"),i("br"),n._v("“Ăn ta một chiêu cuối cùng...”"),i("br"),i("br"),n._v("Gào lên một tiếng, trong thần điện niên nhân lòng bàn tay nhiều ra một thanh trường kiếm, kiếm khí gào thét, thẳng tắp hướng lão giả nhanh đâm mà tới."),i("br"),i("br"),n._v("Nhìn kiếm này uy thế, hẳn là thi triển mạnh nhất tuyệt chiêu, không chết không thôi."),i("br"),i("br"),n._v("Vẻ mặt nghiêm túc, lão giả vừa định nghênh đón, liền cảm thấy thấy hoa mắt, bốn đại Tiên Quân cường giả đỉnh phong, tính cả trung niên nhân đồng thời biến mất không thấy gì nữa."),i("br"),i("br"),n._v("“Tha cho ngươi một lần, lần sau đừng để ta đụng tới...”"),i("br"),i("br"),n._v("Âm thanh quanh quẩn, mà năm vị Thần điện cường giả, đã mất đi dấu vết."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Hiển nhiên không nghĩ tới, đường đường Thần điện cường giả, vậy mà thi triển ra giương đông kích tây chiêu số, trực tiếp chạy trốn, lão giả nhịn không được cười lên, bất đắc dĩ lắc đầu, bay đến trên không tỉ mỉ quan sát một phen, xác định đối phương quả thực rời khỏi, lúc này mới lần nữa tới đến Sở Âm, trước mặt Đại trưởng lão."),i("br"),i("br"),n._v("“Đa tạ tiền bối ân cứu mạng...”"),i("br"),i("br"),n._v("Hai người quỳ gối."),i("br"),i("br"),n._v("“Không cần khách khí, ta cũng là dâng nhà ta tông chủ lệnh, tới hỗ trợ, để Trục Tinh cung cái này duy trì mấy ngàn năm tông môn, không đến mức hổ thẹn...”"),i("br"),i("br"),n._v("Lão giả mỉm cười."),i("br"),i("br"),n._v("“Tông chủ?”"),i("br"),i("br"),n._v("Hai người liếc mắt nhìn nhau, đại trưởng lão khom người: “Còn chưa thỉnh giáo tiền bối họ tên, cùng với vị trí tông môn...”"),i("br"),i("br"),n._v("Thân là Trục Tinh cung đại trưởng lão, sáu đại tông môn đỉnh cao cường giả, cơ bản đều biết."),i("br"),i("br"),n._v("Trước mắt vị này đi đến Ngụy Thần, lại còn không phải tông chủ, đến cùng là ai? Lại từ đâu bên trong đi ra?"),i("br"),i("br"),n._v("“Tại hạ Phù Trầm Tử...” Vuốt vuốt sợi râu, lão giả ánh mắt lộ ra tự hào: “Chính là Thông Thần điện trưởng lão!”"),i("br"),i("br"),n._v("“Thông Thần điện?”"),i("br"),i("br"),n._v("Sở Âm, đại trưởng lão đồng thời sững sờ."),i("br"),i("br"),n._v("Đoán nửa ngày, không nghĩ tới lại là Thông Thần điện người!"),i("br"),i("br"),n._v("Trương Huyền cũng hơi hơi kinh ngạc."),i("br"),i("br"),n._v("Sớm biết Khổng sư sáng lập cái này Thông Thần điện, cao thủ khẳng định không ít, không nghĩ tới... Một trưởng lão liền đạt đến Ngụy Thần cảnh!"),i("br"),i("br"),n._v("Hơn nữa tại thời khắc mấu chốt, cứu Trục Tinh cung người."),i("br"),i("br"),n._v("Không hổ là hái được hoàn chỉnh “Thần” chữ."),i("br"),i("br"),n._v("“Không sai...” Phù Trầm Tử trưởng lão gật gật đầu, nói: “Khổng điện chủ, phát hiện Thần điện dị động, phái ta đến đây trợ giúp Trục Tinh cung, may mắn không có tới xong...”"),i("br"),i("br"),n._v("“Đa tạ Khổng điện chủ, đa tạ Phù Trầm Tử trưởng lão...”"),i("br"),i("br"),n._v("Đại trưởng lão lần nữa ôm quyền."),i("br"),i("br"),n._v("“Khách khí!”"),i("br"),i("br"),n._v("Phù Trầm Tử đáp lễ, ngay sau đó cau mày, tựa hồ có chút không hiểu: “Thần điện từ trước tới nay không để ý tới thế sự, chẳng biết tại sao, đột nhiên tìm Trục Tinh cung phiền phức?”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Đại trưởng lão chần chừ."),i("br"),i("br"),n._v("“Không tiện nói thì cũng thôi đi!”"),i("br"),i("br"),n._v("Gặp nàng biểu lộ, Phù Trầm Tử vẻ mặt như thường, gật gật đầu, nói: “Có điều, hiện tại Đỗ cung chủ vô cùng có khả năng rơi vào nguy hiểm, không biết chuyện cụ thể lời nói, sẽ rất khó ra tay trợ giúp...”"),i("br"),i("br"),n._v("“Đại trưởng lão, là Thông Thần điện trưởng lão, vừa mới đã cứu chúng ta...” Sở Âm nhìn về phía trước mắt đại trưởng lão."),i("br"),i("br"),n._v("Hiện tại trọng yếu nhất chính là, cứu sư phụ, thực lực của các nàng, khẳng định không làm được, duy nhất có thể hoàn thành, chỉ có trước mắt vị này."),i("br"),i("br"),n._v("Không hiểu, đều loại cục diện này, đại trưởng lão vì sao còn muốn chần chừ, giấu diếm."),i("br"),i("br"),n._v("“Thôi được!”"),i("br"),i("br"),n._v("Nghe nàng nói như vậy, đại trưởng lão lắc đầu, nói: “Đã Phù Trầm Tử tiền bối là Thông Thần điện cường giả, nói cho cũng không sao!”"),i("br"),i("br"),n._v("Thông Thần điện tuy là thần bí, lại vẫn luôn là vì đại lục, chưa từng nghe nói qua, cùng Thần điện có cái gì liên quan."),i("br"),i("br"),n._v("Cũng chưa từng nghe qua có cái gì việc xấu."),i("br"),i("br"),n._v("“Thật ra thì, Thần điện lần này tới, là muốn cướp đoạt, chúng ta Trục Tinh cung trấn cung chí bảo!”"),i("br"),i("br"),n._v("“Trấn cung chí bảo?”"),i("br"),i("br"),n._v("Phù Trầm Tử trưởng lão cau mày."),i("br"),i("br"),n._v("“Không sai, chính là... Trục Tinh cung dùng để tế tự tế đàn!”"),i("br"),i("br"),n._v("Đại trưởng lão giải thích nói."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2061"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2063"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);