(window.webpackJsonp=window.webpackJsonp||[]).push([[1204],{1559:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-198-lan-sau-khong-the-chieu-theo-le-nay-nua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-198-lan-sau-khong-the-chieu-theo-le-nay-nua"}},[n._v("#")]),n._v(" Chương 198: Lần sau không thể chiếu theo lệ này nữa")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Dát?” "),t("br"),t("br"),n._v(" Một mặt cung kính Điền Long, thấy cảnh này, thân thể cứng đờ, như là bị đại bổng đập trúng đầu."),t("br"),t("br"),n._v(" Gọi hắn cái gì?"),t("br"),t("br"),n._v(" Đại sư?"),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư là ai? Chân chính y đạo đại sư, chính thức y sư, cùng tổ phụ Điền lão đứng ngang hàng tồn tại, đi đến chỗ nào đều được người tôn sùng, liền Thẩm Truy bệ hạ đều muốn lấy lễ đến tiếp đón."),t("br"),t("br"),n._v(" Dù cho hắn loại thiên tài này, trẻ tuổi bên trong tiếng tăm lừng lẫy tồn tại, cùng với nói chuyện, đều phải cẩn thận, không dám lớn tiếng chỉ lo đắc tội, nhân vật như vậy... Giờ khắc này dĩ nhiên một mặt cung kính chạy đến cái này so với mình còn nhỏ gia hỏa trước mặt, xưng hô... Đại sư?"),t("br"),t("br"),n._v(" Xem cái kia thái độ, cùng nhìn thấy thần tượng minh tinh như thế..."),t("br"),t("br"),n._v(" Giời ạ, xảy ra chuyện gì?"),t("br"),t("br"),n._v(" Mấu chốt nhất chính là, bản thân mới vừa nói cái tên này có tư cách gì xưng hô đại sư, Nguyên Ngữ đại sư liền chạy tới, này mặt đánh..."),t("br"),t("br"),n._v(" Điền Long chỉ cảm thấy khuôn mặt vặn vẹo, môi đều có chút nóng rát."),t("br"),t("br"),n._v(" Bất quá, để hắn rung mạnh sự tình còn không kết thúc, vốn tưởng rằng Nguyên Ngữ đại sư như thế cao thân phận, chuyên chạy tới, tiểu tử này nhất định sẽ cảm thấy vinh hạnh cực kỳ, tràn đầy đắc ý, đã thấy hắn hơi nhướng mày: “Không phải mấy ngày trước mới thấy sao?” "),t("br"),t("br"),n._v(" Cau mày?"),t("br"),t("br"),n._v(" Ngươi nhíu cái rắm lông mày a!"),t("br"),t("br"),n._v(" Điền Long khóe miệng kéo."),t("br"),t("br"),n._v(" Đối phương là trưởng bối, nói đã lâu không gặp... Là khách sáo, cái tên này ngược lại tốt, ngay mặt vạch trần... Còn có thể cố gắng tán gẫu không?"),t("br"),t("br"),n._v(" Vốn tưởng rằng Nguyên Ngữ đại sư sẽ bởi vậy tức giận, lại thấy hắn vỗ trán một cái, bỗng nhiên tỉnh ngộ: “Là, là! Chủ yếu ta đem đại sư tác phẩm hội họa treo ở gian phòng, ngày ngày quan sát, càng xem càng cảm thấy trình độ chênh lệch quá lớn, lúc này mới đã quên chúng ta mấy ngày trước mới từng gặp mặt.” "),t("br"),t("br"),n._v(" “Một bức họa, có cái gì có thể quan sát, thật muốn học, ta có thể dạy ngươi!” Trương Huyền gật đầu."),t("br"),t("br"),n._v(" “Tốt, vậy thì rất cảm tạ...” Nguyên Ngữ đại sư kích động râu nhếch lên."),t("br"),t("br"),n._v(" Phù phù!"),t("br"),t("br"),n._v(" Điền Long loáng một cái, cảm thấy trước mắt thiên lôi từng trận."),t("br"),t("br"),n._v(" Nguyên Ngữ... Ngươi còn có phải là đại sư? Làm sao giờ khắc này xem ra, như là học sinh tiểu học như thế?"),t("br"),t("br"),n._v(" Hướng tiểu tử này học tập? Học cái gì quỷ?"),t("br"),t("br"),n._v(" Hắn có tư cách này?"),t("br"),t("br"),n._v(" “Hắn...” "),t("br"),t("br"),n._v(" Thực sự không nhịn được, nhìn về phía Bạch Tốn."),t("br"),t("br"),n._v(" “Trương lão sư là chân chính thư họa đại sư, liền Lục Trầm đại sư đều khâm phục không thôi, nếu như hắn đều không xứng với đại sư danh hiệu, ai còn xứng được với?” Bạch Tốn tràn đầy đắc ý, khinh bỉ nhìn sang."),t("br"),t("br"),n._v(" Để ngươi trang bức!"),t("br"),t("br"),n._v(" Lần này nói không ra lời!"),t("br"),t("br"),n._v(" Xem ngươi vừa nãy hung hăng dáng vẻ, còn “Không biết trời cao đất rộng”, là ngươi không biết trời cao đất rộng đi!"),t("br"),t("br"),n._v(" “Thư họa đại sư?” "),t("br"),t("br"),n._v(" Điền Long lúc này mới nhớ tới đến, Nguyên Ngữ đại sư không những y đạo tinh thông, liền ngay cả thư họa cũng hết sức lợi hại."),t("br"),t("br"),n._v(" Chỉ là làm sao đều không nghĩ ra, cái này liền hai mươi cũng chưa tới, mất hết tên tuổi học viện lão sư, đến cùng đạt đến cái gì trình độ, liền Nguyên Ngữ loại này chân chính thư họa mọi người, cũng như này tôn sùng."),t("br"),t("br"),n._v(" Thư họa tuy rằng không sánh được trà đạo thịnh hành, nhưng cũng là thật cửu lưu nghề nghiệp một trong, rất khó học có thành tựu."),t("br"),t("br"),n._v(" “Khả năng là vẽ một số tiền bối tác phẩm hội họa, hơi có chút tiếng tăm, đại sư, chỉ là khen tặng xưng hô, không phải vậy, loại này tuổi, coi như vừa sinh ra học tập vẽ tranh, cũng không thể đạt đến đại sư trình độ a...” "),t("br"),t("br"),n._v(" Trà đạo, thư họa tương tự, hắn biết trong đó độ khó."),t("br"),t("br"),n._v(" Hắn theo bảy tuổi liền bắt đầu học tập trà đạo, đến hiện tại đã ròng rã mười tám năm, cũng chỉ đạt đến trà đạo nhị trọng  cảnh giới."),t("br"),t("br"),n._v(" Dùng luyện đan sư cấp bậc đến phân chia mà nói, tương đương với cao cấp luyện đan học đồ đỉnh phong, khoảng cách chính thức, còn có khoảng cách không nhỏ."),t("br"),t("br"),n._v(" Đối phương so với mình còn nhỏ, làm sao có khả năng thật đạt đến đại sư cấp bậc?"),t("br"),t("br"),n._v(" Đại sư, thế nhưng xưng hô chính thức nghề nghiệp."),t("br"),t("br"),n._v(" Thật giống như chính thức luyện đan sư, có thể xưng là luyện đan đại sư, chính thức thư họa sư, hoặc là có thể vẽ ra tứ cảnh tác phẩm hội họa, cũng có thể xưng hô như vậy."),t("br"),t("br"),n._v(" Một cái không đủ hai mươi tiểu tử làm ra tứ cảnh thư họa?"),t("br"),t("br"),n._v(" Rồi cùng nói hắn có thể pha ra tứ cảnh nước trà như thế, đùa gì thế!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Không biết một bên Điền Long không hề tin tưởng, Trương Huyền thấy Nguyên Ngữ đại sư chạy tìm đến mình, hấp dẫn không ít ánh mắt, khoát tay áo một cái: “Ngươi trước vội vàng đi, thật muốn học, tranh thủ tìm ta là được!” "),t("br"),t("br"),n._v(" Hắn đến đây chính là muốn gặp gỡ ba sư, thuận tiện hỏi một lần trong cơ thể hắc khí sự tình, không muốn quá mức xuất chúng, chung quy, làm người phải khiêm tốn mà!"),t("br"),t("br"),n._v(" Nếu như cái tên này vẫn theo, còn làm sao điệu thấp?"),t("br"),t("br"),n._v(" Căn bản không phù hợp hắn làm người phẩm chất cùng yêu cầu mà!"),t("br"),t("br"),n._v(" “Là, vậy ta tới trước bên kia, chờ rảnh rỗi lại tìm Trương đại sư học tập.” Nguyên Ngữ đại sư cũng biết thân phận của hắn quá làm người khác chú ý, vẫn lưu lại tại này, nhất định sẽ dẫn tới không cần thiết phiền phức, ôm quyền rời đi."),t("br"),t("br"),n._v(" Đạt giả vi sư, đối phương tuy rằng tuổi trẻ, lại có thể làm ra cảnh giới thứ năm tác phẩm hội họa, chỉ bằng vào điểm ấy, đã đáng giá hắn tôn kính cùng học tập."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Đến thăm khiếp sợ, tức giận, hai người trò chuyện vẫn chưa nghe rõ, lúc này lần thứ hai nhìn về phía vị này Trương Huyền lão sư, không những không có khâm phục, càng thêm tức giận."),t("br"),t("br"),n._v(" Hắn yêu thích Hoàng Ngữ không phải bí mật gì, chính vì như thế, mới cùng Bạch Tốn vẫn đối chọi gay gắt."),t("br"),n._v(" Biết gia gia đại thọ Hoàng Ngữ sẽ đến, chuyên môn học một bộ pha trà thủ pháp, muốn biểu hiện một chút, còn chưa kịp liền bị cái tên này làm mất mặt, còn đánh như thế đau, không không nhịn được động thủ, coi như rất có định lực."),t("br"),t("br"),n._v(" “Coi như không phải cái gì chân chính đại sư, có thể làm cho Nguyên Ngữ đại sư tự mình lại đây thăm hỏi, cũng khẳng định không đơn giản...” "),t("br"),t("br"),n._v(" Tuy rằng tức giận, lại không mất đi bình tĩnh, chính muốn như thế nào để cái tên này cũng ra một lần xấu, hòa nhau một thành, đột nhiên con mắt hơi chuyển động, một ý nghĩ xông ra."),t("br"),t("br"),n._v(" “Đúng rồi, ngày hôm nay bọn họ cũng tới, vừa nãy có người hướng ta bẩm báo, chỉ nghĩ chờ tiểu Ngữ, còn chưa kịp đi qua, để bọn họ gặp mặt, khẳng định rất thú vị...” "),t("br"),t("br"),n._v(" Nghĩ tới đây, bước lên trước: “Trương lão sư, thực sự thật không tiện, ta không biết ngươi am hiểu thư họa, có bao nhiêu mạo phạm, vẫn xin xem xét!” "),t("br"),t("br"),n._v(" Ngoài miệng nói thật không tiện, trên mặt không hề có một chút thật không tiện biểu cảm."),t("br"),t("br"),n._v(" Đối với loại này hai mặt, trong lòng không giống nhau gia hỏa, Trương Huyền cũng lười tính toán, tùy ý khoát tay áo một cái: “Lần này thì thôi, lần sau không thể chiếu theo lệ này nữa!” "),t("br"),t("br"),n._v(" “Ngươi...” "),t("br"),t("br"),n._v(" Thấy mình khách khí một câu, đối phương lại mang theo giáo huấn giọng điệu, giống như lão sư dạy bảo học sinh, Điền Long lần thứ hai cảm thấy ngực bị đè nén, suýt chút nữa nổ tung."),t("br"),t("br"),n._v(" Ta còn có thể cố gắng tán gẫu không?"),t("br"),t("br"),n._v(" Ta nghe người ta nói, am hiểu thư họa, đều tao nhã, nói chuyện có hàm dưỡng có chiều sâu, cái tên này làm sao câu nói đầu tiên có thể làm người ta tức chết..."),t("br"),t("br"),n._v(" Thực sự là thư họa đại sư?"),t("br"),t("br"),n._v(" Nắm đấm xiết chặt, suýt chút nữa nổ tung, bất quá, hắn càng là tức giận, càng không biểu hiện ra, một đạo ý lạnh tại đáy mắt lấp loé, trên mặt lại khẽ mỉm cười: “Là, lần sau ta chú ý, Lưu sư bọn họ còn không biết lúc nào lại đây, ta xem chúng ta không bằng đi trên lầu chờ đi, chỉ cần bọn họ đến, nhất định có thể nhìn thấy!” "),t("br"),t("br"),n._v(" Cho người quen biết hắn nhìn thấy hắn dáng dấp này, tuyệt đối biết, nhất định phải có người muốn xui xẻo rồi."),t("br"),t("br"),n._v(" “Chuyện này...” Hoàng Ngữ chần chờ một chút, nhìn về phía bênh cạnh thanh niên."),t("br"),t("br"),n._v(" Tuy rằng nàng là danh sư học đồ, địa vị tôn sùng, nhưng Trương Huyền thế nhưng cùng Lục Trầm đại sư ngang hàng luận giao, không cam lòng tự ý làm chủ."),t("br"),t("br"),n._v(" Trương Huyền gật đầu."),t("br"),t("br"),n._v(" Vừa nãy Nguyên Ngữ đại sư bái kiến, đã có không ít người chú ý, đối với hắn mà nói, đi trên lầu chờ cũng như thế, ngược lại ngày hôm nay thời gian đều làm lỡ, không cái gì vừa vừa sốt ruột."),t("br"),t("br"),n._v(" “Được rồi!” Thấy hắn đồng ý, Hoàng Ngữ đồng ý."),t("br"),t("br"),n._v(" Thấy cảnh này, Điền Long càng thêm rung mạnh."),t("br"),t("br"),n._v(" Hắn không biết Trương Huyền cùng Hoàng Ngữ ở giữa quan hệ, thấy hai người bộ này dáng vẻ còn tưởng rằng là liếc ngang liếc dọc, tình chàng ý thiếp..."),t("br"),t("br"),n._v(" Mãnh liệt đố kị, để da mặt co giật, bất cứ lúc nào cũng sẽ bạo phát."),t("br"),t("br"),n._v(" Đại sảnh lầu hai, mới là chiêu đãi khách mời địa phương, có thể uống trà, cũng có thể ngồi ở cửa sổ vừa thưởng thức phong cảnh, từ nơi này có thể quan sát toàn bộ sơn trang, khác nào một cái to lớn hoa viên, vui tai vui mắt."),t("br"),t("br"),n._v(" Dọc theo cầu thang, bốn người đi lên đi."),t("br"),t("br"),n._v(" Lầu hai xếp đầy trong một phòng trang nhã, không ít người đã đi vào ngồi, pha trà uống nước, chuyện trò vui vẻ."),t("br"),t("br"),n._v(" Có thể tới tham gia Điền lão tiệc mừng thọ, hầu như đều là Thiên Huyền Vương quốc nhân vật nổi danh, thân phận không thấp, đều có chuyên môn nha hoàn hầu hạ."),t("br"),t("br"),n._v(" Những nha hoàn này, xem ra cũng chuyên môn đã học, đều quen thuộc trà đạo, pha trà nước chảy mây trôi, làm cho người ta dị dạng vẻ đẹp."),t("br"),t("br"),n._v(" “Bên này...” "),t("br"),t("br"),n._v(" Điền Long đi đầu dẫn đường, vừa đi một bên nhìn quanh hai bên, tựa hồ đang tìm cái gì người, đột nhiên ánh mắt sáng lên, ngừng lại."),t("br"),t("br"),n._v(" “Lục huynh, Vương huynh, đến rồi cũng không chiêu hô ta một tiếng, cũng không biết các ngươi tại chuyện này...” "),t("br"),t("br"),n._v(" Thuận hắn nói chuyện phương hướng nhìn lại, liền thấy hai người ngồi ở cách đó không xa."),t("br"),t("br"),n._v(" Chính là Hồng Thiên học viện minh tinh giáo sư, Lục Tầm cùng Vương Siêu."),t("br"),t("br"),n._v(" Bọn họ ngày hôm nay cũng tới tham gia Điền lão tiệc mừng thọ."),t("br"),t("br"),n._v(" “Điền Long, ngươi là cố ý?” Nhìn thấy cái tên này, dĩ nhiên đem nhóm người mình mang tới đây, Hoàng Ngữ nơi nào không biết mục đích của đối phương, sầm mặt lại."),t("br"),t("br"),n._v(" Người người đều biết Trương Huyền lão sư muốn cùng Lục Tầm lão sư giao đấu, hai người như nước với lửa, mang tới trước mặt, rất rõ ràng rắp tâm bất lương."),t("br"),t("br"),n._v(" “Tiểu Ngữ, ngươi này nơi nào lời nói, ta này không phải mới vừa nhìn thấy Lục huynh, Vương huynh sao? Nếu đều biết, không bằng một chỗ ngồi uống trà, cũng vừa tốt nếm thử thủ nghệ của ta.” "),t("br"),t("br"),n._v(" Điền Long khóe miệng giương lên, lén lút nhìn về phía Trương Huyền."),t("br"),t("br"),n._v(" Tiểu tử, ngươi vừa nãy không phải rất hung hăng sao?"),t("br"),t("br"),n._v(" “Lần sau không thể chiếu theo lệ này nữa”, hạ ngươi muội a!"),t("br"),t("br"),n._v(" Hiện tại Lục Tầm lão sư tại đây, còn đại sư, còn cao nhân... Ta xem ngươi còn làm sao cho ta trang!"),t("br"),t("br"),n._v(" Lục Tầm Vương Siêu hiển nhiên cũng không nghĩ tới ở đây sẽ gặp phải Trương Huyền, Vương Siêu hơi nhướng mày, nhìn về phía Điền Long: “Điền huynh, Điền lão tiệc mừng thọ không kiểm tra thư mời sao? Làm sao người nào đều có thể đi vào?” "),t("br"),t("br"),n._v(" Nghe nói như thế, Điền Long hưng phấn suýt chút nữa nhảy lên đến."),t("br"),t("br"),n._v(" Sớm nghe nói Vương Siêu tính cách nôn nóng, không nghĩ tới quả nhiên ra sức."),t("br"),t("br"),n._v(" Còn sầu làm sao đem mâu thuẫn dẫn lại đây, cái tên này vừa mở miệng liền trên cầu."),t("br"),t("br"),n._v(" Trong lòng mừng nở hoa, trên mặt lại cố ý lộ ra vẻ khó khăn: “Thư mời khẳng định đã kiểm tra, Hoàng Ngữ trợ giáo, Bạch tiểu vương gia đều là ta tự mình mời, còn vị này... Trương lão sư, ta tuy rằng không mời, nhưng nếu là bọn họ khách mời, ta cũng không tốt nhiều lời...” "),t("br"),t("br"),n._v(" “Khách mời? Một cái đê cấp giáo sư, cũng có thể xưng là khách mời?” "),t("br"),t("br"),n._v(" Vương Siêu cười nhạo."),t("br"),t("br"),n._v(" Trương Huyền chỉ là học viện đê cấp giáo sư, liền cao cấp đều không hỗn bên trên, chớ nói rõ chi là tinh giáo sư, thật muốn bàn về địa vị, xác thực không đủ tới tham gia Điền lão tiệc mừng thọ."),t("br"),t("br"),n._v(" “Được rồi, Vương Siêu!” "),t("br"),t("br"),n._v(" Lục Tầm khoát tay áo một cái, đánh gãy bạn tốt trào phúng, nhìn Trương Huyền một chút, vẻ mặt hờ hững: “Thật không tiện, Trương lão sư, Vương Siêu nói chuyện luôn luôn nhanh mồm nhanh miệng, không cái gì quy củ. Bất quá... Nơi này xác thực không phải ngươi nên đến, mau đi trở về đi, không nên để cho Hoàng Ngữ tiểu thư, bởi vì ngươi mà bị mắng, cũng đừng làm cho Hồng Thiên học viện, ném danh tiếng!” "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-206"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-208"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);