(window.webpackJsonp=window.webpackJsonp||[]).push([[1215],{1569:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-199-nguoi-chinh-la-truong-huyen-lao-su-đi"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-199-nguoi-chinh-la-truong-huyen-lao-su-đi"}},[n._v("#")]),n._v(" Chương 199: Ngươi chính là Trương Huyền lão sư đi!")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v(" “Lục Tầm, ngươi có ý gì?” "),g("br"),g("br"),n._v(" Nghe nói như thế, một bên Hoàng Ngữ lại không nhịn được."),g("br"),g("br"),n._v(" Cái gì bị mắng? Cái gì ném danh tiếng?"),g("br"),g("br"),n._v(" Trương lão sư tới tham gia tiệc mừng thọ, là ba sư mời, làm sao đến trong miệng ngươi, liền mất mặt?"),g("br"),g("br"),n._v(" “Không có ý gì, thân phận gì, đi ra sao địa phương. Hoàng Ngữ tiểu thư, ta biết ngươi là trợ giáo, rất được Lưu sư tin cậy cùng sủng ái. Bất quá, cũng không thể tùy hứng làm bậy, đây là Điền lão tiệc mừng thọ, tốt nhất chú ý một chút! Điền lão là ai? Lưu sư đã từng lão sư, nắm giữ nửa sư tình nghĩa, ngươi mang một cái mất hết tên tuổi đê cấp giáo sư lại đây, một khi trêu đến Lưu sư không vui, liền phiền phức!” "),g("br"),g("br"),n._v(" Lục Tầm lắc đầu."),g("br"),g("br"),n._v(" Sắc mặt của hắn không có trào phúng cũng không hề tức giận, chỉ giống là trần thuật nhất bình thản sự thực."),g("br"),g("br"),n._v(" Ở trong mắt hắn, Trương Huyền giáo viên sát hạch đến cùng có phải là thật hay không không điểm, cũng không đáng kể, cái tên này chỉ là cái đê cấp giáo sư, loại này cấp bậc cao tiệc mừng thọ, căn bản không tư cách tham gia, cưỡng ép mang tới, chỉ có thể kéo thấp bản thân đẳng cấp."),g("br"),g("br"),n._v(" Vậy thì cùng mang một tên ăn mày đi đại tửu lâu như thế, mặc trên người xinh đẹp nữa, lại có khí chất, bị đồng bạn liên lụy, cũng là trở nên trở thành chuyện cười."),g("br"),g("br"),n._v(" Long cùng Phượng Hoàng cùng nhau, mới là long, cùng heo cùng nhau, vậy thì là chó."),g("br"),g("br"),n._v(" “Ngươi...” "),g("br"),g("br"),n._v(" Nghe nói như thế, Hoàng Ngữ tức giận còn muốn nói gì, liền thấy Trương Huyền khoát tay chặn lại, đi tới cái bàn trước mặt, ngồi xuống."),g("br"),g("br"),n._v(" Tiện tay bưng lên một cái chén trà đổ tràn đầy một bát trà, ngẩng đầu nhìn hướng trước mắt hai cái tự cho là đúng minh tinh giáo sư."),g("br"),g("br"),n._v(" “Náo loạn nửa ngày, ta là mất hết tên tuổi đê cấp giáo sư, đã như vậy... Lục Đại lão sư, vì sao lại muốn cùng ta tiến hành sư giả bình trắc? Chẳng phải ném ngươi anh minh uy phong tiếng tăm?” "),g("br"),g("br"),n._v(" Vốn là hai người này trang bức, hắn không thèm để ý, ai biết..."),g("br"),g("br"),n._v(" Cho các ngươi mặt không phải? Không chà đạp ta không cao hứng sao?"),g("br"),g("br"),n._v(" Nghe nói như thế, Hoàng Ngữ, Bạch Tốn hai người liếc nhau một cái, đồng thời cảm khái, không hổ là Trương đại sư, nói chuyện chính là tàn nhẫn."),g("br"),g("br"),n._v(" Đúng vậy, ngươi không phải nói mang hắn sẽ kéo ta chân sau sao?"),g("br"),g("br"),n._v(" Đã như vậy, ngươi đường đường đại minh tinh giáo sư, cùng hắn tỷ thí làm cái gì? Này không phải tự mình đánh mình mặt sao?"),g("br"),g("br"),n._v(" “Ngươi?” Lục Tầm nhẹ nhàng nở nụ cười, không chút nào bị làm mất mặt ý tứ, mí mắt nhấc lên: “Ngươi chỉ là cái ta hướng lên trên đặt chân ván cầu mà thôi, muốn trách, cũng chỉ trách ngươi vận may không được, đụng vào trên đầu ta.” "),g("br"),g("br"),n._v(" “Ván cầu?” "),g("br"),g("br"),n._v(" “Không sai, ngày mai sư giả bình trắc, nhất định sẽ hấp dẫn Dương sư cùng với ba vị danh sư đến đây, đến thời điểm Lục huynh sẽ đem chính mình dạy học thành quả triển lộ ra, đến lúc đó, bốn vị danh sư tất nhiên nhìn với cặp mắt khác xưa, Lục huynh là có thể nhảy một cái Long môn, thành là chân chính trợ giáo học đồ, mà ngươi, chỉ là cái làm nền mà thôi!” "),g("br"),g("br"),n._v(" Vương Siêu nhẹ nhàng nở nụ cười, dùng ánh mắt đáng thương nhìn sang."),g("br"),g("br"),n._v(" đọc tｒuyện cùng //ngantruyen.com/"),g("br"),n._v(" Chỉ là cái đá kê chân mà thôi, vẫn đúng là coi chính mình là người trưởng thành đồ vật?"),g("br"),g("br"),n._v(" Nói thật, có thể chà đạp hô ngươi cũng là để mắt ngươi, cần phải cảm thấy vinh hạnh mới là."),g("br"),g("br"),n._v(" “Các ngươi liền xác định có thể thắng?” "),g("br"),g("br"),n._v(" Thế mới biết đối phương đánh ý đồ này, Trương Huyền lắc lắc đầu."),g("br"),g("br"),n._v(" Ba sư ý nghĩ hắn không biết, nhưng “Dương sư” chắc chắn sẽ không đến xem, hơn nữa, càng sẽ không thu vị này Lục Tầm vì học đồ."),g("br"),g("br"),n._v(" “Kỳ thực thắng không thắng cũng không đáng kể, khả năng còn có việc, các ngươi không biết!” "),g("br"),g("br"),n._v(" Vương Siêu, Lục Tầm chính muốn nói tiếp, một bên Điền Long nở nụ cười."),g("br"),g("br"),n._v(" “Cái gì?” "),g("br"),g("br"),n._v(" Vương Siêu nhìn sang."),g("br"),g("br"),n._v(" Điền Long nhẹ nhàng nở nụ cười, một mặt tự tin: “Lưu sư lần này đến Vương thành, cho ông nội ta chúc thọ, chỉ là một người trong đó mục đích, chủ yếu nhất chính là muốn thu Lục huynh vì trợ giáo, chuyện này vốn là là bí mật, nhưng Lưu sư cho lão gia tử viết trong thư nói rồi, ta vừa vặn nhìn thấy, biết đến rõ ràng.” "),g("br"),g("br"),n._v(" “Ồ? Thật sự?” "),g("br"),g("br"),n._v(" Hiển nhiên cũng không biết tin tức này, Lục Tầm ánh mắt sáng lên."),g("br"),g("br"),n._v(" “Cái kia là tự nhiên, Lục huynh danh tiếng lớn như vậy, đạt được danh sư thưởng thức, cũng rất bình thường, Lưu sư là ta học sinh của gia gia, không dám ẩn giấu, rõ ràng mười mươi đều ở trong thư viết. Liền bởi vì biết những cái này, mới để ta cho ngươi phát thiệp mời cùng mời, dự định ở đây hỗ trợ dẫn tiến một phen, cuối cùng đến cái thầy trò quen biết nhau, tại chỗ thu đồ đệ, truyền vì giai thoại!” "),g("br"),g("br"),n._v(" Điền Long nói."),g("br"),g("br"),n._v(" “Đa tạ Điền lão ân tình...” "),g("br"),g("br"),n._v(" Nghe được đối phương nói như vậy xác nhận, Lục Tầm đứng lên, đầy mặt hưng phấn."),g("br"),g("br"),n._v(" Biết Dương sư bên này không hi vọng, hắn lần thứ hai đưa mắt chuyển đến ba sư nơi này, chỉ có điều hai ngày nay ba sư thật giống đều đi vắng, mỗi lần đều bái phỏng không tới, để hắn có chút sốt ruột."),g("br"),g("br"),n._v(" Vốn tưởng rằng sẽ cùng Dương sư như vậy bị nhỡ, nằm mơ đều không nghĩ tới, Lưu sư nguyên lai đã sớm coi trọng bản thân."),g("br"),g("br"),n._v(" Thực sự là quá tốt rồi."),g("br"),g("br"),n._v(" “Xem ra chúng ta sau đó muốn trở thành đồng môn học đồ...” "),g("br"),g("br"),n._v(" Cao hứng nở nụ cười, nhìn về phía Hoàng Ngữ, Lục Tầm lộ ra hiền lành tiếu ý."),g("br"),g("br"),n._v(" “Ai cùng ngươi đồng môn!” Hoàng Ngữ hừ lạnh một tiếng."),g("br"),g("br"),n._v(" Lưu sư đến cùng ý tưởng gì, nàng cũng không biết, Điền Long nếu dám nói như vậy, khẳng định chắc chắn, chẳng lẽ... Thật muốn cùng cái tên này đồng môn?"),g("br"),g("br"),n._v(" Hoàng Ngữ có chút băn khoăn."),g("br"),g("br"),n._v(" Cái tên này một mặt tự cho là đúng, làm cho nàng vô cùng khó chịu."),g("br"),g("br"),n._v(" Bất quá, nói thật, cái tên này nhưng cũng là có thật năng lực, liền ngay cả nàng cũng khâm phục không thôi."),g("br"),g("br"),n._v(" Không có danh sư chỉ đạo, đều có thể tại Thiên Huyền Vương quốc lăn lộn như vậy vui vẻ sung sướng, đối với giáo dục học sinh, khẳng định có rất sâu kiến giải."),g("br"),g("br"),n._v(" “Nghe đến chưa? Lục huynh lập tức liền là danh sư học đồ, ngươi chẳng lẽ còn có mặt đợi ở chỗ này?” "),g("br"),g("br"),n._v(" Đạt được tốt như vậy tin tức, Vương Siêu cũng tràn đầy cao hứng, lần thứ hai nhìn về phía ngồi ở trước mặt một mặt không sao cả Trương Huyền, càng thêm căm ghét."),g("br"),g("br"),n._v(" “Lập tức liền là? Không còn không thu sao?” "),g("br"),g("br"),n._v(" Đối với mấy người này cao hứng, Trương Huyền không thèm để ý."),g("br"),g("br"),n._v(" Hắn cũng không nghĩ tới, Lưu sư nguyên lai đã sớm coi trọng Lục Tầm, trong lòng không khỏi cân nhắc có muốn hay không dùng “Dương Huyền” thân phận, khuyên can một lần."),g("br"),n._v(" Chung quy, vạn nhất “Dương Huyền” thật thu lấy Lưu Lăng vì học sinh, Lục Tầm chính là của hắn đồ tôn, cái tên này như vậy tự cho là đúng, giả vờ giả vịt..."),g("br"),g("br"),n._v(" Muốn như vậy đồ tôn rất khó chịu!"),g("br"),g("br"),n._v(" Có chút không quá muốn muốn a!"),g("br"),g("br"),n._v(" “Để ngươi trang, ta xem ngươi một lúc nữa còn có thể hay không thể trang lên đến...” "),g("br"),g("br"),n._v(" Thấy cái tên này thấy quan tài đều không rơi lệ, vẫn còn ở nơi này giả vờ giả vịt, hai cái liều mạng muốn trở thành hắn đồ tôn trong mắt người tràn đầy khinh bỉ."),g("br"),g("br"),n._v(" Gặp qua không biết trời cao đất rộng, chưa từng thấy như thế không biết trời cao đất rộng."),g("br"),g("br"),n._v(" Chỉ cần ba sư đi tới, Lục Tầm liền ngay lập tức sẽ trở thành danh sư trợ giáo, lời nói đều nói đến đây phần, còn không thấy ngại ngốc tại chuyện này... Sao không biết xấu hổ như vậy đây?"),g("br"),g("br"),n._v(" Hơn nữa, còn ngồi ở tại chỗ không nhúc nhích châm trà uống... Này nước trà, sao uống được xuống!"),g("br"),g("br"),n._v(" Nếu như ta, sợ là sớm đã mất mặt ném nhảy lầu..."),g("br"),g("br"),n._v(" “Ha ha, Vương huynh cũng không thể nói như vậy, ta mới vừa rồi còn nghe tiểu Ngữ, Bạch tiểu vương gia, xưng hô vị này Trương lão sư vì Trương đại sư, nghe nói hắn thế nhưng thư họa đại sư, uy phong rất a!” "),g("br"),g("br"),n._v(" Thấy Trương Huyền da mặt dày vượt qua tưởng tượng, Điền Long tựa như cười mà không phải cười nói."),g("br"),g("br"),n._v(" “Thư họa đại sư?” "),g("br"),g("br"),n._v(" Lục Tầm hai tay một lưng, liếc mắt nhìn sang, tràn đầy xem thường: “Nói với ta thư họa đại sư? Ngươi không cảm thấy buồn cười? Khoác lác cũng phải có cái mức độ!” "),g("br"),g("br"),n._v(" Tuy rằng hắn nhiều năm không về nhà, nhưng muốn trở thành thư họa đại sư độ khó lớn bao nhiêu, sẽ không biết?"),g("br"),g("br"),n._v(" Một cái học viện nổi tiếng rác rưởi lão sư, không biết như thế nào cùng Hoàng Ngữ cài đặt quan hệ, nhảy nhót một lần cũng là thôi, vẫn đúng là càng trang càng nghiện không phải?"),g("br"),g("br"),n._v(" “Đúng đấy, Điền huynh nghe lầm đi, Lục huynh thế nhưng sinh ra tại thư họa thế gia, phụ thân Lục Trầm đại sư, là Thiên Huyền Vương quốc lợi hại nhất thư họa đại sư, Thẩm Truy bệ hạ đều từng theo học tập. Lục huynh từ nhỏ đến lớn tiếp xúc nhiều nhất chính là vật này, trong vương quốc có hay không thư họa đại sư, sao lại không biết?” "),g("br"),g("br"),n._v(" Vương Siêu cũng cười lắc đầu."),g("br"),g("br"),n._v(" “Có hai người các ngươi mở miệng, ta liền yên tâm, mới vừa rồi còn thật sự cho rằng là thật sự, chung quy Nguyên Ngữ...” "),g("br"),g("br"),n._v(" Điền Long đang muốn đem Nguyên Ngữ đại sư vừa nãy thái độ nói ra, liền nghe đến dưới lầu một trận ầm ĩ, hỗn loạn."),g("br"),g("br"),n._v(" “Nhanh lên một chút, ba sư đến rồi!” "),g("br"),g("br"),n._v(" “Còn có Thẩm Truy bệ hạ, nhanh hơn đi nghênh đón...” "),g("br"),g("br"),n._v(" Tiếng bàn luận vang lên."),g("br"),g("br"),n._v(" “Ba sư cùng Thẩm Truy bệ hạ tới?” "),g("br"),g("br"),n._v(" Nghe đến phía dưới cãi vã, Điền Long lập tức khàn giọng ở, nhìn về phía Lục Tầm hai người: “Lục huynh, Vương huynh, chúng ta cũng hạ đi nghênh đón đi!” "),g("br"),g("br"),n._v(" “Tốt!” "),g("br"),g("br"),n._v(" Tam đại danh sư đi tới, người trong phòng cũng không ngồi yên được nữa, nhao nhao xuống lầu."),g("br"),g("br"),n._v(" “Hoàng Ngữ học tỷ, đi thôi, đi nghênh đón lão sư!” "),g("br"),g("br"),n._v(" Lục Tầm cười nói."),g("br"),g("br"),n._v(" “Ai là ngươi học tỷ...” Hoàng Ngữ hừ một tiếng, nhìn về phía Trương Huyền."),g("br"),g("br"),n._v(" “Đi xuống đi!” "),g("br"),g("br"),n._v(" Biết Hoàng Ngữ thân là học sinh, lão sư đi tới, nhất định phải đi nghênh đón, Trương Huyền gật gù."),g("br"),g("br"),n._v(" Lại nói, hắn đến đây chính là thấy ba sư, nếu lại đây, vừa vặn qua xem một chút."),g("br"),g("br"),n._v(" Ào ào ào!"),g("br"),g("br"),n._v(" Mấy người nối đuôi nhau mà ra, lúc này một tầng đã chạy không ai."),g("br"),g("br"),n._v(" Đi ra đại sảnh, quả nhiên thấy phía trước trên lối đi, đi rồi mấy bóng người, chính là Lưu Lăng, Trang Hiền, Trịnh Phi tam đại danh sư cùng Thẩm Truy bệ hạ."),g("br"),g("br"),n._v(" Mấy người này tại Dương sư trước mặt một mực cung kính, ở đây, vẫn là khí tràng mười phần."),g("br"),g("br"),n._v(" “Bái kiến Lưu sư, Trang sư, Trịnh sư, bái kiến bệ hạ!” "),g("br"),g("br"),n._v(" Tới tham gia tiệc mừng thọ, không thiếu gia tộc lớn tộc trưởng, trưởng lão, giờ khắc này tất cả đều cúi đầu."),g("br"),g("br"),n._v(" “Chúng ta giống như các ngươi, cũng là cho Điền lão chúc thọ, mọi người không cần câu nệ.” "),g("br"),g("br"),n._v(" Thẩm Truy bệ hạ cười nói."),g("br"),g("br"),n._v(" Hắn nói ung dung, mọi người cũng không dám, từng cái từng cái cười xòa, không dám nói lời nào."),g("br"),g("br"),n._v(" “Chúng ta đi qua đi!” "),g("br"),g("br"),n._v(" Điền Long thấy ba sư cùng Thẩm Truy bệ hạ đi tới, khẽ mỉm cười, mang người Lục Tầm, Vương Siêu tiến lên nghênh tiếp, Hoàng Ngữ làm danh sư học đồ, làm sao có khả năng rớt lại phía sau, cùng Bạch Tốn, Trương Huyền cùng một chỗ, song song đi tới."),g("br"),g("br"),n._v(" “Lão sư!” "),g("br"),g("br"),n._v(" Đi tới trước mặt, Hoàng Ngữ, Bạch Tốn đồng thời khom người."),g("br"),g("br"),n._v(" “Người mời tới sao?” "),g("br"),g("br"),n._v(" Nhìn thấy hai người, Lưu Lăng, Trang Hiền, Trịnh Phi con mắt đồng thời sáng ngời."),g("br"),g("br"),n._v(" Hai ngày nay bọn họ vẫn thay đổi quần áo, canh giữ ở Hồng Thiên học viện, chờ Trương Huyền đi ra, muốn nhìn một chút có thể hay không tìm cơ hội thu làm học đồ, nằm mơ đều không nghĩ tới, cái tên này căn bản là không trở về, thật giống biến mất rồi như thế, liền ngay cả Thẩm Truy bệ hạ cũng không tìm tới tung tích."),g("br"),g("br"),n._v(" Không có cách nào không thể làm gì khác hơn là để Hoàng Ngữ, Bạch Tốn hai người đi tìm, vốn là không báo hy vọng quá lớn, giờ khắc này thấy hai người trên mặt không có một chút nào ủ rũ, trong lòng đồng thời hơi động."),g("br"),g("br"),n._v(" “Mời tới, chính là bên cạnh ta vị này...” "),g("br"),g("br"),n._v(" Hoàng Ngữ đang muốn giới thiệu, một bên Lục Tầm đánh gãy nàng mà nói, đầy mặt kích động tiến lên ôm quyền: “Tại hạ Lục Tầm, gặp qua Lưu sư, Trang sư, Trịnh sư...” "),g("br"),g("br"),n._v(" Tự giới thiệu mình xong, vốn tưởng rằng tam đại danh sư sẽ lập tức rất cao hứng hỏi han ân cần, thu làm học đồ, đã thấy ba người không để ý tí nào, thật giống không thấy giống như vậy, thẳng đến Điền Long đi tới, đem hắn vây vào giữa, một mặt tươi cười."),g("br"),g("br"),n._v(" “Ngươi chính là Trương Huyền lão sư đi, rốt cục để ta nhìn thấy người thật, có thể để ta dễ tìm...” "),g("br"),g("br"),n._v(" “Ế?” Điền Long ngốc tại chỗ."),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-207"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-209"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);