(window.webpackJsonp=window.webpackJsonp||[]).push([[1218],{1573:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2068-đi-vao-than-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2068-đi-vao-than-đien"}},[n._v("#")]),n._v(" Chương 2068: Đi vào Thần điện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Đi vào Thần điện【 canh thứ nhất】"),t("br"),t("br"),n._v("Chương trước trở về mục lục chương sau trở về trang sách"),t("br"),t("br"),n._v("Đem cái này hai đầu Ngụy Thần thi thể thu lại, Trương Huyền mang theo mọi người tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Đi không xa, một vị Tiên Quân cảnh đỉnh phong Thần điện võ sĩ xuất hiện, chém giết sau, nhìn thấy một cái vòng tròn đài xuất hiện tại phía trước."),t("br"),t("br"),n._v("“Đây là đột phá Ngụy Thần cơ hội, ngươi đi qua a!”"),t("br"),t("br"),n._v("Cũng không tiến lên, Trương Huyền đối một vị Tiên Quân đỉnh phong nói."),t("br"),t("br"),n._v("“Đa tạ tông chủ...”"),t("br"),t("br"),n._v("Vị thiên tài này đầy mặt cảm kích gật gật đầu, tiến vào bên trong, nặng tâm tu luyện, quả nhiên cảm thấy lực lượng trong cơ thể, tựa như đun sôi đồng dạng, bất cứ lúc nào cũng sẽ đột phá."),t("br"),t("br"),n._v("Mọi người tiếp tục hướng phía trước, rất nhanh, lại tìm đến hai cái viên đài."),t("br"),t("br"),n._v("Mỗi cái viên đài phía trước nhất định có Thần điện võ sĩ bảo vệ, đổi lại những người khác, khẳng định không có cách nào, nhưng đối với Trương Huyền tới nói, đơn giản cực kỳ."),t("br"),t("br"),n._v("Đem canh gác võ sĩ chém giết, đem Hồng Vũ trưởng lão hai người an bài đi vào, Trương Huyền tiếp tục đi tới."),t("br"),t("br"),n._v("Lần nữa thấy được một cái vòng tròn đài, thân thể run rẩy, ngồi xuống."),t("br"),t("br"),n._v("Ngưng thần tĩnh tức, lập tức cảm nhận được bốn phía, kích động một cỗ đặc thù lực lượng, cùng trước đó tại Di Khí đại lục hấp thu linh khí hoàn toàn khác biệt, Tiên Quân đỉnh phong tu vi, tại đây cỗ khí tức bên dưới, rục rà rục rịch, bất cứ lúc nào cũng sẽ đột phá."),t("br"),t("br"),n._v("“Đáng tiếc, ta đột phá Ngụy Thần công pháp, còn không có cân nhắc đi ra...”"),t("br"),t("br"),n._v("Tu luyện một hồi, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Đạo kia ý niệm một mực tại Xuân Thu đại điển bên trong cân nhắc, nhưng đến bây giờ, đều không có thành công."),t("br"),t("br"),n._v("Hiện tại loại tình huống này, cưỡng ép xung kích lời nói, là có cơ hội thành tựu Ngụy Thần, nhưng... Không có thích hợp công pháp, ngược lại sẽ là về sau tiến bộ, lưu lại tai hoạ ngầm, được không bù mất!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Bàn tay lớn vồ một cái, đem trên sân khấu di tán khí tức, thu vào một cái bình ngọc, cất vào trữ vật giới chỉ."),t("br"),t("br"),n._v("Đi vào viên đài, hắn cũng rõ ràng, đột phá Ngụy Thần, dựa vào là loại này đặc thù linh khí, thứ này, cùng ban đầu ở danh sư đại lục có Cổ Thánh chi khí đồng dạng, một khi công pháp thôi diễn đi ra, liền có thể tùy thời tùy chỗ đột phá!"),t("br"),t("br"),n._v("Rời đi viên đài, tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Trong thông đạo, loại này bộ dáng viên đài dường như không ít, chỉ chốc lát lại tìm đến một cái, xử lý sạch canh gác võ sĩ, Trương Huyền dừng lại một chút, một cái giống nhau như đúc phân thân, chui vào."),t("br"),t("br"),n._v("Một lát sau, phân thân đột phá, đi đến Ngụy Thần cảnh giới."),t("br"),t("br"),n._v("Tiếp tục tiến lên, rất nhanh, giao long, Cửu Đầu Hỏa Phượng thú chờ Tiên Quân đỉnh phong Tiên thú, cũng tất cả đều thành công đột phá."),t("br"),t("br"),n._v("“Như vậy tiếp tục đi, một ngày thời gian, khẳng định không đến được Thần điện...”"),t("br"),t("br"),n._v("Bên người tất cả thú sủng phân thân, đều đạt đến Ngụy Thần cấp bậc, tất cả Tiên Quân đỉnh phong trường kiếm, cũng đặt ở loại khí tức này bên trong ôn dưỡng, có cơ hội trở thành Ngụy Thần cảnh pháp bảo, Trương Huyền lúc này mới hướng về phía trước nhìn lại."),t("br"),t("br"),n._v("Thông Thiên kiều vẫn như cũ đắm chìm trong hắc ám bên trong, không biết bao xa, không nhìn thấy phần cuối."),t("br"),t("br"),n._v("Cái này cầu nối chỉ mở ra một ngày thời gian, như vậy tiếp tục đi, khẳng định không đến được cái gọi là Thần điện, cái gọi là cướp đoạt “Thần” Chữ, cũng đã thành lời nói suông."),t("br"),t("br"),n._v("Những này bệ đá, số lượng là nhất định, một khi thành công cướp đoạt “Thần” Chữ, một cái tông môn liền sẽ cùng Thông Thần điện đồng dạng, có thể sinh ra vô số Ngụy Thần, thậm chí rèn đúc ra Ngụy Thần cảnh vũ khí."),t("br"),t("br"),n._v("Chân chính liên tục không ngừng, không cần lại bị quản chế tại đối phương!"),t("br"),t("br"),n._v("Chỉ bất quá..."),t("br"),t("br"),n._v("Thần điện vẫn muốn giết hắn, vây công rất nhiều lần, đều không có thành công, thật muốn đi qua, có thể hay không gặp phải mai phục?"),t("br"),t("br"),n._v("“Thông Thiên kiều trăm năm mới có thể mở ra một lần, Thần điện có lẽ nắm giữ đi vào Thần giới cơ hội, bất luận nhiều nguy hiểm, đều muốn đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Dừng lại một chút, thân ảnh biến mất tại nguyên chỗ, một lát sau xuất hiện lần nữa, đã đổi một kiện trường sam màu xanh, bay về phía trước vút đi."),t("br"),t("br"),n._v("Không có Thông Thiên kiều, cho dù đột phá Ngụy Thần, cũng không có khả năng tìm tới Thần điện, đánh trượt cơ hội này, cần đợi thêm trăm năm... Thời gian lâu như vậy, còn không bằng hiện tại bắt buộc mạo hiểm."),t("br"),t("br"),n._v("Phía trước, lại không còn viên đài, xem ra thứ này cũng là có số lượng."),t("br"),t("br"),n._v("Những vật này, phía trước nhất hai cái, không có võ sĩ bảo vệ, phía sau cơ bản đều có, nói cách khác, Thần điện ngầm thừa nhận, là để sáu đại trong tông môn hai người đột phá, phía sau, loại trừ Khổng sư cùng Lăng Vân kiếm các tông chủ dạng này cường giả, hầu như không có người có thể đi tới, chớ nói chi là sử dụng."),t("br"),t("br"),n._v("Trăm năm một lần, mỗi lần sáu đại tông môn phần lớn đều chỉ đột phá một người, mấy ngàn năm thời gian, cũng chỉ gom góp tích luỹ mười mấy cái mà thôi."),t("br"),t("br"),n._v("Đem những này trong sân khấu linh khí, thu sạch đi, một thanh trường kiếm xuất hiện tại dưới chân, trong cơ thể kiếm ý thôi thúc, Trương Huyền tốc độ đột nhiên tăng tốc."),t("br"),t("br"),n._v("Để tất cả thú sủng đột phá, cùng với thu thập trong sân khấu linh khí, đã lãng phí tiếp cận ba canh giờ, không thêm rất nhanh, đi vào Thần điện, gần như không có khả năng!"),t("br"),t("br"),n._v("Thần chi kiếm ý tràn ngập, Trương Huyền tốc độ nhanh vượt qua đồng dạng Ngụy Thần cảnh cường giả."),t("br"),t("br"),n._v("Chẳng qua, cái này cầu nối thực sự quá dài."),t("br"),t("br"),n._v("Liên tục phi hành ba canh giờ, không biết bao xa, vậy mà vẫn như cũ không thấy phần cuối."),t("br"),t("br"),n._v("“Một ngày thời gian đánh cái qua lại, hơn nữa xông vào Thần điện... Làm sao có thể làm được!” Đi vào Thông Thần điện, đã sáu canh giờ, trọn đã nửa ngày, thế mà còn không có nhìn thấy phần cuối, chẳng lẽ lấy thực lực của hắn bây giờ, căn bản đến không được?"),t("br"),t("br"),n._v("Căn cứ trước đó ghi chép, một khi Thông Thiên kiều biến mất trước, không quay lại về tiếp thiên thạch, rất có thể sẽ rơi vào nghiền nát không gian, cuối cùng Sinh Tử không biết, Ngụy Thần cường giả, cũng rất khó sống sót."),t("br"),t("br"),n._v("Qua nửa ngày, còn có một nửa thời gian!"),t("br"),t("br"),n._v("“Lại bay một canh giờ...”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền nghiến răng."),t("br"),t("br"),n._v("Trước đó ba canh giờ, không có toàn lực tiến lên, căn cứ tính toán, giờ phút này trở về, bốn canh giờ liền có thể trở lại tiếp thiên thạch."),t("br"),t("br"),n._v("Nói cách khác, lại bay một canh giờ nhìn một chút, nếu như vẫn như cũ không cách nào đến Thần điện, cũng chỉ có thể đường cũ trở về!"),t("br"),t("br"),n._v("Trong cơ thể tiên lực vận chuyển, dùng hết toàn lực, tăng thêm tốc độ hướng về phía trước."),t("br"),t("br"),n._v("Sau nửa canh giờ, không nhìn thấy phần cuối mặt cầu, xuất hiện biến hóa, hư không bên trong một tòa đen kịt đại điện, chậm rãi hiện lên ở tầm mắt."),t("br"),t("br"),n._v("“Thật là xa... Lúc trước Khổng sư cùng Lăng Vân kiếm các khai phái tổ sư, có thể tiến vào bên trong, tu vi so với hiện tại ta, đều chỉ mạnh không yếu...”"),t("br"),t("br"),n._v("Nhìn thấy Thần điện, Trương Huyền thở phào nhẹ nhõm, đồng thời cảm xúc."),t("br"),t("br"),n._v("Hắn tuy là Tiên Quân đỉnh phong, nhưng thực lực chân chính, so với bình thường Ngụy Thần đều chỉ mạnh không yếu, lại phối hợp Thần chi kiếm ý, Huyền Bối Quy chờ thú, muốn đuổi theo cũng khó khăn, loại tốc độ này, vậy mà đều liên tục phi hành hơn sáu canh giờ, mới nhìn đến..."),t("br"),n._v("Thông Thiên kiều chiều dài, quả nhiên đủ đáng sợ!"),t("br"),t("br"),n._v("Khổng sư đám người có thể thành công tiến vào bên trong, khẳng định cũng là thông qua được dài như vậy cầu nối, tốc độ nhanh chóng, so với bản thân, chỉ mạnh không yếu."),t("br"),t("br"),n._v("Cung điện to lớn, lơ lửng giữa không trung, lại hình như khảm nạm trong đêm tối, lại phi hành đại khái một phút trái phải, Trương Huyền mới đi đến trước mặt."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Dường như cảm thụ có sinh mệnh đến, cung điện cửa chính chậm chạp mở ra."),t("br"),t("br"),n._v("Thân thể run rẩy, Trương Huyền đi vào."),t("br"),t("br"),n._v("Mới vừa vào cửa, cửa đại điện hộ, chậm rãi đóng lại, nguyên bản đen như mực cung điện, vô số bó đuốc trong nháy mắt thiêu đốt lên, đem bốn phía chiếu sáng."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Xung quanh lóe ra mười cái thân ảnh, mỗi một cái khí tức trầm ổn, nhìn không ra sâu cạn!"),t("br"),t("br"),n._v("Đều là Ngụy Thần cảnh cường giả!"),t("br"),t("br"),n._v("Không hổ là Thần điện, ngoại giới khó gặp Ngụy Thần, ở đây, vậy mà vừa xuất hiện chính là hai chữ số."),t("br"),t("br"),n._v("“Biết rõ, Thần điện đối ngươi vây quét, lại còn dám đến nơi này, Trương Huyền, ngươi can đảm thật không nhỏ!”"),t("br"),t("br"),n._v("Ở chính giữa vương tọa bên trên, hỏa diễm thiêu đốt, một cái đen kịt thân ảnh cao lớn, bất ngờ xuất hiện, đứng ở hỏa diễm phía trên, tựa như từ trong lửa đi ra, áo dài che khuất dung nhan, thấy không rõ diện mạo, âm thanh băng lãnh, không mang theo mảy may tình cảm."),t("br"),t("br"),n._v("“Di Khí đại lục, thiên tài vô số, ta chỉ là kỳ lạ, vì sao đối ta theo đuổi không bỏ, chẳng lẽ cũng bởi vì lĩnh ngộ Thần chi kiếm ý?”"),t("br"),t("br"),n._v("Loại tình huống này, Trương Huyền đã sớm đoán được, cũng không căng thẳng, mà là nhíu nhíu mày."),t("br"),t("br"),n._v("Từ khi đi tới Thượng Thương, hắn một mực rất biết điều, kết quả vẫn như cũ gặp phải Thần điện liên tục ba lần vây công, thực sự không làm rõ ràng được, đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("“Thần chi kiếm ý?”"),t("br"),t("br"),n._v("Vương tọa bên trên bóng người hừ lạnh: “Chỉ bằng vào thứ này, còn không đáng phải bảo Thần điện ra tay!”"),t("br"),t("br"),n._v("“Cái kia...” Trương Huyền cau mày."),t("br"),t("br"),n._v("Bóng người nói “Ngươi chắc là từ danh sư đại lục tới a!”"),t("br"),t("br"),n._v("“Ngươi biết danh sư đại lục?” Trương Huyền lông mày giương lên."),t("br"),t("br"),n._v("“Ta muốn sống...”"),t("br"),t("br"),n._v("Cũng không giải thích, vương tọa bên trên bóng người, vung tay lên."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Mười vị Ngụy Thần cảnh Thần điện võ sĩ, lập tức vây quanh."),t("br"),t("br"),n._v("“Muốn bắt ta, không dễ dàng như vậy!”"),t("br"),t("br"),n._v("Đã dám đến Thần điện, liền biết khẳng định có nguy hiểm, Trương Huyền hướng về phía sau lùi lại, bàn tay lắc một cái."),t("br"),t("br"),n._v("Hô! Hô!"),t("br"),t("br"),n._v("Trước đó tại Thông Thiên kiều, thu vào nhẫn hai đầu Ngụy Thần cường giả thi thể, bất ngờ xuất hiện."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Chỉ thoáng cái liền nổ tung lên."),t("br"),t("br"),n._v("Trên đường tới, hắn đã phân ra tinh thần, đem cái này hai đầu thi thể luyện hóa thành Vô Hồn Kim Nhân."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Bùng nổ lực lượng, còn không có triệt để lan ra, hừ lạnh một tiếng, ngay sau đó thần điện bên trong bộ, quang mang lấp lánh, sau một khắc, khuấy động mà xuất lực lượng liền bị giam cầm thành một đoàn, “Ba!” Một tiếng, tiêu tán tại nguyên chỗ."),t("br"),t("br"),n._v("Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Vốn cho rằng hai đại Ngụy Thần đồng thời tự bạo, chí ít có thể cho hắn tranh thủ một chút thời gian, không nghĩ tới, Thần điện phòng ngự trận pháp thực sự quá mạnh, chỉ thoáng cái liền đem xung kích sinh ra lực lượng, tiêu tán cùng vô hình."),t("br"),t("br"),n._v("“Trốn!”"),t("br"),t("br"),n._v("Bàn tay lắc một cái, Đồng Thường kiếm xuất hiện tại lòng bàn tay, Trương Huyền nhẹ nhàng vạch một cái, Thần chi kiếm ý tản mát ra, hướng về phía Thần điện đóng chặt môn hộ, chém xuống mà xuống."),t("br"),t("br"),n._v("Đồng Thường kiếm, vốn chỉ kém Ngụy Thần cảnh một bước, trên đường thời điểm, mượn trong sân khấu linh khí, thành công đột phá, phối hợp Thần chi kiếm ý, uy lực cực mạnh, phổ thông Ngụy Thần cảnh cường giả, liền một chiêu đều không tiếp nổi."),t("br"),t("br"),n._v("Đinh đinh đinh!"),t("br"),t("br"),n._v("Kiếm khí rơi vào trên cánh cửa, phát ra sắt thép giao kích âm thanh, thậm chí ngay cả một đạo bạch ấn đều không có lưu lại."),t("br"),t("br"),n._v("“Chẳng lẽ... Cái đại môn này, cũng là Ngụy Thần cảnh pháp bảo?”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy khó tin."),t("br"),t("br"),n._v("Ngụy Thần cảnh Đồng Thường kiếm, phối hợp Thần chi kiếm ý, Hàn Kiếm Thu loại thực lực này, cũng không dám thẳng Sakura kỳ phong, cái đại môn này bị một đòn, không chút nào tổn hại, dấu vết đều không có lưu lại, cấp bậc cao, không dám tưởng tượng!"),t("br"),t("br"),n._v("“Đừng làm vô vị vùng vẫy...”"),t("br"),t("br"),n._v("Dường như đã sớm biết hắn biết cái này phó biểu tình, vây chung quanh Ngụy Thần cảnh cường giả, đồng thời cười một tiếng, đồng loạt ra tay."),t("br"),t("br"),n._v("Một trương từ tiên lực tạo thành lưới lớn, từ trên trời giáng xuống, thời gian nháy mắt, đem Trương Huyền tất cả đường lui, toàn bộ đóng kín."),t("br"),t("br"),n._v("“Vỡ!”"),t("br"),t("br"),n._v("Kiếm khí từ mũi kiếm lần nữa tuôn ra, Trương Huyền vừa vặn hướng lưới lớn vọt tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Kiếm khí không có chút nào ngăn cản, phảng phất lưới lớn không tồn tại đồng dạng, Trương Huyền chỉ cảm thấy trên người xiết chặt, bị trói chặt chẽ vững vàng, trong cơ thể tiên lực, cũng giống là bị giam cầm ở đồng dạng, lại không cách nào động đậy."),t("br"),t("br"),n._v("(Hôm nay ba canh!)."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2081"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2083"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);