(window.webpackJsonp=window.webpackJsonp||[]).push([[122],{473:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1087-khong-su-loi-khuyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1087-khong-su-loi-khuyen"}},[n._v("#")]),n._v(" Chương 1087: Khổng sư lời khuyên")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1087: Khổng sư lời khuyên "),t("br"),t("br"),n._v("Chỉ thấy thủy tinh bên trong phản chiếu ra một bóng người, cao quan áo dài, cho hắn một loại Thánh thể cùng reo vang cảm giác, dường như cùng hắn không khác nhau chút nào, đều là thiên địa công nhận Thánh Nhân, công nhận danh sư."),t("br"),t("br"),n._v("Trong lịch sử chỉ có một người có tình huống như vậy, không cần nghĩ, khẳng định là Khổng sư."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Ý nghĩ này mới vừa xuất hiện, liền cảm thấy trước mắt một cỗ cực lớn hấp lực truyền đến, ý thức lập tức thoát ly thân thể, sau một khắc đã xuất hiện tại thủy tinh bên trong."),t("br"),t("br"),n._v("Nhìn chung quanh một chút, phát hiện vẫn như cũ là cái không gian gấp, chỉ bất quá phạm vi không lớn, chỉ có mấy chục mét vuông, hơn nữa trống trải không có bất kỳ cái gì vật phẩm, một mảnh trắng xóa, có vẻ mười phần thô ráp."),t("br"),t("br"),n._v("“Khổng sư, ngươi làm sao...”"),t("br"),t("br"),n._v("Nhìn lướt qua, lần nữa nhìn về phía trước mắt vị này, Trương Huyền cau mày, trong mắt tràn đầy kỳ quái."),t("br"),t("br"),n._v("Lần trước đi Phong Thánh đài, gặp qua Khổng sư ý niệm, tuổi trẻ đẹp trai, cho người ta một loại như gió xuân ấm áp cảm giác, làm sao cái này... Trên mặt thoáng có chút sưng tấy, trên người áo dài, cũng có chút dơ dáy bẩn thỉu, thoạt nhìn có chút chật vật... Giống càng bị người đánh qua đồng dạng?"),t("br"),t("br"),n._v("Khoan sư?"),t("br"),t("br"),n._v("Nói đùa, không nói trước thực lực không bằng, cho dù thật có thể đánh qua, cũng không ai dám động thủ ah."),t("br"),t("br"),n._v("“Đoán chừng là vừa mới phong Thánh kết thúc, liền đem ý niệm lưu lại...”"),t("br"),t("br"),n._v("Trong lòng hơi động, âm thầm phỏng đoán."),t("br"),t("br"),n._v("Hắn mới vừa phong Thánh kết thúc, trải qua thể kiếp, mặc dù đã khôi phục, trên quần áo vết máu, lại là trong thời gian ngắn loại trừ không mất, cùng đối phương đồng dạng, tràn đầy chật vật, thậm chí còn hơn."),t("br"),t("br"),n._v("Trước mắt Khổng sư cũng hẳn là như vậy, cứ việc chỉ là một đoạn ý niệm, cũng thoạt nhìn không như vậy sạch sẽ, chỉnh tề."),t("br"),t("br"),n._v("Để hậu bối, biết phong Thánh tàn khốc."),t("br"),t("br"),n._v("“Ta ở lại chỗ này thời gian không nhiều...”"),t("br"),t("br"),n._v("Khoát tay áo, Khổng sư cũng không giải thích, mà là một mặt ngưng trọng."),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("“Nếu như không nhìn lầm, trên người ngươi cũng có Tiên Thiên thai độc a?” Khổng sư ánh mắt như điện."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Nghe hắn nhắc tới chuyện này, Trương Huyền kích động ánh mắt sáng lên, vội vàng nhìn qua: “Như thế nào mới có thể giải quyết? Còn cầu Khổng sư từ bi, chỉ điểm phương hướng...”"),t("br"),t("br"),n._v("Lần trước hắn vừa định đến hỏi, kết quả cái tên này nói câu “Ta đạo không cô” liền biến mất, hiện tại chủ động đưa ra, hẳn là có thể tìm tới phương pháp giải quyết."),t("br"),t("br"),n._v("Chỉ cần có thể giải quyết chuyện này, chẳng khác nào một cọc tâm sự, không cần tiếp tục vô duyên vô cớ lo lắng."),t("br"),t("br"),n._v("“Cái này một lát nữa lại nói...”"),t("br"),t("br"),n._v("Lắc đầu, Khổng sư nhìn qua: “Ngươi có thể thành công phong Thánh, nói rõ ngươi ta gặp gỡ là giống nhau, về sau khẳng định gặp được vô cùng phiền toái cực lớn. Ta cho ngươi một cái lời khuyên, muốn đi càng xa, muốn chạy trốn vận mệnh, cần phải đi ra thuộc về mình con đường, một vị kế thừa tiền nhân trí tuệ, mặc dù có thể để cho tiến bộ nhanh hơn, nhưng tương tự sẽ ràng buộc ngươi tiến lên phương hướng cùng động lực!”"),t("br"),t("br"),n._v("“Đi con đường của mình?”"),t("br"),t("br"),n._v("Không nghĩ tới hắn không nói Tiên Thiên thai độc sự tình, mà là trước tiên là nói về một câu như vậy không giải thích được, Trương Huyền hơi hơi kinh ngạc."),t("br"),t("br"),n._v("“Không sai!” Khổng sư nhẹ gật đầu, nói tiếp: “Ta có một bộ Xuân Thu đại điển, ngươi hẳn nghe nói qua a?”"),t("br"),t("br"),n._v("Trương Huyền đáp một tiếng."),t("br"),t("br"),n._v("Xuân Thu đại điển nghe nói là Khổng sư vô song pháp bảo, năm đó chỉ bằng mượn cái này, trấn áp Dị Linh tộc nhân, không cách nào phản kháng., chỉ bất quá, thứ này sớm đã nương theo Khổng sư biến mất, mất đi tung tích, cũng lại tìm không tới."),t("br"),t("br"),n._v("“Xuân Thu đại điển, quan hệ nhân tộc vận mệnh, ngươi đã giống như ta, liền có khả năng đem hắn khởi động, tuyệt đối không nên để hắn rơi vào trong tay người khác, nhớ lấy nhớ lấy! Mặt khác, nhất định phải đề phòng...”"),t("br"),t("br"),n._v("Giọng nói nhanh chóng, dường như có cái gì cấp bách việc cần hoàn thành, Khổng sư bàn tay chậm rãi nâng lên, còn chưa nói xong, Trương Huyền liền nghe đến một cái tràn đầy thanh âm lo lắng ở bên tai vang lên: “Trương Huyền, ngươi không sao chứ?”"),t("br"),t("br"),n._v("Thân thể chấn động, tinh thần quay về thân thể, mở mắt nhìn lại, chỉ thấy Lạc Nhược Hi chẳng biết lúc nào đã đi tới trước mặt, trong đôi mắt đẹp mang theo lo lắng."),t("br"),t("br"),n._v("Hẳn là nàng thấy mình ý thức ly thể, sợ xuất hiện biến cố, lúc này mới chạy tới."),t("br"),t("br"),n._v("“Ta không sao...”"),t("br"),t("br"),n._v("Đưa tay bóp qua nữ hài tay chưởng, Trương Huyền cười cười."),t("br"),t("br"),n._v("Đối phương vừa rồi bảo hộ hắn cùng Thiên Diệp Vương chiến đấu sự tình, đã biết, cứ việc nàng thực lực mạnh, thân phận không rõ, nhưng cho dù lại khó, cũng sẽ không bỏ đi."),t("br"),t("br"),n._v("“Không có việc gì là tốt rồi...” Nữ hài thở phào nhẹ nhõm, nhẹ nhàng cười cười."),t("br"),t("br"),n._v("“Ngươi chờ một chút, ta nhìn nhìn lại thứ này...”"),t("br"),t("br"),n._v("Không nhìn tới đối phương, Trương Huyền lần nữa hướng thủy tinh nhìn lại, một lát sau tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Khổng sư ý niệm liền vừa rồi công phu, đã biến mất, lại tìm không tới tung tích, trước đó tình cảnh, hình như giống như nằm mơ, vô luận như thế nào đều khôi phục không được nữa."),t("br"),t("br"),n._v("“Khổng sư sáng lập danh sư, chịu nhiều như vậy danh sư cung phụng cùng tôn sùng, còn tưởng rằng làm việc có thể ổn trọng chút... Không nghĩ tới, cùng phân thân đồng dạng không đáng tin cậy...”"),t("br"),t("br"),n._v("Gãi gãi đầu, Trương Huyền có chút bất đắc dĩ, cũng có chút phát điên."),t("br"),t("br"),n._v("Hắn chỉ là muốn hỏi Tiên Thiên thai độc sự tình, kết quả, gặp qua đối phương ý niệm hai lần, nói nhảm nói không ít, hữu dụng sự tình, đến một điểm không nâng... Suy nghĩ một chút đều để người buồn bực."),t("br"),t("br"),n._v("Cho cái lời khuyên, nói cái gì Xuân Thu đại điển... Thứ này, ta cũng không biết ở nơi nào, đề phòng cái gì?"),t("br"),t("br"),n._v("Ngươi xem người ta Khưu Ngô cổ thánh, mặc dù bị đả kích, thế nhưng nói hết lời, đợi đến thời gian thật dài..."),t("br"),t("br"),n._v("Đường đường vạn thế chi sư, có phần quá yếu một chút."),t("br"),t("br"),n._v("“Lưu lại ý niệm thời điểm, Khổng sư mới vừa vặn phong Thánh thành công, thực lực kém xa Khưu Ngô cổ thánh, nhưng kiên trì thời gian, ngắn như vậy... Cũng là say!”"),t("br"),t("br"),n._v("Lắc đầu, vẻ mặt khó coi."),t("br"),t("br"),n._v("Thật vất vả lần nữa có gặp được Khổng sư cơ hội, có thể cố gắng hỏi thăm thoáng cái Tiên Thiên thai độc sự tình, kết quả, lần nữa bởi vì Khổng sư chờ thời thời gian quá ngắn, mà bỏ qua..."),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Biết buồn bực cũng không dùng, đương thời Khổng sư chỉ có Thánh vực nhất trọng thực lực, ý niệm có thể sống được vài vạn năm, hơn nữa nói chuyện với mình, đã rất tốt."),t("br"),t("br"),n._v("Không có cách nào yêu cầu quá cao."),t("br"),t("br"),n._v("“Nhìn tới Tiên Thiên thai độc, chỉ có thể dựa vào chính mình, dựa vào ai cũng vô dụng...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm."),t("br"),t("br"),n._v("Còn tưởng rằng chỉ cần tìm được Khổng sư, liền có thể tìm tới phương pháp giải quyết, hiện tại xem ra... Suy nghĩ nhiều!"),t("br"),t("br"),n._v("Tất cả đều chỉ có thể dựa vào bản thân."),t("br"),n._v("“Trước luyện hóa thứ này đi!”"),t("br"),t("br"),n._v("Không đi nghĩ chuyện này, lần nữa nhìn về phía trước mắt thủy tinh."),t("br"),t("br"),n._v("Lúc này thủy tinh đã cùng bản đồ hoàn mỹ dung hợp lại cùng nhau, như là một cái mô hình địa cầu, mặt ngoài che kín đủ loại địa hình, cùng trước đó đã gặp đồng dạng."),t("br"),t("br"),n._v("Tinh thần hướng bên trong diễn, cho hắn một loại bao la vô biên cảm giác, giống như chỉ cần luyện hóa thứ này, chẳng khác nào hoàn toàn khống chế toàn bộ Khưu Ngô cung, nắm trong tay lực lượng kinh người."),t("br"),t("br"),n._v("“Luyện hóa!”"),t("br"),t("br"),n._v("Trong đầu, sinh ra luyện hóa phương pháp, bàn tay chạm đến ở phía trên, chân khí chậm rãi chảy chuyến."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Trong nháy mắt, Trương Huyền liền cảm thấy mình ý niệm, giống như là lan tràn đến toàn bộ Khưu Ngô cung bầu trời."),t("br"),t("br"),n._v("Vô Cương giới, Hắc Yên Trần Sa, lơ lửng Thiên cung..."),t("br"),t("br"),n._v("Trước đó trải qua địa phương, giờ phút này rõ ràng xuất hiện ở trước mắt, không rõ chi tiết, so trước kia nhìn thấy đều muốn rõ ràng."),t("br"),t("br"),n._v("Thậm chí, một đạo ý niệm bên dưới, có thể dễ dàng thay đổi toàn bộ cung điện trận pháp, để nó biến đến càng thêm hung mãnh hoặc là vững vàng."),t("br"),t("br"),n._v("“Quả nhiên luyện hóa thứ này, liền nắm trong tay toàn bộ Khưu Ngô cung...”"),t("br"),t("br"),n._v("Trương Huyền hiểu được."),t("br"),t("br"),n._v("Thủy tinh cùng bản đồ dung hợp lại cùng nhau, chẳng khác nào hoàn toàn nắm trong tay cái này không gian gấp, hoàn toàn có thể trong một ý nghĩ, đem hắn lấy đi."),t("br"),t("br"),n._v("Nói cách khác, chỉ cần cầm đi thủy tinh, chẳng khác nào mang đi cả tòa Khưu Ngô cung, về sau suy nghĩ gì thời điểm đi vào, liền có thể lúc nào đi vào."),t("br"),t("br"),n._v("Hiểu điểm ấy, Trương Huyền con mắt tỏa ánh sáng, nắm đấm lặng lẽ xiết chặt."),t("br"),t("br"),n._v("Khưu Ngô cổ thánh ý niệm biến mất, mặc dù truyền thừa không có, nhưng nếu như có thể đem nơi này mang đi, cẩn thận nghiên cứu lời nói, đối không gian lý giải, tuyệt đối có thể gia tăng một bước dài!"),t("br"),t("br"),n._v("Đối sau này thực lực gia tăng, cũng có tác dụng rất lớn."),t("br"),t("br"),n._v("Chân khí không ngừng truyền bá, cẩn thận rèn luyện thủy tinh mỗi một chỗ địa phương."),t("br"),t("br"),n._v("Không biết qua bao lâu, con mắt bỗng nhiên mở ra."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Bàn tay nhẹ nhàng khẽ động, đem thủy tinh thu nhập trữ vật giới chỉ."),t("br"),t("br"),n._v("Khống chế Khưu Ngô cung hạch tâm đồ vật, đã thành công luyện hóa."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Vừa đem thủy tinh lấy đi, hắn cũng lập tức cảm thấy toàn thân khí tức nhanh chóng yếu bớt, Phong Thánh đài gia trì lực lượng, hoàn toàn biến mất, lần nữa khôi phục đến Thánh vực nhất trọng đỉnh phong thực lực."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy trước đó thánh quang lấp lánh Phong Thánh đài, giống như là bị hấp thu hết năng lượng, từ trung gian nứt ra, lại không có trước đó thần kỳ."),t("br"),t("br"),n._v("“Cho dù đem thứ này ném đi, chỉ sợ cũng không có người tin tưởng, Khổng sư cùng ta đều ở trên đây, phong Thánh thành công...”"),t("br"),t("br"),n._v("Nhìn thấy trước mắt thần thánh đồ vật, cùng bình thường bệ đá không có khác nhau, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Phong Thánh đài đã hoàn thành sứ mệnh, lại không giá trị tồn tại."),t("br"),t("br"),n._v("Dứt bỏ trong lòng suy nghĩ lung tung, quay đầu hướng mọi người nhìn lại, chỉ thấy Ngô sư vẫn tại luyện chế đan dược, mặc dù thủ pháp bên trên không ít sai lầm, nhưng bằng mượn hắn hiện tại nửa bước Xuất Khiếu cảnh thực lực, luyện chế thành công ra Đoạn Tục đan, cũng không khó khăn."),t("br"),t("br"),n._v("Hàn hội trưởng cùng những người khác đang củng cố tu vi, theo đại chiến bên trong khôi phục."),t("br"),t("br"),n._v("“Ta vừa rồi nhìn thấy Khổng sư...”"),t("br"),t("br"),n._v("Biết không cần bản thân hỗ trợ, Trương Huyền không tiếp tục để ý, nhìn về phía cách đó không xa nữ hài."),t("br"),t("br"),n._v("Nhẹ gật đầu, Lạc Nhược Hi mỉm cười: “Nơi này là hắn chân chính phong Thánh địa phương, lưu lại ý niệm mười phần bình thường!”"),t("br"),t("br"),n._v("“Đúng vậy a, hắn để cho ta đừng một mực kế thừa tiền nhân trí tuệ, muốn đi ra con đường của mình...”"),t("br"),t("br"),n._v("Vừa rồi không kịp phản ứng, hiện tại suy nghĩ kỹ một chút, đối phương nói không sai."),t("br"),t("br"),n._v("Theo bắt đầu tu luyện đến nay, hắn vẫn luôn là mượn nhờ Thiên Đạo thư viện, ngưng tụ Thiên Đạo công pháp, cũng không có bất kỳ cái gì sáng tạo, quả thực là một vị kế thừa tiền nhân trí tuệ."),t("br"),t("br"),n._v("“Tu luyện cần phải đi ra thuộc về mình con đường, Khổng sư nói rất đúng!”"),t("br"),t("br"),n._v("Lạc Nhược Hi gật đầu."),t("br"),t("br"),n._v("“Xem ra sau này tu luyện, không thể cùng trước kia đồng dạng...”"),t("br"),t("br"),n._v("Khổng sư làm vạn thế chi sư, danh sư nghề nghiệp người sáng lập, có thể trịnh trọng việc nói như vậy, nhất định là nhìn ra cái gì, không thể trò đùa."),t("br"),t("br"),n._v("“Ta biết làm như vậy rất khó... Bất quá, muốn trở thành cường giả chân chính, không có thứ thuộc về chính mình, quả thực không được!” Lạc Nhược Hi cười cười: “Cũng không cần gấp, ngươi bây giờ tốc độ tu luyện đã rất nhanh, chỉ cần tiếp tục tu luyện đi xuống, khẳng định có thể tìm tới!”"),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Phong Thánh thành công, giải quyết lớn nhất một nan đề, để hắn bất kể thực lực vẫn là tự tin, đều chợt tăng thật nhiều lần."),t("br"),t("br"),n._v("Sau này con đường cho dù khó... Hắn cũng tin tưởng, nhất định có thể đi đến cuối cùng, chân chính đứng ở danh sư đại lục đỉnh phong nhất!"),t("br"),t("br"),n._v("“Nhược Hi, ngươi yên tâm, mặc kệ bao nhiêu khó khăn... Ta cũng sẽ cùng ngươi tiến tới cùng nhau!”"),t("br"),t("br"),n._v("Lần nữa nắm nữ hài tay chưởng, Trương Huyền ánh mắt kiên định."),t("br"),t("br"),n._v("PS: Chương cuối quyển này! Quyển thứ tám,  kết thúc, cái này cuốn là quyển sách trọng yếu nhất một cái chuyển hướng, trong đó chôn giấu rất lớn phục bút, trong đó dính dáng cả quyển sách chuyện xưa phát triển, đương nhiên, hiện tại khẳng định là không nhìn ra. Nếu như hoàn thành về sau, một lần nữa nhìn một lần, liền sẽ phát hiện những này phục bút, thật ra thì chôn cực kỳ sâu, có đôi khi chỉ là cái rất nhỏ động tác. Ha ha!"),t("br"),t("br"),n._v("Phía dưới sắp mở ra, quyển thứ chín ."),t("br"),t("br"),n._v("Tại đây cuốn một cái, Trương Huyền thực sự tiếp xúc đại lục đỉnh phong nhất thế lực, kiến thức đến chân chính Thánh Nhân quý tộc nội tình, cũng dần dần biết Lạc Nhược Hi “Thân phận chân thật”, hắn rất nhiều học sinh, cũng sẽ toàn bộ tìm tới con đường thuộc về mình."),t("br"),t("br"),n._v("Dựa theo đại cương, danh sư đại lục, tổng cộng mười ba quyển, đây là đếm ngược quyển thứ năm."),t("br"),t("br"),n._v("Kính thỉnh kỳ vọng!"),t("br"),t("br"),n._v("Cuối cùng, cầu thoáng cái công chúng số Fan hâm mộ, Wechat lục soát “Hoành Tảo Thiên Nhai”, tăng thêm chú ý là được, lúc rảnh rỗi ta sẽ đem đằng sau mấy cuốn cuốn tên phát đến phía trên, xem như trước thời hạn kịch thấu, cũng chứng minh lão nhai thật là có đại cương, không phải hồ viết..."),t("br"),t("br"),n._v("Mặt khác, còn có nguyệt phiếu ư?"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1095"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1097"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);