(window.webpackJsonp=window.webpackJsonp||[]).push([[1220],{1576:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2070-khong-su-truong-huyen-gap-nhau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2070-khong-su-truong-huyen-gap-nhau"}},[n._v("#")]),n._v(" Chương 2070: Khổng sư Trương huyền gặp nhau")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Tiếng gió bên tai gào thét, Trương Huyền chỉ cảm thấy trước mắt cảnh tượng không ngừng nhảy, trước đó quen thuộc phong cảnh, mắt thường có thể thấy được nhanh chóng lùi về phía sau."),i("br"),i("br"),n._v("“Tốc độ thật nhanh...”"),i("br"),i("br"),n._v("Chấn động trong lòng, Trương Huyền híp mắt lại."),i("br"),i("br"),n._v("Cách đó không xa vị này, mang theo một người, tốc độ vậy mà đều so với hắn, phải nhanh hơn gấp bội."),i("br"),i("br"),n._v("“Coi như đứng đầu nhất Ngụy Thần, cũng không có khả năng có nhanh như vậy... Chẳng lẽ hắn là Thần Linh?”"),i("br"),i("br"),n._v("Trước đó, tại Trục Tinh đảo hải vực bị hắn ép tự bạo Ngụy Thần, hẳn là đạt đến đỉnh phong nhất, nhưng cùng vị này so ra, kém vẫn như cũ không biết bao nhiêu."),i("br"),i("br"),n._v("Ngay tại nghi ngờ, bên tai vang lên thanh âm của đối phương."),i("br"),i("br"),n._v("“Ta còn không có đi đến Thần Linh, sở hữu tốc độ nhanh, là bởi vì Phiêu Miễu giày!”"),i("br"),i("br"),n._v("Nói chuyện, chính là vừa đem hắn cứu đi vị này."),i("br"),i("br"),n._v("“Phiêu Miễu giày?” Trương Huyền cúi đầu nhìn lại, quả nhiên thấy đối phương trên mặt bàn chân, một cái kim sắc giày, không ngừng tỏa ra đặc thù lực lượng, để thứ nhất bước kéo dài mấy chục cây số, tựa như thuấn di."),i("br"),i("br"),n._v("“Phiêu Miễu tiên tông trấn tông chi bảo... Ngươi chẳng lẽ là...”"),i("br"),i("br"),n._v("Nghĩ đến một người, Trương Huyền thân thể cứng đờ."),i("br"),i("br"),n._v("Chỉ sợ cũng chỉ có hắn, mới có thể để cho thư viện, đều không thể nhìn thấu."),i("br"),i("br"),n._v("“Quay đầu trò chuyện tiếp, Thông Thiên kiều đã sớm thu hồi, không nhanh chút, vô cùng có khả năng hai chúng ta đều sẽ chết ở chỗ này...”"),i("br"),i("br"),n._v("Bóng người cười ha ha một tiếng, cũng không trả lời, mà là ánh mắt ngưng trọng nhìn về phía trước."),i("br"),i("br"),n._v("“Sớm thu về?”"),i("br"),i("br"),n._v("Trương Huyền cúi đầu, quả nhiên nhìn không đúng, dưới chân cầu nối, ngay tại chậm rãi hướng Thần điện lùi về, chấn động trong lòng, tràn đầy không hiểu: “Thông Thiên kiều... Không phải muốn xuất hiện một ngày ư? Lúc này mới bao lâu liền thu hồi?”"),i("br"),i("br"),n._v("Dựa theo trước kia quy luật, thông thiên trước dưới tình huống bình thường, đều sẽ xuất hiện một ngày thời gian, cũng chính là mười hai canh giờ."),i("br"),i("br"),n._v("Hắn không đến bảy canh giờ, tiến vào Thần điện, ở trong đó, đợi chưa tới nửa canh giờ, làm sao sẽ trực tiếp lùi về?"),i("br"),i("br"),n._v("Chẳng lẽ, Thông Thiên kiều xuất hiện cùng biến mất, không phải cố định, mà là vị kia Thần điện điện chủ có thể tùy ý thay đổi?"),i("br"),i("br"),n._v("“Đã xuất hiện một ngày...”"),i("br"),i("br"),n._v("Bóng người cười khổ: “Thần điện tốc độ thời gian trôi qua, cùng ngoại giới khác biệt, có mười so một chênh lệch, ngươi chẳng lẽ không có phát hiện a...”"),i("br"),i("br"),n._v("“Mười so một?”"),i("br"),i("br"),n._v("Vừa tiến vào Thần điện, liền gặp Ngụy Thần cảnh cường giả mai phục, căn bản không có thời gian dò xét, ngay sau đó bị bắt lại đưa lên tế đàn... Cẩn thận nhớ lại một cái, dường như, Thần điện cùng ngoại giới tốc độ thời gian trôi qua, quả thực có chút không giống."),i("br"),i("br"),n._v("Mười so một..."),i("br"),i("br"),n._v("Cùng Thượng Thương cùng danh sư đại lục tốc độ thời gian trôi qua tương đồng!"),i("br"),i("br"),n._v("“Nghe nói Thần giới, cùng Di Khí đại lục thời gian so, cũng là mười so một! Thần điện cùng hắn tương đồng!” Bóng người nói."),i("br"),i("br"),n._v("“Thì ra là thế...”"),i("br"),i("br"),n._v("Trương Huyền gật gật đầu."),i("br"),i("br"),n._v("Hắn tại Thần điện chiến đấu, tế tự, đợi tiếp cận nửa canh giờ, mười so một thời gian lưu tốc, còn kém không nhiều tiếp cận năm canh giờ, tính như vậy, Thông Thiên kiều một ngày thời gian, quả thực lập tức liền muốn tới."),i("br"),i("br"),n._v("“Tốt, tiên lực hộ thể, ta tiến hành xuyên qua không gian...”"),i("br"),i("br"),n._v("Thấy hắn hiểu được, bóng người không nói thêm lời, cười nhạt một tiếng, bàn chân bỗng nhiên một bước."),i("br"),i("br"),n._v("Cả người tốc độ, lập tức đạt đến cực hạn, trước mắt không gian trở nên kỳ quái."),i("br"),i("br"),n._v("Tê lạp!"),i("br"),i("br"),n._v("Không gian vỡ ra tới, Trương Huyền chỉ cảm thấy áp lực cực lớn, từ bốn phía bát phương nghiền ép mà tới, sắp đem hắn ép thành mảnh vỡ."),i("br"),i("br"),n._v("Răng rắc!"),i("br"),i("br"),n._v("Thân thể bị lách vào thành bánh thịt, tinh thần khẽ động, vội vàng khôi phục lại."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Ép lực biến mất, Trương Huyền miệng lớn thở hổn hển, lúc này mới phát hiện, đã rời đi Thông Thiên kiều, xuất hiện tại một cái mây mù Phiêu Miểu cung điện khổng lồ."),i("br"),i("br"),n._v("“Đây là cái nào?”"),i("br"),i("br"),n._v("Đứng thẳng người, Trương Huyền nghi ngờ."),i("br"),i("br"),n._v("Hắn đó có thể thấy được, nơi này tuy là vẫn tại Thượng Thương, nhưng không tại Di Khí đại lục bất kỳ một chỗ, phảng phất nhảy ra đại lục cùng hải dương, tại một chỗ không gian gấp bên trong."),i("br"),i("br"),n._v("“Đây là Thông Thần điện tổng bộ...”"),i("br"),i("br"),n._v("Cười cười, bóng người xoay người lại."),i("br"),i("br"),n._v("Trương Huyền lúc này mới nhìn rõ ràng dung mạo của đối phương, cùng danh sư đại lục, tất cả Danh Sư đường cung phụng vị kia, giống nhau như đúc, thân hình cao lớn, ba sợi sợi râu, lông mi bên trong mang theo trách trời thương dân chi ý."),i("br"),i("br"),n._v("“Hậu bối cuối học Trương Huyền, gặp qua Khổng sư!”"),i("br"),i("br"),n._v("Chấn động trong lòng, Trương Huyền quỳ mọp xuống đất."),i("br"),i("br"),n._v("Trước đó cũng đã đoán được, tận mắt nhìn đến dung mạo cùng khí tức, mới xác nhận xuống."),i("br"),i("br"),n._v("Không phải người khác, chính là sáng lập Danh Sư đường, dựa vào sức một người, đem Dị Linh tộc đuổi đi cứu vô số nhân tộc vạn thế chi sư!"),i("br"),i("br"),n._v("Gặp qua nhiều lần linh hồn ấn ký, đây là lần đầu tiên nhìn thấy Chân Nhân."),i("br"),i("br"),n._v("Không nghĩ tới so với trong tưởng tượng càng thêm ôn hoà, cho người ta một loại sưởi ấm cảm giác."),i("br"),i("br"),n._v("“Đứng lên đi!”"),i("br"),i("br"),n._v("Cười cười, Khổng sư đưa tay đem hắn đỡ dậy, nhìn lại: “Danh Sư đường còn tốt?”"),i("br"),i("br"),n._v("“Còn tốt, tất cả tuân theo Khổng sư Lúc trước lưu lại quy định, lấy bảo vệ nhân tộc làm nhiệm vụ của mình...”"),i("br"),n._v(" Trương Huyền đem Danh Sư đường tình huống nói rõ chi tiết một lần."),i("br"),i("br"),n._v("“Ừm!” Khổng sư từng sợi sợi râu: “Nhân loại chỉ có liên hợp cùng một chỗ, mới có thể chống lại những chủng tộc khác, sừng sững với thế giới chi đỉnh! Ta sáng lập Danh Sư đường ý nghĩa chính, vì người người như rồng, hiện tại xem ra, cũng không khiến ta thất vọng!”"),i("br"),i("br"),n._v("Người người như rồng, thực sự quá khó khăn."),i("br"),i("br"),n._v("Nhưng Danh Sư đường, truyền đạo giải thích nghi hoặc, hữu giáo vô loại, chỉ cần tồn tại, sớm muộn cũng có một ngày, nhân tộc sẽ trở nên càng thêm cường đại."),i("br"),i("br"),n._v("“Đúng vậy a, Dị Linh tộc hiện tại cũng đã ngoan ngoãn trở lại cổ chiến trường, nếu không ra...”"),i("br"),i("br"),n._v("Trương Huyền đem Dị Linh tộc tình huống, cũng nói rõ chi tiết một lần."),i("br"),i("br"),n._v("Nghe hắn nói xong, Khổng sư mỉm cười, nhìn qua: “Ngươi có thể hay không phát hiện, Trục Tinh đảo người, cùng Dị Linh tộc nhân, giống nhau y hệt?”"),i("br"),i("br"),n._v("“Là!” Trương Huyền cũng không phủ nhận."),i("br"),i("br"),n._v("Điểm ấy, vừa tới Trục Tinh Cung liền phát hiện."),i("br"),i("br"),n._v("Khổng sư nói “Thật ra thì, Dị Linh tộc nhân cùng Trục Tinh đảo người, đồng căn đồng nguyên... Đều là bị vứt bỏ Thần Linh!”"),i("br"),i("br"),n._v("“Bị vứt bỏ Thần Linh?” Trương Huyền nhíu nhíu mày: “Lăng Vân kiếm các chờ tông môn không phải nói, bọn họ là bị vứt bỏ Thần Linh ư?”"),i("br"),i("br"),n._v("Năm đại tông môn quản lý ở dưới tu luyện giả, đều nói mình là bị vứt bỏ Thần Linh, Trục Tinh đảo người là thổ dân, làm sao đến Khổng sư trong miệng, Trục Tinh đảo người trái ngược?"),i("br"),i("br"),n._v("Khổng sư cười nói: “Năm đại tông môn chỉ là tìm cho mình cái tốt xuất thân mà thôi! Nếu như bọn họ là vứt bỏ Thần Linh, vì sao chỉ có Trục Tinh Cung có thể đi qua tế tự, câu thông Thần giới, mà bọn họ không làm được?”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Trương Huyền sững sờ."),i("br"),i("br"),n._v("Trước đó thật đúng là không nghĩ tới chuyện này, vừa nói như vậy, quả thực có đạo lý."),i("br"),i("br"),n._v("Nếu như, năm đại tông môn sinh mệnh, là bị vứt bỏ Thần Linh, vì sao bọn họ không cách nào câu thông Thần giới, ngược lại bị hắn bọn họ gọi là thổ dân Trục Tinh Cung có thể làm được?"),i("br"),i("br"),n._v("Khổng sư lắc đầu, nói “Người người đều hi vọng bản thân có cái tốt hơn xuất thân thôi, dần dà, giả liền truyền thành thật, thật cũng liền truyền thành giả, thật thật giả giả, ai có thể phân biệt rõ ràng!”"),i("br"),i("br"),n._v("Trương Huyền lặng im."),i("br"),i("br"),n._v("Ai được thiên hạ, đều sẽ tuyên dương một phen, vương quyền Thần trao, đủ loại kỳ quái thần thoại gia trì ở trên người, tốt hiển lộ rõ ràng bản thân là chân mệnh thiên tử."),i("br"),i("br"),n._v("Di Khí đại lục, cũng là như vậy."),i("br"),i("br"),n._v("Năm đại tông môn khoe khoang bản thân là vứt bỏ Thần Linh, thời gian lâu dài, hậu nhân cũng đều tin tưởng."),i("br"),i("br"),n._v("Ngược lại chân chính bị vứt bỏ Thần Linh, biến thành bọn họ trong miệng “Thổ dân”..."),i("br"),i("br"),n._v("“Nếu là Thần Linh, tại sao lại có mạnh như vậy sát lục chi khí...”"),i("br"),i("br"),n._v("Hiểu được, Trương Huyền không tại vấn đề này rầu rĩ, mà là nghi ngờ hỏi."),i("br"),i("br"),n._v("Ai là chính chung, đối với hắn mà nói không có gì có thể xoắn xuýt, dù sao hắn hai bên đều không thuộc về, chỉ là Thượng Thương khách qua đường thôi!"),i("br"),i("br"),n._v("“Có lẽ cùng Thần giới liên quan đến... Không phải là chúng ta có thể thảo luận!”"),i("br"),i("br"),n._v("Khổng sư lắc đầu."),i("br"),i("br"),n._v("“Là!” Trương Huyền lại không hỏi thăm, lần nữa nhìn qua: “Ta còn có chuyện không hiểu, hi vọng Khổng sư có thể giúp đỡ giải đáp nghi vấn giải thích nghi hoặc!”"),i("br"),i("br"),n._v("“Mời nói!”"),i("br"),i("br"),n._v("“Khổng sư ngươi... Đi tới Thượng Thương đã có mấy ngàn năm, chẳng lẽ một mực không có phá vỡ không gian, đi vào Thần giới?”"),i("br"),i("br"),n._v("Đây là hắn nghi ngờ nhất địa phương."),i("br"),i("br"),n._v("Khổng sư thiên phú và năng lực, so với hắn đều cường đại hơn không biết gấp bao nhiêu lần."),i("br"),i("br"),n._v("Chính mình tới chưa tới một tháng, liền khoảng cách Ngụy Thần chỉ có kém một bước, bất cứ lúc nào cũng sẽ đột phá, trước mắt vị này vạn thế chi sư, làm sao có thể, còn một mực ở lại chỗ này, không cách nào rời đi?"),i("br"),i("br"),n._v("“Thần giới... Không phải ngươi tưởng tượng đơn giản như vậy! Hơn nữa, Lúc trước... Ta lẻ loi một mình xông vào Thần điện, cùng Thần Linh giao thủ, mặc dù là Thông Thần điện đoạt được ‘Thần’ chữ, nhưng cũng bị trọng thương, ngủ say đến trước đây không lâu, mới thanh tỉnh lại!”"),i("br"),i("br"),n._v("Dừng lại một chút, Khổng sư dường như không nguyện ý nói nhiều, chuyện lúc trước."),i("br"),i("br"),n._v("“Nếu như không tiện nói tỉ mỉ, ta liền không hỏi...” Trương Huyền tiếp tục nói: “Khổng sư trước ngươi, cho ta một cái có thể tại Thông Thần điện mua sắm bất kỳ đồ vật lệnh bài, vì sao... Ta vừa sử dụng, liền gặp phải Thần điện cường giả vây giết?”"),i("br"),i("br"),n._v("Đây là trong lòng của hắn lớn nhất khúc mắc."),i("br"),i("br"),n._v("Nếu như không hỏi rõ ràng, trước mắt vị này cho dù là hắn chỗ bội phục Khổng sư, cũng khẳng định không thể thản nhiên tin tưởng."),i("br"),i("br"),n._v("“Nói đến chuyện này, là ta sơ sẩy...”"),i("br"),i("br"),n._v("Nhìn qua, Khổng sư trên mặt lộ ra ý tứ xin lỗi: “Ta ngủ say thời gian quá lâu, Thông Thần điện vận chuyển mấy ngàn năm, đã sớm được Thần điện thẩm thấu, không ít người đã quy thuận Thần điện, mặt ngoài, nhưng là không thể nhận ra cảm giác! Lúc trước đưa cho ngươi lệnh bài, là ta một cái tín nhiệm thuộc hạ, luyện chế mà thành, không nghĩ tới, hắn đã sớm là Thần điện người! Biết sau chuyện này, ta đã đem hắn xử tử. Những ngày này, một mực tại dò xét Thông Thần điện phía trong nội gian, cho nên, mới không có đi qua tìm ngươi, nếu không, biết ngươi từ danh sư đại lục mà tới, khẳng định đã sớm đi qua!”"),i("br"),i("br"),n._v("Trương Huyền hiểu được."),i("br"),i("br"),n._v("Xem ra cùng trước đó đoán đồng dạng, đối phương cũng là bị mơ mơ màng màng."),i("br"),i("br"),n._v("“Ngươi bây giờ thân kiêm bốn đại tông môn tông chủ, chắc hẳn cũng biết ta mượn dùng Huyền Kính Môn cùng Phiêu Miễu tiên tông trấn tông bảo vật sự tình!” Khổng sư nói."),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("“Ta mượn dùng những bảo vật này, có tính toán của mình, muốn một lần loại bỏ Thần điện thống trị! Cũng không phải là vì người tư dục, liền giống như ta hiện tại ăn mặc Phiêu Miễu giày, nếu không phải nó, muốn đem ngươi từ Thần điện cứu ra, khẳng định không làm được! Sáu đại tông môn trấn tông chi bảo, cứ việc liền Ngụy Thần cảnh đều không đạt tới, nhưng ẩn chứa mấy ngàn năm qua, vô số sinh mệnh ý niệm, uy lực không tầm thường.”"),i("br"),i("br"),n._v("Khổng sư giải thích."),i("br"),i("br"),n._v("Trương Huyền giật mình."),i("br"),i("br"),n._v("Náo loạn nửa ngày, tất cả đều hiểu lầm."),i("br"),i("br"),n._v("Khổng sư còn là cái kia đại công vô tư Khổng sư, vạn thế gương tốt."),i("br"),i("br"),n._v("“Ngươi muốn biết, hẳn là đều biết, hiện tại ta cũng có chuyện muốn cùng ngươi nói!”"),i("br"),i("br"),n._v("Thấy hắn lại không hỏi thăm, Khổng sư vẻ mặt nghiêm túc nhìn lại, bàn tay vung vẩy, bốn phía trận pháp vận chuyển lên tới, đem khí tức triệt để ngăn cách: “Ngươi có biết... Ngươi bây giờ đã đại họa lâm đầu, không còn sống lâu nữa!”"),i("br"),i("br"),n._v("(Tấu chương xong)."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-2083"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-2085"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);