(window.webpackJsonp=window.webpackJsonp||[]).push([[1221],{1575:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-2071-đe-nhat-danh-sach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2071-đe-nhat-danh-sach"}},[n._v("#")]),n._v(" Chương 2071: Đệ nhất danh sách")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Đại họa lâm đầu?” Trương Huyền tràn đầy khó tin."),t("br"),t("br"),n._v("Nhiều nhất Thần điện muốn giết hắn, làm sao lại đại họa lâm đầu?"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Khổng sư trong mắt chợt lóe sáng: “Trong cơ thể ngươi, hẳn là nắm giữ Thiên đạo một bộ phận a!”"),t("br"),t("br"),n._v("“Ngươi...” Trương Huyền chấn động."),t("br"),t("br"),n._v("Thiên đạo thư viện là hắn bí ẩn nhất đồ vật, ngày hôm nay trước bị Thần điện vị kia thủ lĩnh phát giác, giờ phút này lại bị Khổng sư một cái nói ra, phảng phất lớn nhất cơ mật, triệt để hiện ra ở trước mắt người đời, để hắn nhịn không được mày nhăn lại."),t("br"),t("br"),n._v("“Ta thế nào biết được có đúng không?”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Khổng sư cười cười: “Không cần khẩn trương! Thật ra thì... Ta và ngươi đồng dạng, cũng đã nhận được Thiên đạo một bộ phận!”"),t("br"),t("br"),n._v("“Khổng sư, cũng đã nhận được thư viện...”"),t("br"),t("br"),n._v("Trương Huyền con mắt trợn tròn."),t("br"),t("br"),n._v("Danh sư đại lục, biết được Khổng sư trải qua thời điểm, liền suy đoán qua, đối phương có phải hay không cũng cùng mình một chuyến, đạt được một cái khác thư viện, cho nên mới có thể tu luyện ra hoàn mỹ Thiên đạo công pháp, tiến bộ so với chính mình đều muốn nhanh."),t("br"),t("br"),n._v("“Thư viện?”"),t("br"),t("br"),n._v("Khổng sư sững sờ: “Không phải, ta được đến Thiên đạo thời không năng lực, nói cách khác cái gọi là Thiên đạo tự động!”"),t("br"),t("br"),n._v("“Thiên đạo tự động?”"),t("br"),t("br"),n._v("“Vũ trụ vạn vật, thiên địa đại đạo, trong ngày thì trắc, trăng tròn thì khuyết. Chỉ cần có sinh mệnh, nhất định phải có trật tự tồn tại, mà thời gian, không gian, chính là hắn căn bản nhất cần, cơ sở nhất trật tự.” Khổng sư gật đầu."),t("br"),t("br"),n._v("Sinh mệnh muốn sống sót, nhất định phải có không gian cùng thời gian, đây là tạo dựng toàn bộ thế giới đệ nhất pháp tắc, đệ nhất danh sách."),t("br"),t("br"),n._v("Khó trách, Khổng sư sáng lập ra pháp bảo, Xuân Thu đại điển, đã ẩn chứa thời gian pháp tắc, lại có không gian pháp tắc, cho dù tại trong thư viện, đều có thể hình thành đặc thù tu luyện vị trí, nhìn tới cùng đối phương “Thiên đạo tự động” Liên quan đến."),t("br"),t("br"),n._v("Có thời gian cùng không gian, tất cả mới có thể trở nên ngay ngắn trật tự, tất cả mới có thể thuận theo tự nhiên phát triển, mà không xuất hiện hỗn loạn."),t("br"),t("br"),n._v("Không có hai thứ này, nhiều nhất chỉ là Hỗn Độn thôi!"),t("br"),t("br"),n._v("Trước đó, thấy Khổng Thi Dao huyết mạch lực lượng, là “Một lời thành quy tắc, vạn pháp tới đi theo”, vẫn kỳ lạ, Khổng sư lĩnh ngộ đến cùng là cái gì, nghe được giờ mới hiểu được."),t("br"),t("br"),n._v("Thì ra là thế!"),t("br"),t("br"),n._v("Có trật tự, mới sáng lập Danh Sư đường, loại này chuẩn mực trật tự, truyền thụ trật tự địa phương."),t("br"),t("br"),n._v("Mới để cho toàn bộ nhân tộc, tự động phát triển, vài vạn năm kiên trì, Dị Linh tộc loại này bị vứt bỏ Thần Linh, trời sinh chủng tộc mạnh mẽ, đều khó mà vượt qua."),t("br"),t("br"),n._v("“Có trật tự, mới có quy củ, mới có thể càng tốt tu luyện!” Thấy hắn hiểu được, Khổng sư gật gật đầu, nghi ngờ nhìn qua: “Ngươi được Thiên đạo, chẳng lẽ... Không phải tự động? Mà là thư viện? Cái gì thư viện?”"),t("br"),t("br"),n._v("Mặc dù rõ ràng đối phương cũng giống như mình, nắm giữ Thiên đạo một bộ phận, nhưng Thiên đạo cuồn cuộn, bao la, cụ thể là một bộ nào phân, cũng không biết."),t("br"),t("br"),n._v("“A, ta được đến... Tuy nói là thư viện, nhưng nếu như đoán không sai, chắc là... Thiên đạo có thiếu!”"),t("br"),t("br"),n._v("Trầm tư một chút, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vừa kích hoạt thư viện thời điểm, trong đầu liền xuất hiện qua âm thanh Thiên đạo có thiếu!"),t("br"),t("br"),n._v("Bởi vì có loại năng lực này, thư viện mới có thể nhìn ra vạn vật thiếu hụt, hơn nữa tìm tới chính xác con đường, kèm theo hắn đi xa như vậy, một mực đi đến ngày hôm nay."),t("br"),t("br"),n._v("“Thiên đạo tự động, Thiên đạo có thiếu...”"),t("br"),t("br"),n._v("Khổng sư giật mình: “Thì ra là thế!”"),t("br"),t("br"),n._v("“Khổng sư vừa nói, ta đại họa lâm đầu, không còn sống lâu nữa... Chẳng lẽ cùng thư viện liên quan đến?” Trương Huyền nói."),t("br"),t("br"),n._v("“Không sai!” Khổng sư gật đầu: “Đạt được Thiên đạo một bộ phận, là cơ duyên lớn lao, cũng là tu luyện giả khát vọng nhất sự tình, nhưng... Ngươi có hay không nghĩ tới, dùng lâu có thể sẽ bị Thiên đạo xâm nhập, cuối cùng trở thành Thiên đạo một bộ phận?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Loại chuyện này, còn chưa hề nghĩ tới."),t("br"),t("br"),n._v("Chẳng qua, kèm theo thời gian tu luyện càng lâu, thật sự là hắn phát hiện, đúng thư viện ỷ lại càng ngày càng mạnh, còn hiện tại, không có thiên đạo công pháp, cũng không biết nên như thế nào tu luyện!"),t("br"),t("br"),n._v("Chẳng lẽ, đây là bị Thiên đạo xâm nhập?"),t("br"),t("br"),n._v("“Thiên Công nói, số mệnh, đều có nhân quả, có thể có được Thiên đạo một bộ phận, cấp tốc tấn cấp tu vi, để thực lực bạo tăng, khẳng định cũng có chỗ gọi là tai hại! Nếu như không nhìn lầm, ngươi tu luyện cũng là Thiên đạo công pháp a!”"),t("br"),t("br"),n._v("Khổng sư nhìn qua, ánh mắt nghiêm túc: “Tu luyện tới Tiên Quân đỉnh phong, có thể phát hiện, trong cơ thể một mực ẩn chứa một đạo một đoàn đen thui thể khí? Thiên Hỏa, lôi đình, thậm chí tiên lực, đều không thể khu trừ?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền gật đầu: “Là! Chẳng lẽ đây là ngươi lưu tại danh sư đại lục ý niệm, đã từng nói cái loại này... Giấu ở thai độc bên trong đồ vật?”"),t("br"),t("br"),n._v("Lúc trước, sử dụng Thiên Hỏa thiêu đốt, đem Tiên Thiên thai độc giải quyết đi sau, trong cơ thể nhưng lưu lại một đạo đen thui sắc thể khí, bất kể như thế nào nung khô, đều đuổi không xong."),t("br"),t("br"),n._v("Vốn là còn chút lo lắng, nhưng cho tới nay, không có xuất hiện bất kỳ thay đổi, cũng không có để thân thể xuất hiện biến cố, dần dà cũng liền yên lòng, không tiếp tục để ý."),t("br"),t("br"),n._v("Giờ phút này, Khổng sư nhắc tới, nhất thời nghĩ tới."),t("br"),t("br"),n._v("Dường như, hắn lưu tại danh sư đại lục đạo kia ý niệm, từng đặc biệt nhắc nhở qua một câu, chỉ bất quá, cuối cùng không có nói tỉ mỉ thôi."),t("br"),t("br"),n._v("Khổng sư vẻ mặt nghiêm túc nói “Không sai, ngươi bây giờ nhìn một chút, đạo này màu đen thể khí, cùng vừa nhìn thấy thời điểm, có cái gì khác biệt?”"),t("br"),n._v(" “Khác biệt?”"),t("br"),t("br"),n._v("Tinh thần nội thị, Trương Huyền nhìn kỹ quá khứ, một lát sau, thân thể cứng đờ: “Hình như... Thể tích so trước kia càng lớn hơn, hơn nữa lại không là đen thui màu sắc, mà là... Tối tăm mờ mịt!”"),t("br"),t("br"),n._v("Trước đó thể khí, chỉ có một chỉ dài ngắn, quá mức bé nhỏ, nhìn cũng không thế nào cẩn thận, cho nên cảm giác là màu đen, hiện tại tăng lên tiếp cận gấp đôi, tiểu xà đồng dạng, xoay quanh ở trong người, màu sắc cũng nhìn rõ ràng, lại là tối tăm mờ mịt màu sắc, tựa như Thần thuốc."),t("br"),t("br"),n._v("“Đúng, chính là loại màu sắc này!” Khổng sư gật đầu: “Ngươi có thể phát giác nó chỗ khác biệt?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thứ này ở trong người tồn tại thời gian rất lâu, một mực không hiểu nổi cụ thể là cái gì, tự nhiên cũng không có phát giác chỗ khác biệt, dù sao đối với hắn tu luyện, cùng chân khí di động, không có chút nào ảnh hưởng, cũng không cảm giác được sẽ đối với thân thể sinh ra tổn thương."),t("br"),t("br"),n._v("“Ngươi vận dụng một cái ‘Thiên đạo có thiếu’ năng lực thử một chút... Nhất định phải tập trung tinh thần, chú ý đạo này màu xám thể khí!” Khổng sư nói."),t("br"),t("br"),n._v("“Cái này... Tốt!” Chần chờ một chút, Trương Huyền ngẩng đầu hướng vừa rồi Khổng sư bố trí trận pháp nhìn sang, đồng thời trong óc hô khẽ “Thiếu hụt!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thư viện chấn động, một quyển sách xuất hiện, ghi chép trận pháp tốt thiếu sót."),t("br"),t("br"),n._v("Tuỳ nhìn lướt qua, Trương Huyền không khỏi cảm xúc."),t("br"),t("br"),n._v("Trước mắt vị này đối với trận pháp lý giải, quả nhiên mạnh mẽ, tiện tay sắp đặt, liền đạt đến Thiên đạo cấp bậc, một điểm thiếu hụt đều không có, cho dù là mình bây giờ, đều không làm được điểm ấy."),t("br"),t("br"),n._v("“Thế nào, nhìn lại một chút thể khí nhưng có biến hóa gì...” Khổng sư nói."),t("br"),t("br"),n._v("Trương Huyền vội vàng hướng thể khí nhìn sang, nhíu nhíu mày: “Không biến hóa... Không đúng, hình như tăng thêm một tia, trở nên càng thêm cường đại...”"),t("br"),t("br"),n._v("Trong tầm mắt màu xám thể khí, tại vừa rồi sử dụng thư viện sau, tăng lên một tia, cứ việc nhỏ yếu hầu như không nhìn thấy, nhưng lấy thực lực của hắn bây giờ, còn có thể phát giác được không đúng."),t("br"),t("br"),n._v("“Không sai, chắc là tăng lên...” Khổng sư gật đầu: “Ngươi mỗi vận dụng một lần Thiên đạo có thiếu, liền sẽ sinh ra một tia như vậy khí lưu, để hắn từ từ tăng thêm!”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt: “Ta trước kia vì sao không có phát giác?”"),t("br"),t("br"),n._v("Nếu như là như vậy, trước đó làm sao không có phát giác được?"),t("br"),t("br"),n._v("Tu vi đi đến hắn loại cảnh giới này, đúng thân thể biến hóa rất nhỏ, đều như lòng bàn tay, dựa theo tình huống bình thường, hẳn là có thể phát hiện."),t("br"),t("br"),n._v("Khổng sư nói “Danh sư đại lục võ đạo, vũ khí, đều mười phần nhỏ yếu, cho dù sử dụng Thiên đạo có thiếu năng lực, sinh ra màu xám thể khí cũng sẽ không quá nhiều, nhưng... Nơi này khác biệt, kèm theo đồ vật cấp bậc càng ngày càng cao, sinh ra tác dụng phụ, cũng sẽ càng lúc càng lớn! Trước đó, không phát hiện được bình thường, nhưng... Một khi tiếp xúc đến Ngụy Thần, thậm chí đi vào Thần giới, tác dụng phụ liền sẽ nổi bật đi ra!”"),t("br"),t("br"),n._v("Liền giống như ngựa kéo xe, kéo xe trống không cảm thấy cái gì, không biết sinh ra quá nhiều tác dụng phụ, kèm theo xe càng nặng, thể lực tự nhiên tiêu hao càng nhanh."),t("br"),t("br"),n._v("Thượng Thương bảo vật, bất luận một cái nào đều so danh sư đại lục cao minh quá nhiều, dùng thư viện giám định, tiêu phí lực lượng càng lớn, sinh ra màu xám thể khí, một cách tự nhiên cũng liền tăng lên."),t("br"),t("br"),n._v("“Cái này... Màu xám thể khí, sẽ trí mạng?”"),t("br"),t("br"),n._v("Trương Huyền run lên."),t("br"),t("br"),n._v("“Trí mạng, ngược lại cũng thôi!” Khổng sư cười khổ lắc đầu: “Sợ là sợ, thứ này ở trong người càng tích tụ càng nhiều, hơn nữa không cách nào khu trừ, dần dà, sẽ đem ngươi thay thế, cuối cùng để ngươi, biến thành Thiên đạo một bộ phận, mất đi bản thân ý thức!”"),t("br"),t("br"),n._v("Trương Huyền thân thể cứng ngắc."),t("br"),t("br"),n._v("Cái này màu xám thể khí, đến cùng là cái gì, trước đó không thèm để ý thì cũng thôi đi, giờ phút này nghe Khổng sư nói chuyện, thật là có khả năng!"),t("br"),t("br"),n._v("Cái đồ chơi này không cách nào khu trừ, kèm theo thời gian trôi qua, khẳng định sẽ ở trong cơ thể càng tích tụ càng nhiều, cuối cùng, dẫn đến cả người hắn cũng sẽ cùng thư viện dung hợp lại cùng nhau, cuối cùng biến thành không có tình cảm, không có ý thức Thiên đạo."),t("br"),t("br"),n._v("Thật muốn như vậy, cùng chết khác nhau ở chỗ nào?"),t("br"),t("br"),n._v("“Chẳng lẽ, Khổng sư ngươi...”"),t("br"),t("br"),n._v("Vội vàng nhìn qua."),t("br"),t("br"),n._v("Trước mắt vị này, cũng giống như mình, nắm giữ Thiên đạo một bộ phận, cũng chính là cái gọi là Thiên đạo tự động, đã như vậy, có thể hay không cũng giống như hắn, đồng dạng bị loại này màu xám thể khí quấy nhiễu, không cách nào khu trừ?"),t("br"),t("br"),n._v("“Không sai, ta trước đó cũng giống như ngươi, không cách nào khu trừ, suýt chút nữa mất đi bản thân ý thức... May mà, ta sớm phát hiện, đã thành công giải quyết!”"),t("br"),t("br"),n._v("Khổng sư gật đầu."),t("br"),t("br"),n._v("“Giải quyết?”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền khom người: “Còn xin Khổng sư nói cho phương pháp giải quyết, nếu như có thể giải quyết cái này tai hoạ ngầm, Trương Huyền vô cùng cảm kích!”"),t("br"),t("br"),n._v("Thư viện năng lực là tốt, là có thể để cho hắn tu luyện càng nhanh, sức chiến đấu mạnh hơn, nhưng mà nếu là bởi vậy mất đi tính mạng lời nói, đánh chết cũng phải giải quyết ah!"),t("br"),t("br"),n._v("Xem ra, không có giải quyết triệt để màu xám thể khí trước, trong thời gian ngắn, còn là đừng có dùng thư viện."),t("br"),t("br"),n._v("“Rất đơn giản!”"),t("br"),t("br"),n._v("Khổng sư nhìn qua: “Liền nhìn ngươi có nguyện ý hay không, cam lòng không nỡ lòng bỏ!”"),t("br"),t("br"),n._v("“Chỉ cần có thể giải quyết tai hoạ ngầm, chuyện gì đều nguyện ý!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi, thật ra thì nói trắng ra là cũng không khó... Sinh ra màu xám thể khí nguyên nhân chủ yếu, là bởi vì, làm nhân loại, nhưng nắm trong tay Thiên đạo, vi phạm với quy tắc!”"),t("br"),t("br"),n._v("Khổng sư híp mắt lại: “Chỉ cần ngươi đem thứ này, cũng chính là trong miệng ngươi cái này thư viện, từ trong cơ thể tách ra ngoài, tự nhiên cũng liền không còn nguy hiểm đến tính mạng!”."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-2084"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-2086"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);